INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (1,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',1,'Jeesuksen Kristuksen, Daavidin pojan ja Abrahamin\npojan, sukuluettelo: ',1,NULL),
 (2,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',2,'Abrahamille syntyi Iisak, Iisakille Jaakob,\nJaakobille Juuda ja tämän veljet,',1,NULL),
 (3,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',3,'Juudalle Peres\nja Serah, joiden äiti oli Tamar, Peresille Hesron,\nHesronille Ram,',1,NULL),
 (4,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',4,'Ramille Amminadab, Amminadabille\nNahson, Nahsonille Salma,',1,NULL),
 (5,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',5,'Salmalle Boas, jonka\näiti oli Rahab, Boasille Obed, jonka äiti oli Ruut,\nObedille Iisai',1,NULL),
 (6,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',6,'ja Iisaille Daavid, kuningas. Daavidille syntyi Salomo, jonka äiti oli Urian\nvaimo,',1,NULL),
 (7,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',7,'Salomolle Rehabeam, Rehabeamille Abia,\nAbialle Asaf,',1,NULL),
 (8,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',8,'Asafille Josafat, Josafatille\nJoram, Joramille Ussia,',1,NULL),
 (9,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',9,'Ussialle Jotam, Jotamille\nAhas, Ahasille Hiskia,',1,NULL),
 (10,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',10,'0Hiskialle Manasse,\nManasselle Amos, Amosille Josia',1,NULL),
 (11,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',11,'1ja Josialle\nJekonja ja tämän veljet Babylonian\npakkosiirtolaisuuden aikoihin. ',1,NULL),
 (12,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',12,'2Kansan pakkosiirron jälkeen Jekonjalle syntyi\nSealtiel, Sealtielille Serubbabel,',1,NULL),
 (13,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',13,'3Serubbabelille Abihud, Abihudille Eljakim,\nEljakimille Asur,',1,NULL),
 (14,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',14,'4Asurille Sadok, Sadokille\nJakin, Jakinille Elihud,',1,NULL),
 (15,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',15,'5Elihudille Eleasar,\nEleasarille Mattan, Mattanille Jaakob',1,NULL),
 (16,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',16,'6ja\nJaakobille Joosef, Marian mies. Maria synnytti\nJeesuksen, jota sanotaan Kristukseksi*. ',1,NULL),
 (17,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',17,'7Sukupolvia on siis Abrahamista Daavidiin\nkaikkiaan neljätoista, Daavidista pakkosiirron\naikaan samoin neljätoista, ja siitä taas neljätoista\nKristukseen.   Maria, Jeesuksen äiti, oli kihlattu Joosefille.\nEnnen kuin heidän liittonsa oli vahvistettu, kävi\nilmi, että Maria, Pyhän Hengen vaikutuksesta, oli\nraskaana.',1,NULL),
 (18,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',19,'9Joosef oli lakia kunnioittava mies\nmutta ei halunnut häpäistä kihlattuaan julkisesti.\nHän aikoi purkaa avioliittosopimuksen kaikessa\nhiljaisuudessa. ',1,NULL),
 (19,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',20,'0Kun Joosef ajatteli tätä, hänelle ilmestyi yöllä\nunessa Herran enkeli, joka sanoi: \"Joosef, Daavidin\npoika, älä pelkää ottaa Mariaa vaimoksesi. Se, mikä\nhänessä on siinnyt, on lähtöisin Pyhästä Hengestä.',1,NULL),
 (20,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',21,'1Hän synnyttää pojan, ja sinun tulee antaa\npojalle nimeksi Jeesus, sillä hän pelastaa kansansa\nsen synneistä.\"* ',1,NULL),
 (21,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',22,'2Tämä kaikki tapahtui, jotta kävisi toteen, mitä\nHerra on profeetan suulla ilmoittanut:   ',1,NULL),
 (22,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',24,'4Unesta herättyään Joosef teki niin kuin Herran\nenkeli oli käskenyt ja otti Marian vaimokseen.',1,NULL),
 (23,'Anonymous','2008-10-17 16:31:42','Anonymous','2008-10-17 16:31:42',25,'5Hän ei kuitenkaan koskenut vaimoonsa ennen kuin\ntämä oli synnyttänyt pojan. Joosef antoi pojalle\nnimen Jeesus. ',1,NULL),
 (24,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',1,'Kun Jeesus oli syntynyt Juudean Betlehemissä\nkuningas Herodeksen*aikana, Jerusalemiin tuli\nidästä tietäjiä.',2,NULL),
 (25,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',2,'He kysyivät: \"Missä se\njuutalaisten kuningas on, joka nyt on syntynyt? Me\nnäimme hänen tähtensä nousevan taivaalle ja tulimme\nosoittamaan hänelle kunnioitustamme.\"',2,NULL),
 (26,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',3,'Kuullessaan\ntästä kuningas Herodes pelästyi, ja hänen kanssaan\nkoko Jerusalem.',2,NULL),
 (27,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',4,'Hän kutsui koolle kansan ylipapit\nja lainopettajat*ja tiedusteli heiltä, missä\nmessiaan*oli määrä syntyä.',2,NULL),
 (28,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',5,'\"Juudean*Betlehemissä\", he vastasivat, \"sillä näin on\nilmoitettu profeetan kirjassa:   ',2,NULL),
 (29,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',7,'Silloin Herodes kutsui salaa tietäjät luokseen ja\notti heiltä juurta jaksain selville, milloin tähti\noli tullut näkyviin.',2,NULL),
 (30,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',8,'Sitten hän lähetti heidät\nBetlehemiin. \"Menkää sinne\", hän sanoi, \"ja ottakaa\nasiasta tarkka selko. Kun löydätte lapsen, niin\nilmoittakaa minulle, jotta minäkin voisin tulla\nkumartamaan häntä.\"',2,NULL),
 (31,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',9,'Kuninkaan sanat kuultuaan\ntietäjät lähtivät matkaan, ja tähti, jonka he olivat\nnähneet nousevan taivaalle, kulki heidän edellään.\nKun tähti tuli sen paikan yläpuolelle, missä lapsi\noli, se pysähtyi siihen.',2,NULL),
 (32,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',10,'0Miehet näkivät tähden,\nja heidät valtasi suuri ilo.',2,NULL),
 (33,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',11,'1He menivät taloon\nja näkivät lapsen ja hänen äitinsä Marian. Silloin\nhe maahan heittäytyen kumarsivat lasta, avasivat\narkkunsa ja antoivat hänelle kalliita lahjoja:\nkultaa, suitsuketta ja mirhaa. ',2,NULL),
 (34,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',12,'2Unessa Jumala varoitti tietäjiä palaamasta\nHerodeksen luo, ja niin he menivät toista tietä\ntakaisin omaan maahansa.   ',2,NULL),
 (35,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',14,'4Joosef heräsi unestaan, otti heti yöllä mukaansa\nlapsen ja hänen äitinsä ja lähti kulkemaan kohti\nEgyptiä.',2,NULL),
 (36,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',15,'5Siellä hän pysytteli Herodeksen\nkuolemaan asti. Näin kävi toteen, mitä Herra oli\nprofeetan suulla ilmoittanut: \"Egyptistä minä\nkutsuin poikani.\"      ',2,NULL),
 (37,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',21,'1Joosef heräsi, otti mukaansa lapsen ja hänen\näitinsä ja palasi Israelin maahan.',2,NULL),
 (38,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',22,'2Mutta kun hän\nkuuli, että Juudean kuninkaaksi oli Herodeksen\njälkeen tullut tämän poika Arkelaos, hän ei\nuskaltanut mennä sinne. Saatuaan sitten unessa\nohjeen hän meni Galileaan',2,NULL),
 (39,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',23,'3ja asettui siellä\nkaupunkiin, jonka nimi on Nasaret. Näin tapahtui,\njotta kävisi toteen profeettojen ennustus: häntä\nkutsutaan Nasaretilaiseksi.  ',2,NULL),
 (40,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',1,'Noihin aikoihin Johannes Kastaja tuli ja alkoi\njulistaa Juudean autiomaassa:',3,NULL),
 (41,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',2,'\"Kääntykää, sillä\ntaivasten valtakunta on tullut lähelle!\"',3,NULL),
 (42,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',3,'Juuri\nhäntä tarkoittaa profeetta Jesajan sana:   ',3,NULL),
 (43,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',4,'Johanneksella oli yllään kamelinkarvavaate ja\nvyötäisillään nahkavyö, ja hänen ruokanaan olivat\nheinäsirkat ja villimehiläisten hunaja.',3,NULL),
 (44,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',5,'Hänen\nluokseen tuli paljon väkeä Jerusalemista, joka\npuolelta Juudeaa ja kaikkialta Jordanin\nympäristöstä.',3,NULL),
 (45,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',6,'He tunnustivat syntinsä, ja\nJohannes kastoi heidät Jordanissa. ',3,NULL),
 (46,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',7,'Mutta kun Johannes näki, että hänen kasteelleen\noli tulossa myös monia fariseuksia*ja\nsaddukeuksia*, hän sanoi heille: \"Te käärmeen sikiöt! Kuka teille on sanonut, että te\nvoitte välttää tulevan vihan?',3,NULL),
 (47,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',8,'Tehkää hedelmää,\njossa kääntymyksenne näkyy!',3,NULL),
 (48,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',9,'Älkää luulko, että\nvoitte ajatella: \'Olemmehan me Abrahamin lapsia.\'\nMinä sanon teille: Jumala pystyy herättämään\nAbrahamille lapsia vaikka näistä kivistä.',3,NULL),
 (49,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',10,'0Kirves\non jo pantu puun juurelle. Jokainen puu, joka ei tee\nhyvää hedelmää, kaadetaan ja heitetään tuleen. ',3,NULL),
 (50,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',11,'1\"Minä kastan teidät vedellä parannukseen, mutta\nminun jälkeeni tulee toinen, joka on minua\nväkevämpi. Minä en kelpaa edes riisumaan kenkiä\nhänen jalastaan. Hän kastaa teidät Pyhällä Hengellä\nja tulella.',3,NULL),
 (51,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',12,'2Hänellä on kädessään viskain, ja\nsillä hän puhdistaa puimatantereensa viljan. Jyvät\nhän kokoaa aittaansa, mutta ruumenet hän polttaa\ntulessa, joka ei koskaan sammu.\"   ',3,NULL),
 (52,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',16,'6Kun Jeesus oli kastettu, hän nousi heti vedestä.\nSamassa taivaat aukenivat, ja Jeesus näki Jumalan\nHengen laskeutuvan kyyhkysen tavoin ja asettuvan\nhänen päälleen.',3,NULL),
 (53,'Anonymous','2008-10-17 16:31:43','Anonymous','2008-10-17 16:31:43',17,'7Ja taivaista kuului ääni: \"Tämä\non minun rakas Poikani, johon minä olen mieltynyt.\" ',3,NULL),
 (54,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',1,'Sitten Henki vei Jeesuksen autiomaahan Paholaisen\nkiusattavaksi.',4,NULL),
 (55,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',2,'Kun Jeesus oli paastonnut\nneljäkymmentä päivää ja neljäkymmentä yötä, hänen\nvihdoin tuli nälkä.',4,NULL),
 (56,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',3,'Silloin kiusaaja tuli hänen\nluokseen ja sanoi hänelle: \"Jos kerran olet Jumalan\nPoika, niin käske näiden kivien muuttua leiviksi.\"',4,NULL),
 (57,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',4,'Mutta Jeesus vastasi: \"On kirjoitettu: \'Ei\nihminen elä ainoastaan leivästä, vaan jokaisesta\nsanasta, joka lähtee Jumalan suusta.\'\" ',4,NULL),
 (58,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',5,'Sitten Paholainen vei Jeesuksen pyhään kaupunkiin\nja asetti hänet temppelimuurin harjalle.',4,NULL),
 (59,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',6,'Hän\nsanoi Jeesukselle: \"Jos kerran olet Jumalan Poika,\nniin heittäydy alas. Onhan kirjoitettu: \'Hän antaa\nenkeleilleen käskyn. He kantavat sinua käsillään,\nettet loukkaa jalkaasi kiveen.\'\"',4,NULL),
 (60,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',7,'Jeesus vastasi\nhänelle: \"On myös kirjoitettu: \'Älä kiusaa Herraa,\nJumalaasi.\'\" ',4,NULL),
 (61,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',8,'Vielä Paholainen vei Jeesuksen hyvin korkealle\nvuorelle, näytti hänelle maailman kaikki valtakunnat\nja niiden loiston',4,NULL),
 (62,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',9,'ja sanoi: \"Kaiken tämän minä\nannan sinulle, jos polvistut eteeni ja kumarrat\nminua.\"',4,NULL),
 (63,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',10,'0Silloin Jeesus sanoi hänelle: \"Mene\npois, Saatana! On kirjoitettu: \'Herraa, Jumalaasi,\nsinun tulee kunnioittaa ja ainoastaan häntä\npalvella.\'\" ',4,NULL),
 (64,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',11,'1Silloin Paholainen jätti Jeesuksen rauhaan, ja\nhänen luokseen tuli enkeleitä, jotka palvelivat\nhäntä.     ',4,NULL),
 (65,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',17,'7Tästä lähtien Jeesus julisti: \"Kääntykää, sillä\ntaivasten valtakunta on tullut lähelle!\"   ',4,NULL),
 (66,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',21,'1Sieltä jonkin matkaa kuljettuaan Jeesus näki\ntoiset kaksi veljestä, Jaakobin ja hänen veljensä\nJohanneksen, Sebedeuksen pojat. He olivat veneessä\nisänsä Sebedeuksen kanssa verkkojaan\nselvittelemässä. Jeesus kutsui heidätkin.',4,NULL),
 (67,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',22,'2He\njättivät heti veneen ja isänsä ja lähtivät\nseuraamaan Jeesusta.    ',4,NULL),
 (68,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',1,'Nähdessään kansanjoukot Jeesus nousi vuorelle.\nHän istuutui, ja opetuslapset tulivat hänen\nluokseen.',5,NULL),
 (69,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',2,'Silloin hän alkoi puhua ja opetti heitä\nnäin:   ',5,NULL),
 (70,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',11,'1\"Autuaita olette te, kun teitä minun tähteni\nherjataan ja vainotaan ja kun teistä valheellisesti\npuhutaan kaikkea pahaa.',5,NULL),
 (71,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',12,'2Iloitkaa ja riemuitkaa,\nsillä palkka, jonka te taivaissa saatte, on suuri.\nNiinhän vainottiin profeettojakin, jotka elivät\nennen teitä.   ',5,NULL),
 (72,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',14,'4\"Te olette maailman valo. Ei kaupunki voi pysyä\nkätkössä, jos se on ylhäällä vuorella.',5,NULL),
 (73,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',15,'5Eikä\nlamppua, kun se sytytetään, panna vakan alle, vaan\nlampunjalkaan. Siitä sen valo loistaa kaikille\nhuoneessa oleville.',5,NULL),
 (74,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',16,'6Näin loistakoon teidänkin\nvalonne ihmisille, jotta he näkisivät teidän hyvät\ntekonne ja ylistäisivät Isäänne, joka on taivaissa.     ',5,NULL),
 (75,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',23,'3\"Jos siis olet viemässä uhrilahjaasi alttarille\nja siinä muistat, että veljelläsi on jotakin sinua\nvastaan,',5,NULL),
 (76,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',24,'4niin jätä lahjasi alttarin eteen ja käy\nensin sopimassa veljesi kanssa. Mene sitten vasta\nantamaan lahjasi. ',5,NULL),
 (77,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',25,'5\"Tarjoa vastapuolellesi sovintoa jo silloin, kun\nvielä olet hänen kanssaan matkalla oikeuteen. Muuten\nhän saattaa luovuttaa sinut tuomarille ja tuomari\nvartijalle, ja niin sinut teljetään vankilaan.',5,NULL),
 (78,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',26,'6Usko minua: sieltä sinä et pääse, ennen kuin\nolet maksanut kaiken viimeistä kolikkoa myöten.   ',5,NULL),
 (79,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',31,'1\"On myös opetettu, että jos mies tahtoo erota\nvaimostaan, hänen on annettava tälle erokirja.',5,NULL),
 (80,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',32,'2Mutta minä sanon teille: jokainen, joka hylkää\nvaimonsa muun syyn kuin haureuden tähden, ajaa hänet\naviorikokseen. Ja aviorikoksen tekee myös se, joka\nnai miehensä hylkäämän naisen.     ',5,NULL),
 (81,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',43,'3\"Teille on opetettu: \'Rakasta lähimmäistäsi ja\nvihaa vihamiestäsi.\'',5,NULL),
 (82,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',44,'4Mutta minä sanon teille:\nrakastakaa vihamiehiänne ja rukoilkaa vainoojienne\npuolesta,',5,NULL),
 (83,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',45,'5jotta olisitte taivaallisen Isänne\nlapsia. Hän antaa aurinkonsa nousta niin hyville\nkuin pahoille ja lähettää sateen niin hurskaille\nkuin jumalattomille.',5,NULL),
 (84,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',46,'6Jos te rakastatte niitä,\njotka rakastavat teitä, minkä palkan te siitä\nansaitsette? Eivätkö publikaanitkin*tee niin?',5,NULL),
 (85,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',47,'7Jos te tervehditte vain ystäviänne, mitä\nerinomaista siinä on? Eivätkö pakanatkin tee niin?',5,NULL),
 (86,'Anonymous','2008-10-17 16:31:44','Anonymous','2008-10-17 16:31:44',48,'8Olkaa siis täydellisiä, niin kuin teidän\ntaivaallinen Isänne on täydellinen. ',5,NULL),
 (87,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',1,'\"Varokaa tuomasta hurskaita tekojanne ihmisten\nkatseltavaksi, muuten ette saa palkkaa\ntaivaalliselta Isältänne. ',6,NULL),
 (88,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',2,'\"Kun siis autat köyhiä, älä toitota siitä niin\nkuin tekopyhät tekevät synagogissa ja kujilla, jotta\nihmiset kiittelisivät heitä. Totisesti: he ovat jo\npalkkansa saaneet.',6,NULL),
 (89,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',3,'Kun annat almun, älköön vasen\nkätesi tietäkö mitä oikea tekee,',6,NULL),
 (90,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',4,'jotta hyvä\ntekosi pysyisi salassa. Isäsi, joka näkee myös sen,\nmikä on salassa, palkitsee sinut. ',6,NULL),
 (91,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',5,'\"Kun rukoilette, älkää tehkö sitä tekopyhien\ntavoin. He asettuvat mielellään synagogiin ja\nkadunkulmiin rukoilemaan, jotta olisivat ihmisten\nnäkyvissä. Totisesti: he ovat jo palkkansa saaneet.',6,NULL),
 (92,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',6,'Kun sinä rukoilet, mene sisälle huoneeseesi,\nsulje ovi ja rukoile sitten Isääsi, joka on salassa.\nIsäsi, joka näkee myös sen, mikä on salassa,\npalkitsee sinut. ',6,NULL),
 (93,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',7,'\"Rukoillessanne älkää hokeko tyhjää niin kuin\npakanat, jotka kuvittelevat tulevansa kuulluiksi,\nkun vain latelevat sanoja.',6,NULL),
 (94,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',8,'Älkää ruvetko heidän\nkaltaisikseen. Teidän Isänne kyllä tietää mitä te\ntarvitsette, jo ennen kuin olette häneltä\npyytäneetkään.',6,NULL),
 (95,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',9,'Rukoilkaa te siis näin:   ',6,NULL),
 (96,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',14,'4\"Jos te annatte toisille ihmisille anteeksi\nheidän rikkomuksensa, antaa myös taivaallinen Isänne\nteille anteeksi.',6,NULL),
 (97,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',15,'5Mutta jos te ette anna anteeksi\ntoisille, ei Isännekään anna anteeksi teidän\nrikkomuksianne. ',6,NULL),
 (98,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',16,'6\"Kun paastoatte, älkää olko synkän näköisiä niin\nkuin tekopyhät. He muuttavat muotonsa surkeaksi,\njotta kaikki varmasti huomaisivat heidän paastoavan.\nTotisesti: he ovat jo palkkansa saaneet.',6,NULL),
 (99,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',17,'7Kun\nsinä paastoat, voitele hiuksesi ja pese kasvosi.',6,NULL),
 (100,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',18,'8Silloin sinun paastoasi eivät näe ihmiset, vaan\nIsäsi, joka on salassa. Isäsi, joka näkee myös sen,\nmikä on salassa, palkitsee sinut.   ',6,NULL),
 (101,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',22,'2\"Silmä on ruumiin lamppu. Jos silmäsi on terve,\nkoko ruumiisi on valaistu.',6,NULL),
 (102,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',23,'3Jos silmäsi ovat\nhuonot, koko ruumiisi on pimeä. Jos siis se valo,\njoka sinussa on, on pimeyttä, millainen onkaan\npimeys! ',6,NULL),
 (103,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',24,'4\"Kukaan ei voi palvella kahta herraa. Jos hän\ntoista rakastaa, hän vihaa toista; jos hän toista\npitää arvossa, hän halveksii toista. Te ette voi\npalvella sekä Jumalaa että mammonaa.   ',6,NULL),
 (104,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',28,'8\"Mitä te vaatetuksesta huolehditte! Katsokaa\nkedon kukkia, kuinka ne nousevat maasta: eivät ne\nnäe vaivaa eivätkä kehrää.',6,NULL),
 (105,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',29,'9Minä sanon teille:\nedes Salomo kaikessa loistossaan ei ollut niin\nvaatetettu kuin mikä tahansa niistä.',6,NULL),
 (106,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',30,'0Kun Jumala\nnäin pukee kedon ruohon, joka tänään kasvaa ja\nhuomenna joutuu uuniin, niin tottahan hän teistä\nhuolehtii, te vähäuskoiset! ',6,NULL),
 (107,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',31,'1\"Älkää siis murehtiko: \'Mitä me nyt syömme?\' tai\n\'Mitä me juomme?\' tai \'Mistä me saamme vaatteet?\'',6,NULL),
 (108,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',32,'2Tätä kaikkea pakanat tavoittelevat. Teidän\ntaivaallinen Isänne tietää kyllä, että te\ntarvitsette kaikkea tätä.',6,NULL),
 (109,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',33,'3Etsikää ennen kaikkea\nJumalan valtakuntaa ja hänen vanhurskasta tahtoaan,\nniin teille annetaan kaikki tämäkin.',6,NULL),
 (110,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',34,'4Älkää siis\nhuolehtiko huomispäivästä, se pitää kyllä itsestään\nhuolen. Kullekin päivälle riittävät sen omat\nmurheet. ',6,NULL),
 (111,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',1,'\"Älkää tuomitko, ettei teitä tuomittaisi.',7,NULL),
 (112,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',2,'Niin\nkuin te tuomitsette, niin tullaan teidät\ntuomitsemaan, ja niin kuin te mittaatte, niin\ntullaan teille mittaamaan.',7,NULL),
 (113,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',3,'Kuinka näet roskan\nveljesi silmässä, mutta et huomaa, että omassa\nsilmässäsi on hirsi?',7,NULL),
 (114,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',4,'Kuinka voit sanoa\nveljellesi: \'Annapa kun otan roskan silmästäsi\', kun\nomassa silmässäsi on hirsi?',7,NULL),
 (115,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',5,'Sinä tekopyhä! Ota\nensin hirsi omasta silmästäsi, vasta sitten näet\nottaa roskan veljesi silmästä. ',7,NULL),
 (116,'Anonymous','2008-10-17 16:31:45','Anonymous','2008-10-17 16:31:45',6,'\"Älkää antako koirille sitä, mikä on pyhää,\nälkääkä heittäkö helmiänne sikojen eteen, etteivät\nne tallaa niitä jalkoihinsa ja käy teidän kimppuunne\nja raatele teitä. ',7,NULL),
 (117,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',7,'\"Pyytäkää, niin teille annetaan. Etsikää, niin te\nlöydätte. Kolkuttakaa, niin teille avataan.',7,NULL),
 (118,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',8,'Sillä\njokainen pyytävä saa ja jokainen etsijä löytää, ja\njokaiselle, joka kolkuttaa, avataan.',7,NULL),
 (119,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',9,'Ei kai\nkukaan teistä anna pojalleen kiveä, kun hän pyytää\nleipää?',7,NULL),
 (120,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',10,'0Tai käärmettä, kun hän pyytää kalaa?',7,NULL),
 (121,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',11,'1Jos kerran te pahat ihmiset osaatte antaa\nlapsillenne kaikenlaista hyvää, niin paljon ennemmin\nteidän taivaallinen Isänne antaa hyvää niille, jotka\nsitä häneltä pyytävät. ',7,NULL),
 (122,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',12,'2\"Kaikki, minkä tahdotte ihmisten tekevän teille,\ntehkää te heille. Tässä on laki ja profeetat. ',7,NULL),
 (123,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',13,'3\"Menkää sisään ahtaasta portista. Monet menevät\navarasta portista ja laveaa tietä, mutta se vie\nkadotukseen.',7,NULL),
 (124,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',14,'4Miten ahdas onkaan se portti ja\nkapea se tie, joka vie elämään, ja vain harvat\nlöytävät sen!   ',7,NULL),
 (125,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',21,'1\"Ei jokainen, joka sanoo minulle: \'Herra,\nHerra\', pääse taivasten valtakuntaan. Sinne pääsee\nse, joka tekee taivaallisen Isäni tahdon.',7,NULL),
 (126,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',22,'2Monet\nsanovat minulle sinä päivänä: \'Herra, Herra! Sinun\nnimessäsihän me profetoimme, sinun nimessäsi me\nkarkotimme pahoja henkiä ja sinun nimessäsi teimme\nmonia voimatekoja.\'',7,NULL),
 (127,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',23,'3Mutta silloin he saavat\nminulta vastauksen: \'En tunne teitä. Menkää pois\nminun luotani, vääryydentekijät!\'   ',7,NULL),
 (128,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',26,'6\"Jokainen, joka kuulee nämä sanani mutta ei tee\nniiden mukaan, on kuin tyhmä mies, joka rakensi\ntalonsa hiekalle.',7,NULL),
 (129,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',27,'7Alkoi sataa, tulvavesi virtasi\nja myrskytuuli pieksi taloa, ja se sortui, maan\ntasalle saakka.\"    ',7,NULL),
 (130,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',1,'Kun Jeesus tuli alas vuorelta, suuri ihmisjoukko\nseurasi häntä.',8,NULL),
 (131,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',2,'Hänen luokseen tuli silloin muuan\nspitaalinen. Mies heittäytyi maahan hänen eteensä ja\nsanoi: \"Herra! Jos vain tahdot, sinä voit puhdistaa\nminut.\"',8,NULL),
 (132,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',3,'Jeesus ojensi kätensä, kosketti miestä ja\nsanoi: \"Minä tahdon. Tule puhtaaksi.\" Heti mies\nparani taudistaan.',8,NULL),
 (133,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',4,'Jeesus sanoi hänelle: \"Katso,\nettet puhu tästä kenellekään. Mene vain näyttämään\nitsesi papille ja anna Mooseksen määräämä uhrilahja,\ntodisteeksi parantumisesta.\"   ',8,NULL),
 (134,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',10,'0Tämän kuullessaan Jeesus hämmästyi ja sanoi\nniille, jotka häntä seurasivat: \"Totisesti: näin\nvahvaa uskoa en ole tavannut yhdelläkään\nisraelilaisella.',8,NULL),
 (135,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',11,'1Minä sanon teille, että niin\nidästä kuin lännestä tulee monia, jotka taivasten\nvaltakunnassa käyvät aterialle yhdessä Abrahamin,\nIisakin ja Jaakobin kanssa.',8,NULL),
 (136,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',12,'2Mutta ne, joiden oli\nmäärä periä valtakunta, heitetään ulos pimeyteen.\nSiellä itketään ja kiristellään hampaita.\"',8,NULL),
 (137,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',13,'3Sitten Jeesus sanoi sadanpäällikölle: \"Mene.\nTapahtukoon niin kuin uskot.\" Sillä hetkellä\npalvelija parani.          ',8,NULL),
 (138,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',30,'0Jonkin matkan päässä oli suuri sikalauma\nlaitumella.',8,NULL),
 (139,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',31,'1Pahat henget pyysivät Jeesukselta:\n\"Jos ajat meidät pois, niin lähetä meidät\nsikalaumaan.\"',8,NULL),
 (140,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',32,'2\"Menkää!\" Jeesus sanoi. Silloin\nhenget lähtivät miehistä ja menivät sikoihin, ja\nkoko lauma syöksyi jyrkännettä alas järveen ja\nhukkui. ',8,NULL),
 (141,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',33,'3Sikopaimenet lähtivät pakoon. He menivät\nkaupunkiin ja kertoivat, kuinka oli käynyt ja mitä\npahojen henkien vaivaamille oli tapahtunut.',8,NULL),
 (142,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',34,'4Silloin kaikki lähtivät kaupungista Jeesusta\nvastaan. Kun he näkivät hänet, he pyysivät häntä\npoistumaan siltä seudulta. ',8,NULL),
 (143,'Anonymous','2008-10-17 16:31:46','Anonymous','2008-10-17 16:31:46',1,'Jeesus astui veneeseen, meni järven yli ja tuli\nkotikaupunkiinsa.',9,NULL),
 (144,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',2,'Siellä ihmiset toivat hänen\nluokseen halvaantuneen, joka makasi vuoteella. Kun\nJeesus näki heidän uskonsa, hän sanoi\nhalvaantuneelle: \"Ole rohkealla mielellä, poikani,\nsinun syntisi annetaan anteeksi.\" ',9,NULL),
 (145,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',3,'Jotkut lainopettajat sanoivat silloin itsekseen:\n\"Tuohan herjaa Jumalaa.\"',9,NULL),
 (146,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',4,'Mutta Jeesus näki, mitä\nhe ajattelivat, ja sanoi: \"Miksi teillä on pahoja\najatuksia sydämessänne?',9,NULL),
 (147,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',5,'Kumpi on helpompaa,\nsanoa: \'Sinun syntisi annetaan anteeksi\', vai sanoa:\n\'Nouse ja kävele\'?',9,NULL),
 (148,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',6,'Mutta jotta te tietäisitte,\nettä Ihmisen Pojalla on valta antaa maan päällä\nsyntejä anteeksi\" -- hän puhui nyt halvaantuneelle -\n- \"nouse, ota vuoteesi ja mene kotiisi.\"',9,NULL),
 (149,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',7,'Silloin\nmies nousi ja lähti kotiinsa.',9,NULL),
 (150,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',8,'Tämän nähdessään\nkansa pelästyi ja ylisti Jumalaa, joka oli antanut\nihmisille sellaisen vallan.   ',9,NULL),
 (151,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',10,'0Jeesus oli sitten aterialla hänen kodissaan.\nSinne tuli myös useita publikaaneja ja muita\nsyntisiä, ja he aterioivat Jeesuksen ja hänen\nopetuslastensa kanssa.',9,NULL),
 (152,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',11,'1Tämän nähdessään\nfariseukset sanoivat Jeesuksen opetuslapsille:\n\"Kuinka teidän opettajanne syö yhdessä publikaanien\nja muiden syntisten kanssa!\"',9,NULL),
 (153,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',12,'2Jeesus kuuli sen ja\nsanoi: \"Eivät terveet tarvitse parantajaa, vaan\nsairaat.',9,NULL),
 (154,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',13,'3Menkää ja tutkikaa, mitä tämä\ntarkoittaa: \'Armahtavaisuutta minä tahdon, en\nuhrimenoja.\' En minä ole tullut kutsumaan hurskaita,\nvaan syntisiä.\"     ',9,NULL),
 (155,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',20,'0Silloin Jeesusta lähestyi muuan nainen, jota\nkaksitoista vuotta oli vaivannut verenvuoto. Hän\ntuli Jeesuksen taakse ja kosketti hänen viittansa\ntupsua.',9,NULL),
 (156,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',21,'1Hän näet ajatteli: \"Jos vain saan\nkoskettaa hänen viittaansa, minä paranen.\"',9,NULL),
 (157,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',22,'2Jeesus kääntyi, näki naisen ja sanoi: \"Ole\nrohkealla mielellä, tyttäreni, uskosi on parantanut\nsinut.\"*Siitä hetkestä nainen oli terve. ',9,NULL),
 (158,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',23,'3Kun Jeesus tuli esimiehen taloon ja näki\nhuilunsoittajat ja hälisevän ihmisjoukon,',9,NULL),
 (159,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',24,'4hän\nsanoi: \"Menkää pois! Ei tyttö ole kuollut, hän\nnukkuu.\" Hänelle naurettiin.',9,NULL),
 (160,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',25,'5Mutta kun väki oli\najettu ulos, Jeesus meni sisään ja otti tyttöä\nkädestä, ja tyttö nousi.',9,NULL),
 (161,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',26,'6Tästä levisi tieto koko\nsille seudulle.       ',9,NULL),
 (162,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',36,'6Kun hän näki väkijoukot, hänet valtasi sääli,\nsillä ihmiset olivat näännyksissä ja heitteillä,\nkuin lammaslauma paimenta vailla.',9,NULL),
 (163,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',37,'7Silloin hän\nsanoi opetuslapsilleen: \"Satoa on paljon, mutta\nsadonkorjaajia vähän.',9,NULL),
 (164,'Anonymous','2008-10-17 16:31:47','Anonymous','2008-10-17 16:31:47',38,'8Pyytäkää siis herraa,\njolle sato kuuluu, lähettämään väkeä\nelonkorjuuseen.\"  ',9,NULL),
 (165,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',1,'Jeesus kutsui kaksitoista opetuslastaan luokseen\nja antoi heille vallan karkottaa saastaisia henkiä\nja parantaa kaikkia tauteja ja vaivoja. ',10,NULL),
 (166,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',2,'Nämä ovat niiden kahdentoista apostolin nimet:\nensimmäisenä Simon, toiselta nimeltä Pietari, ja\nhänen veljensä Andreas, Jaakob Sebedeuksen poika ja\nhänen veljensä Johannes,',10,NULL),
 (167,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',3,'Filippus ja Bartolomeus,\nTuomas ja publikaani Matteus, Jaakob Alfeuksen poika\nja Taddeus,',10,NULL),
 (168,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',4,'Simon Kananeus*ja Juudas Iskariot,\njoka sitten kavalsi Jeesuksen.   \"Älkää menkö vierasheimoisten keskuuteen älkääkä\nmihinkään Samarian kaupunkiin.',10,NULL),
 (169,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',6,'Sen sijaan menkää\nIsraelin kansan eksyneiden lampaiden luo',10,NULL),
 (170,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',7,'ja\njulistakaa: \'Taivasten valtakunta on tullut\nlähelle.\'',10,NULL),
 (171,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',8,'Parantakaa sairaita, herättäkää\nkuolleita, puhdistakaa spitaalisia ja ajakaa pois\npahoja henkiä. Lahjaksi olette saaneet, lahjaksi\nantakaa. ',10,NULL),
 (172,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',9,'\"Älkää varatko vyöhönne kultaa, hopeaa tai\nkuparia',10,NULL),
 (173,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',10,'0älkääkä ottako mukaanne laukkua, älkää\nylimääräistä paitaa, älkää jalkineita tai sauvaa.\nTyömies on ruokansa ansainnut. ',10,NULL),
 (174,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',11,'1\"Kun tulette johonkin kaupunkiin tai kylään,\nottakaa selvää, kuka siellä on käyntinne arvoinen,\nja jääkää asumaan hänen luokseen lähtöönne saakka.',10,NULL),
 (175,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',12,'2Kun menette taloon, toivottakaa talon väelle\nrauhaa.',10,NULL),
 (176,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',13,'3Jos talo on sen arvoinen, saakoon se\ntoivottamanne rauhan, mutta jos se ei ansaitse sitä,\npalatkoon toivotus teille takaisin.',10,NULL),
 (177,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',14,'4Jos teitä\njossakin talossa tai kaupungissa ei oteta vastaan\neikä teidän sanojanne kuunnella, lähtekää sieltä ja\npudistakaa pöly jaloistanne.',10,NULL),
 (178,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',15,'5Totisesti: Sodoman\nja Gomorran maa pääsee tuomiopäivänä vähemmällä kuin\nse kaupunki.   ',10,NULL),
 (179,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',17,'7\"Pitäkää varanne, sillä ihmiset vievät teidät\noikeuden eteen ja ruoskivat teitä synagogissaan.',10,NULL),
 (180,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',18,'8Maaherrojen ja kuninkaidenkin eteen teitä\ntullaan viemään minun takiani, todistukseksi heille\nja kansoille.',10,NULL),
 (181,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',19,'9Mutta kun teidät luovutetaan\nviranomaisten käsiin, älkää olko huolissanne siitä,\nmiten tai mitä puhuisitte, sillä teille annetaan\ntuona hetkenä sanat, jotka teidän tulee puhua.',10,NULL),
 (182,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',20,'0Te ette puhu itse, teissä puhuu Isänne Henki. ',10,NULL),
 (183,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',21,'1\"Veli antaa veljensä surmattavaksi, isä\nlapsensa, ja lapset nousevat vanhempiaan vastaan ja\ntuottavat heille kuoleman.',10,NULL),
 (184,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',22,'2Kaikki tulevat\nvihaamaan teitä minun nimeni tähden, mutta se, joka\nkestää loppuun asti, pelastuu. ',10,NULL),
 (185,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',23,'3\"Kun teitä yhdessä kaupungissa vainotaan,\npaetkaa toiseen. Totisesti: te ette ehdi käydä\nkaikissa Israelin kaupungeissa ennen kuin Ihmisen\nPoika tulee. ',10,NULL),
 (186,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',24,'4\"Ei opetuslapsi ole opettajaansa ylempi eikä\npalvelija isäntäänsä.',10,NULL),
 (187,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',25,'5Tyytyköön opetuslapsi\nsamaan kuin opettaja ja palvelija samaan kuin\nisäntä. Jos talon isäntää nimitetään Belsebuliksi*,\nkuinka paljon helpommin sen nimen saa talonväki! ',10,NULL),
 (188,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',26,'6\"Älkää siis pelätkö ihmisiä. Ei ole kätköä, joka\nei paljastuisi, eikä salaisuutta, joka ei tulisi\nilmi.',10,NULL),
 (189,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',27,'7Minkä minä sanon teille pimeässä, se\njulistakaa päivänvalossa, ja mitä kuulette korvaanne\nkuiskattavan, se kuuluttakaa julki katoilta.',10,NULL),
 (190,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',28,'8Älkää pelätkö niitä, jotka tappavat ruumiin\nmutta eivät kykene tappamaan sielua. Pelätkää sen\nsijaan häntä, joka voi sekä sielun että ruumiin\nhukuttaa helvettiin.',10,NULL),
 (191,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',29,'9Varpusia saa kolikolla\nkaksi, mutta yksikään niistä ei putoa maahan, ellei\nteidän Isänne sitä salli.',10,NULL),
 (192,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',30,'0Teidän jokainen\nhiuskarvannekin on laskettu.',10,NULL),
 (193,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',31,'1Älkää siis pelätkö.\nOlettehan te arvokkaampia kuin kaikki varpuset.   ',10,NULL),
 (194,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',34,'4\"Älkää luulko, että minä olen tullut tuomaan\nmaan päälle rauhaa. En minä ole tullut tuomaan\nrauhaa, vaan miekan.',10,NULL),
 (195,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',35,'5Minä olen tullut nostamaan\npojan isäänsä, tyttären äitiään ja miniän anoppiaan\nvastaan.',10,NULL),
 (196,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',36,'6Viholliset ovat oman talon väkeä. ',10,NULL),
 (197,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',37,'7\"Joka rakastaa isäänsä tai äitiänsä enemmän kuin\nminua, ei kelpaa minulle, eikä se, joka rakastaa\npoikaansa tai tytärtänsä enemmän kuin minua, kelpaa\nminulle.',10,NULL),
 (198,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',38,'8Joka ei ota ristiään ja seuraa minua,\nse ei kelpaa minulle.',10,NULL),
 (199,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',39,'9Joka varjelee elämäänsä,\nkadottaa sen, mutta joka elämänsä minun tähteni\nkadottaa, on sen löytävä. ',10,NULL),
 (200,'Anonymous','2008-10-17 16:31:48','Anonymous','2008-10-17 16:31:48',40,'0\"Joka ottaa vastaan teidät, ottaa vastaan minut,\nja joka ottaa minut vastaan, ottaa vastaan sen, joka\non minut lähettänyt.',10,NULL),
 (201,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',41,'1Joka ottaa profeetan\nvastaan siksi, että tämä on profeetta, saa profeetan\npalkan, ja joka ottaa vanhurskaan vastaan siksi,\nettä tämä on vanhurskas, saa vanhurskaalle kuuluvan\npalkan.',10,NULL),
 (202,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',42,'2Ja joka antaa yhdellekin näistä\nvähäisistä maljallisen raikasta vettä vain siksi,\nettä tämä on opetuslapsi -- totisesti: hän ei jää\npalkkaansa vaille.\"  ',10,NULL),
 (203,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',1,'Annettuaan kaikki nämä ohjeet kahdelletoista\nopetuslapselleen Jeesus lähti muihin sen seudun\nkaupunkeihin opettamaan ja julistamaan. ',11,NULL),
 (204,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',2,'Kun Johannes vankilassa kuuli Kristuksen teoista,\nhän lähetti opetuslapsensa',11,NULL),
 (205,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',3,'kysymään: \"Oletko sinä\nse, jonka on määrä tulla, vai pitääkö meidän odottaa\ntoista?\"',11,NULL),
 (206,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',4,'Jeesus vastasi heille: \"Kertokaa\nJohannekselle, mitä kuulette ja näette:    \"Mitä te lähditte autiomaahan katsomaan? Ruokoako,\njota tuuli huojuttaa?',11,NULL),
 (207,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',8,'Vai mitä odotitte\nnäkevänne? Kenties hienosti pukeutuneen miehen?\nKuninkaanlinnoista te niitä löydätte, jotka\nhienostelevat vaatteillaan!',11,NULL),
 (208,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',9,'Mitä te sitten\nodotitte näkevänne? Profeetanko? Aivan oikein, ja\nminä sanon teille, että hän on enemmänkin kuin\nprofeetta.',11,NULL),
 (209,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',10,'0Hän on se, josta on kirjoitettu:   ',11,NULL),
 (210,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',11,'1\"Totisesti: yksikään naisesta syntynyt ei ole\nollut Johannes Kastajaa suurempi, mutta kaikkein\nvähäisin, joka on taivasten valtakunnassa, on\nsuurempi kuin hän.',11,NULL),
 (211,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',12,'2Johannes Kastajan päivistä\nasti taivasten valtakunta on ollut murtautumassa\nesiin, ja jotkut yrittävät väkivalloin temmata sen\nitselleen.*',11,NULL),
 (212,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',13,'3Kaikki profeetat ja laki ovat\nJohannekseen asti olleet ennustusta,',11,NULL),
 (213,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',14,'4ja uskokaa\ntai älkää, juuri hän on Elia, jonka oli määrä tulla.',11,NULL),
 (214,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',15,'5Jolla on korvat, se kuulkoon! ',11,NULL),
 (215,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',16,'6\"Mihin minä vertaisin tätä sukupolvea? Se on\nkuin torilla istuvat lapset, jotka huutavat\ntoisilleen:',11,NULL),
 (216,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',17,'7\'Me soitimme teille huilua, mutta te\nette tanssineet, me pidimme valittajaisia, mutta te\nette itkeneet mukana.\'',11,NULL),
 (217,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',18,'8Johannes tuli, hän ei syö\neikä juo, ja ihmiset sanovat: \'Hänessä on paha\nhenki.\'',11,NULL),
 (218,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',19,'9Ihmisen Poika tuli, hän syö ja juo, ja\nihmiset sanovat: \'Mikä syömäri ja juomari,\npublikaanien ja muiden syntisten ystävä!\' Mutta\nViisauden teoista Viisaus tunnetaan!\"   ',11,NULL),
 (219,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',21,'1\"Voi sinua, Korasin! Voi sinua, Betsaida! Jos\nteidän kaduillanne tehdyt voimateot olisi tehty\nTyroksessa tai Sidonissa, niiden asukkaat olisivat\njo aikoja sitten verhoutuneet säkkiin ja tuhkaan ja\nkääntyneet.',11,NULL),
 (220,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',22,'2Minä sanon teille: Tyros ja Sidon\npääsevät tuomiopäivänä vähemmällä kuin te. ',11,NULL),
 (221,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',23,'3\"Entä sinä, Kapernaum, korotetaanko sinut muka\ntaivaaseen? Alas sinut syöstään, alas tuonelaan\nsaakka! Jos sinun kaduillasi tehdyt voimateot olisi\ntehty Sodomassa, se olisi pystyssä vielä tänäkin\npäivänä.',11,NULL),
 (222,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',24,'4Minä sanon: Sodoman maa pääsee\ntuomiopäivänä vähemmällä kuin sinä.\"   \"Minä ylistän sinua, Isä, taivaan ja maan Herra,\nsiitä että olet salannut tämän viisailta ja\noppineilta mutta ilmoittanut sen lapsenmielisille.',11,NULL),
 (223,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',26,'6Näin sinä, Isä, olet hyväksi nähnyt. ',11,NULL),
 (224,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',27,'7\"Kaiken on Isäni antanut minun haltuuni. Poikaa\nei tunne kukaan muu kuin Isä eikä Isää kukaan muu\nkuin Poika ja se, jolle Poika tahtoo hänet\nilmoittaa. ',11,NULL),
 (225,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',28,'8\"Tulkaa minun luokseni, kaikki te työn ja\nkuormien uuvuttamat. Minä annan teille levon.',11,NULL),
 (226,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',29,'9Ottakaa minun ikeeni harteillenne ja katsokaa\nminua: minä olen sydämeltäni lempeä ja nöyrä. Näin\nteidän sielunne löytää levon.',11,NULL),
 (227,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',30,'0Minun ikeeni on\nhyvä kantaa ja minun kuormani on kevyt.\" ',11,NULL),
 (228,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',1,'Niihin aikoihin Jeesus kerran kulki sapattina\nviljapellon laitaa. Hänen opetuslastensa tuli nälkä,\nja he alkoivat katkoa tähkäpäitä ja syödä.',12,NULL),
 (229,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',2,'Fariseukset näkivät sen ja sanoivat Jeesukselle:\n\"Katso nyt, opetuslapsesi tekevät sellaista, mitä\nsapattina ei ole lupa tehdä.\"',12,NULL),
 (230,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',3,'Mutta Jeesus\nvastasi heille: \"Ettekö ole lukeneet, mitä Daavid\nteki, kun hän ja hänen miehensä olivat nälissään?',12,NULL),
 (231,'Anonymous','2008-10-17 16:31:49','Anonymous','2008-10-17 16:31:49',4,'Hän meni Jumalan huoneeseen ja söi uhrileivät,\nvaikka niiden syöminen oli sallittua ainoastaan\npapeille, ei hänelle eikä hänen miehilleen.',12,NULL),
 (232,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',5,'Tai\nettekö ole laista lukeneet, että papit tekevät\ntemppelissä sapattinakin työtä mutta ovat silti\nsyyttömiä?',12,NULL),
 (233,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',6,'Minä sanon: tässä teillä on enemmän\nkuin temppeli.',12,NULL),
 (234,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',7,'Jos te ymmärtäisitte, mitä tämä\ntarkoittaa: \'Armahtavaisuutta minä tahdon, en\nuhrimenoja\', te ette tuomitsisi syyttömiä.',12,NULL),
 (235,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',8,'Ihmisen Poika on sapatin herra.\"        ',12,NULL),
 (236,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',25,'5Jeesus tiesi heidän ajatuksensa ja sanoi heille: \"Jokainen valtakunta, joka jakautuu ja taistelee\nitseään vastaan, tuhoutuu, eikä myöskään kaupunki\ntai perhe kestä, jos sitä repivät riidat.',12,NULL),
 (237,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',26,'6Jos\nSaatana ajaa ulos Saatanan, silloin se on noussut\nitseään vastaan. Kuinka sen valtakunta silloin voi\npysyä koossa?',12,NULL),
 (238,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',27,'7Jos minä karkotan pahoja henkiä\nBelsebulin avulla, kenen avulla sitten teikäläiset\nniitä karkottavat? Heistä te saatte itsellenne\ntuomarit.',12,NULL),
 (239,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',28,'8Mutta jos minä karkotan pahoja henkiä\nJumalan Hengen voimalla, silloinhan Jumalan\nvaltakunta on jo tullut teidän luoksenne. ',12,NULL),
 (240,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',29,'9\"Kuinka kukaan voi tunkeutua väkevän miehen\ntaloon ja ryöstää hänen tavaroitaan, ellei ensin\nsido häntä? Vasta sitten hän voi ryöstää talon ja\ntavarat.',12,NULL),
 (241,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',30,'0Joka ei ole minun puolellani, on minua\nvastaan, ja joka ei yhdessä minun kanssani kokoa, se\nhajottaa. ',12,NULL),
 (242,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',31,'1\"Sen tähden sanon teille: jokainen synti ja\nherjaus tullaan antamaan ihmisille anteeksi, mutta\nHengen herjaamista ei anteeksi anneta.',12,NULL),
 (243,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',32,'2Joka\nsanoo jotakin Ihmisen Poikaa vastaan, hänelle\nannetaan anteeksi, mutta joka puhuu Pyhää Henkeä\nvastaan, hänelle ei anteeksi anneta, ei tässä eikä\ntulevassa maailmanajassa.     ',12,NULL),
 (244,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',39,'9Mutta Jeesus vastasi heille: \"Tämä paha ja uskoton sukupolvi vaatii merkkiä,\nmutta ainoa merkki, joka sille annetaan, on Joonan\nmerkki.',12,NULL),
 (245,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',40,'0Niin kuin profeetta Joona oli meripedon\nvatsassa kolme päivää ja kolme yötä, niin on Ihmisen\nPoika oleva maan povessa kolme päivää ja kolme yötä.',12,NULL),
 (246,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',41,'1Niniven asukkaat nousevat tuomiolle yhdessä\ntämän sukupolven kanssa ja langettavat sille\ntuomion. He kääntyivät, kun Joona saarnasi heille,\nja tässä teillä on enemmän kuin Joona!',12,NULL),
 (247,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',42,'2Etelän\nkuningatar herää tuomiolle yhdessä tämän sukupolven\nkanssa ja langettaa sille tuomion. Hän tuli maan\nääristä saakka kuulemaan Salomon viisautta, ja tässä\nteillä on enemmän kuin Salomo!      ',12,NULL),
 (248,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',1,'Samana päivänä Jeesus lähti ulos, meni järven\nrantaan ja asettui istumaan.',13,NULL),
 (249,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',2,'Hänen ympärilleen\nkokoontui silloin niin suuri joukko ihmisiä, että\nhänen oli siirryttävä veneeseen. Hän istui veneessä\nja väkijoukko seisoi rannalla,',13,NULL),
 (250,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',3,'ja hän puhui\nheille pitkään vertauksin. Hän sanoi: \"Mies lähti kylvämään.',13,NULL),
 (251,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',4,'Ja kun hän\nkylvi, osa siemenestä putosi tien oheen, ja linnut\ntulivat ja söivät jyvät.',13,NULL),
 (252,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',5,'Osa putosi kallioiseen\npaikkaan, missä jyville ei ollut paljon maata. Ne\nnousivat kohta oraalle, koska maata ei ollut\nsyvälti,',13,NULL),
 (253,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',6,'mutta auringon noustua oraat helteessä\nkuivettuivat, koska niillä ei ollut juurta.',13,NULL),
 (254,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',7,'Osa\ntaas putosi ohdakkeisiin, ja ohdakkeet kasvoivat ja\ntukahduttivat oraan.',13,NULL),
 (255,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',8,'Mutta osa jyvistä putosi\nhyvään maahan ja antoi sadon, mikä sata, mikä\nkuusikymmentä, mikä kolmekymmentä jyvää.',13,NULL),
 (256,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',9,'Jolla on\nkorvat, se kuulkoon!\"   ',13,NULL),
 (257,'Anonymous','2008-10-17 16:31:50','Anonymous','2008-10-17 16:31:50',11,'1Jeesus vastasi: \"Siksi, että te olette saaneet oppia tuntemaan\ntaivasten valtakunnan salaisuudet, mutta he eivät.',13,NULL),
 (258,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',12,'2Jolla on, sille annetaan, ja hän on saava\nyltäkyllin, mutta jolla ei ole, siltä otetaan pois\nsekin mitä hänellä on.',13,NULL),
 (259,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',13,'3Minä puhun heille\nvertauksin, koska he näkevät eivätkä kuitenkaan näe\nja kuulevat eivätkä kuitenkaan kuule eivätkä\nymmärrä.',13,NULL),
 (260,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',14,'4Heissä käy toteen tämä Jesajan\nennustus:   ',13,NULL),
 (261,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',16,'6\"Autuaat ovat teidän silmänne, koska ne näkevät,\nja korvanne, koska ne kuulevat!',13,NULL),
 (262,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',17,'7Totisesti: monet\nprofeetat ja vanhurskaat ovat halunneet nähdä mitä\nte näette, eivätkä ole nähneet, ja kuulla mitä te\nkuulette, eivätkä ole kuulleet.       ',13,NULL),
 (263,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',33,'3Vielä hän esitti heille vertauksen: \"Taivasten\nvaltakunta on kuin hapate. Kun nainen sekoitti sen\nkolmeen vakalliseen jauhoja, koko taikina happani.\" ',13,NULL),
 (264,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',34,'4Kaiken tämän Jeesus puhui väkijoukolle\nvertauksin. Vertauksitta hän ei puhunut heille\nmitään,',13,NULL),
 (265,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',35,'5jotta tämä profeetan sana kävisi toteen:    \"Mies, joka kylvi hyvää siementä, on Ihmisen Poika.',13,NULL),
 (266,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',38,'8Pelto on maailma. Hyvä siemen tarkoittaa niitä,\njotka kuuluvat taivasten valtakuntaan, rikkavilja\nniitä, jotka ovat Paholaisen vallassa.',13,NULL),
 (267,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',39,'9Paholainen, joka kylvi rikkaviljaa, on Saatana,\nelonkorjuu on maailman loppu, ja korjuumiehet ovat\nenkeleitä.',13,NULL),
 (268,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',40,'0Niin kuin rikkavilja kootaan ja\nhävitetään polttamalla, niin tapahtuu maailman\nlopussa.',13,NULL),
 (269,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',41,'1Ihmisen Poika lähettää enkelinsä, ja he\nkokoavat hänen valtakunnastaan kaikki, jotka\nviettelevät pahaan ja harjoittavat vääryyttä.',13,NULL),
 (270,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',42,'2Enkelit heittävät heidät tuliseen pätsiin, ja\nsiellä itketään ja kiristellään hampaita.',13,NULL),
 (271,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',43,'3Mutta\nJumalan omat loistavat silloin Isänsä valtakunnassa\nniin kuin aurinko. Jolla on korvat, se kuulkoon!   ',13,NULL),
 (272,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',45,'5\"Taivasten valtakunta on myös tällainen.\nKauppias etsi kauniita helmiä.',13,NULL),
 (273,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',46,'6Kun hän löysi\nyhden kallisarvoisen helmen, hän myi kaiken minkä\nomisti ja osti sen. ',13,NULL),
 (274,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',47,'7\"Vielä taivasten valtakunta on kuin nuotta, joka\nlaskettiin mereen ja joka keräsi kaikenlaisia\nkaloja.',13,NULL),
 (275,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',48,'8Kun se tuli täyteen, kalastajat vetivät\nsen rantaan, istuutuivat ja lajittelivat hyvät kalat\nkoreihin mutta viskasivat huonot pois.',13,NULL),
 (276,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',49,'9Samoin\nkäy maailman lopussa: enkelit tulevat, erottavat\npahat vanhurskaista',13,NULL),
 (277,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',50,'0ja heittävät heidät tuliseen\npätsiin. Siellä itketään ja kiristellään hampaita.\"      Jeesus sanoi heille: \"Missään ei profeetta ole niin\nväheksytty kuin kotikaupungissaan ja omassa\nkodissaan.\"',13,NULL),
 (278,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',58,'8Ja koska he eivät uskoneet häneen,\nhän ei tehnyt siellä montakaan voimatekoa. ',13,NULL),
 (279,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',1,'Noihin aikoihin neljännesruhtinas Herodes*kuuli\nJeesuksesta.',14,NULL),
 (280,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',2,'Hän sanoi hovimiehilleen: \"Se on\nJohannes Kastaja. Hän on herännyt kuolleista, siksi\nhänessä vaikuttavat nuo voimat.\" ',14,NULL),
 (281,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',3,'Herodes näet oli pidättänyt Johanneksen, pannut\nhänet kahleisiin ja heittänyt vankilaan. Tämän hän\noli tehnyt veljensä Filippoksen vaimon Herodiaan\ntähden,',14,NULL),
 (282,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',4,'sillä Johannes oli sanonut hänelle:\n\"Sinun ei ole lupa elää hänen kanssaan.\"',14,NULL),
 (283,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',5,'Herodes\nolisi halunnut surmata Johanneksen mutta pelkäsi\nkansaa, joka piti Johannesta profeettana. ',14,NULL),
 (284,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',6,'Sitten Herodes vietti syntymäpäiviään ja\nHerodiaan tytär tanssi vieraille. Tyttö miellytti\nHerodesta niin,',14,NULL),
 (285,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',7,'että tämä valalla vannoen lupasi\nantaa hänelle mitä hän vain pyytäisi.',14,NULL),
 (286,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',8,'Äitinsä\nyllytyksestä tyttö sanoi: \"Anna minulle nyt heti\nvadilla Johannes Kastajan pää.\"',14,NULL),
 (287,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',9,'Kuningas tuli\npahoille mielin, mutta koska hän oli vieraittensa\nkuullen vannonut valan, hän käski täyttää pyynnön.',14,NULL),
 (288,'Anonymous','2008-10-17 16:31:51','Anonymous','2008-10-17 16:31:51',10,'0Hän lähetti vankilaan sanan ja käski mestata\nJohanneksen.',14,NULL),
 (289,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',11,'1Tämän pää tuotiin vadilla ja\nannettiin tytölle, ja tyttö vei sen äidilleen. ',14,NULL),
 (290,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',12,'2Johanneksen opetuslapset tulivat sitten hakemaan\nruumiin ja hautasivat sen. Sen jälkeen he menivät\nkertomaan Jeesukselle, mitä oli tapahtunut.   ',14,NULL),
 (291,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',15,'5Illan suussa opetuslapset menivät hänen luokseen\nja sanoivat: \"Tämä on asumatonta seutua, ja päivä on\njo pitkällä. Lähetä ihmiset pois, että he menisivät\nkyliin ostamaan itselleen ruokaa.\"',14,NULL),
 (292,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',16,'6Mutta Jeesus\nsanoi: \"Ei heidän tarvitse mihinkään mennä. Antakaa\nte heille syötävää.\"',14,NULL),
 (293,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',17,'7\"Ei meillä ole mukana muuta\nkuin viisi leipää ja kaksi kalaa\", he vastasivat.',14,NULL),
 (294,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',18,'8\"Tuokaa ne tänne minulle\", sanoi Jeesus.',14,NULL),
 (295,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',19,'9Hän\nkäski väkijoukon asettua nurmelle. Sitten hän otti\nne viisi leipää ja kaksi kalaa, katsoi ylös\ntaivaaseen ja lausui kiitoksen. Hän mursi leivät ja\nantoi palat opetuslapsille, ja opetuslapset jakoivat\nne väelle.',14,NULL),
 (296,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',20,'0Kaikki söivät kyllikseen, ja\ntähteeksi jääneitä paloja kerättiin kaksitoista\ntäyttä korillista.',14,NULL),
 (297,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',21,'1Syömässä oli noin\nviisituhatta miestä ja lisäksi naisia ja lapsia.   ',14,NULL),
 (298,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',25,'5Neljännen yövartion*aikaan Jeesus tuli\nopetuslapsia kohti kävellen vettä pitkin.',14,NULL),
 (299,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',26,'6Kun he\nnäkivät hänen kävelevän järven aalloilla, he\nsäikähtivät ja huusivat pelosta, sillä he luulivat\nnäkevänsä aaveen.',14,NULL),
 (300,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',27,'7Mutta samassa Jeesus jo puhui\nheille: \"Pysykää rauhallisina, minä tässä olen.\nÄlkää pelätkö.\"',14,NULL),
 (301,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',28,'8Silloin Pietari sanoi hänelle:\n\"Herra, jos se olet sinä, niin käske minun tulla\nluoksesi vettä pitkin.\"',14,NULL),
 (302,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',29,'9\"Tule!\" sanoi Jeesus.\nPietari astui veneestä ja käveli vettä pitkin\nJeesuksen luo.',14,NULL),
 (303,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',30,'0Mutta huomatessaan, miten rajusti\ntuuli, hän pelästyi ja alkoi vajota. \"Herra, pelasta\nminut!\" hän huusi.',14,NULL),
 (304,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',31,'1Jeesus ojensi heti kätensä,\ntarttui häneen ja sanoi: \"Vähäinenpä on uskosi!\nMiksi aloit epäillä?\" ',14,NULL),
 (305,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',32,'2Kun he olivat nousseet veneeseen, tuuli tyyntyi.',14,NULL),
 (306,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',33,'3Ja kaikki, jotka veneessä olivat, polvistuivat\nhänen eteensä ja sanoivat: \"Sinä olet todella\nJumalan Poika.\"   ',14,NULL),
 (307,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',1,'Silloin Jeesuksen luo tuli Jerusalemista\nfariseuksia ja lainopettajia.',15,NULL),
 (308,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',2,'\"Miksi sinun\nopetuslapsesi rikkovat isien perinnäissääntöjä?\" he\nkysyivät. \"Hehän eivät pese käsiään, kun rupeavat\nsyömään.\" ',15,NULL),
 (309,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',3,'Jeesus vastasi heille: \"Miksi te itse rikotte\nJumalan käskyä perinnäissäännöillänne?',15,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (310,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',4,'Jumala on\nsanonut: \'Kunnioita isääsi ja äitiäsi\' ja \'Joka\nkiroaa isäänsä tai äitiään, häntä rangaistakoon\nkuolemalla\'.',15,NULL),
 (311,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',5,'Mutta te opetatte, että jos joku\nsanoo isälleen tai äidilleen: \'Sen, mitä sinun piti\nminulta saada, olen luvannut uhrilahjaksi\',',15,NULL),
 (312,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',6,'hänen\nei enää tarvitse kunnioittaa isäänsä tai äitiänsä.\nNäin te olette perinnäissäännöllänne tehneet\ntyhjäksi Jumalan sanan.',15,NULL),
 (313,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',7,'Te tekopyhät! Oikein\nJesaja teistä ennusti, kun hän sanoi:    ',15,NULL),
 (314,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',12,'2Silloin opetuslapset tulivat hänen luokseen ja\nsanoivat: \"Tiedätkö, että fariseukset suuttuivat\nsinuun, kun puhuit noin?\"',15,NULL),
 (315,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',13,'3Hän vastasi: \"Jokainen\nistutus, joka ei ole minun taivaallisen Isäni\nistuttama, revitään juurineen pois.',15,NULL),
 (316,'Anonymous','2008-10-17 16:31:52','Anonymous','2008-10-17 16:31:52',14,'4Älkää\nvälittäkö heistä, he ovat sokeita sokeiden\ntaluttajia. Ja kun sokea taluttaa toista sokeaa,\nmolemmat putoavat kuoppaan.\" ',15,NULL),
 (317,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',15,'5Pietari sanoi hänelle: \"Puhuit vertauskuvin.\nSelitä meille, mitä tarkoitit.\"',15,NULL),
 (318,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',16,'6Jeesus vastasi:\n\"Oletteko tekin yhä noin ymmärtämättömiä?',15,NULL),
 (319,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',17,'7Ettekö\nkäsitä, että se, mikä menee suusta sisään, jatkaa\nkulkuaan vatsaan, ja sitten se ulostetaan.',15,NULL),
 (320,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',18,'8Mutta\nse, mikä tulee suusta ulos, on lähtöisin sydämestä,\nja se saastuttaa ihmisen.',15,NULL),
 (321,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',19,'9Juuri sydämestähän\nlähtevät pahat ajatukset, murhat, aviorikokset,\nsiveettömyys, varkaudet, väärät todistukset ja\nherjaukset.',15,NULL),
 (322,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',20,'0Nämä ne ihmisen saastuttavat, ei se,\nettä syö pesemättömin käsin.\"   Opetuslapset tulivat Jeesuksen luo ja pyysivät: \"Tee\nhänelle jotakin. Hän kulkee perässämme ja huutaa.\"',15,NULL),
 (323,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',24,'4Mutta Jeesus vastasi: \"Ei minua ole lähetetty\nmuita kuin Israelin kansan kadonneita lampaita\nvarten.\"',15,NULL),
 (324,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',25,'5Silti nainen tuli lähemmäs, heittäytyi\nmaahan Jeesuksen eteen ja sanoi: \"Herra, auta\nminua!\"',15,NULL),
 (325,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',26,'6Mutta Jeesus sanoi hänelle: \"Ei ole\noikein ottaa lapsilta leipä ja heittää se\nkoiranpenikoille.\"',15,NULL),
 (326,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',27,'7\"Ei olekaan, Herra\", vastasi\nnainen, \"mutta saavathan koiratkin syödä isäntänsä\npöydältä putoilevia palasia.\"',15,NULL),
 (327,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',28,'8Silloin Jeesus\nsanoi hänelle: \"Suuri on sinun uskosi, nainen!\nTapahtukoon niin kuin tahdot.\" Siitä hetkestä tytär\noli terve.     ',15,NULL),
 (328,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',35,'5Jeesus käski väen asettua istumaan.',15,NULL),
 (329,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',36,'6Sitten\nhän otti ne seitsemän leipää ja kalat, lausui\nkiitoksen ja mursi leivät ja antoi palat\nopetuslapsille, jotka jakoivat ne ihmisille.',15,NULL),
 (330,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',37,'7Kaikki söivät kyllikseen, ja tähteeksi jääneitä\npaloja kerättiin seitsemän täyttä korillista.',15,NULL),
 (331,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',38,'8Syömässä oli neljätuhatta miestä ja lisäksi\nnaisia ja lapsia. ',15,NULL),
 (332,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',39,'9Sitten Jeesus lähetti väkijoukon pois, astui\nveneeseen ja tuli Magadanin*seudulle. ',15,NULL),
 (333,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',1,'Jeesuksen luo tuli fariseuksia ja saddukeuksia,\njotka halusivat panna hänet koetukselle ja pyysivät\nhäntä näyttämään merkin taivaasta.',16,NULL),
 (334,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',2,'Mutta Jeesus\nvastasi heille: \"Illalla te sanotte: \'Tulee kaunis\nilma, kun taivas ruskottaa\',',16,NULL),
 (335,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',3,'ja aamulla: \'Tänään\ntulee ruma ilma, sillä taivas on synkän ruskottava.\'\nTaivasta te kyllä osaatte lukea, mutta ette aikojen\nmerkkejä.',16,NULL),
 (336,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',4,'Tämä paha ja uskoton sukupolvi vaatii\nmerkkiä, mutta ainoa merkki, joka sille annetaan, on\nJoonan merkki.\" Hän jätti heidät siihen ja lähti\npois.     ',16,NULL),
 (337,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',17,'7Jeesus sanoi hänelle: \"Autuas olet sinä, Simon,\nJoonan poika. Tätä ei sinulle ole ilmoittanut liha\neikä veri, vaan minun Isäni, joka on taivaissa.',16,NULL),
 (338,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',18,'8Ja minä sanon sinulle: Sinä olet Pietari*, ja\ntälle kalliolle minä rakennan kirkkoni. Sitä eivät\ntuonelan portit voita.',16,NULL),
 (339,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',19,'9Minä olen antava sinulle\ntaivasten valtakunnan avaimet. Minkä sinä sidot maan\npäällä, se on sidottu taivaissa, ja minkä sinä\nvapautat maan päällä, se on myös taivaissa\nvapautettu.\"',16,NULL),
 (340,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',20,'0Sitten hän kielsi opetuslapsiaan\nkertomasta kenellekään, että hän oli Messias.   ',16,NULL),
 (341,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',22,'2Pietari veti hänet erilleen ja alkoi nuhdella\nhäntä: \"Jumala varjelkoon! Sitä ei saa tapahtua\nsinulle, Herra!\"',16,NULL),
 (342,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',23,'3Mutta hän kääntyi pois ja sanoi\nPietarille: \"Väisty tieltäni, Saatana! Sinä tahdot\nsaada minut lankeamaan. Sinun ajatuksesi eivät ole\nJumalasta, vaan ihmisestä!\"   ',16,NULL),
 (343,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',1,'Kuuden päivän kuluttua Jeesus otti mukaansa\nPietarin sekä Jaakobin ja tämän veljen Johanneksen\nja vei heidät korkealle vuorelle yksinäisyyteen.',17,NULL),
 (344,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',2,'Siellä hänen ulkomuotonsa muuttui heidän\nnähtensä: hänen kasvonsa loistivat kuin aurinko ja\nhänen vaatteensa tulivat valkeiksi kuin valo. ',17,NULL),
 (345,'Anonymous','2008-10-17 16:31:53','Anonymous','2008-10-17 16:31:53',3,'Samassa heille ilmestyivät Mooses ja Elia, jotka\nkeskustelivat Jeesuksen kanssa.',17,NULL),
 (346,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',4,'Pietari puuttui\npuheeseen ja sanoi Jeesukselle: \"Herra, on hyvä,\nettä me olemme täällä. Jos tahdot, teen tänne kolme\nmajaa: sinulle ja Moosekselle ja Elialle.\" ',17,NULL),
 (347,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',5,'Pietarin vielä puhuessa loistava pilvi verhosi\nheidät ja pilvestä kuului ääni: \"Tämä on minun rakas\nPoikani, johon minä olen mieltynyt. Kuulkaa häntä!\"',17,NULL),
 (348,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',6,'Kun opetuslapset kuulivat äänen, he heittäytyivät\nmaahan kasvoilleen suuren pelon vallassa.',17,NULL),
 (349,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',7,'Mutta\nJeesus tuli heidän luokseen, kosketti heitä ja\nsanoi: \"Nouskaa, älkää pelätkö.\"',17,NULL),
 (350,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',8,'Ja kun he\nnostivat katseensa, he eivät nähneet ketään muuta\nkuin Jeesuksen yksin.     ',17,NULL),
 (351,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',19,'9Kun he olivat taas keskenään, opetuslapset\nkysyivät Jeesukselta: \"Miksi me emme kyenneet\najamaan sitä henkeä pojasta?\"',17,NULL),
 (352,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',20,'0\"Koska teillä on\nniin vähän uskoa\", vastasi Jeesus. \"Totisesti: jos\nteillä olisi uskoa edes sinapinsiemenen verran, te\nvoisitte sanoa tälle vuorelle: \'Siirry täältä\ntuonne\', ja se siirtyisi. Mikään ei olisi teille\nmahdotonta. [',17,NULL),
 (353,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',21,'1Tämä laji ei lähde muulla kuin\nrukouksella ja paastolla.\"]*      ',17,NULL),
 (354,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',1,'Kohta sen jälkeen opetuslapset tulivat Jeesuksen\nluo ja kysyivät: \"Kuka on suurin taivasten\nvaltakunnassa?\" ',18,NULL),
 (355,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',2,'Silloin Jeesus kutsui luokseen lapsen, asetti\nhänet heidän keskelleen',18,NULL),
 (356,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',3,'ja sanoi: \"Totisesti: ellette käänny ja tule lasten\nkaltaisiksi, te ette pääse taivasten valtakuntaan.',18,NULL),
 (357,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',4,'Se, joka nöyrtyy tämän lapsen kaltaiseksi, on\nsuurin taivasten valtakunnassa.',18,NULL),
 (358,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',5,'Ja joka minun\nnimessäni ottaa luokseen yhdenkin tällaisen lapsen,\nse ottaa luokseen minut.',18,NULL),
 (359,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',6,'Mutta jos joku johdattaa\nlankeemukseen yhdenkin näistä vähäisistä, jotka\nuskovat minuun, hänelle olisi parempi, että hänen\nkaulaansa pantaisiin myllynkivi ja hänet\nupotettaisiin meren syvyyteen.   ',18,NULL),
 (360,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',10,'0\"Katsokaa, ettette halveksi yhtäkään näistä\nvähäisistä. Sillä minä sanon teille: heidän\nenkelinsä saavat taivaissa joka hetki katsella minun\ntaivaallisen Isäni kasvoja. [',18,NULL),
 (361,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',11,'1Juuri sitä, mikä on\nkadonnut, Ihmisen Poika on tullut etsimään ja\npelastamaan.]*     ',18,NULL),
 (362,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',19,'9\"Minä sanon teille: mitä tahansa asiaa kaksi\nteistä yhdessä sopien maan päällä rukoilee, sen he\nsaavat minun Isältäni, joka on taivaissa.',18,NULL),
 (363,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',20,'0Sillä\nmissä kaksi tai kolme on koolla minun nimessäni,\nsiellä minä olen heidän keskellään.\"   ',18,NULL),
 (364,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',22,'2\"Ei seitsemän, vaan seitsemänkymmentäseitsemän\nkertaa\", vastasi Jeesus. ',18,NULL),
 (365,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',23,'3\"Taivasten valtakunta on kuin kuningas, joka\nvaati palvelijoiltaan tilitykset.',18,NULL),
 (366,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',24,'4Kun hän alkoi\ntarkastaa niitä, hänen eteensä tuotiin palvelija,\njoka oli hänelle velkaa kymmenentuhatta talenttia*.',18,NULL),
 (367,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',25,'5Miehellä ei ollut, millä maksaa, ja niin\nkuningas määräsi, että hänet, hänen vaimonsa ja\nlapsensa ja koko hänen omaisuutensa oli myytävä ja\nvelka maksettava.',18,NULL),
 (368,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',26,'6Silloin palvelija heittäytyi\nmaahan hänen eteensä ja pyysi: \'Ole kärsivällinen!\nMinä maksan sinulle kyllä kaiken.\'',18,NULL),
 (369,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',27,'7Kuninkaan\ntuli sääli palvelijaansa, ja hän päästi miehen\nmenemään ja antoi velan anteeksi. ',18,NULL),
 (370,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',28,'8\"Mutta kun palvelija meni ulos, hän tapasi\ntoisen palvelijan, joka oli hänelle velkaa sata\ndenaaria*. Hän kävi mieheen käsiksi, kuristi häntä\nkurkusta ja sanoi: \'Maksa, mitä olet velkaa!\'',18,NULL),
 (371,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',29,'9Mies heittäytyi maahan ja pyysi: \'Ole\nkärsivällinen! Kyllä minä maksan sinulle.\'',18,NULL),
 (372,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',30,'0Mutta\ntoinen ei suostunut siihen. Hän meni ja toimitti\ntyötoverinsa vankilaan, kunnes tämä maksaisi\nvelkansa. ',18,NULL),
 (373,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',31,'1\"Muut palvelijat näkivät, mitä tapahtui, ja\npanivat sen pahakseen. He menivät kuninkaan luo ja\nkertoivat hänelle kaiken.',18,NULL),
 (374,'Anonymous','2008-10-17 16:31:54','Anonymous','2008-10-17 16:31:54',32,'2Silloin kuningas\nkutsutti palvelijan luokseen ja sanoi: \'Sinä\nkelvoton! Minä annoin sinulle anteeksi koko velan,\nkun sitä minulta pyysit.',18,NULL),
 (375,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',33,'3Eikö sinunkin olisi\npitänyt armahtaa työtoveriasi, niin kuin minä\narmahdin sinua?\'',18,NULL),
 (376,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',34,'4Vihoissaan kuningas pani\npalvelijansa ankaraan vankeuteen, kunnes tämä\nmaksaisi koko velan. ',18,NULL),
 (377,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',35,'5\"Näin tekee minun taivaallinen Isänikin teille,\njos te ette kaikesta sydämestä anna kukin\nveljellenne anteeksi.\"  ',18,NULL),
 (378,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',1,'Kun Jeesus oli puhunut kaiken tämän, hän lähti\nGalileasta ja tuli sitten Jordanin itäpuolta kulkien\nJuudeaan.',19,NULL),
 (379,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',2,'Häntä seurasi suuri joukko ihmisiä, ja\nhän paransi heitä. ',19,NULL),
 (380,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',3,'Hänen luokseen tuli myös fariseuksia, jotka\nhalusivat panna hänet koetukselle ja kysyivät:\n\"Saako mies hylätä vaimonsa mistä syystä tahansa?\"',19,NULL),
 (381,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',4,'Hän vastasi heille: \"Ettekö ole lukeneet, että\nLuoja alun perin teki ihmisen mieheksi ja naiseksi?\"',19,NULL),
 (382,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',5,'Ja hän jatkoi: \"Sen tähden mies jättää isänsä ja\näitinsä ja liittyy vaimoonsa, niin että nämä kaksi\ntulevat yhdeksi lihaksi.',19,NULL),
 (383,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',6,'He eivät siis enää ole\nkaksi, he ovat yksi. Ja minkä Jumala on yhdistänyt,\nsitä älköön ihminen erottako.\" ',19,NULL),
 (384,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',7,'Fariseukset kysyivät häneltä: \"Miksi sitten\nMooses on säätänyt, että jos mies hylkää vaimonsa,\nhänen on annettava tälle erokirja?\"',19,NULL),
 (385,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',8,'Hän vastasi:\n\"Mooses antoi teille luvan hylätä vaimonne, koska te\nolette niin kovasydämisiä. Mutta alun perin ei ollut\nniin.',19,NULL),
 (386,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',9,'Minä sanon teille: se, joka hylkää vaimonsa\nmuun syyn kuin haureuden tähden ja menee naimisiin\ntoisen kanssa, tekee aviorikoksen. [Aviorikoksen\ntekee myös se, joka nai miehensä hylkäämän\nnaisen.\"]* ',19,NULL),
 (387,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',10,'0Opetuslapset sanoivat: \"Jos avioliitto merkitsee\nmiehelle tätä, on parempi olla menemättä naimisiin.\"',19,NULL),
 (388,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',11,'1Mutta hän sanoi heille: \"Se ratkaisu ei sovellu\nkaikille, ainoastaan niille, joille se osa on\nannettu.',19,NULL),
 (389,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',12,'2On sellaisia, jotka äitinsä kohdusta\nsaakka ovat avioliittoon kelpaamattomia, on toisia,\njoista ihmiset ovat tehneet sellaisia, ja on niitä,\njotka itse, taivasten valtakunnan tähden, ovat\nottaneet osakseen naimattomuuden. Joka voi valita\ntämän ratkaisun, valitkoon.\"     ',19,NULL),
 (390,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',23,'3Silloin Jeesus sanoi opetuslapsilleen:\n\"Totisesti: rikkaan on vaikea päästä taivasten\nvaltakuntaan.',19,NULL),
 (391,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',24,'4Minä sanon teille: helpompi on\nkamelin mennä neulansilmästä kuin rikkaan päästä\nJumalan valtakuntaan.\"',19,NULL),
 (392,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',25,'5Kun opetuslapset kuulivat\ntämän, he olivat ihmeissään ja kysyivät: \"Kuka\nsitten voi pelastua?\"',19,NULL),
 (393,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',26,'6Jeesus katsoi heihin ja\nsanoi: \"Ihmiselle se on mahdotonta, mutta Jumalalle\non kaikki mahdollista.\"   ',19,NULL),
 (394,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',28,'8Jeesus sanoi heille: \"Totisesti: kun Ihmisen Poika uuden maailman\nsyntyessä istuutuu kirkkautensa valtaistuimelle,\nsilloin tekin, jotka olette seuranneet minua, saatte\nistua kahdellatoista valtaistuimella ja hallita\nIsraelin kahtatoista heimoa.',19,NULL),
 (395,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',29,'9Ja jokainen, joka\nminun nimeni tähden on luopunut talostaan,\nveljistään tai sisaristaan, isästään, äidistään tai\nlapsistaan tai pelloistaan, saa satakertaisesti\ntakaisin ja perii iankaikkisen elämän.',19,NULL),
 (396,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',30,'0Mutta\nmonet ensimmäiset tulevat olemaan viimeisiä ja\nviimeiset ensimmäisiä. ',19,NULL),
 (397,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',1,'\"Taivasten valtakuntaa voi verrata isäntään, joka\naamuvarhaisella lähti palkkaamaan työmiehiä\nviinitarhaansa.',20,NULL),
 (398,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',2,'Hän sopi miesten kanssa yhden\ndenaarin päiväpalkasta ja lähetti heidät\nviinitarhaan.',20,NULL),
 (399,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',3,'Päivän kolmannella tunnilla*hän\nlähti taas ulos ja näki, että torilla seisoi vielä\nmiehiä jouten.',20,NULL),
 (400,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',4,'\'Menkää tekin viinitarhaan\', hän\nsanoi heille, \'minä maksan teille sen, mitä kuuluu\nmaksaa.\'',20,NULL),
 (401,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',5,'Miehet lähtivät. Kuudennen ja\nyhdeksännen tunnin aikaan isäntä lähti taas ulos ja\nteki samoin.',20,NULL),
 (402,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',6,'Kun hän sitten meni ulos\nyhdennellätoista tunnilla, hän näki vieläkin\nmuutamia joutilaita ja kysyi heiltä: \'Miksi te\nseisotte täällä kaiken päivää toimettomina?\'',20,NULL),
 (403,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',7,'\'Kukaan ei ole palkannut meitä\', he vastasivat.\nHän sanoi miehille: \'Menkää tekin minun\nviinitarhaani.\' ',20,NULL),
 (404,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',8,'\"Kun sitten tuli ilta, viinitarhan omistaja sanoi\ntilanhoitajalleen: \'Kutsu työmiehet ja maksa heille\npalkka, viimeksi tulleille ensin ja ensimmäisille\nvasta sitten.\'',20,NULL),
 (405,'Anonymous','2008-10-17 16:31:55','Anonymous','2008-10-17 16:31:55',9,'Ne, jotka oli palkattu\nyhdennellätoista tunnilla, tulivat ja saivat kukin\ndenaarinsa.',20,NULL),
 (406,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',10,'0Kun ensiksi palkatut tulivat, he\nluulivat saavansa enemmän, mutta hekin saivat vain\ndenaarin.',20,NULL),
 (407,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',11,'1Silloin he nostivat metelin ja\nsanoivat isännälle:',20,NULL),
 (408,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',12,'2\'Nämä viimeksi tulleet\ntekivät työtä yhden ainoan tunnin, ja silti sinä\nannat heille saman kuin meille, jotka olemme\nkantaneet päivän kuorman ja helteen.\'',20,NULL),
 (409,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',13,'3Mutta\nisäntä sanoi yhdelle miehistä: \'Ystäväni, enhän minä\ntee sinulle vääryyttä. Emmekö me sopineet\ndenaarista?',20,NULL),
 (410,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',14,'4Ota omasi ja mene. Minä tahdon\nmaksaa tälle viimeksi tulleelle saman kuin sinulle,',20,NULL),
 (411,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',15,'5ja kai minä saan omallani tehdä mitä haluan?\nKatsotko sinä karsaasti sitä, että minä olen hyvä?\' ',20,NULL),
 (412,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',16,'6\"Näin viimeiset tulevat ensimmäisiksi ja\nensimmäiset viimeisiksi.\"     ',20,NULL),
 (413,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',24,'4Kun muut kymmenen kuulivat tästä, he suuttuivat\nveljeksille.',20,NULL),
 (414,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',25,'5Mutta Jeesus kutsui heidät luokseen\nja sanoi: \"Te tiedätte, että hallitsijat ovat\nkansojensa herroja ja maan mahtavat pitävät kansoja\nvaltansa alla.',20,NULL),
 (415,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',26,'6Niin ei saa olla teidän\nkeskuudessanne. Joka tahtoo teidän joukossanne tulla\nsuureksi, se olkoon toisten palvelija,',20,NULL),
 (416,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',27,'7ja joka\ntahtoo tulla teidän joukossanne ensimmäiseksi, se\nolkoon toisten orja.',20,NULL),
 (417,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',28,'8Ei Ihmisen Poikakaan tullut\npalveltavaksi, vaan palvelemaan ja antamaan henkensä\nlunnaiksi kaikkien puolesta.\"    ',20,NULL),
 (418,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',1,'Kun he lähestyivät Jerusalemia ja olivat tulossa\nBetfageen Öljymäelle, Jeesus lähetti edeltä kaksi\nopetuslastaan',21,NULL),
 (419,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',2,'ja sanoi heille: \"Menkää tuolla\nnäkyvään kylään. Siellä on aasintamma kiinni\nsidottuna ja varsa sen vierellä; löydätte ne heti.\nOttakaa ne siitä ja tuokaa minulle.',21,NULL),
 (420,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',3,'Jos joku\nsanoo teille jotakin, vastatkaa, että Herra\ntarvitsee niitä mutta palauttaa ne pian.\" ',21,NULL),
 (421,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',4,'Näin tapahtui, jotta kävisi toteen tämä profeetan\nsana:   ',21,NULL),
 (422,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',6,'Opetuslapset lähtivät ja tekivät niin kuin Jeesus\noli käskenyt.',21,NULL),
 (423,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',7,'He toivat aasin ja varsan ja\npanivat niiden selkään vaatteitaan, ja Jeesus\nistuutui aasin selkään.',21,NULL),
 (424,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',8,'Ihmisiä oli hyvin paljon,\nja he levittivät vaatteitaan tielle, toiset\nkatkoivat puista oksia ja levittivät tielle niitä.',21,NULL),
 (425,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',9,'Ja ihmisjoukko, joka kulki hänen edellään ja\nperässään, huusi:   ',21,NULL),
 (426,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',10,'0Kun hän tuli Jerusalemiin, koko kaupunki alkoi\nkuohua ja kaikki kyselivät: \"Kuka hän on?\"',21,NULL),
 (427,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',11,'1Ihmiset sanoivat: \"Hän on Jeesus, profeetta\nGalilean Nasaretista.\"   ',21,NULL),
 (428,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',14,'4Jeesuksen luo temppeliin tuli sokeita ja\nrampoja, ja hän paransi heidät.',21,NULL),
 (429,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',15,'5Mutta kun\nylipapit ja lainopettajat näkivät, mitä kaikkea\nhämmästyttävää hän teki, ja kuulivat lasten huutavan\ntemppelissä: \"Hoosianna, Daavidin Poika!\", he\nsuuttuivat',21,NULL),
 (430,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',16,'6ja sanoivat hänelle: \"Kuuletko, mitä\nnuo huutavat?\" \"Kuulen\", vastasi Jeesus. \"Ettekö ole\nkoskaan lukeneet tätä sanaa: \'Lasten ja imeväisten\nsuusta sinä olet hankkinut kiitoksesi\'?\"',21,NULL),
 (431,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',17,'7Hän\njätti heidät siihen, meni kaupungin ulkopuolelle\nBetaniaan ja oli siellä yötä.   ',21,NULL),
 (432,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',20,'0Kun opetuslapset sen näkivät, he hämmästyivät ja\nsanoivat: \"Kuinka tuo puu noin äkkiä kuivettui?\"',21,NULL),
 (433,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',21,'1Jeesus vastasi: \"Totisesti: jos teillä olisi\nuskoa ettekä epäilisi, te ette ainoastaan tekisi\ntätä viikunapuulle, vaan te voisitte sanoa tälle\nvuorellekin: \'Nouse paikaltasi ja paiskaudu mereen\',\nja niin tapahtuisi.',21,NULL),
 (434,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',22,'2Mitä tahansa te uskossa\nrukoillen pyydätte, sen te saatte.\"   He neuvottelivat keskenään: \"Jos sanomme:\n\'Taivaasta\', hän sanoo meille: \'Miksi sitten ette\nuskoneet häntä?\'',21,NULL),
 (435,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',26,'6Jos taas sanomme: \'Ihmisistä\',\nsaamme pelätä kansaa, sillä kaikki pitävät\nJohannesta profeettana.\"',21,NULL),
 (436,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',27,'7Niin he vastasivat\nJeesukselle: \"Emme tiedä.\" Silloin Jeesus sanoi: \"En\nminäkään sitten sano teille, millä valtuuksilla minä\nkaikkea tätä teen.\"   ',21,NULL),
 (437,'Anonymous','2008-10-17 16:31:56','Anonymous','2008-10-17 16:31:56',30,'0\"Isä meni toisen pojan luo ja sanoi tälle saman.\nPoika vastasi: \'Menen kyllä, isä\', mutta ei\nmennytkään.',21,NULL),
 (438,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',31,'1Kumpi näistä kahdesta teki, mitä\nhänen isänsä tahtoi?\" \"Edellinen\", he vastasivat. Jeesus sanoi: \"Totisesti: portot ja publikaanit menevät Jumalan\nvaltakuntaan ennemmin kuin te.',21,NULL),
 (439,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',32,'2Johannes avasi\nteille vanhurskauden tien, mutta te ette uskoneet\nhäntä. Portot ja publikaanit sen sijaan uskoivat, ja\nvaikka te sen näitte, te ette jälkeenpäinkään\ntulleet katumukseen ettekä uskoneet häntä.   ',21,NULL),
 (440,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',34,'4\"Kun korjuuaika oli käsillä, hän lähetti\npalvelijoitaan perimään viinitarhan viljelijöiltä\nhänelle kuuluvan sadon.',21,NULL),
 (441,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',35,'5Mutta nämä ottivat\npalvelijat kiinni. Jonkun he pieksivät, jonkun\niskivät kuoliaaksi, jonkun kivittivät.',21,NULL),
 (442,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',36,'6Isäntä\nlähetti uusia palvelijoita, enemmän kuin\nensimmäisellä kerralla, mutta heidän kävi samalla\ntavoin.',21,NULL),
 (443,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',37,'7Lopulta hän lähetti vuokraajien luo\npoikansa ajatellen: \'Minun omaan poikaani he eivät\nsentään uskalla koskea.\'',21,NULL),
 (444,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',38,'8Mutta kun nämä näkivät\npojan, he sanoivat toisilleen: \'Hän on perillinen.\nTapetaan hänet, niin perintö on meidän.\'',21,NULL),
 (445,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',39,'9He\nottivat hänet kiinni, raahasivat ulos viinitarhasta\nja tappoivat hänet. ',21,NULL),
 (446,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',40,'0\"Kun viinitarhan omistaja tulee, mitä hän tekee\nnoille viljelijöille?\" ',21,NULL),
 (447,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',41,'1He vastasivat: \"Hän antaa noille pahoille pahan\nlopun ja vuokraa tarhan toisille viljelijöille,\njotka toimittavat hänelle kuuluvan sadon\nmääräaikaan.\" ',21,NULL),
 (448,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',42,'2Jeesus sanoi heille: \"Ettekö ole koskaan kirjoituksista lukeneet:   ',21,NULL),
 (449,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',43,'3\"Tämän takia minä sanon teille, että Jumalan\nvaltakunta otetaan teiltä pois ja annetaan kansalle,\njoka tekee sen hedelmiä.',21,NULL),
 (450,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',44,'4Joka tähän kiveen\nkaatuu, se ruhjoutuu, ja jonka päälle tämä kivi\nkaatuu, sen se murskaa.\" ',21,NULL),
 (451,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',45,'5Nämä vertaukset kuultuaan ylipapit ja\nfariseukset ymmärsivät, että Jeesus puhui heistä.',21,NULL),
 (452,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',46,'6He olisivat halunneet ottaa hänet kiinni mutta\npelkäsivät kansaa, koska se piti häntä profeettana. ',21,NULL),
 (453,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',1,'Jeesus jatkoi vertauspuheitaan ja sanoi heille: ',22,NULL),
 (454,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',2,'\"Taivasten valtakuntaa voi verrata kuninkaaseen,\njoka valmisti häät pojalleen.',22,NULL),
 (455,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',3,'Hän lähetti\npalvelijoitaan kutsumaan häävieraita, mutta kutsun\nsaaneet eivät tahtoneet tulla.',22,NULL),
 (456,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',4,'Silloin hän\nlähetti toisia palvelijoita ja käski heidän sanoa\nkutsutuille: \'Olen valmistanut ateriani, härät ja\nsyöttövasikat on teurastettu, kaikki on valmiina.\nTulkaa häihin!\'',22,NULL),
 (457,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',5,'Mutta kutsun saaneista toiset\neivät välittäneet siitä, vaan menivät muualle, kuka\npellolleen, kuka kaupoilleen,',22,NULL),
 (458,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',6,'toiset taas ottivat\nkuninkaan palvelijat kiinni, pieksivät heitä ja\nlöivät heidät hengiltä.',22,NULL),
 (459,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',7,'Silloin kuningas\nvihastui. Hän lähetti sotajoukkonsa, surmasi\nmurhamiehet ja poltti heidän kaupunkinsa.',22,NULL),
 (460,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',8,'Sitten\nkuningas sanoi palvelijoilleen: \'Kaikki on valmiina\nhääjuhlaa varten, mutta kutsutut eivät olleet juhlan\narvoisia.',22,NULL),
 (461,'Anonymous','2008-10-17 16:31:57','Anonymous','2008-10-17 16:31:57',9,'Menkää nyt teille ja toreille ja\nkutsukaa häihin keitä vain tapaatte.\'',22,NULL),
 (462,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',10,'0Palvelijat\nmenivät ja keräsivät kaikki, jotka he tapasivat,\nniin pahat kuin hyvät, ja häähuone täyttyi aterialle\ntulleista. ',22,NULL),
 (463,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',11,'1\"Kun kuningas tuli sisään katsomaan\njuhlavieraitaan, hän näki siellä miehen, jolla ei\nollut hääpukua.',22,NULL),
 (464,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',12,'2Hän kysyi tältä: \'Ystäväni,\nkuinka saatoit tulla tänne ilman häävaatteita?\' Mies\nei saanut sanaa suustaan.',22,NULL),
 (465,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',13,'3Silloin kuningas sanoi\npalvelijoilleen: \'Sitokaa hänet käsistä ja jaloista\nja heittäkää ulos pimeyteen. Siellä itketään ja\nkiristellään hampaita.',22,NULL),
 (466,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',14,'4Monet ovat kutsuttuja,\nmutta harvat valittuja.\'\"     ',22,NULL),
 (467,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',29,'9Jeesus vastasi heille: \"Te kuljette eksyksissä,\nkoska ette tunne pyhiä kirjoituksia ettekä Jumalan\nvoimaa.',22,NULL),
 (468,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',30,'0Ei ylösnousemuksessa oteta vaimoa eikä\nmennä vaimoksi. Ylösnousseet ovat kuin enkelit\ntaivaassa.',22,NULL),
 (469,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',31,'1Mutta ylösnousemuksesta puheen ollen \n-- ettekö ole lukeneet, mitä Jumala on teille\nsanonut:',22,NULL),
 (470,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',32,'2\'Minä olen Abrahamin Jumala, Iisakin\nJumala ja Jaakobin Jumala\'? Ei hän ole kuolleiden\nJumala, vaan elävien.\"',22,NULL),
 (471,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',33,'3Kun väkijoukko kuuli\ntämän, se oli ihmeissään hänen opetuksestaan.      ',22,NULL),
 (472,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',45,'5Jos kerran Daavid sanoo Messiasta herraksi,\nkuinka Messias silloin voi olla hänen poikansa?\" ',22,NULL),
 (473,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',46,'6Kukaan ei pystynyt vastaamaan hänelle, eikä\nyksikään siitä päivästä lähtien enää rohjennut kysyä\nhäneltä mitään.  ',22,NULL),
 (474,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',1,'Sitten Jeesus puhui väkijoukolle ja\nopetuslapsilleen: ',23,NULL),
 (475,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',2,'\"Mooseksen istuin on nyt lainopettajien ja\nfariseusten hallussa.',23,NULL),
 (476,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',3,'Tehkää siis niin kuin he\nsanovat ja noudattakaa heidän opetustaan. Älkää\nkuitenkaan ottako oppia heidän teoistaan, sillä he\npuhuvat yhtä ja tekevät toista.',23,NULL),
 (477,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',4,'He köyttävät\nkokoon raskaita ja hankalia taakkoja ja sälyttävät\nne ihmisten kannettaviksi, mutta itse he eivät halua\nniitä sormellaankaan liikauttaa.',23,NULL),
 (478,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',5,'Kaiken minkä\ntekevät he tekevät vain siksi, että heidät\nhuomattaisiin. He käyttävät leveitä\nraamatunlausekoteloita ja panevat viittaansa isot\ntupsut,',23,NULL),
 (479,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',6,'he istuvat pidoissa mielellään\nkunniapaikalla ja synagogassa etumaisilla istuimilla',23,NULL),
 (480,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',7,'ja ovat hyvillään, kun ihmiset toreilla\ntervehtivät heitä ja kutsuvat heitä rabbiksi*. ',23,NULL),
 (481,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',8,'\"Älkää te antako kutsua itseänne rabbiksi, sillä\nteillä on vain yksi opettaja ja te olette kaikki\nveljiä.',23,NULL),
 (482,'Anonymous','2008-10-17 16:31:58','Anonymous','2008-10-17 16:31:58',9,'Älkää myöskään kutsuko isäksi ketään,\njoka on maan päällä, sillä vain yksi on teille isä,\nhän, joka on taivaissa.',23,NULL),
 (483,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',10,'0Älkää antako kutsua\nitseänne oppimestariksi, sillä teillä on vain yksi\nmestari, Kristus.',23,NULL),
 (484,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',11,'1Joka teistä on suurin, se\nolkoon toisten palvelija.',23,NULL),
 (485,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',12,'2Sillä joka itsensä\nkorottaa, se alennetaan, mutta joka itsensä alentaa,\nse korotetaan.   [',23,NULL),
 (486,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',14,'4\"Voi teitä, lainopettajat ja fariseukset! Te\nviette leskiltä talot ja latelette pitkiä\nrukouksianne vain näön vuoksi. Sitä ankarampi tulee\nolemaan teidän tuomionne.]* ',23,NULL),
 (487,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',15,'5\"Voi teitä, lainopettajat ja fariseukset! Te\nteeskentelijät! Te kierrätte maat ja meret\nhankkiaksenne edes yhden käännynnäisen, ja kun siinä\nonnistutte, teette hänestä helvetin oman, kahta\nvertaa pahemman kuin itse olette. ',23,NULL),
 (488,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',16,'6\"Voi teitä, te sokeat oppaat! Te sanotte: \'Jos\nvannoo temppelin kautta, se ei merkitse mitään,\nmutta jos vannoo temppelin kullan kautta, vala on\nsitova.\'',23,NULL),
 (489,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',17,'7Te sokeat typerykset! Kumpi on\ntärkeämpi, kulta vai temppeli, joka sen kullan\npyhittää?',23,NULL),
 (490,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',18,'8Te sanotte myös: \'Jos vannoo alttarin\nkautta, se ei merkitse mitään, mutta jos vannoo\nalttarille pannun uhrilahjan kautta, vala on\nsitova.\'',23,NULL),
 (491,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',19,'9Te sokeat! Kumpi on tärkeämpi,\nuhrilahja vai alttari, joka lahjan pyhittää?',23,NULL),
 (492,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',20,'0Se,\njoka vannoo alttarin kautta, vannoo sekä alttarin\nettä kaiken sen päällä olevan kautta,',23,NULL),
 (493,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',21,'1ja se,\njoka vannoo temppelin kautta, vannoo samalla hänen\nkauttaan, jonka asuinpaikka se on.',23,NULL),
 (494,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',22,'2Niin myös se,\njoka vannoo taivaan kautta, vannoo Jumalan\nvaltaistuimen kautta ja hänen kauttaan, joka sillä\nistuu. ',23,NULL),
 (495,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',23,'3\"Voi teitä, lainopettajat ja fariseukset! Te\nteeskentelijät! Te maksatte kymmenykset jopa\nmintusta, tillistä ja kuminasta, mutta laiminlyötte\nsen, mikä laissa on tärkeintä: oikeudenmukaisuuden,\nlaupeuden ja uskollisuuden. Näitä teidän pitäisi\nnoudattaa, noita muitakaan unohtamatta.',23,NULL),
 (496,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',24,'4Te\nsokeat oppaat! Hyttysen te siivilöitte, mutta\nnielaisette kamelin! ',23,NULL),
 (497,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',25,'5\"Voi teitä, lainopettajat ja fariseukset! Te\nteeskentelijät! Te puhdistatte maljanne ja vatinne\nulkopuolelta, mutta mitä niissä on sisällä: riistoa\nja hillittömyyttä!',23,NULL),
 (498,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',26,'6Sinä sokea fariseus, puhdista\nmalja ensin sisältä, muuten sitä ei saada\nulkopuoleltakaan puhtaaksi! ',23,NULL),
 (499,'Anonymous','2008-10-17 16:31:59','Anonymous','2008-10-17 16:31:59',27,'7\"Voi teitä, lainopettajat ja fariseukset! Te\nteeskentelijät! Te olette kuin kalkilla valkaistut\nhaudat. Ulkopuolelta ne kyllä ovat kauniita mutta\nsisältä täynnä kuolleiden luita ja kaikkea saastaa.',23,NULL),
 (500,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',28,'8Samalla tavoin tekin olette hurskaita\nulkonaisesti, ihmisten silmissä, mutta sisältä\ntäynnä teeskentelyä ja vääryyttä. ',23,NULL),
 (501,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',29,'9\"Voi teitä, lainopettajat ja fariseukset! Te\nteeskentelijät! Te rakennatte profeettojen haudoille\nmuistomerkkejä, te koristelette vanhurskaiden\nhautakiviä',23,NULL),
 (502,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',30,'0ja sanotte: \'Jos me olisimme eläneet\nisiemme aikana, emme olisi heidän kanssaan\ntahranneet käsiämme profeettojen vereen.\'',23,NULL),
 (503,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',31,'1Näin\nte kuitenkin itse tunnustatte olevanne niiden\njälkeläisiä, jotka murhasivat profeetat.',23,NULL),
 (504,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',32,'2Jatkakaa toki isienne tapaan, kunnes mitta on\ntäysi!',23,NULL),
 (505,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',33,'3Te käärmeet, te kyykäärmeitten sikiöt!\nMiten te voisitte välttää kadotustuomion? ',23,NULL),
 (506,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',34,'4\"Kuulkaa siis: Minä lähetän teidän luoksenne\nprofeettoja, viisaita miehiä ja lainopettajia.\nToiset heistä te tapatte ja ristiinnaulitsette,\ntoisia ruoskitte synagogissanne ja ajatte takaa\nkaupungista toiseen.',23,NULL),
 (507,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',35,'5Näin te tulette syypäiksi\nkaikkeen viattomaan vereen, joka maan päällä on\nvuodatettu, hurskaan Abelin verestä aina Sakarjan,\nBarakjan pojan, vereen asti, hänen, jonka te\ntapoitte temppelin ja alttarin välille.',23,NULL),
 (508,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',36,'6Totisesti: tämä sukupolvi joutuu vielä\nvastaamaan tästä kaikesta!    ',23,NULL),
 (509,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',1,'Kun Jeesus oli tullut ulos temppelistä ja oli\nlähdössä pois, opetuslapset tulivat hänen luokseen\nnäyttääkseen hänelle temppelialueen rakennuksia.',24,NULL),
 (510,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',2,'Mutta Jeesus sanoi heille: \"Näettekö kaiken\ntämän? Totisesti: kaikki revitään maahan, tänne ei\njää kiveä kiven päälle.\"   ',24,NULL),
 (511,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',4,'Jeesus vastasi: \"Varokaa, ettei kukaan johda teitä harhaan.',24,NULL),
 (512,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',5,'Monet\ntulevat esiintymään minun nimelläni. He sanovat:\n\'Minä olen Messias\', ja eksyttävät monia.',24,NULL),
 (513,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',6,'Te\nkuulette taistelun ääniä ja sanomia sodista, mutta\nälkää antako sen pelästyttää itseänne. Niin täytyy\nkäydä, mutta vielä ei loppu ole käsillä.',24,NULL),
 (514,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',7,'Kansa\nnousee kansaa vastaan ja valtakunta valtakuntaa\nvastaan, ja joka puolella on nälänhätää ja\nmaanjäristyksiä.',24,NULL),
 (515,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',8,'Mutta kaikki tämä on vasta\nsynnytystuskien alkua. ',24,NULL),
 (516,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',9,'\"Silloin teidät pannaan ahtaalle, monia teistä\nsurmataan ja kaikki kansat vihaavat teitä minun\nnimeni tähden.',24,NULL),
 (517,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',10,'0Monet silloin luopuvat, he\nkavaltavat toisensa ja vihaavat toinen toistaan.',24,NULL),
 (518,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',11,'1Monta väärää profeettaa ilmaantuu, ja he\njohtavat useita harhaan.',24,NULL),
 (519,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',12,'2Ja kun laittomuus\nlisääntyy, monien rakkaus kylmenee.',24,NULL),
 (520,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',13,'3Mutta joka\nkestää loppuun asti, pelastuu.',24,NULL),
 (521,'Anonymous','2008-10-17 16:32:00','Anonymous','2008-10-17 16:32:00',14,'4Tämä valtakunnan\nevankeliumi julistetaan kaikkialle maailmaan,\nkaikille kansoille todistukseksi, ja sitten tulee\nloppu.   ',24,NULL),
 (522,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',23,'3\"Jos joku silloin sanoo teille: \'Täällä on\nMessias\', tai: \'Messias on tuolla\', älkää uskoko.',24,NULL),
 (523,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',24,'4Sillä vääriä messiaita ja vääriä profeettoja\nilmaantuu, ja he tekevät suuria tunnustekoja ja\nihmeitä, niin että he johtavat, jos mahdollista,\nvalitutkin harhaan.',24,NULL),
 (524,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',25,'5Tämän minä olen teille nyt\nennalta ilmoittanut. ',24,NULL),
 (525,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',26,'6\"Jos siis teille sanotaan: \'Hän on tuolla\nautiomaassa\', älkää lähtekö sinne, tai jos sanotaan:\n\'Hän on sisällä talossa\', älkää uskoko sitä.',24,NULL),
 (526,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',27,'7Sillä niin kuin salama leimahtaa idässä ja\nvalaisee taivaan länteen asti, niin on oleva Ihmisen\nPojan tulo.',24,NULL),
 (527,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',28,'8Missä on raato, sinne kokoontuvat\nkorppikotkat.   ',24,NULL),
 (528,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',32,'2\"Ottakaa oppia vertauskuvasta, jonka viikunapuu\ntarjoaa: kun sen oksa virkoaa ja alkaa tulla\nlehteen, te tiedätte, että kesä on lähellä.',24,NULL),
 (529,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',33,'3Samalla tavoin te kaiken tämän nähdessänne\ntiedätte, että se aika on lähellä, aivan ovella.',24,NULL),
 (530,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',34,'4Totisesti: tämä sukupolvi ei katoa ennen kuin\nkaikki tämä tapahtuu.',24,NULL),
 (531,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',35,'5Taivas ja maa katoavat,\nmutta minun sanani eivät katoa. ',24,NULL),
 (532,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',36,'6\"Mutta sitä päivää ja hetkeä ei tiedä kukaan,\neivät taivaan enkelit eikä edes Poika, sen tietää\nvain Isä.',24,NULL),
 (533,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',37,'7Niin kuin kävi Nooan päivinä, niin on\nkäyvä silloinkin, kun Ihmisen Poika tulee.',24,NULL),
 (534,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',38,'8Vedenpaisumuksen edellä ihmiset söivät ja\njoivat, menivät naimisiin ja naittivat tyttäriään\naina siihen päivään asti, jona Nooa meni arkkiin.',24,NULL),
 (535,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',39,'9Kukaan ei aavistanut mitään, ennen kuin tulva\ntuli ja vei heidät kaikki mennessään. Samoin käy,\nkun Ihmisen Poika tulee.',24,NULL),
 (536,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',40,'0Kaksi miestä on\npellolla: toinen otetaan, toinen jätetään.',24,NULL),
 (537,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',41,'1Kaksi\nnaista on jauhamassa viljaa: toinen otetaan, toinen\njätetään. ',24,NULL),
 (538,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',42,'2\"Valvokaa siis, sillä te ette tiedä, minä\npäivänä teidän Herranne tulee.',24,NULL),
 (539,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',43,'3Ymmärrättehän,\nettä jos talon isäntä tietäisi, mihin aikaan yöstä\nvaras tulee, hän valvoisi eikä antaisi murtautua\ntaloonsa.',24,NULL),
 (540,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',44,'4Olkaa siis tekin valmiit, sillä\nIhmisen Poika tulee hetkellä, jota ette aavista.   ',24,NULL),
 (541,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',48,'8\"Mutta jos palvelija on kelvoton, hän ajattelee:\n\'Herrani ei tule vielä pitkään aikaan\',',24,NULL),
 (542,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',49,'9ja hän\nalkaa lyödä tovereitaan ja syö ja juo juoppojen\nseurassa.',24,NULL),
 (543,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',50,'0Mutta päivänä, jota tuo palvelija ei\narvaa, hetkenä, jota hän ei tiedä, hänen herransa\ntulee,',24,NULL),
 (544,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',51,'1hakkaa hänet kuoliaaksi ja tuomitsee\nhänet samaan paikkaan, jossa teeskentelijät ovat.\nSiellä itketään ja kiristellään hampaita. ',24,NULL),
 (545,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',1,'\"Silloin taivasten valtakunta on oleva tällainen.\nOli kymmenen morsiusneitoa, jotka ottivat lamppunsa\nja lähtivät sulhasta vastaan.',25,NULL),
 (546,'Anonymous','2008-10-17 16:32:01','Anonymous','2008-10-17 16:32:01',2,'Viisi heistä oli\ntyhmää ja viisi viisasta.',25,NULL),
 (547,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',3,'Tyhmät ottivat\nlamppunsa mutta eivät varanneet mukaansa öljyä.',25,NULL),
 (548,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',4,'Viisaat sitä vastoin ottivat lampun lisäksi\nmukaansa öljyastian.',25,NULL),
 (549,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',5,'Kun sulhanen viipyi, heitä\nkaikkia alkoi väsyttää ja he nukahtivat.',25,NULL),
 (550,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',6,'Mutta\nkeskellä yötä kuului huuto: \'Ylkä tulee! Menkää\nhäntä vastaan!\'',25,NULL),
 (551,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',7,'Silloin kaikki morsiusneidot\nheräsivät ja panivat lamppunsa kuntoon.',25,NULL),
 (552,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',8,'Tyhmät\nsanoivat viisaille: \'Antakaa meille vähän öljyä,\nmeidän lamppumme sammuvat.\'',25,NULL),
 (553,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',9,'Mutta viisaat\nvastasivat: \'Emme me voi, ei se riitä meille\nkaikille. Menkää ostamaan kauppiailta.\'',25,NULL),
 (554,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',10,'0Mutta\nkun he olivat ostamassa öljyä, sulhanen tuli. Ne,\njotka olivat valmiit, menivät hänen kanssaan\nhäätaloon, ja ovi suljettiin.',25,NULL),
 (555,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',11,'1Jonkin ajan\nkuluttua toisetkin saapuivat sinne ja huusivat:\n\'Herra, Herra, avaa meille!\'',25,NULL),
 (556,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',12,'2Mutta hän vastasi:\n\'Totisesti, minä en tunne teitä.\' ',25,NULL),
 (557,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',13,'3\"Valvokaa siis, sillä te ette tiedä päivää\nettekä hetkeä.   ',25,NULL),
 (558,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',16,'6\"Se, joka oli saanut viisi talenttia, ryhtyi\nheti toimeen: hän kävi niillä kauppaa ja hankki\nvoittoa toiset viisi talenttia.',25,NULL),
 (559,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',17,'7Samoin se, joka\noli saanut kaksi talenttia, voitti toiset kaksi.',25,NULL),
 (560,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',18,'8Mutta se, joka oli saanut vain yhden talentin,\nkaivoi maahan kuopan ja kätki sinne isäntänsä rahan. ',25,NULL),
 (561,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',19,'9\"Pitkän ajan kuluttua isäntä palasi ja vaati\npalvelijoiltaan tilitykset.',25,NULL),
 (562,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',20,'0Se, joka oli saanut\nviisi talenttia, toi toiset viisi niiden lisäksi ja\nsanoi: \'Herra, sinä annoit minulle viisi talenttia.\nKuten näet, olen hankkinut voittoa toiset viisi.\'',25,NULL),
 (563,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',21,'1Isäntä sanoi hänelle: \'Hyvin tehty! Olet hyvä ja\nluotettava palvelija. Vähässä olet ollut uskollinen,\nminä panen sinut paljon haltijaksi. Tule herrasi\nilojuhlaan!\' ',25,NULL),
 (564,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',22,'2\"Myös se, joka oli saanut kaksi talenttia, tuli\nja sanoi: \'Herra, sinä annoit minulle kaksi\ntalenttia. Kuten näet, olen hankkinut voittoa toiset\nkaksi.\'',25,NULL),
 (565,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',23,'3Isäntä sanoi hänelle: \'Hyvin tehty! Olet\nhyvä ja luotettava palvelija. Vähässä olet ollut\nuskollinen, minä panen sinut paljon haltijaksi. Tule\nherrasi ilojuhlaan!\' ',25,NULL),
 (566,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',24,'4\"Viimeksi tuli se palvelija, joka oli saanut\nvain yhden talentin, ja sanoi: \'Herra, minä tiesin,\nettä sinä olet ankara mies. Sinä leikkaat sieltä,\nminne et ole kylvänyt, ja kokoat sieltä, minne et\nole siementä viskannut.',25,NULL),
 (567,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',25,'5Minä pelkäsin ja kaivoin\ntalenttisi maahan. Tässä on omasi.\'',25,NULL),
 (568,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',26,'6Isäntä\nvastasi hänelle: \'Sinä kelvoton ja laiska palvelija!\nSinä tiesit, että minä leikkaan sieltä, minne en ole\nkylvänyt, ja kokoan sieltä, minne en ole siementä\nviskannut.',25,NULL),
 (569,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',27,'7Silloinhan sinun olisi pitänyt viedä\nminun rahani pankkiin, niin että olisin palatessani\nsaanut omani takaisin korkoineen.',25,NULL),
 (570,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',28,'8-- Ottakaa\npois hänen talenttinsa ja antakaa se sille, jolla on\nkymmenen talenttia.',25,NULL),
 (571,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',29,'9Jokaiselle, jolla on,\nannetaan, ja hän on saava yltäkyllin, mutta jolla ei\nole, siltä otetaan pois sekin mitä hänellä on.',25,NULL),
 (572,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',30,'0Heittäkää tuo kelvoton palvelija ulos pimeyteen.\nSiellä itketään ja kiristellään hampaita.\'   ',25,NULL),
 (573,'Anonymous','2008-10-17 16:32:02','Anonymous','2008-10-17 16:32:02',37,'7\"Silloin vanhurskaat vastaavat hänelle: \'Herra,\nmilloin me näimme sinut nälissäsi ja annoimme\nsinulle ruokaa, tai janoissasi ja annoimme sinulle\njuotavaa?',25,NULL),
 (574,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',38,'8Milloin me näimme sinut kodittomana ja\notimme sinut luoksemme, tai alasti ja vaatetimme\nsinut?',25,NULL),
 (575,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',39,'9Milloin me näimme sinut sairaana tai\nvankilassa ja kävimme sinun luonasi?\'',25,NULL),
 (576,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',40,'0Kuningas\nvastaa heille: \'Totisesti: kaiken, minkä te olette\ntehneet yhdelle näistä vähäisimmistä veljistäni, sen\nte olette tehneet minulle.\' ',25,NULL),
 (577,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',41,'1\"Sitten hän sanoo vasemmalla puolellaan\noleville: \'Menkää pois minun luotani, te kirotut,\nikuiseen tuleen, joka on varattu Saatanalle ja hänen\nenkeleilleen.',25,NULL),
 (578,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',42,'2Minun oli nälkä, mutta te ette\nantaneet minulle ruokaa. Minun oli jano, mutta te\nette antaneet minulle juotavaa.',25,NULL),
 (579,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',43,'3Minä olin\nkoditon, mutta te ette ottaneet minua luoksenne.\nMinä olin alasti, mutta te ette vaatettaneet minua.\nMinä olin sairas ja vankilassa, mutta te ette\nkäyneet minua katsomassa.\' ',25,NULL),
 (580,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',44,'4\"Silloin nämäkin kysyvät: \'Herra, milloin me\nnäimme sinut nälissäsi tai janoissasi, kodittomana\ntai alasti, tai sairaana tai vankilassa, emmekä\nauttaneet sinua?\'',25,NULL),
 (581,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',45,'5Silloin hän vastaa heille:\n\'Totisesti: kaiken, minkä te olette jättäneet\ntekemättä yhdelle näistä vähäisimmistä, sen te\nolette jättäneet tekemättä minulle.\' ',25,NULL),
 (582,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',46,'6\"Ja niin he lähtevät, toiset iankaikkiseen\nrangaistukseen, mutta vanhurskaat iankaikkiseen\nelämään.\"  ',25,NULL),
 (583,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',1,'Kun Jeesus oli puhunut kaiken tämän, hän sanoi\nopetuslapsilleen:',26,NULL),
 (584,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',2,'\"Kahden päivän kuluttua, niin\nkuin tiedätte, on pääsiäinen. Silloin Ihmisen Poika\nluovutetaan ristiinnaulittavaksi.\" ',26,NULL),
 (585,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',3,'Samaan aikaan kokoontuivat ylin papisto ja kansan\nvanhimmat ylipappi Kaifaksen palatsiin',26,NULL),
 (586,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',4,'neuvottelemaan, millaisella juonella he saisivat\nJeesuksen käsiinsä tappaakseen hänet.',26,NULL),
 (587,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',5,'\"Ei\nkuitenkaan kesken juhlan\", he sanoivat, \"ettei kansa\nala mellakoida.\"   ',26,NULL),
 (588,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',10,'0Jeesus huomasi tämän ja sanoi heille: \"Miksi te\npahoitatte naisen mielen? Hän teki minulle hyvän\nteon.',26,NULL),
 (589,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',11,'1Köyhät teillä on luonanne aina, mutta\nminua teillä ei aina ole.',26,NULL),
 (590,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',12,'2Kun hän voiteli minut,\nse tapahtui hautaamistani varten.',26,NULL),
 (591,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',13,'3Totisesti:\nkaikkialla maailmassa, missä ikinä evankeliumin\nsanoma julistetaan, tullaan muistamaan myös tämä\nnainen ja kertomaan, mitä hän teki.\"     ',26,NULL),
 (592,'Anonymous','2008-10-17 16:32:03','Anonymous','2008-10-17 16:32:03',20,'0Illan tultua Jeesus kävi aterialle kahdentoista\nopetuslapsensa kanssa.',26,NULL),
 (593,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',21,'1Heidän syödessään hän\nsanoi: \"Totisesti: yksi teistä on kavaltava minut.\"',26,NULL),
 (594,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',22,'2Murheen vallassa he alkoivat toinen toisensa\njälkeen kysellä: \"Herra, en kai se ole minä?\"',26,NULL),
 (595,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',23,'3Jeesus vastasi heille: \"Minut kavaltaa mies,\njoka syö samasta vadista kuin minä.',26,NULL),
 (596,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',24,'4Ihmisen\nPoika lähtee pois juuri niin kuin kirjoituksissa\nhänestä sanotaan, mutta voi sitä, josta tulee\nIhmisen Pojan kavaltaja! Sille ihmiselle olisi\nparempi, ettei hän olisi syntynytkään.\"',26,NULL),
 (597,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',25,'5Silloin\nJuudas, hänen kavaltajansa, kysyi: \"Rabbi, en kai se\nole minä?\" \"Itsepä sen sanoit\", vastasi Jeesus.       ',26,NULL),
 (598,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',40,'0Hän palasi opetuslasten luo ja tapasi heidät\nnukkumasta. Silloin hän sanoi Pietarille: \"Ettekö te\nedes hetken vertaa jaksaneet valvoa kanssani?',26,NULL),
 (599,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',41,'1Valvokaa ja rukoilkaa, ettette joutuisi\nkiusaukseen. Tahtoa ihmisellä on, mutta luonto on\nheikko.\" ',26,NULL),
 (600,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',42,'2Hän meni taas etäämmäksi ja rukoili\ntoistamiseen: \"Isä, ellei tämä malja voi mennä\nohitseni minun sitä juomatta, niin toteutukoon sinun\ntahtosi.\"',26,NULL),
 (601,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',43,'3Palatessaan hän tapasi taas\nopetuslapset nukkumasta, sillä uni oli alkanut\npainaa heidän silmiään.',26,NULL),
 (602,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',44,'4Hän jätti heidät siihen,\nmeni taas kauemmaksi ja rukoili kolmannen kerran\nsamoin sanoin.',26,NULL),
 (603,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',45,'5Sitten hän tuli opetuslasten luo\nja sanoi heille: \"Yhäkö te nukutte ja lepäätte?\nHetki on tullut. Ihmisen Poika annetaan syntisten\nkäsiin.',26,NULL),
 (604,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',46,'6Nouskaa, nyt me lähdemme! Minun\nkavaltajani on jo lähellä.\"   ',26,NULL),
 (605,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',51,'1Yksi Jeesuksen seuralaisista ryhtyi\nvastarintaan, veti miekkansa esiin ja iski ylipapin\npalvelijaa niin että tältä irtosi korva.',26,NULL),
 (606,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',52,'2Silloin\nJeesus sanoi hänelle: \"Pane miekkasi tuppeen. Joka\nmiekkaan tarttuu, se miekkaan kaatuu.',26,NULL),
 (607,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',53,'3Luuletko,\netten voisi pyytää apua Isältäni? Hän lähettäisi\ntänne heti kaksitoista legioonaa*enkeleitä ja\nenemmänkin.',26,NULL),
 (608,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',54,'4Mutta kuinka silloin kävisivät\ntoteen kirjoitukset, joiden mukaan näin täytyy\ntapahtua?\" ',26,NULL),
 (609,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',55,'5Jeesus sanoi sitten miesjoukolle: \"Tehän olette\nlähteneet kuin rosvon kimppuun! Miekat ja seipäät\nkäsissä te tulette minua vangitsemaan. Minä olen\njoka päivä istunut temppelissä opettamassa, ettekä\nte ole ottaneet minua kiinni.',26,NULL),
 (610,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',56,'6Mutta kaikki tämä\non tapahtunut, jotta profeettojen kirjoitukset\nkävisivät toteen.\" Silloin kaikki opetuslapset jättivät hänet ja\npakenivat.   ',26,NULL),
 (611,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',59,'9Ylipapit ja koko neuvosto etsivät Jeesusta\nvastaan väärää todistusta, jonka perusteella\nsaisivat hänet surmatuksi.',26,NULL),
 (612,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',60,'0Sellaista ei\nkuitenkaan löytynyt, vaikka monta väärää todistajaa\nesiintyi. Viimein astui esiin kaksi miestä,',26,NULL),
 (613,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',61,'1jotka väittivät Jeesuksen sanoneen: \"Minä voin\nhajottaa Jumalan temppelin ja rakentaa sen uudelleen\nkolmessa päivässä.\" ',26,NULL),
 (614,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',62,'2Silloin ylipappi nousi paikaltaan ja kysyi:\n\"Etkö lainkaan vastaa heidän syytöksiinsä?\"',26,NULL),
 (615,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',63,'3Mutta Jeesus pysyi vaiti. Silloin ylipappi sanoi\nhänelle: \"Minä vannotan sinua elävän Jumalan\nnimessä: sano meille, oletko sinä Messias, Jumalan\nPoika.\"',26,NULL),
 (616,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',64,'4\"Itsepä sen sanoit\", vastasi Jeesus. \"Ja\nminä sanon teille: vastedes te saatte nähdä Ihmisen\nPojan istuvan Voiman oikealla puolella ja tulevan\ntaivaan pilvien päällä.\"',26,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (617,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',65,'5Silloin ylipappi\nrepäisi vaatteensa ja sanoi: \"Hän herjaa Jumalaa.\nMitä me enää todistajia tarvitsemme! Kuulittehan,\nmiten hän herjasi.',26,NULL),
 (618,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',66,'6Mitä mieltä olette?\" He\nvastasivat: \"Hän on ansainnut kuoleman.\" ',26,NULL),
 (619,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',67,'7He sylkivät nyt Jeesusta kasvoihin ja löivät\nhäntä. Muutamat läimäyttivät häntä poskelle',26,NULL),
 (620,'Anonymous','2008-10-17 16:32:04','Anonymous','2008-10-17 16:32:04',68,'8ja\nilkkuivat: \"Profetoi, Messias, sano kuka sinua löi!\"   ',26,NULL),
 (621,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',1,'Varhain aamulla kaikki ylipapit ja kansan\nvanhimmat kokoontuivat ja sopivat keskenään\nJeesuksen surmaamisesta.',27,NULL),
 (622,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',2,'Jeesus pantiin köysiin,\nvietiin pois ja luovutettiin Pilatukselle,\nmaaherralle.   ',27,NULL),
 (623,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',6,'Ylipapit ottivat rahat ja sanoivat: \"Nämä ovat\nverirahoja, näitä ei ole lupa panna\ntemppelirahastoon.\"',27,NULL),
 (624,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',7,'Neuvoteltuaan he päättivät\nostaa niillä savenvalajan pellon muukalaisten\nhautapaikaksi.',27,NULL),
 (625,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',8,'Vielä tänäkin päivänä sitä peltoa\nkutsutaan Veripelloksi.',27,NULL),
 (626,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',9,'Näin kävi toteen tämä\nprofeetta Jeremian sana:    ',27,NULL),
 (627,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',15,'5Tapana oli, että maaherra aina juhlien aikana\npäästi vapaaksi yhden vangin, jonka väkijoukko sai\nvalita.',27,NULL),
 (628,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',16,'6Vankien joukossa oli nyt Barabbas-\nniminen kuuluisa mies.',27,NULL),
 (629,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',17,'7Kun väkijoukko oli\nkoolla, Pilatus kysyi: \"Kumman haluatte? Vapautanko\nBarabbaksen vai Jeesuksen, jota sanotaan\nKristukseksi?\"',27,NULL),
 (630,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',18,'8Hän näet tiesi, että Jeesus oli\npelkästä kateudesta jätetty hänen käsiinsä. ',27,NULL),
 (631,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',19,'9Kun Pilatus istui tuomarinistuimellaan, hän sai\nvaimoltaan sanan: \"Älä tee mitään sille pyhälle\nmiehelle. Näin hänestä viime yönä pahaa unta.\"',27,NULL),
 (632,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',20,'0Mutta ylipapit ja vanhimmat yllyttivät\nväkijoukon pyytämään Barabbakselle armahdusta ja\nJeesukselle kuolemantuomiota.',27,NULL),
 (633,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',21,'1Maaherra kysyi\nnyt: \"Kumman näistä kahdesta haluatte? Kumman\npäästän vapaaksi?\" Väki vastasi: \"Barabbaksen.\"',27,NULL),
 (634,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',22,'2Pilatus kysyi: \"Mitä minä sitten teen\nJeesukselle, jota sanotaan Kristukseksi?\" Kaikki\nvastasivat: \"Ristiinnaulittakoon!\"',27,NULL),
 (635,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',23,'3\"Mitä pahaa\nhän on tehnyt?\" kysyi Pilatus. Mutta he vain\nhuusivat entistä kovemmin: \"Ristiinnaulittakoon!\" ',27,NULL),
 (636,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',24,'4Kun Pilatus näki, ettei mitään ollut tehtävissä,\nja kun meteli yhä paheni, hän otti vettä, pesi\nkätensä väkijoukon nähden ja sanoi: \"Minä olen\nsyytön tämän miehen vereen. Tämä on teidän asianne.\"',27,NULL),
 (637,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',25,'5Kaikki huusivat yhteen ääneen: \"Hänen verensä\nsaa tulla meidän ja meidän lastemme päälle!\"',27,NULL),
 (638,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',26,'6Silloin Pilatus antoi heille myöten ja vapautti\nBarabbaksen, mutta Jeesuksen hän ruoskitti ja\nluovutti ristiinnaulittavaksi.     ',27,NULL),
 (639,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',35,'5Kun he olivat ristiinnaulinneet Jeesuksen, he\njakoivat keskenään hänen vaatteensa heittämällä\nniistä arpaa.',27,NULL),
 (640,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',36,'6Sitten he jäivät sinne istumaan ja\nvartioivat häntä.',27,NULL),
 (641,'Anonymous','2008-10-17 16:32:05','Anonymous','2008-10-17 16:32:05',37,'7Hänen päänsä yläpuolelle he\nkiinnittivät kirjoituksen, josta kävi ilmi hänen\ntuomionsa syy: \"Tämä on Jeesus, juutalaisten\nkuningas.\"',27,NULL),
 (642,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',38,'8Yhdessä Jeesuksen kanssa\nristiinnaulittiin kaksi rosvoa, toinen hänen\noikealle, toinen hänen vasemmalle puolelleen. ',27,NULL),
 (643,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',39,'9Ohikulkijat pilkkasivat häntä. Päätään\nnyökyttäen',27,NULL),
 (644,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',40,'0he sanoivat: \"Sinähän pystyt\nhajottamaan temppelin ja rakentamaan sen uudelleen\nkolmessa päivässä. Pelasta nyt itsesi, jos kerran\nolet Jumalan Poika. Tule alas ristiltä!\"',27,NULL),
 (645,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',41,'1Ylipapit yhtyivät hekin pilkkaan yhdessä\nlainopettajien ja vanhimpien kanssa. He sanoivat:',27,NULL),
 (646,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',42,'2\"Muita hän kyllä on auttanut, mutta itseään hän\nei pysty auttamaan. Onhan hän Israelin kuningas,\ntulkoon nyt ristiltä alas! Silloin me uskomme\nhäneen.',27,NULL),
 (647,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',43,'3Hän on pannut luottamuksensa Jumalaan --\npelastakoon Jumala nyt hänet, jos on häneen\nmieltynyt! Onhan hän sanonut olevansa Jumalan\nPoika.\"',27,NULL),
 (648,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',44,'4Samalla tavoin häntä pilkkasivat myös\nrosvot, jotka oli ristiinnaulittu yhdessä hänen\nkanssaan.   ',27,NULL),
 (649,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',51,'1Sillä hetkellä temppelin väliverho repesi\nkahtia, ylhäältä alas asti. Maa vavahteli, kalliot\nhalkeilivat,',27,NULL),
 (650,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',52,'2haudat aukenivat, ja monien\npoisnukkuneiden pyhien ruumiit nousivat ylös.',27,NULL),
 (651,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',53,'3He\nlähtivät haudoistaan, ja Jeesuksen ylösnousemuksen\njälkeen he tulivat pyhään kaupunkiin ja\nnäyttäytyivät siellä monille. ',27,NULL),
 (652,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',54,'4Kun sadanpäällikkö ja miehet, jotka hänen\nkanssaan vartioivat Jeesusta, näkivät maan\nvavahtelun ja kaiken, mitä tapahtui, he pelästyivät\nsuunniltaan ja sanoivat: \"Tämä oli todella Jumalan\nPoika!\" ',27,NULL),
 (653,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',55,'5Siellä oli myös useita naisia jonkin matkan\npäässä tätä katselemassa. Galileasta lähtien he\nolivat kulkeneet Jeesuksen mukana ja palvelleet\nhäntä.',27,NULL),
 (654,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',56,'6Heidän joukossaan olivat Magdalan Maria,\nJaakobin ja Joosefin äiti Maria ja Sebedeuksen\npoikien äiti.   ',27,NULL),
 (655,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',62,'2Seuraavana päivänä, sapatin valmistuspäivän\nmentyä, ylipapit ja fariseukset lähtivät yhdessä\nPilatuksen luo.',27,NULL),
 (656,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',63,'3He sanoivat: \"Kunnioitettu\nmaaherra, meille tuli mieleen, että eläessään tuo\nvillitsijä sanoi: \'Kolmen päivän kuluttua nousen\nkuolleista.\'',27,NULL),
 (657,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',64,'4Käske siis vartioida hautaa tarkoin\nkolmanteen päivään asti, etteivät hänen\nopetuslapsensa pääse varastamaan häntä ja sanomaan\nihmisille: \'Hän on noussut kuolleista.\' Viimeinen\nvillitys olisi silloin ensimmäistä pahempi.\"',27,NULL),
 (658,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',65,'5Pilatus vastasi heille: \"Saatte vartioväkeä.\nMenkää ja järjestäkää haudan vartiointi niin hyvin\nkuin taidatte.\"',27,NULL),
 (659,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',66,'6He lähtivät ja varmistivat\nhaudan sinetöimällä kiven ja asettamalla vartion. ',27,NULL),
 (660,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',1,'Sapatin päätyttyä, viikon ensimmäisen päivän\nkoittaessa, tulivat Magdalan Maria ja se toinen\nMaria katsomaan hautaa.',28,NULL),
 (661,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',2,'Äkkiä maa alkoi\nvavahdella ja järistä, sillä Herran enkeli laskeutui\ntaivaasta. Hän tuli haudalle, vieritti kiven pois ja\nistuutui sille.',28,NULL),
 (662,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',3,'Hän oli hohtava kuin salama ja\nhänen vaatteensa olivat valkeat kuin lumi.',28,NULL),
 (663,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',4,'Vartijat pelästyivät häntä niin, että alkoivat\nvapista ja kaatuivat maahan kuin kuolleet. ',28,NULL),
 (664,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',5,'Enkeli kääntyi naisten puoleen ja sanoi: \"Älkää\nte pelätkö. Minä tiedän, että te etsitte\nristiinnaulittua Jeesusta.',28,NULL),
 (665,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',6,'Ei hän ole täällä, hän\non noussut kuolleista, niin kuin itse sanoi. Tulkaa\nkatsomaan, tuossa on paikka, jossa hän makasi.',28,NULL),
 (666,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',7,'Menkää kiireesti sanomaan hänen opetuslapsilleen:\n\'Hän on noussut kuolleista. Hän menee teidän\nedellänne Galileaan, siellä te näette hänet.\' Tämä\noli minun sanomani teille.\"',28,NULL),
 (667,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',8,'Naiset lähtivät heti\nhaudalta, yhtaikaa peloissaan ja riemuissaan, ja\nriensivät viemään sanaa Jeesuksen opetuslapsille. ',28,NULL),
 (668,'Anonymous','2008-10-17 16:32:06','Anonymous','2008-10-17 16:32:06',9,'Mutta yhtäkkiä Jeesus tuli heitä vastaan ja\ntervehti heitä. He menivät hänen luokseen,\nsyleilivät hänen jalkojaan ja kumarsivat häntä.',28,NULL),
 (669,'Anonymous','2008-10-17 16:32:07','Anonymous','2008-10-17 16:32:07',10,'0Silloin Jeesus sanoi heille: \"Älkää pelätkö!\nMenkää sanomaan veljilleni, että heidän pitää lähteä\nGalileaan. Siellä he näkevät minut.\" ',28,NULL),
 (670,'Anonymous','2008-10-17 16:32:07','Anonymous','2008-10-17 16:32:07',11,'1Naisten ollessa matkalla muutamat vartiomiehistä\nmenivät kaupunkiin ja kertoivat ylipapeille kaiken,\nmitä oli tapahtunut.',28,NULL),
 (671,'Anonymous','2008-10-17 16:32:07','Anonymous','2008-10-17 16:32:07',12,'2Silloin nämä kokoontuivat\nneuvottelemaan yhdessä vanhimpien kanssa ja\npäättivät antaa sotilaille suuren summan rahaa.',28,NULL),
 (672,'Anonymous','2008-10-17 16:32:07','Anonymous','2008-10-17 16:32:07',13,'3He sanoivat sotilaille: \"Sanokaa, että hänen\nopetuslapsensa tulivat yöllä, kun te nukuitte, ja\nvarastivat hänet.',28,NULL),
 (673,'Anonymous','2008-10-17 16:32:07','Anonymous','2008-10-17 16:32:07',14,'4Ja jos tämä tulee maaherran\nkorviin, me kyllä lepytämme hänet ja järjestämme\nniin, ettei teille koidu ikävyyksiä.\"',28,NULL),
 (674,'Anonymous','2008-10-17 16:32:07','Anonymous','2008-10-17 16:32:07',15,'5Sotilaat\nottivat rahat ja tekivät niin kuin heille oli\nsanottu. Heidän kertomustaan on juutalaisten\nkeskuudessa levitetty tähän päivään asti.    ',28,NULL),
 (675,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',1,'Ilosanoma Jeesuksesta Kristuksesta, Jumalan\nPojasta, lähti liikkeelle näin. ',29,NULL),
 (676,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',2,'Profeetta Jesajan kirjassa sanotaan:   ',29,NULL),
 (677,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',4,'Ja näin tapahtui. Johannes Kastaja julisti\nautiomaassa, että ihmisten tuli kääntyä ja ottaa\nkaste, jotta synnit annettaisiin heille anteeksi.',29,NULL),
 (678,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',5,'Hänen luokseen tuli paljon väkeä Jerusalemista ja\njoka puolelta Juudeaa. He tunnustivat syntinsä, ja\nJohannes kastoi heidät Jordanissa. ',29,NULL),
 (679,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',6,'Johanneksella oli yllään kamelinkarvavaate ja\nvyötäisillään nahkavyö, ja hänen ruokanaan olivat\nheinäsirkat ja villimehiläisten hunaja.',29,NULL),
 (680,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',7,'Hän\njulisti: \"Minun jälkeeni tulee minua väkevämpi. Minä\nen kelpaa edes kumartumaan ja avaamaan hänen kenkiensä\nnauhoja.',29,NULL),
 (681,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',8,'Minä olen kastanut teidät vedellä, mutta\nhän kastaa teidät Pyhällä Hengellä.\"   ',29,NULL),
 (682,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',12,'2Heti sen jälkeen Henki ajoi hänet autiomaahan.',29,NULL),
 (683,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',13,'3Neljäkymmentä päivää hän oli autiomaassa Saatanan\nkiusattavana. Hän eli villieläinten joukossa, ja\nenkelit pitivät hänestä huolta.     ',29,NULL),
 (684,'Anonymous','2008-10-17 16:32:08','Anonymous','2008-10-17 16:32:08',19,'9Jonkin matkaa kuljettuaan Jeesus näki Jaakobin,\nSebedeuksen pojan, ja hänen veljensä Johanneksen.\nHekin olivat veneessä, verkkojaan selvittelemässä.',29,NULL),
 (685,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',20,'0Jeesus kutsui heti heidät. He jättivät isänsä\nSebedeuksen ja tämän palkkalaiset veneeseen ja\nlähtivät seuraamaan Jeesusta.   ',29,NULL),
 (686,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',23,'3Kaupungin synagogassa oli juuri silloin mies, joka\noli saastaisen hengen vallassa. Hän alkoi huutaa:',29,NULL),
 (687,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',24,'4\"Mitä sinä meistä tahdot, Jeesus Nasaretilainen?\nOletko tullut tuhoamaan meidät? Minä tiedän, kuka sinä\nolet, Jumalan Pyhä!\"',29,NULL),
 (688,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',25,'5Mutta Jeesus käski henkeä\nankarin sanoin: \"Vaiti! Lähde hänestä!\"',29,NULL),
 (689,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',26,'6Silloin\nsaastainen henki kouristi miestä ja lähti hänestä\nhuutaen kovalla äänellä.',29,NULL),
 (690,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',27,'7Kaikki joutuivat ymmälle\nja kyselivät toisiltaan: \"Mitä tämä on? Tällä miehellä\non valta opettaa uudella tavalla! Saastaiset hengetkin\ntottelevat, kun hän käskee.\"',29,NULL),
 (691,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',28,'8Hänen maineensa\nlevisi kohta kaikkialle, ympäri koko Galilean.   ',29,NULL),
 (692,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',32,'2Illalla, auringonlaskun jälkeen Jeesuksen luo\ntuotiin kaikki sairaat ja pahojen henkien vaivaamat.',29,NULL),
 (693,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',33,'3Koko kaupunki oli kerääntynyt oven edustalle.',29,NULL),
 (694,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',34,'4Hän paransi useita erilaisten tautien vaivaamia ja\najoi ulos monia pahoja henkiä. Hän ei antanut henkien\npuhua, koska ne tunsivat hänet.     ',29,NULL),
 (695,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',1,'Jonkin ajan kuluttua Jeesus meni taas Kapernaumiin.\nKun ihmiset kuulivat hänen olevan kotona,',30,NULL),
 (696,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',2,'väkeä\ntuli koolle niin paljon, etteivät kaikki mahtuneet\nedes oven edustalle. Jeesus julisti heille sanaa.',30,NULL),
 (697,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',3,'Hänen luokseen oltiin tuomassa halvaantunutta.\nSairasta kantamassa oli neljä miestä,',30,NULL),
 (698,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',4,'jotka eivät\ntungoksessa kuitenkaan päässeet tuomaan häntä\nJeesuksen eteen. Silloin he purkivat katon siltä\nkohden, missä Jeesus oli, ja aukon tehtyään laskivat\nsiitä alas vuodematon, jolla halvaantunut makasi.',30,NULL),
 (699,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',5,'Kun Jeesus näki heidän uskonsa, hän sanoi\nhalvaantuneelle: \"Poikani, sinun syntisi annetaan\nanteeksi.\" ',30,NULL),
 (700,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',6,'Mutta siellä istui myös muutamia lainopettajia, ja\nhe sanoivat itsekseen:',30,NULL),
 (701,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',7,'\"Miten hän tuolla tavalla\npuhuu? Hän herjaa Jumalaa. Kuka muu kuin Jumala voi\nantaa syntejä anteeksi?\"',30,NULL),
 (702,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',8,'Jeesus tunsi heti\nhengessään, mitä he ajattelivat, ja sanoi heille:\n\"Kuinka te tuollaista ajattelette?',30,NULL),
 (703,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',9,'Kumpi on\nhelpompaa, sanoa halvaantuneelle: \'Sinun syntisi\nannetaan anteeksi\', vai sanoa: \'Nouse, ota vuoteesi ja\nkävele\'?',30,NULL),
 (704,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',10,'0Mutta jotta te tietäisitte, että Ihmisen\nPojalla on valta antaa maan päällä syntejä anteeksi\" -\n- hän puhui nyt halvaantuneelle --',30,NULL),
 (705,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',11,'1\"nouse, ota\nvuoteesi ja mene kotiisi.\"',30,NULL),
 (706,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',12,'2Silloin mies heti\nnousi, otti vuoteensa ja käveli pois kaikkien nähden.\nKaikki olivat tästä hämmästyksissään, ylistivät\nJumalaa ja sanoivat: \"Tällaista emme ole ikinä\nnähneet.\"   ',30,NULL),
 (707,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',15,'5Jeesus oli sitten aterialla Leevin kodissa. Monia\npublikaaneja*ja muita syntisiä aterioi Jeesuksen ja\nhänen opetuslastensa kanssa, sillä heitä oli paljon\nhänen seuraajiensa joukossa.',30,NULL),
 (708,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',16,'6Kun fariseuksiin*kuuluvat lainopettajat näkivät Jeesuksen syövän\nsyntisten ja publikaanien seurassa, he sanoivat hänen\nopetuslapsilleen: \"Kuinka hän syö yhdessä publikaanien\nja muiden syntisten kanssa!\"',30,NULL),
 (709,'Anonymous','2008-10-17 16:32:09','Anonymous','2008-10-17 16:32:09',17,'7Jeesus kuuli sen ja\nsanoi: \"Eivät terveet tarvitse parantajaa, vaan\nsairaat. En minä ole tullut kutsumaan hurskaita, vaan\nsyntisiä.\"     ',30,NULL),
 (710,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',1,'Jeesus meni taas synagogaan. Siellä oli mies, jonka\nkäsi oli surkastunut.',31,NULL),
 (711,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',2,'Fariseukset pitivät silmällä,\nparantaisiko Jeesus miehen sapattina, sillä he\ntahtoivat nostaa syytteen häntä vastaan.',31,NULL),
 (712,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',3,'Jeesus\nsanoi miehelle, jonka käsi oli surkastunut: \"Astu\nesiin\",',31,NULL),
 (713,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',4,'ja kysyi sitten fariseuksilta: \"Kumpi on\nsapattina luvallista, tehdä hyvää vai tehdä pahaa,\npelastaa ihmishenki vai tappaa ihminen?\" He eivät\nvastanneet mitään.',31,NULL),
 (714,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',5,'Jeesus loi heihin vihaisen\nkatseen; hän oli murheissaan heidän sydämensä\nkovuudesta. Hän sanoi miehelle: \"Ojenna kätesi.\" Mies\nojensi kätensä, ja se tuli ennalleen.',31,NULL),
 (715,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',6,'Mutta\nfariseukset menivät ulos ja ryhtyivät heti Herodeksen\nkannattajien kanssa suunnittelemaan, miten saisivat\nJeesuksen raivatuksi pois tieltä.       ',31,NULL),
 (716,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',22,'2Mutta lainopettajat, joita oli tullut\nJerusalemista, sanoivat: \"Hänessä on Belsebul*,\nitsensä pääpaholaisen voimin hän pahoja henkiä\nkarkottaa.\"',31,NULL),
 (717,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',23,'3Silloin Jeesus kutsui heidät luokseen\nja puhui heille vertauksin: \"Miten Saatana voi ajaa\nulos Saatanan?',31,NULL),
 (718,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',24,'4Jos valtakunta jakautuu ja\ntaistelee itseään vastaan, se ei voi kestää,',31,NULL),
 (719,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',25,'5eikä\nmyöskään perhe kestä, jos sitä repivät riidat.',31,NULL),
 (720,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',26,'6Ja\njos Saatana nousee itseään vastaan, se ei voi kestää,\nvaan sen loppu on tullut.',31,NULL),
 (721,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',27,'7Eihän kukaan voi\ntunkeutua väkevän miehen taloon ja ryöstää hänen\ntavaroitaan, ellei ensin sido häntä. Vasta sitten hän\nvoi ryöstää talon ja tavarat.',31,NULL),
 (722,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',28,'8Totisesti: Kaikki\ntullaan antamaan ihmisille anteeksi, kaikki synnit ja\nherjaukset, miten he sitten herjaavatkin.',31,NULL),
 (723,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',29,'9Mutta\njos joku herjaa Pyhää Henkeä, hän ei saa ikinä\nanteeksi vaan on syypää ikuiseen syntiin.\"',31,NULL),
 (724,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',30,'0He näet\nolivat sanoneet, että Jeesuksessa oli saastainen\nhenki.    ',31,NULL),
 (725,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',1,'Kerran Jeesus taas opetti järven rannalla. Hänen\nympärilleen kokoontui niin paljon ihmisiä, että hän\nsiirtyi veneeseen. Hän istui järvellä veneessä, ja\nväkijoukko oli rannalla.',32,NULL),
 (726,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',2,'Hän opetti heille\nvertauksin monia asioita. Näin hän heitä opetti:',32,NULL),
 (727,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',3,'\"Kuulkaa! Mies lähti\nkylvämään.',32,NULL),
 (728,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',4,'Ja kun hän kylvi, kävi niin, että osa\nsiemenistä putosi tien oheen, ja linnut tulivat ja\nsöivät jyvät.',32,NULL),
 (729,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',5,'Osa putosi kallioiseen paikkaan,\nmissä jyville ei ollut paljon maata. Ne nousivat kohta\noraalle, koska maata ei ollut syvälti,',32,NULL),
 (730,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',6,'mutta\nauringon noustua oraat helteessä kuivettuivat, koska\nniillä ei ollut juurta.',32,NULL),
 (731,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',7,'Osa taas putosi ohdakkeiden\nsekaan, ja ohdakkeet kasvoivat ja tukahduttivat oraan,\neikä siitä saatu satoa.',32,NULL),
 (732,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',8,'Mutta muut jyvät putosivat\nhyvään maahan. Ne nousivat oraalle, kasvoivat ja\nantoivat sadon, mikä kolmekymmentä, mikä\nkuusikymmentä, mikä sata jyvää.\"',32,NULL),
 (733,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',9,'Ja hän sanoi:\n\"Jolla on korvat, se kuulkoon!\"            ',32,NULL),
 (734,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',33,'3Monin tällaisin vertauksin hän julisti ihmisille\nsanaa, sen mukaan kuin he sitä saattoivat ymmärtää.',32,NULL),
 (735,'Anonymous','2008-10-17 16:32:10','Anonymous','2008-10-17 16:32:10',34,'4Vertauksitta hän ei heille puhunut, mutta\nopetuslapsilleen hän selitti kaiken, kun he olivat\nkeskenään.    ',32,NULL),
 (736,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',1,'He tulivat toiselle puolen järveä Gerasan alueelle.',33,NULL),
 (737,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',2,'Heti kun Jeesus nousi veneestä, häntä vastaan tuli\nhautaluolista mies, jossa oli saastainen henki.',33,NULL),
 (738,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',3,'Mies asusti haudoissa, eikä kukaan pystynyt enää\nköyttämään eikä kahlehtimaan häntä.',33,NULL),
 (739,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',4,'Monet kerrat\nhänet oli kahlittu sekä käsistä että jaloista, mutta\nhän oli särkenyt kahleet ja katkonut köydet, eikä\nkukaan kyennyt hillitsemään häntä.',33,NULL),
 (740,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',5,'Kaiket päivät ja\nyöt hän oleskeli haudoissa ja vuorilla, huusi ja\nrunteli itseään kivillä.',33,NULL),
 (741,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',6,'Kun hän nyt kaukaa näki\nJeesuksen, hän tuli juosten paikalle, heittäytyi\nmaahan hänen eteensä',33,NULL),
 (742,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',7,'ja huusi kovalla äänellä:\n\"Mitä sinä minusta tahdot, Jeesus, Korkeimman Jumalan\npoika? Jumalan tähden, älä kiduta minua!\"',33,NULL),
 (743,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',8,'Jeesus\nnäet oli jo käskemässä saastaista henkeä lähtemään\nmiehestä.',33,NULL),
 (744,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',9,'\"Mikä sinun nimesi on?\" Jeesus kysyi, ja\nsai vastauksen: \"Legioona*, sillä meitä on monta.\"',33,NULL),
 (745,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',10,'0Henget pyysivät ja rukoilivat, ettei Jeesus ajaisi\nniitä pois siltä seudulta. ',33,NULL),
 (746,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',11,'1Lähistöllä oli vuoren rinteellä suuri sikalauma\nlaitumella.',33,NULL),
 (747,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',12,'2Pahat henget pyysivät Jeesukselta:\n\"Päästä meidät sikalaumaan, anna meidän mennä\nsikoihin.\"',33,NULL),
 (748,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',13,'3Hän antoi niille luvan. Silloin\nsaastaiset henget lähtivät miehestä ja menivät\nsikoihin, ja lauma syöksyi jyrkännettä alas järveen.\nSikoja oli noin kaksituhatta, ja ne hukkuivat kaikki. ',33,NULL),
 (749,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',14,'4Sikopaimenet lähtivät pakoon ja kertoivat kaikesta\ntästä kaupungissa ja kylissä. Ihmisiä lähti katsomaan,\nmitä oli tapahtunut.',33,NULL),
 (750,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',15,'5He tulivat Jeesuksen luo ja\nnäkivät pahojen henkien vaivaaman miehen istuvan\nsiellä. Mies, jossa oli ollut legioona henkiä, istui\nvaatteet yllään ja täydessä järjessään. Tämä sai\nheidät pelon valtaan.',33,NULL),
 (751,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',16,'6Paikalla olleet kertoivat\nheille, mitä pahojen henkien vaivaamalle oli\ntapahtunut ja kuinka sikojen oli käynyt.',33,NULL),
 (752,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',17,'7Silloin\nkaikki alkoivat pyytää, että Jeesus poistuisi siltä\nseudulta. ',33,NULL),
 (753,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',18,'8Kun Jeesus nousi veneeseen, pahoista hengistä\nvapautunut pyysi saada jäädä hänen seuraansa.',33,NULL),
 (754,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',19,'9Jeesus ei siihen suostunut vaan sanoi hänelle:\n\"Mene kotiisi omaistesi luo ja kerro tästä suuresta\nteosta, jonka Herra on armossaan sinulle tehnyt.\"',33,NULL),
 (755,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',20,'0Mies lähti ja alkoi Dekapoliin alueella julistaa,\nmitä Jeesus oli tehnyt hänelle. Kaikki olivat\nihmeissään.   ',33,NULL),
 (756,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',25,'5Siellä oli myös nainen, jota kaksitoista vuotta\noli vaivannut verenvuoto.',33,NULL),
 (757,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',26,'6Hän oli kärsinyt paljon\nmonien lääkärien käsissä ja kuluttanut kaiken\nomaisuutensa saamatta mitään apua; pikemminkin hänen\ntilansa oli huonontunut.',33,NULL),
 (758,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',27,'7Hän oli kuullut\nJeesuksesta, ja nyt hän väentungoksessa tuli Jeesuksen\ntaakse ja kosketti hänen viittaansa.',33,NULL),
 (759,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',28,'8Nainen näet\najatteli: \"Jos pääsen koskettamaan edes hänen\nviittaansa, niin minä paranen.\"',33,NULL),
 (760,'Anonymous','2008-10-17 16:32:11','Anonymous','2008-10-17 16:32:11',29,'9Siinä samassa\nverenvuoto tyrehtyi ja hän tunsi ruumiissaan, että\nvaiva oli poissa. ',33,NULL),
 (761,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',30,'0Jeesus tunsi heti, että hänestä oli lähtenyt\nvoimaa. Hän kääntyi tungoksessa ja kysyi: \"Kuka koski\nvaatteisiini?\"',33,NULL),
 (762,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',31,'1Opetuslapset sanoivat hänelle:\n\"Sinä näet, millaisen tungoksen keskellä olet, ja\nkysyt: kuka koski minuun?\"',33,NULL),
 (763,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',32,'2Mutta Jeesus katseli\nympärilleen nähdäkseen sen, joka niin oli tehnyt.',33,NULL),
 (764,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',33,'3Nainen vapisi pelosta, sillä hän tiesi mitä\nhänelle oli tapahtunut. Hän tuli Jeesuksen eteen,\nheittäytyi maahan ja kertoi hänelle totuudenmukaisesti\nkaiken.',33,NULL),
 (765,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',34,'4Jeesus sanoi hänelle: \"Tyttäreni, uskosi\non parantanut sinut. Mene rauhassa, sinä olet päässyt\nvaivastasi.\" ',33,NULL),
 (766,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',35,'5Jeesuksen vielä puhuessa tuotiin synagogan\nesimiehelle kotoa sana: \"Tyttäresi kuoli jo, miksi\nenää vaivaisit opettajaa.\"',33,NULL),
 (767,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',36,'6Tämän kuultuaan Jeesus\nsanoi esimiehelle: \"Älä pelkää, vaan usko.\"',33,NULL),
 (768,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',37,'7Hän ei\nantanut kenenkään muun tulla mukaansa kuin Pietarin ja\nJaakobin sekä Johanneksen, Jaakobin veljen.',33,NULL),
 (769,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',38,'8He\ntulivat synagogan esimiehen kotiin, ja Jeesus näki\nhälisevän väenpaljouden ja kuuli ihmisten itkevän ja\nvalittavan suureen ääneen.',33,NULL),
 (770,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',39,'9Hän meni sisään ja\nsanoi: \"Miksi te noin hälisette ja itkette? Ei lapsi\nole kuollut, hän nukkuu.\"',33,NULL),
 (771,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',40,'0Jeesukselle naurettiin.\nMutta hän ajoi kaikki ulos, otti mukaansa lapsen isän\nja äidin sekä seuralaisensa ja meni huoneeseen, jossa\nlapsi oli.',33,NULL),
 (772,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',41,'1Hän otti lasta kädestä ja sanoi\nhänelle: \"Talita kuum!\"*',33,NULL),
 (773,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',42,'2Heti tyttö nousi ja\nkäveli; hän oli kaksitoistavuotias. Kaikki olivat\nhämmästyksestä suunniltaan.',33,NULL),
 (774,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',43,'3Jeesus kielsi\nankarasti heitä kertomasta tästä kenellekään. Tytölle\nhän käski antaa syötävää.  ',33,NULL),
 (775,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',1,'Jeesus lähti sieltä ja tuli kotikaupunkiinsa, ja\nopetuslapset olivat hänen kanssaan.',34,NULL),
 (776,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',2,'Kun tuli\nsapatti, hän ryhtyi opettamaan synagogassa, ja häntä\nkuunnellessaan monet kyselivät hämmästyneinä: \"Mistä\nhän on saanut tämän kaiken? Mikä on tämä viisaus, joka\nhänelle on annettu? Mitä ovat nuo voimateot, jotka\ntapahtuvat hänen kättensä kautta?',34,NULL),
 (777,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',3,'Eikö tämä ole se\nrakennusmies, Marian poika, Jaakobin, Joosefin,\nJuudaksen ja Simonin veli? Täällä hänen sisarensakin\nasuvat, meidän keskuudessamme.\" Näin he torjuivat\nhänet. ',34,NULL),
 (778,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',4,'Jeesus sanoi heille: \"Missään ei profeetta ole niin\nväheksytty kuin kotikaupungissaan, sukulaistensa\nparissa ja omassa kodissaan.\"',34,NULL),
 (779,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',5,'Niinpä hän ei voinut\ntehdä siellä yhtään voimatekoa; vain muutamia sairaita\nhän paransi panemalla kätensä heidän päälleen.',34,NULL),
 (780,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',6,'Ihmisten epäusko hämmästytti häntä. Hän kulki\nsitten kylästä kylään ja opetti.   ',34,NULL),
 (781,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',12,'2Niin opetuslapset lähtivät matkaan ja julistivat,\nettä kaikkien tuli kääntyä.',34,NULL),
 (782,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',13,'3He karkottivat monta\npahaa henkeä sekä voitelivat öljyllä sairaita ja\nparansivat heidät.   ',34,NULL),
 (783,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',17,'7Herodes oli näet pidättänyt Johanneksen, pannut\nhänet kahleisiin ja teljennyt vankilaan. Tämän hän oli\ntehnyt veljensä Filippoksen vaimon Herodiaan tähden.\nHän oli nainut Herodiaan,',34,NULL),
 (784,'Anonymous','2008-10-17 16:32:12','Anonymous','2008-10-17 16:32:12',18,'8mutta Johannes oli\nsanonut hänelle: \"Sinun ei ole lupa elää veljesi\nvaimon kanssa.\"',34,NULL),
 (785,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',19,'9Herodias ei sietänyt Johannesta ja\nolisi halunnut tappaa hänet, mutta ei voinut,',34,NULL),
 (786,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',20,'0koska Herodes pelkäsi Johannesta. Herodes tiesi\nhänet hurskaaksi ja pyhäksi mieheksi ja suojeli häntä.\nHän kuunteli Johannesta mielellään, vaikka tämän\npuheet usein jäivätkin vaivaamaan häntä. ',34,NULL),
 (787,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',21,'1Sopiva tilaisuus Herodiaalle tarjoutui, kun\nHerodes syntymäpäivänään järjesti pidot\nhoviherroilleen ja sotaväen päälliköille sekä Galilean\njohtomiehille.',34,NULL),
 (788,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',22,'2Herodiaan tytär tuli silloin sisään\nja tanssi, ja Herodes ja hänen pöytävieraansa olivat\nihastuksissaan. Kuningas sanoi tytölle: \"Pyydä minulta\nmitä vain tahdot, niin minä annan sen sinulle.\"',34,NULL),
 (789,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',23,'3Ja\nhän lupasi ja vannoi: \"Mitä ikinä minulta pyydät, sen\nminä sinulle annan, vaikka puolet valtakuntaani.\"',34,NULL),
 (790,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',24,'4Tyttö meni ulos ja kysyi äidiltään: \"Mitä minä\npyytäisin?\" Äiti vastasi: \"Pyydä Johannes Kastajan\npäätä.\"',34,NULL),
 (791,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',25,'5Tyttö kiiruhti heti takaisin kuninkaan luo\nja sanoi: \"Tahdon, että annat minulle nyt heti vadilla\nJohannes Kastajan pään.\" ',34,NULL),
 (792,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',26,'6Kuningas tuli pahoille mielin, mutta koska hän oli\nvieraittensa kuullen vannonut valan, hän ei voinut\nvastata kieltävästi.',34,NULL),
 (793,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',27,'7Hän antoi heti pyövelille\nkäskyn, että tämän oli tuotava Johanneksen pää.\nPyöveli meni vankilaan ja mestasi siellä Johanneksen.',34,NULL),
 (794,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',28,'8Sitten hän toi pään vadilla ja antoi sen tytölle,\nja tyttö antoi pään äidilleen. ',34,NULL),
 (795,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',29,'9Kun Johanneksen opetuslapset kuulivat tästä, he\ntulivat hakemaan ruumiin ja veivät sen hautaan.   ',34,NULL),
 (796,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',34,'4Kun Jeesus veneestä noustessaan näki kaikki nämä\nihmiset, hänen tuli heitä sääli, sillä he olivat kuin\nlammaslauma paimenta vailla. Hän alkoi opettaa ja\npuhui heille pitkään.',34,NULL),
 (797,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',35,'5Kun päivä oli jo illassa,\nopetuslapset tulivat hänen luokseen ja sanoivat: \"Tämä\non asumatonta seutua, ja on jo myöhä.',34,NULL),
 (798,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',36,'6Lähetä\nheidät pois, että he menisivät lähiseudun taloihin ja\nkyliin ostamaan itselleen syötävää.\"',34,NULL),
 (799,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',37,'7\"Antakaa te\nheille syötävää\", vastasi Jeesus. He sanoivat hänelle:\n\"Pitäisikö meidän mennä ostamaan kahdellasadalla\ndenaarilla leipiä ja antaa ne heille syötäväksi?\"',34,NULL),
 (800,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',38,'8Jeesus kysyi: \"Montako leipää teillä on? Käykää\nkatsomassa.\" He tekivät niin ja ilmoittivat: \"Viisi,\nja kaksi kalaa.\" ',34,NULL),
 (801,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',39,'9Jeesus käski heidän sijoittaa kaikki\nruokakunnittain aterioimaan vihreälle nurmelle.',34,NULL),
 (802,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',40,'0Ihmiset asettuivat istumaan sadan ja\nviidenkymmenen hengen ryhmiin.',34,NULL),
 (803,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',41,'1Sitten hän otti ne\nviisi leipää ja kaksi kalaa, katsoi ylös taivaaseen ja\nlausui kiitoksen. Hän mursi leivät ja antoi palat\nopetuslapsilleen, ja nämä jakoivat ne kansalle. Samoin\nhän jakoi kaikkien kesken ne kaksi kalaa.',34,NULL),
 (804,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',42,'2Kaikki\nsöivät kyllikseen.',34,NULL),
 (805,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',43,'3Leiväntähteitä kerättiin\nkaksitoista täyttä korillista, ja kalaakin jäi.',34,NULL),
 (806,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',44,'4Aterioimassa oli ollut viisituhatta miestä.   ',34,NULL),
 (807,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',47,'7Illan tullessa vene oli keskellä järveä ja Jeesus\nyksin maissa.',34,NULL),
 (808,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',48,'8Hän näki, että opetuslapsilla oli\ntäysi työ soutaa vastatuuleen. Neljännen yövartion*vaiheilla hän tuli vettä pitkin kävellen heitä kohti\nja aikoi mennä heidän ohitseen.',34,NULL),
 (809,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',49,'9Kun he näkivät\nhänen kävelevän vettä pitkin, he luulivat häntä\naaveeksi ja rupesivat huutamaan.',34,NULL),
 (810,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',50,'0Kaikki he näkivät\nhänet ja säikähtivät. Mutta samassa Jeesus jo puhui\nheille: \"Pysykää rauhallisina, minä tässä olen. Älkää\npelätkö.\" ',34,NULL),
 (811,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',51,'1Hän nousi veneeseen heidän luokseen, ja tuuli\ntyyntyi. Opetuslapset olivat hämmästyksestä\nsuunniltaan.',34,NULL),
 (812,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',52,'2Sekään, mitä leiville tapahtui, ei\nollut avannut heidän silmiään. Niin paatuneet heidän\nsydämensä olivat.   ',34,NULL),
 (813,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',1,'Muutamia lainopettajia tuli Jerusalemista, ja\nyhdessä fariseusten kanssa he menivät Jeesuksen luo.',35,NULL),
 (814,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',2,'He panivat merkille, että jotkut hänen\nopetuslapsensa aterioivat epäpuhtain, pesemättömin\nkäsin.',35,NULL),
 (815,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',3,'Fariseukset sen paremmin kuin juutalaiset\nyleensäkään eivät näet käy aterialle ennen kuin ovat\npesseet kätensä, vaikkapa vain tilkkasella vettä. Näin\nhe tarkoin noudattavat isien perinnäissääntöä.',35,NULL),
 (816,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',4,'He\neivät myöskään syö mitään torilta tuotua ennen kuin\novat vihmoneet sen. Monia muitakin tapoja he ovat\nottaneet noudattaakseen, kuten juomamaljojen,\nsaviruukkujen ja pronssiastioiden pesuja. ',35,NULL),
 (817,'Anonymous','2008-10-17 16:32:13','Anonymous','2008-10-17 16:32:13',5,'Niinpä fariseukset ja lainopettajat kysyivät\nJeesukselta: \"Miksi sinun opetuslapsesi eivät elä\nisien perinnäissääntöjen mukaan, vaan aterioivat\nepäpuhtain käsin?\" ',35,NULL),
 (818,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',6,'Hän vastasi heille: \"Te tekopyhät! Oikein on Jesaja\nteistä ennustanut. Onhan kirjoitettu:  ',35,NULL),
 (819,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',8,'Te olette hylänneet Jumalan käskyn ja noudatatte\nihmisten perinnäissääntöjä.\" ',35,NULL),
 (820,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',9,'Ja hän jatkoi: \"Taitavasti te teette tyhjäksi\nJumalan käskyn, jotta voisitte noudattaa omia\nsääntöjänne.',35,NULL),
 (821,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',10,'0Mooses on sanonut: \'Kunnioita isääsi\nja äitiäsi\' ja \'Joka kiroaa isäänsä tai äitiään, häntä\nrangaistakoon kuolemalla\'.',35,NULL),
 (822,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',11,'1Mutta te opetatte\ntoisin. Jos joku sanoo isälleen tai äidilleen: \'Se,\nmitä sinun piti minulta saada, on nyt korban*: olen\nluvannut sen uhrilahjaksi\',',35,NULL),
 (823,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',12,'2te ette salli hänen\nenää tehdä mitään isänsä tai äitinsä hyväksi.',35,NULL),
 (824,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',13,'3Näin\nte perinnäissääntöjä seuratessanne ja opettaessanne\nteette tyhjäksi Jumalan sanan. Ja paljon muutakin te\nteette, samanlaista.\"   ',35,NULL),
 (825,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',17,'7Kun Jeesus sitten lähti väkijoukon keskeltä ja\ntuli kotiin, opetuslapset pyysivät häntä selittämään\narvoitukselliset sanansa.',35,NULL),
 (826,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',18,'8Hän sanoi heille:\n\"Oletteko tekin noin ymmärtämättömiä? Ettekö käsitä,\nettei mikään ulkoapäin ihmiseen tuleva voi saastuttaa\nhäntä?',35,NULL),
 (827,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',19,'9Eihän se mene hänen sydämeensä -- vatsaan\nse menee, ja sieltä se sitten ulostuu.\" Näin Jeesus\nteki selväksi, että kaikki ruoka on puhdasta.',35,NULL),
 (828,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',20,'0Ja\nhän jatkoi: \"Se mikä tulee ihmisestä ulos, se\nsaastuttaa ihmisen.',35,NULL),
 (829,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',21,'1Juuri ihmisen sisältä,\nsydämestä, lähtevät pahat ajatukset, ja niiden mukana\nsiveettömyys, varkaudet, murhat,',35,NULL),
 (830,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',22,'2aviorikokset,\nahneus, häijyys, vilppi, irstaus, pahansuopuus,\nherjaus, ylpeys ja uhmamieli.',35,NULL),
 (831,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',23,'3Kaikki tämä paha\ntulee ihmisen sisältä ja saastuttaa hänet.\"     ',35,NULL),
 (832,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',36,'6Jeesus kielsi ihmisiä kertomasta tästä\nkenellekään, mutta mitä enemmän hän heitä kielsi, sitä\nenemmän he levittivät siitä tietoa.',35,NULL),
 (833,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',37,'7Kaikki olivat\nylen määrin hämmästyksissään ja sanoivat: \"Hyvin hän\non kaiken tehnyt. Kuurot hän saa kuulemaan ja mykät\npuhumaan.\" ',35,NULL),
 (834,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',1,'Siihen aikaan oli taas kerran koolla paljon\nihmisiä. Kun heillä ei ollut syötävää, Jeesus kutsui\nopetuslapset luokseen ja sanoi:',36,NULL),
 (835,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',2,'\"Minun käy sääliksi\nnäitä ihmisiä. He ovat olleet luonani jo kolme päivää,\neikä heillä ole mitään syötävää.',36,NULL),
 (836,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',3,'Jos lähetän heidät\nkotiin nälkäisinä, he uupuvat matkalla. Monet heistä\novat tulleet kaukaakin.\"',36,NULL),
 (837,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',4,'Opetuslapset sanoivat\nhänelle: \"Mistä kukaan täällä asumattomalla seudulla\nvoi hankkia leipää näiden kaikkien ruokkimiseksi?\"',36,NULL),
 (838,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',5,'\"Paljonko teillä on leipiä?\" Jeesus kysyi. He\nvastasivat: \"Seitsemän.\" ',36,NULL),
 (839,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',6,'Jeesus käski väen asettua istumaan. Sitten hän otti\nne seitsemän leipää, lausui kiitoksen ja mursi leivät\nja antoi palat opetuslapsille. Opetuslapset jakoivat\nne sitten kansalle.',36,NULL),
 (840,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',7,'Heillä oli myös muutama pieni\nkala. Jeesus siunasi kalat ja käski tarjota nekin.',36,NULL),
 (841,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',8,'Ihmiset söivät kyllikseen, ja tähteeksi jääneitä\npaloja kerättiin seitsemän korillista.',36,NULL),
 (842,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',9,'Väkeä oli\nnoin neljätuhatta. Kun Jeesus oli lähettänyt heidät pois,',36,NULL),
 (843,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',10,'0hän kohta\nnousi opetuslasten kanssa veneeseen ja meni Dalmanutan\nseudulle.              ',36,NULL),
 (844,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',1,'Hän sanoi vielä: \"Totisesti: tässä joukossa on\nmuutamia, jotka eivät kohtaa kuolemaa ennen kuin\nnäkevät, että Jumalan valtakunta on tullut\nvoimassaan.\"   ',37,NULL),
 (845,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',4,'Sitten heille ilmestyi Elia ja hänen kanssaan\nMooses, ja nämä keskustelivat Jeesuksen kanssa.',37,NULL),
 (846,'Anonymous','2008-10-17 16:32:14','Anonymous','2008-10-17 16:32:14',5,'Pietari puuttui puheeseen ja sanoi Jeesukselle:\n\"Rabbi, on hyvä, että me olemme täällä. Me teemme\nkolme majaa: sinulle ja Moosekselle ja Elialle.\"',37,NULL),
 (847,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',6,'Hän ei näet tiennyt mitä sanoa, sillä he olivat\nkovin peloissaan. ',37,NULL),
 (848,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',7,'Samassa tuli pilvi, joka peitti heidät varjoonsa,\nja pilvestä kuului ääni: \"Tämä on minun rakas Poikani,\nkuulkaa häntä!\"',37,NULL),
 (849,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',8,'Ja yhtäkkiä, kun he katsahtivat\nympärilleen, he eivät enää nähneet siellä ketään muuta\nkuin Jeesuksen yksin. ',37,NULL),
 (850,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',9,'Kun he laskeutuivat vuorenrinnettä, Jeesus varoitti\nheitä kertomasta näkemäänsä kenellekään ennen kuin\nIhmisen Poika olisi noussut kuolleista.',37,NULL),
 (851,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',10,'0He pitivät\nJeesuksen sanat mielessään ja pohtivat keskenään, mitä\nkuolleista nouseminen saattoi merkitä.     ',37,NULL),
 (852,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',19,'9Silloin Jeesus sanoi heille: \"Voi tätä epäuskoista\nsukupolvea! Kuinka kauan minun on vielä oltava teidän\nkeskuudessanne? Kuinka kauan minun pitää kestää teitä?\nTuokaa poika minun luokseni.\"',37,NULL),
 (853,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',20,'0He toivat pojan\nJeesuksen luo. Jeesuksen nähdessään henki heti\nkouristi poikaa, ja tämä kaatui, kieriskeli maassa ja\nkuolasi.',37,NULL),
 (854,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',21,'1Jeesus kysyi pojan isältä: \"Kuinka kauan\nhänellä on ollut tämä vaiva?\" \"Pienestä pitäen\",\nvastasi mies.',37,NULL),
 (855,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',22,'2\"Henki on monet kerrat kaatanut\nhänet, jopa tuleen ja veteen, jotta saisi hänet\ntapetuksi. Sääli meitä ja auta, jos sinä jotakin\nvoit!\"',37,NULL),
 (856,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',23,'3\"Jos voit?\" vastasi Jeesus. \"Kaikki on\nmahdollista sille, joka uskoo.\"',37,NULL),
 (857,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',24,'4Silloin pojan isä\nheti huusi: \"Minä uskon! Auta minua epäuskossani!\" ',37,NULL),
 (858,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',25,'5Kun Jeesus näki, että väkeä tuli aina vain lisää,\nhän käski saastaista henkeä sanoen: \"Mykkä ja kuuro\nhenki, minä käsken sinua: lähde pojasta äläkä enää\nmene häneen!\"',37,NULL),
 (859,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',26,'6Henki huusi, kouristi poikaa rajusti\nja lähti hänestä. Poika jäi makaamaan elottoman\nnäköisenä, ja monet sanoivatkin: \"Nyt hän kuoli.\"',37,NULL),
 (860,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',27,'7Mutta Jeesus tarttui häntä kädestä ja auttoi hänet\njalkeille, ja hän nousi. ',37,NULL),
 (861,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',28,'8Kun Jeesus sitten oli mennyt sisään ja vain\nopetuslapset olivat paikalla, nämä kysyivät häneltä:\n\"Miksi me emme kyenneet ajamaan sitä henkeä pojasta?\"',37,NULL),
 (862,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',29,'9Hän vastasi: \"Tätä lajia ei saa lähtemään muulla\nkuin rukouksella.\"*       ',37,NULL),
 (863,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',39,'9Mutta Jeesus vastasi: \"Älkää estäkö häntä. Eihän yksikään, joka tekee\nvoimateon minun nimessäni, voi heti perään puhua\nminusta pahaa.',37,NULL),
 (864,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',40,'0Joka ei ole meitä vastaan, on\nmeidän puolellamme.',37,NULL),
 (865,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',41,'1Totisesti: joka antaa teille\nmaljallisen vettä sen tähden, että te olette\nKristuksen omia, ei jää palkkaansa vaille.   ',37,NULL),
 (866,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',49,'9\"Jokainen tullaan suolaamaan tulella.',37,NULL),
 (867,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',50,'0Suola on\ntarpeellista. Mutta jos se menettää suolaisuutensa,\nmillä te sen suolaatte? Olkoon teissä suola itsessänne\nja eläkää sovussa toinen toisenne kanssa.\" ',37,NULL),
 (868,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',1,'Jeesus lähti sieltä ja tuli Jordanin itäpuolta\nkulkien Juudean alueelle. Hänen kanssaan kulki nytkin\nsuuri joukko ihmisiä, ja tapansa mukaan hän opetti\nheitä. ',38,NULL),
 (869,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',2,'Hänen luokseen tuli myös fariseuksia, jotka häntä\nkoetellakseen kysyivät, saako mies hylätä vaimonsa.',38,NULL),
 (870,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',3,'Hän vastasi heille: \"Mitä Mooses on siitä\nsäätänyt?\"',38,NULL),
 (871,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',4,'He sanoivat: \"Mooses antoi meille luvan\nkirjoittaa erokirjan ja hylätä vaimon.\"',38,NULL),
 (872,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',5,'Silloin\nJeesus sanoi: \"Te olette kovasydämisiä, siksi hän\nlaati teille sen säännöksen.',38,NULL),
 (873,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',6,'Mutta maailman alussa\nJumala loi ihmisen mieheksi ja naiseksi.',38,NULL),
 (874,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',7,'Sen tähden\nmies jättää isänsä ja äitinsä ja liittyy vaimoonsa,',38,NULL),
 (875,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',8,'niin että nämä kaksi tulevat yhdeksi lihaksi. He\neivät siis enää ole kaksi, he ovat yksi.',38,NULL),
 (876,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',9,'Ja minkä\nJumala on yhdistänyt, sitä älköön ihminen erottako.\" ',38,NULL),
 (877,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',10,'0Kun he olivat keskenään, opetuslapset kysyivät\nuudestaan tätä asiaa.',38,NULL),
 (878,'Anonymous','2008-10-17 16:32:15','Anonymous','2008-10-17 16:32:15',11,'1Jeesus sanoi heille: \"Se,\njoka hylkää vaimonsa ja menee naimisiin toisen kanssa,\non avionrikkoja ja tekee väärin vaimoaan kohtaan.',38,NULL),
 (879,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',12,'2Ja jos vaimo hylkää miehensä ja menee naimisiin\ntoisen kanssa, hänkin tekee aviorikoksen.\"     ',38,NULL),
 (880,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',20,'0\"Opettaja, kaikkea tätä olen noudattanut nuoresta\npitäen\", vastasi mies.',38,NULL),
 (881,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',21,'1Jeesus katsahti häneen,\nrakasti häntä ja sanoi: \"Yksi sinulta puuttuu. Mene ja\nmyy kaikki, mitä sinulla on, ja anna rahat köyhille,\nniin sinulla on aarre taivaassa. Tule sitten ja seuraa\nminua.\"',38,NULL),
 (882,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',22,'2Mies synkistyi näistä sanoista. Hän lähti\nsurullisena pois, sillä hänellä oli paljon omaisuutta. ',38,NULL),
 (883,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',23,'3Jeesus kääntyi, katsoi opetuslapsiinsa ja sanoi:\n\"Kuinka vaikea onkaan niiden, jotka paljon omistavat,\npäästä Jumalan valtakuntaan!\"',38,NULL),
 (884,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',24,'4Opetuslapset\nhämmästelivät hänen sanojaan, mutta Jeesus jatkoi:\n\"Lapseni, Jumalan valtakuntaan on todella vaikea\npäästä.',38,NULL),
 (885,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',25,'5Helpompi on kamelin mennä neulansilmästä\nkuin rikkaan päästä Jumalan valtakuntaan.\"',38,NULL),
 (886,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',26,'6Opetuslapset olivat yhä enemmän ihmeissään ja\nkyselivät toisiltaan: \"Kuka sitten voi pelastua?\"',38,NULL),
 (887,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',27,'7Jeesus katsoi heihin ja sanoi: \"Ihmiselle se on\nmahdotonta, mutta ei Jumalalle. Jumalalle on kaikki\nmahdollista.\"       ',38,NULL),
 (888,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',41,'1Kun muut kymmenen kuulivat tästä, he suuttuivat\nJaakobille ja Johannekselle.',38,NULL),
 (889,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',42,'2Mutta Jeesus kutsui\nheidät luokseen ja sanoi: \"Te tiedätte, että ne, jotka\novat hallitsijan asemassa, ovat kansojen herroja ja\nmaan mahtavat pitävät kansoja valtansa alla.',38,NULL),
 (890,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',43,'3Niin\nei saa olla teidän keskuudessanne. Joka tahtoo teidän\njoukossanne tulla suureksi, se olkoon toisten\npalvelija,',38,NULL),
 (891,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',44,'4ja joka tahtoo tulla teidän joukossanne\nensimmäiseksi, se olkoon kaikkien orja.',38,NULL),
 (892,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',45,'5Ei Ihmisen\nPoikakaan tullut palveltavaksi, vaan palvelemaan ja\nantamaan henkensä lunnaiksi kaikkien puolesta.\"    ',38,NULL),
 (893,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',1,'Kun he lähestyivät Jerusalemia ja tulivat Betfageen\nja Betaniaan Öljymäen rinteelle, Jeesus lähetti edeltä\nkaksi opetuslastaan',39,NULL),
 (894,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',2,'ja sanoi heille: \"Menkää tuolla\nnäkyvään kylään. Heti kun te tulette sinne, te näette\nkiinni sidotun aasinvarsan, jonka selässä ei kukaan\nvielä ole istunut. Ottakaa se siitä ja tuokaa tänne.',39,NULL),
 (895,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',3,'Jos joku kysyy, miksi te niin teette, vastatkaa,\nettä Herra tarvitsee sitä mutta lähettää sen pian takaisin.\" ',39,NULL),
 (896,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',4,'Opetuslapset lähtivät ja löysivät varsan, joka oli\nsidottu kujalle oven eteen. He ottivat sen.',39,NULL),
 (897,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',5,'Paikalla olevat ihmiset kysyivät: \"Mitä te oikein\nteette? Miksi te viette varsan?\"',39,NULL),
 (898,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',6,'He vastasivat niin\nkuin Jeesus oli käskenyt, ja heidän annettiin mennä.',39,NULL),
 (899,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',7,'He toivat varsan Jeesukselle ja heittivät\nvaatteitaan sen selkään, ja Jeesus nousi ratsaille.',39,NULL),
 (900,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',8,'Monet levittivät vaatteitaan tielle, toiset taas\nlehviä, joita he katkoivat tienvarresta.',39,NULL),
 (901,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',9,'Ja ne,\njotka kulkivat hänen edellään ja perässään, huusivat:   ',39,NULL),
 (902,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',11,'1Niin Jeesus saapui Jerusalemiin. Hän meni\ntemppeliin ja katseli siellä kaikkea, mutta koska oli\njo myöhä, hän lähti kahdentoista opetuslapsensa kanssa\nBetaniaan.     ',39,NULL),
 (903,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',18,'8Ylipapit ja lainopettajat kuulivat tämän ja\nmiettivät, miten saisivat Jeesuksen raivatuksi pois\ntieltä. He pelkäsivät häntä, koska hänen opetuksensa\noli tehnyt ihmisiin voimakkaan vaikutuksen. ',39,NULL),
 (904,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',19,'9Illan tultua Jeesus ja opetuslapset lähtivät\nkaupungin ulkopuolelle.     ',39,NULL),
 (905,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',31,'1He neuvottelivat keskenään: \"Jos sanomme:\n\'Taivaasta\', hän sanoo: \'Miksi sitten ette uskoneet\nhäntä?\'',39,NULL),
 (906,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',32,'2Sanoisimmeko sitten: \'Ihmisistä\'?\" Mutta\nhe pelkäsivät kansaa, sillä kaikkien mielestä Johannes\noli todella profeetta.',39,NULL),
 (907,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',33,'3Niin he vastasivat\nJeesukselle: \"Emme tiedä.\" Silloin Jeesus sanoi: \"En\nminäkään sitten sano teille, millä valtuuksilla minä\nkaikkea tätä teen.\" ',39,NULL),
 (908,'Anonymous','2008-10-17 16:32:16','Anonymous','2008-10-17 16:32:16',1,'Jeesus alkoi puhua heille vertauksin: \"Mies istutti viinitarhan, ympäröi sen aidalla, louhi\nkallioon viinikuurnan ja rakensi vartiotornin. Sitten\nhän vuokrasi tarhan viininviljelijöille ja muutti itse\npois maasta. ',40,NULL),
 (909,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',2,'\"Sovittuun aikaan hän lähetti palvelijansa\nviinitarhan viljelijöiden luo, jotta saisi heiltä\nosansa tarhan sadosta.',40,NULL),
 (910,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',3,'Mutta nämä ottivat miehen\nkiinni, pieksivät hänet ja lähettivät takaisin tyhjin\nkäsin.',40,NULL),
 (911,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',4,'Omistaja lähetti heidän luokseen toisen\npalvelijan, mutta tätäkin he pahoinpitelivät ja\nhäpäisivät.',40,NULL),
 (912,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',5,'Hän lähetti taas uuden palvelijan, ja\ntämän he tappoivat. Samoin kävi seuraavienkin: toiset\nhe pieksivät, toiset tappoivat.',40,NULL),
 (913,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',6,'Nyt oli jäljellä\nenää yksi, hänen rakas poikansa. Tämän hän lähetti\nvuokraajien luo viimeiseksi ajatellen: \'Minun omaan\npoikaani he eivät sentään uskalla koskea.\'',40,NULL),
 (914,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',7,'Mutta\nnämä sanoivat toisilleen: \'Hän on perillinen. Tapetaan\nhänet, niin perintö on meidän.\'',40,NULL),
 (915,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',8,'He ottivat hänet\nkiinni, tappoivat hänet ja heittivät ulos\nviinitarhasta. ',40,NULL),
 (916,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',9,'\"Mitä viinitarhan omistaja nyt tekee? Hän tulee ja\nottaa nuo viljelijät hengiltä ja antaa viinitarhansa\ntoisille.',40,NULL),
 (917,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',10,'0Olette kai lukeneet kirjoituksista tämän\nkohdan:   ',40,NULL),
 (918,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',12,'2Neuvoston jäsenet olisivat halunneet ottaa\nJeesuksen kiinni, sillä he ymmärsivät, että Jeesus oli\nvertauksessaan puhunut heistä. Mutta koska he\npelkäsivät kansaa, he antoivat hänen olla ja lähtivät\npois.   ',40,NULL),
 (919,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',15,'5Jeesus tiesi heidän teeskentelevän ja sanoi:\n\"Miksi te yritätte saada minut ansaan? Näyttäkää\nminulle denaarin raha.\"',40,NULL),
 (920,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',16,'6He ottivat esiin rahan, ja\nhän kysyi: \"Kenen kuva ja nimi siinä on?\" \"Keisarin\",\nhe vastasivat.',40,NULL),
 (921,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',17,'7Silloin Jeesus sanoi heille:\n\"Antakaa keisarille mikä keisarille kuuluu ja\nJumalalle mikä Jumalalle kuuluu.\" Tämä vastaus jätti\nheidät ymmälle.   ',40,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (922,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',24,'4Jeesus vastasi heille: \"Te kuljette eksyksissä, ja\nmistä syystä? Koska ette tunne kirjoituksia ettekä\nJumalan voimaa.',40,NULL),
 (923,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',25,'5Kun noustaan kuolleista, ei oteta\nvaimoa eikä mennä vaimoksi. Ylösnousseet ovat kuin\nenkelit taivaassa.',40,NULL),
 (924,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',26,'6Ja kun nyt puhumme kuolleiden\nherättämisestä -- ettekö ole Mooseksen kirjasta\nlukeneet kertomusta palavasta pensaasta? Siinä Jumala\npuhuu Moosekselle ja sanoo: \'Minä olen Abrahamin\nJumala, Iisakin Jumala ja Jaakobin Jumala.\'',40,NULL),
 (925,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',27,'7Ei hän\nole kuolleiden Jumala, vaan elävien. Te olette pahasti\neksyksissä.\"   ',40,NULL),
 (926,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',32,'2Lainopettaja sanoi hänelle: \"Oikein, opettaja!\nTotta puhuit, kun sanoit, että Herra on ainoa Jumala,\nei ole muita kuin hän.',40,NULL),
 (927,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',33,'3Ja kun rakastaa häntä koko\nsydämestään, kaikella ymmärryksellään ja kaikella\nvoimallaan ja rakastaa lähimmäistään niin kuin\nitseään, se on enemmän kuin polttouhrit ja kaikki muut\nuhrit.\"',40,NULL),
 (928,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',34,'4Jeesus näki, että hän vastasi viisaasti,\nja sanoi hänelle: \"Sinä et ole kaukana Jumalan\nvaltakunnasta.\" Kukaan ei enää rohjennut kysyä Jeesukselta mitään.    ',40,NULL),
 (929,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',37,'7Daavid itse sanoo Messiasta herraksi. Kuinka\nMessias silloin voi olla Daavidin poika?\"      ',40,NULL),
 (930,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',1,'Kun Jeesus oli lähdössä temppelistä, sanoi eräs\nhänen opetuslapsistaan: \"Opettaja, katso! Mitkä kivet,\nmikä rakennus!\"',41,NULL),
 (931,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',2,'Jeesus vastasi: \"Katso nyt näitä\nmahtavia rakennuksia. Kaikki revitään maahan, tänne ei\njää kiveä kiven päälle.\"   ',41,NULL),
 (932,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',5,'Jeesus alkoi puhua heille: \"Varokaa, ettei kukaan johda teitä harhaan.',41,NULL),
 (933,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',6,'Monet\ntulevat esiintymään minun nimelläni. He sanovat: \'Minä\nse olen\', ja eksyttävät monia.',41,NULL),
 (934,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',7,'Kun kuulette\ntaistelun ääniä ja sanomia sodista, älkää pelästykö.\nNiin täytyy käydä, mutta se ei vielä merkitse loppua.',41,NULL),
 (935,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',8,'Kansa nousee kansaa vastaan ja valtakunta\nvaltakuntaa vastaan, joka puolella on maanjäristyksiä\nja tulee nälänhätä. Tämä on synnytystuskien alkua. ',41,NULL),
 (936,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',9,'\"Pitäkää varanne! Teidät viedään oikeuteen ja teitä\npiestään synagogissa. Maaherrojen ja kuninkaiden eteen\nte joudutte minun nimeni tähden, todistukseksi heille.',41,NULL),
 (937,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',10,'0Mutta sitä ennen on evankeliumi julistettava\nkaikille kansoille.',41,NULL),
 (938,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',11,'1Kun teitä viedään\nluovutettaviksi viranomaisille, älkää etukäteen olko\nhuolissanne siitä mitä puhuisitte. Puhukaa ne sanat,\njotka teille tuona hetkenä annetaan. Silloin ette puhu\nte, vaan Pyhä Henki. ',41,NULL),
 (939,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',12,'2\"Veli antaa veljensä surmattavaksi, isä lapsensa,\nja lapset nousevat vanhempiaan vastaan ja tuottavat\nheille kuoleman.',41,NULL),
 (940,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',13,'3Kaikki vihaavat teitä minun\nnimeni tähden, mutta se, joka kestää loppuun asti,\npelastuu.   ',41,NULL),
 (941,'Anonymous','2008-10-17 16:32:17','Anonymous','2008-10-17 16:32:17',21,'1\"Jos joku sanoo teille: \'Täällä on Messias\', tai:\n\'Katso, Messias on tuolla\', älkää uskoko.',41,NULL),
 (942,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',22,'2Sillä\nvääriä messiaita ja vääriä profeettoja ilmaantuu, ja\nhe tekevät tunnustekoja ja ihmeitä johtaakseen, jos\nmahdollista, valitut harhaan.',41,NULL),
 (943,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',23,'3Pitäkää siis\nvaranne. Minä olen ennalta ilmoittanut teille kaiken.   ',41,NULL),
 (944,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',28,'8\"Ottakaa oppia vertauskuvasta, jonka viikunapuu\ntarjoaa: kun sen oksa virkoaa ja alkaa tulla lehteen,\nte tiedätte, että kesä on lähellä.',41,NULL),
 (945,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',29,'9Samalla tavoin\nte nähdessänne tämän tapahtuvan tiedätte, että se aika\non lähellä, aivan ovella.',41,NULL),
 (946,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',30,'0Totisesti: tämä\nsukupolvi ei katoa ennen kuin kaikki tämä tapahtuu.',41,NULL),
 (947,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',31,'1Taivas ja maa katoavat, mutta minun sanani eivät\nkatoa.',41,NULL),
 (948,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',32,'2Mutta sitä päivää ja hetkeä ei tiedä\nkukaan, eivät enkelit taivaassa eikä edes Poika, ei\nkukaan muu kuin Isä.    ',41,NULL),
 (949,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',1,'Oli kaksi päivää pääsiäiseen ja happamattoman\nleivän juhlaan. Ylipapit ja lainopettajat miettivät,\nmillaisella juonella he saisivat Jeesuksen käsiinsä\ntappaakseen hänet.',42,NULL),
 (950,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',2,'\"Ei kuitenkaan kesken juhlan\",\nhe sanoivat, \"ettei kansa ala mellakoida.\"   ',42,NULL),
 (951,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',6,'Mutta Jeesus sanoi: \"Antakaa hänen olla. Miksi te\npahoitatte hänen mielensä? Hän teki minulle hyvän\nteon.',42,NULL),
 (952,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',7,'Köyhät teillä on luonanne aina, ja te voitte\ntehdä heille hyvää milloin tahdotte, mutta minua\nteillä ei aina ole.',42,NULL),
 (953,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',8,'Hän teki minkä voi. Hän voiteli\nedeltäkäsin minun ruumiini hautaamista varten.',42,NULL),
 (954,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',9,'Totisesti: kaikkialla maailmassa, missä ikinä\nevankeliumin sanoma julistetaan, tullaan muistamaan\nmyös tämä nainen ja kertomaan, mitä hän teki.\"     ',42,NULL),
 (955,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',17,'7Illan tultua Jeesus saapui sinne kahdentoista\nopetuslapsensa kanssa.',42,NULL),
 (956,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',18,'8Kun he olivat asettuneet\naterioimaan, Jeesus sanoi: \"Totisesti: yksi teistä\nkavaltaa minut -- mies, joka syö minun kanssani.\"',42,NULL),
 (957,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',19,'9Opetuslapset järkyttyivät ja alkoivat yksi\ntoisensa jälkeen kysellä: \"En kai minä?\"',42,NULL),
 (958,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',20,'0Jeesus\nvastasi heille: \"Yksi teistä kahdestatoista, mies,\njoka syö samasta vadista kuin minä.',42,NULL),
 (959,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',21,'1Ihmisen Poika\nlähtee pois juuri niin kuin kirjoituksissa hänestä\nsanotaan, mutta voi sitä, josta tulee Ihmisen Pojan\nkavaltaja! Sille ihmiselle olisi parempi, ettei hän\nolisi syntynytkään.\"       ',42,NULL),
 (960,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',37,'7Sitten hän tuli takaisin ja tapasi opetuslapset\nnukkumasta. Hän sanoi Pietarille: \"Simon, nukutko\nsinä? Etkö edes hetken vertaa jaksanut valvoa?',42,NULL),
 (961,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',38,'8Valvokaa ja rukoilkaa, ettette joutuisi\nkiusaukseen. Tahtoa ihmisellä on, mutta luonto on\nheikko.\" ',42,NULL),
 (962,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',39,'9Hän meni taas etäämmäksi ja rukoili samoin sanoin.',42,NULL),
 (963,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',40,'0Palatessaan hän jälleen tapasi opetuslapset\nnukkumasta, sillä uni oli alkanut raskaasti painaa\nheidän silmiään. He eivät tienneet, mitä vastaisivat\nhänelle.',42,NULL),
 (964,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',41,'1Tullessaan kolmannen kerran Jeesus sanoi\nheille: \"Yhäkö te nukutte ja lepäätte? Kaikki on\nselvää. Hetki on tullut. Ihmisen Poika annetaan\nsyntisten käsiin.',42,NULL),
 (965,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',42,'2Nouskaa, nyt me lähdemme! Minun\nkavaltajani on jo lähellä.\"   ',42,NULL),
 (966,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',48,'8Jeesus kääntyi miesjoukon puoleen ja sanoi: \"Tehän\nolette lähteneet kuin rosvon kimppuun! Miekat ja\nseipäät käsissä te tulette minua vangitsemaan.',42,NULL),
 (967,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',49,'9Minä olen joka päivä teidän nähtenne opettanut\ntemppelissä, ettekä te ole ottaneet minua kiinni.\nMutta kirjoitusten oli käytävä toteen.\"',42,NULL),
 (968,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',50,'0Silloin\nne, jotka olivat Jeesuksen kanssa, jättivät hänet ja\npakenivat. ',42,NULL),
 (969,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',51,'1Jeesuksen mukaan oli lähtenyt nuori mies, jolla ei\nollut yllään muuta kuin pellavainen vaate. Hänet\notettiin kiinni,',42,NULL),
 (970,'Anonymous','2008-10-17 16:32:18','Anonymous','2008-10-17 16:32:18',52,'2mutta hän riistäytyi vaatteestaan\nja pakeni alasti.   ',42,NULL),
 (971,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',55,'5Ylipapit ja koko neuvosto etsivät todistusta\nJeesusta vastaan saadakseen hänet surmatuksi.\nSellaista ei kuitenkaan löytynyt.',42,NULL),
 (972,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',56,'6Monet kyllä\ntodistivat valheellisesti häntä vastaan, mutta\ntodistukset olivat ristiriitaisia.',42,NULL),
 (973,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',57,'7Silloin astui\nesiin muutamia, jotka esittivät häntä vastaan väärän\ntodistuksen:',42,NULL),
 (974,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',58,'8\"Me olemme kuulleet hänen sanovan:\n\'Minä hajotan tämän ihmiskäsin tehdyn temppelin ja\nrakennan kolmessa päivässä uuden, joka ei ole ihmisten\ntekemä.\'\"',42,NULL),
 (975,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',59,'9Mutta tässäkään asiassa todistukset\neivät pitäneet yhtä. ',42,NULL),
 (976,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',60,'0Silloin ylipappi nousi paikaltaan, astui esiin ja\nkysyi Jeesukselta: \"Etkö lainkaan vastaa heidän\nsyytöksiinsä?\"',42,NULL),
 (977,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',61,'1Mutta Jeesus pysyi vaiti, hän ei\nvastannut mitään. Silloin ylipappi teki hänelle uuden\nkysymyksen: \"Oletko Messias, ylistetyn Jumalan poika?\"',42,NULL),
 (978,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',62,'2\"Olen\", vastasi Jeesus, \"ja te saatte nähdä\nIhmisen Pojan istuvan Voiman oikealla puolella ja\ntulevan taivaan pilvien keskellä.\"',42,NULL),
 (979,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',63,'3Silloin\nylipappi repäisi vaatteensa ja sanoi: \"Mitä me enää\ntodistajia tarvitsemme!',42,NULL),
 (980,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',64,'4Kuulittehan, miten hän\nherjasi Jumalaa. Mitä ehdotatte?\" Kaikki olivat yhtä\nmieltä siitä, että hän oli syyllinen ja ansaitsi\nkuoleman. ',42,NULL),
 (981,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',65,'5Muutamat alkoivat nyt sylkeä hänen päälleen. He\npeittivät hänen kasvonsa, iskivät häntä nyrkillä ja\nsanoivat: \"Profetoi nyt!\" Vartijatkin löivät häntä.   ',42,NULL),
 (982,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',1,'Heti aamulla ylipapit pitivät neuvottelun\nvanhimpien ja lainopettajien kanssa, ja sitten\nneuvosto teki päätöksen: Jeesus pantiin köysiin,\nvietiin pois ja luovutettiin Pilatukselle.   ',43,NULL),
 (983,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',6,'Juhlan aikana maaherra aina päästi vapaaksi yhden\nvangin, sen jota kansa pyysi.',43,NULL),
 (984,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',7,'Sillä kertaa oli\nvangittuna kapinoitsijoita, jotka kapinan aikana\nolivat tehneet murhan. Heidän joukossaan oli Barabbas-\nniminen mies.',43,NULL),
 (985,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',8,'Pilatuksen luo kerääntyi nyt\nväkijoukko, joka pyysi häntä tekemään niin kuin hänen\ntapansa oli.',43,NULL),
 (986,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',9,'Pilatus kysyi: \"Tahdotteko, että\nvapautan teille juutalaisten kuninkaan?\"',43,NULL),
 (987,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',10,'0Hän näet\nymmärsi, että ylipapit olivat pelkästä kateudesta\njättäneet Jeesuksen hänen käsiinsä.',43,NULL),
 (988,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',11,'1Ylipapit\nkuitenkin kiihottivat väkijoukkoa pyytämään, että hän\npikemminkin vapauttaisi Barabbaksen.',43,NULL),
 (989,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',12,'2Vielä Pilatus\nkysyi kansalta: \"Mitä minä sitten teen tälle, jota te\nsanotte juutalaisten kuninkaaksi?\"',43,NULL),
 (990,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',13,'3He huusivat:\n\"Ristiinnaulitse!\"',43,NULL),
 (991,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',14,'4\"Mitä pahaa hän on tehnyt?\"\nkysyi Pilatus. Mutta he huusivat entistä kovemmin:\n\"Ristiinnaulitse se mies!\" ',43,NULL),
 (992,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',15,'5Tehdäkseen kansalle mieliksi Pilatus päästi\nBarabbaksen vapaaksi, mutta Jeesuksen hän ruoskitti ja\nluovutti ristiinnaulittavaksi.     ',43,NULL),
 (993,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',25,'5Oli kolmas tunti, kun he ristiinnaulitsivat\nhänet.*',43,NULL),
 (994,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',26,'6Kaikkien nähtäväksi oli kirjoitettu hänen\ntuomionsa syy: \"Juutalaisten kuningas\".',43,NULL),
 (995,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',27,'7Samalla\nkertaa he ristiinnaulitsivat kaksi rosvoa, toisen\nhänen oikealle, toisen hänen vasemmalle puolelleen.\n[',43,NULL),
 (996,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',28,'8Näin kävivät toteen kirjoitusten sanat: \"Hänet\nluettiin rikollisten joukkoon.\"]* ',43,NULL),
 (997,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',29,'9Ohikulkijat pilkkasivat häntä. Päätään nyökyttäen\nhe sanoivat: \"No niin, sinähän pystyt hajottamaan\ntemppelin ja rakentamaan sen uudelleen kolmessa\npäivässä.',43,NULL),
 (998,'Anonymous','2008-10-17 16:32:19','Anonymous','2008-10-17 16:32:19',30,'0Pelasta nyt itsesi, tule alas ristiltä!\"',43,NULL),
 (999,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',31,'1Ylipapit yhtyivät hekin pilkkaan yhdessä\nlainopettajien kanssa. He puhuivat keskenään: \"Muita\nhän kyllä on auttanut, mutta itseään hän ei pysty\nauttamaan.',43,NULL),
 (1000,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',32,'2Tulkoon nyt ristiltä alas -- Messias,\nIsraelin kuningas! Kun sen näemme, me uskomme häneen.\"\nMyös ne, jotka oli ristiinnaulittu yhdessä hänen\nkanssaan, pilkkasivat häntä.   ',43,NULL),
 (1001,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',38,'8Silloin temppelin väliverho repesi kahtia,\nylhäältä alas asti.',43,NULL),
 (1002,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',39,'9Kun vastapäätä seisova\nsadanpäällikkö näki Jeesuksen tällä tavoin kuolevan,\nhän sanoi: \"Tämä mies oli todella Jumalan Poika!\" ',43,NULL),
 (1003,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',40,'0Paikalla oli myös naisia jonkin matkan päässä tätä\nkatselemassa. Heidän joukossaan olivat Magdalan Maria,\ntoinen Maria, joka oli nuoremman Jaakobin ja Joosefin\näiti, sekä Salome.',43,NULL),
 (1004,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',41,'1Jo Galileassa he olivat\nkulkeneet Jeesuksen mukana ja palvelleet häntä. Siellä\noli monia muitakin naisia, jotka olivat tulleet\nJerusalemiin Jeesuksen mukana.   ',43,NULL),
 (1005,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',1,'Kun sapatti oli ohi, Magdalan Maria, Jaakobin äiti\nMaria ja Salome ostivat tuoksuöljyä mennäkseen\nvoitelemaan Jeesuksen.',44,NULL),
 (1006,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',2,'Sapatin jälkeisenä päivänä\nani varhain, kohta auringon noustua he lähtivät\nhaudalle.',44,NULL),
 (1007,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',3,'Matkalla he puhuivat keskenään: \"Kuka\nauttaisi meitä ja vierittäisi kiven hautakammion\novelta?\"',44,NULL),
 (1008,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',4,'Mutta tultuaan paikalle he huomasivat,\nettä kivi oli vieritetty pois; se oli hyvin suuri\nkivi. ',44,NULL),
 (1009,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',5,'Sisälle hautaan mentyään he näkivät oikealla\npuolella istumassa nuorukaisen, jolla oli yllään\nvalkoiset vaatteet. He säikähtivät.',44,NULL),
 (1010,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',6,'Mutta\nnuorukainen sanoi: \"Älkää pelästykö. Te etsitte Jeesus\nNasaretilaista, joka oli ristiinnaulittu. Hän on\nnoussut kuolleista, ei hän ole täällä. Tuossa on\npaikka, johon hänet pantiin.',44,NULL),
 (1011,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',7,'Menkää nyt sanomaan\nhänen opetuslapsilleen, myös Pietarille: \'Hän menee\nteidän edellänne Galileaan. Siellä te näette hänet,\nniin kuin hän itse teille sanoi.\'\" ',44,NULL),
 (1012,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',8,'Ulos tultuaan naiset pakenivat haudalta\njärkytyksestä vapisten. He eivät kertoneet kenellekään\nmitään, sillä he pelkäsivät.   ',44,NULL),
 (1013,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',12,'2Tämän jälkeen Jeesus ilmestyi hahmoltaan vieraana\nkahdelle heistä, kun he olivat kulkemassa pois\nkaupungista.',44,NULL),
 (1014,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',13,'3Myös nämä menivät viemään toisille\nsanaa, mutta heitäkään ei uskottu. ',44,NULL),
 (1015,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',14,'4Viimein Jeesus ilmestyi myös yhdelletoista\nopetuslapselleen heidän ollessaan aterialla. Hän\nmoitti heidän epäuskoaan ja heidän sydämensä kovuutta,\nkun he eivät olleet uskoneet niitä, jotka olivat\nnähneet hänet kuolleista nousseena.',44,NULL),
 (1016,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',15,'5Hän sanoi\nheille: \"Menkää kaikkialle maailmaan ja julistakaa\nevankeliumi kaikille luoduille.',44,NULL),
 (1017,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',16,'6Joka sen uskoo ja\nsaa kasteen, on pelastuva. Joka ei usko, se tuomitaan\nkadotukseen.',44,NULL),
 (1018,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',17,'7Ja niitä, jotka uskovat, seuraavat\nnämä tunnusmerkit: Minun nimissäni he ajavat pois\npahoja henkiä. He puhuvat vierailla kielillä.',44,NULL),
 (1019,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',18,'8He\ntarttuvat käsin käärmeisiin, ja vaikka he juovat\ntappavaa myrkkyä, se ei vahingoita heitä. He panevat\nkätensä sairaiden päälle, ja nämä paranevat.\" ',44,NULL),
 (1020,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',19,'9Kun Herra Jeesus oli puhunut heille tämän, hänet\notettiin ylös taivaaseen ja hän istuutui Jumalan\noikealle puolelle.',44,NULL),
 (1021,'Anonymous','2008-10-17 16:32:20','Anonymous','2008-10-17 16:32:20',20,'0Opetuslapset lähtivät matkaan\nja saarnasivat kaikkialla. Herra toimi heidän kanssaan\nja vahvisti sanan tunnusmerkeillä.] ',44,NULL),
 (1022,'Anonymous','2008-10-17 16:32:22','Anonymous','2008-10-17 16:32:22',1,'Jo monet ovat ryhtyneet työhön ja laatineet\nkertomuksia niistä asioista, jotka meidän\nkeskuudessamme ovat toteutuneet,',45,NULL),
 (1023,'Anonymous','2008-10-17 16:32:22','Anonymous','2008-10-17 16:32:22',2,'sen mukaan kuin\nmeille ovat kertoneet ne, jotka alusta alkaen olivat\nsilminnäkijöitä ja joista tuli sanan palvelijoita.',45,NULL),
 (1024,'Anonymous','2008-10-17 16:32:22','Anonymous','2008-10-17 16:32:22',3,'Niin olen nyt minäkin, otettuani alusta asti\nkaikesta tarkan selon, päättänyt kirjoittaa\nyhtenäisen esityksen sinua varten, kunnioitettu\nTeofilos,',45,NULL),
 (1025,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',4,'jotta tietäisit, kuinka luotettavaa\nsinulle annettu opetus on.    ',45,NULL),
 (1026,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',8,'Eräänä päivänä Sakarias oli osastonsa vuorolla\ntoimittamassa papin tehtäviä Jumalan edessä.',45,NULL),
 (1027,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',9,'Palvelustoimia tavan mukaan arvottaessa tuli\nhänen tehtäväkseen mennä Herran temppeliin\ntoimittamaan suitsutusuhri.',45,NULL),
 (1028,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',10,'0Kansanjoukko oli\nsuitsuttamisen aikana ulkopuolella ja rukoili.',45,NULL),
 (1029,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',11,'1Silloin Sakariaalle ilmestyi Herran enkeli\nsuitsutusalttarin oikealla puolella. ',45,NULL),
 (1030,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',12,'2Enkelin nähdessään Sakarias säikähti ja joutui\npelon valtaan.',45,NULL),
 (1031,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',13,'3Mutta enkeli puhui hänelle: \"Älä\npelkää, Sakarias. Rukouksesi on kuultu, vaimosi\nElisabet synnyttää sinulle pojan ja sinä annat\nhänelle nimeksi Johannes.',45,NULL),
 (1032,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',14,'4Ilo ja riemu täyttävät\nsinut, ja monet iloitsevat hänen syntymästään.',45,NULL),
 (1033,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',15,'5Hän on oleva suuri Jumalan mies. Viiniä ja\nväkijuomaa hän ei juo. Jo äitinsä kohdusta asti hän\non täynnä Pyhää Henkeä.',45,NULL),
 (1034,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',16,'6Hän kääntää Israelin\nkansasta monet jälleen Herran, heidän Jumalansa,\npuoleen.',45,NULL),
 (1035,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',17,'7Hän kulkee Herran edelläkävijänä Elian\nhengessä ja voimassa, hän kääntää isien sydämet\nlasten puoleen ja ohjaa tottelemattomat ajattelemaan\nhurskaiden tavoin, näin valmistaakseen kansan Herran\ntuloa varten.\" ',45,NULL),
 (1036,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',18,'8Sakarias kysyi enkeliltä: \"Mistä voin tietää,\nettä niin käy? Minähän olen jo vanha, ja vaimonikin\non iäkäs.\"',45,NULL),
 (1037,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',19,'9Enkeli vastasi: \"Minä olen Gabriel,\nyksi niistä, jotka seisovat Jumalan edessä. Minut on\nlähetetty puhumaan sinulle ja tuomaan tätä iloista\nsanomaa.',45,NULL),
 (1038,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',20,'0Mutta sinun suusi mykistyy nyt, etkä\nkykene puhumaan ennen kuin sinä päivänä, jona tämä\ntapahtuu, koska et uskonut sanojani, jotka aikanaan\nkäyvät toteen.\" ',45,NULL),
 (1039,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',21,'1Kansa odotti Sakariasta, ja kaikki kummeksuivat\nsitä, että hän viipyi temppelissä niin pitkään.',45,NULL),
 (1040,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',22,'2Kun hän sitten tuli ulos, hän ei kyennyt\npuhumaan heille, ja siitä he ymmärsivät, että hän\noli nähnyt temppelissä näyn. Hän vain viittoili\nheille ja jäi mykäksi. ',45,NULL),
 (1041,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',23,'3Palveluspäivien päätyttyä Sakarias meni\nkotiinsa,',45,NULL),
 (1042,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',24,'4ja jonkin ajan kuluttua hänen vaimonsa\nElisabet tuli raskaaksi. Viisi kuukautta Elisabet\npysytteli poissa näkyviltä. Hän ajatteli:',45,NULL),
 (1043,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',25,'5\"Tämän\non Herra minulle tehnyt. Hän on katsonut minun\npuoleeni ja päästää minut ihmisten halveksunnasta.\"         ',45,NULL),
 (1044,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',56,'6Maria viipyi Elisabetin luona noin kolme\nkuukautta ja palasi sitten kotiinsa.   ',45,NULL),
 (1045,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',65,'5Sillä seudulla joutuivat kaikki pelon valtaan,\nja näistä tapahtumista puhuttiin laajalti koko\nJuudean vuoriseudulla.',45,NULL),
 (1046,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',66,'6Ne, jotka niistä\nkuulivat, painoivat kaiken mieleensä ja sanoivat:\n\"Mikähän tästä lapsesta tulee?\" Sillä Herran käsi\noli hänen yllään.     ',45,NULL),
 (1047,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',80,'0Lapsi kasvoi ja vahvistui Hengessä. Hän oli\nautiomaassa siihen päivään saakka, jolloin hänen oli\nmäärä astua Israelin eteen. ',45,NULL),
 (1048,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',1,'Siihen aikaan antoi keisari Augustus käskyn, että\nkoko valtakunnassa oli toimitettava verollepano.',46,NULL),
 (1049,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',2,'Tämä verollepano oli ensimmäinen ja tapahtui\nQuiriniuksen ollessa Syyrian käskynhaltijana.',46,NULL),
 (1050,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',3,'Kaikki menivät kirjoittautumaan veroluetteloon,\nkukin omaan kaupunkiinsa. ',46,NULL),
 (1051,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',4,'Niin myös Joosef lähti Galileasta, Nasaretin\nkaupungista ja meni verollepanoa varten Juudeaan,\nDaavidin kaupunkiin Betlehemiin, sillä hän kuului\nDaavidin sukuun.',46,NULL),
 (1052,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',5,'Hän lähti sinne yhdessä\nkihlattunsa Marian kanssa, joka odotti lasta.',46,NULL),
 (1053,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',6,'Heidän siellä ollessaan tuli Marian synnyttämisen\naika,',46,NULL),
 (1054,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',7,'ja hän synnytti pojan, esikoisensa. Hän\nkapaloi lapsen ja pani hänet seimeen, koska heille\nei ollut tilaa majapaikassa. ',46,NULL),
 (1055,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',8,'Sillä seudulla oli paimenia yöllä ulkona\nvartioimassa laumaansa.',46,NULL),
 (1056,'Anonymous','2008-10-17 16:32:23','Anonymous','2008-10-17 16:32:23',9,'Yhtäkkiä heidän edessään\nseisoi Herran enkeli, ja Herran kirkkaus ympäröi\nheidät. Pelko valtasi paimenet,',46,NULL),
 (1057,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',10,'0mutta enkeli\nsanoi heille: \"Älkää pelätkö! Minä ilmoitan teille\nilosanoman, suuren ilon koko kansalle.',46,NULL),
 (1058,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',11,'1Tänään on\nteille Daavidin kaupungissa syntynyt Vapahtaja. Hän\non Kristus*, Herra.',46,NULL),
 (1059,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',12,'2Tämä on merkkinä teille: te\nlöydätte lapsen, joka makaa kapaloituna seimessä.\"',46,NULL),
 (1060,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',13,'3Ja samalla hetkellä oli enkelin ympärillä suuri\ntaivaallinen sotajoukko, joka ylisti Jumalaa sanoen:   ',46,NULL),
 (1061,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',15,'5Kun enkelit olivat menneet takaisin taivaaseen,\npaimenet sanoivat toisilleen: \"Nyt Betlehemiin!\nSiellä me näemme sen, mitä on tapahtunut, sen, minkä\nHerra meille ilmoitti.\"',46,NULL),
 (1062,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',16,'6He lähtivät kiireesti ja\nlöysivät Marian ja Joosefin ja lapsen, joka makasi\nseimessä.',46,NULL),
 (1063,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',17,'7Tämän nähdessään he kertoivat, mitä\nheille oli lapsesta sanottu.',46,NULL),
 (1064,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',18,'8Kaikki, jotka\nkuulivat paimenten sanat, olivat ihmeissään.',46,NULL),
 (1065,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',19,'9Mutta Maria kätki sydämeensä kaiken, mitä oli\ntapahtunut, ja tutkisteli sitä. ',46,NULL),
 (1066,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',20,'0Paimenet palasivat kiittäen ja ylistäen Jumalaa\nsiitä, mitä olivat kuulleet ja nähneet. Kaikki oli\njuuri niin kuin heille oli sanottu.       ',46,NULL),
 (1067,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',33,'3Jeesuksen isä ja äiti olivat ihmeissään siitä,\nmitä hänestä sanottiin.',46,NULL),
 (1068,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',34,'4Simeon siunasi heitä ja\nsanoi Marialle, lapsen äidille: \"Tämä lapsi on pantu\nkoetukseksi: monet israelilaiset kompastuvat ja\nmonet nousevat. Hänet on pantu merkiksi, jota ei\ntunnusteta,',46,NULL),
 (1069,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',35,'5ja sinun omankin sydämesi läpi on\nmiekka käyvä. Näin tulevat julki monien sisimmät\najatukset.\" ',46,NULL),
 (1070,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',36,'6Siellä oli myös naisprofeetta Hanna, Asserin\nheimoon kuuluvan Penuelin tytär. Hän oli jo hyvin\nvanha. Mentyään neitsyenä naimisiin hän oli elänyt\nmiehensä kanssa seitsemän vuotta,',46,NULL),
 (1071,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',37,'7mutta nyt hän\noli ollut leskenä jo kahdeksankymmenenneljän vuoden\najan. Hän ei poistunut temppelistä minnekään, vaan\npalveli Jumalaa yötä päivää paastoten ja rukoillen.',46,NULL),
 (1072,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',38,'8Juuri sillä hetkellä hän tuli paikalle, ja hän\nylisti Jumalaa ja puhui lapsesta kaikille, jotka\nodottivat Jerusalemin lunastusta. ',46,NULL),
 (1073,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',39,'9Kun he olivat tehneet kaiken, mitä Herran laki\nvaati, he palasivat Galileaan kotikaupunkiinsa\nNasaretiin.',46,NULL),
 (1074,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',40,'0Lapsi kasvoi, vahvistui ja täyttyi\nviisaudella, ja Jumalan armo seurasi häntä.   ',46,NULL),
 (1075,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',46,'6Kolmen päivän kuluttua he löysivät hänet\ntemppelistä. Hän istui opettajien keskellä, kuunteli\nheitä ja teki heille kysymyksiä.',46,NULL),
 (1076,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',47,'7Kaikki, jotka\nkuulivat mitä hän puhui, ihmettelivät hänen\nymmärrystään ja hänen antamiaan vastauksia.',46,NULL),
 (1077,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',48,'8Hänet nähdessään vanhemmat hämmästyivät kovasti,\nja hänen äitinsä sanoi: \"Poikani, miksi teit meille\ntämän? Isäsi ja minä olemme etsineet sinua, ja me\nolimme jo huolissamme.\"',46,NULL),
 (1078,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',49,'9Jeesus vastasi heille:\n\"Mitä te minua etsitte? Ettekö tienneet, että minun\ntulee olla Isäni luona?\"',46,NULL),
 (1079,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',50,'0Mutta he eivät\nymmärtäneet, mitä hän tällä tarkoitti. ',46,NULL),
 (1080,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',51,'1Jeesus lähti kotimatkalle heidän kanssaan, tuli\nNasaretiin ja oli heille kuuliainen. Kaiken, mitä\noli tapahtunut, hänen äitinsä kätki sydämeensä.',46,NULL),
 (1081,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',52,'2Jeesukselle karttui ikää ja viisautta; Jumalan\nja ihmisten suosio seurasi häntä.  ',46,NULL),
 (1082,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',1,'Keisari Tiberiuksen viidentenätoista\nhallitusvuotena, siihen aikaan kun Pontius Pilatus\noli Juudean maaherrana, kun neljännesruhtinaina\nhallitsivat Galileassa Herodes*, Itureassa ja\nTrakonitiksen maassa hänen veljensä Filippos ja\nAbilenessa Lysanias',47,NULL),
 (1083,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',2,'ja kun ylipappeina olivat\nHannas ja Kaifas, tuli Jumalan sana Johannekselle,\nSakariaan pojalle, joka eli autiomaassa.',47,NULL),
 (1084,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',3,'Niin hän\nlähti liikkeelle ja kulki kaikkialla Jordanin\nseudulla saarnaten ja kehottaen ihmisiä kääntymään\nja ottamaan kasteen, jotta synnit annettaisiin\nheille anteeksi.',47,NULL),
 (1085,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',4,'Tapahtui niin kuin on\nkirjoitettuna profeetta Jesajan kirjassa:   ',47,NULL),
 (1086,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',7,'Kun ihmisiä tuli joukoittain kastettavaksi,\nJohannes sanoi heille: \"Te käärmeen sikiöt! Kuka\nteille on sanonut, että te voitte välttää tulevan\nvihan?',47,NULL),
 (1087,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',8,'Tehkää hedelmiä, joissa kääntymyksenne\nnäkyy! Älkää ruvetko ajattelemaan: \'Olemmehan me\nAbrahamin lapsia.\' Minä sanon teille: Jumala pystyy\nherättämään Abrahamille lapsia vaikka näistä\nkivistä.',47,NULL),
 (1088,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',9,'Nyt on jo kirves pantu puun juurelle.\nJokainen puu, joka ei tee hyvää hedelmää, kaadetaan\nja heitetään tuleen.\" ',47,NULL),
 (1089,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',10,'0Ihmiset kysyivät häneltä: \"Mitä meidän sitten\ntulee tehdä?\"',47,NULL),
 (1090,'Anonymous','2008-10-17 16:32:24','Anonymous','2008-10-17 16:32:24',11,'1Hän vastasi heille: \"Jolla on\nkaksi paitaa, antakoon toisen sille, jolla ei ole\nyhtään. Jolla on ruokaa, tehköön samoin.\"',47,NULL),
 (1091,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',12,'2Myös\npublikaaneja*tuli kastettavaksi, ja he kysyivät\nJohannekselta: \"Opettaja, mitä meidän tulee tehdä?\"',47,NULL),
 (1092,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',13,'3Hän sanoi heille: \"Älkää vaatiko enempää kuin on\nsäädetty.\"',47,NULL),
 (1093,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',14,'4Sotilaatkin kysyivät häneltä: \"Entä\nme, mitä meidän tulee tehdä?\" Hän sanoi heille:\n\"Älkää ryöstäkö älkääkä kiristäkö keneltäkään, vaan\ntyytykää palkkaanne.\" ',47,NULL),
 (1094,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',15,'5Kansa oli odotuksen vallassa. Kaikki\npohdiskelivat, oliko Johannes ehkä Messias*.',47,NULL),
 (1095,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',16,'6He\nsaivat Johannekselta vastauksen: \"Minä kastan teidät\nvedellä, mutta on tuleva minua väkevämpi. Minä en\nkelpaa edes avaamaan hänen kenkiensä nauhoja. Hän\nkastaa teidät Pyhällä Hengellä ja tulella.',47,NULL),
 (1096,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',17,'7Hänellä on kädessään viskain, ja sillä hän\npuhdistaa puimatantereensa viljan. Jyvät hän kokoaa\naittaansa, mutta ruumenet hän polttaa tulessa, joka\nei koskaan sammu.\" ',47,NULL),
 (1097,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',18,'8Monilla muillakin tavoin Johannes vetosi\nihmisiin julistaessaan heille evankeliumia.',47,NULL),
 (1098,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',19,'9Mutta kun hän nuhteli neljännesruhtinas\nHerodesta tämän veljen vaimon Herodiaan tähden ja\nmoitti häntä myös muista pahoista teoista,',47,NULL),
 (1099,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',20,'0Herodes teki vielä senkin, että telkesi\nJohanneksen vankilaan.     ',47,NULL),
 (1100,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',1,'Jeesus lähti Jordanilta Pyhää Henkeä täynnä.\nHengen johdattamana hän kulki autiomaassa',48,NULL),
 (1101,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',2,'neljäkymmentä päivää, ja Paholainen kiusasi\nhäntä. Hän ei syönyt noina päivinä mitään, ja kun\ntämä aika oli kulunut, hänen tuli nälkä.',48,NULL),
 (1102,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',3,'Silloin\nPaholainen sanoi hänelle: \"Jos kerran olet Jumalan\nPoika, niin käske tämän kiven muuttua leiväksi.\"',48,NULL),
 (1103,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',4,'Jeesus vastasi: \"On kirjoitettu: \'Ei ihminen elä\nainoastaan leivästä.\'\" ',48,NULL),
 (1104,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',5,'Paholainen vei Jeesuksen korkealle paikalle,\nnäytti hänelle yhdessä hetkessä kaikki maailman\nvaltakunnat',48,NULL),
 (1105,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',6,'ja sanoi: \"Kaiken tämän, koko\nmaailman ja sen loiston minä annan sinun valtaasi,\nsillä se on annettu minun haltuuni ja minä voin\nantaa sen kenelle tahdon.',48,NULL),
 (1106,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',7,'Jos kumarrat minua,\ntämä kaikki on sinun.\"',48,NULL),
 (1107,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',8,'Jeesus vastasi: \"On\nkirjoitettu: \'Herraa, Jumalaasi, sinun tulee\nkunnioittaa ja ainoastaan häntä palvella.\'\" ',48,NULL),
 (1108,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',9,'Paholainen vei Jeesuksen Jerusalemiin, asetti\nhänet temppelimuurin harjalle ja sanoi: \"Jos kerran\nolet Jumalan Poika, niin heittäydy tästä alas.',48,NULL),
 (1109,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',10,'0Onhan kirjoitettu: \'Hän antaa enkeleilleen\nkäskyn varjella sinua.',48,NULL),
 (1110,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',11,'1He kantavat sinua\nkäsillään, ettet loukkaa jalkaasi kiveen.\'\"',48,NULL),
 (1111,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',12,'2Jeesus vastasi: \"On myös sanottu: \'Älä kiusaa\nHerraa, Jumalaasi.\'\" ',48,NULL),
 (1112,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',13,'3Kiusattuaan näin Jeesusta kaikin tavoin\nPaholainen jätti hänet joksikin aikaa rauhaan.        ',48,NULL),
 (1113,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',20,'0Hän kääri kirjan kokoon, antoi sen avustajalle\nja istuutui. Kaikki, jotka synagogassa olivat,\nkatsoivat tarkkaavasti häneen.',48,NULL),
 (1114,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',21,'1Hän alkoi puhua\nheille: \"Tänään, teidän kuultenne, on tämä kirjoitus\nkäynyt toteen.\"',48,NULL),
 (1115,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',22,'2Kaikki kiittelivät häntä ja\nihmettelivät niitä armon sanoja, joita hänen\nhuuliltaan lähti. Kuitenkin he sanoivat: \"Eikö tuo ole Joosefin\npoika?\"',48,NULL),
 (1116,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',23,'3Jeesus sanoi heille: \"Kohta te kaiketi\ntarjoatte minulle sananlaskua \'Lääkäri, paranna\nitsesi!\' ja sanotte: \'Tee täälläkin, omassa\nkaupungissasi, kaikkea sitä, mitä sinun kerrotaan\ntehneen Kapernaumissa.\'\"',48,NULL),
 (1117,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',24,'4Ja hän jatkoi:\n\"Totisesti: kukaan ei ole profeetta omalla maallaan.',48,NULL),
 (1118,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',25,'5Uskokaa minua: Israelissa oli monta leskeä Elian\naikana, silloin kun taivas ei antanut vettä kolmeen\nja puoleen vuoteen ja koko maahan tuli kova\nnälänhätä.',48,NULL),
 (1119,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',26,'6Silti Eliaa ei lähetetty heidän\nluokseen, vaan Sidonin maahan, Sarpatissa asuvan\nleskivaimon luo.',48,NULL),
 (1120,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',27,'7Samoin Israelissa oli monta\nspitaalista profeetta Elisan aikana, mutta yhtäkään\nheistä ei puhdistettu, ainoastaan Naaman, joka oli\nsyyrialainen.\" ',48,NULL),
 (1121,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',28,'8Tämän kuullessaan kaikki, jotka olivat\nsynagogassa, joutuivat raivon valtaan.',48,NULL),
 (1122,'Anonymous','2008-10-17 16:32:25','Anonymous','2008-10-17 16:32:25',29,'9He\nryntäsivät paikaltaan, ajoivat Jeesuksen ulos\nkaupungista ja veivät hänet jyrkänteelle syöstäkseen\nhänet sieltä alas; kaupunki näet oli rakennettu\nvuorelle.',48,NULL),
 (1123,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',30,'0Mutta Jeesus kulki väkijoukon halki ja\njatkoi matkaansa.   ',48,NULL),
 (1124,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',33,'3Synagogassa oli mies, jossa oli saastainen\nhenki. Se huusi kovalla äänellä:',48,NULL),
 (1125,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',34,'4\"Voi, mitä sinä\nmeistä tahdot, Jeesus Nasaretilainen? Oletko tullut\ntuhoamaan meidät? Minä tiedän, kuka sinä olet,\nJumalan Pyhä!\"',48,NULL),
 (1126,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',35,'5Mutta Jeesus käski henkeä\nankarasti: \"Ole vaiti! Lähde hänestä.\" Silloin paha\nhenki paiskasi miehen maahan väkijoukon keskelle ja\nlähti hänestä vahingoittamatta häntä.',48,NULL),
 (1127,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',36,'6Kaikki\nolivat ymmällä ja sanoivat toisilleen: \"Mitä tämä\npuhe oikein on? Hän käskee saastaisia henkiä\nvallalla ja voimalla, ja ne lähtevät tiehensä.\"',48,NULL),
 (1128,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',37,'7Niin Jeesuksen maine levisi sillä seudulla\nkaikkialle.   ',48,NULL),
 (1129,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',40,'0Monien kotona oli joku sairaana, kuka missäkin\ntaudissa. Auringon laskiessa sairaat tuotiin\nJeesuksen luo, ja hän pani kätensä jokaisen päälle\nja paransi heidät.',48,NULL),
 (1130,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',41,'1Monista lähti myös pahoja\nhenkiä, jotka huusivat: \"Sinä olet Jumalan Poika!\"\nMutta Jeesus nuhteli niitä eikä sallinut niiden\npuhua, koska ne tiesivät, että hän oli Messias.   ',48,NULL),
 (1131,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',1,'Kun Jeesus eräänä päivänä seisoi\nGennesaretinjärven rannalla ja väkijoukko tungeksi\nhänen ympärillään kuulemassa Jumalan sanaa,',49,NULL),
 (1132,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',2,'hän\nnäki rannassa kaksi venettä. Kalastajat olivat\nnousseet niistä ja huuhtoivat verkkojaan.',49,NULL),
 (1133,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',3,'Jeesus\nastui toiseen veneistä ja pyysi Simonia, jonka vene\nse oli, soutamaan rannasta vähän ulommaksi. Sitten\nhän opetti kansaa veneessä istuen. ',49,NULL),
 (1134,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',4,'Lopetettuaan puheensa Jeesus sanoi Simonille:\n\"Souda vene syvään veteen, laskekaa sinne\nverkkonne.\"',49,NULL),
 (1135,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',5,'Tähän Simon vastasi: \"Opettaja, me\nolemme jo tehneet työtä koko yön emmekä ole saaneet\nmitään. Mutta lasken vielä verkot, kun sinä niin\nkäsket.\"',49,NULL),
 (1136,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',6,'Näin he tekivät ja saivat saarretuksi\nniin suuren kalaparven, että heidän verkkonsa\nrepeilivät.',49,NULL),
 (1137,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',7,'He viittoivat toisessa veneessä\nolevia tovereitaan apuun. Nämä tulivat, ja he saivat\nmolemmat veneet niin täyteen kalaa, että ne olivat\nupota.',49,NULL),
 (1138,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',8,'Tämän nähdessään Simon Pietari lankesi\nJeesuksen jalkoihin ja sanoi: \"Mene pois minun\nluotani, Herra! Minä olen syntinen mies.\"',49,NULL),
 (1139,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',9,'Hän ja\nkoko hänen venekuntansa olivat pelon ja hämmennyksen\nvallassa kalansaaliin tähden,',49,NULL),
 (1140,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',10,'0samoin Jaakob ja\nJohannes, Sebedeuksen pojat, jotka olivat Simonin\nkalastuskumppaneita. Mutta Jeesus sanoi hänelle:\n\"Älä pelkää. Tästä lähtien sinä olet ihmisten\nkalastaja.\"',49,NULL),
 (1141,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',11,'1He vetivät veneet maihin ja jättäen\nkaiken lähtivät seuraamaan Jeesusta.     ',49,NULL),
 (1142,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',18,'8Paikalle tuli miehiä, jotka kantoivat vuoteella\nhalvaantunutta. He yrittivät tuoda hänet sisälle\ntaloon asettaakseen hänet Jeesuksen eteen.',49,NULL),
 (1143,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',19,'9Tungoksessa he eivät kuitenkaan saaneet tuoduksi\nhäntä sisään, ja niin he kiipesivät katolle,\nirrottivat tiiliä ja laskivat sairaan vuoteineen\nkaikkien keskelle Jeesuksen eteen.',49,NULL),
 (1144,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',20,'0Kun Jeesus\nnäki heidän uskonsa, hän sanoi: \"Ystäväni, sinun\nsyntisi on annettu anteeksi.\" ',49,NULL),
 (1145,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',21,'1Silloin lainopettajat ja fariseukset\najattelivat: \"Mikä mies tämä on? Hän herjaa Jumalaa,\nkun puhuu noin. Kuka muu kuin Jumala voi antaa\nsyntejä anteeksi?\"',49,NULL),
 (1146,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',22,'2Mutta Jeesus oli selvillä\nsiitä, mitä he ajattelivat, ja sanoi heille: \"Mitä\nte oikein ajattelette?',49,NULL),
 (1147,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',23,'3Kumpi on helpompaa,\nsanoa: \'Sinun syntisi on annettu anteeksi\', vai\nsanoa: \'Nouse ja kävele\'?',49,NULL),
 (1148,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',24,'4Mutta jotta\ntietäisitte, että Ihmisen Pojalla*on valta antaa\nmaan päällä syntejä anteeksi\" -- hän puhui nyt\nhalvaantuneelle -- \"minä sanon sinulle: nouse, ota\nvuoteesi ja mene kotiisi.\" ',49,NULL),
 (1149,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',25,'5Heti paikalla mies kaikkien nähden nousi\njalkeille, otti vuoteen, jolla oli maannut, ja lähti\nkotiinsa ylistäen Jumalaa.',49,NULL),
 (1150,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',26,'6Kaikki olivat\nhämmästyksestä suunniltaan ja ylistivät Jumalaa.\nPelon vallassa he sanoivat: \"Me olemme tänään\nnähneet sellaista, mitä ei voisi todeksi uskoa.\"   ',49,NULL),
 (1151,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',29,'9Leevi järjesti sitten kotonaan Jeesukselle\nsuuret pidot. Heidän kanssaan oli aterialla paljon\npublikaaneja ja muita vieraita.',49,NULL),
 (1152,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',30,'0Fariseukset ja\nheihin kuuluvat lainopettajat olivat tästä äkeissään\nja sanoivat Jeesuksen opetuslapsille: \"Kuinka te\nsyötte ja juotte yhdessä publikaanien ja muiden\nsyntisten kanssa!\"',49,NULL),
 (1153,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',31,'1Jeesus vastasi heille: \"Eivät\nterveet tarvitse parantajaa, vaan sairaat.',49,NULL),
 (1154,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',32,'2En\nminä ole tullut kutsumaan hurskaita, vaan syntisiä,\njotta he kääntyisivät.\"   ',49,NULL),
 (1155,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',36,'6Jeesus esitti heille vielä vertauksen: \"Ei\nkukaan leikkaa uudesta viitasta paikkaa ja ompele\nsitä vanhaan viittaan. Silloinhan uusi viitta menee\npilalle eikä myöskään uudesta kankaasta otettu\npaikka sovi vanhaan.',49,NULL),
 (1156,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',37,'7Eikä kukaan laske uutta\nviiniä vanhoihin leileihin. Silloinhan uusi viini\nrikkoo leilit, viini valuu maahan ja leilit ovat\npilalla.',49,NULL),
 (1157,'Anonymous','2008-10-17 16:32:26','Anonymous','2008-10-17 16:32:26',38,'8Ei, uusi viini on laskettava uusiin\nleileihin.',49,NULL),
 (1158,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',39,'9Ei kukaan, joka on juonut vanhaa\nviiniä, halua uutta. Hänen mielestään vanha on\nhyvää.\" ',49,NULL),
 (1159,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',1,'Jeesus kulki kerran sapattina viljapellon laitaa,\nja hänen opetuslapsensa katkoivat tähkäpäitä,\nhiersivät niitä käsissään ja söivät.',50,NULL),
 (1160,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',2,'Muutamat\nfariseukset sanoivat: \"Miksi teette sellaista, mitä\nsapattina ei ole lupa tehdä?\"',50,NULL),
 (1161,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',3,'Jeesus vastasi:\n\"Ettekö ole lukeneet, mitä Daavid teki, kun hänen ja\nhänen miestensä tuli nälkä?',50,NULL),
 (1162,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',4,'Hän meni Jumalan\nhuoneeseen ja otti uhrileivät, söi ja antoi\nmiehilleen, vaikka niiden syöminen on sallittua\nainoastaan papeille.\"',50,NULL),
 (1163,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',5,'Ja Jeesus sanoi vielä:\n\"Ihmisen Poika on sapatin herra.\"           ',50,NULL),
 (1164,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',22,'2\"Autuaita olette te, kun teitä Ihmisen Pojan\ntähden vihataan ja herjataan, kun ihmiset erottavat\nteidät keskuudestaan ja inhoavat teidän nimeännekin.',50,NULL),
 (1165,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',23,'3Iloitkaa silloin, hyppikää riemusta, sillä\npalkka, jonka te taivaassa saatte, on suuri.\nSamoinhan tekivät heidän isänsä profeetoille.    ',50,NULL),
 (1166,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',26,'6\"Voi teitä, kun kaikki ihmiset puhuvat teistä\nhyvää! Samoinhan tekivät heidän isänsä väärille\nprofeetoille.   ',50,NULL),
 (1167,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',31,'1\"Niin kuin te tahdotte ihmisten tekevän teille,\nniin tehkää te heille.',50,NULL),
 (1168,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',32,'2Jos te rakastatte niitä,\njotka rakastavat teitä, mitä kiitettävää siinä on?\nSyntisetkin rakastavat niitä, joilta itse saavat\nrakkautta.',50,NULL),
 (1169,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',33,'3Jos te teette hyvää niille, jotka\ntekevät hyvää teille, mitä kiitettävää siinä on?\nSyntisetkin tekevät niin.',50,NULL),
 (1170,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',34,'4Ja jos te lainaatte\nniille, joiden uskotte maksavan takaisin, mitä\nkiitettävää siinä on? Syntisetkin lainaavat\ntoisilleen, kun tietävät saavansa takaisin saman\nverran.',50,NULL),
 (1171,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',35,'5Ei, rakastakaa vihamiehiänne, tehkää\nhyvää ja lainatkaa, vaikka ette uskoisikaan saavanne\ntakaisin. Silloin teidän palkkanne on suuri ja te\nolette Korkeimman lapsia, sillä hän on hyvä\nkiittämättömille ja pahoille. ',50,NULL),
 (1172,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',36,'6\"Olkaa valmiit armahtamaan, niin kuin teidän\nIsännekin armahtaa.   ',50,NULL),
 (1173,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',39,'9Hän puhui heille vielä vertauksin: \"Miten sokea voisi taluttaa sokeaa? Molemmathan\nsiinä putoavat kuoppaan.',50,NULL),
 (1174,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',40,'0Ei oppilas ole\nopettajaansa etevämpi, mutta kyllin oppia saatuaan\njokainen on opettajansa veroinen. ',50,NULL),
 (1175,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',41,'1\"Kuinka näet roskan veljesi silmässä, mutta et\nhuomaa, että omassa silmässäsi on hirsi?',50,NULL),
 (1176,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',42,'2Kuinka\nvoit sanoa veljellesi: \'Annapa, veli, kun otan\nroskan silmästäsi\'? Ethän sinä näe edes hirttä\nomassa silmässäsi. Sinä tekopyhä! Ota ensin hirsi\nomasta silmästäsi, vasta sitten näet ottaa roskan\nveljesi silmästä.     ',50,NULL),
 (1177,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',1,'Kun Jeesus oli puhunut kaiken tämän heille kansan\nkuunnellessa, hän lähti Kapernaumiin.',51,NULL),
 (1178,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',2,'Erään\nsadanpäällikön palvelija oli kuolemansairaana.\nSadanpäällikkö piti palvelijaa suuressa arvossa,',51,NULL),
 (1179,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',3,'ja kuultuaan Jeesuksesta hän lähetti juutalaisten\nvanhimpia pyytämään, että Jeesus tulisi ja\npelastaisi palvelijan hengen.',51,NULL),
 (1180,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',4,'He tulivat\nJeesuksen luo ja vetosivat häneen sanoen: \"Se mies\nansaitsee apusi.',51,NULL),
 (1181,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',5,'Hän rakastaa meidän kansaamme,\nja meidän synagogammekin on hänen rakennuttamansa.\" ',51,NULL),
 (1182,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',6,'Jeesus lähti heidän mukaansa. Mutta kun hän jo\noli lähellä taloa, sadanpäällikkö lähetti ystäviään\ntuomaan hänelle sanaa: \"Herra, älä vaivaa itseäsi.\nMinä en ole sen arvoinen, että tulisit kattoni alle.',51,NULL),
 (1183,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',7,'En pitänyt itseäni senkään arvoisena, että olisin\ntullut sinun luoksesi. Käske, niin palvelijani\nparanee.',51,NULL),
 (1184,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',8,'Minä tottelen itsekin toisten käskyjä ja\nkomennan omia sotilaitani. Kun sanon sotilaalle:\n\'Mene\', niin hän menee, tai toiselle: \'Tule\', niin\nhän tulee, tai palvelijalleni: \'Tee tämä\', niin hän\ntekee.\"',51,NULL),
 (1185,'Anonymous','2008-10-17 16:32:27','Anonymous','2008-10-17 16:32:27',9,'Jeesus hämmästyi näistä sanoista. Hän\nkääntyi perässään tulevan ihmisjoukon puoleen ja\nsanoi: \"Kuulkaa, mitä sanon: tällaista uskoa en ole\ntavannut edes Israelin kansan keskuudessa.\"',51,NULL),
 (1186,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',10,'0Kun\nJeesusta vastaan lähetetyt miehet palasivat taloon,\nhe tapasivat palvelijan terveenä.   ',51,NULL),
 (1187,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',16,'6Kaikki joutuivat pelon valtaan ja ylistivät\nJumalaa sanoen: \"Meidän keskuuteemme on ilmaantunut\nsuuri profeetta. Jumala on tullut kansansa avuksi.\"',51,NULL),
 (1188,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',17,'7Tähän tapaan Jeesuksesta puhuttiin kohta joka\npuolella Juudeaa ja kaikkialla sen ympäristössä.   ',51,NULL),
 (1189,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',21,'1Jeesus oli silloin juuri parantanut monien\ntaudit ja vaivat, karkottanut pahoja henkiä ja\nantanut monelle sokealle näön.',51,NULL),
 (1190,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',22,'2Niinpä hän\nvastasi: \"Menkää ja kertokaa Johannekselle, mitä\nolette nähneet ja kuulleet:    \"Mitä te lähditte autiomaahan katsomaan? Ruokoako,\njota tuuli huojuttaa?',51,NULL),
 (1191,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',25,'5Vai mitä odotitte\nnäkevänne? Kenties hienosti pukeutuneen miehen?\nKuninkaanlinnoista te niitä löydätte, jotka\nkoreilevat vaatteillaan ja elävät ylellisesti!',51,NULL),
 (1192,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',26,'6Mitä te sitten odotitte näkevänne? Profeetanko?\nAivan oikein, ja minä sanon teille, että hän on\nenemmänkin kuin profeetta.',51,NULL),
 (1193,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',27,'7Hän on se, josta on\nkirjoitettu:   ',51,NULL),
 (1194,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',28,'8\"Minä sanon teille: yksikään naisesta syntynyt\nei ole Johannesta suurempi, mutta kaikkein vähäisin,\njoka on Jumalan valtakunnassa, on suurempi kuin hän. ',51,NULL),
 (1195,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',29,'9\"Kaikki, jotka kuulivat Johannesta,\npublikaanitkin, taipuivat tunnustamaan, että Jumalan\nvaatimus oli oikea, ja ottivat Johannekselta\nkasteen.',51,NULL),
 (1196,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',30,'0Mutta fariseukset ja lainopettajat\nasettuivat Jumalan tahtoa vastaan eivätkä ottaneet\nJohannekselta kastetta. ',51,NULL),
 (1197,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',31,'1\"Mihin siis vertaisin tämän sukupolven ihmisiä?\nMillaisia he ovat?',51,NULL),
 (1198,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',32,'2He ovat kuin torilla istuvat\nlapset, jotka huutavat toisilleen: \'Me soitimme\nteille huilua, mutta te ette tanssineet, me pidimme\nvalittajaisia, mutta te ette itkeneet mukana.\'',51,NULL),
 (1199,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',33,'3Johannes Kastaja on tullut, hän ei syö leipää\neikä juo viiniä, ja te sanotte: \'Hänessä on paha\nhenki.\'',51,NULL),
 (1200,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',34,'4Ihmisen Poika on tullut, hän syö ja juo,\nja te sanotte: \'Mikä syömäri ja juomari,\npublikaanien ja syntisten ystävä!\'',51,NULL),
 (1201,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',35,'5Mutta\nViisauden tunnustavat oikeaksi kaikki Viisauden\nlapset.\"   ',51,NULL),
 (1202,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',39,'9Fariseus, joka oli kutsunut Jeesuksen, näki sen\nja ajatteli: \"Jos tämä mies olisi profeetta, hän\nkyllä tietäisi, millainen nainen häneen koskee.\nNainenhan on syntinen.\"',51,NULL),
 (1203,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',40,'0Silloin Jeesus sanoi\nhänelle: \"Simon, minulla on sinulle puhuttavaa.\"\n\"Puhu vain, opettaja\", fariseus vastasi. ',51,NULL),
 (1204,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',41,'1\"Oli kaksi miestä\", sanoi Jeesus. \"He olivat\nvelkaa rahanlainaajalle, toinen viisisataa, toinen\nviisikymmentä denaaria.',51,NULL),
 (1205,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',42,'2Kun heillä ei ollut\nmillä maksaa, rahanlainaaja antoi molemmille velan\nanteeksi. Miten on, kumpi heistä nyt rakastaa häntä\nenemmän?\"',51,NULL),
 (1206,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',43,'3Simon vastasi: \"Eiköhän se, joka sai\nenemmän anteeksi.\" \"Aivan oikein\", sanoi Jeesus.',51,NULL),
 (1207,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',44,'4Hän kääntyi naiseen päin ja puhui Simonille:\n\"Katso tätä naista. Kun tulin kotiisi, sinä et\nantanut vettä jalkojeni pesuun, mutta hän kasteli\njalkani kyynelillään ja kuivasi ne hiuksillaan.',51,NULL),
 (1208,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',45,'5Sinä et tervehtinyt minua suudelmalla, mutta hän\non suudellut jalkojani siitä saakka kun tänne tulin.',51,NULL),
 (1209,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',46,'6Sinä et voidellut päätäni öljyllä, mutta hän\nvoiteli jalkani tuoksuöljyllä.',51,NULL),
 (1210,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',47,'7Niinpä sanonkin\nsinulle: hän sai paljot syntinsä anteeksi, sen\nvuoksi hän rakasti paljon. Mutta joka saa anteeksi\nvähän, se myös rakastaa vähän.\"',51,NULL),
 (1211,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',48,'8Ja hän sanoi\nnaiselle: \"Kaikki sinun syntisi on annettu\nanteeksi.\" ',51,NULL),
 (1212,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',49,'9Pöytävieraat alkoivat ihmetellä: \"Kuka tuo mies\non? Hänhän antaa syntejäkin anteeksi.\"',51,NULL),
 (1213,'Anonymous','2008-10-17 16:32:28','Anonymous','2008-10-17 16:32:28',50,'0Mutta\nJeesus sanoi naiselle: \"Uskosi on pelastanut sinut.\nMene rauhassa.\" ',51,NULL),
 (1214,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',1,'Sen jälkeen Jeesus kulki kaupungista kaupunkiin\nja kylästä kylään julistaen ilosanomaa Jumalan\nvaltakunnasta. Hänellä oli seurassaan kaksitoista\nopetuslastaan',52,NULL),
 (1215,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',2,'sekä muutamia naisia, jotka hän oli\nparantanut taudeista ja vapauttanut pahojen henkien\nvallasta. Näitä olivat Magdalan Maria, josta hän oli\najanut ulos seitsemän pahaa henkeä,',52,NULL),
 (1216,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',3,'Johanna,\njonka aviomies Kuusas oli Herodeksen korkeita\nvirkamiehiä, sekä Susanna. Lisäksi oli monia muita\nnaisia, ja kaikki nämä avustivat heitä omilla\nvaroillaan.     ',52,NULL),
 (1217,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',10,'0Hän sanoi: \"Te olette saaneet oppia tuntemaan Jumalan\nvaltakunnan salaisuudet, mutta muille ne esitetään\nvertauksina, jotta he nähdessäänkään eivät näkisi\neivätkä kuullessaankaan ymmärtäisi.',52,NULL),
 (1218,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',11,'1Vertaus\ntarkoittaa tätä: Siemen on Jumalan sana.',52,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (1219,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',12,'2Tien\nlaitaan pudonnut siemen tarkoittaa ihmisiä, jotka\nkuulevat sanan mutta joiden sydämestä Paholainen\nheti tulee ottamaan sen pois, jotta he eivät uskoisi\nja pelastuisi.',52,NULL),
 (1220,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',13,'3Kalliolle pudonnut siemen\ntarkoittaa niitä, jotka sanan kuullessaan ottavat\nsen iloiten vastaan mutta uskovat vain hetken aikaa.\nHeillä ei ole juurta, ja niin he koetukseen\njoutuessaan luopuvat.',52,NULL),
 (1221,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',14,'4Ohdakkeisiin pudonnut osa\ntarkoittaa niitä, jotka kuulevat sanan mutta sitten\ntukahtuvat elämän huoliin, rikkauteen ja\nnautintoihin. He eivät tuota kypsää satoa.',52,NULL),
 (1222,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',15,'5Mutta\nhyvään maahan pudonnut siemen tarkoittaa niitä,\njotka sanan kuultuaan pysyvät siinä puhtain ja ehein\nsydämin ja kestävinä tuottavat satoa.         ',52,NULL),
 (1223,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',32,'2Läheisellä vuorella oli iso sikalauma\nlaitumella. Pahat henget pyysivät Jeesukselta lupaa\nmennä sikoihin, ja hän salli sen.',52,NULL),
 (1224,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',33,'3Silloin henget\nlähtivät miehestä ja menivät sikoihin, ja lauma\nsyöksyi jyrkännettä alas järveen ja hukkui. ',52,NULL),
 (1225,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',34,'4Kun sikopaimenet näkivät tämän, he lähtivät\npakoon ja kertoivat kaiken kaupungissa ja kylissä.',52,NULL),
 (1226,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',35,'5Monet lähtivät katsomaan, mitä oli tapahtunut.\nHe tulivat Jeesuksen luo ja tapasivat miehen, josta\npahat henget olivat lähteneet. Mies istui Jeesuksen\njalkojen juuressa vaatteet yllään ja täydessä\njärjessään. Tämä sai heidät pelon valtaan.',52,NULL),
 (1227,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',36,'6Paikalla olleet kertoivat heille, millä tavoin\npahojen henkien vaivaama oli parantunut.',52,NULL),
 (1228,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',37,'7Silloin\nkaikki Gerasan seudun ihmiset pyysivät Jeesusta\npoistumaan sieltä, sillä he olivat hyvin peloissaan.\nJeesus nousikin veneeseen ja lähti paluumatkalle.',52,NULL),
 (1229,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',38,'8Mies, josta pahat henget olivat lähteneet,\npyysi, että saisi jäädä hänen seuraansa, mutta\nJeesus lähetti hänet luotaan sanoen:',52,NULL),
 (1230,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',39,'9\"Palaa\nkotiisi ja kerro tästä suuresta teosta, jonka Jumala\non sinulle tehnyt.\" Mies lähti, kulki ympäri\nkaupunkia ja julisti, mitä Jeesus oli tehnyt\nhänelle.   Matkalla Jairoksen kotiin väkijoukko tungeksi\nJeesuksen ympärillä.',52,NULL),
 (1231,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',43,'3Siellä oli myös nainen,\njota kaksitoista vuotta oli vaivannut verenvuoto.\nHän oli kuluttanut kaikki varansa lääkäreihin,*mutta kukaan ei ollut kyennyt parantamaan häntä.',52,NULL),
 (1232,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',44,'4Hän tuli Jeesuksen taakse ja kosketti hänen\nviittansa tupsua, ja verenvuoto lakkasi heti.',52,NULL),
 (1233,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',45,'5Jeesus kysyi: \"Kuka koski minuun?\" Kukaan ei\nmyöntänyt koskeneensa, ja Pietari sanoi: \"Opettaja,\njoka puoleltahan tässä ihmiset tunkevat päällesi.\"',52,NULL),
 (1234,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',46,'6Mutta Jeesus sanoi: \"Joku koski minuun. Minä\ntunsin, että minusta lähti voimaa.\"',52,NULL),
 (1235,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',47,'7Kun nainen\nhuomasi, ettei hän voinut pysyä salassa, hän tuli\nvavisten esiin ja heittäytyi Jeesuksen jalkojen\njuureen. Kaiken kansan kuullen hän kertoi, miksi oli\nkoskenut Jeesukseen ja miten hän heti oli tullut\nterveeksi.',52,NULL),
 (1236,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',48,'8Jeesus sanoi hänelle: \"Tyttäreni,\nuskosi on parantanut sinut.*Mene rauhassa.\" ',52,NULL),
 (1237,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',49,'9Hänen vielä puhuessaan tuotiin synagogan\nesimiehelle kotoa sana: \"Tyttäresi on kuollut. Älä\nenää vaivaa opettajaa.\"',52,NULL),
 (1238,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',50,'0Mutta kun Jeesus kuuli\ntämän, hän sanoi esimiehelle: \"Älä pelkää. Usko,\nniin hän pelastuu.\"',52,NULL),
 (1239,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',51,'1Perille saavuttuaan hän ei\nantanut kenenkään muun tulla mukanaan sisään kuin\nPietarin, Johanneksen ja Jaakobin sekä tytön isän ja\näidin.',52,NULL),
 (1240,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',52,'2Kaikki itkivät ja valittivat tytön\nkuolemaa, mutta Jeesus sanoi: \"Älkää itkekö! Ei hän\nole kuollut, hän nukkuu.\"',52,NULL),
 (1241,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',53,'3He nauroivat hänelle,\nkoska tiesivät, että tyttö oli kuollut.',52,NULL),
 (1242,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',54,'4Mutta\nJeesus otti tyttöä kädestä ja sanoi kuuluvalla\näänellä: \"Tyttö, nouse!\"',52,NULL),
 (1243,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',55,'5Silloin henki palasi\ntyttöön. Hän nousi heti jalkeille, ja Jeesus käski\nantaa hänelle syötävää.',52,NULL),
 (1244,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',56,'6Tytön vanhemmat olivat\nhämmästyksestä suunniltaan. Jeesus kielsi heitä\nkertomasta kenellekään, mitä oli tapahtunut. ',52,NULL),
 (1245,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',1,'Jeesus kutsui koolle kaksitoista opetuslastaan ja\nantoi heille voiman ja vallan parantaa taudit ja\nkarkottaa pahat henget.',53,NULL),
 (1246,'Anonymous','2008-10-17 16:32:29','Anonymous','2008-10-17 16:32:29',2,'Hän lähetti heidät\njulistamaan Jumalan valtakuntaa ja parantamaan\nsairaita',53,NULL),
 (1247,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',3,'ja sanoi heille: \"Älkää ottako matkalle\nmukaanne mitään -- ei sauvaa, ei laukkua, ei leipää\neikä rahaa. Älkää ottako toista paitaakaan.',53,NULL),
 (1248,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',4,'Ja\nmihin taloon majoituttekin, siellä asukaa ja sieltä\ntaas lähtekää.',53,NULL),
 (1249,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',5,'Jos teitä ei jossakin paikassa\noteta vastaan, lähtekää siitä kaupungista ja\npudistakaa pöly jaloistanne. Tämä on oleva todiste\nheitä vastaan.\" ',53,NULL),
 (1250,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',6,'Niin opetuslapset lähtivät matkaan. He kulkivat\nkylästä kylään julistaen evankeliumia ja parantaen\nsairaita kaikkialla.     ',53,NULL),
 (1251,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',12,'2Kun päivä alkoi kallistua iltaan, Jeesuksen\nkaksitoista opetuslasta tulivat hänen luokseen ja\nsanoivat: \"Lähetä ihmiset pois, että he menisivät\nlähiseudun kyliin ja taloihin ja hankkisivat sieltä\nyösijan ja ruokaa. Tämä on aivan asumatonta seutua.\"',53,NULL),
 (1252,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',13,'3\"Antakaa te heille syötävää\", sanoi Jeesus. He\nvastasivat: \"Ei meillä ole enempää kuin viisi leipää\nja kaksi kalaa -- vai menemmekö ostamaan ruokaa koko\ntälle joukolle?\"',53,NULL),
 (1253,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',14,'4Siellä näet oli noin\nviisituhatta miestä. Jeesus sanoi opetuslapsille:\n\"Pankaa heidät istumaan aterialle viidenkymmenen\nhengen ryhmiin.\"',53,NULL),
 (1254,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',15,'5He tekivät niin ja järjestivät\nkaikki aterioimaan.',53,NULL),
 (1255,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',16,'6Sitten Jeesus otti ne viisi\nleipää ja kaksi kalaa, katsoi ylös taivaaseen ja\nlausui niistä kiitoksen. Hän mursi leivät ja antoi\npalat opetuslapsilleen kansalle tarjottaviksi.',53,NULL),
 (1256,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',17,'7Kaikki söivät kyllikseen, ja tähteeksi jääneitä\npaloja kerättiin kaksitoista korillista.       ',53,NULL),
 (1257,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',32,'2Pietari ja hänen kanssaan olevat opetuslapset\nolivat vaipuneet syvään uneen. Havahtuessaan he\nnäkivät Jeesuksen kirkkaudessaan ja ne kaksi miestä,\njotka olivat hänen kanssaan.',53,NULL),
 (1258,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',33,'3Kun nämä olivat\nlähtemässä Jeesuksen luota, Pietari sanoi:\n\"Opettaja, on hyvä, että me olemme täällä. Me teemme\nkolme majaa: sinulle ja Moosekselle ja Elialle.\"\nMutta hän ei tiennyt mitä sanoi. ',53,NULL),
 (1259,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',34,'4Pietarin puhuessa tuli pilvi ja peitti paikan\nvarjoonsa. Opetuslapset pelästyivät, kun näkivät\nmiesten peittyneen pilveen.',53,NULL),
 (1260,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',35,'5Pilvestä kuului\nääni: \"Tämä on minun Poikani, minun valittuni,\nkuulkaa häntä!\"',53,NULL),
 (1261,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',36,'6Äänen vaiettua opetuslapset\nnäkivät Jeesuksen olevan yksin. He pysyivät vaiti\nkaikesta kokemastaan eivätkä vielä silloin kertoneet\nsiitä kenellekään.              ',53,NULL),
 (1262,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',59,'9Eräälle toiselle Jeesus sanoi: \"Seuraa minua!\"\nTämä vastasi: \"Herra, anna minun ensin käydä\nhautaamassa isäni.\"',53,NULL),
 (1263,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',60,'0Mutta Jeesus sanoi hänelle:\n\"Anna kuolleiden haudata kuolleensa. Lähde sinä\njulistamaan Jumalan valtakuntaa.\" ',53,NULL),
 (1264,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',61,'1Vielä eräs toinen sanoi: \"Herra, minä seuraan\nsinua, mutta anna minun ensin käydä hyvästelemässä\nkotiväkeni.\"',53,NULL),
 (1265,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',62,'2Hänelle Jeesus vastasi: \"Joka\ntarttuu auraan ja katsoo taakseen, ei ole sopiva\nJumalan valtakuntaan.\" ',53,NULL),
 (1266,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',1,'Tämän jälkeen Herra valitsi vielä\nseitsemänkymmentäkaksi opetuslasta ja lähetti heidät\nkaksittain edellään jokaiseen kaupunkiin ja kylään,\njohon hän aikoi itse mennä. ',54,NULL),
 (1267,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',2,'Hän sanoi heille: \"Satoa on paljon, mutta sadonkorjaajia vähän.\nPyytäkää siis herraa, jolle sato kuuluu, lähettämään\nväkeä elonkorjuuseen. ',54,NULL),
 (1268,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',3,'\"Menkää, minä lähetän teidät kuin lampaat susien\nkeskelle.',54,NULL),
 (1269,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',4,'Älkää ottako mukaanne rahakukkaroa,\nälkää laukkua älkääkä jalkineita. Älkää matkan\nvarrella pysähtykö tervehtimään ketään.',54,NULL),
 (1270,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',5,'Ja kun\ntulette johonkin taloon, sanokaa ensiksi: \'Rauha\ntälle kodille.\'',54,NULL),
 (1271,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',6,'Jos siellä on joku, joka on\nrauhan arvoinen, hän saa teidän toivottamanne\nrauhan. Ellei ole, toivotuksenne palaa teille.',54,NULL),
 (1272,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',7,'Jääkää siihen taloon ja syökää ja juokaa mitä\nteille tarjotaan, sillä työmies on palkkansa\nansainnut. Älkää siirtykö talosta toiseen. ',54,NULL),
 (1273,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',8,'\"Kun tulette kaupunkiin ja teidät otetaan siellä\nvastaan, syökää mitä teille tarjotaan,',54,NULL),
 (1274,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',9,'parantakaa\nkaupungin sairaat ja kertokaa kaikille: \'Jumalan\nvaltakunta on tullut teitä lähelle.\'',54,NULL),
 (1275,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',10,'0Mutta jos\nteitä johonkin kaupunkiin tultuanne ei oteta\nvastaan, menkää sen kaduille ja julistakaa:',54,NULL),
 (1276,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',11,'1\'Me\npyyhimme pois pölynkin, joka teidän kaupungistanne\non jalkoihimme tarttunut -- pitäkää hyvänänne! Mutta\ntietäkää, että Jumalan valtakunta on tullut\nlähelle!\'',54,NULL),
 (1277,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',12,'2Minä sanon teille: Sodomakin pääsee\ntuomiopäivänä vähemmällä kuin sellainen kaupunki. ',54,NULL),
 (1278,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',13,'3\"Voi sinua, Korasin! Voi sinua, Betsaida! Jos\nteidän kaduillanne tehdyt voimateot olisi tehty\nTyroksessa tai Sidonissa, niiden asukkaat olisivat\njo aikoja sitten verhoutuneet säkkiin, istuneet\ntuhkassa ja katuneet.',54,NULL),
 (1279,'Anonymous','2008-10-17 16:32:30','Anonymous','2008-10-17 16:32:30',14,'4Tuomiopäivänä Tyros ja\nSidon pääsevätkin vähemmällä kuin te.',54,NULL),
 (1280,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',15,'5Entä sinä,\nKapernaum, korotetaanko sinut muka taivaaseen? Alas\nsinut syöstään, alas tuonelaan saakka! ',54,NULL),
 (1281,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',16,'6\"Joka kuulee teitä, kuulee minua, ja joka hylkää\nteidät, hylkää minut. Mutta joka hylkää minut,\nhylkää sen, joka on minut lähettänyt.\"     \"Minä ylistän sinua, Isä, taivaan ja maan Herra,\nsiitä, että olet salannut tämän järkeviltä ja\nviisailta mutta olet ilmoittanut sen\nlapsenmielisille. Näin sinä, Isä, olet hyväksi\nnähnyt. ',54,NULL),
 (1282,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',22,'2\"Kaiken on Isäni antanut minun haltuuni. Ei\nkukaan muu kuin Isä tiedä, kuka Poika on. Eikä sitä,\nkuka Isä on, tiedä kukaan muu kuin Poika ja se,\njolle Poika tahtoo hänet ilmoittaa.\" ',54,NULL),
 (1283,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',23,'3Jeesus kääntyi opetuslastensa puoleen ja sanoi\nheille erikseen: \"Autuaat ne silmät, jotka näkevät\nsen, mitä te näette!',54,NULL),
 (1284,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',24,'4Minä sanon teille: monet\nprofeetat ja kuninkaat ovat halunneet nähdä sen,\nmitä te näette, eivätkä ole nähneet, ja kuulla sen,\nmitä te kuulette, eivätkä ole kuulleet.\"   ',54,NULL),
 (1285,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',29,'9Mies tahtoi osoittaa, että hän noudatti lakia,\nja jatkoi: \"Kuka sitten on minun lähimmäiseni?\" ',54,NULL),
 (1286,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',30,'0Jeesus vastasi hänelle näin: \"Eräs mies oli matkalla Jerusalemista Jerikoon, kun\nrosvojoukko yllätti hänet. Rosvot veivät häneltä\nvaatteetkin päältä ja pieksivät hänet verille.\nSitten he lähtivät tiehensä ja jättivät hänet\nhenkihieveriin.',54,NULL),
 (1287,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',31,'1Samaa tietä sattui tulemaan\npappi, mutta miehen nähdessään hän väisti ja meni\nohi.',54,NULL),
 (1288,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',32,'2Samoin teki paikalle osunut leeviläinen:\nkun hän näki miehen, hänkin väisti ja meni ohi. ',54,NULL),
 (1289,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',33,'3\"Mutta sitten tuli samaa tietä muuan\nsamarialainen. Kun hän saapui paikalle ja näki\nmiehen, hänen tuli tätä sääli.',54,NULL),
 (1290,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',34,'4Hän meni miehen\nluo, valeli tämän haavoihin öljyä ja viiniä ja sitoi\nne. Sitten hän nosti miehen juhtansa selkään, vei\nhänet majataloon ja piti hänestä huolta.',54,NULL),
 (1291,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',35,'5Seuraavana aamuna hän otti kukkarostaan kaksi\ndenaaria, antoi ne majatalon isännälle ja sanoi:\n\'Hoida häntä. Jos sinulle koituu enemmän kuluja,\nminä korvaan ne, kun tulen takaisin.\'',54,NULL),
 (1292,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',36,'6Kuka\nnäistä kolmesta sinun mielestäsi oli ryöstetyn\nmiehen lähimmäinen?\" ',54,NULL),
 (1293,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',37,'7Lainopettaja vastasi: \"Se, joka osoitti hänelle\nlaupeutta.\" Jeesus sanoi: \"Mene ja tee sinä samoin.\"   ',54,NULL),
 (1294,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',1,'Kerran, kun Jeesus oli ollut rukoilemassa, eräs\nhänen opetuslapsistaan pyysi: \"Herra, opeta meitä\nrukoilemaan, niin kuin Johanneskin opetti\nopetuslapsiaan.\" ',55,NULL),
 (1295,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',2,'Jeesus sanoi heille: \"Kun rukoilette, sanokaa\nnäin:   ',55,NULL),
 (1296,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',5,'Jeesus sanoi vielä: \"Kuvitelkaa, että joku teistä menee keskellä yötä\nystävänsä luo ja sanoo: \'Veli hyvä, lainaa minulle\nkolme leipää.',55,NULL),
 (1297,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',6,'Eräs ystäväni poikkesi matkallaan\nluokseni, eikä minulla ole tarjota hänelle mitään.\'',55,NULL),
 (1298,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',7,'Toinen vastaa sisältä: \'Älä häiritse minua. Ovi\non jo lukossa, ja minä olen nukkumassa lasten\nkanssa. En minä voi nousta antamaan mitään.\'',55,NULL),
 (1299,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',8,'Mutta minä sanon teille: vaikka hän ei\nnousisikaan antamaan toiselle leipää pelkkää\nystävyyttään, hän kuitenkin tekee sen, kun tämä\nhellittämättä pyytää, ja hän antaa niin paljon kuin\ntoinen tarvitsee. ',55,NULL),
 (1300,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',9,'\"Niinpä sanon teille: Pyytäkää, niin teille\nannetaan. Etsikää, niin te löydätte. Kolkuttakaa,\nniin teille avataan.',55,NULL),
 (1301,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',10,'0Sillä pyytävä saa, etsijä\nlöytää, ja jokaiselle, joka kolkuttaa, avataan.',55,NULL),
 (1302,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',11,'1Ei kai kukaan teistä ole sellainen isä, että\nantaa pojalleen käärmeen, kun poika pyytää kalaa?',55,NULL),
 (1303,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',12,'2Tai skorpionin, kun hän pyytää munaa?',55,NULL),
 (1304,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',13,'3Jos\nkerran te pahat ihmiset osaatte antaa lapsillenne\nkaikenlaista hyvää, niin totta kai teidän Isänne\npaljon ennemmin antaa taivaasta Pyhän Hengen niille,\njotka sitä häneltä pyytävät.\"   ',55,NULL),
 (1305,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',17,'7Mutta Jeesus tiesi, mitä heillä oli mielessä, ja\nsanoi: \"Jokainen valtakunta, joka jakautuu ja taistelee\nitseään vastaan, tuhoutuu, ja talot sortuvat toinen\ntoisensa päälle.',55,NULL),
 (1306,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',18,'8Jos nyt Saatana taistelee\nitseään vastaan, kuinka sen valtakunta voi pysyä\nkoossa? Tehän sanotte, että minä ajan pahoja henkiä\nihmisistä Belsebulin avulla.',55,NULL),
 (1307,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',19,'9Mutta jos minä ajan\npahoja henkiä ihmisistä Belsebulin avulla, kenen\navulla sitten teikäläiset niitä karkottavat? Heistä\nte saatte itsellenne tuomarit.',55,NULL),
 (1308,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',20,'0Jos minä sitä\nvastoin ajan pahoja henkiä ihmisistä Jumalan\nsormella, silloinhan Jumalan valtakunta on jo tullut\nteidän luoksenne. ',55,NULL),
 (1309,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',21,'1\"Kun väkevä mies vartioi linnaansa ase kädessä,\nhänen omaisuutensa on turvassa.',55,NULL),
 (1310,'Anonymous','2008-10-17 16:32:31','Anonymous','2008-10-17 16:32:31',22,'2Mutta jos toinen\nvielä väkevämpi hyökkää hänen kimppuunsa ja voittaa\nhänet, tuo väkevämpi ottaa häneltä aseet ja\nvarusteet, joihin hän luotti, ja jakaa saamansa\nsaaliin.',55,NULL),
 (1311,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',23,'3Joka ei ole minun puolellani, on minua\nvastaan, ja joka ei yhdessä minun kanssani kokoa, se\nhajottaa.       \"Tämä sukupolvi on paha sukupolvi. Se vaatii\nmerkkiä, mutta ainoa merkki, joka sille annetaan, on\nJoonan merkki.',55,NULL),
 (1312,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',30,'0Niin kuin Joona oli merkki\nniniveläisille, niin on Ihmisen Poika oleva merkkinä\ntälle sukupolvelle.',55,NULL),
 (1313,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',31,'1Etelän kuningatar herää\ntuomiolle yhdessä tämän sukupolven ihmisten kanssa\nja langettaa heille tuomion. Hän tuli maan ääristä\nsaakka kuulemaan Salomon viisautta, ja tässä teillä\non enemmän kuin Salomo!',55,NULL),
 (1314,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',32,'2Niniven asukkaat\nnousevat tuomiolle yhdessä tämän sukupolven kanssa\nja langettavat sille tuomion. He kääntyivät, kun\nJoona saarnasi heille, ja tässä teillä on enemmän\nkuin Joona!     ',55,NULL),
 (1315,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',39,'9Mutta Herra sanoi hänelle: \"Te fariseukset kyllä puhdistatte maljanne ja\nvatinne ulkopuolen, mutta teidän oma sisimpänne on\ntäynnä riistoa ja pahuutta.',55,NULL),
 (1316,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',40,'0Te mielettömät! Eikö\nsama, joka on tehnyt ulkopuolen, ole tehnyt\nsisäpuolenkin?',55,NULL),
 (1317,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',41,'1Mitä maljassa sisällä on, se\nantakaa köyhille. Silloin kaikki on teille puhdasta. ',55,NULL),
 (1318,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',42,'2\"Voi teitä, fariseukset! Te maksatte kymmenykset\njopa mintusta ja ruudanlehdistä ja kaikista\nvihanneksista, mutta vähät välitätte\noikeudenmukaisuudesta ja rakkaudesta Jumalaan.\nNäitähän teidän pitäisi noudattaa, eikä lyödä laimin\nnoita muitakaan käskyjä. ',55,NULL),
 (1319,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',43,'3\"Voi teitä, fariseukset! Te istutte synagogassa\nmielellänne etumaisilla paikoilla ja olette\nhyvillänne, kun ihmiset toreilla tervehtivät teitä.',55,NULL),
 (1320,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',44,'4Voi teitä! Te olette kuin merkitsemättömät\nhaudat, joita ihmiset tietämättään tallaavat.\"* ',55,NULL),
 (1321,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',45,'5Silloin eräs lainopettajista huomautti hänelle:\n\"Opettaja, tuollaisella puheella sinä loukkaat myös\nmeitä.\" ',55,NULL),
 (1322,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',46,'6Jeesus vastasi: \"Voi teitäkin, lainopettajat! Te panette ihmisten\nharteille taakkoja, jotka ovat raskaita kantaa,\nmutta itse ette kajoa niihin edes yhdellä sormella. ',55,NULL),
 (1323,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',47,'7\"Voi teitä! Te rakennatte muistomerkkejä\nprofeettojen haudoille, mutta ketkä heidät\ntappoivat? Teidän isänne.',55,NULL),
 (1324,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',48,'8Näin te tunnustatte ja\nhyväksytte isienne teot: he tappoivat profeetat, ja\nte rakennatte profeetoille muistomerkit.',55,NULL),
 (1325,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',49,'9Tämän\nvuoksi onkin Jumalan Viisaus sanonut: \'Minä lähetän\nheidän luokseen profeettoja ja apostoleja, ja he\ntappavat ja vainoavat näitä.\'',55,NULL),
 (1326,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',50,'0Niinpä tämä\nsukupolvi saa maksaa kaikesta profeettojen verestä,\njoka maailman luomisesta lähtien on vuodatettu,',55,NULL),
 (1327,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',51,'1Abelin verestä aina Sakarjan vereen asti, hänen,\njoka surmattiin alttarin ja pyhäkön välille. Niin,\nminä sanon teille: tämä sukupolvi vaaditaan vielä\ntilille. ',55,NULL),
 (1328,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',52,'2\"Voi teitä, lainopettajat! Te olette vieneet\ntiedon avaimen. Itse te ette ole menneet sisälle, ja\nniitäkin, jotka ovat halunneet mennä, te olette\nestäneet.\" ',55,NULL),
 (1329,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',53,'3Kun Jeesus lähti sieltä, lainopettajat ja\nfariseukset ahdistivat hellittämättä häntä\nkysymyksillään ja ottivat puheeksi kaikenlaisia\nasioita yrittäen houkutella hänet ansaan.',55,NULL),
 (1330,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',54,'4He\nkuuntelivat valppaasti saadakseen hänet kiinni hänen\nomista sanoistaan. ',55,NULL),
 (1331,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',1,'Väkeä oli tällä välin kokoontunut tuhatmäärin,\nniin että ihmiset jo tallasivat toisiaan. Jeesus rupesi ensin puhumaan opetuslapsilleen: \"Varokaa fariseusten hapatetta, tekopyhyyttä.',56,NULL),
 (1332,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',2,'Ei\nole kätköä, joka ei paljastu, eikä salaisuutta, joka\nei tule ilmi.',56,NULL),
 (1333,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',3,'Niinpä se, mitä te sanotte\npimeässä, kuullaan päivänvalossa, ja mitä te neljän\nseinän sisällä kuiskaatte toisen korvaan, se\nkuulutetaan julki katoilta.   ',56,NULL),
 (1334,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',8,'\"Minä sanon teille: Joka tunnustautuu minun\nomakseni ihmisten edessä, sen on Ihmisen Poika\ntunnustava omakseen Jumalan enkelien edessä.',56,NULL),
 (1335,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',9,'Mutta joka ihmisten edessä kieltää minut, se\ntullaan kieltämään Jumalan enkelien edessä.',56,NULL),
 (1336,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',10,'0Ja\njokaiselle, joka sanoo jotakin Ihmisen Poikaa\nvastaan, annetaan anteeksi, mutta sille, joka herjaa\nPyhää Henkeä, ei anteeksi anneta. ',56,NULL),
 (1337,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',11,'1\"Kun teitä kuljetetaan synagogiin ja\nviranomaisten ja esivallan eteen, älkää olko\nhuolissanne siitä, mitä puhutte ja miten puolustatte\nitseänne.',56,NULL),
 (1338,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',12,'2Kun se hetki tulee, Pyhä Henki neuvoo,\nmitä teidän on sanottava.\"   ',56,NULL),
 (1339,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',16,'6Ja hän esitti heille vertauksen: \"Oli rikas mies, joka sai maastaan hyvän sadon.',56,NULL),
 (1340,'Anonymous','2008-10-17 16:32:32','Anonymous','2008-10-17 16:32:32',17,'7Hän mietti itsekseen: \'Mitä tekisin? Minun\nsatoni ei mahdu enää mihinkään.\'',56,NULL),
 (1341,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',18,'8Hän päätti:\n\'Minäpä teen näin: puran aittani ja rakennan isommat\nniiden sijaan. Niihin minä kerään koko satoni ja\nkaiken muun, mitä omistan.',56,NULL),
 (1342,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',19,'9Sitten sanon\nitselleni: Kelpaa sinun elää! Sinulla on kaikkea\nhyvää varastossa moneksi vuodeksi. Lepää nyt, syö,\njuo ja nauti elämästä!\'',56,NULL),
 (1343,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',20,'0Mutta Jumala sanoi\nhänelle: \'Sinä hullu! Tänä yönä sinun sielusi\nvaaditaan sinulta takaisin. Ja kaikki, minkä olet\nitsellesi varannut -- kenelle se joutuu?\' ',56,NULL),
 (1344,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',21,'1\"Näin käy sen, joka kerää rikkautta itselleen\nmutta jolla ei ole aarretta Jumalan luona.\"   \"Sen tähden minä sanon teille: älkää kantako huolta\nhengestänne, siitä mitä söisitte, älkää\nruumiistanne, siitä millä sen vaatettaisitte.',56,NULL),
 (1345,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',23,'3Onhan henki enemmän kuin ruoka ja ruumis enemmän\nkuin vaatteet.',56,NULL),
 (1346,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',24,'4Katsokaa korppeja: eivät ne kylvä\neivätkä leikkaa, ei niillä ole vajaa eikä varastoa,\nja silti Jumala ruokkii ne. Kuinka paljon\narvokkaampia te olettekaan kuin linnut!',56,NULL),
 (1347,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',25,'5Kuka\nteistä voi murehtimalla lisätä elämänsä pituutta\nkyynäränkään vertaa?',56,NULL),
 (1348,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',26,'6Jos siis näinkään vähäinen\nasia ei ole teidän vallassanne, mitä te muusta\nhuolehditte? ',56,NULL),
 (1349,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',27,'7\"Katsokaa kukkia, kuinka ne nousevat maasta:\neivät ne näe vaivaa eivätkä kehrää. Minä sanon\nteille: ei edes Salomo kaikessa loistossaan ollut\nniin vaatetettu kuin mikä tahansa niistä.',56,NULL),
 (1350,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',28,'8Kun\nJumala tuolla tavoin pukee ruohon, joka tänään\nkasvaa kedolla ja huomenna joutuu uuniin, niin\npaljon ennemmin hän teistä huolehtii, te\nvähäuskoiset! ',56,NULL),
 (1351,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',29,'9\"Älkää siitä murehtiko, mitä söisitte tai\njoisitte. Älkää sitä etsikö.',56,NULL),
 (1352,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',30,'0Tätä kaikkea\nmaailman ihmiset tavoittelevat; teidän Isänne tietää\nkyllä, että te sitä tarvitsette.',56,NULL),
 (1353,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',31,'1Etsikää hänen\nvaltakuntaansa, niin te saatte myös kaiken tämän. ',56,NULL),
 (1354,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',32,'2\"Älä pelkää, pieni laumani. Teidän Isänne on\npäättänyt antaa teille valtakunnan. ',56,NULL),
 (1355,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',33,'3\"Myykää, mitä teillä on, ja antakaa köyhille.\nHankkikaa kukkarot, jotka eivät tyhjene, kootkaa\ntaivaisiin aarre, joka ei ehdy. Siellä ei varas\npääse siihen käsiksi eikä koi tee tuhojaan.',56,NULL),
 (1356,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',34,'4Missä on aarteenne, siellä on myös sydämenne.   ',56,NULL),
 (1357,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',39,'9\"Ymmärrättehän, että jos talon isäntä tietäisi,\nminä yön tuntina varas tulee, hän ei antaisi\nmurtautua taloonsa.',56,NULL),
 (1358,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',40,'0Olkaa tekin valmiit, sillä\nIhmisen Poika tulee hetkellä, jota ette aavista.\" ',56,NULL),
 (1359,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',41,'1Pietari sanoi: \"Herra, tarkoitatko näillä\nvertauksilla kaikkia vai ainoastaan meitä?\" ',56,NULL),
 (1360,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',42,'2Herra vastasi: \"Kuka on uskollinen ja viisas taloudenhoitaja,\nsellainen jonka isäntä asettaa huolehtimaan\npalvelusväestään ja jakamaan vilja-annokset\najallaan?',56,NULL),
 (1361,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',43,'3Autuas se palvelija, jonka hänen\nisäntänsä palatessaan tapaa näin tekemästä!',56,NULL),
 (1362,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',44,'4Totisesti: hänen hoitoonsa isäntä uskoo koko\nomaisuutensa. ',56,NULL),
 (1363,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',45,'5\"Palvelija saattaa kuitenkin ajatella: \'Isäntä\nei tule vielä pitkään aikaan!\' Niin hän alkaa piestä\npalvelijoita ja palvelustyttöjä, syödä ja juoda ja\njuopotella.',56,NULL),
 (1364,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',46,'6Mutta päivänä, jota tuo palvelija ei\narvaa, hetkenä, jota hän ei tiedä, hänen isäntänsä\ntulee ja hakkaa hänet kuoliaaksi, ja niin palvelija\nsaa saman kohtalon kuin epäuskoiset. ',56,NULL),
 (1365,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',47,'7\"Jos palvelija tietää, mitä hänen isäntänsä\ntahtoo, mutta ei varaudu siihen eikä toimi hänen\ntahtonsa mukaan, hän saa monta raipaniskua.',56,NULL),
 (1366,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',48,'8Jos\ntaas palvelija tietämättään tekee sellaista, mistä\nrangaistaan raipoin, hän pääsee vähillä iskuilla.\nJolle on paljon annettu, siltä paljon vaaditaan, ja\njolle on paljon uskottu, se pannaan paljosta\nvastaamaan.     \"Kun te näette pilven nousevan lännestä, sanotte\nheti: \'Tulee sade\', ja niin tuleekin.',56,NULL),
 (1367,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',55,'5Tai kun\ntuulee etelästä, te sanotte: \'Tulee helle\', ja niin\ntulee.',56,NULL),
 (1368,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',56,'6Te tekopyhät! Maan ja taivaan ilmiöt te\nkyllä osaatte tulkita, kuinka sitten ette tätä\naikaa? ',56,NULL),
 (1369,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',57,'7\"Miksi te ette jo itse ratkaise, mikä on oikein?',56,NULL),
 (1370,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',58,'8Kun olet vastapuolesi kanssa menossa oikeuteen,\ntee matkalla kaikkesi, että pääsisit sovintoon hänen\nkanssaan. Muuten hän raahaa sinut tuomarin eteen, ja\ntuomari luovuttaa sinut vartijalle, joka panee sinut\nvankilaan.',56,NULL),
 (1371,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',59,'9Usko minua: sieltä sinä et pääse,\nennen kuin olet maksanut kaiken viimeistä lanttia\nmyöten.\" ',56,NULL),
 (1372,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',1,'Juuri siihen aikaan Jeesuksen luo tuli ihmisiä,\njotka kertoivat Pilatuksen surmauttaneen uhraamaan\ntulleita galilealaisia, niin että heidän verensä oli\nsekoittunut uhrieläinten vereen.',57,NULL),
 (1373,'Anonymous','2008-10-17 16:32:33','Anonymous','2008-10-17 16:32:33',2,'Jeesus sanoi\nsiihen: \"Luuletteko, että he olivat suurempia\nsyntisiä kuin kaikki muut galilealaiset, koska\nsaivat tuollaisen lopun?',57,NULL),
 (1374,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',3,'Eivät suinkaan --\nsamalla tavoin te kaikki olette tuhon omia, ellette\nkäänny.',57,NULL),
 (1375,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',4,'Entä ne kahdeksantoista, jotka saivat\nsurmansa, kun Siloan torni sortui heidän päälleen?\nLuuletteko, että he olivat syyllistyneet johonkin\npahempaan kuin muut jerusalemilaiset?',57,NULL),
 (1376,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',5,'Eivät\nsuinkaan -- yhtä lailla te kaikki olette tuhon omia,\nellette käänny.\"     ',57,NULL),
 (1377,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',14,'4Kun synagogan esimies näki, että Jeesus paransi\nsairaan sapattina, hän suutuksissaan sanoi paikalla\noleville: \"Viikossa on kuusi päivää työtä varten,\ntulkaa silloin parannettaviksi älkääkä sapattina.\"',57,NULL),
 (1378,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',15,'5Herra vastasi: \"Te tekopyhät! Jokainen teistä\nkyllä päästää sapattina härkänsä tai aasinsa\nkytkyestä ja vie sen juomaan.',57,NULL),
 (1379,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',16,'6Tätä Abrahamin\ntytärtä on Saatana pitänyt siteissään jo\nkahdeksantoista vuotta. Eikö häntä olisi saanut\npäästää vapaaksi sapatinpäivänä?\"',57,NULL),
 (1380,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',17,'7Jeesuksen\nvastauksen kuullessaan kaikki hänen vastustajansa\nolivat häpeissään, mutta kansa iloitsi kaikista\nihmeteltävistä teoista, joita hän teki.   ',57,NULL),
 (1381,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',20,'0Vielä hän sanoi: \"Mihin vertaisin Jumalan\nvaltakuntaa?',57,NULL),
 (1382,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',21,'1Se on kuin hapate. Kun nainen\nsekoitti sen kolmeen vakalliseen jauhoja, koko\ntaikina happani.\"   Jeesus vastasi: ',57,NULL),
 (1383,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',24,'4\"Kilvoitelkaa päästäksenne sisään ahtaasta\novesta. Minä sanon teille: monet yrittävät mennä\nsisään mutta eivät siihen kykene. ',57,NULL),
 (1384,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',25,'5\"Tulee hetki, jolloin talon isäntä nousee ja\nsulkee oven. Kun te sitten seisotte ulkona,\nkolkutatte ovelle ja huudatte: \'Herra, avaa\nmeille!\', hän vastaa: \'Keitä te olette? En tunne\nteitä.\'',57,NULL),
 (1385,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',26,'6Silloin te alatte selittää: \'Mehän\nsöimme ja joimme sinun kanssasi, ja sinä opetit\nmeidän kaduillamme.\'',57,NULL),
 (1386,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',27,'7Mutta hän sanoo: \'Keitä te\nolette? En tunne teitä. Menkää pois minun luotani,\nkaikki te vääryydentekijät.\' ',57,NULL),
 (1387,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',28,'8\"Siellä te itkette hammasta purren, kun näette\nAbrahamin ja Iisakin ja Jaakobin ja kaikki profeetat\nJumalan valtakunnassa mutta huomaatte, että teidät\nitsenne on suljettu ulos.',57,NULL),
 (1388,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',29,'9Idästä ja lännestä,\npohjoisesta ja etelästä tulee ihmisiä, jotka käyvät\naterialle Jumalan valtakunnassa.',57,NULL),
 (1389,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',30,'0Monet viimeiset\novat silloin ensimmäisiä, monet ensimmäiset\nviimeisiä.\"   ',57,NULL),
 (1390,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',32,'2Mutta hän vastasi: \"Menkää ja sanokaa sille ketulle: \'Tänään ja\nhuomenna minä ajan ihmisistä pahoja henkiä ja\nparannan sairaita, ja kolmantena päivänä saan työni\npäätökseen.\'',57,NULL),
 (1391,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',33,'3Mutta tänään ja huomenna ja\nseuraavanakin päivänä minun on jatkettava kulkuani -\n- eihän ole mahdollista, että profeetta surmataan\nmuualla kuin Jerusalemissa. ',57,NULL),
 (1392,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',34,'4\"Jerusalem, Jerusalem! Sinä tapat profeetat ja\nkivität ne, jotka on lähetetty sinun luoksesi. Miten\nmonesti olenkaan tahtonut koota lapsesi, niin kuin\nkanaemo kokoaa poikaset siipiensä suojaan! Mutta te\nette tahtoneet tulla.',57,NULL),
 (1393,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',35,'5Kuulkaa siis: teidän\ntemppelinne on jäävä asujaansa vaille. Ja minä sanon\nteille, että te ette minua näe ennen kuin sinä\npäivänä, jona sanotte: \'Siunattu olkoon hän, joka\ntulee Herran nimessä!\'\" ',57,NULL),
 (1394,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',1,'Jeesus meni sapattina erään fariseusten\njohtomiehen kotiin aterialle, ja kaikki\ntarkkailivat, mitä hän tekisi.',58,NULL),
 (1395,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',2,'Kävi niin, että\nhänen luokseen tuli vesipöhöä sairastava mies.',58,NULL),
 (1396,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',3,'Jeesus kääntyi lainopettajien ja fariseusten\npuoleen ja kysyi: \"Onko sapattina lupa parantaa vai\nei?\"',58,NULL),
 (1397,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',4,'He eivät sanoneet siihen mitään. Silloin\nJeesus kosketti miestä, paransi hänet ja lähetti\nhänet pois.',58,NULL),
 (1398,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',5,'Sitten hän taas kysyi: \"Miten te itse\nteette? Jos jonkun poika tai härkä putoaa kaivoon,\nniin kai hän heti nostaa sen sieltä, vaikka olisikin\nsapatti?\"',58,NULL),
 (1399,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',6,'Tähän he eivät kyenneet vastaamaan.   ',58,NULL),
 (1400,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',12,'2Sitten Jeesus sanoi talon isännälle: \"Kun\njärjestät päivälliset tai illalliset, älä kutsu\nystäviäsi, älä veljiäsi, sukulaisiasi äläkä rikkaita\nnaapureita. Hehän saattavat vuorostaan kutsua sinut,\nja näin sinä saat kaikesta palkan.',58,NULL),
 (1401,'Anonymous','2008-10-17 16:32:34','Anonymous','2008-10-17 16:32:34',13,'3Ei -- kun\nsinä järjestät pidot, kutsu köyhiä ja raajarikkoja,\nrampoja ja sokeita.',58,NULL),
 (1402,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',14,'4Autuas olet, kun aika tulee,\nsillä he eivät pysty palkitsemaan sinua. Sinä saat\npalkkasi silloin, kun vanhurskaat herätetään\nkuolleista.\"   ',58,NULL),
 (1403,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',16,'6Jeesus vastasi näin: \"Eräs mies järjesti suuret pidot ja oli kutsunut\npaljon vieraita.',58,NULL),
 (1404,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',17,'7Kun pitojen oli määrä alkaa,\nhän lähetti palvelijansa sanomaan kutsutuille:\n\'Tulkaa, kaikki on jo valmiina.\'',58,NULL),
 (1405,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',18,'8Mutta yksi\ntoisensa jälkeen nämä alkoivat esittää verukkeita.\n\'Olen ostanut pellon\', sanoi yksi, \'minun täytyy\nmennä katsomaan sitä. Suothan anteeksi, etten pääse\ntulemaan.\'',58,NULL),
 (1406,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',19,'9\'Ostin viisi härkäparia\', sanoi\ntoinen, \'ja olen lähdössä kokeilemaan niitä. Suothan\nanteeksi, etten pääse tulemaan.\'',58,NULL),
 (1407,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',20,'0Kolmas sanoi:\n\'Olen juuri mennyt naimisiin enkä siksi voi tulla.\' ',58,NULL),
 (1408,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',21,'1\"Palvelija palasi ja kertoi tämän herralleen.\nSilloin isäntä vihastui ja sanoi palvelijalle: \'Mene\nkiireesti kaupungin kaduille ja toreille ja tuo\ntänne köyhät ja raajarikot, sokeat ja rammat.\'',58,NULL),
 (1409,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',22,'2Palvelija tuli sanomaan: \'Herra, olen tehnyt\nniin kuin käskit, mutta vielä on tilaa.\'',58,NULL),
 (1410,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',23,'3Silloin\nherra sanoi: \'Mene maanteille ja kylien kujille ja\nvaadi ihmisiä tulemaan, jotta taloni täyttyisi.',58,NULL),
 (1411,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',24,'4Ja siitä voitte olla varmat, että yksikään\nnoista, jotka minä kutsuin, ei pääse minun\npitopöytääni!\'\"   ',58,NULL),
 (1412,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',26,'6\"Jos joku tulee minun luokseni mutta ei ole\nvalmis luopumaan isästään ja äidistään, vaimostaan\nja lapsistaan, veljistään ja sisaristaan, vieläpä\nomasta elämästään, hän ei voi olla minun\nopetuslapseni.',58,NULL),
 (1413,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',27,'7Joka ei kanna ristiään ja kulje\nminun jäljessäni, ei voi olla minun opetuslapseni.',58,NULL),
 (1414,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',28,'8Jos joku teistä aikoo rakentaa tornin, niin kai\nhän ensin istuutuu arvioimaan kustannuksia\nnähdäkseen, onko hänellä varoja rakentaa se\nvalmiiksi.',58,NULL),
 (1415,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',29,'9Muuten voi käydä niin, että hän\nlaskee perustuksen mutta joutuu jättämään työn\nkesken. Silloin kaikki, jotka tämän näkevät, alkavat\npilkata häntä:',58,NULL),
 (1416,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',30,'0\'On siinäkin mies! Alkoi\nrakentaa, mutta kesken se jäi.\' ',58,NULL),
 (1417,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',31,'1\"Tai jos kuningas on lähdössä taisteluun toista\nkuningasta vastaan, niin kai hän ensin istuutuu\nharkitsemaan, pystyykö hän kymmenellätuhannella\nmiehellä kohtaamaan vihollisen, joka on tulossa\nkahdenkymmenentuhannen miehen voimalla.',58,NULL),
 (1418,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',32,'2Ellei\nhän siihen pysty, hän lähettää\nrauhanneuvottelijoita, kun vihollinen on vielä\nkaukana. ',58,NULL),
 (1419,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',33,'3\"Tietäkää siis: yksikään teistä ei voi olla\nminun opetuslapseni, ellei hän luovu kaikesta, mitä\nhänellä on. ',58,NULL),
 (1420,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',34,'4\"Suola on tarpeellista. Mutta jos suolakin\nmenettää makunsa, millä se saadaan taas suolaiseksi?',58,NULL),
 (1421,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',35,'5Ei sillä ole käyttöä, ei pellossa eikä\ntunkiossa. Pois se heitetään. Jolla on korvat, se\nkuulkoon!\" ',58,NULL),
 (1422,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',1,'Publikaanit ja muut syntiset tulivat Jeesuksen\nluo kuullakseen häntä.',59,NULL),
 (1423,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',2,'Fariseukset ja\nlainopettajat sanoivat paheksuen: \"Tuo mies hyväksyy\nsyntiset seuraansa ja syö heidän kanssaan.\" ',59,NULL),
 (1424,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',3,'Silloin Jeesus esitti heille vertauksen: ',59,NULL),
 (1425,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',4,'\"Jos jollakin teistä on sata lammasta ja yksi\nniistä katoaa autiomaahan, niin totta kai hän jättää\nne yhdeksänkymmentäyhdeksän, lähtee sen kadonneen\nperään ja etsii, kunnes löytää sen.',59,NULL),
 (1426,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',5,'Kun hän\nlöytää lampaansa, hän nostaa sen iloiten\nhartioilleen,',59,NULL),
 (1427,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',6,'ja kotiin tultuaan hän kutsuu\nystävänsä ja naapurinsa ja sanoo heille: \'Iloitkaa\nkanssani! Minä löysin lampaani, joka oli\nkadoksissa.\'',59,NULL),
 (1428,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',7,'Minä sanon teille: näin on\ntaivaassakin. Yhdestä syntisestä, joka kääntyy,\niloitaan siellä enemmän kuin\nyhdeksästäkymmenestäyhdeksästä hurskaasta, jotka\neivät ole parannuksen tarpeessa. ',59,NULL),
 (1429,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',8,'\"Tai jos naisella on kymmenen hopearahaa ja hän\nkadottaa niistä yhden, niin totta kai hän sytyttää\nlampun, lakaisee huoneen ja etsii tarkoin, kunnes\nlöytää sen.',59,NULL),
 (1430,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',9,'Ja rahan löydettyään hän kutsuu\nystävättärensä ja naapurin naiset ja sanoo:\n\'Iloitkaa kanssani! Minä löysin rahan, jonka olin\nkadottanut.\'',59,NULL),
 (1431,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',10,'0Yhtä lailla, sen sanon teille,\niloitsevat Jumalan enkelit yhdestäkin syntisestä,\njoka tekee parannuksen.\"   \"Eräällä miehellä oli kaksi poikaa.',59,NULL),
 (1432,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',12,'2Nuorempi\nheistä sanoi isälleen: \'Isä, anna minulle osuuteni\nomaisuudestasi.\' Isä jakoi omaisuutensa poikien\nkesken.',59,NULL),
 (1433,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',13,'3Jo muutaman päivän päästä nuorempi\nkokosi kaikki varansa ja lähti kauas vieraille\nmaille. Siellä hän tuhlasi koko omaisuutensa\nviettäen holtitonta elämää.',59,NULL),
 (1434,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',14,'4Kun hän oli pannut\nkaiken menemään, siihen maahan tuli ankara\nnälänhätä, ja hän joutui kärsimään puutetta.',59,NULL),
 (1435,'Anonymous','2008-10-17 16:32:35','Anonymous','2008-10-17 16:32:35',15,'5Silloin hän meni erään sikäläisen miehen\npalvelukseen, ja tämä lähetti hänet tiluksilleen\nsikopaimeneksi.',59,NULL),
 (1436,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',16,'6Nälkäänsä hän olisi halunnut\nsyödä palkoja, sikojen ruokaa, mutta niitäkään ei\nhänelle annettu. ',59,NULL),
 (1437,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',17,'7\"Silloin poika meni itseensä ja ajatteli: \'Minun\nisäni palkkalaisilla on kaikilla yllin kyllin\nruokaa, mutta minä näännyn täällä nälkään.',59,NULL),
 (1438,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',18,'8Ei,\nnyt minä lähden isäni luo ja sanon hänelle: Isä,\nminä olen tehnyt syntiä taivasta vastaan ja sinua\nvastaan.',59,NULL),
 (1439,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',19,'9En ole enää sen arvoinen, että minua\nkutsutaan pojaksesi. Ota minut palkkalaistesi\njoukkoon.\'',59,NULL),
 (1440,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',20,'0Niin hän lähti isänsä luo. \"Kun poika vielä oli kaukana, isä näki hänet ja\nheltyi. Hän juoksi poikaa vastaan, sulki hänet\nsyliinsä ja suuteli häntä.',59,NULL),
 (1441,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',21,'1Poika sanoi hänelle:\n\'Isä, minä olen tehnyt syntiä taivasta vastaan ja\nsinua vastaan. En ole enää sen arvoinen, että minua\nkutsutaan pojaksesi.\'',59,NULL),
 (1442,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',22,'2Mutta isä sanoi\npalvelijoilleen: \'Hakekaa joutuin parhaat vaatteet\nja pukekaa hänet niihin, pankaa hänelle sormus\nsormeen ja kengät jalkaan.',59,NULL),
 (1443,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',23,'3Tuokaa syöttövasikka\nja teurastakaa se. Nyt syödään ja vietetään\nilojuhlaa!',59,NULL),
 (1444,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',24,'4Minun poikani oli kuollut mutta\nheräsi eloon, hän oli kadoksissa, mutta nyt hän on\nlöytynyt.\' Niin alkoi iloinen juhla. ',59,NULL),
 (1445,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',25,'5\"Vanhempi poika oli pellolla. Kun hän sieltä\npalatessaan lähestyi kotia, hän kuuli laulun, soiton\nja tanssin.',59,NULL),
 (1446,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',26,'6Hän huusi luokseen yhden\npalvelijoista ja kysyi, mitä oli tekeillä.',59,NULL),
 (1447,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',27,'7Palvelija vastasi: \'Veljesi tuli kotiin, ja\nisäsi käski teurastaa syöttövasikan, kun sai hänet\nterveenä takaisin.\'',59,NULL),
 (1448,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',28,'8Silloin vanhempi veli\nsuuttui eikä halunnut mennä sisään. Isä tuli ulos ja\nsuostutteli häntä,',59,NULL),
 (1449,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',29,'9mutta hän vastasi: \'Kaikki\nnämä vuodet minä olen raatanut sinun hyväksesi enkä\nole kertaakaan jättänyt käskyäsi täyttämättä. Silti\net ole koskaan antanut minulle edes vuohipahaista\njuhliakseni ystävieni kanssa.',59,NULL),
 (1450,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',30,'0Mutta kun tämä\nsinun poikasi tulee, tämä, joka on hävittänyt\nomaisuutesi porttojen parissa, sinä teurastat\nhänelle syöttövasikan!\'',59,NULL),
 (1451,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',31,'1Isä vastasi hänelle:\n\'Poikani, sinä olet aina minun luonani, ja kaikki,\nmikä on minun, on sinun.',59,NULL),
 (1452,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',32,'2Mutta olihan nyt täysi\nsyy iloita ja riemuita. Sinun veljesi oli kuollut\nmutta heräsi eloon, hän oli kadoksissa mutta on nyt\nlöytynyt.\'\" ',59,NULL),
 (1453,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',1,'Jeesus puhui sitten opetuslapsilleen: \"Oli rikas\nmies, jolla oli taloudenhoitaja. Hänelle kanneltiin,\nettä taloudenhoitaja tuhlasi hänen omaisuuttaan.',60,NULL),
 (1454,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',2,'Hän kutsui tämän luokseen ja sanoi: \'Mitä minä\nsinusta kuulen! Tee tili toimistasi, minun\ntalouttani sinä et enää hoida.\'',60,NULL),
 (1455,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',3,'Mies mietti:\n\'Mitä minä nyt teen? Isäntä panee minut pois\ntaloudenhoitajan toimesta. Kaivaa en jaksa, kerjätä\nen kehtaa.',60,NULL),
 (1456,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',4,'-- Nytpä tiedän! Järjestän niin, että\ntoiset ottavat minut taloonsa, kun joudun lähtemään\ntyöpaikastani.\'',60,NULL),
 (1457,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',5,'Hän kutsui isäntänsä velalliset\nvuoron perään luokseen. \'Paljonko olet velkaa\nisännälleni?\' hän kysyi ensimmäiseltä.',60,NULL),
 (1458,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',6,'\'Sata\nastiaa öljyä\', tämä vastasi. Taloudenhoitaja sanoi:\n\'Tässä on velkakirjasi, istu ja merkitse äkkiä\nviisikymmentä.\'',60,NULL),
 (1459,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',7,'Sitten hän kysyi toiselta: \'Entä\nsinä, paljonko sinä olet velkaa?\' Tämä vastasi:\n\'Sata tynnyriä vehnää.\' Taloudenhoitaja sanoi:\n\'Tässä on velkakirjasi, merkitse\nkahdeksankymmentä.\'\" ',60,NULL),
 (1460,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',8,'Ja Herra kehui epärehellisen taloudenhoitajan\nviisautta. Hän sanoi: \"Tämän maailman lapset menettelevät toisiaan kohtaan\nviisaammin kuin valon lapset.',60,NULL),
 (1461,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',9,'Minä sanonkin\nteille: hankkikaa väärällä rikkaudella ystäviä,\njotka ottavat teidät iäisiin asuntoihin, kun tuota\nrikkautta ei enää ole.   ',60,NULL),
 (1462,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',14,'4Fariseukset kuulivat kaiken tämän. He olivat\nrahanahneita, ja niinpä he ivailivat Jeesusta. ',60,NULL),
 (1463,'Anonymous','2008-10-17 16:32:36','Anonymous','2008-10-17 16:32:36',15,'5Jeesus sanoi heille: \"Ihmisten edessä te olette olevinanne hurskaita,\nmutta Jumala tuntee teidän sydämenne. Se, mikä on\nihmisten silmissä arvokasta, on Jumalalle iljetys.     ',60,NULL),
 (1464,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',22,'2\"Sitten köyhä kuoli, ja enkelit veivät hänet\nAbrahamin huomaan. Rikaskin kuoli, ja hänet\nhaudattiin.',60,NULL),
 (1465,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',23,'3Kun hän tuonelan tuskissa kohotti\nkatseensa, hän näki kaukana Abrahamin ja Lasaruksen\nhänen rintaansa vasten.',60,NULL),
 (1466,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',24,'4Silloin hän huusi: \'Isä\nAbraham, armahda minua! Lähetä Lasarus tänne, että\nhän kastaisi sormenpäänsä veteen ja vilvoittaisi\nkieltäni. Näissä liekeissä on kauhea olla.\'',60,NULL),
 (1467,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',25,'5Mutta Abraham sanoi: \'Muista, poikani, että sinä\nsait eläessäsi hyvän osan, Lasarus huonon. Nyt hän\nsaa täällä vaivoihinsa lohdun, mutta sinä saat\nkärsiä tuskaa.',60,NULL),
 (1468,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',26,'6Sitä paitsi meidän välillämme on\nsyvä, ylipääsemätön kuilu, niin ettei täältä kukaan\nvoi tulla teidän luoksenne, vaikka tahtoisikin, eikä\nsieltä pääse kukaan kuilun yli meidän puolellemme.\'',60,NULL),
 (1469,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',27,'7Rikas mies sanoi: \'Isä, minä pyydän, lähetä\nhänet sitten vanhempieni taloon.',60,NULL),
 (1470,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',28,'8Minulla on\nviisi veljeä -- hänen pitäisi varoittaa heitä,\netteivät hekin joutuisi tähän kärsimyksen paikkaan.\'',60,NULL),
 (1471,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',29,'9Abraham vastasi: \'Heillä on Mooses ja profeetat.\nKuulkoot heitä.\'',60,NULL),
 (1472,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',30,'0\'Ei, isä Abraham\', mies sanoi,\n\'mutta jos joku kuolleiden joukosta menisi heidän\nluokseen, he kääntyisivät.\'',60,NULL),
 (1473,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',31,'1Mutta Abraham sanoi:\n\'Jos he eivät kuuntele Moosesta ja profeettoja, ei\nheitä saada uskomaan, vaikka joku nousisi\nkuolleista.\'\" ',60,NULL),
 (1474,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',1,'Jeesus sanoi opetuslapsilleen: \"Viettelysten täytyy tulla, se on väistämätöntä,\nmutta voi sitä, jonka kautta ne tulevat!',61,NULL),
 (1475,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',2,'Parempi\nolisi hänelle, että hänet heitettäisiin mereen\nmyllynkivi kaulassa, kuin että hän johdattaa\nlankeemukseen yhdenkään näistä vähäisistä.',61,NULL),
 (1476,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',3,'Pitäkää varanne! \"Jos veljesi tekee väärin, nuhtele häntä, ja jos hän\nsitten katuu, anna hänelle anteeksi.',61,NULL),
 (1477,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',4,'Vaikka hän\nseitsemästi päivässä rikkoisi sinua vastaan ja\nseitsemästi tulisi sanomaan sinulle: \'Minä kadun\',\nanna hänelle anteeksi.\" ',61,NULL),
 (1478,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',5,'Apostolit sanoivat Herralle: \"Anna meille\nvahvempi usko!\" ',61,NULL),
 (1479,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',6,'Herra sanoi: \"Jos teillä olisi uskoa edes sinapinsiemenen verran,\nte voisitte sanoa tälle silkkiäispuulle: \'Nouse\njuurinesi maasta ja istuta itsesi mereen\', ja se\ntottelisi teitä.         \"Tulee aika, jolloin te toivotte näkevänne edes\nyhden Ihmisen Pojan päivän mutta ette saa nähdä.',61,NULL),
 (1480,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',23,'3Teille sanotaan silloin: \'Hän on tuolla\', ja:\n\'Hän on täällä\', mutta älkää lähtekö minnekään,\nälkää juosko perässä.',61,NULL),
 (1481,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',24,'4Sillä niin kuin salama\nvälähtää ja valaisee taivaan äärestä ääreen, niin on\nIhmisen Poika oleva ilmestymisensä päivänä.',61,NULL),
 (1482,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',25,'5Mutta sitä ennen hänen täytyy kärsiä paljon, ja\ntämä sukupolvi hylkää hänet. ',61,NULL),
 (1483,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',26,'6\"Niin kuin oli Nooan päivinä, niin on Ihmisen\nPojan päivinä oleva.',61,NULL),
 (1484,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',27,'7Ihmiset söivät ja joivat,\nmenivät naimisiin ja naittivat tyttäriään aina\nsiihen päivään asti, jona Nooa meni arkkiin, ja\ntulva tuli ja tuhosi heidät kaikki.',61,NULL),
 (1485,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',28,'8On oleva\nniin kuin oli Lootin päivinä. Ihmiset söivät ja\njoivat, ostivat ja myivät, istuttivat ja rakensivat.',61,NULL),
 (1486,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',29,'9Mutta sinä päivänä, jona Loot lähti Sodomasta,\ntaivaasta satoi tulta ja tulikiveä, ja se tuhosi\nheidät kaikki. ',61,NULL),
 (1487,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',30,'0\"Samoin käy sinä päivänä, jona Ihmisen Poika\nilmestyy.',61,NULL),
 (1488,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',31,'1Jos sinä päivänä joku on katolla ja\nhänen tavaransa ovat sisällä talossa, hänen ei pidä\nmennä alas noutamaan niitä, ja jos joku on pellolla,\nhänen ei pidä palata kotiin.',61,NULL),
 (1489,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',32,'2Muistakaa Lootin\nvaimoa!',61,NULL),
 (1490,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',33,'3Joka yrittää turvata elämänsä, kadottaa\nsen, mutta joka sen kadottaa, on pelastava sen\nomakseen.',61,NULL),
 (1491,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',34,'4Minä sanon teille: Sinä yönä on kaksi\nsamalla vuoteella: toinen otetaan, toinen jätetään.',61,NULL),
 (1492,'Anonymous','2008-10-17 16:32:37','Anonymous','2008-10-17 16:32:37',35,'5Kaksi naista on yhdessä jauhamassa: toinen\notetaan, toinen jätetään. [',61,NULL),
 (1493,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',36,'6Kaksi miestä on\npellolla: toinen otetaan, toinen jätetään.]\"*',61,NULL),
 (1494,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',37,'7\"Missä, Herra?\" kysyivät opetuslapset. Hän\nsanoi: \"Missä on haaska, sinne kokoontuvat\nkorppikotkat.\" ',61,NULL),
 (1495,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',1,'Jeesus esitti opetuslapsille vertauksen\nopettaakseen, että aina tulee rukoilla, koskaan\nlannistumatta:',62,NULL),
 (1496,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',2,'\"Eräässä kaupungissa oli tuomari,\njoka ei pelännyt Jumalaa eikä piitannut ihmisistä.',62,NULL),
 (1497,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',3,'Samassa kaupungissa eli myös leskivaimo, joka\ntavan takaa tuli tuomarin luo pyytämään: \'Ratkaise\njo asiani, tuomitse riitapuoleni.\'',62,NULL),
 (1498,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',4,'Pitkään aikaan\ntuomari ei suostunut. Sitten hän kuitenkin ajatteli:\n\'Kaukana siitä, että pelkäisin Jumalaa tai\npiittaisin ihmisistä,',62,NULL),
 (1499,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',5,'mutta tästä leskestä on\nminulle vaivaa. Minä autan häntä saamaan oikeuden,\nmuuten hän ennen pitkää repii minulta silmät\npäästä.\'\" ',62,NULL),
 (1500,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',6,'Herra sanoi: \"Huomatkaa: noin ajatteli\nväärämielinen tuomari.',62,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (1501,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',7,'Tottahan sitten Jumala\nhankkii oikeuden valituilleen, jotka päivin ja öin\nhuutavat häntä avuksi. Hänkö viivyttäisi apuaan?',62,NULL),
 (1502,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',8,'Minä sanon teille: hän hankkii heille oikeuden\nviipymättä. Mutta kun Ihmisen Poika tulee, löytääkö\nhän uskoa maan päältä?\"   ',62,NULL),
 (1503,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',10,'0\"Kaksi miestä meni temppeliin rukoilemaan.\nToinen oli fariseus, toinen publikaani.',62,NULL),
 (1504,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',11,'1Fariseus\nasettui paikalleen seisomaan ja rukoili itsekseen:\n\'Jumala, minä kiitän sinua, etten ole sellainen kuin\nmuut ihmiset, rosvot, huijarit, huorintekijät tai\nvaikkapa tuo publikaani.',62,NULL),
 (1505,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',12,'2Minä paastoan kahdesti\nviikossa ja maksan kymmenykset kaikesta, siitäkin\nmitä ostan.\'',62,NULL),
 (1506,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',13,'3Publikaani seisoi taempana. Hän ei\ntohtinut edes kohottaa katsettaan taivasta kohti\nvaan löi rintaansa ja sanoi: \'Jumala, ole minulle\nsyntiselle armollinen!\' ',62,NULL),
 (1507,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',14,'4\"Minä sanon teille: hän lähti kotiinsa\nvanhurskaana, tuo toinen ei.*Jokainen, joka itsensä\nkorottaa, alennetaan, mutta joka itsensä alentaa, se\nkorotetaan.\"     ',62,NULL),
 (1508,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',24,'4Nähdessään sen Jeesus sanoi: \"Kuinka vaikea\nonkaan niiden, jotka paljon omistavat, päästä\nJumalan valtakuntaan!',62,NULL),
 (1509,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',25,'5Helpompi on kamelin mennä\nneulansilmästä kuin rikkaan päästä Jumalan\nvaltakuntaan.\"',62,NULL),
 (1510,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',26,'6\"Kuka sitten voi pelastua?\"\nkysyivät kuulijat.',62,NULL),
 (1511,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',27,'7Jeesus vastasi: \"Mikä on\nihmiselle mahdotonta, se on mahdollista Jumalalle.\"       ',62,NULL),
 (1512,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',1,'Jeesus tuli Jerikoon ja kulki kaupungin halki.',63,NULL),
 (1513,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',2,'Siellä asui mies, jonka nimi oli Sakkeus. Hän oli\npublikaanien esimies ja hyvin rikas.',63,NULL),
 (1514,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',3,'Hän halusi\nnähdä, mikä mies Jeesus oli, mutta ei pienikokoisena\nylettynyt kurkistamaan väkijoukon takaa.',63,NULL),
 (1515,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',4,'Niinpä\nhän juoksi jonkin matkaa edemmäs ja kiipesi\nmetsäviikunapuuhun nähdäkseen Jeesuksen, joka oli\ntulossa sitä tietä.',63,NULL),
 (1516,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',5,'Mutta tultuaan sille kohtaa\nJeesus katsoi ylös ja sanoi: \"Sakkeus, tule\nkiireesti alas. Tänään minun on määrä olla vieraana\nsinun kodissasi.\" ',63,NULL),
 (1517,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',6,'Sakkeus tuli kiireesti alas ja otti iloiten\nJeesuksen vieraakseen.',63,NULL),
 (1518,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',7,'Kun ihmiset näkivät tämän,\nhe sanoivat paheksuen: \"Syntisen miehen talon hän\notti majapaikakseen.\"',63,NULL),
 (1519,'Anonymous','2008-10-17 16:32:38','Anonymous','2008-10-17 16:32:38',8,'Mutta Sakkeus sanoi\nHerralle kaikkien kuullen: \"Herra, näin minä teen:\npuolet omaisuudestani annan köyhille, ja keneltä\nolen liikaa kiskonut, sille maksan nelinkertaisesti\ntakaisin.\"',63,NULL),
 (1520,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',9,'Sen kuultuaan Jeesus sanoi häneen\nviitaten: \"Tänään on pelastus tullut tämän perheen\nosaksi. Onhan hänkin Abrahamin poika.',63,NULL),
 (1521,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',10,'0Juuri\nsitä, mikä on kadonnut, Ihmisen Poika on tullut\netsimään ja pelastamaan.\"   ',63,NULL),
 (1522,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',12,'2Hän sanoi: \"Ylhäinen mies lähti matkalle kaukaiseen maahan\nsaadakseen siellä nimityksen kuninkaaksi ja\npalatakseen sitten takaisin.',63,NULL),
 (1523,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',13,'3Hän kutsui luokseen\nkymmenen palvelijaansa, antoi heille kullekin\nkultarahan*ja sanoi: \'Käyttäkää näitä parhaanne\nmukaan, kunnes minä palaan.\'',63,NULL),
 (1524,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',14,'4Hänen maanmiehensä\nkuitenkin vihasivat häntä ja lähettivät hänen\nperäänsä lähetystön sanomaan: \'Me emme tahdo sitä\nmiestä kuninkaaksemme.\' ',63,NULL),
 (1525,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',15,'5\"Mies palasi maahansa kuninkaana. Tultuaan hän\nkutsutti palvelijat luokseen kuullakseen, kuinka\ntuottavasti kukin oli saamaansa rahaa käyttänyt.',63,NULL),
 (1526,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',16,'6Ensimmäinen tuli hänen eteensä ja sanoi: \'Herra,\nantamasi kultaraha on tuottanut kymmenen lisää.\'',63,NULL),
 (1527,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',17,'7Kuningas sanoi hänelle: \'Mainiota! Olet hyvä\npalvelija. Koska aivan vähässäkin olet ollut\nuskollinen, saat hallintaasi kymmenen kaupunkia.\'',63,NULL),
 (1528,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',18,'8Toinen tuli ja sanoi: \'Herra, antamasi kultaraha\non tuottanut viisi kultarahaa.\'',63,NULL),
 (1529,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',19,'9Hänelle kuningas\nsanoi: \'Sinut minä panen hallitsemaan viittä\nkaupunkia.\' ',63,NULL),
 (1530,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',20,'0\"Mutta kun seuraava palvelija tuli, tämä sanoi:\n\'Herra, tässä on antamasi kultaraha. Olen\nsäilyttänyt sitä liinaan käärittynä.',63,NULL),
 (1531,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',21,'1Minä\npelkäsin sinua, koska olet armoton mies. Sinä otat,\nmitä et ole talteen pannut, ja leikkaat, mitä et ole\nkylvänyt.\'',63,NULL),
 (1532,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',22,'2Kuningas sanoi hänelle: \'Omien\nsanojesi mukaan minä sinut tuomitsen. Sinä kelvoton\npalvelija! Sinä siis tiesit, että minä olen armoton\nmies, että otan, mitä en ole talteen pannut, ja\nleikkaan, mitä en ole kylvänyt.',63,NULL),
 (1533,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',23,'3Mikset sitten\nvienyt minun rahaani pankkiin, niin että olisin\npalattuani voinut nostaa sen korkoineen?\' ',63,NULL),
 (1534,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',24,'4\"Kuningas sanoi vieressään seisoville miehille:\n\'Ottakaa häneltä raha pois ja antakaa se sille,\njolla on kymmenen kultarahaa.\'',63,NULL),
 (1535,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',25,'5He sanoivat:\n\'Herra, hänellä on jo kymmenen kultarahaa.\'',63,NULL),
 (1536,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',26,'6Mutta hän jatkoi: \'Jokaiselle, jolla on,\nannetaan, mutta jolla ei ole, siltä otetaan pois\nsekin, mitä hänellä on.',63,NULL),
 (1537,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',27,'7Mutta nuo viholliseni,\njotka eivät tahtoneet minua kuninkaakseen -- tuokaa\nheidät tänne ja teloittakaa heidät minun edessäni.\'\"    ',63,NULL),
 (1538,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',32,'2Miehet lähtivät ja havaitsivat kaiken olevan\nniin kuin Jeesus oli heille sanonut.',63,NULL),
 (1539,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',33,'3Kun he\nolivat irrottamassa varsaa, sen omistajat kysyivät:\n\"Miksi te viette varsan?\"',63,NULL),
 (1540,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',34,'4He vastasivat: \"Herra\ntarvitsee sitä.\"',63,NULL),
 (1541,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',35,'5He toivat varsan Jeesukselle,\nheittivät vaatteitaan sen selkään ja auttoivat\nJeesuksen ratsaille.',63,NULL),
 (1542,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',36,'6Kun hän sitten ratsasti,\nopetuslapset levittivät vaatteitaan tielle. ',63,NULL),
 (1543,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',37,'7Jeesuksen lähestyessä sitä paikkaa, mistä tie\nlaskeutuu Öljymäen rinnettä alas, koko opetuslasten\njoukko alkoi riemuissaan suureen ääneen ylistää\nJumalaa kaikista niistä voimateoista, jotka he\nolivat nähneet.',63,NULL),
 (1544,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',38,'8He huusivat:   ',63,NULL),
 (1545,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',39,'9Muutamat fariseukset sanoivat väkijoukon\nkeskeltä Jeesukselle: \"Opettaja, kiellä\nopetuslapsiasi!\"',63,NULL),
 (1546,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',40,'0Mutta Jeesus vastasi: \"Minä\nsanon teille: jos he olisivat vaiti, niin kivet\nhuutaisivat.\"     ',63,NULL),
 (1547,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',47,'7Hän opetti sitten joka päivä temppelissä.\nYlipapit, lainopettajat ja muut kansan johtomiehet\nmiettivät, miten raivaisivat hänet pois tieltä.',63,NULL),
 (1548,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',48,'8He eivät kuitenkaan keksineet, mitä tehdä, sillä\nkoko kansa oli jatkuvasti Jeesuksen ympärillä\nkuuntelemassa häntä. ',63,NULL),
 (1549,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',1,'Kun Jeesus eräänä päivänä taas opetti temppelissä\nkansaa ja julisti hyvää sanomaa, ylipapit ja\nlainopettajat tulivat yhdessä vanhimpien kanssa\nhänen luokseen',64,NULL),
 (1550,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',2,'ja sanoivat hänelle: \"Kerro\nmeille, millä valtuuksilla sinä tällaista teet. Kuka\non valtuuttanut sinut tähän?\"',64,NULL),
 (1551,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',3,'Jeesus vastasi\nheille: \"Minäkin teen teille kysymyksen. Sanokaa te\nminulle,',64,NULL),
 (1552,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',4,'oliko Johanneksen kaste peräisin\ntaivaasta vai ihmisistä.\"',64,NULL),
 (1553,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',5,'He neuvottelivat\nkeskenään: \"Jos sanomme: \'Taivaasta\', hän sanoo:\n\'Miksi sitten ette uskoneet häntä?\'',64,NULL),
 (1554,'Anonymous','2008-10-17 16:32:39','Anonymous','2008-10-17 16:32:39',6,'Jos taas\nsanomme: \'Ihmisistä\', kansa kivittää meidät, se kun\nuskoo lujasti, että Johannes oli profeetta.\"',64,NULL),
 (1555,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',7,'Niin\nhe vastasivat, etteivät tienneet sitä.',64,NULL),
 (1556,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',8,'Silloin\nJeesus sanoi: \"En minäkään sitten sano teille, millä\nvaltuuksilla minä kaikkea tätä teen.\"   \"Mies istutti viinitarhan, vuokrasi sen\nviljelijöille ja muutti pitkäksi aikaa pois maasta. ',64,NULL),
 (1557,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',10,'0\"Aikanaan hän sitten lähetti palvelijan\nviinitarhan viljelijöiden luo, jotta nämä antaisivat\nhänelle osan sadosta. Mutta viljelijät pieksivät\npalvelijan ja lähettivät tämän pois tyhjin käsin.',64,NULL),
 (1558,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',11,'1Silloin omistaja lähetti toisen palvelijan.\nViljelijät pieksivät tämänkin, häpäisivät häntä ja\nlähettivät hänet pois tyhjin käsin.',64,NULL),
 (1559,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',12,'2Mies lähetti\nvielä kolmannen palvelijan, mutta tämänkin he\nhakkasivat verille ja ajoivat tiehensä. ',64,NULL),
 (1560,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',13,'3\"Mitä minä teen?\' mietti viinitarhan omistaja.\n\'Minä lähetän sinne rakkaan poikani. Häneen he\nvarmaankaan eivät uskalla koskea.\'',64,NULL),
 (1561,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',14,'4Mutta kun\nvuokraajat näkivät pojan, he päättelivät yhdessä:\n\'Hän on perillinen. Tapetaan hänet, niin saamme\nhänen perintönsä.\'',64,NULL),
 (1562,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',15,'5He raahasivat hänet ulos\nviinitarhasta ja tappoivat hänet. -- Mitä\nviinitarhan omistaja nyt tekee heille?',64,NULL),
 (1563,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',16,'6Hän tulee\nja ottaa nuo viljelijät hengiltä ja antaa\nviinitarhansa toisille.\" \"Ei, ei!\" huusi kansa.',64,NULL),
 (1564,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',17,'7Jeesus katsoi ihmisiin ja\nsanoi: \"Mitä sitten tarkoittaa tämä kirjoitusten\nkohta:  ',64,NULL),
 (1565,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',18,'8Kuka ikinä tähän kiveen kaatuu, se ruhjoutuu, ja\nkenen päälle tämä kivi kaatuu, sen se murskaa.\" ',64,NULL),
 (1566,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',19,'9Lainopettajien ja ylipappien teki mieli ottaa\nJeesus kiinni siltä seisomalta, mutta he pelkäsivät\nkansaa. He ymmärsivät, että Jeesus oli\nvertauksessaan puhunut heistä.     ',64,NULL),
 (1567,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',34,'4Jeesus vastasi heille: \"Tässä maailmassa otetaan\nvaimo ja mennään vaimoksi.',64,NULL),
 (1568,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',35,'5Mutta tulevassa\nmaailmassa ne, jotka on katsottu ylösnousemuksen\narvoisiksi, eivät enää mene naimisiin.',64,NULL),
 (1569,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',36,'6He eivät\nenää voi kuolla, sillä he ovat enkelien kaltaisia.\nHe ovat Jumalan lapsia, ylösnousemuksesta osallisia.',64,NULL),
 (1570,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',37,'7Ja sen, että kuolleet nousevat ylös, on\nMooseskin osoittanut kertomuksessa palavasta\npensaasta. Hänhän sanoo, että Herra on Abrahamin\nJumala, Iisakin Jumala ja Jaakobin Jumala.',64,NULL),
 (1571,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',38,'8Ei\nhän ole kuolleiden Jumala, vaan elävien. Hänelle\nkaikki ovat eläviä.\" ',64,NULL),
 (1572,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',39,'9Jotkut lainopettajista sanoivat tähän: \"Hyvin\nvastasit, opettaja.\"',64,NULL),
 (1573,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',40,'0Silloin ei enää kenelläkään\nollut rohkeutta kysyä häneltä mitään.    ',64,NULL),
 (1574,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',44,'4Daavid siis kutsuu Messiasta herraksi. Kuinka\nMessias silloin voi olla hänen poikansa?\"   ',64,NULL),
 (1575,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',1,'Jeesus käänsi katseensa uhriarkkuun päin ja näki\nrikkaiden panevan siihen lahjoja.',65,NULL),
 (1576,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',2,'Hän näki myös,\nkuinka eräs köyhä leskivaimo pani arkkuun kaksi\npientä lanttia.',65,NULL),
 (1577,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',3,'Hän sanoi: \"Totisesti: tämä köyhä\nleski antoi enemmän kuin yksikään toinen.',65,NULL),
 (1578,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',4,'Kaikki\nnuo muut antoivat lahjansa liiastaan, mutta hän\nantoi vähästään, kaiken mitä hän elääkseen\ntarvitsi.\"      ',65,NULL),
 (1579,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',10,'0Ja hän jatkoi: \"Kansa nousee kansaa vastaan ja valtakunta\nvaltakuntaa vastaan,',65,NULL),
 (1580,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',11,'1ja joka puolella on suuria\nmaanjäristyksiä, nälänhätää ja ruttotauteja.\nKauhistuttavia asioita tapahtuu, ja taivaalla näkyy\nsuuria ennusmerkkejä. ',65,NULL),
 (1581,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',12,'2\"Mutta jo ennen tätä teidän kimppuunne käydään\nja teitä vainotaan. Teitä viedään tuomiolle\nsynagogiin, teitä teljetään vankilaan, te joudutte\nkuninkaiden ja maaherrojen eteen minun nimeni\ntähden.',65,NULL),
 (1582,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',13,'3Näin te saatte tilaisuuden todistaa\nminusta.',65,NULL),
 (1583,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',14,'4Painakaa vain mieleenne, että teidän ei\ntule etukäteen miettiä, miten itseänne puolustatte.',65,NULL),
 (1584,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',15,'5Minä annan teille viisauden puhua niin, ettei\nyksikään vastustajanne kykene kiistämään eikä\nkumoamaan sanojanne. ',65,NULL),
 (1585,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',16,'6\"Jopa teidän vanhempanne, veljenne, sukulaisenne\nja ystävänne luovuttavat teidät viranomaisille, ja\nmuutamia teistä surmataan.',65,NULL),
 (1586,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',17,'7Kaikki vihaavat teitä\nminun nimeni tähden.',65,NULL),
 (1587,'Anonymous','2008-10-17 16:32:40','Anonymous','2008-10-17 16:32:40',18,'8Mutta edes hiuskarva\npäästänne ei mene hukkaan.',65,NULL),
 (1588,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',19,'9Pysykää lujina, niin\nvoitatte omaksenne elämän.   ',65,NULL),
 (1589,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',25,'5\"Auringossa, kuussa ja tähdissä näkyy merkkejä.\nMeren aallot pauhaavat jylisten, ja maan päällä ovat\nkansat ahdistuksen ja epätoivon vallassa.',65,NULL),
 (1590,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',26,'6Kaikki\nlamaantuvat pelosta odottaessaan sitä, mikä on\nkohtaava ihmiskuntaa, sillä taivaiden voimat\njärkkyvät.',65,NULL),
 (1591,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',27,'7Silloin nähdään Ihmisen Pojan tulevan\npilven päällä suuressa voimassaan ja kirkkaudessaan.',65,NULL),
 (1592,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',28,'8Kun nuo tapahtumat alkavat, nostakaa rohkeasti\npäänne pystyyn, sillä teidän vapautuksenne on\nlähellä.\" ',65,NULL),
 (1593,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',29,'9Hän esitti heille myös vertauksen: \"Katsokaa viikunapuuta, tai mitä puuta tahansa.',65,NULL),
 (1594,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',30,'0Kun näette sen puhkeavan lehteen, te tiedätte\nilman muuta, että kesä on jo lähellä.',65,NULL),
 (1595,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',31,'1Samalla\ntavoin te nähdessänne tämän tapahtuvan tiedätte,\nettä Jumalan valtakunta on lähellä.',65,NULL),
 (1596,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',32,'2Totisesti:\ntämä sukupolvi ei katoa ennen kuin kaikki tämä\ntapahtuu.',65,NULL),
 (1597,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',33,'3Taivas ja maa katoavat, mutta minun\nsanani eivät katoa.   ',65,NULL),
 (1598,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',37,'7Päivät Jeesus opetti temppelissä, mutta illalla\nhän aina lähti sieltä ja vietti yönsä vuorella, jota\nsanotaan Öljymäeksi.',65,NULL),
 (1599,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',38,'8Jo varhain aamulla kansa\ntuli temppeliin hänen luokseen kuulemaan häntä.  ',65,NULL),
 (1600,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',1,'Happamattoman leivän juhla, jota kutsutaan myös\npääsiäiseksi, oli jo lähellä.',66,NULL),
 (1601,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',2,'Ylipapit ja\nlainopettajat miettivät miettimistään, millä keinoin\nsaisivat Jeesuksen raivatuksi tieltä, sillä he\npelkäsivät kansaa. ',66,NULL),
 (1602,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',3,'Silloin meni Saatana Juudakseen, jota kutsuttiin\nIskariotiksi; hän oli yksi kahdestatoista\nopetuslapsesta.',66,NULL),
 (1603,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',4,'Hän lähti ylipappien ja\ntemppelivartioston päällikköjen luo ja keskusteli\nheidän kanssaan siitä, miten toimittaisi Jeesuksen\nheidän käsiinsä.',66,NULL),
 (1604,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',5,'He ilahtuivat ja tarjosivat\nhänelle rahaa.',66,NULL),
 (1605,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',6,'Juudas suostui tarjoukseen ja etsi\nnyt sopivaa tilaisuutta saattaakseen Jeesuksen\nheidän käsiinsä kaikessa hiljaisuudessa.     ',66,NULL),
 (1606,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',19,'9Sitten hän otti leivän, siunasi, mursi ja antoi\nsen opetuslapsilleen sanoen: \"Tämä on minun\nruumiini, joka annetaan teidän puolestanne. Tehkää\ntämä minun muistokseni.\"',66,NULL),
 (1607,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',20,'0Aterian jälkeen hän\nsamalla tavoin otti maljan ja sanoi: \"Tämä malja on\nuusi liitto minun veressäni, joka vuodatetaan teidän\npuolestanne.',66,NULL),
 (1608,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',21,'1Mutta tälle samalle pöydälle\nojentaa yhdessä minun kanssani kätensä myös minun\nkavaltajani.',66,NULL),
 (1609,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',22,'2Ihmisen Poika lähtee täältä juuri\nniin kuin hänen on määrä lähteä, mutta voi sitä,\njosta tulee hänen kavaltajansa!\"',66,NULL),
 (1610,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',23,'3Opetuslapset\nalkoivat kysellä toisiltaan, kuka heistä se\nsaattaisi olla.   ',66,NULL),
 (1611,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',25,'5Silloin Jeesus sanoi heille: \"Kuninkaat hallitsevat herroina kansojaan, ja\nvallanpitäjät vaativat, että heitä kutsutaan\nhyväntekijöiksi.*',66,NULL),
 (1612,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',26,'6Niin ei saa olla teidän\nkeskuudessanne. Joka teidän joukossanne on suurin,\nse olkoon kuin nuorin, ja joka on johtaja, olkoon\nkuin palvelija.',66,NULL),
 (1613,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',27,'7Kumpi on suurempi, se, joka on\naterialla, vai se, joka palvelee häntä? Eikö se,\njoka on aterialla? Mutta minä olen teidän\nkeskellänne niin kuin se, joka palvelee. ',66,NULL),
 (1614,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',28,'8\"Koko ajan, kaikissa koettelemuksissani, te\nolette pysyneet minun kanssani.',66,NULL),
 (1615,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',29,'9Niinpä minä\nannan teille kuninkaallisen vallan, niin kuin Isäni\non minulle antanut.',66,NULL),
 (1616,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',30,'0Te saatte minun\nvaltakunnassani syödä ja juoda minun pöydässäni, ja\nte istutte valtaistuimilla ja hallitsette Israelin\nkahtatoista heimoa.   ',66,NULL),
 (1617,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',33,'3Pietari sanoi hänelle: \"Herra, sinun kanssasi\nminä olen valmis menemään vaikka vankilaan tai\nkuolemaankin.\"',66,NULL),
 (1618,'Anonymous','2008-10-17 16:32:41','Anonymous','2008-10-17 16:32:41',34,'4Mutta Jeesus vastasi: \"Minä sanon\nsinulle, Pietari: ennen kuin kukko tänään laulaa,\nsinä olet kolmesti kieltänyt tuntevasi minut.\"             ',66,NULL),
 (1619,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',1,'He lähtivät sieltä kaikki yhdessä, veivät\nJeesuksen Pilatuksen eteen',67,NULL),
 (1620,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',2,'ja rupesivat\nesittämään syytöksiä häntä vastaan. He sanoivat: \"Me\nolemme todenneet, että tämä mies johtaa kansaamme\nharhaan. Hän kieltää maksamasta veroa keisarille ja\nväittää olevansa Messias, kuningas.\"',67,NULL),
 (1621,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',3,'Pilatus\nkysyi Jeesukselta: \"Oletko sinä juutalaisten\nkuningas?\" \"Sinä sen sanoit\", Jeesus vastasi.',67,NULL),
 (1622,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',4,'Pilatus sanoi ylipapeille ja väkijoukolle: \"En\nvoi havaita tämän miehen syyllistyneen mihinkään\nrikokseen.\"',67,NULL),
 (1623,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',5,'Mutta he pysyivät itsepintaisesti\nsyytöksissään ja sanoivat: \"Hän villitsee kansaa,\nkulkee opettamassa joka puolella Juudeaa, Galileasta\ntänne asti.\"   ',67,NULL),
 (1624,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',8,'Herodes ilahtui kovin nähdessään Jeesuksen, sillä\nhän oli jo pitkään halunnut tavata hänet. Hän oli\nkuullut Jeesuksesta ja toivoi, että tämä tekisi\nhänen nähtensä jonkin ihmeteon.',67,NULL),
 (1625,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',9,'Hän kyseli\nJeesukselta kaikenlaista, mutta Jeesus ei vastannut\nhänelle mitään.',67,NULL),
 (1626,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',10,'0Ylipapit ja lainopettajat, jotka\nolivat paikalla, syyttivät Jeesusta kiivaasti.',67,NULL),
 (1627,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',11,'1Herodes ja hänen sotilaansa alkoivat nyt\nkohdella Jeesusta halveksuvasti. Herodes teki\nJeesuksesta pilkkaa puettamalla hänet komeaan\npukuun, ja sitten hän lähetti Jeesuksen takaisin\nPilatuksen luo.',67,NULL),
 (1628,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',12,'2Tuona päivänä Herodeksesta ja\nPilatuksesta tuli ystävät; siihen saakka he olivat\nolleet vihoissa keskenään.   ',67,NULL),
 (1629,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',18,'8Silloin kaikki huusivat yhteen ääneen: \"Kuolema\nsille miehelle! Päästä meille Barabbas!\"',67,NULL),
 (1630,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',19,'9Barabbas oli kaupungissa puhjenneen mellakan ja\nmurhan tähden vangittu mies.',67,NULL),
 (1631,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',20,'0Pilatus vetosi\nuudestaan heihin, sillä hän halusi vapauttaa\nJeesuksen.',67,NULL),
 (1632,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',21,'1Mutta he huusivat vastaan:\n\"Ristiinnaulitse, ristiinnaulitse!\"',67,NULL),
 (1633,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',22,'2Pilatus\nsanoi heille kolmannen kerran: \"Mitä pahaa hän\nsitten on tehnyt? En ole havainnut mitään, minkä\nvuoksi hänet pitäisi tuomita kuolemaan. Minä päästän\nhänet vapaaksi, kunhan olen antanut kurittaa häntä.\"',67,NULL),
 (1634,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',23,'3Mutta he eivät antaneet periksi vaan huusivat\nyhä kovemmin ja vaativat, että Jeesus oli\nristiinnaulittava. Huutonsa voimalla he saivat tahtonsa läpi.',67,NULL),
 (1635,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',24,'4Pilatus päätti lopulta suostua heidän\nvaatimukseensa.',67,NULL),
 (1636,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',25,'5Hän vapautti sen miehen, jonka\nhe halusivat -- miehen, joka oli vangittu mellakan\nja murhan tähden -- mutta Jeesuksen hän luovutti\nheidän valtaansa.     ',67,NULL),
 (1637,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',35,'5Kansa seisoi katselemassa. Hallitusmiehiäkin oli\nsiellä ivailemassa Jeesusta. He sanoivat: \"Muita hän\nkyllä on auttanut -- auttakoon nyt itseään, jos hän\nkerran on Messias, Jumalan valittu.\"',67,NULL),
 (1638,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',36,'6Myös\nsotilaat pilkkasivat häntä. He tulivat hänen\nluokseen, tarjosivat hänelle hapanviiniä',67,NULL),
 (1639,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',37,'7ja\nsanoivat: \"Jos olet juutalaisten kuningas, niin\npelasta itsesi.\"',67,NULL),
 (1640,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',38,'8Jeesuksen pään yläpuolella oli\nmyös kirjoitus: \"Tämä on juutalaisten kuningas.\" ',67,NULL),
 (1641,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',39,'9Toinen ristillä riippuvista pahantekijöistä\nherjasi hänkin Jeesusta. Hän sanoi: \"Etkö sinä ole\nMessias? Pelasta nyt itsesi ja meidät!\"',67,NULL),
 (1642,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',40,'0Mutta\ntoinen moitti häntä: \"Etkö edes sinä pelkää Jumalaa,\nvaikka kärsit samaa rangaistusta?',67,NULL),
 (1643,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',41,'1Mehän olemme\nansainneet tuomiomme, meitä rangaistaan tekojemme\nmukaan, mutta tämä mies ei ole tehnyt mitään pahaa.\"',67,NULL),
 (1644,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',42,'2Ja hän sanoi: \"Jeesus, muista minua, kun tulet\nvaltakuntaasi.\"',67,NULL),
 (1645,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',43,'3Jeesus vastasi: \"Totisesti: jo\ntänään olet minun kanssani paratiisissa.\"   ',67,NULL),
 (1646,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',47,'7Kun sadanpäällikkö näki, mitä tapahtui, hän\nantoi Jumalalle kunnian ja sanoi: \"Tämä mies oli\ntodella viaton.\"',67,NULL),
 (1647,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',48,'8Ja kaikki ne ihmiset, jotka\nsuurin joukoin olivat kerääntyneet katselemaan tätä\nnäytelmää, palasivat tämän nähtyään kaupunkiin\nrintaansa lyöden.',67,NULL),
 (1648,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',49,'9Jeesuksen tuttavat seisoivat\nkaikki etäämpänä ja seurasivat sieltä tapahtumia.\nSiellä olivat myös naiset, jotka olivat tulleet\nGalileasta hänen mukanaan.   ',67,NULL),
 (1649,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',55,'5Naiset, jotka olivat Jeesuksen kanssa tulleet\nGalileasta, lähtivät Joosefin mukaan. He näkivät,\nkuinka Jeesuksen ruumis pantiin hautaan.',67,NULL),
 (1650,'Anonymous','2008-10-17 16:32:42','Anonymous','2008-10-17 16:32:42',56,'6Palattuaan kaupunkiin he hankkivat tuoksuvia\nöljyjä ja voiteita, mutta sapatin he viettivät\nlevossa lain käskyn mukaan. ',67,NULL),
 (1651,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',1,'Ensimmäisenä päivänä sapatin jälkeen naiset jo\naamuvarhaisella menivät haudalle ja ottivat\nhankkimansa tuoksuöljyt mukaan.',68,NULL),
 (1652,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',2,'He havaitsivat,\nettä kivi oli vieritetty haudan suulta,',68,NULL),
 (1653,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',3,'ja kun he\nmenivät sisälle hautaan, he eivät löytäneet Herran\nJeesuksen ruumista.',68,NULL),
 (1654,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',4,'Kun he olivat ymmällä tästä,\nheidän edessään seisoi yhtäkkiä kaksi miestä\nsädehtivän kirkkaissa vaatteissa.',68,NULL),
 (1655,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',5,'Naiset\npelästyivät ja painoivat katseensa maahan. Mutta\nmiehet sanoivat heille: \"Miksi etsitte elävää\nkuolleiden joukosta?',68,NULL),
 (1656,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',6,'Ei hän ole täällä, hän on\nnoussut kuolleista. Muistakaa, mitä hän sanoi teille\nollessaan vielä Galileassa:',68,NULL),
 (1657,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',7,'\'Näin täytyy käydä:\nIhmisen Poika annetaan syntisten ihmisten käsiin ja\nristiinnaulitaan, mutta kolmantena päivänä hän\nnousee kuolleista.\'\"',68,NULL),
 (1658,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',8,'Silloin he muistivat, mitä\nJeesus oli puhunut. ',68,NULL),
 (1659,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',9,'Haudalta palattuaan naiset veivät tästä sanan\nyhdelletoista opetuslapselle ja kaikille muille.',68,NULL),
 (1660,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',10,'0Nämä naiset olivat Magdalan Maria, Johanna ja\nJaakobin äiti Maria, ja vielä muitakin oli heidän\nkanssaan. He kertoivat kaiken apostoleille,',68,NULL),
 (1661,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',11,'1mutta nämä arvelivat naisten puhuvan omiaan\neivätkä uskoneet heitä.',68,NULL),
 (1662,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',12,'2Pietari lähti kuitenkin\njuoksujalkaa haudalle. Kurkistaessaan sisään hän\nnäki ainoastaan käärinliinat, ja hän lähti pois\nihmetellen mielessään sitä, mikä oli tapahtunut.   ',68,NULL),
 (1663,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',17,'7Jeesus kysyi heiltä: \"Mistä te oikein\nkeskustelette, matkamiehet?\" He pysähtyivät\nmurheellisina,',68,NULL),
 (1664,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',18,'8ja toinen heistä, Kleopas\nnimeltään, vastasi: \"Taidat olla Jerusalemissa ainoa\nmuukalainen, joka ei tiedä, mitä siellä on näinä\npäivinä tapahtunut.\" ',68,NULL),
 (1665,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',19,'9\"Mitä te tarkoitatte?\" Jeesus kysyi. He\nvastasivat: \"Sitä, mitä tapahtui Jeesus\nNasaretilaiselle. Se mies oli tosi profeetta,\nvoimallinen sanoissa ja teoissa, sekä Jumalan että\nkaiken kansan edessä.',68,NULL),
 (1666,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',20,'0Meidän ylipappimme ja\nhallitusmiehemme luovuttivat hänet tuomittavaksi\nkuolemaan ja ristiinnaulitsivat hänet.',68,NULL),
 (1667,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',21,'1Me\nkuitenkin olimme eläneet siinä toivossa, että hän\nolisi se, joka lunastaa Israelin. Eikä siinä kaikki.\nTänään on jo kolmas päivä siitä kun se tapahtui,',68,NULL),
 (1668,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',22,'2ja nyt ovat muutamat naiset meidän joukostamme\nsaattaneet meidät kerta kaikkiaan hämmennyksiin. He\nkävivät varhain aamulla haudalla',68,NULL),
 (1669,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',23,'3mutta eivät\nlöytäneet hänen ruumistaan. Sieltä tultuaan he\nlisäksi kertoivat nähneensä näyn: enkeleitä, jotka\nsanoivat, että Jeesus elää.',68,NULL),
 (1670,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',24,'4Muutamat meistä\nmenivät silloin haudalle ja totesivat, että asia oli\nniin kuin naiset olivat sanoneet. Jeesusta he eivät\nnähneet.\" ',68,NULL),
 (1671,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',25,'5Silloin Jeesus sanoi heille: \"Voi teitä\nymmärtämättömiä! Noinko hitaita te olette uskomaan\nkaikkea sitä, mitä profeetat ovat puhuneet?',68,NULL),
 (1672,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',26,'6Juuri niinhän Messiaan piti kärsiä ja sitten\nmennä kirkkauteensa.\"',68,NULL),
 (1673,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',27,'7Ja hän selitti heille\nMooseksesta ja kaikista profeetoista alkaen, mitä\nhänestä oli kaikissa kirjoituksissa sanottu. ',68,NULL),
 (1674,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',28,'8He olivat jo saapumassa kylään, jonne olivat\nmenossa. Jeesus oli jatkavinaan matkaansa,',68,NULL),
 (1675,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',29,'9mutta\nhe estivät häntä lähtemästä ja sanoivat: \"Jää meidän\nluoksemme. Päivä on jo kääntymässä iltaan.\" Niin hän\nmeni sisään ja jäi heidän luokseen.',68,NULL),
 (1676,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',30,'0Kun hän\nsitten aterioi heidän kanssaan, hän otti leivän,\nkiitti Jumalaa, mursi leivän ja antoi sen heille.',68,NULL),
 (1677,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',31,'1Silloin heidän silmänsä aukenivat ja he tunsivat\nhänet. Mutta samassa hän jo oli poissa heidän\nnäkyvistään.',68,NULL),
 (1678,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',32,'2He sanoivat toisilleen: \"Eikö\nsydämemme hehkunut innosta, kun hän kulkiessamme\npuhui meille ja opetti meitä ymmärtämään\nkirjoitukset?\" ',68,NULL),
 (1679,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',33,'3Heti paikalla he lähtivät matkaan ja palasivat\nJerusalemiin. Siellä olivat koolla yksitoista\nopetuslasta ja muut heidän joukkoonsa kuuluvat.',68,NULL),
 (1680,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',34,'4Nämä sanoivat: \"Herra on todella noussut\nkuolleista! Hän on ilmestynyt Simonille.\"',68,NULL),
 (1681,'Anonymous','2008-10-17 16:32:43','Anonymous','2008-10-17 16:32:43',35,'5Nuo\nkaksi puolestaan kertoivat, mitä matkalla oli\ntapahtunut ja miten he olivat tunteneet Jeesuksen,\nkun hän mursi leivän.       ',68,NULL),
 (1682,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',6,'Tuli mies, Jumalan lähettämä, hänen nimensä oli\nJohannes.',69,NULL),
 (1683,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',7,'Hän tuli todistajaksi, todistamaan\nvalosta, jotta kaikki uskoisivat siihen.',69,NULL),
 (1684,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',8,'Ei hän\nitse ollut tuo valo, mutta valon todistaja hän oli.     ',69,NULL),
 (1685,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',15,'5Johannes todisti hänestä ja huusi: \"Hän on se,\njosta sanoin: Minun jälkeeni tuleva kulkee edelläni,\nsillä hän on ollut ennen minua.\"    ',69,NULL),
 (1686,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',24,'4Niiden joukossa, jotka oli lähetetty Johanneksen\nluo, oli myös fariseuksia*.',69,NULL),
 (1687,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',25,'5He kysyivät häneltä:\n\"Miksi sitten kastat ihmisiä, jos et ole Messias, et\nElia etkä se profeetta?\"',69,NULL),
 (1688,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',26,'6Johannes vastasi: \"Minä\nkastan vedellä, mutta teidän keskellänne on jo toinen,\nvaikka te ette häntä tunne,',69,NULL),
 (1689,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',27,'7hän, joka tulee minun\njälkeeni. Minä olen arvoton edes avaamaan hänen\nkenkiensä nauhoja.\" ',69,NULL),
 (1690,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',28,'8Tämä tapahtui Jordanin tuolla puolen, Betaniassa,\nmissä Johannes oli kastamassa.   ',69,NULL),
 (1691,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',32,'2Johannes todisti: \"Minä olen nähnyt, kuinka Henki\nlaskeutui taivaasta kyyhkysen tavoin ja jäi hänen\npäälleen.',69,NULL),
 (1692,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',33,'3Minäkään en häntä tuntenut. Mutta hän,\njoka lähetti minut kastamaan vedellä, sanoi minulle:\n\'Se, jonka päälle näet Hengen laskeutuvan ja jäävän,\nkastaa Pyhällä Hengellä.\'',69,NULL),
 (1693,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',34,'4Minä olen sen nähnyt ja\ntodistan, että tämä mies on Jumalan Poika.\"   ',69,NULL),
 (1694,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',40,'0Toinen näistä, jotka kuulivat Johanneksen sanat ja\nlähtivät seuraamaan Jeesusta, oli Andreas, Simon\nPietarin veli.',69,NULL),
 (1695,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',41,'1Hän etsi ensiksi käsiinsä veljensä\nSimonin ja sanoi hänelle: \"Me olemme löytäneet\nMessiaan!\" -- se merkitsee: Voidellun.*',69,NULL),
 (1696,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',42,'2Andreas\nvei hänet Jeesuksen luo. Jeesus katsoi häneen ja\nsanoi: \"Sinä olet Simon, Johanneksen poika. Nimesi on\noleva Keefas\" -- se merkitsee: Kallio.* ',69,NULL),
 (1697,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',43,'3Seuraavana päivänä, ollessaan lähdössä Galileaan,\nJeesus tapasi Filippuksen ja sanoi hänelle: \"Seuraa\nminua.\"',69,NULL),
 (1698,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',44,'4Filippus oli kotoisin Betsaidasta, samasta\nkaupungista kuin Andreas ja Pietari.',69,NULL),
 (1699,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',45,'5Filippus\ntapasi Natanaelin ja sanoi hänelle: \"Me olemme\nlöytäneet sen, josta Mooseksen laki ja profeettojen\nkirjat todistavat! Hän on Jeesus, Joosefin poika\nNasaretista.\"',69,NULL),
 (1700,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',46,'6\"Nasaretistako?\" Natanael kysyi.\n\"Voiko Nasaretista tulla mitään hyvää?\" \"Tule, niin\nnäet\", sanoi Filippus. ',69,NULL),
 (1701,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',47,'7Kun Jeesus näki Natanaelin tulevan, hän sanoi:\n\"Siinä on oikea israelilainen, mies vailla vilppiä!\"',69,NULL),
 (1702,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',48,'8\"Mistä sinä minut tunnet?\" kysyi Natanael. Jeesus\nvastasi hänelle: \"Jo ennen kuin Filippus kutsui sinua,\nnäin sinut viikunapuun alla.\"',69,NULL),
 (1703,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',49,'9Natanael sanoi:\n\"Rabbi, sinä olet Jumalan Poika, sinä olet Israelin\nkuningas!\"',69,NULL),
 (1704,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',50,'0Jeesus sanoi hänelle: \"Uskotko jo sen\nperusteella, että sanoin nähneeni sinut viikunapuun\nalla? Paljon suurempaakin saat vielä nähdä.\"',69,NULL),
 (1705,'Anonymous','2008-10-17 16:32:45','Anonymous','2008-10-17 16:32:45',51,'1Ja\nhän jatkoi: \"Totisesti, totisesti: te saatte nähdä\ntaivaan avoinna, ja te näette Jumalan enkelien\nkulkevan ylös ja alas siinä, missä Ihmisen Poika*on.\" ',69,NULL),
 (1706,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',1,'Viikon kolmantena päivänä vietettiin Galilean\nKaanassa häitä. Jeesuksen äiti oli siellä,',70,NULL),
 (1707,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',2,'ja myös\nJeesus ja hänen opetuslapsensa kutsuttiin häihin.',70,NULL),
 (1708,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',3,'Viini loppui kesken, ja äiti sanoi Jeesukselle:\n\"Heillä ei ole viiniä.\"',70,NULL),
 (1709,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',4,'Mutta Jeesus vastasi: \"Anna\nminun olla, nainen. Minun aikani ei ole vielä tullut.\"',70,NULL),
 (1710,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',5,'Hänen äitinsä sanoi palvelijoille: \"Mitä hän teille\nsanookin, tehkää se.\" ',70,NULL),
 (1711,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',6,'Siellä oli kuusi kivistä vesiastiaa juutalaisten\ntapojen mukaisia pesuja varten; ne olivat parin kolmen\nmitan*vetoisia.',70,NULL),
 (1712,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',7,'Jeesus sanoi palvelijoille:\n\"Täyttäkää astiat vedellä\", ja he täyttivät ne reunoja\nmyöten.',70,NULL),
 (1713,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',8,'Sitten hän sanoi: \"Ottakaa nyt siitä ja\nviekää pitojen valvojalle\", ja he veivät.',70,NULL),
 (1714,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',9,'Valvoja\nmaistoi vettä: se oli muuttunut viiniksi. Toisin kuin\npalvelijat, jotka olivat veden astiasta ottaneet, hän\nei tiennyt, mistä viini oli peräisin. Hän kutsui\nsulhasen luokseen',70,NULL),
 (1715,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',10,'0ja sanoi: \"Kaikki tarjoavat\nensiksi hyvän viinin ja sitten, kun vieraat alkavat\njuopua, huonompaa. Mutta sinä olet säästänyt hyvän\nviinin tähän asti.\" ',70,NULL),
 (1716,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',11,'1Tämä oli Jeesuksen tunnusteoista ensimmäinen, ja\nhän teki sen Galilean Kaanassa. Hän ilmaisi sillä\nkirkkautensa, ja hänen opetuslapsensa uskoivat häneen. ',70,NULL),
 (1717,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',12,'2Tämän jälkeen Jeesus sekä hänen äitinsä, veljensä\nja opetuslapsensa menivät Kapernaumiin ja viipyivät\nsiellä muutaman päivän.   ',70,NULL),
 (1718,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',18,'8Juutalaiset sanoivat Jeesukselle: \"Mikä oikeus\nsinulla on tehdä noin? Millä tunnusteolla todistat\nmeille oikeutesi?\"',70,NULL),
 (1719,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',19,'9Jeesus vastasi: \"Hajottakaa\ntämä temppeli, niin minä saan sen nousemaan kolmessa\npäivässä.\"',70,NULL),
 (1720,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',20,'0Silloin juutalaiset sanoivat: \"Tätä\ntemppeliä on rakennettu neljäkymmentäkuusi vuotta.\nSinäkö pystyttäisit sen kolmessa päivässä?\"',70,NULL),
 (1721,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',21,'1Jeesus\ntarkoitti kuitenkin temppelillä omaa ruumistaan.',70,NULL),
 (1722,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',22,'2Kun hän sitten oli noussut kuolleista,\nopetuslapset muistivat nämä hänen sanansa, ja he\nuskoivat kirjoituksiin ja siihen, mitä Jeesus oli\npuhunut. ',70,NULL),
 (1723,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',23,'3Kun Jeesus oli pääsiäisjuhlan aikaan\nJerusalemissa, monet uskoivat häneen nähdessään ne\ntunnusteot, jotka hän teki.',70,NULL),
 (1724,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',24,'4Jeesus sitä vastoin ei\nuskoutunut heille, koska hän tunsi heidät kaikki.',70,NULL),
 (1725,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',25,'5Hän ei tarvinnut kenenkään todistusta ihmisestä,\nsillä hän tiesi itse, mitä ihmisessä on. ',70,NULL),
 (1726,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',1,'Fariseusten joukossa oli Nikodemos-niminen mies,\njuutalaisten neuvoston jäsen.',71,NULL),
 (1727,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',2,'Hän tuli yöllä\nJeesuksen luo ja sanoi: \"Rabbi, me tiedämme, että sinä\nolet Jumalan lähettämä opettaja. Ei kukaan pysty\ntekemään sellaisia tunnustekoja kuin sinä, ellei\nJumala ole hänen kanssaan.\"',71,NULL),
 (1728,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',3,'Jeesus vastasi hänelle:\n\"Totisesti, totisesti: jos ihminen ei synny uudesti,\nylhäältä, hän ei pääse näkemään Jumalan valtakuntaa.\"',71,NULL),
 (1729,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',4,'Nikodemos kysyi: \"Miten joku voisi vanhana syntyä?\nMiten joku voisi mennä takaisin äitinsä kohtuun ja\nsyntyä toisen kerran?\" ',71,NULL),
 (1730,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',5,'Jeesus vastasi: \"Totisesti, totisesti: jos ihminen\nei synny vedestä ja Hengestä, hän ei pääse Jumalan\nvaltakuntaan.',71,NULL),
 (1731,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',6,'Mikä on syntynyt lihasta, on lihaa,\nmikä on syntynyt Hengestä, on henkeä.',71,NULL),
 (1732,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',7,'Älä kummeksu\nsitä, että sanoin sinulle: \'Teidän täytyy syntyä\nuudesti.\'',71,NULL),
 (1733,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',8,'Tuuli*puhaltaa missä tahtoo. Sinä kuulet\nsen huminan, mutta et tiedä, mistä se tulee ja minne\nse menee. Samoin on jokaisen Hengestä syntyneen\nlaita.\" ',71,NULL),
 (1734,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',9,'\"Miten tämä kaikki on mahdollista?\" Nikodemos\nkysyi. ',71,NULL),
 (1735,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',10,'0Jeesus vastasi: \"Etkö sinä, Israelin opettaja, ymmärrä sitä? ',71,NULL),
 (1736,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',11,'1\"Totisesti, totisesti: me puhumme mitä tiedämme ja\ntodistamme siitä mitä olemme nähneet, mutta te ette\nota vastaan meidän todistustamme.',71,NULL),
 (1737,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',12,'2Jos te ette\nusko, kun puhun teille tämän maailman asioista, kuinka\nvoisitte uskoa, kun puhun taivaallisista!',71,NULL),
 (1738,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',13,'3Kukaan\nei ole noussut taivaaseen, paitsi hän, joka on\ntaivaasta tänne tullut: Ihmisen Poika. ',71,NULL),
 (1739,'Anonymous','2008-10-17 16:32:46','Anonymous','2008-10-17 16:32:46',14,'4\"Niin kuin Mooses autiomaassa nosti käärmeen\nkorkealle, niin on myös Ihmisen Poika korotettava,',71,NULL),
 (1740,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',15,'5jotta jokainen, joka uskoo häneen, saisi\niankaikkisen elämän.',71,NULL),
 (1741,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',16,'6Jumala on rakastanut maailmaa\nniin paljon, että antoi ainoan Poikansa, jottei\nyksikään, joka häneen uskoo, joutuisi kadotukseen,\nvaan saisi iankaikkisen elämän. ',71,NULL),
 (1742,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',17,'7\"Ei Jumala lähettänyt Poikaansa maailmaan sitä\ntuomitsemaan, vaan pelastamaan sen.',71,NULL),
 (1743,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',18,'8Sitä, joka\nuskoo häneen, ei tuomita, mutta se, joka ei usko, on\njo tuomittu, koska hän ei uskonut Jumalan ainoaan\nPoikaan.',71,NULL),
 (1744,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',19,'9Ja tuomio on tämä: valo on tullut\nmaailmaan, mutta pahojen tekojensa tähden ihmiset ovat\nvalinneet sen asemesta pimeyden.',71,NULL),
 (1745,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',20,'0Se, joka tekee\npahaa, kaihtaa valoa; hän ei tule valoon, etteivät\nhänen tekonsa paljastuisi.',71,NULL),
 (1746,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',21,'1Mutta se, joka\nnoudattaa totuutta, tulee valoon, jotta kävisi ilmi,\nettä hänen tekonsa ovat lähtöisin Jumalasta.\"   ',71,NULL),
 (1747,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',25,'5Kerran Johanneksen opetuslapset kiistelivät erään\njuutalaisen kanssa puhdistautumisesta.',71,NULL),
 (1748,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',26,'6Opetuslapset menivät Johanneksen luo ja sanoivat:\n\"Rabbi, nyt on ruvennut kastamaan myös se mies, joka\noli kanssasi Jordanin toisella puolen ja josta annoit\nhyvän todistuksen. Kaikki menevät hänen luokseen.\" ',71,NULL),
 (1749,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',27,'7Johannes vastasi: \"Kukaan ei voi ottaa mitään,\nellei sitä anneta hänelle taivaasta.',71,NULL),
 (1750,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',28,'8Te voitte\nitse todistaa, että minä sanoin: \'En minä ole Messias.\nMinut on lähetetty kulkemaan hänen edellään.\'',71,NULL),
 (1751,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',29,'9Sulhanen on se, jolla on morsian. Mutta sulhasen\nystävä seisoo hänen vieressään ja kuuntelee, mitä hän\npuhuu, ja iloitsee suuresti sulhasta kuunnellessaan.\nNiin iloitsen minäkin, ja iloni on nyt täydellinen.',71,NULL),
 (1752,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',30,'0Hänen on tultava suuremmaksi, minun pienemmäksi.\" ',71,NULL),
 (1753,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',31,'1Hän, joka tulee ylhäältä, on kaikkien yläpuolella.\nJoka on maasta, on maan tomua ja puhuu tämän maailman\nasioista. Hän, joka tulee taivaasta,',71,NULL),
 (1754,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',32,'2todistaa\nsiitä, mitä on nähnyt ja kuullut, mutta kukaan ei ota\nvastaan hänen todistustaan.',71,NULL),
 (1755,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',33,'3Joka ottaa hänen\ntodistuksensa vastaan, tunnustaa, että Jumala puhuu\ntotta,',71,NULL),
 (1756,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',34,'4sillä hän, jonka Jumala on lähettänyt,\npuhuu julki Jumalan sanat. Ei Jumala anna Henkeään\nmäärämitalla.',71,NULL),
 (1757,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',35,'5Isä rakastaa Poikaa ja on antanut\nkaiken hänen valtaansa.',71,NULL),
 (1758,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',36,'6Sillä, joka uskoo Poikaan,\non ikuinen elämä, mutta joka ei tottele Poikaa, se ei\npääse näkemään elämää, vaan Jumalan viha pysyy hänen\nyllään. ',71,NULL),
 (1759,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',1,'Jeesus sai tietää fariseusten kuulleen, että hän\nkokosi ympärilleen enemmän opetuslapsia ja kastoi\nuseampia kuin Johannes --',72,NULL),
 (1760,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',2,'tosin hän ei itse\nkastanut, vaan hänen opetuslapsensa.',72,NULL),
 (1761,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',3,'Silloin hän\nlähti Juudeasta ja siirtyi takaisin Galileaan.',72,NULL),
 (1762,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',4,'Hänen oli kuljettava Samarian kautta. ',72,NULL),
 (1763,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',5,'Matkallaan Jeesus tuli Sykar-nimiseen Samarian\nkaupunkiin. Sen lähellä oli maa-alue, jonka Jaakob oli\nantanut pojalleen Joosefille,',72,NULL),
 (1764,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',6,'ja siellä oli\nJaakobin kaivo. Matkasta uupuneena Jeesus istahti\nkaivolle. Oli keskipäivä, noin kuudes tunti. ',72,NULL),
 (1765,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',7,'Eräs samarialainen nainen tuli noutamaan vettä, ja\nJeesus sanoi hänelle: \"Anna minun juoda astiastasi.\"',72,NULL),
 (1766,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',8,'Opetuslapset olivat menneet kaupunkiin ostamaan\nruokaa.',72,NULL),
 (1767,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',9,'Samarialaisnainen sanoi: \"Sinähän olet\njuutalainen, kuinka sinä pyydät juotavaa\nsamarialaiselta naiselta?\" Juutalaiset eivät näet ole\nmissään tekemisissä samarialaisten kanssa. ',72,NULL),
 (1768,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',10,'0Jeesus sanoi naiselle: \"Jos tietäisit, minkä\nlahjan Jumala on antanut, ja ymmärtäisit, kuka sinulta\npyytää juotavaa, pyytäisit itse häneltä, ja hän\nantaisi sinulle elävää vettä.\"',72,NULL),
 (1769,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',11,'1Nainen sanoi:\n\"Herra, eihän sinulla edes ole astiaa, ja kaivo on\nsyvä. Mistä sinä lähdevettä ottaisit?',72,NULL),
 (1770,'Anonymous','2008-10-17 16:32:47','Anonymous','2008-10-17 16:32:47',12,'2Et kai sinä\nole suurempi kuin isämme Jaakob, jolta olemme saaneet\ntämän kaivon? Hän joi itse tämän kaivon vettä, ja sitä\njoivat hänen poikansa ja hänen karjansakin.\"',72,NULL),
 (1771,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',13,'3Jeesus vastasi hänelle: \"Joka juo tätä vettä, sen\ntulee uudelleen jano,',72,NULL),
 (1772,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',14,'4mutta joka juo minun\nantamaani vettä, ei enää koskaan ole janoissaan. Siitä\nvedestä, jota minä annan, tulee hänessä lähde, joka\nkumpuaa ikuisen elämän vettä.\"',72,NULL),
 (1773,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',15,'5Nainen sanoi:\n\"Herra, anna minulle sitä vettä. Silloin minun ei enää\ntule jano eikä minun tarvitse käydä täällä veden\nhaussa.\" ',72,NULL),
 (1774,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',16,'6Jeesus sanoi hänelle: \"Mene hakemaan miehesikin\ntänne.\"',72,NULL),
 (1775,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',17,'7\"Ei minulla ole miestä\", nainen vastasi.\nJeesus sanoi: \"Totta puhuit: ei sinulla ole miestä.',72,NULL),
 (1776,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',18,'8Viisi miestä sinulla on ollut, ja se, jonka kanssa\nnyt elät, ei ole sinun miehesi. Siinä puhuit totta.\"',72,NULL),
 (1777,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',19,'9Nainen sanoi: \"Herra, minä huomaan, että sinä olet\nprofeetta.',72,NULL),
 (1778,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',20,'0Meidän isämme ovat kumartaneet ja\nrukoilleet Jumalaa tällä vuorella, kun taas te\nväitätte, että oikea paikka rukoilla on\nJerusalemissa.\"',72,NULL),
 (1779,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',21,'1Jeesus vastasi: \"Usko minua,\nnainen: tulee aika, jolloin ette rukoile Isää tällä\nvuorella ettekä Jerusalemissa.',72,NULL),
 (1780,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',22,'2Te kumarratte\nsellaista, mitä ette tunne, mutta me kumarramme häntä,\njonka tunnemme, sillä pelastaja nousee juutalaisten\nkeskuudesta.',72,NULL),
 (1781,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',23,'3Tulee aika  -- ja se on jo nyt --\njolloin kaikki oikeat rukoilijat rukoilevat Isää\nhengessä ja totuudessa. Sellaisia rukoilijoita Isä\ntahtoo.',72,NULL),
 (1782,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',24,'4Jumala on henki, ja siksi niiden, jotka\nhäntä rukoilevat, tulee rukoilla hengessä ja\ntotuudessa.\" ',72,NULL),
 (1783,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',25,'5Nainen sanoi: \"Minä tiedän kyllä, että Messias\ntulee.\" -- Messias tarkoittaa Kristusta. -- \"Kun hän\ntulee, hän ilmoittaa meille kaiken.\"',72,NULL),
 (1784,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',26,'6Jeesus sanoi:\n\"Minä se olen, minä, joka tässä puhun kanssasi.\" ',72,NULL),
 (1785,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',27,'7Juuri silloin tulivat opetuslapset paikalle. Heitä\nhämmästytti, että Jeesus puhui naisen kanssa, mutta\nkukaan ei kuitenkaan kysynyt, mitä hän tahtoi ja miksi\nhän puhui naiselle.',72,NULL),
 (1786,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',28,'8Nainen jätti vesiruukkunsa\nsiihen, meni kaupunkiin ja sanoi ihmisille:',72,NULL),
 (1787,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',29,'9\"Tulkaa katsomaan, tuolla on mies, joka kertoi\nminulle kaiken mitä olen tehnyt! Olisiko hän Messias?\"',72,NULL),
 (1788,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',30,'0Ihmiset lähtivät kaupungista ja tulivat Jeesuksen\nluo. ',72,NULL),
 (1789,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',31,'1Sillä välin opetuslapset sanoivat Jeesukselle:\n\"Rabbi, tule syömään.\"',72,NULL),
 (1790,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',32,'2Mutta hän sanoi heille:\n\"Minulla on ruokaa, josta te ette tiedä.\"',72,NULL),
 (1791,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',33,'3Opetuslapset kummastelivat keskenään: \"Onko joku\ntuonut hänelle syötävää?\"',72,NULL),
 (1792,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',34,'4Mutta Jeesus jatkoi:\n\"Minun ruokani on se, että täytän lähettäjäni tahdon\nja vien hänen työnsä päätökseen.',72,NULL),
 (1793,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',35,'5Te sanotte:\n\'Neljä kuuta kylvöstä korjuuseen.\' Minä sanon:\nKatsokaa tuonne! Vainio on jo vaalennut, vilja on\nkypsä korjattavaksi.',72,NULL),
 (1794,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',36,'6Sadonkorjaaja saa palkkansa\njo nyt, hän kokoaa satoa iankaikkiseen elämään, ja\nkylväjä saa iloita yhdessä korjaajan kanssa.',72,NULL),
 (1795,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',37,'7Tässä\npitää paikkansa sanonta: \'Toinen kylvää, toinen\nkorjaa.\'',72,NULL),
 (1796,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',38,'8Minä olen lähettänyt teidät korjaamaan\nsatoa, josta ette ole nähneet vaivaa. Toiset ovat\ntehneet työn, mutta te pääsette korjaamaan heidän\nvaivannäkönsä hedelmät.\" ',72,NULL),
 (1797,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',39,'9Monet tuon Samarian kaupungin asukkaista uskoivat\nJeesukseen kuultuaan naisen todistavan: \"Hän kertoi\nminulle kaiken mitä olen tehnyt.\"',72,NULL),
 (1798,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',40,'0Kun\nsamarialaiset tulivat Jeesuksen luo, he pyysivät häntä\njäämään kaupunkiin, ja hän jäikin sinne kahdeksi\npäiväksi.',72,NULL),
 (1799,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',41,'1Yhä useammat uskoivat Jeesukseen\nkuultuaan hänen itsensä puhuvan,',72,NULL),
 (1800,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',42,'2ja he sanoivat\nnaiselle: \"Nyt emme enää usko vain sinun puheesi\nperusteella. Me olemme nyt itse kuulleet häntä ja\ntiedämme, että hän todella on maailman pelastaja.\"   ',72,NULL),
 (1801,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',46,'6Jeesus tuli taas Galilean Kaanaan, missä hän oli\nmuuttanut veden viiniksi. Kapernaumissa oli kuninkaan\nvirkamies, jonka poika oli sairaana.',72,NULL),
 (1802,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',47,'7Kuultuaan\nJeesuksen tulleen Juudeasta Galileaan hän lähti\nJeesuksen luo ja pyysi, että tämä tulisi parantamaan\npojan, joka oli kuolemaisillaan.',72,NULL),
 (1803,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',48,'8Jeesus sanoi\nhänelle: \"Te ette usko, ellette näe tunnustekoja ja\nihmeitä.\"',72,NULL),
 (1804,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',49,'9Mutta virkamies pyysi: \"Herra, tule,\nennen kuin poikani kuolee.\"',72,NULL),
 (1805,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',50,'0Silloin Jeesus sanoi:\n\"Mene kotiisi. Poikasi elää.\" Mies uskoi, mitä Jeesus hänelle sanoi, ja lähti.',72,NULL),
 (1806,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',51,'1Jo kesken matkan tulivat hänen palvelijansa häntä\nvastaan ja kertoivat pojan parantuneen.',72,NULL),
 (1807,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',52,'2Mies kysyi\nheiltä, mihin aikaan poika oli alkanut toipua, ja he\nsanoivat: \"Eilen seitsemännellä tunnilla*kuume\nhellitti.\"',72,NULL),
 (1808,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',53,'3Silloin isä ymmärsi, että se oli\ntapahtunut juuri silloin, kun Jeesus sanoi hänelle:\n\"Poikasi elää\", ja hän ja koko hänen talonsa väki\nuskoivat Jeesukseen. ',72,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (1809,'Anonymous','2008-10-17 16:32:48','Anonymous','2008-10-17 16:32:48',54,'4Tämä oli Jeesuksen toinen tunnusteko, ja hän teki\nsen tultuaan Juudeasta Galileaan. ',72,NULL),
 (1810,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',1,'Tämän jälkeen oli eräs juutalaisten juhla, ja\nJeesus lähti Jerusalemiin.',73,NULL),
 (1811,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',2,'Jerusalemissa on\nLammasportin lähellä allas, jonka hepreankielinen nimi\non Betesda. Sitä reunustaa viisi pylväshallia,',73,NULL),
 (1812,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',3,'ja\nniissä makasi suuri joukko sairaita: sokeita, rampoja\nja halvaantuneita.*[Nämä odottivat, että vesi alkaisi\nliikkua.',73,NULL),
 (1813,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',4,'Aika ajoin näet Herran enkeli laskeutui\nlammikkoon ja pani veden kuohumaan, ja se, joka\nensimmäisenä astui kuohuvaan veteen, tuli terveeksi,\nsairastipa hän mitä tautia tahansa.] ',73,NULL),
 (1814,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',5,'Siellä oli mies, joka oli sairastanut\nkolmekymmentäkahdeksan vuotta.',73,NULL),
 (1815,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',6,'Jeesus näki hänet\nsiellä makaamassa vuodematolla ja tiesi, että hän oli\njo pitkään ollut sairas. Jeesus kysyi: \"Tahdotko tulla\nterveeksi?\"',73,NULL),
 (1816,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',7,'Sairas vastasi: \"Herra, minulla ei ole\nketään, joka auttaisi minut altaaseen, kun vesi\nkuohahtaa. Aina kun yritän sinne, joku toinen ehtii\nennen minua.\"',73,NULL),
 (1817,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',8,'Jeesus sanoi hänelle: \"Nouse, ota\nvuoteesi ja kävele.\"',73,NULL),
 (1818,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',9,'Mies tuli heti terveeksi, otti\nvuoteensa ja käveli. Mutta se päivä oli sapatti.',73,NULL),
 (1819,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',10,'0Niinpä juutalaiset\nsanoivat parannetulle: \"Nyt on sapatti, ei sinun ole\nlupa kantaa vuodettasi.\"',73,NULL),
 (1820,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',11,'1Mies vastasi heille: \"Se,\njoka teki minut terveeksi, sanoi minulle: \'Ota\nvuoteesi ja kävele.\'\"',73,NULL),
 (1821,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',12,'2Silloin juutalaiset\nkysyivät: \"Kuka se mies oli, joka käski sinun ottaa\nvuoteesi ja kävellä?\"',73,NULL),
 (1822,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',13,'3Parannettu ei kuitenkaan\ntiennyt, kuka hän oli, sillä Jeesus oli jo hävinnyt\nväkijoukkoon.',73,NULL),
 (1823,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',14,'4Myöhemmin Jeesus tapasi miehen\ntemppelissä ja sanoi hänelle: \"Sinä olet nyt terve.\nÄlä enää tee syntiä, ettei sinulle kävisi entistä\npahemmin.\"',73,NULL),
 (1824,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',15,'5Mies lähti sieltä ja kertoi\njuutalaisille, että Jeesus oli hänet parantanut. ',73,NULL),
 (1825,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',16,'6Juutalaiset alkoivat vainota Jeesusta, koska hän\nteki tällaista sapattina.',73,NULL),
 (1826,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',17,'7Mutta Jeesus sanoi\nheille: \"Minun Isäni tekee työtään taukoamatta, ja\nniin teen myös minä.\"',73,NULL),
 (1827,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',18,'8Tämä vain lisäsi\njuutalaisten halua ottaa Jeesus hengiltä. Hänhän ei\nainoastaan rikkonut sapattikäskyä, vaan vieläpä sanoi\nJumalaa omaksi isäkseen ja teki siten itsestään\nJumalan vertaisen.   \"Totisesti, totisesti: ei Poika voi tehdä mitään omin\nneuvoin, hän tekee vain sitä, mitä näkee Isän tekevän.\nMitä Isä tekee, sitä tekee myös Poika.',73,NULL),
 (1828,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',20,'0Isä\nrakastaa Poikaa ja näyttää hänelle kaiken, mitä itse\ntekee. Hän näyttää Pojalle vielä suurempiakin tekoja,\nsellaisia, että hämmästytte.',73,NULL),
 (1829,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',21,'1Niin kuin Isä\nherättää kuolleet ja antaa heille elämän, niin antaa\nmyös Poika elämän kenelle tahtoo.',73,NULL),
 (1830,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',22,'2Isä itse ei\ntuomitse ketään, vaan hän on antanut kaiken\ntuomiovallan Pojalle,',73,NULL),
 (1831,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',23,'3jotta kaikki\nkunnioittaisivat Poikaa niin kuin he kunnioittavat\nIsää. Se, joka ei kunnioita Poikaa, ei kunnioita\nmyöskään Isää, joka on hänet lähettänyt. ',73,NULL),
 (1832,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',24,'4\"Totisesti, totisesti: se, joka kuulee minun\nsanani ja uskoo minun lähettäjääni, on saanut ikuisen\nelämän. Hän ei joudu tuomittavaksi, vaan hän on jo\nsiirtynyt kuolemasta elämään.',73,NULL),
 (1833,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',25,'5Totisesti,\ntotisesti: tulee aika -- ja se on jo nyt -- jolloin\nkuolleet kuulevat Jumalan Pojan äänen. Ne, jotka sen\nkuulevat, saavat elää,',73,NULL),
 (1834,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',26,'6sillä Isä, elämän lähde, on\ntehnyt myös Pojasta elämän lähteen.',73,NULL),
 (1835,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',27,'7Isä on myös\nantanut hänelle tuomiovallan, koska hän on Ihmisen\nPoika.',73,NULL),
 (1836,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',28,'8Älkää ihmetelkö tätä! Tulee aika, jolloin\nkaikki, jotka lepäävät haudoissaan, kuulevat hänen\näänensä.',73,NULL),
 (1837,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',29,'9He nousevat haudoistaan -- hyvää tehneet\nelämän ylösnousemukseen, pahaa tehneet tuomion\nylösnousemukseen.   ',73,NULL),
 (1838,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',31,'1\"Jos itse todistan puolestani, ei todistukseni ole\npätevä.',73,NULL),
 (1839,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',32,'2Mutta minusta todistaakin toinen, ja minä\ntiedän, että todistus, jonka hän minusta antaa, on\npätevä.',73,NULL),
 (1840,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',33,'3Te lähetitte edustajanne Johanneksen luo,\nja hän todisti totuuden puolesta.',73,NULL),
 (1841,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',34,'4En minä tarvitse\nkenenkään ihmisen todistusta, mutta sanon teille\ntämän, jotta pelastuisitte.',73,NULL),
 (1842,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',35,'5Johannes oli\nkirkkaalla liekillä palava lamppu, mutta vain hetken\naikaa te halusitte nauttia hänen valostaan. ',73,NULL),
 (1843,'Anonymous','2008-10-17 16:32:49','Anonymous','2008-10-17 16:32:49',36,'6\"Minulla on todistus, joka on Johanneksen\ntodistusta painavampi: teot, jotka Isä on antanut\ntehtäväkseni. Nämä teot, jotka minä teen, todistavat,\nettä Isä on minut lähettänyt.',73,NULL),
 (1844,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',37,'7Myös Isä itse, minun\nlähettäjäni, on antanut minusta todistuksen. Te ette\nole koskaan kuulleet hänen ääntään ettekä nähneet\nhänen kasvojaan,',73,NULL),
 (1845,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',38,'8eikä hänen sanansa pysy teissä,\nsillä te ette usko sitä, jonka hän on lähettänyt.',73,NULL),
 (1846,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',39,'9Te kyllä tutkitte kirjoituksia, koska luulette\nniistä löytävänne ikuisen elämän -- ja nehän juuri\ntodistavat minusta.',73,NULL),
 (1847,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',40,'0Mutta te ette tahdo tulla\nminun luokseni, että saisitte elämän. ',73,NULL),
 (1848,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',41,'1\"En minä ole ihmisten kiitoksen tarpeessa.',73,NULL),
 (1849,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',42,'2Minä tunnen teidät: teissä ei ole rakkautta\nJumalaan.',73,NULL),
 (1850,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',43,'3Minä olen tullut Isäni nimissä, ettekä\nte ota minua vastaan. Mutta jos joku toinen tulee\nomissa nimissään, silloin te kyllä olette\nvastaanottavaisia.',73,NULL),
 (1851,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',44,'4Kuinka te voisittekaan uskoa!\nTe kärkytte kunniaa toisiltanne ettekä etsi sitä\nkunniaa, joka tulee yhdeltä ja ainoalta Jumalalta. ',73,NULL),
 (1852,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',45,'5\"Älkää luulko, että minä teitä Isäni edessä\nsyytän. Teidän syyttäjänne on Mooses, hän, johon\nolette panneet toivonne.',73,NULL),
 (1853,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',46,'6Jos te uskoisitte\nMoosesta, uskoisitte myös minua -- juuri minusta hän\non kirjoittanut.',73,NULL),
 (1854,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',47,'7Mutta kun te ette usko hänen\nkirjoituksiaan, kuinka voisitte uskoa minun\npuhettani!\" ',73,NULL),
 (1855,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',1,'Tämän jälkeen Jeesus lähti Galileanjärven eli\nTiberiaanjärven toiselle puolen.',74,NULL),
 (1856,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',2,'Häntä seurasi\nsuuri väkijoukko, sillä ihmiset olivat nähneet\ntunnusteot, joita hän teki parantamalla sairaita.',74,NULL),
 (1857,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',3,'Jeesus nousi vuorenrinteelle ja asettui\nopetuslapsineen sinne istumaan.',74,NULL),
 (1858,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',4,'Juutalaisten\npääsiäisjuhla oli lähellä. ',74,NULL),
 (1859,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',5,'Jeesus kohotti katseensa ja näki, että suuri\nihmisjoukko oli tulossa. Hän kysyi Filippukselta:\n\"Mistä voisimme ostaa leipää, että he saisivat\nsyödäkseen?\"',74,NULL),
 (1860,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',6,'Tämän hän sanoi koetellakseen\nFilippusta, sillä hän tiesi kyllä, mitä tekisi.',74,NULL),
 (1861,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',7,'Filippus vastasi: \"Kahdensadan denaarin leivistä ei\nriittäisi heille edes pientä palaa kullekin.\"',74,NULL),
 (1862,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',8,'Silloin eräs opetuslapsi, Simon Pietarin veli\nAndreas, sanoi Jeesukselle:',74,NULL),
 (1863,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',9,'\"Täällä on poika, jolla\non viisi ohraleipää ja kaksi kalaa. Mutta miten ne\nriittäisivät noin suurelle joukolle?\" ',74,NULL),
 (1864,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',10,'0Jeesus sanoi: \"Käskekää kaikkien asettua\nistumaan.\" Rinteellä kasvoi rehevä nurmi, ja ihmiset\nistuutuivat maahan. Paikalla oli noin viisituhatta\nmiestä.',74,NULL),
 (1865,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',11,'1Jeesus otti leivät, kiitti Jumalaa ja\njakoi leivät syömään asettuneille. Samoin hän jakoi\nkalat, ja kaikki saivat niin paljon kuin halusivat.',74,NULL),
 (1866,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',12,'2Kun kaikki olivat kylläisiä, Jeesus sanoi\nopetuslapsilleen: \"Kerätkää tähteeksi jääneet palaset,\nettei mitään menisi hukkaan.\"',74,NULL),
 (1867,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',13,'3He tekivät niin, ja\nviidestä ohraleivästä kertyi vielä kaksitoista täyttä\nkorillista palasia, jotka olivat jääneet syömättä. ',74,NULL),
 (1868,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',14,'4Kun ihmiset näkivät, minkä tunnusteon Jeesus teki,\nhe sanoivat: \"Tämä on todella se profeetta, jonka oli\nmäärä tulla maailmaan.\"',74,NULL),
 (1869,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',15,'5Mutta Jeesus tiesi, että\nihmiset aikoivat väkisin tehdä hänestä kuninkaan, ja\nsiksi hän vetäytyi taas vuorelle. Hän meni sinne\nyksin.     ',74,NULL),
 (1870,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',25,'5He löysivät Jeesuksen järven toiselta puolelta ja\nkysyivät häneltä: \"Rabbi, milloin sinä olet tullut\ntänne?\"',74,NULL),
 (1871,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',26,'6Jeesus vastasi: \"Totisesti, totisesti:\nette te minua sen tähden etsi, että olette nähneet\ntunnustekoja, vaan siksi, että saitte leipää ja söitte\nitsenne kylläisiksi.',74,NULL),
 (1872,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',27,'7Älkää tavoitelko katoavaa\nruokaa, vaan katoamatonta, sitä, joka antaa ikuisen\nelämän. Sitä teille antaa Ihmisen Poika, sillä Isä,\nJumala itse, on merkinnyt hänet sinetillään.\"',74,NULL),
 (1873,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',28,'8He\nkysyivät: \"Mitä meidän tulee tehdä, että tekomme\nolisivat Jumalan tekoja?\"',74,NULL),
 (1874,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',29,'9Jeesus vastasi: \"Uskokaa\nhäneen, jonka Jumala on lähettänyt. Se on Jumalan\nteko.\" ',74,NULL),
 (1875,'Anonymous','2008-10-17 16:32:50','Anonymous','2008-10-17 16:32:50',30,'0He sanoivat Jeesukselle: \"Minkä tunnusteon teet,\nettä me sen nähtyämme uskomme sinuun? Mitä sinä teet?',74,NULL),
 (1876,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',31,'1Meidän isämme  söivät autiomaassa mannaa, niin\nkuin kirjoituksissa sanotaan: \'Hän antoi taivaasta\nleipää heille syötäväksi.\'\"',74,NULL),
 (1877,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',32,'2Tähän Jeesus vastasi:\n\"Totisesti, totisesti: ei Mooses teille antanut\ntaivaasta leipää, vaan todellista taivaan leipää\nteille antaa minun Isäni.',74,NULL),
 (1878,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',33,'3Jumalan leipä on se,\njoka tulee taivaasta ja antaa maailmalle elämän.\" ',74,NULL),
 (1879,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',34,'4He sanoivat: \"Anna meille aina sitä leipää.\"',74,NULL),
 (1880,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',35,'5Jeesus sanoi: \"Minä olen elämän leipä. Joka tulee\nminun luokseni, ei koskaan ole nälissään, ja joka\nuskoo minuun, ei enää koskaan ole janoissaan.',74,NULL),
 (1881,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',36,'6Mutta te, niin kuin olen teille sanonut, olette\nnähneet minut ettekä kuitenkaan usko.',74,NULL),
 (1882,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',37,'7Kaikki ne,\njotka Isä minulle antaa, tulevat minun luokseni, ja\nsitä, joka luokseni tulee, minä en aja pois.',74,NULL),
 (1883,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',38,'8Enhän\nminä ole tullut taivaasta tekemään oman tahtoni\nmukaan, vaan täyttämään lähettäjäni tahdon.',74,NULL),
 (1884,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',39,'9Ja\nlähettäjäni tahto on, etten minä anna yhdenkään\nniistä, jotka hän on uskonut haltuuni, joutua hukkaan,\nvaan viimeisenä päivänä herätän heidät kaikki.',74,NULL),
 (1885,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',40,'0Minun Isäni tahtoo, että jokaisella, joka näkee\nPojan ja uskoo häneen, on ikuinen elämä. Viimeisenä\npäivänä minä herätän hänet.\" ',74,NULL),
 (1886,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',41,'1Juutalaisissa herätti ärtymystä se, että Jeesus\nsanoi: \"Minä olen se leipä, joka on tullut alas\ntaivaasta.\"',74,NULL),
 (1887,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',42,'2He sanoivat: \"Eikö tämä ole Jeesus,\nJoosefin poika? Me tiedämme hänen isänsä ja äitinsä.\nKuinka hän voi sanoa tulleensa alas taivaasta?\"',74,NULL),
 (1888,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',43,'3Jeesus sanoi tähän: \"Älkää nurisko!',74,NULL),
 (1889,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',44,'4Ei kukaan\nvoi tulla minun luokseni, ellei Isä, joka minut on\nlähettänyt, vedä häntä. Sen, joka tulee, minä herätän\nviimeisenä päivänä.',74,NULL),
 (1890,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',45,'5Profeettojen kirjoituksissa\nsanotaan: \'Heistä kaikista tulee Jumalan\nopetuslapsia.\' Jokainen, joka on kuunnellut Isää ja\nottanut vastaan hänen opetuksensa, tulee minun\nluokseni.',74,NULL),
 (1891,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',46,'6Isää tosin ei kukaan ole nähnyt;\nainoastaan hän, joka on tullut Jumalan luota, on\nnähnyt Isän.',74,NULL),
 (1892,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',47,'7Totisesti, totisesti: sillä, joka\nuskoo, on ikuinen elämä.',74,NULL),
 (1893,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',48,'8Minä olen elämän leipä.',74,NULL),
 (1894,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',49,'9Teidän isänne söivät autiomaassa mannaa, ja silti\nhe ovat kuolleet.',74,NULL),
 (1895,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',50,'0Mutta tämä leipä tulee\ntaivaasta, ja se, joka tätä syö, ei kuole.',74,NULL),
 (1896,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',51,'1Minä\nolen tämä elävä leipä, joka on tullut taivaasta, ja\nse, joka syö tätä leipää, elää ikuisesti. Leipä, jonka\nminä annan, on minun ruumiini. Minä annan sen, että\nmaailma saisi elää.\" ',74,NULL),
 (1897,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',52,'2Tästä sukeutui kiivas väittely juutalaisten\nkesken. He kysyivät toisiltaan: \"Kuinka tuo mies voisi\nantaa ruumiinsa meidän syötäväksemme?\"',74,NULL),
 (1898,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',53,'3Jeesus\nsanoi heille: \"Totisesti, totisesti: ellette te syö\nIhmisen Pojan lihaa ja juo hänen vertaan, teillä ei\nole elämää.',74,NULL),
 (1899,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',54,'4Mutta sillä, joka syö minun lihani ja\njuo minun vereni, on ikuinen elämä, ja viimeisenä\npäivänä minä herätän hänet.',74,NULL),
 (1900,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',55,'5Minun lihani on\ntodellinen ruoka, minun vereni on todellinen juoma.',74,NULL),
 (1901,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',56,'6Joka syö minun lihani ja juo minun vereni, pysyy\nminussa, ja minä pysyn hänessä.',74,NULL),
 (1902,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',57,'7Isä, joka elää, on\nminut lähettänyt, ja niin kuin minä saan elämäni\nIsältä, niin saa minulta elämän se, joka minua syö.',74,NULL),
 (1903,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',58,'8Tämä on se leipä, joka on tullut alas taivaasta.\nSe on toisenlaista kuin se ruoka, jota teidän isänne\nsöivät: he ovat kuolleet, mutta se, joka syö tätä\nleipää, elää ikuisesti.\" ',74,NULL),
 (1904,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',59,'9Näin Jeesus puhui opettaessaan Kapernaumin\nsynagogassa.   ',74,NULL),
 (1905,'Anonymous','2008-10-17 16:32:51','Anonymous','2008-10-17 16:32:51',66,'6Monet Jeesuksen opetuslapset vetäytyivät tämän\njälkeen joukosta eivätkä enää kulkeneet hänen\nmukanaan.',74,NULL),
 (1906,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',67,'7Jeesus kysyi niiltä kahdeltatoista:\n\"Aiotteko tekin lähteä?\"',74,NULL),
 (1907,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',68,'8Simon Pietari vastasi\nhänelle: \"Herra, kenen luo me menisimme? Sinulla on\nikuisen elämän sanat.',74,NULL),
 (1908,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',69,'9Me uskomme ja tiedämme, että\nsinä olet Jumalan Pyhä.\"',74,NULL),
 (1909,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',70,'0Jeesus sanoi heille:\n\"Itsehän minä teidät valitsin, kaikki kaksitoista.\nSilti yksi teistä on Saatana.\"',74,NULL),
 (1910,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',71,'1Hän tarkoitti\nJuudasta, Simon Iskariotin poikaa. Tämä oli kavaltava\nJeesuksen, vaikka oli yksi noista kahdestatoista. ',74,NULL),
 (1911,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',1,'Tämän jälkeen Jeesus kiersi eri puolilla Galileaa.\nJuudeassa hän ei halunnut liikkua, koska juutalaiset\netsivät tilaisuutta tappaa hänet. ',75,NULL),
 (1912,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',2,'Juutalaisten lehtimajanjuhla oli lähellä.',75,NULL),
 (1913,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',3,'Jeesuksen veljet sanoivat hänelle: \"Lähde Juudeaan,\nniin opetuslapsesikin näkevät, millaisia tekoja sinä\nteet.',75,NULL),
 (1914,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',4,'Eihän kukaan salaile tekojaan, jos haluaa\njulkisuutta. Jos kerran pystyt tällaiseen, näytä\nmaailmalle, kuka sinä olet!\"',75,NULL),
 (1915,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',5,'Veljetkään eivät näet\nuskoneet häneen.',75,NULL),
 (1916,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',6,'Jeesus sanoi heille: \"Nyt ei ole\nvielä minun aikani -- teille taas sopii mikä aika\nhyvänsä.',75,NULL),
 (1917,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',7,'Maailma ei voi vihata teitä, mutta minua\nse vihaa, koska minä todistan, että sen teot ovat\npahat.',75,NULL),
 (1918,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',8,'Menkää te vain juhlille. Minä en tule tähän\njuhlaan, sillä minun aikani ei ole vielä tullut.\"',75,NULL),
 (1919,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',9,'Näin hän sanoi ja jäi Galileaan. ',75,NULL),
 (1920,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',10,'0Mutta kun veljet olivat lähteneet juhlille, myös\nJeesus lähti sinne, ei ihmisten tieten vaan salaa.',75,NULL),
 (1921,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',11,'1Juhlien alettua juutalaiset etsivät häntä ja\nkyselivät: \"Missä se mies on?\"',75,NULL),
 (1922,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',12,'2Kansan keskuudessa\nhän oli yleisen kiistelyn aiheena. Toiset kiittivät:\n\"Hän on hyvä mies\", toiset sanoivat: \"Kaikkea muuta,\nhän on kansanvillitsijä.\"',75,NULL),
 (1923,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',13,'3Julkisesti kukaan ei\nkuitenkaan uskaltanut puhua hänestä, sillä kaikki\npelkäsivät juutalaisia. ',75,NULL),
 (1924,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',14,'4Juhlan jo ehdittyä puoliväliin Jeesus meni ylös\ntemppeliin ja alkoi opettaa.',75,NULL),
 (1925,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',15,'5Juutalaiset olivat\nihmeissään ja sanoivat: \"Kuinka tuo oppimaton mies voi\ntuntea kirjoitukset?\"',75,NULL),
 (1926,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',16,'6Jeesus sanoi heille: \"Se,\nmitä minä opetan, ei ole minun oppiani, vaan hänen,\njoka on minut lähettänyt.',75,NULL),
 (1927,'Anonymous','2008-10-17 16:32:52','Anonymous','2008-10-17 16:32:52',17,'7Joka tahtoo noudattaa\nhänen tahtoaan, pääsee kyllä selville siitä, onko\nopetukseni lähtöisin Jumalasta vai puhunko omiani.',75,NULL),
 (1928,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',18,'8Se, joka esittää omia ajatuksiaan, tahtoo kunniaa\nitselleen. Mutta se, joka tahtoo kunniaa\nlähettäjälleen, puhuu totta, hänessä ei ole vääryyttä.',75,NULL),
 (1929,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',19,'9Mooses antoi teille lain, vai kuinka? Silti\nyksikään teistä ei noudata sitä. Miksi te haluatte\ntappaa minut?\" ',75,NULL),
 (1930,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',20,'0Ihmiset sanoivat: \"Sinussa on paha henki. Kuka\nsinut yrittäisi tappaa?\"',75,NULL),
 (1931,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',21,'1Jeesus vastasi: \"Minä\ntein yhden ainoan teon, ja sitä te kaikki\nihmettelette.',75,NULL),
 (1932,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',22,'2Mooses sääti teille\nympärileikkauksen -- se ei tosin ole peräisin\nMoosekselta, vaan kantaisiltä -- ja te toimitatte sen\nmyös sapattina.',75,NULL),
 (1933,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',23,'3Jos kerran ihminen ympärileikataan\nsapattinakin, ettei Mooseksen lakia rikottaisi, niin\nmiksi te olette vihoissanne siitä, että minä sapattina\nparansin koko ihmisen?',75,NULL),
 (1934,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',24,'4Älkää tuomitko sen mukaan,\nmiltä asia päältä katsoen näyttää, vaan tuomitkaa\noikein.\"   ',75,NULL),
 (1935,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',28,'8Opettaessaan temppelissä Jeesus huusi kovalla\näänellä: \"Te siis tiedätte, kuka minä olen ja mistä\ntulen. En minä ole tulostani itse päättänyt -- minut\nlähetti hän, joka on totuus itse. Te ette häntä tunne,',75,NULL),
 (1936,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',29,'9mutta minä tunnen hänet, koska tulen hänen luotaan\nja koska hän on minut lähettänyt.\"',75,NULL),
 (1937,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',30,'0Hallitusmiesten\nteki mieli pidättää hänet, mutta kukaan ei kuitenkaan\nkäynyt häneen käsiksi, sillä hänen hetkensä ei ollut\nvielä tullut.',75,NULL),
 (1938,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',31,'1Monet kansanihmiset sen sijaan\nuskoivat häneen. He sanoivat: \"Tokkopa Messias\ntullessaan tekee useampia tunnustekoja kuin tämä on jo\ntehnyt.\" ',75,NULL),
 (1939,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',32,'2Kun fariseukset kuulivat, että kansan parissa\npuhuttiin Jeesuksesta tähän tapaan, he ja ylipapit\nlähettivät miehiään pidättämään hänet.',75,NULL),
 (1940,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',33,'3Jeesus\nsanoi: \"Vähän aikaa minä vielä olen teidän luonanne.\nSitten menen hänen luokseen, joka minut on lähettänyt.',75,NULL),
 (1941,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',34,'4Silloin te etsitte minua mutta ette löydä, ettekä\nte pääse sinne, missä minä olen.\" ',75,NULL),
 (1942,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',35,'5Juutalaiset kyselivät toisiltaan: \"Minne hän aikoo\nmennä? Mistä me emme häntä löytäisi? Onko hän lähdössä\nniiden luo, jotka asuvat kreikkalaisten keskuudessa?\nAikooko hän opettaa kreikkalaisiakin?',75,NULL),
 (1943,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',36,'6Mitä hän\nmahtoi tarkoittaa sanoessaan: \'Te etsitte minua mutta\nette löydä\', ja: \'Te ette pääse sinne, missä minä\nolen\'?\" ',75,NULL),
 (1944,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',37,'7Juhlan suurena päätöspäivänä Jeesus nousi puhumaan\nja huusi kovalla äänellä: \"Jos jonkun on jano, tulkoon\nminun luokseni ja juokoon!',75,NULL),
 (1945,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',38,'8Joka uskoo minuun,\n\'hänen sisimmästään kumpuavat elävän veden virrat\',\nniin kuin kirjoituksissa sanotaan.\"',75,NULL),
 (1946,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',39,'9Tällä Jeesus\ntarkoitti Henkeä, jonka häneen uskovat tulisivat\nsaamaan. Vielä ei Henki ollut tullut, koska Jeesusta\nei vielä ollut kirkastettu. ',75,NULL),
 (1947,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',40,'0Jeesuksen sanat kuultuaan jotkut väkijoukosta\nsanoivat: \"Tämän täytyy olla se profeetta.\"',75,NULL),
 (1948,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',41,'1Toiset\nsanoivat: \"Hän on Messias.\" Mutta toiset epäilivät:\n\"Ei kai Messias Galileasta tule!',75,NULL),
 (1949,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',42,'2Kirjoituksissahan\nsanotaan, että Messias on Daavidin jälkeläinen ja\ntulee Betlehemistä, Daavidin kotikaupungista.\"',75,NULL),
 (1950,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',43,'3Näin ihmiset alkoivat kiistellä hänestä.',75,NULL),
 (1951,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',44,'4Muutamat halusivat ottaa hänet kiinni, mutta\nkukaan ei kuitenkaan käynyt häneen käsiksi. ',75,NULL),
 (1952,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',45,'5Jeesusta pidättämään lähetetyt miehet palasivat\nylipappien ja fariseusten luo, ja nämä kysyivät:\n\"Miksi ette tuoneet häntä?\"',75,NULL),
 (1953,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',46,'6Miehet vastasivat:\n\"Yksikään ihminen ei ikinä ole puhunut sillä tavoin\nkuin hän.\"',75,NULL),
 (1954,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',47,'7Silloin fariseukset sanoivat: \"Oletteko\ntekin antaneet eksyttää itsenne?',75,NULL),
 (1955,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',48,'8Onko kukaan\nhallitusmies uskonut häneen? Tai yksikään fariseus?',75,NULL),
 (1956,'Anonymous','2008-10-17 16:32:53','Anonymous','2008-10-17 16:32:53',49,'9Tuo rahvas ei tiedä laista mitään -- kirottuja\nkaikki!\"',75,NULL),
 (1957,'Anonymous','2008-10-17 16:32:54','Anonymous','2008-10-17 16:32:54',50,'0Silloin Nikodemos, joka itse oli fariseus\nja oli aiemmin käynyt tapaamassa Jeesusta, sanoi:',75,NULL),
 (1958,'Anonymous','2008-10-17 16:32:54','Anonymous','2008-10-17 16:32:54',51,'1\"Eihän meidän lakimme mukaan ketään voi tuomita,\nennen kuin on kuultu häntä ja otettu selville, mitä\nhän on tehnyt.\"',75,NULL),
 (1959,'Anonymous','2008-10-17 16:32:54','Anonymous','2008-10-17 16:32:54',52,'2Mutta toiset sanoivat hänelle:\n\"Taidat olla itsekin Galileasta. Tutki kirjoituksia,\nniin opit, ettei Galileasta tule profeettaa.\" ',75,NULL),
 (1960,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',1,'Edellisessä kirjassani, hyvä Teofilos, kerroin\nkaikesta siitä, mitä Jeesus teki ja mitä hän opetti,\nalusta alkaen',76,NULL),
 (1961,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',2,'aina siihen päivään saakka, jona hän\nPyhän Hengen voimalla antoi valitsemilleen\napostoleille käskynsä, ennen kuin hänet otettiin\ntaivaaseen.',76,NULL),
 (1962,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',3,'Heille hän myös monin kiistattomin\ntodistein osoitti kuolemansa jälkeen olevansa elossa.\nHän näyttäytyi heille neljänkymmenen päivän aikana\nuseasti ja puhui Jumalan valtakunnasta.',76,NULL),
 (1963,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',4,'Ollessaan\nheidän kanssaan aterialla Jeesus sanoi: \"Älkää lähtekö\nJerusalemista, vaan jääkää odottamaan sitä, minkä Isä\non teille luvannut ja mistä olette minulta kuulleet.',76,NULL),
 (1964,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',5,'Johannes kastoi vedellä, mutta teidät kastetaan\nPyhällä Hengellä. Siihen ei ole enää montakaan\npäivää.\"   ',76,NULL),
 (1965,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',9,'Kun hän oli sanonut tämän, he näkivät, kuinka hänet\notettiin ylös, ja pilvi vei hänet heidän näkyvistään.',76,NULL),
 (1966,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',10,'0Ja kun he Jeesuksen etääntyessä vielä tähysivät\ntaivaalle, heidän vieressään seisoi yhtäkkiä kaksi\nvalkopukuista miestä.',76,NULL),
 (1967,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',11,'1Nämä sanoivat: \"Galilean\nmiehet, mitä te siinä seisotte katselemassa taivaalle?\nTämä Jeesus, joka otettiin teidän luotanne taivaaseen,\ntulee kerran takaisin, samalla tavoin kuin näitte\nhänen taivaaseen menevän.\" ',76,NULL),
 (1968,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',12,'2Silloin he lähtivät tuolta vuorelta, jota\nkutsutaan Öljymäeksi ja joka on lähellä Jerusalemia,\nsapatinmatkan*päässä. He palasivat Jerusalemiin',76,NULL),
 (1969,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',13,'3ja menivät siellä siihen taloon, jonka yläkerrasta\noli tullut heidän kokoontumispaikkansa: Pietari,\nJohannes ja Jaakob, Andreas, Filippus, Tuomas,\nBartolomeus, Matteus ja Jaakob Alfeuksen poika, Simon\nKiivailija ja Juudas Jaakobin poika.',76,NULL),
 (1970,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',14,'4He pitivät\nkaikki yhtä ja rukoilivat lakkaamatta yhdessä\njoukkoonsa kuuluvien naisten sekä Jeesuksen äidin\nMarian ja Jeesuksen veljien kanssa.    \"Veljet! Sen kirjoitusten sanan oli käytävä toteen,\njonka Pyhä Henki Daavidin suulla oli ennalta\nJuudaksesta lausunut -- tuosta miehestä, joka ryhtyi\noppaaksi Jeesuksen vangitsijoille.',76,NULL),
 (1971,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',17,'7Hän kuului\nmeidän joukkoomme ja oli saanut osalleen\npalvelutehtävän niin kuin mekin.',76,NULL),
 (1972,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',18,'8Petoksensa\npalkalla hän hankki itselleen kappaleen maata, mutta\nsitten hän syöksyi päistikkaa alas, niin että hänen\nvatsansa halkesi ja sisälmykset pursuivat ulos.\"',76,NULL),
 (1973,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',19,'9-- Tämä tuli yleisesti tiedoksi Jerusalemissa, ja niin\nse paikka sai heidän kielellään nimen Hakeldama,\nVeripelto. ',76,NULL),
 (1974,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',20,'0\"Jo Psalmien kirjassa sanotaan: \'Jääköön hänen\nasuinsijansa autioksi, älköön siinä kukaan asuko\', ja:\n\'Ottakoon toinen hänen kaitsijantehtävänsä.\'',76,NULL),
 (1975,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',21,'1Siispä on nyt valittava yksi niistä miehistä,\njotka ovat kulkeneet kanssamme koko sen ajan, jolloin\nHerra Jeesus eli ja vaikutti meidän keskuudessamme,',76,NULL),
 (1976,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',22,'2siitä alkaen, kun Johannes hänet kastoi, aina\nsiihen päivään, jona hänet otettiin luotamme\ntaivaaseen. Yksi heistä olkoon hänen ylösnousemuksensa\ntodistajana yhdessä meidän kanssamme.\" ',76,NULL),
 (1977,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',23,'3Ehdolle pantiin kaksi miestä: Joosef Barsabbas,\ntoiselta nimeltään Justus, sekä Mattias.',76,NULL),
 (1978,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',24,'4Sitten\nkaikki rukoilivat: \"Herra, sinä joka tunnet kaikkien\nsydämet, ilmoita, kumman näistä kahdesta olet valinnut',76,NULL),
 (1979,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',25,'5astumaan tähän palvelutehtävään ja apostolin\nvirkaan, josta Juudas luopui mennäkseen sinne, minne\nkuului.\"',76,NULL),
 (1980,'Anonymous','2008-10-17 16:32:56','Anonymous','2008-10-17 16:32:56',26,'6Sen jälkeen he heittivät miehistä arpaa,\nja arpa lankesi Mattiakselle. Näin hänet valittiin ja\nliitettiin kahdentenatoista apostolien joukkoon. ',76,NULL),
 (1981,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',1,'Kun sitten koitti helluntaipäivä*, he olivat kaikki\nyhdessä koolla.',77,NULL),
 (1982,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',2,'Yhtäkkiä kuului taivaalta kohahdus,\nkuin olisi käynyt raju tuulenpuuska, ja se täytti koko\nsen talon, jossa he olivat.',77,NULL),
 (1983,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',3,'He näkivät\ntulenlieskoja, kuin kieliä, jotka jakautuivat ja\nlaskeutuivat itse kunkin päälle.',77,NULL),
 (1984,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',4,'He tulivat täyteen\nPyhää Henkeä ja alkoivat puhua eri kielillä sitä mitä\nHenki antoi heille puhuttavaksi. ',77,NULL),
 (1985,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',5,'Jerusalemissa asui hurskaita juutalaisia, joita oli\ntullut sinne kaikkien kansojen keskuudesta, mitä\ntaivaan alla on.',77,NULL),
 (1986,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',6,'Kun tämä ääni kuului, paikalle\nkerääntyi paljon väkeä, ja hämmästys valtasi kaikki,\nsillä jokainen kuuli puhuttavan omaa kieltään.',77,NULL),
 (1987,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',7,'He\nkysyivät ihmeissään: \"Eivätkö nuo, jotka puhuvat, ole\nkaikki galilealaisia?',77,NULL),
 (1988,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',8,'Kuinka me sitten kuulemme\nkukin oman synnyinmaamme kieltä?',77,NULL),
 (1989,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',9,'Meitä on täällä\npartilaisia, meedialaisia ja elamilaisia, meitä on\nMesopotamiasta, Juudeasta ja Kappadokiasta,\nPontoksesta ja Aasian maakunnasta,',77,NULL),
 (1990,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',10,'0Frygiasta,\nPamfyliasta, Egyptistä ja Libyasta Kyrenen seudulta,\nmeitä on tullut Roomasta,',77,NULL),
 (1991,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',11,'1toiset meistä ovat\nsyntyperäisiä juutalaisia, toiset uskoomme\nkääntyneitä, meitä on kreetalaisia ja arabialaisia --\nja me kaikki kuulemme heidän julistavan omalla\nkielellämme Jumalan suuria tekoja.\"',77,NULL),
 (1992,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',12,'2He eivät\ntienneet, mitä ajatella. Ihmeissään he kyselivät\ntoinen toiseltaan: \"Mitä tämä oikein on?\"',77,NULL),
 (1993,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',13,'3Mutta\njotkut pilkkasivat: \"He ovat juovuksissa, makeaa\nviiniä täynnä.\"   \"Kuulkaa minua, juutalaiset, te kaikki, jotka asutte\nJerusalemissa! Tietäkää tämä:',77,NULL),
 (1994,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',15,'5Eivät nämä miehet\nole juovuksissa, niin kuin te luulette -- nythän on\nvasta aamu, päivän kolmas tunti*.',77,NULL),
 (1995,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',16,'6Tämä on sitä,\nmikä profeetta Joelin suulla on ilmoitettu:   ',77,NULL),
 (1996,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',22,'2\"Israelilaiset, kuulkaa mitä teille sanon! Jeesus\nNasaretilainen oli Jumalan teitä varten valitsema\nmies. Sen Jumala osoitti niillä voimateoilla,\nmerkeillä ja ihmeillä, joita Jeesus hänen\nlähettämänään teki teidän keskuudessanne, niin kuin\ntiedätte.',77,NULL),
 (1997,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',23,'3Tämän Jeesuksen te surmasitte. Te\npanitte lakia tuntemattomat pakanat naulitsemaan hänet\nristiin, kun hänet oli annettu teidän käsiinne niin\nkuin Jumala oli suunnitellut ja ennalta nähnyt.',77,NULL),
 (1998,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',24,'4Mutta Jumala herätti hänet, päästi hänet kuoleman\nkidasta. Eihän edes ollut mahdollista, että kuolema\nolisi voinut pitää häntä vallassaan,',77,NULL),
 (1999,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',25,'5sillä Daavid\nsanoo hänestä:   ',77,NULL),
 (2000,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',29,'9\"Veljeni, kantaisästämme Daavidista voi varmasti\nsanoa, että hän kuoli ja hänet haudattiin. Onhan hänen\nhautansa tänäkin päivänä meidän nähtävissämme.',77,NULL),
 (2001,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',30,'0Mutta Daavid oli profeetta, ja Jumala oli valalla\nvannoen luvannut asettaa Daavidin valtaistuimelle\nhänen oman jälkeläisensä. Daavid tiesi tämän',77,NULL),
 (2002,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',31,'1ja\nnäki ennalta Kristuksen*ylösnousemuksen. Sitä hän\ntarkoitti sanoessaan, ettei Herra hylkää häntä\ntuonelaan eikä hänen ruumiinsa maadu.',77,NULL),
 (2003,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',32,'2Tämän\nJeesuksen on Jumala herättänyt kuolleista; me kaikki\nolemme sen todistajia.',77,NULL),
 (2004,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',33,'3Jumala on korottanut hänet\noikealle puolelleen, ja hän on ottanut vastaan Isän\nlupaaman Pyhän Hengen lahjan ja vuodattanut sen, niin\nkuin te voitte nähdä ja kuulla.',77,NULL),
 (2005,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',34,'4Ei Daavid itse\nnoussut taivaisiin. Hänhän sanoo:  ',77,NULL),
 (2006,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',36,'6Tästä siis saa koko Israelin kansa olla varma:\nJumala on tehnyt Jeesuksen Herraksi ja Messiaaksi*--\ntämän Jeesuksen, jonka te ristiinnaulitsitte.\" ',77,NULL),
 (2007,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',37,'7Kuullessaan tämän kaikki tunsivat piston\nsydämessään, ja he sanoivat Pietarille ja muille\napostoleille: \"Veljet, mitä meidän pitää tehdä?\"',77,NULL),
 (2008,'Anonymous','2008-10-17 16:32:57','Anonymous','2008-10-17 16:32:57',38,'8Pietari vastasi: \"Kääntykää ja ottakaa itse kukin\nkaste Jeesuksen Kristuksen nimeen, jotta syntinne\nannettaisiin anteeksi. Silloin te saatte lahjaksi\nPyhän Hengen.',77,NULL),
 (2009,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',39,'9Teitä tämä lupaus tarkoittaa, teitä\nja teidän lapsianne, ja myös kaikkia niitä, jotka ovat\netäällä -- keitä ikinä Herra, meidän Jumalamme,\nkutsuu.\" ',77,NULL),
 (2010,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',40,'0Pietari jatkoi vielä puhettaan. Hän vetosi heihin\nja kehotti: \"Antakaa pelastaa itsenne, ettette\nhukkuisi tämän kieroutuneen sukupolven mukana.\"',77,NULL),
 (2011,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',41,'1Ne, jotka ottivat hänen sanomansa vastaan,\nkastettiin, ja uskovien joukkoon tuli sinä päivänä\nlisää noin kolmetuhatta henkeä.   ',77,NULL),
 (2012,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',1,'Pietari ja Johannes menivät temppeliin. Oli\nrukoushetken aika, yhdeksäs tunti*.',78,NULL),
 (2013,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',2,'Silloin sinne\nkannettiin miestä, joka oli ollut rampa syntymästään\nsaakka. Hänet pantiin joka päivä temppelin niin\nsanotulle Kauniilleportille, jotta hän voisi kerjätä\ntemppeliin meneviltä.',78,NULL),
 (2014,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',3,'Kun mies näki Pietarin ja\nJohanneksen menossa temppeliin, hän pyysi heiltä\nalmua.',78,NULL),
 (2015,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',4,'Pietari loi katseensa häneen, samoin\nJohannes, ja Pietari sanoi: \"Katso meihin.\"',78,NULL),
 (2016,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',5,'Mies\nkatsoi tarkkaavasti odottaen saavansa heiltä jotakin.',78,NULL),
 (2017,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',6,'Mutta Pietari sanoi: \"Hopeaa ja kultaa minulla ei\nole, mutta mitä minulla on, sitä minä sinulle annan.\nJeesuksen Kristuksen, Nasaretilaisen, nimessä: nouse\nja kävele.\"',78,NULL),
 (2018,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',7,'Hän tarttui miestä oikeasta kädestä ja\nauttoi hänet ylös, ja siinä samassa mies sai voimaa\njalkoihinsa ja nilkkoihinsa.',78,NULL),
 (2019,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',8,'Hän hypähti pystyyn,\nseisoi jalkojensa varassa ja käveli, ja hän tuli\nheidän kanssaan temppeliin, hyppeli kulkiessaan ja\nylisti Jumalaa.',78,NULL),
 (2020,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',9,'Kaikki näkivät, kuinka hän käveli\nja ylisti Jumalaa.',78,NULL),
 (2021,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',10,'0He tunsivat hänet samaksi\nmieheksi, joka oli istunut Kauniillaportilla\nkerjäämässä, ja olivat hämmästyksestä suunniltaan sen\njohdosta, mitä hänelle oli tapahtunut.   \"Israelilaiset, miksi te tätä ihmettelette? Miksi te\ntuijotatte meitä, aivan kuin me omalla voimallamme tai\nhurskaudellamme olisimme saaneet tämän miehen\nkävelemään?',78,NULL),
 (2022,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',13,'3Ei -- Abrahamin, Iisakin ja Jaakobin\nJumala, meidän isiemme Jumala, on korottanut\nkirkkauteen palvelijansa Jeesuksen, hänet, jonka te\nluovutitte tuomittavaksi ja kielsitte Pilatuksen\nedessä, vaikka Pilatus oli päättänyt vapauttaa hänet.',78,NULL),
 (2023,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',14,'4Hänet te kielsitte, pyhän ja vanhurskaan, ja\npyysitte vapaaksi murhamiehen.',78,NULL),
 (2024,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',15,'5Te surmasitte\nelämän ruhtinaan! Mutta Jumala herätti hänet\nkuolleista; me olemme sen todistajia.',78,NULL),
 (2025,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',16,'6Jeesuksen\nnimi ja usko siihen antoi voimaa tälle miehelle, jonka\nte näette ja tunnette. Usko, jonka Jeesus antaa, on\ntehnyt tästä miehestä terveen, niin kuin te kaikki\nnäette. ',78,NULL),
 (2026,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',17,'7\"Minä kyllä tiedän, veljet, että te niin kuin\nhallitusmiehennekin teitte tietämättömyyttänne sen,\nminkä teitte.',78,NULL),
 (2027,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',18,'8Mutta sillä tavoin Jumala pani\ntäytäntöön sen, minkä hän ennalta oli ilmoittanut\nkaikkien profeettojen suulla: hänen Voideltunsa tuli\nkärsiä ja kuolla.',78,NULL),
 (2028,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',19,'9Katukaa siis syntejänne, jotta\nne pyyhittäisiin pois,',78,NULL),
 (2029,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',20,'0kääntykää, jotta Herra\nantaisi tulla virvoituksen ajan ja lähettäisi\nJeesuksen, teille ennalta valitsemansa Voidellun. ',78,NULL),
 (2030,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',21,'1\"Taivas oli oleva hänen asuinsijansa siihen aikaan\nsaakka, jolloin kaikki pannaan kohdalleen, niin kuin\nJumala ikiajoista asti on luvannut pyhien\nprofeettojensa suulla.',78,NULL),
 (2031,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',22,'2Mooses on sanonut: \'Herra,\nteidän Jumalanne, on veljienne joukosta nostava esiin\nprofeetan, minun kaltaiseni. Kuulkaa häntä, tehkää\nkaikessa niin kuin hän teille sanoo.',78,NULL),
 (2032,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',23,'3Jokainen,\njoka ei tätä profeettaa kuule, poistetaan kansansa\nparista ja tuhotaan.\'',78,NULL),
 (2033,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',24,'4Samoin ovat muut profeetat,\nniin Samuel kuin kaikki myöhemmätkin, kansalle\npuhuessaan myös ennustaneet näistä päivistä.',78,NULL),
 (2034,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',25,'5Te\nolette profeettojen lapsia, te olette perineet sen\nliiton, jonka Jumala teki isienne kanssa sanoessaan\nAbrahamille: \'Sinun jälkeläisesi kautta tulee siunaus\nkaikille sukukunnille maan päällä.\'',78,NULL),
 (2035,'Anonymous','2008-10-17 16:32:58','Anonymous','2008-10-17 16:32:58',26,'6Teitä varten\nennen muita Jumala on nostanut kuolleista\npalvelijansa. Hänet Jumala on lähettänyt siunaamaan\nteitä kaikkia, kun käännytte pois pahuudestanne.\" ',78,NULL),
 (2036,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',1,'Kun Pietari ja Johannes vielä puhuivat kansalle,\ntulivat paikalle papit, temppelivartioston päällikkö\nja saddukeukset*.',79,NULL),
 (2037,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',2,'He olivat suutuksissaan siitä,\nettä apostolit opettivat kansaa ja Jeesuksen\nylösnousemukseen vedoten julistivat ylösnousemusta\nkuolleista.',79,NULL),
 (2038,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',3,'He pidättivät Pietarin ja Johanneksen\nja panivat nämä yöksi vankilaan; oli näet jo ilta.',79,NULL),
 (2039,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',4,'Mutta monet niistä, jotka olivat kuulleet heidän\npuheensa, uskoivat. Miehiä oli nyt jo noin\nviisituhatta. ',79,NULL),
 (2040,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',5,'Seuraavana päivänä kokoontuivat Jerusalemissa\njuutalaisten hallitusmiehet, vanhimmat ja\nlainopettajat*.',79,NULL),
 (2041,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',6,'Ylipappi Hannas oli paikalla,\nsamoin Kaifas, Johannes, Aleksandros ja kaikki muut,\njotka olivat ylipapillista sukua.',79,NULL),
 (2042,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',7,'He käskivät tuoda\napostolit eteensä ja kysyivät: \"Millä voimalla ja\nkenen nimessä te tämän teitte?\" ',79,NULL),
 (2043,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',8,'Silloin Pietari, Pyhää Henkeä täynnä, sanoi heille:\n\"Kansan hallitusmiehet ja vanhimmat!',79,NULL),
 (2044,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',9,'Jos meidät nyt\npannaan vastaamaan sairaalle tekemästämme hyvästä\nteosta ja ilmoittamaan, kenen nimessä hänet on\nparannettu,',79,NULL),
 (2045,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',10,'0niin tietäkää tämä, te kaikki ja koko\nIsraelin kansa: se tapahtui Jeesuksen Kristuksen,\nNasaretilaisen, nimessä. Hänet te ristiinnaulitsitte,\nmutta Jumala herätti hänet kuolleista. Hänen\nvoimastaan tämä mies seisoo terveenä teidän edessänne.',79,NULL),
 (2046,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',11,'1Hän on se kivi, joka ei teille rakentajille\nkelvannut mutta josta on tullut kulmakivi.',79,NULL),
 (2047,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',12,'2Ei\nkukaan muu voi pelastaa kuin hän. Mitään muuta nimeä,\njoka meidät pelastaisi, ei ole ihmisille annettu koko\ntaivaankannen alla.\" ',79,NULL),
 (2048,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',13,'3Kun neuvoston jäsenet näkivät Pietarin ja\nJohanneksen rohkeuden ja totesivat heidät\noppimattomiksi kansanmiehiksi, he olivat ihmeissään.\nHe tunsivat nämä Jeesuksen seuralaisiksi,',79,NULL),
 (2049,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',14,'4ja kun\nhe vielä näkivät parannetun miehen seisovan apostolien\nvieressä, he eivät voineet väittää vastaan.',79,NULL),
 (2050,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',15,'5He\nkäskivät apostolien poistua istuntosalista ja\nneuvottelivat keskenään:',79,NULL),
 (2051,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',16,'6\"Mitä me teemme näille\nmiehille? Heidän toimestaan on selvästikin tapahtunut\nihme. Jerusalemilaiset sen jo tietävät, emmekä me voi\nsitä kiistää.',79,NULL),
 (2052,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',17,'7Mutta ettei tapaus tulisi kansan\nparissa vielä laajemmalti tunnetuksi, meidän on paras\njyrkästi kieltää heitä enää puhumasta sen miehen\nnimessä kenellekään.\" ',79,NULL),
 (2053,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',18,'8He kutsuivat apostolit sisään ja kielsivät heitä\nenää puhumasta tai opettamasta yhtään mitään Jeesuksen\nnimessä.',79,NULL),
 (2054,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',19,'9Mutta Pietari ja Johannes vastasivat\nheille: \"Onko Jumalan edessä oikein totella ennemmin\nteitä kuin Jumalaa? Ratkaiskaa itse.',79,NULL),
 (2055,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',20,'0Me emme voi\nolla puhumatta siitä, mitä olemme nähneet ja\nkuulleet.\"',79,NULL),
 (2056,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',21,'1Silloin neuvoston jäsenet uhkailivat\nheitä entistä ankarammin, mutta päästivät heidät\nsitten vapaaksi kansan tähden. He katsoivat\nmahdottomaksi rangaista miehiä, sillä kaikki ylistivät\nJumalaa sen johdosta, mitä oli tapahtunut.',79,NULL),
 (2057,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',22,'2Olihan\nmies, joka tällä tunnusteolla oli parannettu, jo yli\nneljänkymmenen vuoden ikäinen.   He sanoivat: \"Valtias, sinä joka olet luonut taivaan ja maan ja\nmeren ja kaiken, mitä niissä on!',79,NULL),
 (2058,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',25,'5Sinä olet antanut\nPyhän Henkesi puhua palvelijasi Daavidin, meidän\nisämme, suulla:   ',79,NULL),
 (2059,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',27,'7\"Juuri näin on käynyt. Tässä kaupungissa Herodes\nja Pontius Pilatus liittoutuivat pakanoiden ja\nIsraelin kansan kanssa pyhää palvelijaasi Jeesusta\nvastaan, jonka sinä olit voidellut.',79,NULL),
 (2060,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',28,'8He toteuttivat\nsen, minkä sinä voimallasi ja päätökselläsi olit\nennalta määrännyt tapahtuvaksi.',79,NULL),
 (2061,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',29,'9Katso nyt, Herra,\nkuinka he meitä uhkailevat! Anna palvelijoillesi\nvoimaa pelotta julistaa sanaasi.',79,NULL),
 (2062,'Anonymous','2008-10-17 16:32:59','Anonymous','2008-10-17 16:32:59',30,'0Ojenna kätesi,\nniin että sairaat paranevat, että tapahtuu\ntunnustekoja ja ihmeitä pyhän palvelijasi Jeesuksen\nnimessä.\" ',79,NULL),
 (2063,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',31,'1Kun he olivat päättäneet rukouksensa, vavahti\npaikka, jossa he olivat koolla, ja he kaikki\ntäyttyivät Pyhästä Hengestä ja julistivat rohkeasti\nJumalan sanaa.   ',79,NULL),
 (2064,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',1,'Muuan Ananias-niminen mies myi yhdessä vaimonsa\nSafiran kanssa maatilan,',80,NULL),
 (2065,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',2,'mutta pani vaimonsa tieten\nosan kauppahinnasta syrjään. Loput hän luovutti\napostolien haltuun.',80,NULL),
 (2066,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',3,'Silloin Pietari sanoi:\n\"Ananias, miksi olet antanut sydämesi Saatanan\nvaltaan? Miksi yritit pettää Pyhää Henkeä ja jättää\nmaatilan hinnasta osan itsellesi?',80,NULL),
 (2067,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',4,'Tilahan oli\nsinun, kun et vielä ollut myynyt sitä, ja sinun olivat\nmyös rahat, kun sen myit. Kuinka saatoit ryhtyä\ntällaiseen tekoon? Et sinä ole valehdellut ihmisille,\nvaan Jumalalle.\"',80,NULL),
 (2068,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',5,'Nämä sanat kuullessaan Ananias\nlyyhistyi kuolleena maahan, ja kaikki, jotka olivat\ntätä kuulemassa, joutuivat kauhun valtaan.',80,NULL),
 (2069,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',6,'Nuoret\nmiehet kietoivat Ananiaan ruumiin vaatteeseen,\nkantoivat hänet ulos ja hautasivat hänet. ',80,NULL),
 (2070,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',7,'Kolmisen tuntia myöhemmin tuli Ananiaan vaimo\nsisään, tapahtuneesta mitään tietämättä.',80,NULL),
 (2071,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',8,'Pietari\nkysyi häneltä: \"Sano minulle, tähänkö hintaan te\nmaatilan myitte?\" \"Niin, juuri siihen hintaan\",\nvastasi nainen.',80,NULL),
 (2072,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',9,'Silloin Pietari sanoi: \"Kuinka te\nyksissä tuumin ryhdyitte koettelemaan Herran Henkeä?\nKuuletko askelia oven takaa? Ne, jotka hautasivat\nmiehesi, kantavat täältä myös sinut.\"',80,NULL),
 (2073,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',10,'0Siinä\nsamassa nainen kaatui kuolleena Pietarin jalkoihin.\nNuoret miehet tulivat sisään ja näkivät naisen\nkuolleen. He veivät hänet pois ja hautasivat hänet\nmiehensä viereen.',80,NULL),
 (2074,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',11,'1Kauhu valtasi koko seurakunnan\nja kaikki jotka tästä kuulivat.       ',80,NULL),
 (2075,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',27,'7Apostolit tuotiin neuvoston eteen, ja ylipappi\nalkoi kuulustella heitä.',80,NULL),
 (2076,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',28,'8Hän sanoi: \"Me kielsimme\njyrkästi teitä opettamasta sen miehen nimessä. Mutta\nnyt koko Jerusalem on täynnä teidän oppianne ja te\nyritätte panna sen miehen kuoleman meidän syyksemme.\"',80,NULL),
 (2077,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',29,'9Tähän Pietari ja muut apostolit vastasivat:\n\"Ennemmin tulee totella Jumalaa kuin ihmisiä.',80,NULL),
 (2078,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',30,'0Meidän isiemme Jumala on herättänyt kuolleista\nJeesuksen, jonka te ripustitte ristinpuulle ja\nmurhasitte.',80,NULL),
 (2079,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',31,'1Jumala on korottanut hänet oikealle\npuolelleen Ruhtinaaksi ja Pelastajaksi johdattaakseen\nIsraelin kääntymykseen ja antaakseen sille synnit\nanteeksi.',80,NULL),
 (2080,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',32,'2Me olemme kaiken tämän todistajat --\nsamoin Pyhä Henki, jonka Jumala on antanut niille\njotka häntä tottelevat.\" ',80,NULL),
 (2081,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',33,'3Tämän kuullessaan neuvoston jäsenet olivat\npakahtua raivosta ja aikoivat tappaa heidät.',80,NULL),
 (2082,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',34,'4Silloin nousi neuvoston keskeltä eräs fariseus*,\nGamaliel-niminen lainopettaja, jota koko kansa piti\narvossa. Hän käski viedä miehet hetkeksi ulos',80,NULL),
 (2083,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',35,'5ja\nsanoi sitten: \"Israelilaiset, harkitkaa tarkoin, ennen\nkuin teette mitään näille miehille.',80,NULL),
 (2084,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',36,'6Ennen meidän\npäiviämme esiintyi Teudas. Hän väitti jokin olevansa\nja saikin mukaansa neljäsataa miestä, mutta hänet\ntapettiin ja koko hänen kannattajajoukkonsa hävisi\njäljettömiin.',80,NULL),
 (2085,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',37,'7Hänen jälkeensä esiintyi\nverollepanon aikoihin Juudas Galilealainen. Myös\nJuudas houkutteli kansaa puolelleen, mutta hänkin sai\nsurmansa ja koko hänen kannattajajoukkonsa joutui\nhajalle.',80,NULL),
 (2086,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',38,'8Siksi annan teille nyt tämän neuvon:\njättäkää nämä miehet rauhaan, antakaa heidän olla. Jos\ntämä heidän ajamansa hanke on lähtöisin ihmisistä, se\nkukistuu itsestään.',80,NULL),
 (2087,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',39,'9Jos se taas on Jumalasta, te\nette pysty heitä kukistamaan. Pitäkää varanne! Entä\njos te taistelettekin itseään Jumalaa vastaan?\" Gamalielin puhe saavutti vastakaikua.',80,NULL),
 (2088,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',40,'0Neuvosto\nkutsui apostolit sisään, ruoskitti heidät ja kielsi\nheitä puhumasta Jeesuksen nimessä, mutta päästi heidät\nvapaaksi.',80,NULL),
 (2089,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',41,'1Apostolit lähtivät neuvostosta iloisina\nsiitä, että olivat saaneet kunnian kärsiä häväistystä\nJeesuksen nimen tähden.',80,NULL),
 (2090,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',42,'2Entiseen tapaan he joka\npäivä opettivat temppelissä ja kodeissa ja julistivat,\nettä Jeesus on Messias. ',80,NULL),
 (2091,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',1,'Noihin aikoihin, opetuslasten joukon yhä kasvaessa,\nkreikkaa puhuvat alkoivat syyttää hepreankielisiä*siitä, että heidän leskiään syrjittiin päivittäisiä\navustuksia jaettaessa.',81,NULL),
 (2092,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',2,'Silloin apostolit, ne\nkaksitoista, kutsuivat koolle koko opetuslasten joukon\nja sanoivat: \"Ei ole oikein, että me ruoan jakamisen\ntähden lyömme laimin Jumalan sanan.',81,NULL),
 (2093,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',3,'Valitkaa siis,\nveljet, keskuudestanne seitsemän hyvämaineista miestä,\njotka ovat Hengen ja viisauden täyttämiä, niin me\nasetamme heidät tähän tehtävään.',81,NULL),
 (2094,'Anonymous','2008-10-17 16:33:00','Anonymous','2008-10-17 16:33:00',4,'Silloin me voimme\nomistautua rukoukseen ja Jumalan sanan jakamiseen.\" ',81,NULL),
 (2095,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',5,'Kaikki, jotka olivat koolla, pitivät tätä ehdotusta\nhyvänä. He valitsivat Stefanoksen, miehen, joka oli\ntäynnä uskoa ja Pyhää Henkeä, sekä Filippoksen,\nProkoroksen, Nikanorin, Timonin, Parmenaksen ja\nNikolaoksen, antiokialaisen käännynnäisen.',81,NULL),
 (2096,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',6,'Nämä\ntuotiin apostolien eteen, ja apostolit rukoilivat ja\npanivat kätensä heidän päälleen. ',81,NULL),
 (2097,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',7,'Jumalan sana levisi leviämistään. Opetuslasten\nmäärä kasvoi Jerusalemissa nopeasti, ja usko voitti\npuolelleen myös monia pappeja.   ',81,NULL),
 (2098,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',11,'1Silloin he värväsivät joitakin miehiä sanomaan:\n\"Olemme kuulleet hänen puhuvan herjaavia sanoja\nMooseksesta ja Jumalasta.\"',81,NULL),
 (2099,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',12,'2He yllyttivät kansaa,\nkansan vanhimpia ja lainopettajia, ja sitten he\nkävivät käsiksi Stefanokseen ja veivät hänet Suuren\nneuvoston eteen.',81,NULL),
 (2100,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',13,'3Siellä he toivat kuultavaksi\nvääriä todistajia, jotka sanoivat: \"Tämä mies puhuu\nlakkaamatta tätä pyhää paikkaa ja lakia vastaan.',81,NULL),
 (2101,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',14,'4Me olemme kuulleet hänen sanovan, että Jeesus, se\nNasaretilainen, hajottaa tämän paikan ja muuttaa\nsäädökset, jotka Mooses on meille antanut.\"',81,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (2102,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',15,'5Kaikki, jotka istuivat neuvostossa, kiinnittivät\nkatseensa Stefanokseen, ja hänen kasvonsa olivat\nheistä niin kuin enkelin kasvot. ',81,NULL),
 (2103,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',1,'Ylipappi kysyi: \"Pitääkö syytös paikkansa?\" ',82,NULL),
 (2104,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',2,'Stefanos vastasi: \"Veljet, kansamme isät, kuulkaa minua. Kirkkauden\nJumala ilmestyi isällemme Abrahamille, kun hän oli\nMesopotamiassa eikä vielä ollut asettunut Harraniin.',82,NULL),
 (2105,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',3,'Jumala sanoi hänelle: \'Lähde maastasi ja sukusi\nparista ja mene maahan, jonka minä sinulle osoitan.\'',82,NULL),
 (2106,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',4,'Silloin hän lähti kaldealaisten maasta ja asettui\nHarraniin, ja hänen isänsä kuoltua Jumala siirsi hänet\ntähän maahan, jossa te nyt asutte.',82,NULL),
 (2107,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',5,'Jumala ei\nantanut siitä Abrahamille jalanleveyden vertaa, mutta\nlupasi, että hän antaa sen omaksi hänelle ja hänen\njälkeläisilleen -- vaikka Abrahamilla ei ollut lapsia.',82,NULL),
 (2108,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',6,'Jumala sanoi: \'Sinun jälkeläisesi tulevat elämään\nmuukalaisina vieraassa maassa, ja heitä pidetään\norjuudessa ja sorretaan neljäsataa vuotta.',82,NULL),
 (2109,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',7,'Mutta\nminä\' -- niin sanoi Jumala -- \'tuomitsen sen kansan,\njoka orjuuttaa heitä. Sen jälkeen he lähtevät sieltä\nja palvelevat minua tässä paikassa.\'',82,NULL),
 (2110,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',8,'Jumala teki\nAbrahamin kanssa liiton, jonka merkkinä on\nympärileikkaus. Abrahamille syntyi Iisak, ja Abraham\nympärileikkasi hänet kahdeksantena päivänä, samoin\nIisak Jaakobin ja Jaakob kansamme kaksitoista\nkantaisää. ',82,NULL),
 (2111,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',9,'\"Kantaisämme kadehtivat Joosefia ja myivät hänet\nEgyptiin. Mutta Jumala oli hänen kanssaan',82,NULL),
 (2112,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',10,'0ja\npelasti hänet kaikista ahdingoista. Jumala antoi\nhänelle viisautta, niin että farao, Egyptin kuningas,\nmieltyi häneen ja uskoi hänen hallintaansa koko\nEgyptin maan ja oman taloutensa.',82,NULL),
 (2113,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',11,'1Sitten tuli koko\nEgyptiin ja Kanaaniin nälänhätä, vaikea ahdingon aika,\neivätkä isämme saaneet mistään ravintoa.',82,NULL),
 (2114,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',12,'2Mutta\nJaakob kuuli, että Egyptissä oli viljaa, ja lähetti\nisämme sinne. Ensimmäistä käyntiä',82,NULL),
 (2115,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',13,'3seurasi toinen,\nja silloin Joosef ilmaisi itsensä veljilleen ja farao\nsai tietää hänen suvustaan.',82,NULL),
 (2116,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',14,'4Joosef lähetti sanan\nisälleen Jaakobille ja kutsui luokseen hänet ja koko\nsukunsa, seitsemänkymmentäviisi henkeä.',82,NULL),
 (2117,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',15,'5Niin\nJaakob meni Egyptiin. Siellä hän kuoli, samoin\nkantaisämme,',82,NULL),
 (2118,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',16,'6ja heidät vietiin Sikemiin ja pantiin\nhautaan, jonka Abraham oli hopealla ostanut Sikemistä\nHamorin pojilta. ',82,NULL),
 (2119,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',17,'7\"Yhä lähemmäs tuli se aika, jolloin Jumala oli\ntäyttävä Abrahamille antamansa lupauksen. Israelin\nkansa kasvoi kasvamistaan Egyptissä,',82,NULL),
 (2120,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',18,'8kunnes siellä\nnousi valtaan uusi kuningas, joka ei tiennyt mitään\nJoosefista.',82,NULL),
 (2121,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',19,'9Hän juonitteli kansaamme vastaan,\nsorti isiämme ja pakotti heidät jättämään\nvastasyntyneet lapsensa heitteille, etteivät ne jäisi\neloon.',82,NULL),
 (2122,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',20,'0Tuohon aikaan syntyi Mooses, ja hän oli\nJumalalle mieleinen. Kolme kuukautta häntä hoidettiin\nkotona,',82,NULL),
 (2123,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',21,'1ja sitten hänet jätettiin heitteille.\nMutta faraon tytär pelasti hänet ja hoiti häntä omana\npoikanaan.',82,NULL),
 (2124,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',22,'2Mooses sai parhaan egyptiläisen\nkasvatuksen ja osoitti kykynsä sekä sanoissa että\nteoissa. ',82,NULL),
 (2125,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',23,'3\"Kun Mooses oli tullut neljänkymmenen vuoden\nikään, hänessä heräsi halu mennä katsomaan\nisraelilaisia, heimoveljiään.',82,NULL),
 (2126,'Anonymous','2008-10-17 16:33:01','Anonymous','2008-10-17 16:33:01',24,'4Nähdessään, miten\njulmasti yhtä heistä kohdeltiin, hän riensi apuun ja\nkosti pahoinpidellyn puolesta lyömällä egyptiläisen\nhengiltä.',82,NULL),
 (2127,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',25,'5Hän luuli veljiensä ymmärtävän, että\nJumala oli valinnut hänet heidän pelastajakseen, mutta\nhe eivät ymmärtäneet.',82,NULL),
 (2128,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',26,'6Seuraavana päivänä hän tuli\npaikalle juuri kun kaksi heistä tappeli keskenään, ja\nhän yritti saada sovinnon aikaan. Hän sanoi: \'Miehet,\ntehän olette veljiä! Miksi te käytte toistenne\nkimppuun?\'',82,NULL),
 (2129,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',27,'7Mutta se mies, joka oli tappelun\naloittanut, työnsi Mooseksen syrjään ja sanoi: \'Kuka\nsinut on pannut meidän päälliköksemme ja\ntuomariksemme?',82,NULL),
 (2130,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',28,'8Aiotko tappaa minutkin, niin kuin\neilen tapoit sen egyptiläisen?\'',82,NULL),
 (2131,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',29,'9Tämän kuultuaan\nMooses pakeni. Hän eli sitten muukalaisena Midianin\nmaassa, ja hänelle syntyi siellä kaksi poikaa. ',82,NULL),
 (2132,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',30,'0\"Kun oli kulunut neljäkymmentä vuotta, autiomaassa\nlähellä Siinainvuorta hänelle ilmestyi enkeli\ntulenliekissä, joka nousi orjantappurapensaasta.',82,NULL),
 (2133,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',31,'1Mooses hämmästyi tätä näkyä ja meni lähemmäs\nkatsoakseen tarkemmin, mutta silloin hän kuuli Herran\näänen:',82,NULL),
 (2134,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',32,'2\'Minä olen sinun isiesi Jumala, Abrahamin,\nIisakin ja Jaakobin Jumala.\' Mooses alkoi vapista eikä\nrohjennut katsoa.',82,NULL),
 (2135,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',33,'3Mutta Herra sanoi hänelle:\n\'Riisu kengät jalastasi, sillä paikka, jossa seisot,\non pyhää maata.',82,NULL),
 (2136,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',34,'4Minä olen nähnyt, miten kansaani\nsorretaan Egyptissä, ja kuullut, miten se huokaa. Nyt\nolen astunut alas vapauttaakseni kansani. Tule, minä\nlähetän sinut Egyptiin.\' ',82,NULL),
 (2137,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',35,'5\"Tämän Mooseksen israelilaiset olivat kieltäneet\nsanoen: \'Kuka sinut on pannut päälliköksi ja\ntuomariksi?\' Mutta juuri hänet Jumala lähetti\npäälliköksi ja vapauttajaksi, kun enkeli ilmestyi\nhänelle orjantappurapensaassa.',82,NULL),
 (2138,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',36,'6Juuri hän johti\nheidät pois ja teki ihmeitä ja tunnustekoja Egyptissä,\nPunaisellamerellä ja autiomaassa neljänkymmenen vuoden\najan.',82,NULL),
 (2139,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',37,'7Ja juuri tämä Mooses sanoi israelilaisille:\n\'Jumala on veljienne joukosta nostava teille\nprofeetan, minun kaltaiseni.\'',82,NULL),
 (2140,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',38,'8Juuri Mooses,\nsilloin kun kansa oli koolla autiomaassa, välitti\nisillemme sen, mitä enkeli puhui hänelle\nSiinainvuorella. Hän otti vastaan elävät sanat\nantaakseen ne edelleen meille.',82,NULL),
 (2141,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',39,'9Mutta meidän isämme\neivät suostuneet tottelemaan häntä, vaan työnsivät\nhänet syrjään ja mielittelivät takaisin Egyptiin.',82,NULL),
 (2142,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',40,'0He sanoivat Aaronille: \'Tee meille jumalia, jotka\njohdattavat meitä. Me emme tiedä, mitä on tapahtunut\nMoosekselle, tuolle miehelle, joka toi meidät pois\nEgyptistä.\'',82,NULL),
 (2143,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',41,'1Niin he sitten tekivät sonnipatsaan,\ntoivat sille uhreja ja viettivät ilojuhlaa tämän\nomatekoisen jumalansa kunniaksi.',82,NULL),
 (2144,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',42,'2Mutta Jumala\nkäänsi heille selkänsä ja hylkäsi heidät, antoi heidän\npalvoa taivaan tähtiä. Näinhän sanotaan profeettojen\nkirjassa:   ',82,NULL),
 (2145,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',44,'4\"Meidän isillämme oli autiomaassa todistuksen\nteltta. Mooses oli sen tehnyt, sen käskystä, joka\nhänelle puhui, näkemänsä mallin mukaan.',82,NULL),
 (2146,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',45,'5Sen teltan\nmeidän isämme saivat perinnöksi, ja he toivat sen\nmukanaan vallatessaan Joosuan johdolla tämän maan\nkansoilta, jotka Jumala karkotti heidän tieltään. Näin\noli Daavidin aikaan saakka.',82,NULL),
 (2147,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',46,'6Jumala mieltyi\nDaavidiin, ja niin Daavid pyysi, että saisi etsiä\nasuinpaikan Jaakobin suvun Jumalalle.',82,NULL),
 (2148,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',47,'7Mutta Salomo\nrakensi hänelle asuttavaksi talon! Korkein ei\nkuitenkaan asu ihmiskäsin tehdyissä rakennuksissa.',82,NULL),
 (2149,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',48,'8Profeettahan sanoo:   ',82,NULL),
 (2150,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',51,'1\"Te niskuroijat, ympärileikatut mutta sydämeltänne\nja korviltanne pakanat! Aina te olette vastustamassa\nPyhää Henkeä -- niin kuin isänne, niin myös te!',82,NULL),
 (2151,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',52,'2Onko yhtäkään profeettaa, jota teidän isänne eivät\nolisi vainonneet? He tappoivat ne, jotka ennalta\nilmoittivat, että Vanhurskas oli tuleva. Ja nyt olette\nte kavaltaneet ja murhanneet hänet!',82,NULL),
 (2152,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',53,'3Te saitte\nenkelien tuomana lain, mutta ette ole sitä\nnoudattaneet.\"   ',82,NULL),
 (2153,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',57,'7Silloin he alkoivat huutaa suureen ääneen,\ntukkivat korvansa ja ryntäsivät yhtenä miehenä hänen\nkimppuunsa.',82,NULL),
 (2154,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',58,'8He raahasivat hänet ulos kaupungista\nkivittääkseen hänet, ja todistajat jättivät viittansa\nSaul-nimisen nuoren miehen huostaan.',82,NULL),
 (2155,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',59,'9Kun he\nkivittivät Stefanosta, tämä rukoili Herraa ja sanoi:\n\"Herra Jeesus, ota vastaan minun henkeni.\"',82,NULL),
 (2156,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',60,'0Hän\nvaipui polvilleen ja huusi kovalla äänellä: \"Herra,\nälä vaadi heitä tilille tästä synnistä!\" Sen\nsanottuaan hän nukkui pois. 8:1Saul oli samaa mieltä\nkuin muut ja hyväksyi sen, että Stefanos surmattiin. ',82,NULL),
 (2157,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',2,'Muutamat hurskaat miehet hautasivat Stefanoksen ja\npitivät hänelle suuret valittajaiset.',83,NULL),
 (2158,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',3,'Mutta Saul\ntahtoi tuhota seurakunnan. Hän kulki talosta taloon,\nraastoi miehet ja naiset ulos ja toimitti heidät\nvankilaan.    ',83,NULL),
 (2159,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',9,'Jo pitkään oli kaupungissa harjoittanut noituutta\nmuuan Simon, joka väitti olevansa jotakin suurta ja\npiti Samarian kansaa ihmetyksen vallassa.',83,NULL),
 (2160,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',10,'0Kaikki,\npienimmästä suurimpaan, juoksivat hänen perässään ja\nsanoivat: \"Hän on Suuri Voima, Jumalan voima.\"',83,NULL),
 (2161,'Anonymous','2008-10-17 16:33:02','Anonymous','2008-10-17 16:33:02',11,'1Häntä kuunneltiin, koska hän jo pitkän aikaa oli\nnoitatempuillaan hämmästyttänyt ihmisiä.',83,NULL),
 (2162,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',12,'2Mutta kun\nihmiset kääntyivät uskomaan Filipposta, joka julisti\nhyvää sanomaa Jumalan valtakunnasta ja Jeesuksesta\nKristuksesta, he ottivat kasteen, sekä miehet että\nnaiset.',83,NULL),
 (2163,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',13,'3Jopa Simon itse uskoi, ja hänet\nkastettiin. Hän pysytteli sitten Filippoksen seurassa,\nja nähdessään ne suuret ihmeet ja voimateot, joita\nFilippos teki, hän oli hämmästyksestä suunniltaan.   ',83,NULL),
 (2164,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',18,'8Kun Simon näki, että ne, joiden päälle apostolit\npanivat kätensä, saivat Hengen, hän tarjosi heille\nrahaa',83,NULL),
 (2165,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',19,'9ja sanoi: \"Antakaa minullekin tuollainen\nvalta, että kenen päälle panen käteni, se saa Pyhän\nHengen.\"',83,NULL),
 (2166,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',20,'0Mutta Pietari sanoi hänelle: \"Kadotukseen\njoudut rahoinesi, kun luulet, että Jumalan lahja on\nrahalla ostettavissa!',83,NULL),
 (2167,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',21,'1Sinulla ei ole tässä osaa\neikä arpaa, sillä sinä et ole vilpitön Jumalan edessä.',83,NULL),
 (2168,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',22,'2Käänny siis tästä pahuudestasi ja rukoile Herraa!\nKenties hän antaa ajatuksesi anteeksi.',83,NULL),
 (2169,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',23,'3Minä näen,\nettä sinä olet myrkkyä täynnä ja vääryys pitää sinua\nkahleissaan.\"',83,NULL),
 (2170,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',24,'4Silloin Simon sanoi: \"Rukoilkaa\nminun puolestani Herraa, ettei minulle tapahtuisi\nmitään tuollaista, mitä sanoitte!\" ',83,NULL),
 (2171,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',25,'5Kun apostolit olivat puhuneet Herran sanaa ja\ntodistaneet hänestä, he lähtivät takaisin Jerusalemiin\nja julistivat matkalla evankeliumia monissa Samarian\nkylissä.   ',83,NULL),
 (2172,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',29,'9Henki sanoi Filippokselle: \"Mene lähemmäs ja\npysyttele vaunujen vieressä.\"',83,NULL),
 (2173,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',30,'0Filippos juoksi\nvaunujen luo, ja kuullessaan miehen lukevan profeetta\nJesajaa hän sanoi: \"Sinä kyllä luet, mutta mahdatko\nymmärtää?\"',83,NULL),
 (2174,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',31,'1Mies vastasi: \"Kuinka ymmärtäisin, kun\nkukaan ei minua neuvo.\" Hän pyysi Filipposta nousemaan\nvaunuihin ja istumaan vierellään.',83,NULL),
 (2175,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',32,'2Kohta, jota hän\nkirjasta luki, oli tämä:   ',83,NULL),
 (2176,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',34,'4Hoviherra sanoi Filippokselle: \"Voisitko sanoa,\nkenestä profeetta puhuu, itsestäänkö vai jostakin\ntoisesta?\"',83,NULL),
 (2177,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',35,'5Filippos rupesi puhumaan, aloitti\ntuosta kirjoitusten kohdasta ja julisti miehelle\nevankeliumia Jeesuksesta. ',83,NULL),
 (2178,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',36,'6Matkan jatkuessa he tulivat paikkaan, jossa oli\nvettä. Silloin hoviherra sanoi: \"Tässä on vettä.\nEstääkö mikään kastamasta minua?\" [',83,NULL),
 (2179,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',37,'7Filippos sanoi\nhänelle: \"Jos koko sydämestäsi uskot, se on\nmahdollista.\" Hoviherra vastasi: \"Minä uskon, että\nJeesus Kristus on Jumalan Poika.\"]*',83,NULL),
 (2180,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',38,'8Hän käski\npysäyttää vaunut, ja he molemmat, Filippos ja\nhoviherra, astuivat veteen, ja Filippos kastoi hänet. ',83,NULL),
 (2181,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',39,'9Kun he olivat nousseet vedestä, Herran Henki\ntempasi Filippoksen pois. Hoviherra ei enää nähnyt\nhäntä mutta jatkoi iloisena matkaansa.',83,NULL),
 (2182,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',40,'0Filippos\nilmaantui sitten Asdodiin. Evankeliumia julistaen hän\nkulki kaupungista kaupunkiin, kunnes tuli Kesareaan. ',83,NULL),
 (2183,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',1,'Saul uhkui yhä vihaa ja murhanhimoa Herran\nopetuslapsia kohtaan. Hän meni ylipapin luo',84,NULL),
 (2184,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',2,'ja\npyysi tältä Damaskoksen synagogille osoitettuja\nkirjeitä, jotka valtuuttaisivat hänet vangitsemaan\ntuolle tielle lähteneet, sekä miehet että naiset,\nketkä vain käsiinsä saisi, ja tuomaan heidät\nJerusalemiin. ',84,NULL),
 (2185,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',3,'Matkalla, Saulin ollessa jo lähellä Damaskosta,\ntaivaasta leimahti yhtäkkiä valo hänen ympärilleen.',84,NULL),
 (2186,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',4,'Hän kaatui maahan ja kuuli äänen sanovan: \"Saul,\nSaul, miksi vainoat minua?\"',84,NULL),
 (2187,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',5,'Hän kysyi: \"Herra, kuka\nsinä olet?\" Ääni vastasi: \"Minä olen Jeesus, jota sinä\nvainoat.',84,NULL),
 (2188,'Anonymous','2008-10-17 16:33:03','Anonymous','2008-10-17 16:33:03',6,'Nouse ja mene kaupunkiin. Siellä saat\nkuulla, mitä sinun on tehtävä.\" ',84,NULL),
 (2189,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',7,'Saulin matkatoverit seisoivat sanattomina. He\nkuulivat äänen mutta eivät havainneet ketään.',84,NULL),
 (2190,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',8,'Saul\nnousi maasta, mutta kun hän avasi silmänsä, hän ei\nnähnyt mitään. Toiset veivät hänet Damaskokseen\nkädestä taluttaen.',84,NULL),
 (2191,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',9,'Kolmeen päivään hän ei nähnyt\nmitään, ei syönyt eikä juonut. ',84,NULL),
 (2192,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',10,'0Damaskoksessa oli Ananias-niminen opetuslapsi.\nHerra puhutteli häntä näyssä: \"Ananias!\" \"Tässä olen,\nHerra\", hän vastasi.',84,NULL),
 (2193,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',11,'1Herra sanoi: \"Mene\nSuorallekadulle ja kysy Juudaksen talosta Saulia, jota\nkutsutaan Tarsolaiseksi. Hän rukoilee,',84,NULL),
 (2194,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',12,'2ja näyssä\nhän on nähnyt, että Ananias-niminen mies tulee sisään\nja panee kätensä hänen päälleen, jotta hän saisi\nnäkönsä takaisin.\"',84,NULL),
 (2195,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',13,'3Ananias vastasi: \"Herra, minä\nolen monilta kuullut, kuinka paljon pahaa se mies on\nJerusalemissa tehnyt sinun pyhillesi*.',84,NULL),
 (2196,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',14,'4Tännekin\nhän on tullut ylipappien valtuuttamana vangitsemaan\nkaikki, jotka huutavat avuksi sinun nimeäsi.\"',84,NULL),
 (2197,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',15,'5Mutta Herra sanoi hänelle: \"Mene, minä olen\nvalinnut hänet aseekseni, tunnustamaan nimeäni\nmaailman kansojen ja kuninkaiden ja myös Israelin\nkansan edessä.',84,NULL),
 (2198,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',16,'6Minä tulen osoittamaan hänelle,\nettä hän joutuu paljon kärsimään minun nimeni tähden.\" ',84,NULL),
 (2199,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',17,'7Ananias lähti. Hän meni sisälle taloon, pani\nkätensä Saulin päälle ja sanoi: \"Saul, veljeni! Herra\non lähettänyt minut -- Jeesus, joka ilmestyi sinulle,\nkun olit matkalla tänne. Hän lähetti minut, jotta\nsaisit näkösi takaisin ja täyttyisit Pyhästä\nHengestä.\"',84,NULL),
 (2200,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',18,'8Samassa oli kuin suomut olisivat\npudonneet Saulin silmistä, ja hän näki jälleen. Hän\nnousi jalkeille, ja hänet kastettiin.',84,NULL),
 (2201,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',19,'9Ruokaa\nnautittuaan hän taas voimistui.   ',84,NULL),
 (2202,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',23,'3Jonkin ajan kuluttua juutalaiset päättivät tappaa\nSaulin.',84,NULL),
 (2203,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',24,'4Saul sai kuitenkin vihiä heidän\nhankkeestaan. He pitivät kaupungin porteilla vartiota\nyötä päivää, jotta saisivat hänet surmatuksi,',84,NULL),
 (2204,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',25,'5mutta eräänä yönä opetuslapset auttoivat hänet\npakoon laskemalla hänet korissa muurilta alas.   ',84,NULL),
 (2205,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',31,'1Kaikkialla Juudeassa, Galileassa ja Samariassa\nkirkolla oli nyt rauha. Se eli ja rakentui Herran\npelossa, ja Pyhä Henki vahvisti sitä, niin että se\nyhäti kasvoi.    ',84,NULL),
 (2206,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',36,'6Joppessa*oli opetuslasten joukossa Tabita-niminen\nnainen; nimi on kreikaksi Dorkas.*Hän teki paljon\nhyvää ja avusti köyhiä runsain määrin.',84,NULL),
 (2207,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',37,'7Mutta\nnoihin aikoihin hän sairastui ja kuoli, ja hänet\npestiin ja kannettiin kattohuoneeseen.',84,NULL),
 (2208,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',38,'8Lydda on\nlähellä Joppea, ja kun opetuslapset kuulivat Pietarin\nolevan siellä, he lähettivät kaksi miestä viemään\nhänelle sanan: \"Tule kiireesti meidän luoksemme.\" ',84,NULL),
 (2209,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',39,'9Pietari lähti heti miesten mukaan. Kun hän tuli\npaikalle, hänet vietiin kattohuoneeseen, ja\nleskivaimot kerääntyivät hänen ympärilleen ja\nnäyttivät itkien hänelle paitoja ja muita vaatteita,\njotka Dorkas oli tehnyt ollessaan vielä heidän\nkanssaan.',84,NULL),
 (2210,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',40,'0Pietari käski kaikkien poistua. Hän\npolvistui ja rukoili. Sitten hän kääntyi vainajan\npuoleen ja sanoi: \"Tabita, nouse!\" Nainen avasi\nsilmänsä, ja nähdessään Pietarin hän nousi istumaan.',84,NULL),
 (2211,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',41,'1Pietari ojensi hänelle kätensä ja auttoi hänet\njalkeille. Sitten Pietari kutsui huoneeseen lesket ja\nkaikki pyhät, ja he saivat nähdä, että Tabita oli\nelossa. ',84,NULL),
 (2212,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',42,'2Tapahtumasta levisi tieto ympäri Joppea, ja monet\nuskoivat nyt Herraan.',84,NULL),
 (2213,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',43,'3Pietari jäi vielä joksikin\naikaa Joppeen ja asui erään Simon-nimisen nahkurin\nluona. ',84,NULL),
 (2214,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',1,'Kesareassa oli Cornelius-niminen mies, joka palveli\nsadanpäällikkönä niin sanotussa Italialaisessa\nkohortissa*.',85,NULL),
 (2215,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',2,'Hän oli hurskas ja jumalaapelkäävä,\nniin kuin koko hänen perhekuntansakin, hän antoi\nrunsaasti almuja juutalaisille ja rukoili alati\nJumalaa.',85,NULL),
 (2216,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',3,'Kerran hän iltapäivällä yhdeksännen tunnin\nvaiheilla näki näyssä selvästi Jumalan enkelin, joka\ntuli hänen luokseen ja sanoi: \"Cornelius!\"',85,NULL),
 (2217,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',4,'Cornelius tuijotti peloissaan enkeliä ja kysyi:\n\"Mitä tahdot, herra?\" Enkeli vastasi: \"Rukouksesi ja\nalmusi ovat uhrina nousseet Jumalan eteen, ja hän on\nmuistanut sinut.',85,NULL),
 (2218,'Anonymous','2008-10-17 16:33:04','Anonymous','2008-10-17 16:33:04',5,'Lähetä nyt miehiä Joppeen\nnoutamaan tänne Simon, jota kutsutaan myös Pietariksi.',85,NULL),
 (2219,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',6,'Hän asuu Simon-nimisen nahkurin luona, jonka talo\non meren rannalla.\"',85,NULL),
 (2220,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',7,'Kun enkeli tämän puhuttuaan oli\nmennyt pois, Cornelius kutsui luokseen kaksi\npalvelijaansa sekä yhden läheteistään, hurskaan\nsotilaan.',85,NULL),
 (2221,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',8,'Hän kertoi heille kaiken ja lähetti\nheidät Joppeen. ',85,NULL),
 (2222,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',9,'Seuraavana päivänä, kun miehet olivat matkalla ja\njo lähestyivät kaupunkia, Pietari nousi kuudennen\ntunnin vaiheilla katolle rukoilemaan.',85,NULL),
 (2223,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',10,'0Hänen tuli\nnälkä ja hän halusi ruokaa, mutta sillä aikaa kun\nateriaa valmistettiin hän joutui hurmoksiin.',85,NULL),
 (2224,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',11,'1Hän\nnäki taivaan avoinna, ja taivaasta tuli alas ikään\nkuin suuri purjekangas, joka laskettiin maahan\nneljästä kulmastaan kannateltuna.',85,NULL),
 (2225,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',12,'2Siinä oli\nkaikenlaisia maan eläimiä, nelijalkaisia ja\nmatelijoita, sekä taivaan lintuja.',85,NULL),
 (2226,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',13,'3Samassa hän\nkuuli äänen: \"Nouse, Pietari! Teurasta ja syö!\"',85,NULL),
 (2227,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',14,'4Mutta hän vastasi: \"Ei, ei, Herra! En ole koskaan\nsyönyt mitään epäpuhdasta tai kiellettyä.\"',85,NULL),
 (2228,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',15,'5Silloin\nääni puhui uudelleen: \"Minkä Jumala on puhdistanut,\nsitä älä sinä sano epäpuhtaaksi.\"',85,NULL),
 (2229,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',16,'6Tämä tapahtui\nkolmesti. Heti sen jälkeen kangas nostettiin\ntaivaaseen. ',85,NULL),
 (2230,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',17,'7Kun Pietari vielä ihmetteli, mitä hänen näkynsä\noikein tarkoitti, Corneliuksen lähettämät miehet\nilmaantuivat portille. He olivat kyselemällä löytäneet\nSimonin talon',85,NULL),
 (2231,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',18,'8ja tiedustelivat nyt kuuluvalla\näänellä, oliko talossa vieraana Simon, toiselta\nnimeltä Pietari.',85,NULL),
 (2232,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',19,'9Pietarin yhä pohtiessa näkyään\nHenki sanoi hänelle: \"Kolme miestä kysyy sinua.',85,NULL),
 (2233,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',20,'0Mene alas ja lähde epäröimättä heidän mukaansa.\nMinä olen lähettänyt heidät.\"',85,NULL),
 (2234,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',21,'1Pietari meni alas\nmiesten luo ja sanoi: \"Minä olen se, jota etsitte.\nMikä on asianne?\"',85,NULL),
 (2235,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',22,'2Miehet vastasivat: \"Pyhä enkeli\non ilmestynyt sadanpäällikkö Corneliukselle, joka on\nhurskas ja jumalaapelkäävä mies ja hyvässä maineessa\nkaikkien juutalaisten keskuudessa. Enkeli käski hänen\nkutsua sinut kotiinsa ja kuunnella, mitä sinulla on\nsanottavana.\"',85,NULL),
 (2236,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',23,'3Silloin Pietari pyysi miehet sisään\nvieraikseen. Seuraavana päivänä Pietari lähti matkaan miesten\nkanssa, ja muutamat Joppessa asuvat veljet tulivat\nhänen mukaansa.',85,NULL),
 (2237,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',24,'4Sitä seuraavana päivänä hän tuli\nKesareaan. Cornelius oli kutsunut koolle sukulaisensa\nja lähimmät ystävänsä ja odotti tulijoita.',85,NULL),
 (2238,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',25,'5Kun\nPietari oli astumassa sisään, Cornelius tuli häntä\nvastaan ja kumartui hänen edessään maahan saakka.',85,NULL),
 (2239,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',26,'6Mutta Pietari sanoi: \"Nouse, minä olen vain\nihminen niin kuin sinäkin\", ja auttoi hänet ylös.',85,NULL),
 (2240,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',27,'7Sitten hän Corneliuksen kanssa keskustellen meni\nsisään ja tapasi siellä suuren joukon ihmisiä.',85,NULL),
 (2241,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',28,'8Hän\nsanoi heille: \"Niin kuin tiedätte, juutalaisen ei ole\nsallittua seurustella toiseen kansaan kuuluvien kanssa\neikä mennä heidän luokseen. Jumala on kuitenkin\nosoittanut minulle, ettei ketään ihmistä saa pitää\nepäpuhtaana tai saastaisena.',85,NULL),
 (2242,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',29,'9Siksi lähdin\nvastustelematta, kun minua tultiin hakemaan. Nyt\ntahtoisin tietää, miksi te pyysitte minua tulemaan.\" ',85,NULL),
 (2243,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',30,'0Cornelius vastasi: \"Kolme päivää sitten olin tähän\naikaan, yhdeksännellä tunnilla, täällä kotonani\nrukoilemassa. Silloin seisoi yhtäkkiä edessäni mies,\njolla oli hohtavan valkeat vaatteet,',85,NULL),
 (2244,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',31,'1ja hän sanoi:\n\'Cornelius, Jumala on kuullut rukouksesi ja muistanut\nalmusi.',85,NULL),
 (2245,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',32,'2Lähetä sana Joppeen ja kutsu luoksesi\nSimon, jota sanotaan myös Pietariksi. Hän asuu nahkuri\nSimonin talossa meren rannalla.\'',85,NULL),
 (2246,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',33,'3Niinpä lähetin\nheti noutamaan sinua, ja teit hyvin, kun tulit. Nyt\nolemme kaikki täällä Jumalan edessä kuullaksemme, mitä\nHerra on sinun käskenyt puhua.\"   \"Nyt minä todella käsitän, ettei Jumala erottele\nihmisiä.',85,NULL),
 (2247,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',35,'5Hän hyväksyy jokaisen, joka pelkää häntä\nja noudattaa hänen tahtoaan, kuului tämä mihin kansaan\ntahansa.',85,NULL),
 (2248,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',36,'6Hän lähetti Israelin kansalle sanansa,\nilmoitti hyvän sanoman rauhasta, jonka Jeesus Kristus\non tuonut -- hän, joka on kaikkien Herra. ',85,NULL),
 (2249,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',37,'7\"Te tiedätte, mitä on tapahtunut ensin Galileassa\nja sitten kaikkialla Juudeassa*sen jälkeen kun\nJohannes saarnasi ja kastoi.',85,NULL),
 (2250,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',38,'8Te olette kuulleet\nJeesuksesta, nasaretilaisesta, jonka Jumala voiteli\nPyhällä Hengellä ja voimalla. Hän kulki ympäri maata,\nteki hyvää ja paransi kaikki jotka olivat joutuneet\nPaholaisen valtaan, sillä Jumala oli hänen kanssaan.',85,NULL),
 (2251,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',39,'9Tämän me voimme vahvistaa todeksi, kaiken tämän,\nminkä hän teki Juudean maassa ja Jerusalemissa. Hänet\nhe ripustivat ristinpuulle ja surmasivat,',85,NULL),
 (2252,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',40,'0mutta\nJumala herätti hänet kolmantena päivänä ja antoi hänen\nilmestyä --',85,NULL),
 (2253,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',41,'1ei kaikelle kansalle, vaan\ntodistajille, jotka Jumala oli ennalta valinnut,\nmeille, jotka söimme ja joimme hänen kanssaan sen\njälkeen kun hän oli noussut kuolleista.',85,NULL),
 (2254,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',42,'2Hän käski\nmeidän saarnata kansalle ja todistaa, että juuri hänet\nJumala on asettanut elävien ja kuolleiden tuomariksi.',85,NULL),
 (2255,'Anonymous','2008-10-17 16:33:05','Anonymous','2008-10-17 16:33:05',43,'3Hänestä kaikki profeetat todistavat, että\njokainen, joka uskoo häneen, saa hänen nimensä\nvoimasta syntinsä anteeksi.\" ',85,NULL),
 (2256,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',44,'4Pietarin vielä puhuessa laskeutui Pyhä Henki\nkaikkiin, jotka olivat hänen sanojaan kuulemassa.',85,NULL),
 (2257,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',45,'5Kaikki Pietarin mukana tulleet juutalaiset\nuskonveljet ihmettelivät sitä, että Pyhän Hengen lahja\nvuodatettiin myös pakanoihin;',85,NULL),
 (2258,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',46,'6he kuulivat, kuinka\nnämä puhuivat kielillä ja ylistivät Jumalan suuruutta.\nSilloin Pietari sanoi:',85,NULL),
 (2259,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',47,'7\"Kuka voi estää kastamasta\nvedellä näitä, jotka ovat saaneet Pyhän Hengen niin\nkuin mekin?\"',85,NULL),
 (2260,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',48,'8Hän käski kastaa heidät Jeesuksen\nKristuksen nimeen. He pyysivät sitten häntä jäämään\nsinne vielä muutamaksi päiväksi. ',85,NULL),
 (2261,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',1,'Apostolit ja eri puolilla Juudeaa olevat veljet\nsaivat kuulla, että muutkin kuin juutalaiset olivat\nottaneet vastaan Jumalan sanan.',86,NULL),
 (2262,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',2,'Kun Pietari sitten\ntuli Jerusalemiin, ympärileikatut veljet syyttelivät\nhäntä',86,NULL),
 (2263,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',3,'siitä, että hän oli vieraillut\nympärileikkaamattomien luona ja aterioinut heidän\nkanssaan. ',86,NULL),
 (2264,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',4,'Silloin Pietari kertoi heille alusta alkaen, kuinka\nkaikki oli tapahtunut: ',86,NULL),
 (2265,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',5,'\"Minä olin Joppen kaupungissa ja rukoilin. Silloin\njouduin hurmoksiin ja näin näyn. Taivaasta tuli alas,\naivan minun eteeni, ikään kuin suuri purjekangas\nneljästä kulmastaan kannateltuna.',86,NULL),
 (2266,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',6,'Kun jäin\nkatsomaan sitä, näin siinä kaikenlaisia maan eläimiä,\nnelijalkaisia, petoja ja matelijoita, sekä taivaan\nlintuja.',86,NULL),
 (2267,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',7,'Ja minä kuulin äänen, joka sanoi: \'Nouse,\nPietari! Teurasta ja syö!\'',86,NULL),
 (2268,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',8,'Minä vastasin: \'Ei, ei,\nHerra! En ole koskaan pannut suuhuni mitään\nepäpuhdasta tai kiellettyä.\'',86,NULL),
 (2269,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',9,'Silloin ääni taas\npuhui taivaasta: \'Minkä Jumala on puhdistanut, sitä\nälä sinä sano epäpuhtaaksi.\'',86,NULL),
 (2270,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',10,'0Tämä tapahtui\nkolmesti. Sitten kaikki temmattiin takaisin\ntaivaaseen. ',86,NULL),
 (2271,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',11,'1\"Juuri silloin seisahtui kolme miestä sen talon\neteen, jossa me olimme. Heidät oli Kesareasta\nlähetetty minun luokseni.',86,NULL),
 (2272,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',12,'2Henki sanoi minulle,\nettä minun oli epäröimättä lähdettävä heidän mukaansa.\nMyös nämä kuusi veljeä lähtivät kanssani, ja niin me\nmenimme sen miehen kotiin.',86,NULL),
 (2273,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',13,'3Hän kertoi meille\nnähneensä talossaan enkelin. Enkeli oli sanonut\nhänelle: \'Lähetä miehiä Joppeen noutamaan tänne Simon,\ntoiselta nimeltä Pietari.',86,NULL),
 (2274,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',14,'4Hän puhuu sinulle, ja\nniiden sanojen voimasta sinä pelastut, sinä ja koko\nperhekuntasi.\'',86,NULL),
 (2275,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',15,'5Ja kun aloin puhua, laskeutui Pyhä\nHenki heihin niin kuin alussa meihin.',86,NULL),
 (2276,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',16,'6Silloin\nmuistin, mitä Herra oli sanonut: \'Johannes kastoi\nvedellä, mutta teidät kastetaan Pyhällä Hengellä.\'',86,NULL),
 (2277,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',17,'7Jos siis Jumala silloin, kun heistä tuli Herraan\nJeesukseen uskovia, antoi heille samanlaisen lahjan\nkuin meille, mikä olin minä estämään Jumalaa?\" ',86,NULL),
 (2278,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',18,'8Tämän kuultuaan kaikki rauhoittuivat ja ylistivät\nJumalaa sanoen: \"Jumala on siis avannut pakanoillekin\ntien kääntymykseen ja elämään.\"   ',86,NULL),
 (2279,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',22,'2Tästä kantautui tieto Jerusalemin seurakunnalle,\nja seurakunta lähetti Barnabaksen Antiokiaan.',86,NULL),
 (2280,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',23,'3Kun\nhän perille tultuaan näki, mitä Jumalan armo oli\nsaanut aikaan, hän ilahtui ja kehotti kaikkia\njärkkymättä pysymään uskossa Herraan.',86,NULL),
 (2281,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',24,'4Hän oli hyvä\nmies, täynnä Pyhää Henkeä ja uskoa. Yhä enemmän oli\nniitä, jotka liittyivät Herran omien joukkoon. ',86,NULL),
 (2282,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',25,'5Barnabas lähti sitten Tarsokseen etsimään Saulia',86,NULL),
 (2283,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',26,'6ja löydettyään toi hänet Antiokiaan. Yhdessä he\ntoimivat seurakunnan keskuudessa kokonaisen vuoden, ja\nsuuri joukko ihmisiä sai heiltä opetusta. Antiokiassa\nalettiin ensimmäiseksi käyttää opetuslapsista\nnimitystä kristitty. ',86,NULL),
 (2284,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',27,'7Tuohon aikaan tuli Jerusalemista profeettoja\nAntiokiaan.',86,NULL),
 (2285,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',28,'8Muuan heistä, nimeltään Agabos,\nilmoitti Hengen vaikutuksesta, että koko maailmaan oli\ntuleva suuri nälänhätä. Näin tapahtuikin Claudiuksen\naikana.',86,NULL),
 (2286,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',29,'9Opetuslapset päättivät antaa avustusta\nJuudeassa asuville veljille, kukin varojensa mukaan.',86,NULL),
 (2287,'Anonymous','2008-10-17 16:33:06','Anonymous','2008-10-17 16:33:06',30,'0Niin he tekivätkin ja lähettivät Barnabaksen ja\nSaulin viemään rahat seurakunnan vanhimmille. ',86,NULL),
 (2288,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',1,'Noihin aikoihin kuningas Herodes*ryhtyi\nväkivaltaisiin toimiin muutamia seurakunnan jäseniä\nvastaan.',87,NULL),
 (2289,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',2,'Hän mestautti Jaakobin, Johanneksen\nveljen,',87,NULL),
 (2290,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',3,'ja huomattuaan sen olevan juutalaisille\nmieleen hän vielä käski vangita Pietarin; silloin\nvietettiin juuri happamattoman leivän juhlaa.*',87,NULL),
 (2291,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',4,'Herodes pani Pietarin telkien taakse ja määräsi\nhäntä vartioimaan neljä nelimiehistä\nsotilasvartiostoa. Hän aikoi asettaa Pietarin\npääsiäisen*jälkeen kansan eteen.',87,NULL),
 (2292,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',5,'Niin siis\nPietaria pidettiin vangittuna, mutta seurakunta\nrukoili lakkaamatta Jumalaa hänen puolestaan.   ',87,NULL),
 (2293,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',9,'Pietari lähti enkelin mukaan. Hän ei kuitenkaan\nymmärtänyt, että se, mitä hänelle tapahtui, oli totta,\nvaan luuli sitä näyksi.',87,NULL),
 (2294,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',10,'0He ohittivat ensimmäisen\nvartion, sitten toisen ja tulivat rautaportille, joka\nvei kaupungille. Se aukeni heille itsestään, ja niin\nhe astuivat ulos ja lähtivät kulkemaan kujaa pitkin.\nYhtäkkiä enkeli oli poissa.',87,NULL),
 (2295,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',11,'1Silloin Pietari\ntajusi, mistä oli kysymys, ja sanoi: \"Totisesti, nyt\ntiedän, mitä tapahtui! Herra lähetti enkelinsä ja\nauttoi minut Herodeksen käsistä, pelasti minut\nkaikesta siitä, mitä tämä kansa toivoi minulle\ntapahtuvan.\" ',87,NULL),
 (2296,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',12,'2Kun tämä oli valjennut Pietarille, hän lähti kohti\nJohanneksen äidin Marian taloa, sen Johanneksen, jota\nkutsutaan Markukseksi. Siellä oli paljon väkeä\nrukoilemassa.',87,NULL),
 (2297,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',13,'3Pietari kolkutti talon portille, ja\nRode-niminen palvelustyttö tuli avaamaan.',87,NULL),
 (2298,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',14,'4Tunnettuaan Pietarin äänen tyttö kuitenkin ilahtui\nniin, että portti jäi häneltä avaamatta, kun hän\nryntäsi sisään kertomaan, että Pietari seisoi\nportilla.',87,NULL),
 (2299,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',15,'5\"Oletko järjiltäsi?\" sanoivat toiset.\nTytön yhä inttäessä he sanoivat: \"Se on hänen\nenkelinsä.\"',87,NULL),
 (2300,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',16,'6Mutta Pietari kolkutti\nkolkuttamistaan. Lopulta he menivät avaamaan ja\nsäikähtivät, kun näkivät hänet.',87,NULL),
 (2301,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',17,'7Pietari viittasi\nkädellään heitä olemaan hiljaa ja kertoi, kuinka Herra\noli tuonut hänet ulos vankilasta. Hän pyysi heitä\nilmoittamaan Jaakobille ja muille veljille, mitä oli\ntapahtunut, ja sitten hän lähti pois ja meni toiseen\npaikkaan. ',87,NULL),
 (2302,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',18,'8Aamulla sotilaiden keskuudessa syntyi tavaton\nhämminki. Mitä oli tapahtunut Pietarille?',87,NULL),
 (2303,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',19,'9Kun\nHerodes etsinnöistä huolimatta ei saanut häntä\nkäsiinsä, hän kuulusteli vartijoita ja käski teloittaa\nheidät. Sen jälkeen hän lähti Juudeasta ja siirtyi\nKesareaan.   ',87,NULL),
 (2304,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',24,'4Mutta Jumalan sana sai yhä vankemman jalansijan ja\nlevisi leviämistään.',87,NULL),
 (2305,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',25,'5Kun Barnabas ja Saul olivat\ntoimittaneet avustuksen perille, he lähtivät\nJerusalemista paluumatkalle ja ottivat mukaansa\nJohanneksen, jota kutsuttiin myös Markukseksi. ',87,NULL),
 (2306,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',1,'Antiokian seurakunnassa oli profeettoja ja\nopettajia: Barnabas ja Simeon, josta käytettiin nimeä\nNiger, kyreneläinen Lukios, Menahem, joka oli\nneljännesruhtinas Herodeksen kasvinkumppani, sekä\nSaul.',88,NULL),
 (2307,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',2,'Kerran, kun he olivat palvelemassa Herraa ja\npaastoamassa, Pyhä Henki sanoi: \"Erottakaa Barnabas ja\nSaul minun työhöni, siihen tehtävään, johon minä olen\nheidät kutsunut.\"',88,NULL),
 (2308,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',3,'Niin he paastosivat ja\nrukoilivat, ja sitten he panivat kätensä näiden kahden\npäälle ja lähettivät heidät matkaan.   ',88,NULL),
 (2309,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',6,'He kulkivat sitten koko saaren halki Pafokseen\nsaakka. Siellä he tapasivat Barjesus-nimisen\njuutalaisen miehen, joka oli noita ja väärä profeetta.',88,NULL),
 (2310,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',7,'Tämä oleskeli saaren käskynhaltijan Sergius\nPauluksen luona, joka oli viisas mies. Käskynhaltija\nkutsui Barnabaksen ja Saulin luokseen ja halusi kuulla\nJumalan sanaa.',88,NULL),
 (2311,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',8,'Mutta tuo tietäjä -- sitä hänen\ntoinen nimensä Elymas tarkoittaa -- vastusti heitä ja\nyritti estää käskynhaltijaa tulemasta uskoon.',88,NULL),
 (2312,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',9,'Silloin Saul -- eli Paavali -- täynnä Pyhää Henkeä\nkatsoi mieheen tiukasti',88,NULL),
 (2313,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',10,'0ja sanoi: \"Sinä Paholaisen\nsikiö, vilpin ja petoksen pesä, sinä kaiken hyvän ja\noikean vihollinen! Etkö jo lakkaa vääristämästä Herran\nsuoria teitä!',88,NULL),
 (2314,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',11,'1Nyt Herra nostaa kätensä sinua\nvastaan. Sinä tulet sokeaksi etkä näe aurinkoa ennen\nkuin määrä on.\" Siinä samassa noidan ylle laskeutui\nsynkkä pimeys, ja hän asteli haparoiden sinne tänne ja\netsi taluttajaa.',88,NULL),
 (2315,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',12,'2Kun käskynhaltija näki, mitä\ntapahtui, hän ihmetteli Herran opin voimaa ja uskoi.   ',88,NULL),
 (2316,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',16,'6Silloin Paavali nousi, viittasi kädellään ja alkoi\npuhua: \"Kuulkaa minua, israelilaiset, ja te muut, jotka\npelkäätte Israelin Jumalaa!',88,NULL),
 (2317,'Anonymous','2008-10-17 16:33:07','Anonymous','2008-10-17 16:33:07',17,'7Jumala valitsi isämme,\nja kun he olivat Egyptissä, vieraassa maassa, hän teki\nheistä suuren kansan ja johdatti heidät väkevällä\nkädellään sieltä pois.',88,NULL),
 (2318,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',18,'8Neljänkymmenen vuoden ajan\nhän autiomaassa sieti kansansa niskurointia,',88,NULL),
 (2319,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',19,'9ja\nhän hävitti Kanaanin maasta seitsemän kansaa ja jakoi\nnäiden maat israelilaisille perintömaaksi.',88,NULL),
 (2320,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',20,'0Tähän\nkaikkeen kului noin neljäsataaviisikymmentä vuotta.\nSen jälkeen hän antoi heille tuomareita profeetta\nSamuelin aikaan saakka.',88,NULL),
 (2321,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',21,'1Kun he sitten pyysivät\nitselleen kuningasta, Jumala antoi heille Saulin,\nKisin pojan, Benjaminin heimosta, ja Saul hallitsi\nneljäkymmentä vuotta.',88,NULL),
 (2322,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',22,'2Sitten Jumala pani hänet\nviralta ja nosti heille kuninkaaksi Daavidin.\nDaavidista hän myös antoi todistuksen: \'Minä olen\nlöytänyt Daavidin, Iisain pojan, mieleni mukaisen\nmiehen. Hän on kaikessa toteuttava minun tahtoni.\' ',88,NULL),
 (2323,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',23,'3\"Daavidin jälkeläisistä Jumala on lupauksensa\nmukaan antanut Israelille Pelastajan, Jeesuksen.',88,NULL),
 (2324,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',24,'4Hänen edellään kulki Johannes, joka julisti\nIsraelin kansalle, että kaikkien tuli kääntyä ja ottaa\nkaste.',88,NULL),
 (2325,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',25,'5Kun Johannes oli matkansa päässä, hän\nsanoi: \'En minä ole se, joksi te minua luulette. Minun\njälkeeni tulee toinen, jonka kenkiäkään minä en kelpaa\nriisumaan.\' ',88,NULL),
 (2326,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',26,'6\"Veljet, Abrahamin jälkeläiset, ja te muut, jotka\npelkäätte Jumalaa! Juuri meille on lähetetty sanoma\ntästä Pelastajasta.',88,NULL),
 (2327,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',27,'7Jerusalemilaiset ja heidän\nhallitusmiehensä eivät tajunneet, kuka Jeesus oli. He\ntuomitsivat hänet, mutta näin he panivat täytäntöön\nprofeettojen sanat, joita joka sapattina luetaan.',88,NULL),
 (2328,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',28,'8Mitään syytä kuolemantuomioon he eivät voineet\nesittää, mutta silti he vaativat Pilatusta\nteloittamaan hänet.',88,NULL),
 (2329,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',29,'9Kun he olivat toteuttaneet\nkaiken, mitä hänestä oli kirjoitettu, he ottivat hänet\nristinpuulta ja panivat hautaan.',88,NULL),
 (2330,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',30,'0Mutta Jumala\nherätti hänet kuolleista.',88,NULL),
 (2331,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',31,'1Hän ilmestyi sitten\nusean päivän aikana niille, jotka yhdessä hänen\nkanssaan olivat tulleet Galileasta Jerusalemiin. Nämä\novat nyt hänen todistajiaan, he todistavat hänestä\nIsraelin kansalle. ',88,NULL),
 (2332,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',32,'2\"Me julistamme teille ilosanoman: Minkä Jumala\nlupasi meidän isillemme,',88,NULL),
 (2333,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',33,'3sen hän on täyttänyt\nmeille, heidän lapsilleen. Hän on herättänyt Jeesuksen\nkuolleista, niin kuin toisessa psalmissa sanotaan:  ',88,NULL),
 (2334,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',34,'4Ja kun Jumala on herättänyt Jeesuksen kuolleista,\nJeesus ei enää palaa katoavaisuuteen. Tästä Jumala on\nsanonut:  ',88,NULL),
 (2335,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',35,'5Hänhän sanoo toisessa kohden:   ',88,NULL),
 (2336,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',36,'6\"Palveltuaan omana aikanaan Jumalan suunnitelmaa\nDaavid nukkui pois; hänet vietiin isiensä viereen ja\nhänen ruumiinsa maatui.',88,NULL),
 (2337,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',37,'7Mutta hän, jonka Jumala\nherätti kuolleista, ei maatunut.',88,NULL),
 (2338,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',38,'8--',88,NULL),
 (2339,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',39,'9Tietäkää\nsiis, veljet: hänen ansiostaan teille julistetaan\nsynnit anteeksi, ja hänen ansiostaan jokainen, joka\nuskoo, tulee vanhurskaaksi, saavuttaa kaiken sen, mitä\nte Mooseksen lain avulla ette voineet saavuttaa.',88,NULL),
 (2340,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',40,'0Pitäkää siis varanne, ettei teidän osaksenne tule\nse, mistä profeetat ovat sanoneet:   ',88,NULL),
 (2341,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',42,'2Kun Paavali ja Barnabas lähtivät synagogasta,\nheitä pyydettiin puhumaan näistä asioista\nseuraavanakin sapattina.',88,NULL),
 (2342,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',43,'3Väen hajaantuessa omille\ntahoilleen monet juutalaiset ja jumalaapelkäävät\nkäännynnäiset liittyivät Paavalin ja Barnabaksen\nseuraan. Nämä puhuivat heille ja rohkaisivat heitä\npitämään kiinni Jumalan armosta. ',88,NULL),
 (2343,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',44,'4Seuraavana sapattina melkein koko kaupunki\nkerääntyi kuulemaan Herran sanaa.',88,NULL),
 (2344,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',45,'5Kun juutalaiset\nnäkivät väenpaljouden, kiihko valtasi heidät ja he\nkiistivät herjaten sen, mitä Paavali puhui.',88,NULL),
 (2345,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',46,'6Silloin Paavali ja Barnabas sanoivat heille suorat\nsanat: \"Kaikkein ensimmäiseksi Jumalan sana oli\njulistettava teille. Mutta koska te torjutte sen\nettekä pidä itseänne iankaikkisen elämän arvoisina, me\nkäännymme pakanoiden puoleen.',88,NULL),
 (2346,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',47,'7Herra on näet\nantanut meille tämän käskyn:   ',88,NULL),
 (2347,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',48,'8Tämän kuullessaan pakanat iloitsivat ja ylistivät\nHerran sanaa, ja kaikki ne, jotka oli säädetty\niankaikkiseen elämään, tulivat uskoon.',88,NULL),
 (2348,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',49,'9Herran sana\nlevisi yli koko maakunnan.',88,NULL),
 (2349,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',50,'0Mutta juutalaiset\nyllyttivät jumalaapelkääviä ylhäisiä naisia ja\nkaupungin johtomiehiä Paavalia ja Barnabasta vastaan,\nniin että heitä alettiin vainota ja heidät\nkarkotettiin siltä seudulta.',88,NULL),
 (2350,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',51,'1Silloin Paavali ja\nBarnabas todisteeksi heitä vastaan pudistivat\nkaupungin pölyt jaloistaan ja siirtyivät Ikonioniin.',88,NULL),
 (2351,'Anonymous','2008-10-17 16:33:08','Anonymous','2008-10-17 16:33:08',52,'2Mutta opetuslapset olivat täynnä iloa ja Pyhää\nHenkeä. ',88,NULL),
 (2352,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',1,'Ikonionissa Paavali ja Barnabas menivät samalla\ntavoin juutalaisten synagogaan puhumaan, ja heidän\npuheensa johdosta suuri joukko juutalaisia ja\nkreikkalaisia tuli uskoon.',89,NULL),
 (2353,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',2,'Mutta ne juutalaiset,\njotka eivät uskoneet, lietsoivat suuttumusta muiden\nkaupunkilaisten keskuudessa ja yllyttivät heitä veljiä\nvastaan.',89,NULL),
 (2354,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',3,'Siitä huolimatta apostolit viipyivät\nkaupungissa pitkähkön aikaa. He puhuivat rohkeasti,\nturvaten Herraan, ja Herra vahvisti armonsa sanan\ntunnusteoin ja ihmein, jotka tapahtuivat heidän\nkättensä kautta. ',89,NULL),
 (2355,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',4,'Kaupungin väki jakautui kahtia: toiset olivat\njuutalaisten, toiset apostolien puolella.',89,NULL),
 (2356,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',5,'Juutalaiset johtomiehineen, mukanaan suuri joukko\nkaupunkilaisia, aikoivat jo käydä apostolien kimppuun\nja kivittää heidät,',89,NULL),
 (2357,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',6,'mutta kun nämä huomasivat, mitä\noli tekeillä, he pakenivat Lykaonian puolelle. Lystran\nja Derben kaupungeissa ja lähiseuduilla',89,NULL),
 (2358,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',7,'he sitten\njulistivat evankeliumia.   ',89,NULL),
 (2359,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',11,'1Kun ihmiset näkivät, mitä Paavali oli tehnyt, he\nhuusivat lykaonian kielellä: \"Jumalat ovat\nihmishahmossa tulleet alas meidän luoksemme!\"',89,NULL),
 (2360,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',12,'2He\nkutsuivat Barnabasta Zeukseksi, Paavalia taas\nHermekseksi, koska hän oli heistä kahdesta se, joka\npuhui.',89,NULL),
 (2361,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',13,'3Kaupungin ulkopuolella olevan Zeuksen\ntemppelin pappi toi härkiä ja seppeleitä portille\ntoimittaakseen uhrin yhdessä väkijoukon kanssa. ',89,NULL),
 (2362,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',14,'4Mutta kun apostolit, Barnabas ja Paavali, kuulivat\ntästä, he repäisivät vaatteensa ja ryntäsivät\nväkijoukon keskelle huutaen:',89,NULL),
 (2363,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',15,'5\"Mitä te oikein\nteette? Ihmisiä me vain olemme, samanlaisia kuin te.\nMe julistamme teille hyvää sanomaa ja kehotamme teitä\nluopumaan näistä tyhjänpäiväisistä jumalista ja\nkääntymään elävän Jumalan puoleen, hänen, joka on\nluonut taivaan ja maan ja meren ja kaiken, mitä niissä\non.',89,NULL),
 (2364,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',16,'6Menneiden sukupolvien aikana hän on sallinut\nkaikkien kansojen kulkea omia teitään,',89,NULL),
 (2365,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',17,'7mutta silti\nhän ei ole jättänyt antamatta todistusta itsestään.\nHän on tehnyt teille hyvää, hän on antanut vettä\ntaivaalta ja sadon ajallaan, hän on ravinnut teidät ja\ntäyttänyt teidät ilolla.\"',89,NULL),
 (2366,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',18,'8Näin puhuen he vaivoin\nsaivat kansan estetyksi uhraamasta heille. ',89,NULL),
 (2367,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',19,'9Antiokiasta ja Ikonionista tuli kuitenkin\njuutalaisia, jotka taivuttivat kansan puolelleen.\nPaavalia kivitettiin, ja hänet raahattiin kaupungin\nulkopuolelle, koska hänen luultiin kuolleen.',89,NULL),
 (2368,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',20,'0Mutta\nkun opetuslapset kerääntyivät hänen ympärilleen, hän\nnousi maasta ja palasi kaupunkiin. Seuraavana päivänä\nhän lähti Barnabaksen kanssa Derbeen.   ',89,NULL),
 (2369,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',24,'4Kuljettuaan sitten Pisidian halki Paavali ja\nBarnabas tulivat Pamfyliaan.',89,NULL),
 (2370,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',25,'5He julistivat sanaa\nPergessä ja jatkoivat matkaansa Attaliaan.',89,NULL),
 (2371,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',26,'6Sieltä\nhe purjehtivat Antiokiaan, missä heidät oli annettu\nJumalan armon haltuun siihen työhön, jonka he nyt\nolivat saattaneet päätökseen.',89,NULL),
 (2372,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',27,'7Perille saavuttuaan\nhe kutsuivat seurakunnan koolle ja kertoivat, että\nJumala oli heidän kauttaan tehnyt suuria tekoja ja\nettä hän oli muillekin kuin juutalaisille avannut\nuskon oven.',89,NULL),
 (2373,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',28,'8He viipyivät sitten jonkin aikaa\nopetuslasten keskuudessa. ',89,NULL),
 (2374,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',1,'Juudeasta tuli Antiokian seurakuntaan eräitä, jotka\nopettivat: \"Ellette anna ympärileikata itseänne, niin\nkuin Mooses on säätänyt, ette voi pelastua.\"',90,NULL),
 (2375,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',2,'Paavali ja Barnabas asettuivat vastustamaan heitä,\nja asiasta käytiin kiivasta väittelyä. Niinpä\npäätettiin, että Paavali ja Barnabas ja muutamat muut\nlähtisivät Jerusalemiin apostolien ja vanhimpien\npuheille, jotta saataisiin selvyys tästä\nkiistakysymyksestä.',90,NULL),
 (2376,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',3,'Seurakunnan varustettua heidät\nmatkalle he kulkivat Foinikian ja Samarian halki ja\nkertoivat veljille pakanoiden kääntymisestä. Kaikki\nilahtuivat suuresti kuulemastaan. ',90,NULL),
 (2377,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',4,'Kun he saapuivat Jerusalemiin, seurakunta,\napostolit ja vanhimmat ottivat heidät vastaan, ja he\nkertoivat niistä suurista teoista, joita Jumala heidän\nmatkallaan oli tehnyt.',90,NULL),
 (2378,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',5,'Silloin eräät uskoon tulleet\nfariseukset puuttuivat asiaan ja sanoivat, että\nkääntyneet pakanat oli ympärileikattava ja\nvelvoitettava noudattamaan Mooseksen lakia. ',90,NULL),
 (2379,'Anonymous','2008-10-17 16:33:09','Anonymous','2008-10-17 16:33:09',6,'Apostolit ja vanhimmat kokoontuivat käsittelemään\ntätä kysymystä.',90,NULL),
 (2380,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',7,'Kun väittelyä oli kestänyt pitkään,\nPietari nousi puhumaan ja sanoi: \"Veljet, te tiedätte,\nettä Jumala on jo aikoja sitten teidän keskuudessanne\ntehnyt valintansa: hän on antanut pakanoiden kuulla\nminun suustani evankeliumin ja tulla uskoon.',90,NULL),
 (2381,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',8,'Jumala, joka tuntee kaikkien sydämet, osoitti\nhyväksyvänsä heidät vuodattaessaan Pyhän Hengen heihin\nyhtä lailla kuin meihinkin.',90,NULL),
 (2382,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',9,'Hän ei tehnyt mitään\neroa heidän ja meidän välillämme, vaan puhdisti heidän\nsydämensä uskolla.',90,NULL),
 (2383,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',10,'0Miksi te siis nyt uhmaatte\nJumalaa ja panette opetuslasten harteille ikeen, jota\neivät meidän isämme emmekä me itse ole jaksaneet\nkantaa?',90,NULL),
 (2384,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',11,'1Mehän uskomme, että meidät pelastaa yksin\nHerran Jeesuksen armo, samalla tavoin kuin heidät.\" ',90,NULL),
 (2385,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',12,'2Koko joukko vaikeni. Kaikki kuuntelivat Barnabasta\nja Paavalia, kun nämä kertoivat, miten suuria\ntunnustekoja ja ihmeitä Jumala oli kansojen\nkeskuudessa tehnyt heidän kättensä kautta.',90,NULL),
 (2386,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',13,'3Kun he\nolivat lopettaneet, alkoi Jaakob puhua. Hän sanoi:\n\"Veljet, kuulkaa mitä sanon.',90,NULL),
 (2387,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',14,'4Simon kertoi, kuinka\nJumala ensi kerran lähestyi pakanoita ottaakseen\nheistä itselleen oman kansan.',90,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (2388,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',15,'5Tämä käy yksiin sen\nkanssa, mitä profeetat ovat kirjoituksissa sanoneet:  ',90,NULL),
 (2389,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',19,'9Tästä syystä ei mielestäni pidä hankaloittaa\nniiden elämää, jotka vieraista kansoista kääntyvät\nJumalan puoleen.',90,NULL),
 (2390,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',20,'0Meidän on kuitenkin lähetettävä\nheille kirje ja kehotettava heitä karttamaan\nsellaista, minkä epäjumalanpalvelus on saastuttanut,\nsekä haureutta*, verta ja lihaa, josta verta ei ole\nlaskettu.',90,NULL),
 (2391,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',21,'1Onhan Mooseksen laki jo ikimuistoisista\najoista tullut tunnetuksi joka kaupungissa, kun sitä\nluetaan synagogissa jokaisena sapattina.\"   \"Apostolien ja vanhimpien veljellinen tervehdys\npakanuudesta kääntyneille Antiokian, Syyrian ja\nKilikian veljille.',90,NULL),
 (2392,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',24,'4Me olemme kuulleet, että\nmuutamat, jotka ovat tulleet meidän luotamme mutta\njoita me emme ole lähettäneet, ovat puheillaan\njärkyttäneet teitä ja saattaneet teidät hämmennyksiin.',90,NULL),
 (2393,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',25,'5Siksi olemme yksimielisesti päättäneet lähettää\nedustajamme teidän luoksenne. He tulevat yhdessä\nrakkaiden veljiemme Barnabaksen ja Paavalin kanssa,',90,NULL),
 (2394,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',26,'6jotka ovat panneet henkensä alttiiksi meidän\nHerramme Jeesuksen Kristuksen nimen tähden.',90,NULL),
 (2395,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',27,'7Lähetämme siis Juudaksen ja Silaksen, ja he tuovat\ntämän saman viestin myös suullisesti.',90,NULL),
 (2396,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',28,'8Pyhä Henki\nja me olemme nähneet hyväksi, ettei teidän\nkannettavaksenne pidä panna mitään taakkaa. Annamme\nainoastaan nämä välttämättömät ohjeet:',90,NULL),
 (2397,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',29,'9karttakaa\nepäjumalille uhrattua lihaa, samoin verta ja sellaista\nlihaa, josta ei ole verta laskettu, sekä haureutta.\nKun näitä vältätte, kaikki on kohdallaan. Voikaa\nhyvin.\" ',90,NULL),
 (2398,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',30,'0Näin heidät lähetettiin matkaan. Antiokiaan\nsaavuttuaan he kutsuivat seurakunnan koolle ja\nantoivat kirjeen veljille.',90,NULL),
 (2399,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',31,'1Kirje luettiin, ja sen\nrohkaisevat sanat ilahduttivat kaikkia.',90,NULL),
 (2400,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',32,'2Juudas ja\nSilas, jotka hekin olivat profeettoja, puhuivat\nveljille monin sanoin rohkaisten ja vahvistaen heitä.',90,NULL),
 (2401,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',33,'3He viipyivät kaupungissa jonkin aikaa ja lähtivät\nsitten rauhantoivotusten saattelemina veljien luota\nniiden luo, jotka olivat heidät lähettäneet.\n[',90,NULL),
 (2402,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',34,'4Silas kuitenkin päätti jäädä heidän luokseen, ja\nniin Juudas lähti yksin.]*',90,NULL),
 (2403,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',35,'5Paavali ja Barnabas sen\nsijaan jäivät vielä Antiokiaan. Yhdessä monien muiden\nkanssa he opettivat ja julistivat Herran sanaa.    ',90,NULL),
 (2404,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',1,'Paavali saapui sitten Derbeen ja Lystraan.\nLystrassa oli Timoteus-niminen opetuslapsi, jonka äiti\noli kristitty juutalainen mutta isä kreikkalainen.',91,NULL),
 (2405,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',2,'Hän oli hyvässä maineessa Lystran ja Ikonionin\nveljien keskuudessa.',91,NULL),
 (2406,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',3,'Paavali halusi ottaa\nTimoteuksen matkalle mukaan, ja siksi hän\nympärileikkasi tämän. Näin hän teki niiden seutujen\njuutalaisten tähden, sillä kaikki tiesivät, että\nTimoteuksen isä oli kreikkalainen. ',91,NULL),
 (2407,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',4,'He kulkivat sitten kaupungista kaupunkiin ja\nilmoittivat samalla uskoville apostolien ja vanhimpien\nJerusalemissa tekemistä päätöksistä velvoittaen heidät\nnoudattamaan niitä.',91,NULL),
 (2408,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',5,'Seurakunnat vahvistuivat\nuskossa ja kasvoivat päivä päivältä.   ',91,NULL),
 (2409,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',9,'Yöllä Paavali näki näyn. Makedonialainen mies\nseisoi hänen edessään ja pyysi: \"Tule meren yli tänne\nMakedoniaan ja auta meitä.\"',91,NULL),
 (2410,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',10,'0Kun Paavali oli nähnyt\ntämän näyn, me hankkiuduimme heti lähtemään\nMakedoniaan, sillä ymmärsimme, että Jumala oli\nkutsunut meidät julistamaan evankeliumia siellä.   ',91,NULL),
 (2411,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',13,'3Sapattina menimme kaupungin ulkopuolelle joen\nrantaan; arvelimme, että siellä olisi rukouspaikka.\nIstuuduimme sinne ja puhuimme naisille, joita siellä\noli koolla.',91,NULL),
 (2412,'Anonymous','2008-10-17 16:33:10','Anonymous','2008-10-17 16:33:10',14,'4Meitä kuuntelemassa oli myös muuan\nLyydia, jumalaapelkäävä nainen, joka oli kotoisin\nTyatirasta ja kävi kauppaa purppurakankailla. Herra\navasi hänen sydämensä ottamaan vastaan sen mitä\nPaavali puhui,',91,NULL),
 (2413,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',15,'5ja hänet ja hänen perhekuntansa\nkastettiin. Hän sanoi sitten meille: \"Jos kerran\npidätte minua Herraan uskovana, tulkaa kotiini ja\njääkää vieraikseni.\" Hän vaatimalla vaati meitä\ntulemaan.   ',91,NULL),
 (2414,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',19,'9Mutta kun tytön isännät huomasivat, ettei heillä\nenää ollut toivoa tuloista, he ottivat Paavalin ja\nSilaksen kiinni ja raahasivat heidät torille\nviranomaisten luo.',91,NULL),
 (2415,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',20,'0Kaupungin hallitusmiesten\nedessä he sanoivat: \"Nämä miehet aiheuttavat\nlevottomuutta kaupungissamme. He ovat juutalaisia',91,NULL),
 (2416,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',21,'1ja opettavat tapoja, joita meidän ei ole lupa\nomaksua eikä noudattaa, koska olemme roomalaisia.\"',91,NULL),
 (2417,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',22,'2Kansakin nousi heitä vastaan. Hallitusmiehet\nkäskivät repiä heiltä vaatteet päältä ja ruoskittivat\nheidät.',91,NULL),
 (2418,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',23,'3Kun heidät oli perin pohjin piesty, heidät\nteljettiin vankilaan. Vartija sai määräyksen valvoa\nheitä tarkoin,',91,NULL),
 (2419,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',24,'4ja niinpä hän saamansa käskyn\nmukaisesti vei heidät vankilan perimmäiseen soppeen ja\nvielä varmuudeksi pani heidät jalkapuuhun. ',91,NULL),
 (2420,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',25,'5Keskiyön aikaan Paavali ja Silas rukoilivat ja\nlaulaen ylistivät Jumalaa, ja toiset vangit\nkuuntelivat heitä.',91,NULL),
 (2421,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',26,'6Yhtäkkiä tuli raju\nmaanjäristys. Vankilan perustukset järkkyivät, kaikki\novet lennähtivät auki, ja kaikkien kahleet irtosivat.',91,NULL),
 (2422,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',27,'7Vartija havahtui unestaan, ja kun hän näki\nvankilan ovet selkoselällään, hän luuli vankien\nkaranneen ja tempasi miekan surmatakseen itsensä.',91,NULL),
 (2423,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',28,'8Silloin Paavali huusi kovalla äänellä: \"Älä tee\nitsellesi mitään! Me olemme kaikki täällä.\" ',91,NULL),
 (2424,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',29,'9Vartija pyysi valoa, ryntäsi sisään ja heittäytyi\nvavisten Paavalin ja Silaksen eteen.',91,NULL),
 (2425,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',30,'0Hän vei\nheidät ulos ja kysyi: \"Herrat! Mitä minun on tehtävä,\nettä pelastuisin?\"',91,NULL),
 (2426,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',31,'1He vastasivat: \"Usko Herraan\nJeesukseen, niin pelastut, sinä ja sinun\nperhekuntasi.\"',91,NULL),
 (2427,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',32,'2He puhuivat sitten Herran sanaa\nhänelle ja koko talonväelle.',91,NULL),
 (2428,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',33,'3Heti paikalla,\nkeskellä yötä, vanginvartija otti heidät hoiviinsa ja\npesi ruoskaniskujen haavat, ja hänet ja hänen\nperhekuntansa kastettiin heti.',91,NULL),
 (2429,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',34,'4Hän vei heidät\nkotiinsa ja kattoi heille ruokapöydän, ja yhdessä koko\ntalonväkensä kanssa hän riemuitsi, kun nyt uskoi\nJumalaan. ',91,NULL),
 (2430,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',35,'5Aamulla hallitusmiehet lähettivät\noikeudenpalvelijoita tuomaan sanan: \"Päästä ne miehet\nvapaaksi.\"',91,NULL),
 (2431,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',36,'6Vartija ilmoitti tämän Paavalille. Hän\nsanoi: \"Hallitusmiehet ovat lähettäneet sanan, että\nteidät on päästettävä vapaaksi. Lähtekää siis, menkää\nrauhassa.\"',91,NULL),
 (2432,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',37,'7Mutta Paavali vastasi\noikeudenpalvelijoille: \"He ovat tutkimatta ja\ntuomitsematta julkisesti ruoskittaneet meidät, Rooman\nkansalaiset, ja heittäneet meidät vankilaan. Nytkö he\nvähin äänin toimittaisivat meidät täältä pois? Ei,\ntulkoot itse päästämään meidät täältä.\"',91,NULL),
 (2433,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',38,'8Oikeudenpalvelijat veivät Paavalin vastauksen\nhallitusmiehille, ja nämä säikähtivät, kun kuulivat\nmiesten olevan Rooman kansalaisia.',91,NULL),
 (2434,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',39,'9He tulivat\nvankilaan, puhuivat suostutellen ja saatettuaan miehet\nulos pyysivät heitä lähtemään kaupungista. ',91,NULL),
 (2435,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',40,'0Vankilasta lähdettyään Paavali ja Silas menivät\nLyydian luo. Tavattuaan siellä uskonveljiä he\nrohkaisivat näitä, ja sitten he lähtivät kaupungista. ',91,NULL),
 (2436,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',1,'Amfipoliin ja Apollonian kautta kuljettuaan he\ntulivat Tessalonikaan. Juutalaisilla oli siellä\nsynagoga,',92,NULL),
 (2437,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',2,'ja tapansa mukaan Paavali meni sinne.\nKolmena sapattina hän keskusteli heidän kanssaan\npyhien kirjoitusten pohjalta',92,NULL),
 (2438,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',3,'ja selitti ne heille.\nHän osoitti, että Messiaan täytyi kärsiä ja nousta\nkuolleista, ja sanoi: \"Juuri Jeesus, jota minä teille\njulistan, on tämä Messias, Kristus.\" ',92,NULL),
 (2439,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',4,'Muutamat juutalaiset vakuuttuivat asiasta ja\nasettuivat kannattamaan Paavalia ja Silasta, samoin\nsuuri joukko jumalaapelkääviä kreikkalaisia sekä useat\nylhäiset naiset.',92,NULL),
 (2440,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',5,'Tämä sai juutalaiset kateuden ja\nkiihkon valtaan. He värväsivät torilla maleksivia\nkelvottomia miehiä, haalivat koolle kansaa ja\nnostattivat kaupungissa mellakan. Sitten he ryntäsivät\nJasonin talolle ja yrittivät saada Paavalin ja\nSilaksen käsiinsä viedäkseen heidät kansan eteen.',92,NULL),
 (2441,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',6,'Kun näitä ei löytynyt, he raahasivat Jasonin ja\njoitakin muita veljiä kaupungin viranomaisten luo ja\nhuusivat: \"Nyt ne koko maailman villitsijät ovat\ntulleet tännekin,',92,NULL),
 (2442,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',7,'ja Jason on ottanut heidät\nkotiinsa. Kaikki he rikkovat keisarin säädöksiä ja\npitävät kuninkaana toista miestä, erästä Jeesusta.\"',92,NULL),
 (2443,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',8,'Näin puhuen he saivat kansanjoukon ja viranomaiset\nkiihdyksiin.',92,NULL),
 (2444,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',9,'Jasonilta ja hänen tovereiltaan\nvaadittiin takuusumma, ja sitten heidät päästettiin\nvapaaksi.     ',92,NULL),
 (2445,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',22,'2Paavali astui keskelle Areiopagia ja alkoi puhua: \"Ateenalaiset! Kaikesta näkee, että te tarkoin pidätte\nhuolta jumalien palvonnasta.',92,NULL),
 (2446,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',23,'3Kun kiertelin\nkaupungilla ja katselin teidän pyhiä paikkojanne,\nlöysin sellaisenkin alttarin, jossa oli kirjoitus:\n\'Tuntemattomalle jumalalle.\' Juuri sitä, mitä te\ntuntemattanne palvotte, minä teille julistan. ',92,NULL),
 (2447,'Anonymous','2008-10-17 16:33:11','Anonymous','2008-10-17 16:33:11',24,'4\"Jumala, joka on luonut maailman ja kaiken, mitä\nsiinä on, hän, joka on taivaan ja maan Herra, ei asu\nihmiskäsin tehdyissä temppeleissä.',92,NULL),
 (2448,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',25,'5Häntä ei\nmyöskään palvella ihmiskäsin, ikään kuin hän\ntarvitsisi jotakin -- itse hän antaa kaikille elämän,\nhengen ja kaiken muun.',92,NULL),
 (2449,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',26,'6Yhdestä ihmisestä hän on\nluonut koko ihmissuvun, kaikki kansat asumaan eri\npuolilla maan päällä, hän on säätänyt niille määräajat\nja asuma-alueiden rajat,',92,NULL),
 (2450,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',27,'7jotta ihmiset etsisivät\nJumalaa ja kenties hapuillen löytäisivät hänet. \"Jumala ei kylläkään ole kaukana yhdestäkään meistä:',92,NULL),
 (2451,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',28,'8hänessä me elämme, liikumme ja olemme. Ovathan\nmuutamat teidän runoilijannekin sanoneet: \'Me olemme\nmyös hänen sukuaan.\'',92,NULL),
 (2452,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',29,'9Koska me siis olemme Jumalan\nsukua, meidän ei pidä luulla, että jumaluus olisi\nsamankaltainen kuin kulta, hopea tai kivi, kuin\nihmisen mielikuvituksen ja taidon luomus. ',92,NULL),
 (2453,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',30,'0\"Tällaista tietämättömyyttä Jumala on pitkään\nsietänyt, mutta nyt sen aika on ohi: hän vaatii\nkaikkia ihmisiä kaikkialla tekemään parannuksen.',92,NULL),
 (2454,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',31,'1Hän on näet määrännyt päivän, jona hän\noikeudenmukaisesti tuomitsee koko maailman, ja\ntuomarina on oleva mies, jonka hän on siihen tehtävään\nasettanut. Siitä hän on antanut kaikille takeet\nherättämällä hänet kuolleista.\" ',92,NULL),
 (2455,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',32,'2Jotkut ivailivat Paavalia, kun kuulivat hänen\npuhuvan kuolleiden ylösnousemuksesta, toiset taas\nsanoivat: \"Kenties saamme kuulla sinulta tästä vielä\ntoistekin.\"',92,NULL),
 (2456,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',33,'3Niin Paavali lähti heidän luotaan.',92,NULL),
 (2457,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',34,'4Muutamat kuitenkin hakeutuivat hänen seuraansa ja\ntulivat uskoon. Heidän joukossaan oli Dionysios,\nAreiopagin tuomioistuimen jäsen, Damaris-niminen\nnainen sekä muutamia muita. ',92,NULL),
 (2458,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',1,'Paavali lähti sitten Ateenasta ja meni Korinttiin.',93,NULL),
 (2459,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',2,'Siellä hän tapasi Aquilan, Pontoksesta kotoisin\nolevan juutalaisen, ja tämän vaimon Priscillan. He\nolivat äskettäin tulleet Italiasta, koska Claudius oli\nkarkottanut Roomasta kaikki juutalaiset. Paavali meni\nheidän luokseen',93,NULL),
 (2460,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',3,'ja jäi työskentelemään heidän\nkanssaan, sillä heillä oli yhteinen ammatti; he olivat\nteltantekijöitä.',93,NULL),
 (2461,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',4,'Joka sapattina Paavali kävi\nkeskusteluja synagogassa ja koetti taivuttaa uskoon\nsekä juutalaisia että kreikkalaisia. ',93,NULL),
 (2462,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',5,'Kun Silas ja Timoteus saapuivat Makedoniasta,\nPaavali oli jo kokonaan antautunut julistamaan sanaa.\nHän todisti juutalaisille, että Jeesus on Messias,',93,NULL),
 (2463,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',6,'mutta juutalaiset väittivät herjaten vastaan.\nSilloin hän pudisti pölyn vaatteistaan ja sanoi:\n\"Tulkoon teidän verenne teidän itsenne päälle! Minä en\nenää ole teistä vastuussa. Tästedes puhun pakanoille.\"',93,NULL),
 (2464,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',7,'Hän lähti heidän luotaan ja asettui Titius Justus -\nnimisen jumalaapelkäävän miehen taloon, joka oli aivan\nsynagogan vieressä.',93,NULL),
 (2465,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',8,'Kuitenkin synagogan esimies\nCrispus tuli uskoon, ja myös koko hänen perhekuntansa\nuskoi Herraan. Monet muutkin korinttilaiset, jotka\nkuuntelivat Paavalia, tulivat uskoon, ja heidät\nkastettiin. ',93,NULL),
 (2466,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',9,'Eräänä yönä Herra sanoi näyssä Paavalille: \"Älä\npelkää, vaan puhu edelleen, älä vaikene.',93,NULL),
 (2467,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',10,'0Minä olen\nsinun kanssasi. Kukaan ei käy sinun kimppuusi eikä tee\nsinulle pahaa. Tässä kaupungissa on paljon minun\nkansaani.\"',93,NULL),
 (2468,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',11,'1Niin Paavali viipyi puolitoista vuotta\nkorinttilaisten keskuudessa ja opetti heille Jumalan\nsanaa. ',93,NULL),
 (2469,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',12,'2Mutta sitten, Gallion ollessa Akhaian maakunnan\nkäskynhaltijana, juutalaiset nousivat yksissä tuumin\nPaavalia vastaan. He veivät hänet käskynhaltijan\ntuomioistuimen eteen',93,NULL),
 (2470,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',13,'3ja sanoivat: \"Tämä mies\nhoukuttelee ihmisiä palvelemaan Jumalaa\nlainvastaisella tavalla.\"',93,NULL),
 (2471,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',14,'4Mutta ennen kuin Paavali\nehti vastata, Gallio sanoi: \"Juutalaiset! Jos olisi\nkyseessä rikos tai törkeä ilkityö, olisi oikein ja\nkohtuullista, että kärsivällisesti kuuntelisin teitä.',93,NULL),
 (2472,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',15,'5Mutta kun te riitelette sanoista ja nimistä ja\nomasta laistanne, se on teidän asianne. Siinä minä en\nrupea tuomariksi.\"',93,NULL),
 (2473,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',16,'6Hän ajoi heidät pois\ntuomioistuimen edestä',93,NULL),
 (2474,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',17,'7eikä välittänyt vähääkään,\nkun kansanjoukko kävi käsiksi Sosteneeseen, synagogan\nesimieheen, ja pieksi hänet avoimesti oikeuspaikalla.     ',93,NULL),
 (2475,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',1,'Apolloksen ollessa Korintissa Paavali vaelsi\nylänköseutujen halki ja tuli Efesokseen. Hän tapasi\nsiellä muutamia opetuslapsia',94,NULL),
 (2476,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',2,'ja kysyi heiltä:\n\"Saitteko Pyhän Hengen, kun tulitte uskoon?\" \"Emme me\nole kuulleetkaan mistään Pyhästä Hengestä\", nämä\nvastasivat.',94,NULL),
 (2477,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',3,'\"Millä kasteella teidät sitten on\nkastettu?\" kysyi Paavali. He vastasivat: \"Johanneksen\nkasteella.\"',94,NULL),
 (2478,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',4,'Silloin Paavali sanoi: \"Johannes tosin\nkastoi vedellä parannukseen, mutta hän kehotti ihmisiä\nuskomaan toiseen, joka oli tuleva hänen jälkeensä,\nJeesukseen.\"',94,NULL),
 (2479,'Anonymous','2008-10-17 16:33:12','Anonymous','2008-10-17 16:33:12',5,'Tämän kuultuaan he ottivat kasteen\nHerran Jeesuksen nimeen,',94,NULL),
 (2480,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',6,'ja kun Paavali pani\nkätensä heidän päälleen, Pyhä Henki tuli heihin ja he\npuhuivat kielillä ja profetoivat.',94,NULL),
 (2481,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',7,'Heitä oli\narviolta kaksitoista miestä. ',94,NULL),
 (2482,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',8,'Kolmen kuukauden ajan Paavali sitten kävi\nsynagogassa, keskusteli siellä ja puhui rohkeasti ja\nvetoavasti Jumalan valtakunnasta.',94,NULL),
 (2483,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',9,'Kun muutamat\nkuitenkin kovettivat sydämensä eivätkä uskoneet, vaan\npuhuivat Herran tiestä pahaa kaikkien kuullen, Paavali\nlähti heidän luotaan ja vei opetuslapset mukanaan.\nSiitä alkaen hän opetti ja keskusteli päivittäin\nTyrannoksen koulussa.',94,NULL),
 (2484,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',10,'0Tätä jatkui kaksi vuotta,\nniin että kaikki Aasian maakunnan asukkaat, sekä\njuutalaiset että kreikkalaiset, saivat kuulla Herran\nsanan.   ',94,NULL),
 (2485,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',13,'3Myös jotkut kiertelevät juutalaiset\nhenkienmanaajat alkoivat loitsiessaan lausua Herran\nJeesuksen nimeä. Karkottaessaan sairaista pahoja\nhenkiä he sanoivat: \"Minä vannotan teitä sen Jeesuksen\nnimessä, jota Paavali julistaa.\"',94,NULL),
 (2486,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',14,'4Näin tekivät myös\njuutalaisen ylipapin Skeuaksen seitsemän poikaa,',94,NULL),
 (2487,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',15,'5mutta paha henki vastasi heille: \"Jeesuksen minä\ntunnen ja Paavalinkin tiedän, mutta keitä te olette?\"',94,NULL),
 (2488,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',16,'6Ja mies, jossa paha henki oli, ryntäsi heidän\nkimppuunsa, nujersi heidät kaikki ja runteli heitä\nniin, että heidän oli alasti ja verissä päin paettava\ntalosta. ',94,NULL),
 (2489,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',17,'7Tämä tuli Efesoksessa sekä juutalaisten että\nkreikkalaisten tietoon, ja pelko valtasi kaikki ja\nHerran Jeesuksen nimeä ylistettiin suuresti.',94,NULL),
 (2490,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',18,'8Monet\nuskoon tulleet kävivät avoimesti tunnustamassa, mitä\nkaikkea olivat tehneet,',94,NULL),
 (2491,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',19,'9ja useat, jotka olivat\nharjoittaneet taikuutta, kokosivat kirjansa yhteen ja\npolttivat ne kaikkien nähden. Kun kirjojen arvo\nlaskettiin, päädyttiin viiteenkymmeneentuhanteen\nhopearahaan.',94,NULL),
 (2492,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',20,'0Näin Herran sana osoitti voimansa ja\nlevisi yhä laajemmalle. ',94,NULL),
 (2493,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',21,'1Näiden tapahtumien jälkeen Paavali Hengen\njohdatuksesta päätti matkata Makedonian ja Akhaian\nkautta Jerusalemiin ja sanoi: \"Siellä käytyäni minun\non mentävä myös Roomaan.\"',94,NULL),
 (2494,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',22,'2Kaksi apulaistaan,\nTimoteuksen ja Erastoksen, hän lähetti Makedoniaan,\nmutta itse hän jäi vielä joksikin aikaa Aasian\nmaakuntaan.   ',94,NULL),
 (2495,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',28,'8Tämän kuullessaan miehet vimmastuivat ja huusivat:\n\"Suuri on Efesoksen Artemis!\"',94,NULL),
 (2496,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',29,'9Koko kaupunki joutui\nkuohuksiin. Väkeä tuli virtanaan kaupungin teatteriin,\nja ihmiset riistivät mukaansa Gaioksen ja\nAristarkoksen, Paavalin makedonialaiset matkatoverit.',94,NULL),
 (2497,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',30,'0Paavali halusi mennä kokouspaikalle, mutta\nopetuslapset estivät häntä.',94,NULL),
 (2498,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',31,'1Myös muutamat\nmaakunnan korkeat hallitusmiehet, jotka olivat hänen\nystäviään, lähettivät hänelle sanan ja varoittivat\nhäntä menemästä teatteriin. ',94,NULL),
 (2499,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',32,'2Teatterissa vallitsi täysi sekasorto. Toiset\nhuusivat sitä, toiset tätä, eivätkä useimmat edes\ntienneet, miksi oli kokoonnuttu.',94,NULL),
 (2500,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',33,'3Sitten väkijoukon\nhuomio kohdistui Aleksandrokseen, jota juutalaiset\ntyönsivät esiin. Aleksandros viittasi kädellään\npitääkseen puolustuspuheen.',94,NULL),
 (2501,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',34,'4Mutta kun huomattiin,\nettä hän oli juutalainen, kaikki alkoivat yhteen\nääneen huutaa: \"Suuri on Efesoksen Artemis!\" Tätä\nkesti parin tunnin ajan. ',94,NULL),
 (2502,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',35,'5Lopulta kaupungin sihteeri sai joukon\nrauhoittumaan. \"Efesolaiset!\" hän huusi. \"Kaikkihan\ntietävät, että Efesoksen kaupunki on pantu vaalimaan\nsuuren Artemiin temppeliä ja hänen taivaasta\npudonnutta kuvaansa.',94,NULL),
 (2503,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',36,'6Tätä ei kukaan voi kiistää.\nPysykää siis rauhallisina, älkää tehkö mitään\nharkitsematonta.',94,NULL),
 (2504,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',37,'7Nämä miehet, jotka te toitte\ntänne, eivät ole temppelinryöstäjiä eivätkä liioin ole\nherjanneet meidän jumalatartamme.',94,NULL),
 (2505,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',38,'8Jos Demetrios ja\nhänen ammattitoverinsa haluavat nostaa syytteen\njotakuta vastaan, niin sitä varten on käskynhaltijat\nja oikeusistuimet -- syyttäkööt siellä toisiaan.',94,NULL),
 (2506,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',39,'9Jos teillä taas on joitakin muita vaatimuksia, ne\nkäsitellään laillisessa kansankokouksessa.',94,NULL),
 (2507,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',40,'0Tästä\ntämänpäiväisestä voi meille olla seurauksena\nkapinasyyte, ja meidän olisi vaikea löytää selitystä\ntälle mellakalle.\" Näin puhuen hän sai kokouksen\nhajaantumaan.  ',94,NULL),
 (2508,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',1,'Mellakan laannuttua Paavali kutsui opetuslapset\nkoolle ja puhui heille rohkaisevasti. Sitten hän\nhyvästeli heidät ja lähti kohti Makedoniaa.',95,NULL),
 (2509,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',2,'Matkansa varrella hän kaikkialla rohkaisi\nuskonveljiä. Saavuttuaan Kreikkaan',95,NULL),
 (2510,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',3,'hän viipyi\nsiellä kolme kuukautta. Kun hän oli aikeissa jatkaa\nmatkaansa meritse Syyriaan, hän sai kuulla\njuutalaisten salahankkeesta ja päättikin kiertää\nMakedonian kautta.',95,NULL),
 (2511,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',4,'Hänen mukanaan lähtivät\nberoialainen Sopatros, Pyrrhoksen poika,\ntessalonikalaiset Aristarkos ja Sekundos, derbeläinen\nGaios, Timoteus ja aasialaiset Tykikos ja Trofimos.',95,NULL),
 (2512,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',5,'Nämä menivät edeltä ja odottivat meitä Troaksessa.',95,NULL),
 (2513,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',6,'Me muut purjehdimme happamattoman leivän juhlan\njälkeen Filippistä, ja viisi päivää matkattuamme\nsaavuimme Troakseen heidän luokseen. Viivyimme sitten\nsiellä viikon ajan.       \"Te tiedätte, millä tavoin minä kaiken aikaa, siitä\npäivästä alkaen jona tänne Aasiaan tulin, olen\ntoiminut teidän keskuudessanne.',95,NULL),
 (2514,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',19,'9Olen palvellut\nHerraa nöyrin mielin, kyyneleet silmissä, kaikissa\nkoettelemuksissa, joita juutalaiset juonillaan ovat\nminulle aiheuttaneet.',95,NULL),
 (2515,'Anonymous','2008-10-17 16:33:13','Anonymous','2008-10-17 16:33:13',20,'0En ole vaiennut mistään\nsellaisesta, mikä on teille hyödyksi, vaan olen\njulistanut sanaa ja opettanut teitä sekä julkisesti\nettä kodeissanne.',95,NULL),
 (2516,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',21,'1Sekä juutalaisia että\nkreikkalaisia olen todistuksellani taivuttanut\nkääntymään Jumalan puoleen ja uskomaan meidän\nHerraamme Jeesukseen. ',95,NULL),
 (2517,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',22,'2\"Nyt on Pyhä Henki sitonut minut ja vie minua\nJerusalemiin, enkä tiedä, mitä siellä on osakseni\ntuleva.',95,NULL),
 (2518,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',23,'3Tämän vain tiedän: joka kaupungissa Pyhä\nHenki vakuuttaa, että minua odottavat kahleet ja\nahdinko.',95,NULL),
 (2519,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',24,'4Eloon jäämiseni ei minulle kuitenkaan\nmerkitse mitään sen rinnalla, että pääsen matkani\npäähän ja saatan loppuun Herralta Jeesukselta saamani\npalvelutehtävän: julistaa evankeliumia Jumalan\narmosta. ',95,NULL),
 (2520,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',25,'5\"Nyt tiedän, että te ette enää näe minua, ei\nkukaan teistä, joiden keskuudessa olen elänyt ja\njulistanut Jumalan valtakuntaa.',95,NULL),
 (2521,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',26,'6Siksi minä tänä\npäivänä vakuutan teille, että jos joku teistä joutuu\nkadotukseen, syy ei ole minun:',95,NULL),
 (2522,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',27,'7minä olen avoimesti\njulistanut teille kaiken, mitä Jumalan\npelastussuunnitelmaan kuuluu. ',95,NULL),
 (2523,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',28,'8\"Pitäkää huoli itsestänne ja koko laumasta, jonka\nkaitsijoiksi Pyhä Henki on teidät pannut; huolehtikaa\nseurakunnasta, jonka Herra omalla verellään on\nitselleen lunastanut.*',95,NULL),
 (2524,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',29,'9Minä tiedän, että lähtöni\njälkeen teidän joukkoonne tulee julmia susia, jotka\neivät laumaa säästä.',95,NULL),
 (2525,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',30,'0Teidän omasta joukostanne\nnousee miehiä, jotka julistavat totuudenvastaisia\noppeja vetääkseen opetuslapset mukaansa.',95,NULL),
 (2526,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',31,'1Olkaa\nsiis valveilla ja muistakaa, että minä kolmen vuoden\najan olen lakkaamatta, yötä päivää kyynelsilmin\nopastanut itse kutakin teistä. ',95,NULL),
 (2527,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',32,'2\"Jätän nyt teidät Jumalan ja hänen armonsa sanan\nhaltuun, sen sanan, jossa on voima rakentaa teitä ja\nantaa perintöosa kaikkien pyhitettyjen joukossa.',95,NULL),
 (2528,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',33,'3Hopeaa tai kultaa tai vaatteita en ole keneltäkään\nhalunnut.',95,NULL),
 (2529,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',34,'4Te tiedätte itse, että minä näillä\nkäsilläni olen hankkinut itselleni ja tovereilleni\nsen, mitä olemme tarvinneet.',95,NULL),
 (2530,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',35,'5Kaikin tavoin olen\nteille osoittanut, että näin, työtä tehden, tulee\nhuolehtia vähäosaisista muistaen Herran Jeesuksen omat\nsanat: \'Autuaampi on antaa kuin ottaa.\'\" ',95,NULL),
 (2531,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',36,'6Näin puhuttuaan Paavali polvistui ja rukoili\nyhdessä toisten kanssa.',95,NULL),
 (2532,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',37,'7Kaikki itkivät ja\nsyleilivät ja suutelivat häntä.',95,NULL),
 (2533,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',38,'8Kaikkein suurinta\ntuskaa he tunsivat siitä, etteivät he, niin kuin\nPaavali oli sanonut, enää näkisi häntä. He saattoivat\nsitten hänet laivaan. ',95,NULL),
 (2534,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',1,'Niin siis erosimme heistä. Lähdimme merelle ja\npurjehdimme suoraan Kosin saareen, seuraavana päivänä\nRodokseen ja sieltä Pataraan.',96,NULL),
 (2535,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',2,'Patarassa löysimme\nlaivan, joka oli lähdössä Foinikiaan, ja jatkoimme\nsiinä matkaamme.',96,NULL),
 (2536,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',3,'Kun Kypros tuli näkyviin,\nsuuntasimme kulkumme sen eteläpuolitse Syyriaa kohti,\nja niin saavuimme Tyrokseen, missä laivan oli määrä\npurkaa lastinsa.',96,NULL),
 (2537,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',4,'Tapasimme sikäläiset opetuslapset\nja viivyimme heidän luonaan viikon ajan. Hengen\nvaikutuksesta he varoittivat Paavalia menemästä\nJerusalemiin.',96,NULL),
 (2538,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',5,'Kun viikko oli kulunut, lähdimme taas\nmatkaan, ja kaikki veljet vaimoineen ja lapsineen\nsaattoivat meitä kaupungin ulkopuolelle saakka. Meren\nrannalla me yhdessä polvistuimme ja rukoilimme,',96,NULL),
 (2539,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',6,'ja\nsitten jätimme toisillemme hyvästit. Me nousimme\nlaivaan, ja he palasivat kotiinsa. ',96,NULL),
 (2540,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',7,'Tyroksesta lähdettyämme purjehdimme Ptolemaikseen;\nsinne merimatkamme päättyi. Menimme tervehtimään\nveljiä ja viivyimme päivän heidän luonaan.',96,NULL),
 (2541,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',8,'Seuraavana päivänä lähdimme liikkeelle, ja niin\ntulimme Kesareaan. Siellä menimme evankelista\nFilippoksen luo ja majoituimme hänen kotiinsa. Hän oli\nyksi seitsemästä avustajasta,',96,NULL),
 (2542,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',9,'ja hänellä oli neljä\nnaimatonta tytärtä, joilla oli profetoimisen lahja. ',96,NULL),
 (2543,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',10,'0Olimme olleet kaupungissa jo useita päiviä, kun\nsinne saapui Juudeasta Agabos-niminen profeetta.',96,NULL),
 (2544,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',11,'1Hän tuli luoksemme, otti Paavalilta vyön, sitoi\nsillä jalkansa ja kätensä ja julisti: \"Näin sanoo Pyhä\nHenki: tällä tavoin juutalaiset Jerusalemissa sitovat\nsen miehen, jonka vyö tämä on, ja luovuttavat hänet\npakanoiden käsiin.\"',96,NULL),
 (2545,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',12,'2Tämän kuultuamme me yhdessä\nKesarean uskovien kanssa pyysimme, ettei Paavali\nmenisi Jerusalemiin.',96,NULL),
 (2546,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',13,'3Mutta Paavali sanoi: \"Miksi\nte tuollaista puhutte, miksi itkette ja raastatte\nminun sydäntäni? Minä olen valmis kahleisiin ja\nkuolemaankin Jerusalemissa Herran Jeesuksen nimen\ntähden.\"',96,NULL),
 (2547,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',14,'4Kun hän ei antanut periksi, me\nrauhoituimme ja sanoimme: \"Toteutukoon Herran tahto.\"   ',96,NULL),
 (2548,'Anonymous','2008-10-17 16:33:14','Anonymous','2008-10-17 16:33:14',17,'7Kun saavuimme Jerusalemiin, veljet ottivat meidät\niloiten vastaan.',96,NULL),
 (2549,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',18,'8Heti seuraavana päivänä Paavali\nlähti meidän kanssamme tapaamaan Jaakobia, ja kaikki\nvanhimmatkin kokoontuivat paikalle.',96,NULL),
 (2550,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',19,'9Paavali\ntervehti heitä ja kertoi seikkaperäisesti kaikesta,\nmitä Jumala hänen palvelutyönsä kautta oli tehnyt\npakanoiden parissa. ',96,NULL),
 (2551,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',20,'0Sen kuultuaan vanhimmat ylistivät Jumalaa, mutta\nsitten he sanoivat Paavalille: \"Sinä tiedät, veli, että juutalaisia on tuhansittain\ntullut uskoon, ja he kaikki vaativat kiivaasti lain\nnoudattamista.',96,NULL),
 (2552,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',21,'1Heille on sinusta kerrottu, että\nsinä opetat muiden kansojen keskuudessa eläviä\njuutalaisia hylkäämään Mooseksen lain ja kiellät heitä\nympärileikkaamasta lapsiaan ja elämästä kansamme\ntapojen mukaisesti.',96,NULL),
 (2553,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',22,'2Mitä siis tehdä? Varmastikin\nhe saavat kuulla, että olet täällä.* ',96,NULL),
 (2554,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',23,'3\"Tee neuvomme mukaan: Täällä on neljä miestä,\njotka ovat antaneet uhrilupauksen.',96,NULL),
 (2555,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',24,'4Ota heidät\nmukaasi, puhdistaudu yhdessä heidän kanssaan ja maksa\nheidän puolestaan, että he voivat leikkauttaa\ntukkansa. Siitä kaikki ymmärtävät, että sinusta\nlevitetyissä puheissa ei ole mitään perää vaan sinäkin\npidät kiinni laista ja elät sen mukaisesti.',96,NULL),
 (2556,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',25,'5Uskoon\ntulleiden pakanoiden asian me jo olemme ratkaisseet.\nMe olemme kirjeitse ilmoittaneet heille, että heidän\ntulee karttaa epäjumalille uhrattua lihaa, samoin\nverta ja myös lihaa, josta ei ole verta laskettu, sekä\nhaureutta.\" ',96,NULL),
 (2557,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',26,'6Paavali otti miehet mukaansa ja puhdistautui\nseuraavana päivänä heidän kanssaan. Sitten hän meni\ntemppeliin ja ilmoitti, milloin heidän\npuhdistumisaikansa päättyisi ja kunkin puolesta\nsuoritettaisiin uhri.   ',96,NULL),
 (2558,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',31,'1Väkijoukko olisi tappanut Paavalin, mutta silloin\nroomalaisen sotaväenosaston komentaja sai tiedon, että\nkoko Jerusalem kuohui.',96,NULL),
 (2559,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',32,'2Hän otti kiireesti\nsadanpäälliköitä ja sotamiehiä mukaansa ja riensi\npaikalle. Kun juutalaiset näkivät komentajan ja\nsotilaat, he lakkasivat lyömästä Paavalia.',96,NULL),
 (2560,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',33,'3Komentaja astui lähemmäs, pidätti Paavalin ja\nkäski panna hänet kaksiin kahleisiin. Hän kysyi sitten\nihmisiltä, kuka mies oli ja mitä hän oli tehnyt.',96,NULL),
 (2561,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',34,'4Väkijoukosta huusivat toiset sitä, toiset tätä, ja\nkun komentaja ei meteliltä saanut varmaa selkoa\nasiasta, hän käski viedä Paavalin kasarmiin.',96,NULL),
 (2562,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',35,'5Kun\noli päästy portaille saakka, sotilaiden oli pakko\nkantaa häntä, sillä raivostunut väkijoukko',96,NULL),
 (2563,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',36,'6tunki\nheidän peräänsä ja huusi: \"Kuolema sille miehelle!\"   Hän alkoi puhua heille heprean kielellä: ',96,NULL),
 (2564,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',1,'\"Veljet ja isät! Kuulkaa, mitä nyt sanon\npuolustuksekseni.\" ',97,NULL),
 (2565,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',2,'Väkijoukko hiljeni entisestään kuullessaan hänen\npuhuvan hepreaksi, ja Paavali jatkoi: ',97,NULL),
 (2566,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',3,'\"Minä olen juutalainen. Olen syntynyt Kilikian\nTarsoksessa mutta kasvanut tässä kaupungissa.\nGamalielin oppilaana minut on kasvatettu isiemme lain\nkaikkien vaatimusten mukaan. Kiivaasti minä taistelin\nJumalan puolesta, juuri niin kuin te kaikki tänään\nteette.',97,NULL),
 (2567,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',4,'Minä olin tälle tielle lähteneiden\nverivihollinen ja vainosin heitä, pidätin miehiä ja\nnaisia ja toimitin heidät vankilaan.',97,NULL),
 (2568,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',5,'Tämän voi\nylipappi ja koko vanhimpien neuvosto todistaa. Heiltä\nminä myös sain valtuudet, Damaskoksen veljille\nosoitetut kirjeet, kun lähdin sinne vangitsemaan\nsiellä olevia kristittyjä ja tuomaan heidät\nJerusalemiin rangaistaviksi. ',97,NULL),
 (2569,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',6,'\"Mutta matkalla, kun jo olin lähellä Damaskosta,\ntaivaasta leimahti puolenpäivän aikaan yhtäkkiä kirkas\nvalo ympärilleni.',97,NULL),
 (2570,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',7,'Kaaduin maahan ja kuulin äänen\nsanovan: \'Saul, Saul, miksi vainoat minua?\'',97,NULL),
 (2571,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',8,'Minä\nkysyin: \'Herra, kuka sinä olet?\' Ääni vastasi: \'Minä\nolen Jeesus Nasaretilainen, jota sinä vainoat.\'',97,NULL),
 (2572,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',9,'Ne,\njotka olivat kanssani, näkivät valon mutta eivät\nkuulleet sen ääntä, joka minulle puhui.',97,NULL),
 (2573,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',10,'0Minä\nkysyin: \'Herra, mitä minun tulee tehdä?\' Herra sanoi\nminulle: \'Nouse ja mene Damaskokseen. Siellä saat\nkuulla kaiken, mitä sinun tehtäväksesi on määrätty.\'',97,NULL),
 (2574,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',11,'1Minä en nähnyt mitään, koska kirkas valo oli\nsokaissut minut, ja siksi matkatoverieni oli\ntalutettava minua. Niin sitten saavuin Damaskokseen. ',97,NULL),
 (2575,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',12,'2\"Siellä oli Ananias, hurskas ja lakia noudattava\nmies, jota kaikki kaupungin juutalaiset pitivät\narvossa.',97,NULL),
 (2576,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',13,'3Hän tuli luokseni ja sanoi: \'Saul,\nveljeni, palautukoon näkösi!\' Samassa näköni palasi ja\nnäin hänet.',97,NULL),
 (2577,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',14,'4Hän sanoi: \'Isiemme Jumala on valinnut\nsinut tuntemaan tahtonsa, näkemään Vanhurskaan ja\nkuulemaan hänen oman äänensä.',97,NULL),
 (2578,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',15,'5Sinusta tulee hänen\ntodistajansa, sinä kerrot kaikille ihmisille, mitä\nolet nähnyt ja kuullut.',97,NULL),
 (2579,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',16,'6Älä vitkastele. Huuda\navuksi hänen nimeään, nouse ja anna kastaa itsesi,\nanna pestä syntisi pois.\' ',97,NULL),
 (2580,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',17,'7\"Kun sitten olin palannut Jerusalemiin ja olin\ntemppelissä rukoilemassa, jouduin hurmoksiin',97,NULL),
 (2581,'Anonymous','2008-10-17 16:33:15','Anonymous','2008-10-17 16:33:15',18,'8ja\nnäin Herran. Hän sanoi minulle: \'Lähde heti\nJerusalemista, älä viivyttele. Täällä sinua ei\nkuunnella, kun todistat minusta.\'',97,NULL),
 (2582,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',19,'9Minä sanoin:\n\'Herra, hehän tietävät, että minä kuljin synagogasta\nsynagogaan ja vangitsin ja ruoskitin niitä, jotka\nuskovat sinuun.',97,NULL),
 (2583,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',20,'0Silloinkin, kun Stefanos, sinun\ntodistajasi, surmattiin, minä olin paikalla, hyväksyin\nkaiken ja vartioin hänen murhaajiensa vaatteita.\'',97,NULL),
 (2584,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',21,'1Mutta hän sanoi minulle: \'Mene, minä lähetän sinut\nkauas toisten kansojen keskuuteen.\'\" ',97,NULL),
 (2585,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',22,'2Tähän asti juutalaiset olivat kuunnelleet\nPaavalia, mutta nyt he alkoivat huutaa: \"Pois elävien\njoukosta! Tuollainen ei saa elää!\"',97,NULL),
 (2586,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',23,'3He huusivat\nhuutamistaan, raastoivat vaatteitaan ja heittelivät\nhiekkaa ilmaan.',97,NULL),
 (2587,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',24,'4Silloin komentaja käski viedä\nPaavalin kasarmiin. Hän käski ruoskia Paavalia ja\nkuulustella häntä, jotta saisi selville, miksi\njuutalaiset niin raivokkaasti hyökkäsivät häntä\nvastaan. ',97,NULL),
 (2588,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',25,'5Mutta kun miehet olivat sitoneet Paavalin\nruoskittavaksi, tämä sanoi vieressä seisovalle\nsadanpäällikölle: \"Onko teillä oikeus ruoskia Rooman\nkansalaista, vieläpä tutkimatta ja tuomitsematta?\"',97,NULL),
 (2589,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',26,'6Tämän kuultuaan sadanpäällikkö meni komentajan\nluo, ilmoitti asiasta ja sanoi: \"Mitä olet tekemässä?\nSe mies on Rooman kansalainen.\"',97,NULL),
 (2590,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',27,'7Komentaja tuli\npaikalle ja kysyi Paavalilta: \"Oletko todella Rooman\nkansalainen?\" Kun Paavali vastasi myöntävästi,',97,NULL),
 (2591,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',28,'8komentaja sanoi: \"Minä olen maksanut\nkansalaisuudestani suuret rahat.\" \"Minulla se on ollut\nsyntymästä saakka\", vastasi Paavali.',97,NULL),
 (2592,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',29,'9Ne, joiden\noli määrä kuulustella Paavalia, jättivät hänet heti\nrauhaan, ja komentaja pelästyi, kun havaitsi\nsidottaneensa Rooman kansalaisen.   ',97,NULL),
 (2593,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',1,'Paavali loi katseensa neuvostoon ja sanoi: \"Veljet!\nKoko elämäni, tähän päivään asti, minä olen elänyt\nJumalan edessä vilpittömästi, omaatuntoani\nloukkaamatta.\"',98,NULL),
 (2594,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',2,'Silloin ylipappi Ananias käski\nPaavalin vieressä seisovia miehiä iskemään häntä\nsuulle.',98,NULL),
 (2595,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',3,'Paavali sanoi hänelle: \"Sinä saat vielä\niskun Jumalan kädestä, sinä kalkilla valkaistu seinä!\nSinä istut oikeutta tuomitaksesi minut lain mukaan,\nmutta teet vastoin lakia ja käsket lyödä minua.\"',98,NULL),
 (2596,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',4,'Vieressä seisovat miehet sanoivat: \"Herjaatko sinä\nJumalan ylipappia?\"',98,NULL),
 (2597,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',5,'Tähän Paavali vastasi: \"En\ntiennyt, veljet, että hän on ylipappi. Onhan\nkirjoitettu: \'Älä kiroa kansasi päämiestä.\'\" ',98,NULL),
 (2598,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',6,'Osa neuvostosta oli saddukeuksia, osa fariseuksia,\nja tämän tietäen Paavali sanoi kuuluvalla äänellä:\n\"Veljet! Minä olen fariseus, ja fariseuksia ovat\nisänikin olleet. Nyt olen täällä tuomittavana siksi,\nettä panen toivoni kuolleiden ylösnousemukseen.\"',98,NULL),
 (2599,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',7,'Tuskin hän oli tämän sanonut, kun fariseusten ja\nsaddukeusten kesken puhkesi ankara kiista ja koko\njoukko jakautui kahtia.',98,NULL),
 (2600,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',8,'Saddukeukset näet\nväittävät, ettei mitään ylösnousemusta ole, ei\nmyöskään enkeleitä eikä henkiä, kun taas fariseukset\nuskovat näihin kaikkiin.',98,NULL),
 (2601,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',9,'Meteli kävi yhä\nkovemmaksi, ja fariseusten joukosta muutamat\nlainopettajat alkoivat jo väittää: \"Me emme voi\nhavaita tässä miehessä mitään pahaa. Jospa henki tai\nenkeli on puhunut hänelle.\"',98,NULL),
 (2602,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',10,'0Mellakka kiihtyi\nkiihtymistään, ja komentaja alkoi pelätä, että Paavali\nrevittäisiin kappaleiksi. Hän komensi sotaväenosaston\nhakemaan Paavalin pois neuvoston käsistä ja viemään\nhänet kasarmiin. ',98,NULL),
 (2603,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',11,'1Seuraavana yönä Herra seisoi Paavalin edessä ja\nsanoi: \"Pysy rohkeana. Sinä olet todistanut minusta\ntäällä Jerusalemissa, ja samalla tavoin sinun on\ntodistettava myös Roomassa.\"   ',98,NULL),
 (2604,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',16,'6Paavalin sisarenpoika sai kuitenkin tietää\nväijytyksestä. Hän meni kasarmiin, pääsi Paavalin\npuheille ja kertoi hänelle asiasta.',98,NULL),
 (2605,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',17,'7Paavali kutsui\nyhden sadanpäälliköistä luokseen ja sanoi: \"Vie tämä\nnuori mies komentajan luo. Hänellä on komentajalle\nviesti.\"',98,NULL),
 (2606,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',18,'8Sadanpäällikkö otti nuorukaisen mukaansa,\nvei hänet komentajan luo ja sanoi: \"Vanki Paavali\nkutsui minut luokseen ja pyysi tuomaan tämän nuoren\nmiehen sinun puheillesi. Hänellä on sinulle jotakin\nasiaa.\" ',98,NULL),
 (2607,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',19,'9Komentaja otti nuorukaista kädestä, vei hänet\nsyrjään ja kysyi: \"Mitä asiaa sinulla on minulle?\"',98,NULL),
 (2608,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',20,'0Tämä vastasi: \"Juutalaiset ovat sopineet, että he\npyytävät sinua tuomaan Paavalin huomenna Suuren\nneuvoston istuntoon, jotta muka voisivat ottaa\ntarkemmin selvää hänen asiastaan.',98,NULL),
 (2609,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',21,'1Älä suostu\nsiihen. Yli neljäkymmentä miestä heidän joukostaan on\njärjestänyt väijytyksen. He ovat vannoneet, etteivät\nsyö eivätkä juo, ennen kuin ovat surmanneet Paavalin.\nHe ovat valmiina ja odottavat vain, että annat\nsuostumuksesi.\"',98,NULL),
 (2610,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',22,'2Komentaja sanoi sisarenpojalle:\n\"Älä kerro kenellekään, että olet paljastanut hankkeen\nminulle.\" Sitten hän lähetti nuorukaisen pois.   ',98,NULL),
 (2611,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',25,'5Sitten komentaja kirjoitti maaherralle näin\nkuuluvan kirjeen:',98,NULL),
 (2612,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',26,'6\"Claudius Lysias tervehtii hänen\nkorkea-arvoisuuttaan maaherra Felixiä.',98,NULL),
 (2613,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',27,'7Tämän\nmiehen ottivat juutalaiset kiinni ja olivat tappaa\nhänet. Minä kuitenkin menin sotilaineni paikalle ja\npelastin hänet, kun sain tietää, että hän on Rooman\nkansalainen.',98,NULL),
 (2614,'Anonymous','2008-10-17 16:33:16','Anonymous','2008-10-17 16:33:16',28,'8Saadakseni selville, mistä\njuutalaiset häntä syyttivät, vein hänet heidän\nneuvostonsa eteen.',98,NULL),
 (2615,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',29,'9Siellä totesin, että syytteet\naiheutuivat heidän lakiaan koskevista\nkiistakysymyksistä. Mistään sellaisesta häntä ei\nsyytetty, mistä seuraisi kuolemantuomio tai\nvankeusrangaistus.',98,NULL),
 (2616,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',30,'0Koska tietooni on tullut, että\ntätä miestä vastaan on vireillä salahanke, lähetän\nhänet nyt viipymättä sinun luoksesi. Niille, jotka\nhäntä syyttävät, olen myös tehnyt tiettäväksi, että\nheidän on esitettävä syytteensä sinun edessäsi.\" ',98,NULL),
 (2617,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',31,'1Sotilaat tekivät niin kuin oli käsketty, ottivat\nPaavalin mukaansa ja veivät hänet yötä myöten\nAntipatrikseen.',98,NULL),
 (2618,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',32,'2Seuraavana päivänä lähtivät\nratsumiehet edelleen saattamaan Paavalia, kun taas\nmuut palasivat varuskuntaansa.',98,NULL),
 (2619,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',33,'3Ratsumiehet\nsaapuivat Kesareaan, toimittivat kirjeen maaherralle\nja veivät myös Paavalin hänen eteensä.',98,NULL),
 (2620,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',34,'4Maaherra\nluki kirjeen ja kysyi, mistä maakunnasta Paavali oli.\nKuultuaan, että Paavali oli Kilikiasta,',98,NULL),
 (2621,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',35,'5hän sanoi:\n\"Kuulustelen sinua sitten kun täällä ovat paikalla\nmyös ne, jotka sinua syyttävät.\" Hän antoi määräyksen,\nettä Paavalia oli pidettävä vartioituna Herodeksen\nlinnassa. ',98,NULL),
 (2622,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',1,'Viiden päivän kuluttua Kesareaan saapui ylipappi\nAnanias, muutamia kansan vanhimpia sekä asianajaja\nTertullus, ja maaherran edessä he esittivät syytteen\nPaavalia vastaan.',99,NULL),
 (2623,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',2,'Kun Paavali oli kutsuttu sisään,\nTertullus aloitti syytöspuheensa: \"Sinun ansiostasi,\nkorkeasti kunnioitettu Felix, olemme saaneet nauttia\nvakaasta rauhasta, ja sinun huolehtivassa\nhallinnossasi tämän kansan olot ovat suuresti\nparantuneet.',99,NULL),
 (2624,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',3,'Tämän me kiitollisina tunnustamme,\naina ja kaikin tavoin.',99,NULL),
 (2625,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',4,'En halua vaivata sinua\npitkään, pyydän vain, että hetken aikaa\nhyväntahtoisesti kuuntelisit meitä.',99,NULL),
 (2626,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',5,'Me olemme\nhavainneet, että tämä mies on tuhoisa kuin rutto. Hän\non nasaretilaisten lahkon johtaja, ja kaikkialla, joka\npuolella maailmaa hän on lietsomassa levottomuutta\njuutalaisten keskuudessa.',99,NULL),
 (2627,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',6,'Hän yritti saastuttaa\ntemppelinkin, mutta siitä teosta me otimme hänet\nkiinni.*',99,NULL),
 (2628,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',8,'Häntä kuulustelemalla voit itse päästä\nperille kaikesta siitä, mistä me häntä syytämme.\"',99,NULL),
 (2629,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',9,'Juutalaiset yhtyivät tähän syytökseen ja\nvakuuttivat, että asia oli niin.   \"Tiedän, että olet jo monet vuodet ollut tämän kansan\ntuomarina, ja siksi voin turvallisin mielin puolustaa\nitseäni.',99,NULL),
 (2630,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',11,'1Siitä, kun tulin Jerusalemiin\nrukoilemaan, ei ole enempää kuin kaksitoista päivää --\ntämän voit itse tarkistaa.',99,NULL),
 (2631,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',12,'2Minun ei ole havaittu\nväittelevän kenenkään kanssa eikä kiihottavan kansaa,\nei temppelissä, ei synagogissa eikä kaupungilla.',99,NULL),
 (2632,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',13,'3Liioin eivät nämä miehet voi edessäsi näyttää\ntoteen sitä, mistä he nyt minua syyttävät.',99,NULL),
 (2633,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',14,'4Mutta\nsen minä sinulle tunnustan, että kuljen sitä tietä,\njota he sanovat lahkoksi, ja näin palvelen isieni\nJumalaa: kaiken sen, mitä laissa ja profeettojen\nkirjoissa sanotaan, minä uskon,',99,NULL),
 (2634,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',15,'5ja yhtä lailla\nkuin nämä tässä minä toivon ja odotan, että Jumala\nherättää kuolleista niin hurskaat kuin jumalattomat.',99,NULL),
 (2635,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',16,'6Siksi teen kaikkeni, että minulla aina olisi\npuhdas omatunto Jumalan ja ihmisten edessä. ',99,NULL),
 (2636,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',17,'7\"Oltuani vuosikausia poissa tulin nyt tuomaan\nkansalleni rahalahjaa ja toimittamaan uhreja.',99,NULL),
 (2637,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',18,'8Juuri näissä tehtävissä minä temppelissä olin, kun\neräät Aasiasta tulleet juutalaiset tapasivat minut.\nOlin puhdistautunut, väkeä ei ollut koolla eikä\nmellakasta tietoakaan.',99,NULL),
 (2638,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',19,'9Noiden miesten pitäisi olla\nitse täällä sinun edessäsi minua syyttämässä, jos\nheillä on jotakin minua vastaan.',99,NULL),
 (2639,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',20,'0Tai sanokoot\nnämä, minkä rikkomuksen he havaitsivat silloin, kun\nseisoin neuvoston edessä.',99,NULL),
 (2640,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',21,'1Yhdestä lauseestako\ntässä on kysymys, siitä, että heidän edessään huusin:\n\'Kuolleiden ylösnousemuksen tähden minä tänään olen\nteidän tuomittavananne\'?\" ',99,NULL),
 (2641,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',22,'2Felix, joka oli tarkoin perillä Herran tiestä,\nsiirsi heidän oikeusjuttunsa tuonnemmaksi sanoen: \"Kun\nkomentaja Lysias tulee, otan asianne ratkaistavaksi.\"',99,NULL),
 (2642,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',23,'3Hän antoi sadanpäällikölle käskyn, että Paavalia\noli pidettävä vankilassa mutta hänelle oli suotava\nhelpotuksia eikä hänen ystäviään saanut estää\npitämästä hänestä huolta.   ',99,NULL),
 (2643,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',27,'7Kahden vuoden kuluttua Felixin tilalle tuli\nPorcius Festus, ja koska Felix halusi pysyä hyvissä\nväleissä juutalaisten kanssa, hän jätti Paavalin\nvankeuteen. ',99,NULL),
 (2644,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',1,'Maaherran virkaan astuttuaan Festus lähti kolmen\npäivän kuluttua Kesareasta Jerusalemiin.',100,NULL),
 (2645,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',2,'Ylipapit\nja juutalaisten johtomiehet esittivät hänelle\nsyytteensä Paavalia vastaan',100,NULL),
 (2646,'Anonymous','2008-10-17 16:33:17','Anonymous','2008-10-17 16:33:17',3,'ja pyysivät sitä\nsuosionosoitusta, että hän haettaisi Paavalin\nJerusalemiin. He näet suunnittelivat väijytystä\nsurmatakseen Paavalin matkalla.',100,NULL),
 (2647,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',4,'Festus kuitenkin\nvastasi, että Paavalia säilytettiin Kesareassa ja että\nhän itse aikoi kohta lähteä sinne.',100,NULL),
 (2648,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',5,'\"Tulkoot teidän\njohtajanne mukaan\", hän sanoi, \"ja syyttäkööt häntä\nsiellä, jos hän on jonkin rikoksen tehnyt.\" ',100,NULL),
 (2649,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',6,'Festus viipyi Jerusalemissa vain kymmenkunta päivää\nja palasi sitten Kesareaan. Heti seuraavana päivänä\nhän istuutui tuomarinistuimelleen ja käski tuoda\nPaavalin eteensä.',100,NULL),
 (2650,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',7,'Paavalin saavuttua kerääntyivät\nJerusalemista tulleet juutalaiset hänen ympärilleen ja\nahdistivat häntä monin raskain syytöksin. He eivät\nkuitenkaan kyenneet näyttämään syytöksiään toteen.',100,NULL),
 (2651,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',8,'Paavali kiisti ne sanoen: \"Mitään väärää en ole\ntehnyt, en juutalaisten lakia, en temppeliä enkä\nkeisaria vastaan.\" ',100,NULL),
 (2652,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',9,'Festus kuitenkin halusi päästä hyviin väleihin\njuutalaisten kanssa. Siksi hän kysyi Paavalilta:\n\"Tahdotko lähteä Jerusalemiin? Asiasi ratkaistaisiin\nsiellä minun johdollani.\"',100,NULL),
 (2653,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',10,'0Paavali vastasi: \"Minä\nseison nyt keisarin tuomioistuimen edessä, ja sen\nedessä minun asiani on ratkaistava. Juutalaisia\nvastaan en ole millään tavoin rikkonut, niin kuin\nitsekin hyvin tiedät.',100,NULL),
 (2654,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',11,'1Jos kuitenkin olisin\nsyyllistynyt johonkin rikokseen, josta seuraa\nkuolemanrangaistus, olen kyllä valmis kuolemaan. Mutta\njos näiden miesten syytöksissä ei ole mitään perää, ei\nkukaan voi luovuttaa minua heidän käsiinsä. Minä\nvetoan keisariin.\"',100,NULL),
 (2655,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',12,'2Festus keskusteli\nneuvonantajiensa kanssa ja antoi sitten vastauksensa:\n\"Olet vedonnut keisariin, niinpä menet keisarin\neteen.\"   ',100,NULL),
 (2656,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',22,'2Agrippa sanoi Festukselle: \"Minäkin kuulisin\nmielelläni sitä miestä.\" \"Huomenna kuulet\", vastasi\nFestus.   ',100,NULL),
 (2657,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',1,'Agrippa sanoi Paavalille: \"Saat puhua puolestasi.\" Silloin Paavali kohotti kätensä ja piti\npuolustuspuheen: ',101,NULL),
 (2658,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',2,'\"Kuningas Agrippa! Olen todella onnellinen, kun\nsaan tänään juuri sinun edessäsi puolustautua kaikkia\nniitä syytöksiä vastaan, joita juutalaiset ovat\nesittäneet.',101,NULL),
 (2659,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',3,'Sinähän tunnet perin pohjin kaikki\njuutalaisten säädökset ja kiistakysymykset. Siksi\npyydän, että kärsivällisesti kuuntelet minua. ',101,NULL),
 (2660,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',4,'\"Minun elämäni on alusta alkaen kaikkien\njuutalaisten tiedossa. He tietävät, miten minä\nnuoruusvuosinani olen elänyt kansani parissa\nJerusalemissa.',101,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (2661,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',5,'He tuntevat minut jo entuudestaan ja\nvoivat todistaa -- jos vain haluavat -- että olin\nfariseus ja elin siis uskontomme ankarimman suunnan\nmukaan.',101,NULL),
 (2662,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',6,'Niinpä minä nytkin olen tutkittavana sen\ntähden, että panen toivoni lupaukseen, jonka Jumala on\nantanut meidän isillemme.',101,NULL),
 (2663,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',7,'Yötä päivää meidän\nkansamme kaksitoista heimoa hartaasti palvelevat\nJumalaa ja toivovat tämän lupauksen toteutuvan. Tämän\ntoivon takia, kuningas, juutalaiset syyttävät minua.',101,NULL),
 (2664,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',8,'Miksi teistä on niin uskomatonta, että Jumala\nherättää kuolleet? ',101,NULL),
 (2665,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',9,'\"Minäkin elin ennen siinä luulossa, että minun\ntulee kaikin voimin taistella Jeesus Nasaretilaisen\nnimeä vastaan.',101,NULL),
 (2666,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',10,'0Niin minä myös tein Jerusalemissa.\nHankin ylipapeilta valtuudet ja toimitin vankilaan\nmonia pyhiä, ja kun tuli kyse heidän surmaamisestaan,\näänestin sen puolesta.',101,NULL),
 (2667,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',11,'1Kerran toisensa jälkeen\nminä synagogissa rankaisin heitä ja yritin pakottaa\nheidät herjaamaan Jeesusta, ja ankarassa kiihkossani\nminä ulotin vainoretkeni oman maamme ulkopuolelle\nasti. ',101,NULL),
 (2668,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',12,'2\"Näissä asioissa minä lähdin matkalle\nDamaskokseen. Olin saanut siihen ylipapeilta\nsuostumuksen ja valtuudet.',101,NULL),
 (2669,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',13,'3Mutta silloin,\nkuningas, minä matkaa tehdessäni puolenpäivän aikaan\nnäin, miten taivaasta leimahti aurinkoakin kirkkaampi\nvalo minun ja matkatovereideni ympärille.',101,NULL),
 (2670,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',14,'4Me\nkaikki kaaduimme maahan, ja minä kuulin äänen sanovan\nheprean kielellä: \'Saul, Saul, miksi vainoat minua?\nPaha sinun on potkia pistintä vastaan.\'',101,NULL),
 (2671,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',15,'5Minä\nkysyin: \'Herra, kuka sinä olet?\' Herra sanoi: \'Minä\nolen Jeesus, jota sinä vainoat.',101,NULL),
 (2672,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',16,'6Nouse jaloillesi.\nMinä olen ilmestynyt sinulle, koska olen valinnut\nsinut palvelijakseni ja todistajakseni, kertomaan\nsiitä, mitä nyt olet nähnyt ja mitä vielä olet näkevä,\nkun sinulle ilmestyn.',101,NULL),
 (2673,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',17,'7Minä pelastan sinut oman\nkansasi käsistä ja varjelen sinua, kun lähetän sinut\npakanoiden pariin',101,NULL),
 (2674,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',18,'8avaamaan heidän silmänsä ja\nsaattamaan heidät pimeydestä valoon ja Saatanan\nvallasta Jumalan luo. He saavat syntinsä anteeksi, kun\nuskovat minuun, ja heillä on oleva paikkansa niiden\njoukossa, jotka Jumala on pyhittänyt.\' ',101,NULL),
 (2675,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',19,'9\"Tämän vuoksi, kuningas Agrippa, en voinut olla\ntottelematta taivaallista näkyä.',101,NULL),
 (2676,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',20,'0Ensiksi\nDamaskoksessa ja Jerusalemissa, sitten joka puolella\nJuudeaa ja muiden kansojen parissa minä olen\njulistanut, että kaikkien tulee katua syntejään,\nkääntyä Jumalan puoleen ja tehdä tekoja, joissa heidän\nparannuksensa näkyy.',101,NULL),
 (2677,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',21,'1Tästä syystä juutalaiset\nottivat minut temppelissä kiinni ja yrittivät hakata\nminut hengiltä.',101,NULL),
 (2678,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',22,'2Jumalan avulla olen kuitenkin\nselvinnyt tähän päivään asti ja voin nytkin täyttää\ntodistajan tehtävääni, puhua sekä ylhäisille että\nalhaisille. Minä puhun vain siitä, minkä profeetat ja\nMooses ovat ennustaneet tapahtuvan, en mistään muusta,',101,NULL),
 (2679,'Anonymous','2008-10-17 16:33:18','Anonymous','2008-10-17 16:33:18',23,'3puhun siitä, että Messiaan tuli kärsiä kuolema ja\nensimmäisenä nousta kuolleista sekä julistaa valon\nsanomaa niin meidän kansallemme kuin muillekin\nkansoille.\" ',101,NULL),
 (2680,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',24,'4Paavalin näin puolustautuessa Festus sanoi kovalla\näänellä: \"Paavali, sinä olet järjiltäsi! Kirjaviisaus\non sekoittanut pääsi.\"',101,NULL),
 (2681,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',25,'5Mutta Paavali vastasi: \"En\nole järjiltäni, arvoisa Festus. Minkä puhun, puhun\ntotuuden ja terveen järjen mukaisesti.',101,NULL),
 (2682,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',26,'6Kuninkaalle\nnämä asiat kyllä ovat tuttuja, ja siksi voinkin\navoimesti puhua niistä hänelle. Yksikään niistä ei ole\nhänelle tuntematon, siitä olen varma -- eihän tämä ole\ntapahtunut missään syrjäisessä kolkassa.',101,NULL),
 (2683,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',27,'7Uskotko\nprofeettoja, kuningas Agrippa? Tiedän, että uskot.\"',101,NULL),
 (2684,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',28,'8\"Vähälläpä luulet tekeväsi minusta kristityn\",\nAgrippa sanoi.',101,NULL),
 (2685,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',29,'9\"Vähällä tai paljolla\", vastasi\nPaavali. \"Suokoon Jumala, että niin sinusta kuin\nteistä kaikista muistakin, jotka minua tänään\nkuulette, tulisi sellaisia kuin minä olen -- näitä\nkahleita lukuun ottamatta.\" ',101,NULL),
 (2686,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',30,'0Silloin kuningas nousi, samoin maaherra ja\nBerenike sekä koko heidän seurueensa.',101,NULL),
 (2687,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',31,'1Salista\npoistuessaan he puhuivat keskenään: \"Tuo mies ei ole\ntehnyt mitään, mistä ansaitsisi kuoleman tai kahleet.\"',101,NULL),
 (2688,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',32,'2Agrippa sanoi Festukselle: \"Hänet olisi voitu\nvapauttaa, ellei hän olisi vedonnut keisariin.\" ',101,NULL),
 (2689,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',1,'Kun oli päätetty, että lähtisimme meritse Italiaan,\nPaavali ja muutamat muut vangit annettiin Juliuksen,\nkeisarillisen sotaväen sadanpäällikön, valvontaan.',102,NULL),
 (2690,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',2,'Astuimme adramyttionilaiseen laivaan, jonka oli\nmäärä purjehtia Aasian satamiin, ja lähdimme merelle.\nMukanamme oli myös makedonialainen Aristarkos, joka\noli kotoisin Tessalonikasta.',102,NULL),
 (2691,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',3,'Seuraavana päivänä\nsaavuimme Sidoniin, ja Julius, joka kohteli Paavalia\nhyvin, antoi hänen käydä ystävien luona hoitoa\nsaamassa. ',102,NULL),
 (2692,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',4,'Lähdettyämme sieltä merelle purjehdimme vastatuulen\ntakia Kyproksen suojaan,',102,NULL),
 (2693,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',5,'jatkoimme sitten matkaamme\nKilikian ja Pamfylian edustalla olevan merenselän yli\nja laskimme maihin Lykian Myrassa.',102,NULL),
 (2694,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',6,'Sieltä\nsadanpäällikkö löysi aleksandrialaisen laivan, joka\noli lähdössä Italiaan, ja siirsi meidät siihen.',102,NULL),
 (2695,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',7,'Useiden päivien ajan purjehduksemme sitten edistyi\nhitaasti, ja vain vaivoin pääsimme Knidoksen kohdalle.\nTuuli esti meitä jatkamasta aikomaamme suuntaan, ja\nniin purjehdimme Salmonen niemen ohi Kreetan suojaan.',102,NULL),
 (2696,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',8,'Etenimme vaivalloisesti saaren rannikkoa seuraten\nja saavuimme sitten paikkaan, jonka nimi on\nHyvätsatamat. Se on lähellä Lasaian kaupunkia. ',102,NULL),
 (2697,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',9,'Olimme olleet matkalla jo pitkän aikaa,\npaastopäiväkin*oli ohitse, ja merenkulku alkoi olla\nvaarallista. Paavali varoitti toisia:',102,NULL),
 (2698,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',10,'0\"Miehet,\nminä tiedän, että purjehduksestamme tulee työläs ja\nvaarallinen. Siinä ei ole vaarassa vain lasti ja\nlaiva, vaan myös oma henkemme.\"',102,NULL),
 (2699,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',11,'1Sadanpäällikkö\nkuitenkin uskoi laivuria ja aluksen omistajaa enemmän\nkuin Paavalin puhetta.',102,NULL),
 (2700,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',12,'2Kun paikkakin oli\ntalvisatamaksi soveltumaton, useimmat olivat sitä\nmieltä, että oli jatkettava matkaa ja yritettävä\npäästä talveksi Foiniksiin, Kreetan satamaan, joka\nantaa lounaaseen ja luoteeseen päin.   ',102,NULL),
 (2701,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',21,'1Mutta silloin, kun emme pitkään aikaan olleet\nsaaneet syödyksikään mitään, Paavali nousi toisten\nkeskeltä ja sanoi: \"Miehet! Teidän olisi pitänyt uskoa\nminua eikä lähteä Kreetasta. Silloin olisitte\nvälttäneet nämä vastukset ja vahingot.',102,NULL),
 (2702,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',22,'2Mutta nyt\nkehotan teitä pysymään rohkealla mielin. Yksikään\nteistä ei menetä henkeään, ainoastaan laiva tuhoutuu.',102,NULL),
 (2703,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',23,'3Viime yönä näet vierelläni seisoi sen Jumalan\nenkeli, jonka oma minä olen ja jota minä palvelen.',102,NULL),
 (2704,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',24,'4Enkeli sanoi: \'Älä pelkää, Paavali. Sinä olet\nvielä seisova keisarin edessä, ja Jumala antaa sinulle\nsen lahjan, että myös matkatoverisi pelastuvat.\'',102,NULL),
 (2705,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',25,'5Pysykää siis rohkeina, miehet! Minä luotan\nJumalaan ja uskon, että käy niin kuin minulle on\nsanottu.',102,NULL),
 (2706,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',26,'6Jollekin saarelle me vielä ajaudumme.\"   ',102,NULL),
 (2707,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',33,'3Vielä ennen päivän koittoa Paavali kehotti kaikkia\nsyömään. Hän sanoi: \"Jo neljättätoista päivää te\nolette eläneet jännityksen vallassa ettekä ole syöneet\nruoan murenta.',102,NULL),
 (2708,'Anonymous','2008-10-17 16:33:19','Anonymous','2008-10-17 16:33:19',34,'4Siksi kehotan teitä nyt syömään\njotakin. Se on välttämätöntä, jos mieli pelastua.\nYksikään teistä ei menetä hiuskarvaakaan päästään.\"',102,NULL),
 (2709,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',35,'5Tämän sanottuaan hän otti leivän ja kaikkien\nnähden kiitti Jumalaa, mursi leivän ja alkoi syödä.',102,NULL),
 (2710,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',36,'6Toisetkin saivat tästä rohkeutta ja rupesivat\nruokailemaan.',102,NULL),
 (2711,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',37,'7Meitä oli laivassa kaikkiaan\nkaksisataaseitsemänkymmentäkuusi henkeä.',102,NULL),
 (2712,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',38,'8Kun oli\nkylliksi syöty, merimiehet kevensivät laivaa\nheittämällä viljalastin mereen. ',102,NULL),
 (2713,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',39,'9Aamun valjetessa merimiehet eivät tunteneet edessä\nolevaa maata, mutta he huomasivat matalarantaisen\npoukaman ja päättivät yrittää ohjata laivan sinne.',102,NULL),
 (2714,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',40,'0He katkaisivat ankkuriköydet ja antoivat ankkurien\njäädä mereen, ja kun peräsinairot oli päästetty\nköysistään, he nostivat keulapurjeen tuuleen ja\nohjasivat rantaa kohti.',102,NULL),
 (2715,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',41,'1Laiva kuitenkin ajautui\npäin särkkää ja törmäsi siihen. Keula juuttui tiukasti\npaikoilleen, ja aallokossa perä alkoi hajota\nliitoksistaan.',102,NULL),
 (2716,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',42,'2Sotamiehet aikoivat silloin tappaa\nvangit, ettei kukaan pääsisi uimalla pakoon.',102,NULL),
 (2717,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',43,'3Sadanpäällikkö kuitenkin esti heidän aikeensa,\nsillä hän halusi pelastaa Paavalin. Hän käski\nuimataitoisten hypätä ensimmäisinä mereen ja pyrkiä\nrantaan',102,NULL),
 (2718,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',44,'4ja heidän perässään toisten, joko\nlankkujen varassa tai muilla laivan kappaleilla. Näin\nkaikki pääsivät onnellisesti maihin. ',102,NULL),
 (2719,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',1,'Päästyämme turvaan saimme tietää, että saari oli\nMalta.',103,NULL),
 (2720,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',2,'Saaren asukkaat kohtelivat meitä tavattoman\nystävällisesti. He sytyttivät nuotion ja kutsuivat\nmeidät kaikki sen ääreen, sillä ilma oli kylmä ja oli\nalkanut sataa.',103,NULL),
 (2721,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',3,'Paavali keräsi sylyksen risuja ja\npani ne nuotioon, mutta kuumuus ajoi sieltä esiin\nkyyn, ja se kävi kiinni Paavalin käteen.',103,NULL),
 (2722,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',4,'Kun\nsaarelaiset näkivät käärmeen riippuvan hänen\nkädessään, he sanoivat toisilleen: \"Tuo on varmaankin\nmurhamies. Merestä hän pelastui, mutta kostotar ei\nsallinut hänen elää.\"',103,NULL),
 (2723,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',5,'Mutta Paavali ravisti\nkäärmeen tuleen, eikä hänelle tapahtunut mitään,',103,NULL),
 (2724,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',6,'vaikka saarelaiset odottivat käden ajettuvan tai\nPaavalin kaatuvan äkkiä kuolleena maahan. Kun he\nolivat aikansa odottaneet ja näkivät, ettei Paavalille\ntapahtunut mitään merkillistä, he tulivat toisiin\najatuksiin ja sanoivat: \"Hän on joku jumala.\" ',103,NULL),
 (2725,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',7,'Lähistöllä oli maatiloja, jotka omisti Publius,\nsaaren ylin virkamies. Hän otti meidät luokseen ja\npiti meitä ystävällisesti vierainaan kolmen päivän\najan.',103,NULL),
 (2726,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',8,'Publiuksen isä makasi vuoteenomana; hänellä\noli kuumetta ja paha vatsatauti. Paavali meni hänen\nluokseen, rukoili, pani kätensä hänen päälleen ja\nparansi hänet.',103,NULL),
 (2727,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',9,'Tämän jälkeen tulivat saaren muutkin\nsairaat Paavalin luo, ja he saivat avun vaivoihinsa.',103,NULL),
 (2728,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',10,'0Monin tavoin he sitten osoittivat meille\nkunnioitustaan, ja kun taas jatkoimme matkaa, he\nantoivat meille kaikkea, mitä tarvitsimme.   Näin me saavuimme Roomaan.',103,NULL),
 (2729,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',15,'5Siellä asuvat veljet\nolivat jo kuulleet meistä ja tulivat meitä vastaan\nForum Appiin ja Tres Tabernaen pikkukaupunkeihin\nsaakka. Nähdessään heidät Paavali kiitti Jumalaa ja\nsai lisää rohkeutta.',103,NULL),
 (2730,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',16,'6Kun sitten olimme perillä\nRoomassa, Paavalille annettiin lupa asua omassa\nasunnossa yhdessä häntä vartioivan sotilaan kanssa.   ',103,NULL),
 (2731,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',21,'1Juutalaiset vastasivat: \"Emme ole saaneet\nJuudeasta mitään kirjettä, jossa kerrottaisiin\nsinusta, eikä kukaan veljistä ole täällä käydessään\npuhunut sinusta pahaa, ei julkisesti eikä\nyksityisesti.',103,NULL),
 (2732,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',22,'2Mielellämme kuitenkin kuulisimme,\nmitä sinä näistä asioista ajattelet. Sen näet olemme\nsaaneet tästä lahkosta tietää, että se kaikkialla\nherättää vastustusta.\" ',103,NULL),
 (2733,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',23,'3He sopivat Paavalin kanssa uudesta tapaamisesta,\nja määräpäivänä heitä tuli useampia hänen\nmajapaikkaansa. Aamuvarhaisesta iltamyöhään Paavali\ntodisti heille Jumalan valtakunnasta ja selitti sitä.\nMooseksen lakiin ja profeettoihin vedoten hän yritti\ntaivuttaa heidät uskomaan Jeesukseen.',103,NULL),
 (2734,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',24,'4Jotkut\nvakuuttuivatkin siitä, mitä hän puhui, mutta toiset\npysyivät epäuskoisina.',103,NULL),
 (2735,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',25,'5He eivät päässeet keskenään\nyhteisymmärrykseen, ja heidän lähtiessään Paavali\nsanoi ainoastaan nämä sanat: \"Oikein on Pyhä Henki\nprofeetta Jesajan suulla puhunut teidän isistänne:  ',103,NULL),
 (2736,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',28,'8Tietäkää siis: Jumala on lähettänyt\npelastussanomansa pakanakansoille, ja ne myös kuulevat\nsitä!\"* ',103,NULL),
 (2737,'Anonymous','2008-10-17 16:33:20','Anonymous','2008-10-17 16:33:20',30,'0Täydet kaksi vuotta Paavali sitten asui omassa\nvuokra-asunnossaan ja otti vastaan kaikki, jotka\ntulivat hänen luokseen.',103,NULL),
 (2738,'Anonymous','2008-10-17 16:33:21','Anonymous','2008-10-17 16:33:21',31,'1Hän julisti Jumalan\nvaltakuntaa ja opetti, kuka Herra Jeesus Kristus on,\navoimesti ja kenenkään estämättä.  ',103,NULL),
 (2739,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',1,'Paavali, Kristuksen Jeesuksen palvelija, kutsuttu\napostoliksi ja valittu julistamaan Jumalan\nevankeliumia, tervehtii kaikkia Roomassa olevia\nJumalalle rakkaita ja hänen kutsumiaan pyhiä.* ',104,NULL),
 (2740,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',2,'Julistamani evankeliumi, jonka Jumala on\nprofeettojensa suulla edeltäpäin luvannut pyhissä\nkirjoituksissa,',104,NULL),
 (2741,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',3,'on sanoma hänen Pojastaan.\nInhimillisen syntyperänsä puolelta hän oli Daavidin\njälkeläinen;',104,NULL),
 (2742,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',4,'pyhyyden Hengen puolelta hän oli\nJumalan Poika, jolla on valta, ylösnousemuksessa tähän\nasemaan asetettu. Hän on Jeesus Kristus, meidän\nHerramme,',104,NULL),
 (2743,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',5,'ja häneltä minä olen saanut armon ja\napostolinviran, jotta hänen nimensä kunniaksi\njohtaisin ihmisiä kaikista kansoista\nuskonkuuliaisuuteen.',104,NULL),
 (2744,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',6,'Näitä Jeesuksen Kristuksen\nkutsumia olette myös te.',104,NULL),
 (2745,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',7,'Jumalan, meidän Isämme, ja\nHerran Jeesuksen Kristuksen armo ja rauha teille.*       ',104,NULL),
 (2746,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',22,'2He väittävät olevansa viisaita mutta ovat tulleet\ntyhmiksi,',104,NULL),
 (2747,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',23,'3ja he ovat vaihtaneet katoamattoman\nJumalan kirkkauden katoavaisten ihmisten ja lintujen,\nnelijalkaisten ja matelijoiden kuviin.',104,NULL),
 (2748,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',24,'4Sen vuoksi\nJumala on jättänyt heidät mielihalujensa valtaan\nsellaiseen saastaisuuteen, että he keskinäisissä\nsuhteissaan häpäisevät oman ruumiinsa.',104,NULL),
 (2749,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',25,'5He ovat\nvaihtaneet Jumalan totuuden valheeseen, he ovat\nkunnioittaneet ja palvelleet luotua eivätkä Luojaa --\nolkoon hän ikuisesti ylistetty, aamen.',104,NULL),
 (2750,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',26,'6Siksi\nJumala on jättänyt heidät häpeällisten himojen\nvaltaan. Naiset ovat vaihtaneet luonnollisen\nsukupuoliyhteyden luonnonvastaiseen,',104,NULL),
 (2751,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',27,'7ja miehet\novat samoin luopuneet luonnollisesta yhteydestä\nnaisiin ja heissä on syttynyt himo toisiaan kohtaan.\nMiehet ovat harhautuneet harjoittamaan keskenään\nsäädyttömyyttä ja saavat ansaitsemansa palkan. ',104,NULL),
 (2752,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',28,'8Koska he eivät ole antaneet arvoa Jumalan\ntuntemiselle, on Jumala jättänyt heidät arvottomien\najatusten valtaan, tekemään sellaista mikä ei sovi.',104,NULL),
 (2753,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',29,'9He ovat täynnä kaikenlaista vääryyttä,\nhalpamaisuutta, ahneutta ja pahuutta, täynnä kateutta,\nmurhanhimoa, riitaisuutta, petollisuutta ja\npahansuopuutta, he panettelevat',104,NULL),
 (2754,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',30,'0ja parjaavat,\nvihaavat Jumalaa, ovat röyhkeitä ja pöyhkeitä,\nrehenteleviä ja pahanilkisiä, vanhemmilleen\ntottelemattomia,',104,NULL),
 (2755,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',31,'1ymmärtämättömiä ja\nepäluotettavia, rakkaudettomia ja säälimättömiä.',104,NULL),
 (2756,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',32,'2Vaikka he tietävät Jumalan säätäneen, että ne,\njotka käyttäytyvät tällä tavoin, ovat ansainneet\nkuoleman, he toimivat itse näin, vieläpä osoittavat\nhyväksymistään, kun muut tekevät samoin.   Ilmausta \"pyhät\" käytettiin alun perin Jerusalemin alkuseurakunnan jäsenistä, sittemmin kaikkien seurakuntien kristityistä',104,NULL),
 (2757,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',1,'Sen tähden et voi mitenkään puolustautua,\nihmisparka, sinä joka tuomitset muita, kuka sitten\noletkin. Tuomitessasi toisen julistat tuomion myös\nitsellesi, koska sinä, toisen tuomitsija, teet itse\nsamoja tekoja.',105,NULL),
 (2758,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',2,'Me tiedämme, että Jumala on oikeassa\ntuomitessaan ne, jotka tekevät tällaista.',105,NULL),
 (2759,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',3,'Kuvitteletko sinä välttäväsi Jumalan tuomion, kun\nteet itse sellaista, mistä tuomitset muita?',105,NULL),
 (2760,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',4,'Halveksitko sinä Jumalan suurta hyvyyttä,\nkärsivällisyyttä ja pitkämielisyyttä? Etkö ymmärrä,\nettä Jumalan hyvyys johtaa sinut kääntymiseen?',105,NULL),
 (2761,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',5,'Mutta sinä olet kova etkä sisimmässäsi tahdo\nkääntyä. Näin kartutat vihaa, ja se kohtaa sinut vihan\npäivänä, jolloin Jumalan oikeudenmukainen tuomio tulee\njulki.',105,NULL),
 (2762,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',6,'Silloin Jumala maksaa jokaiselle hänen\ntekojensa mukaan. ',105,NULL),
 (2763,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',7,'Niille, jotka uupumatta hyvää tehden etsivät\nkirkkautta, kunniaa ja katoamattomuutta, hän antaa\nikuisen elämän,',105,NULL),
 (2764,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',8,'mutta niitä, jotka ovat itsekkäitä\nja tottelevat totuuden sijasta vääryyttä, kohtaa\nankara viha.',105,NULL),
 (2765,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',9,'Tuska ja ahdistus tulee jokaisen\nosaksi, joka tekee pahaa, ensin juutalaisen, sitten\nmyös kreikkalaisen.',105,NULL),
 (2766,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',10,'0Kirkkaus, kunnia ja rauha taas\ntulee jokaisen osaksi, joka tekee hyvää, ensin\njuutalaisen, sitten myös kreikkalaisen,',105,NULL),
 (2767,'Anonymous','2008-10-17 16:33:22','Anonymous','2008-10-17 16:33:22',11,'1sillä\nJumala ei tee eroa ihmisten välillä. ',105,NULL),
 (2768,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',12,'2Ne, jotka ovat tehneet syntiä lakia tuntematta,\nmyös tuhoutuvat laista riippumatta. Ne taas, jotka\novat lain tuntien tehneet syntiä, myös tuomitaan lain\nnojalla.',105,NULL),
 (2769,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',13,'3Ei Jumala hyväksy vanhurskaiksi niitä,\njotka vain kuulevat lain sanoja, vaan vanhurskaiksi\njulistetaan ne, jotka myös noudattavat lakia.',105,NULL),
 (2770,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',14,'4Pakanakansatkin, joilla ei ole lakia, saattavat\nluonnostaan tehdä, mitä laki vaatii. Silloin pakanat,\nvaikkei heillä lakia olekaan, ovat itse itselleen\nlaki.',105,NULL),
 (2771,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',15,'5Näin he osoittavat, että lain vaatimus on\nkirjoitettu heidän sydämeensä. Siitä todistaa heidän\nomatuntonsakin, kun heidän ajatuksensa syyttävät tai\nmyös puolustavat heitä.',105,NULL),
 (2772,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',16,'6Tämä tulee näkyviin sinä\npäivänä, jona Jumala minun julistamani evankeliumin\nmukaisesti tuo ihmisten sisimmätkin salaisuudet\nKristuksen Jeesuksen tuomittaviksi.   ',105,NULL),
 (2773,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',25,'5Ympärileikkaus on kyllä hyödyksi, jos noudatat\nlakia. Mutta jos olet lainrikkoja, ympärileikkauksesi\non mitätön.',105,NULL),
 (2774,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',26,'6Jos ympärileikkaamaton noudattaa lain\nsäännöksiä, eikö häntä silloin tule pitää\nympärileikattuna?',105,NULL),
 (2775,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',27,'7Ulkonaisesti ympärileikkaamaton,\njoka noudattaa lain käskyjä, on tuomitseva sinut, joka\nlain kirjaimesta ja ympärileikkauksesta huolimatta\nrikot lakia.',105,NULL),
 (2776,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',28,'8Oikea juutalainen ei ole se, joka on\njuutalainen ulkonaisesti, eikä oikea ympärileikkaus\nole se, joka on ulkonaisesti näkyvänä ruumiissa.',105,NULL),
 (2777,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',29,'9Oikea juutalainen on se, joka on juutalainen\nsisimmässään, ja oikea ympärileikkaus on sydämen\nympärileikkaus, jota ei saa aikaan laki, vaan Henki.\nTällainen juutalainen saa kiitoksen Jumalalta, ei\nihmisiltä. ',105,NULL),
 (2778,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',1,'Mitä etuja sitten on juutalaisilla, ja mitä hyötyä\non ympärileikkauksesta?',106,NULL),
 (2779,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',2,'Paljonkin, kaikin tavoin.\nJumala on ensiksikin uskonut sanansa juuri heille.',106,NULL),
 (2780,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',3,'Mutta vaikka jotkut ovatkin olleet epäuskoisia, ei\nkai heidän epäuskonsa tee Jumalan uskollisuutta\ntyhjäksi?',106,NULL),
 (2781,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',4,'Ei toki! Vaikka kaikki ihmiset olisivat\nvalheellisia, Jumala on luotettava. Onhan kirjoitettu:   ',106,NULL),
 (2782,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',5,'Mutta jos meidän väärämielisyytemme tuo esiin\nJumalan oikeamielisyyden, mitä meidän on silloin\nsanottava? Onko Jumala ehkä väärämielinen -- puhun nyt\nihmisten tavoin -- kun hän antaa vihan kohdata meitä?',106,NULL),
 (2783,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',6,'Ei suinkaan! Kuinka Jumala siinä tapauksessa voisi\ntuomita maailman?',106,NULL),
 (2784,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',7,'Mutta jos minun valheellisuuteni\nvain lisää Jumalan kirkkautta, kun sen ansiosta hänen\ntotuutensa yhä korostuu, miksi minut sitten tuomitaan\nsyntisenä?',106,NULL),
 (2785,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',8,'Ei kai asia niin ole, kuin jotkut\nherjaajat väittävät meidän sanovan: \"Tehdään vain\npahaa, niin saadaan aikaan hyvää!\" He kyllä saavat\nansaitsemansa rangaistuksen.     ',106,NULL),
 (2786,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',19,'9Me tiedämme, että lain sanat kohdistuvat niihin,\njoilla on laki. Näin ei kukaan voi väittää vastaan,\nvaan koko maailma joutuu Jumalan tuomion alaiseksi.',106,NULL),
 (2787,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',20,'0Eihän yksikään ihminen tule Jumalan edessä\nvanhurskaaksi lain käskyjä noudattamalla. Lain\ntehtävänä on opettaa tuntemaan, mitä synti on.   ',106,NULL),
 (2788,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',27,'7Onko siis syytä kerskailuun? Siltä on pohja\npudonnut pois. Mikä laki on saanut tämän aikaan? Sekö,\njoka vaatii tekoja? Ei, vaan uskon laki.',106,NULL),
 (2789,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',28,'8Katsomme\nsiis, että ihminen tulee vanhurskaaksi, kun hän uskoo,\nilman lain vaatimia tekoja.',106,NULL),
 (2790,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',29,'9Vai onko Jumala\npelkästään juutalaisten Jumala? Eikö hän ole myös\nmuiden kansojen Jumala? On toki,',106,NULL),
 (2791,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',30,'0kun kerran Jumala\non yksi ja ainoa. Hän tekee ympärileikatut\nvanhurskaiksi, jos he uskovat, ja\nympärileikkaamattomat, kun he uskovat.',106,NULL),
 (2792,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',31,'1Kumoammeko\nme siis lain vetoamalla uskoon? Emme suinkaan, me\npäinvastoin vahvistamme sen mitä laki sanoo. ',106,NULL),
 (2793,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',1,'Mitä meidän on sanottava Abrahamista, kansamme\nkantaisästä? Mitä hän saikaan osakseen?',107,NULL),
 (2794,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',2,'Jos Abraham\nkatsottiin vanhurskaaksi tekojensa perusteella,\nhänellä oli aihetta ylpeillä -- ei kuitenkaan Jumalan\nedessä.',107,NULL),
 (2795,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',3,'Mitä sanovat kirjoitukset? \"Abraham uskoi\nJumalan lupaukseen, ja Jumala katsoi hänet\nvanhurskaaksi.\"',107,NULL),
 (2796,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',4,'Työntekijälle maksettua palkkaa ei\nkatsota armosta saaduksi vaan ansaituksi.',107,NULL),
 (2797,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',5,'Jos taas\njollakulla ei ole ansioita mutta hän uskoo Jumalaan,\njoka tekee jumalattoman vanhurskaaksi, Jumala lukee\nhänen uskonsa vanhurskaudeksi.',107,NULL),
 (2798,'Anonymous','2008-10-17 16:33:23','Anonymous','2008-10-17 16:33:23',6,'Ylistäähän Daavidkin\nautuaaksi sellaista ihmistä, jonka Jumala katsoo\nvanhurskaaksi hänen teoistaan riippumatta:   ',107,NULL),
 (2799,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',9,'Koskeeko nyt tämä autuaaksi ylistäminen vain\nympärileikattuja vai myös ympärileikkaamattomia? Mehän\nsanomme: \"Jumala katsoi Abrahamille vanhurskaudeksi\nuskon.\"',107,NULL),
 (2800,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',10,'0Oliko Abraham silloin ympärileikattu? Ei\nollut, hän oli vielä ympärileikkaamaton.',107,NULL),
 (2801,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',11,'1Hän sai\nympärileikkauksen merkin sen vakuudeksi, että hänet\noli hyväksytty vanhurskaaksi jo, kun hän\nympärileikkaamattomana uskoi. Näin hänestä tuli\nkaikkien niiden isä, jotka uskovat ja jotka katsotaan\nvanhurskaiksi, vaikka he ovat ympärileikkaamattomia.',107,NULL),
 (2802,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',12,'2Samoin hän on ympärileikattujen isä, jos nämä myös\nseuraavat sitä uskon tietä, jota isämme Abraham kulki\nollessaan vielä ympärileikkaamaton.   ',107,NULL),
 (2803,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',16,'6Lupaus ja usko kuuluvat yhteen sitä varten, että\nkaikki olisi armoa. Näin lupaus on varma ja koskee\nkaikkia Abrahamin jälkeläisiä, ei vain niitä joilla on\nlaki, vaan myös niitä joilla on Abrahamin usko. Hän on\nmeidän kaikkien isä,',107,NULL),
 (2804,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',17,'7niin kuin on kirjoitettu:\n\"Minä olen tehnyt sinut monien kansojen isäksi.\" Tähän\nJumalaan Abraham uskoi, häneen, joka tekee kuolleet\neläviksi ja kutsuu olemattomat olemaan.',107,NULL),
 (2805,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',18,'8Abraham\ntoivoi, vaikka toivoa ei ollut, ja niin hän uskoi\ntulevansa monien kansojen kantaisäksi näiden sanojen\nmukaisesti: \"Näin suuri on oleva sinun jälkeläistesi\nluku.\"',107,NULL),
 (2806,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',19,'9Hänen uskonsa ei horjunut, vaikka hän,\nlähes satavuotiaana, tiesi elinvoimansa sammuneen ja\nSaaran kohdun kuihtuneen.',107,NULL),
 (2807,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',20,'0Abraham ei ollut\nepäuskoinen eikä epäillyt Jumalan lupausta, vaan sai\nvoimaa uskostaan. Hän antoi Jumalalle kunnian',107,NULL),
 (2808,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',21,'1varmana siitä, että Jumala pystyy tekemään sen\nmitä on luvannut.',107,NULL),
 (2809,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',22,'2Sen vuoksi Jumala katsoikin\nhänet vanhurskaaksi. ',107,NULL),
 (2810,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',23,'3Näitä sanoja ei kuitenkaan ole kirjoitettu vain\nhänen tähtensä,',107,NULL),
 (2811,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',24,'4vaan myös meidän vuoksemme. Jumala\non katsova vanhurskaiksi meidätkin, kun uskomme\nhäneen, joka on herättänyt kuolleista Herramme\nJeesuksen.',107,NULL),
 (2812,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',25,'5Jumala antoi Kristuksen kuolla meidän\nrikkomustemme tähden ja herätti hänet kuolleista\nmeidän vanhurskauttamisemme tähden. ',107,NULL),
 (2813,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',1,'Kun nyt Jumala on tehnyt meidät, jotka uskomme,\nvanhurskaiksi, meillä on Herramme Jeesuksen Kristuksen\nansiosta rauha Jumalan kanssa.',108,NULL),
 (2814,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',2,'Kristus on avannut\nmeille pääsyn tähän armoon, jossa nyt lujasti pysymme.\nMe riemuitsemme siitä toivosta, että pääsemme Jumalan\nkirkkauteen.',108,NULL),
 (2815,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',3,'Me riemuitsemme jopa ahdingosta, sillä\ntiedämme, että ahdinko saa aikaan kestävyyttä,',108,NULL),
 (2816,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',4,'kestävyys auttaa selviytymään koetuksesta ja\nkoetuksesta selviytyminen antaa toivoa.',108,NULL),
 (2817,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',5,'Eikä toivo\nole turha, sillä Jumala on vuodattanut rakkautensa\nmeidän sydämiimme antamalla meille Pyhän Hengen. ',108,NULL),
 (2818,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',6,'Meistä ei ollut itseämme auttamaan, mutta Kristus\nkuoli jumalattomien puolesta, kun aika koitti.',108,NULL),
 (2819,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',7,'Tuskin kukaan haluaa kuolla edes nuhteettoman\nihmisen puolesta; hyvän ihmisen puolesta joku ehkä on\nvalmis antamaan henkensä.',108,NULL),
 (2820,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',8,'Mutta Jumala osoittaa\nrakkautensa meitä kohtaan siinä, että Kristus kuoli\nmeidän puolestamme, kun vielä olimme syntisiä.',108,NULL),
 (2821,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',9,'Kun\nhän nyt on vuodattamalla verensä tehnyt meidät\nvanhurskaiksi, hän vielä paljon varmemmin pelastaa\nmeidät tulevalta vihalta.',108,NULL),
 (2822,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',10,'0Jos kerran Jumalan Pojan\nkuolema sovitti meidät Jumalan kanssa, kun olimme\nhänen vihollisiaan, paljon varmemmin on Jumalan Pojan\nelämä pelastava meidät nyt, kun sovinto on tehty.',108,NULL),
 (2823,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',11,'1Eikä siinä vielä kaikki. Me saamme myös riemuita\nJumalastamme, kun nyt olemme vastaanottaneet Herramme\nJeesuksen Kristuksen valmistaman sovituksen.   ',108,NULL),
 (2824,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',15,'5Rikkomusta ei kuitenkaan voi verrata armoon. Yhden\nainoan ihmisen rikkomus on tosin tuottanut kaikille\nkuoleman, mutta vielä paljon runsaammin ovat Jumalan\narmo ja hänen lahjansa tulleet yhden ainoan ihmisen,\nJeesuksen Kristuksen, ansiosta kaikkien osaksi.',108,NULL),
 (2825,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',16,'6Tätä lahjaa ei voi edes verrata yhden ihmisen\nsynnin seurauksiin, sillä yhden ihmisen teon\nseurauksena oli kadotustuomio, mutta Jumalan armosta\nkaikkien rikkomusten seuraukseksi tulikin vapauttava\ntuomio.',108,NULL),
 (2826,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',17,'7Yhden ihmisen rikkomus aiheutti sen, että\nkuolema pääsi hallitsemaan tämän yhden vuoksi. Paljon\nennemmin tulevat ne, jotka saavat omakseen\nvanhurskauden ylenpalttisen armon ja lahjan, elämään\nja hallitsemaan yhden ainoan ansiosta, Jeesuksen\nKristuksen. ',108,NULL),
 (2827,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',18,'8Niin kuin siis yhden ainoan rikkomus tuotti\nkaikille ihmisille kadotustuomion, niin riittää yhden\nainoan vanhurskas teko antamaan kaikille ihmisille\nvanhurskauden ja elämän.',108,NULL),
 (2828,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',19,'9Niin kuin yhden ihmisen\ntottelemattomuus teki kaikista syntisiä, niin yhden\nkuuliaisuus tekee kaikista vanhurskaita.',108,NULL),
 (2829,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',20,'0Laki tuli\nmaailmaan sitä varten, että rikkomus tulisi\nsuuremmaksi. Mutta missä synti on tullut suureksi,\nsiellä on armo tullut ylenpalttiseksi.',108,NULL),
 (2830,'Anonymous','2008-10-17 16:33:24','Anonymous','2008-10-17 16:33:24',21,'1Niin kuin\nsynti on hallinnut ja vienyt kuolemaan, niin on armo\nhallitseva ja johtava ikuiseen elämään, koska Herramme\nJeesus Kristus on lahjoittanut meille vanhurskauden. ',108,NULL),
 (2831,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',1,'Mitä me tähän sanomme? Onko meidän jatkuvasti\ntehtävä syntiä, jotta armo tulisi yhä suuremmaksi?',109,NULL),
 (2832,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',2,'Ei tietenkään. Kun kerran olemme kuolleet pois\nsynnistä, kuinka voisimme edelleen elää siinä?',109,NULL),
 (2833,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',3,'Tiedättehän, että meidät kaikki Kristukseen\nJeesukseen kastetut on kastettu hänen kuolemaansa.',109,NULL),
 (2834,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',4,'Näin meidät kasteessa annettiin kuolemaan ja\nhaudattiin yhdessä hänen kanssaan, jotta mekin\nalkaisimme elää uutta elämää, niin kuin Kristus Isän\nkirkkauden voimalla herätettiin kuolleista.',109,NULL),
 (2835,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',5,'Jos\nkerran yhtäläinen kuolema on liittänyt meidät yhteen\nhänen kanssaan, me myös nousemme kuolleista niin kuin\nhän. ',109,NULL),
 (2836,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',6,'Tiedämme, että vanha minämme on yhdessä hänen\nkanssaan ristiinnaulittu, jotta tämä syntinen ruumis\nmenettäisi valtansa emmekä enää olisi synnin orjia.',109,NULL),
 (2837,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',7,'Se, joka on kuollut, on näet päässyt vapaaksi\nsynnin vallasta.',109,NULL),
 (2838,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',8,'Mutta jos kerran olemme kuolleet\nKristuksen kanssa, uskomme saavamme myös elää hänen\nkanssaan.',109,NULL),
 (2839,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',9,'Tiedämme, että koska Kristus on herätetty\nkuolleista, hän ei enää kuole eikä kuolemalla ole enää\nvaltaa häneen.',109,NULL),
 (2840,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',10,'0Kun Kristus kuoli, hän\nkertakaikkisesti kuoli eroon synnistä. Kun hän nyt\nelää, hän elää Jumalalle.',109,NULL),
 (2841,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',11,'1Ajatelkaa tekin samoin\nitsestänne: te olette kuolleet pois synnistä ja elätte\nJumalalle Kristuksessa Jeesuksessa. ',109,NULL),
 (2842,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',12,'2Synti ei siis saa hallita teidän kuolevaista\nruumistanne, niin että noudatatte sen himoja.',109,NULL),
 (2843,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',13,'3Älkää antako ruumiinne jäseniä synnin käyttöön\nvääryyden aseiksi! Kun nyt kerran olette siirtyneet\nkuolemasta elämään, antakaa itsenne Jumalalle ja\nruumiinne jäsenet hänelle vanhurskauden aseiksi!',109,NULL),
 (2844,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',14,'4Synti ei enää ole teidän herranne, sillä te ette\nole lain vaan armon alaisia.   ',109,NULL),
 (2845,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',20,'0Kun olitte synnin orjia, ette voineet palvella\nvanhurskautta.',109,NULL),
 (2846,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',21,'1Minkä sadon te siitä korjasitte?\nKaikkea sellaista mitä nyt häpeätte, sillä sen loppuna\non kuolema.',109,NULL),
 (2847,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',22,'2Mutta kun nyt olette päässeet vapaiksi\nsynnistä ja tulleet Jumalan palvelijoiksi, te\nkorjaatte satona pyhityksen ja saatte lopuksi ikuisen\nelämän.',109,NULL),
 (2848,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',23,'3Synnin palkka on kuolema, mutta Jumalan\narmolahja on iankaikkinen elämä Kristuksessa\nJeesuksessa, meidän Herrassamme. ',109,NULL),
 (2849,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',1,'Veljet, tehän tunnette lain ja tiedätte, että laki\nhallitsee ihmistä niin kauan kuin hän elää.',110,NULL),
 (2850,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',2,'Laki\nsitoo naimisissa olevan naisen mieheensä niin kauan\nkuin tämä on elossa. Mutta jos mies kuolee, nainen\nvapautuu siitä laista, joka sitoi hänet mieheen.',110,NULL),
 (2851,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',3,'Jos hän miehensä eläessä antautuu toiselle\nmiehelle, hän on avionrikkoja. Mutta miehensä kuoltua\nhän on vapaa lain määräyksestä eikä tee aviorikosta,\njos menee naimisiin toisen miehen kanssa.',110,NULL),
 (2852,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',4,'Samoin on\nteidän laitanne, veljeni. Kristuksen kuolemassa te\nhänen ruumiinsa jäseninä olette kuolleet vapaiksi\nlaista ja kuulutte nyt toiselle, hänelle joka on\nherätetty kuolleista. Näin me kannamme hedelmää\nJumalalle. ',110,NULL),
 (2853,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',5,'Silloin kun vielä olimme turmeltuneen luontomme\nvallassa, ruumiissamme vaikuttivat lain herättämät\nsyntiset himot ja me kannoimme hedelmää kuolemalle.',110,NULL),
 (2854,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',6,'Mutta kun nyt olemme kuolleet pois siitä, mikä piti\nmeitä vallassaan, olemme päässeet vapaiksi laista. Nyt\npalvelemme Jumalaa uudella tavalla, Hengen mukaan,\nemme enää vanhalla tavalla, lain kirjaimen orjina.   ',110,NULL),
 (2855,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',13,'3Onko nyt hyvä tuottanut minulle kuoleman? Ei toki,\nvaan synti. Jotta synti paljastuisi synniksi, se\ntuotti minulle hyvän avulla kuoleman. Näin käsky teki\nsynnin vielä monin verroin pahemmaksi. ',110,NULL),
 (2856,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',14,'4Me tiedämme, että laki on hengellinen. Minä sitä\nvastoin olen turmeltunut ihminen, synnin orjaksi\nmyyty.',110,NULL),
 (2857,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',15,'5En edes ymmärrä, mitä teen: en tee sitä,\nmitä tahdon, vaan sitä, mitä vihaan.',110,NULL),
 (2858,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',16,'6Ja jos kerran\nteen sitä, mitä en tahdo, silloin myönnän, että laki\non hyvä.',110,NULL),
 (2859,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',17,'7Niinpä en enää teekään itse sitä, mitä\nteen, vaan sen tekee minussa asuva synti.',110,NULL),
 (2860,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',18,'8Tiedänhän, ettei minussa, nimittäin minun\nturmeltuneessa luonnossani, ole mitään hyvää.\nTahtoisin kyllä tehdä oikein, mutta en pysty siihen.',110,NULL),
 (2861,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',19,'9En tee sitä hyvää, mitä tahdon, vaan sitä pahaa,\nmitä en tahdo.',110,NULL),
 (2862,'Anonymous','2008-10-17 16:33:25','Anonymous','2008-10-17 16:33:25',20,'0Mutta jos teen sitä, mitä en tahdo,\nen tee sitä enää itse, vaan sen tekee minussa asuva\nsynti. ',110,NULL),
 (2863,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',21,'1Huomaan siis, että minua hallitsee tällainen laki:\nhaluan tehdä hyvää, mutta en pääse irti pahasta.',110,NULL),
 (2864,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',22,'2Sisimmässäni minä iloiten hyväksyn Jumalan lain,',110,NULL),
 (2865,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',23,'3mutta siinä, mitä teen, näen toteutuvan toisen\nlain, joka sotii sisimpäni lakia vastaan. Näin olen\nruumiissani vaikuttavan synnin lain vanki.',110,NULL),
 (2866,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',24,'4Minä\nkurja ihminen! Kuka pelastaa minut tästä kuoleman\nruumiista?',110,NULL),
 (2867,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',25,'5Kiitos Jumalalle Herramme Jeesuksen\nKristuksen tähden! Niin minun sisimpäni noudattaa\nJumalan lakia, mutta turmeltunut luontoni synnin\nlakia. ',110,NULL),
 (2868,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',1,'Mikään kadotustuomio ei siis kohtaa niitä, jotka\novat Kristuksessa Jeesuksessa.*',111,NULL),
 (2869,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',2,'Hengen laki, joka\nantaa elämän Kristuksen Jeesuksen yhteydessä, on näet\nvapauttanut sinut synnin ja kuoleman laista.',111,NULL),
 (2870,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',3,'Jumala\nteki sen, mihin laki ei pystynyt, koska se oli ihmisen\nturmeltuneen luonnon vuoksi voimaton. Syntien\nsovittamiseksi hän lähetti tänne oman Poikansa\nsyntisten ihmisten kaltaisena. Näin hän tuomitsi\nihmisessä ihmisten synnin,',111,NULL),
 (2871,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',4,'jotta meissä, jotka\nelämme Hengen emmekä lihamme mukaista elämää,\ntoteutuisi lain vaatima vanhurskaus.',111,NULL),
 (2872,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',5,'Lihan mukaan\nelävillä on lihan mukaiset pyrkimykset, Hengen mukaan\nelävillä Hengen mukaiset.',111,NULL),
 (2873,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',6,'Lihan pyrkimykset\ntuottavat kuoleman, Hengen pyrkimykset elämän ja\nrauhan.',111,NULL),
 (2874,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',7,'Lihan pyrkimykset sotivat Jumalaa vastaan,\nsillä ne eivät alistu Jumalan lakiin eivätkä voikaan\nalistua.',111,NULL),
 (2875,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',8,'Ne, jotka elävät turmeltuneen luontonsa\nmukaisesti, eivät voi olla Jumalalle mieleen. ',111,NULL),
 (2876,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',9,'Te ette kuitenkaan elä oman luontonne vaan Hengen\nalaisina, jos kerran Jumalan Henki asuu teissä. Mutta\nse, jolla ei ole Kristuksen Henkeä, ei ole hänen\nomansa.',111,NULL),
 (2877,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',10,'0Jos Kristus on teissä, teidän ruumiinne\ntosin on kuollut synnin vuoksi, mutta Henki luo\nelämää, koska teidät on tehty vanhurskaiksi.',111,NULL),
 (2878,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',11,'1Jos\nsiis teissä asuu Jumalan Henki, hänen, joka herätti\nJeesuksen kuolleista, niin hän, joka herätti\nKristuksen kuolleista, on tekevä eläviksi myös teidän\nkuolevaiset ruumiinne teissä asuvan Henkensä voimalla. ',111,NULL),
 (2879,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',12,'2Meillä on siis velvollisuuksia, veljet, mutta ei\nitsekästä luontoamme kohtaan; ei meidän pidä elää sen\nmukaan.',111,NULL),
 (2880,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',13,'3Jos elätte luontonne mukaan, te kuolette,\nmutta jos Hengen avulla kuoletatte syntiset tekonne,\nte saatte elää.',111,NULL),
 (2881,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',14,'4Kaikki, joita Jumalan Henki\njohtaa, ovat Jumalan lapsia.',111,NULL),
 (2882,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',15,'5Te ette ole saaneet\norjuuden henkeä, joka saattaisi teidät jälleen pelon\nvaltaan. Olette saaneet Hengen, joka antaa meille\nlapsen oikeuden, ja niin me huudamme: \"Abba! Isä!\"',111,NULL),
 (2883,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',16,'6Henki itse todistaa yhdessä meidän henkemme\nkanssa, että olemme Jumalan lapsia.',111,NULL),
 (2884,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',17,'7Mutta jos\nolemme lapsia, olemme myös perillisiä, Jumalan\nperillisiä yhdessä Kristuksen kanssa; jos kerran\nkärsimme yhdessä Kristuksen kanssa, pääsemme myös\nosallisiksi samasta kirkkaudesta kuin hän.   ',111,NULL),
 (2885,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',26,'6Myös Henki auttaa meitä, jotka olemme heikkoja.\nEmmehän tiedä, miten meidän tulisi rukoilla, että\nrukoilisimme oikein. Henki itse kuitenkin puhuu meidän\npuolestamme sanattomin huokauksin.',111,NULL),
 (2886,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',27,'7Ja hän, joka\ntutkii sydämet, tietää mitä Henki tarkoittaa, sillä\nHenki puhuu Jumalan tahdon mukaisesti pyhien puolesta. ',111,NULL),
 (2887,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',28,'8Me tiedämme, että kaikki koituu niiden parhaaksi,\njotka rakastavat Jumalaa*ja jotka hän on\nsuunnitelmansa mukaisesti kutsunut omikseen.',111,NULL),
 (2888,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',29,'9Ne,\njotka hän edeltäkäsin on valinnut, hän on myös edeltä\nmäärännyt oman Poikansa kaltaisiksi, niin että hänen\nPoikansa olisi esikoinen suuressa veljesjoukossa.',111,NULL),
 (2889,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',30,'0Ne, jotka Jumala on edeltä määrännyt, hän on myös\nkutsunut; ne, jotka hän on kutsunut, hän on myös\ntehnyt vanhurskaiksi; niille, jotka hän on tehnyt\nvanhurskaiksi, hän on myös lahjoittanut kirkkautensa.     ',111,NULL),
 (2890,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',37,'7Mutta kaikissa näissä ahdingoissa meille antaa\nriemuvoiton hän, joka on meitä rakastanut.',111,NULL),
 (2891,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',38,'8Olen\nvarma siitä, ettei kuolema eikä elämä, eivät enkelit,\neivät henkivallat, ei mikään nykyinen eikä mikään\ntuleva eivätkä mitkään voimat,',111,NULL),
 (2892,'Anonymous','2008-10-17 16:33:26','Anonymous','2008-10-17 16:33:26',39,'9ei korkeus eikä\nsyvyys, ei mikään luotu voi erottaa meitä Jumalan\nrakkaudesta, joka on tullut ilmi Kristuksessa\nJeesuksessa, meidän Herrassamme. ',111,NULL),
 (2893,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',1,'Vakuutan Kristuksen nimessä, että puhun totta, ja\nPyhän Hengen valaisema omatuntoni todistaa, etten\nvalehtele:',112,NULL),
 (2894,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',2,'Minulla on sydämessäni raskas suru ja\nlakkaamaton tuska.',112,NULL),
 (2895,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',3,'Toivoisin suorastaan, että itse\nolisin kirottu ja erotettu Kristuksen yhteydestä, jos\nse vain auttaisi veljiäni, oman kansani jäseniä.',112,NULL),
 (2896,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',4,'Ovathan he israelilaisia, jotka Jumala on ottanut\nlapsikseen ja joille hän on antanut kirkkautensa.\nHeidän kanssaan hän on tehnyt liitot, heille hän on\nantanut lain, jumalanpalveluksen ja lupaukset.',112,NULL),
 (2897,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',5,'Heidän ovat kantaisät, heistä on Kristus ihmisenä\nlähtöisin, hän, joka on kaiken yläpuolella, ikuisesti\nylistetty Jumala, aamen! ',112,NULL),
 (2898,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',6,'Jumalan sana ei ole voinut raueta tyhjiin. Eivät\nkaikki israelilaiset kuulu tosi Israeliin,',112,NULL),
 (2899,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',7,'eivätkä\nkaikki Abrahamin jälkeläiset ole oikeita Abrahamin\nlapsia. Onhan sanottu: \"Vain Iisakin jälkeläisiä\nsanotaan sinun lapsiksesi.\"',112,NULL),
 (2900,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',8,'Tämä tarkoittaa,\netteivät Jumalan lapsia ole luonnolliset jälkeläiset\nvaan että jälkeläisiksi luetaan lupauksen voimasta\nsyntyneet lapset.',112,NULL),
 (2901,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',9,'Lupaus kuului näin: \"Ensi vuonna\nsamaan aikaan palaan sinun luoksesi, ja silloin\nSaaralla on poika.\"',112,NULL),
 (2902,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',10,'0Eikä tämä ollut ainoa kerta.\nRebekan pojilla oli yksi ja sama isä, meidän\nkantaisämme Iisak.',112,NULL),
 (2903,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',11,'1--',112,NULL),
 (2904,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',12,'2Jo ennen kaksospoikien\nsyntymää, ennen kuin he vielä olivat tehneet mitään\nhyvää tai pahaa, Jumala sanoi Rebekalle: \"Vanhempi on\npalveleva nuorempaa.\" Näin Jumala osoitti, että hänen\nsuunnitelmansa perustui hänen omaan valintaansa, ei\nihmisen tekoihin vaan kutsujan tahtoon.',112,NULL),
 (2905,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',13,'3Onhan\nkirjoitettu: \"Jaakobia minä rakastin, mutta Esauta\nvihasin.\" ',112,NULL),
 (2906,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',14,'4Mitä me tähän sanomme? Ei kai Jumala ole\nepäoikeudenmukainen? Ei suinkaan.',112,NULL),
 (2907,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',15,'5Hän sanoo\nMoosekselle: \"Minä armahdan kenet tahdon ja osoitan\nlaupeutta kenelle tahdon.\"',112,NULL),
 (2908,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',16,'6Ratkaisevaa ei siis\nole, mitä ihminen tahtoo tai ehtii, vaan se että\nJumala armahtaa.',112,NULL),
 (2909,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',17,'7Kirjoituksissa sanotaan faraolle:\n\"Sen vuoksi minä korotin sinut, että sinun\nkohtalossasi osoittaisin voimani ja että minun nimeäni\njulistettaisiin koko maailmassa.\"',112,NULL),
 (2910,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',18,'8Jumala siis\narmahtaa kenet tahtoo ja paaduttaa kenet tahtoo.    ',112,NULL),
 (2911,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',26,'6Ja samassa paikassa, jossa heille sanottiin: \"Te\nette ole minun kansaani\", heitä tullaan sanomaan\nelävän Jumalan lapsiksi. ',112,NULL),
 (2912,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',27,'7Mutta Israelista Jesaja huudahtaa:  ',112,NULL),
 (2913,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',29,'9Jesaja on myös ennustanut:     ',112,NULL),
 (2914,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',1,'Veljet, minä toivon sydämestäni ja rukoilen\nJumalaa, että he pelastuisivat.',113,NULL),
 (2915,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',2,'Voin todistaa, että\nhe ovat täynnä intoa Jumalan puolesta, mutta heiltä\npuuttuu ymmärrys.',113,NULL),
 (2916,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',3,'He eivät tiedä, mitä Jumalan\nvanhurskaus on, ja yrittäessään pystyttää omaa\nvanhurskauttaan he eivät ole alistuneet siihen\nvanhurskauteen, joka tulee Jumalalta.',113,NULL),
 (2917,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',4,'Kristus on\nnäet lain loppu, ja niin tulee vanhurskaaksi jokainen,\njoka uskoo.    nimittäin se uskon sana, jota me julistamme.',113,NULL),
 (2918,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',9,'Jos\nsinä suullasi tunnustat, että Jeesus on Herra, ja\nsydämessäsi uskot, että Jumala on herättänyt hänet\nkuolleista, olet pelastuva.',113,NULL),
 (2919,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',10,'0Sydämen usko tuo\nvanhurskauden, suun tunnustus pelastuksen.',113,NULL),
 (2920,'Anonymous','2008-10-17 16:33:27','Anonymous','2008-10-17 16:33:27',11,'1Kirjoituksissa sanotaan: \"Yksikään, joka häneen\nuskoo, ei joudu häpeään.\"',113,NULL),
 (2921,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',12,'2Juutalaisen ja\nkreikkalaisen välillä ei ole eroa. Kaikilla on sama\nHerra, ja häneltä riittää rikkautta kaikille, jotka\nhuutavat häntä avukseen.',113,NULL),
 (2922,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',13,'3Onhan kirjoitettu:\n\"Jokainen, joka huutaa avukseen Herran nimeä,\npelastuu.\" ',113,NULL),
 (2923,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',14,'4Mutta kuinka he voivat huutaa avukseen sitä, johon\neivät usko? Kuinka he voivat uskoa siihen, josta eivät\nole kuulleet? Kuinka he voivat kuulla, ellei kukaan\njulista?',113,NULL),
 (2924,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',15,'5Kuinka kukaan voi julistaa, ellei häntä\nole lähetetty? Onhan kirjoitettu: \"Kuinka ihanat ovat\nilosanoman tuojan askelet!\"',113,NULL),
 (2925,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',16,'6Mutta kaikki eivät ole\nolleet evankeliumille kuuliaisia. Jesaja sanookin:\n\"Herra, kuka on uskonut meidän sanomamme?\"',113,NULL),
 (2926,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',17,'7Usko\nsyntyy kuulemisesta, mutta kuulemisen synnyttää\nKristuksen sana.',113,NULL),
 (2927,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',18,'8Nyt kysyn: eivätkö he ehkä ole\nkuulleetkaan sitä? Varmasti ovat:  ',113,NULL),
 (2928,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',19,'9Mutta eikö Israel ole sitä käsittänyt? Jo Mooses\nsanoo:  ',113,NULL),
 (2929,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',20,'0Jesaja sanoo vielä rohkeammin:   ',113,NULL),
 (2930,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',21,'1Israelista hän sen sijaan sanoo: \"Päivästä päivään\nolen ojentanut käsiäni uppiniskaista ja vastustelevaa\nkansaa kohti.\" ',113,NULL),
 (2931,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',1,'Kysyn siis: ei kai Jumala ole hylännyt omaa\nkansaansa? Ei toki! Olenhan minäkin israelilainen,\nAbrahamin jälkeläinen, Benjaminin heimoa.',114,NULL),
 (2932,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',2,'Ei Jumala\nole hylännyt kansaansa, jonka hän edeltäkäsin on\nvalinnut. Tiedättehän te, mitä kirjoitukset kertovat\nEliasta. Hän esittää Jumalalle Israelia vastaan tämän\nsyytöksen:',114,NULL),
 (2933,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',3,'\"Herra, he ovat tappaneet sinun\nprofeettasi ja hajottaneet sinun alttarisi. Minä yksin\nolen enää jäljellä, ja he tavoittelevat minunkin\nhenkeäni.\"',114,NULL),
 (2934,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',4,'Mutta minkä vastauksen hän saa\nJumalalta? \"Minä olen jättänyt itselleni\nseitsemäntuhatta miestä, jotka eivät ole polvistuneet\nBaalin edessä.\"',114,NULL),
 (2935,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',5,'Samoin on nykyisenäkin aikana\nolemassa jäännös, jonka Jumala on armossaan valinnut.',114,NULL),
 (2936,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',6,'Mutta jos valinta kerran perustuu armoon, se ei\nperustu ihmisen tekoihin -- muutenhan armo ei olisi\narmo.',114,NULL),
 (2937,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',7,'Israel ei siis ole saavuttanut sitä, mitä se\non tavoitellut; vain valitut ovat sen saaneet. Muut\novat paatuneet,',114,NULL),
 (2938,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',8,'niin kuin on kirjoitettu:  ',114,NULL),
 (2939,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',9,'Ja Daavid sanoo:    ',114,NULL),
 (2940,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',13,'3Teille pakanuudesta kääntyneille sanon: minä\nylistän tehtävääni kansojen apostolina,',114,NULL),
 (2941,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',14,'4sillä näin\nherätän ehkä heimolaisissani kateutta teitä kohtaan ja\nvoin pelastaa joitakuita heistä.',114,NULL),
 (2942,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',15,'5Jos juutalaisten\nhylkääminen on avannut maailmalle pääsyn sovintoon,\nniin mitä tapahtuukaan, kun Jumala ottaa heidät\nyhteyteensä? Silloin kuolleet heräävät eloon!',114,NULL),
 (2943,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',16,'6Jos\nensimmäinen leipä pyhitetään, on koko leipomus pyhä,\nja jos puun juuri on pyhä, ovat myös oksat pyhät. ',114,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (2944,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',17,'7Jos jalosta oliivipuusta on katkaistu oksia ja jos\nsinut, joka olet peräisin villistä oliivipuusta, on\noksastettu oikeiden oksien joukkoon niin että olet\npäässyt osalliseksi puun juurinesteestä,',114,NULL),
 (2945,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',18,'8älä\nylvästele alkuperäisten oksien rinnalla! Mutta jos\nylvästelet, muista, ettet sinä kannata juurta vaan\njuuri kannattaa sinua.',114,NULL),
 (2946,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',19,'9Sanot ehkä, että nuo oksat\nkatkaistiin, jotta sinut voitaisiin oksastaa.',114,NULL),
 (2947,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',20,'0Se\non totta. Ne katkaistiin pois epäuskonsa tähden, mutta\nsinä pysyt, kun uskot. Älä silti ole ylimielinen, vaan\npelkää!',114,NULL),
 (2948,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',21,'1Jos Jumala ei säästänyt luonnollisia\noksia, ei hän säästä sinuakaan.',114,NULL),
 (2949,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',22,'2Katso, kuinka\nJumala on sekä lempeä että ankara. Langenneita kohtaan\nhän on ankara, sinua kohtaan lempeä, jos pysyt kiinni\nhänen hyvyydessään; muuten sinutkin leikataan pois.',114,NULL),
 (2950,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',23,'3Mutta myös nuo toiset oksastetaan uudelleen,\nelleivät he jää epäuskonsa valtaan, sillä Jumala\nkykenee liittämään heidät takaisin runkoon.',114,NULL),
 (2951,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',24,'4Jos\nkerran sinut on leikattu irti villistä oliivipuusta,\njohon luonnonmukaisesti kuuluit, ja vastoin luontoa\noksastettu jaloon oliivipuuhun, niin totta kai nämä\nalkuperäiset oksat voidaan liittää takaisin omaan\npuuhunsa.     ',114,NULL),
 (2952,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',28,'8Evankeliumin torjuessaan he ovat Jumalan\nvihollisia, teidän tähtenne, mutta valinnan\nperusteella he ovat Jumalalle rakkaita, isien tähden.',114,NULL),
 (2953,'Anonymous','2008-10-17 16:33:28','Anonymous','2008-10-17 16:33:28',29,'9Jumala ei peruuta lahjojaan eikä antamaansa\nkutsua.',114,NULL),
 (2954,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',30,'0Te olitte ennen tottelemattomia Jumalaa\nkohtaan, mutta noiden toisten tottelemattomuuden\nvuoksi Jumala on nyt armahtanut teidät.',114,NULL),
 (2955,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',31,'1Samalla\ntavoin he ovat nyt olleet tottelemattomia, jotta myös\nheidät armahdettaisiin, kun te olette saaneet\narmahduksen.',114,NULL),
 (2956,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',32,'2Jumala on näet tehnyt kaikki\ntottelemattomuuden vangeiksi, jotta hän voisi antaa\nkaikille armahduksen.   ',114,NULL),
 (2957,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',36,'6Hänestä, hänen kauttaan ja häneen on kaikki. Hänen\non kunnia ikuisesti. Aamen. ',114,NULL),
 (2958,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',1,'Jumalan armahtavaan laupeuteen vedoten kehotan\nteitä, veljet: Antakaa koko elämänne pyhäksi ja\neläväksi, Jumalalle mieluisaksi uhriksi. Näin te\npalvelette Jumalaa järjellisellä tavalla.',115,NULL),
 (2959,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',2,'Älkää\nmukautuko tämän maailman menoon, vaan muuttukaa,\nuudistukaa mieleltänne, niin että osaatte arvioida,\nmikä on Jumalan tahto, mikä on hyvää, hänen mielensä\nmukaista ja täydellistä. ',115,NULL),
 (2960,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',3,'Sen armon perusteella, joka minulle on annettu,\nsanon teille jokaiselle: älkää ajatelko itsestänne\nliikoja, enempää kuin on aihetta ajatella, vaan\npitäkää ajatuksenne kohtuuden rajoissa, kukin sen\nuskon määrän mukaan, jonka Jumala on hänelle antanut.',115,NULL),
 (2961,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',4,'Niin kuin meillä jokaisella on yksi ruumis ja siinä\nmonta jäsentä, joilla on eri tehtävänsä,',115,NULL),
 (2962,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',5,'samoin me\nkaikki olemme Kristuksessa yksi ruumis mutta olemme\nkukin toistemme jäseniä.',115,NULL),
 (2963,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',6,'Meillä on saamamme armon\nmukaan erilaisia armolahjoja. Se, jolla on\nprofetoimisen lahja, käyttäköön sitä sen mukaan kuin\nhänellä on uskoa.',115,NULL),
 (2964,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',7,'Palvelutehtävän saanut\npalvelkoon, opetustehtävän saanut opettakoon,',115,NULL),
 (2965,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',8,'rohkaisemisen lahjan saanut rohkaiskoon. Joka antaa\nomastaan, antakoon pyyteettömästi; joka johtaa,\njohtakoon tarmokkaasti; joka auttaa köyhiä, auttakoon\niloisin mielin. ',115,NULL),
 (2966,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',9,'Olkoon rakkautenne vilpitöntä. Vihatkaa pahaa,\npysykää kiinni hyvässä.',115,NULL),
 (2967,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',10,'0Osoittakaa toisillenne\nlämmintä veljesrakkautta, kunnioittakaa kilvan toinen\ntoistanne.',115,NULL),
 (2968,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',11,'1Älkää olko velttoja, olkaa innokkaita,\npalakoon teissä Hengen tuli, palvelkaa Herraa.',115,NULL),
 (2969,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',12,'2Toivokaa ja iloitkaa, ahdingossa olkaa kestäviä,\nrukoilkaa hellittämättä.',115,NULL),
 (2970,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',13,'3Auttakaa puutteessa\nolevia pyhiä, osoittakaa vieraanvaraisuutta.',115,NULL),
 (2971,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',14,'4Siunatkaa niitä, jotka teitä vainoavat, siunatkaa\nälkääkä kirotko.',115,NULL),
 (2972,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',15,'5Iloitkaa iloitsevien kanssa,\nitkekää itkevien kanssa. ',115,NULL),
 (2973,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',16,'6Olkaa keskenänne yksimielisiä. Älkää pitäkö\nitseänne muita parempina, vaan asettukaa vähäosaisten\nrinnalle. Älkää olko omasta mielestänne viisaita.',115,NULL),
 (2974,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',17,'7Älkää maksako kenellekään pahaa pahalla, vaan\npyrkikää siihen, mikä on hyvää kaikkien silmissä.',115,NULL),
 (2975,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',18,'8Jos on mahdollista ja jos teistä riippuu, eläkää\nrauhassa kaikkien kanssa.',115,NULL),
 (2976,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',19,'9Älkää ottako oikeutta\nomiin käsiinne, rakkaat ystävät, vaan antakaa Jumalan\nosoittaa vihansa. Onhan kirjoitettu: \"Minun on tuomio,\nminä maksan tekojen mukaan\" -- näin sanoo Herra.',115,NULL),
 (2977,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',20,'0Edelleen sanotaan: \"Jos vihamiehelläsi on nälkä,\nanna hänelle ruokaa, jos hänellä on jano, anna\njuotavaa. Näin keräät tulisia hiiliä hänen päänsä\npäälle.\"',115,NULL),
 (2978,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',21,'1Älä anna pahan voittaa itseäsi, vaan\nvoita sinä paha hyvällä. ',115,NULL),
 (2979,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',1,'Jokaisen on suostuttava esivaltansa alaisuuteen.\nEihän ole esivaltaa, joka ei olisi Jumalalta peräisin,\nhäneltä ovat vallankäyttäjät saaneet valtuutensa.',116,NULL),
 (2980,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',2,'Joka vastustaa esivaltaa, nousee siis Jumalan\nsäädöstä vastaan, ja ne, jotka näin tekevät, saavat\nrangaistuksensa.',116,NULL),
 (2981,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',3,'Ei sen, joka tekee oikein,\ntarvitse pelätä viranomaisia, vaan sen, joka tekee\nväärin. Jos siis tahdot elää pelkäämättä esivaltaa,\ntee oikein! Silloin saat siltä kiitosta.',116,NULL),
 (2982,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',4,'Se on\nJumalan palvelija ja toimii sinun parhaaksesi. Mutta\njos teet väärin, pelkää! Esivalta ei kanna miekkaa\nturhaan. Se on Jumalan palvelija ja panee täytäntöön\nväärintekijälle kuuluvan rangaistuksen.',116,NULL),
 (2983,'Anonymous','2008-10-17 16:33:29','Anonymous','2008-10-17 16:33:29',5,'Siksi on\nsuostuttava esivallan alaisuuteen, ei vain\nrangaistuksen pelosta vaan myös omantunnon\nvaatimuksesta.',116,NULL),
 (2984,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',6,'Sen vuoksi te verojakin maksatte,\nsillä viranomaiset ovat Jumalan palveluksessa, kun he\nhoitavat tehtäviään.',116,NULL),
 (2985,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',7,'Antakaa jokaiselle se, mikä\nhänelle kuuluu: kenelle vero, sille vero, kenelle\ntulli, sille tulli, kenelle pelko, sille pelko,\nkenelle kunnia, sille kunnia. ',116,NULL),
 (2986,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',8,'Älkää olko kenellekään mitään velkaa, paitsi että\nrakastatte toisianne. Joka rakastaa toista, on\ntäyttänyt lain vaatimukset.',116,NULL),
 (2987,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',9,'Käskyt \"Älä tee\naviorikosta\", \"Älä tapa\", \"Älä varasta\", \"Älä\nhimoitse\", samoin kaikki muutkin, voidaan koota tähän\nsanaan: \"Rakasta lähimmäistäsi niin kuin itseäsi.\"',116,NULL),
 (2988,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',10,'0Rakkaus ei tee lähimmäiselle mitään pahaa. Näin\nrakkaus toteuttaa koko lain.   ',116,NULL),
 (2989,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',1,'Hyväksykää joukkoonne myös sellainen, joka on\nuskossaan heikko, älkääkä ruvetko kiistelemään\nmielipiteistä.',117,NULL),
 (2990,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',2,'Joku katsoo voivansa syödä kaikkea,\nmutta heikkouskoinen syö vain kasviksia.',117,NULL),
 (2991,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',3,'Joka syö\nkaikkea, älköön halveksiko sitä joka ei syö, ja joka\ntaas ei syö kaikkea, älköön tuomitko sitä joka syö.\nOnhan Jumala ottanut omakseen hänetkin.',117,NULL),
 (2992,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',4,'Mikä oikeus\nsinulla on tuomita toisen palvelijaa? Oman isäntänsä\nedessä hän seisoo tai kaatuu -- vaikka kyllä hän\nseisoo, sillä Herra kykenee pitämään hänet pystyssä. ',117,NULL),
 (2993,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',5,'Joku pitää yhtä päivää toista parempana, toiselle\nkaikki päivät ovat samanarvoisia. Kukin olkoon omassa\nvakaumuksessaan varma.',117,NULL),
 (2994,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',6,'Joka kiinnittää huomionsa\npäiviin, tekee niin Herran kunniaksi, ja joka syö, syö\nHerran kunniaksi, sillä hän kiittää Jumalaa. Joka taas\nei syö, on Herran kunniaksi syömättä, ja hänkin\nkiittää Jumalaa.',117,NULL),
 (2995,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',7,'Kukaan meistä ei elä itseään\nvarten eikä kukaan kuole itseään varten.',117,NULL),
 (2996,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',8,'Jos\nelämme, elämme Herran omina, ja jos kuolemme, kuolemme\nHerran omina. Elämmepä siis tai kuolemme, me kuulumme\nHerralle.',117,NULL),
 (2997,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',9,'Juuri sitä vartenhan Kristus kuoli ja\nheräsi elämään, että hän olisi niin kuolleiden kuin\nelävienkin Herra.',117,NULL),
 (2998,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',10,'0Kuinka sinä voit tuomita\nveljesi? Tai sinä toinen, kuinka voit halveksia\nveljeäsi? Kaikki me joudumme Jumalan tuomioistuimen\neteen.',117,NULL),
 (2999,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',11,'1Onhan kirjoitettu:  ',117,NULL),
 (3000,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',12,'2Jokainen meistä joutuu tekemään Jumalalle tilin\nitsestään.   ',117,NULL),
 (3001,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',19,'9Pyrkikäämme siis rakentamaan rauhaa ja\nvahvistamaan toisiamme.',117,NULL),
 (3002,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',20,'0Älä ruoan vuoksi hajota\nJumalan työtä. Kaikki tosin on puhdasta, mutta\nihmiselle on pahaksi, jos hän syö jotakin, mikä\nloukkaa omaatuntoa.',117,NULL),
 (3003,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',21,'1Sinun on hyvä olla syömättä\nlihaa ja juomatta viiniä ja välttää muutakin, mikä\nloukkaa veljeäsi.',117,NULL),
 (3004,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',22,'2Säilytä sinä oma uskosi Jumalan\nedessä. Onnellinen se, joka ei tuomitse itseään siitä\nminkä uskoo oikeaksi.',117,NULL),
 (3005,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',23,'3Mutta se, joka epäröi ja\nsilti syö, on tuomittu, koska hän ei toimi uskon\nperusteella. Kaikki, mikä ei perustu uskoon, on\nsyntiä. ',117,NULL),
 (3006,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',1,'Meidän, jotka olemme vahvoja, on kestettävä\nheikkojen vajavuuksia. Emme saa ajatella vain sitä,\nmikä on itsellemme mieluista.',118,NULL),
 (3007,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',2,'Meidän on jokaisen\notettava huomioon lähimmäisemme, ajateltava, mikä on\nhänelle hyväksi ja vahvistaa häntä.',118,NULL),
 (3008,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',3,'Ei Kristuskaan\najatellut itseään. Onhan kirjoitettu: \"Sinun\nherjaajiesi herjaukset ovat osuneet minuun.\"',118,NULL),
 (3009,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',4,'Kaikki, mitä pyhät kirjoitukset sisältävät, on\nkirjoitettu meille opiksi, jotta saisimme siitä\nkestävyyttä, lohtua ja toivoa.',118,NULL),
 (3010,'Anonymous','2008-10-17 16:33:30','Anonymous','2008-10-17 16:33:30',5,'Jumala, jolta\nkestävyys ja rohkaisu tulevat, antakoon teidän olla\nkeskenänne yksimielisiä Kristuksen Jeesuksen tahdon\nmukaisesti,',118,NULL),
 (3011,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',6,'niin että te yksimielisesti, yhdestä\nsuusta ylistäisitte Herramme Jeesuksen Kristuksen\nJumalaa ja Isää. ',118,NULL),
 (3012,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',7,'Hyväksykää siis toinen toisenne, niin kuin\nKristuskin on hyväksynyt omikseen teidät, Jumalan\nkunniaksi.',118,NULL),
 (3013,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',8,'Tarkoitan tätä: Kristus on tullut\npalvelemaan juutalaisia. Hän on tullut vahvistamaan\nisille annetut lupaukset osoittaakseen, että Jumala\npysyy sanassaan.',118,NULL),
 (3014,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',9,'Hän on tullut myös, jotta muut\nkansat saisivat ylistää Jumalaa hänen laupeudestaan,\nniin kuin on kirjoitettu:  ',118,NULL),
 (3015,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',10,'0Ja edelleen:  ',118,NULL),
 (3016,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',11,'1Ja vielä:  ',118,NULL),
 (3017,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',12,'2Jesaja taas sanoo:   ',118,NULL),
 (3018,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',13,'3Toivon Jumala täyttäköön ilolla ja rauhalla\nteidät, jotka uskotte, niin että teillä Pyhän Hengen\nvoimasta olisi runsas toivo.      ',118,NULL),
 (3019,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',25,'5Nyt olen kuitenkin lähdössä Jerusalemiin viemään\navustusta pyhille.',118,NULL),
 (3020,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',26,'6Makedonian ja Akhaian\nseurakunnat ovat näet päättäneet antaa yhteisen lahjan\nJerusalemin seurakunnan köyhille.',118,NULL),
 (3021,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',27,'7Näin ne siis\novat päättäneet, ja ne ovatkin tämän heille velkaa.\nJos kerran pakanuudesta kääntyneet ovat päässeet\nosallisiksi Jerusalemin pyhien hengellisistä\naarteista, heillä on puolestaan velvollisuus auttaa\nnäitä aineellisesti.',118,NULL),
 (3022,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',28,'8Sitten kun olen hoitanut\ntämän tehtävän ja asianmukaisesti luovuttanut\nkeräyksen tuoton, lähden Espanjaan ja käyn matkalla\nteidän luonanne.',118,NULL),
 (3023,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',29,'9Tiedän, että tullessani luoksenne\ntuon mukanani Kristuksen täyden siunauksen. ',118,NULL),
 (3024,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',30,'0Herramme Jeesuksen Kristuksen nimessä ja Pyhän\nHengen antamaan rakkauteen vedoten pyydän teitä,\nveljet, tukemaan minua taistelussani. Rukoilkaa\nJumalaa puolestani,',118,NULL),
 (3025,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',31,'1että en Juudeassa joutuisi\nepäuskoisten käsiin ja että lahja, jonka vien\nJerusalemiin, olisi pyhille mieluinen.',118,NULL),
 (3026,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',32,'2Silloin\nvoin, jos Jumala suo, tulla iloisin mielin teidän\nluoksenne ja virkistyä seurassanne.',118,NULL),
 (3027,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',33,'3Rauhan Jumala\nolkoon teidän kaikkien kanssa. Aamen. ',118,NULL),
 (3028,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',1,'Annan teille suositukseni sisarestamme Foibesta,\njoka on Kenkrean seurakunnan palvelija.',119,NULL),
 (3029,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',2,'Ottakaa\nhänet vastaan Herran palvelijana niin kuin pyhille\nsopii ja auttakaa häntä kaikessa, missä hän tarvitsee\nteitä. Onhan hän itsekin ollut tukena monille, myös\nminulle. ',119,NULL),
 (3030,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',3,'Tervehtikää Priscaa ja Aquilaa, työtovereitani\nKristuksen Jeesuksen palveluksessa.',119,NULL),
 (3031,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',4,'He ovat panneet\nhenkensä alttiiksi minun takiani, ja heitä kiittävät\nminun lisäkseni myös kaikki pakanuudesta kääntyneiden\nseurakunnat.',119,NULL),
 (3032,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',5,'Sanokaa terveisiä myös heidän\ntalossaan kokoontuvalle seurakunnalle. Terveisiä\nystävälleni Epainetokselle, joka on Aasian työn ensi\nhedelmä Kristukselle.',119,NULL),
 (3033,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',6,'Terveisiä Marialle, joka on\nnähnyt paljon vaivaa teidän hyväksenne.',119,NULL),
 (3034,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',7,'Terveisiä\nAndronikokselle ja Junialle*, heimolaisilleni ja\nvankilatovereilleni, jotka ovat arvossa pidettyjä\napostolien joukossa ja ovat olleet Kristuksen omia jo\nennen minua. ',119,NULL),
 (3035,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',8,'Terveisiä Ampliatukselle, rakkaalle\nuskonveljelleni.',119,NULL),
 (3036,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',9,'Terveisiä Urbanukselle,\ntyötoverillemme Kristuksen palveluksessa, ja\nystävälleni Stakykselle.',119,NULL),
 (3037,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',10,'0Terveisiä Apellekselle,\njoka on koeteltu Kristuksen palvelija. Terveisiä\nAristobuloksen perheväelle.',119,NULL),
 (3038,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',11,'1Terveisiä\nheimolaiselleni Herodionille. Terveisiä Narkissoksen\ntalonväkeen kuuluville, jotka ovat Herran omia.',119,NULL),
 (3039,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',12,'2Terveisiä Tryfainalle ja Tryfosalle, jotka tekevät\nahkerasti Herran työtä. Terveisiä Persikselle,\nrakkaalle sisarelle, joka on nähnyt paljon vaivaa\nHerran työssä.',119,NULL),
 (3040,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',13,'3Terveisiä Rufukselle, jonka Herra\non valinnut, ja hänen äidilleen, joka on kuin äiti\nminullekin.',119,NULL),
 (3041,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',14,'4Terveisiä Asynkritokselle, Flegonille,\nHermekselle, Patrobakselle, Hermakselle ja heidän\nkanssaan oleville veljille.',119,NULL),
 (3042,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',15,'5Terveisiä\nFilologokselle ja Julialle, Nereukselle ja hänen\nsisarelleen sekä Olympakselle ja kaikille muillekin\npyhille.',119,NULL),
 (3043,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',16,'6Tervehtikää toisianne pyhällä\nsuudelmalla. Kaikki Kristuksen seurakunnat lähettävät\nteille tervehdyksensä. ',119,NULL),
 (3044,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',17,'7Kehotan teitä, veljet, varomaan niitä, jotka\nsaavat aikaan eripuraisuutta ja houkuttelevat teitä\nluopumaan niistä opetuksista, jotka olette saaneet.\nPysykää heistä erossa.',119,NULL),
 (3045,'Anonymous','2008-10-17 16:33:31','Anonymous','2008-10-17 16:33:31',18,'8Sellaiset eivät palvele\nmeidän Herraamme Kristusta vaan omaa vatsaansa, ja\nkauniilla ja vakuuttavilla puheillaan he pettävät\nvilpittömien sydämet.',119,NULL),
 (3046,'Anonymous','2008-10-17 16:33:32','Anonymous','2008-10-17 16:33:32',19,'9Teidän kuuliaisuutenne on\ntullut kaikkien tietoon. Siksi olen teistä iloinen ja\ntoivon, että olisitte taitavia hyvässä mutta\ntaitamattomia pahassa.',119,NULL),
 (3047,'Anonymous','2008-10-17 16:33:32','Anonymous','2008-10-17 16:33:32',20,'0Rauhan Jumala on pian\nmurskaava Saatanan teidän jalkojenne alle. Herramme\nJeesuksen armo olkoon teidän kanssanne. ',119,NULL),
 (3048,'Anonymous','2008-10-17 16:33:32','Anonymous','2008-10-17 16:33:32',21,'1Työtoverini Timoteus lähettää teille terveisiä,\nsamoin lähettävät maanmieheni Lukios, Jason ja\nSosipatros. --',119,NULL),
 (3049,'Anonymous','2008-10-17 16:33:32','Anonymous','2008-10-17 16:33:32',22,'2Terveisiä myös minulta,\nuskonveljeltänne Tertiukselta, joka olen sanelun\nmukaan kirjoittanut tämän kirjeen. --',119,NULL),
 (3050,'Anonymous','2008-10-17 16:33:32','Anonymous','2008-10-17 16:33:32',23,'3Teille\nlähettää terveisiä Gaius, joka on avannut kotinsa\nminulle ja koko seurakunnalle. Terveisiä myös\nkaupungin varainhoitajalta Erastokselta ja veli\nQuartukselta.*    ',119,NULL),
 (3051,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',1,'Paavali, Jumalan tahdosta Kristuksen Jeesuksen\napostoliksi kutsuttu, ja veli Sostenes',120,NULL),
 (3052,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',2,'tervehtivät\nKorintissa olevaa Jumalan seurakuntaa, Kristuksen\nJeesuksen pyhittämiä, Jumalan kutsumia pyhiä sekä\nkaikkia, jotka eri seuduilla, missä asuvatkin,\nhuutavat avukseen Herramme Jeesuksen Kristuksen nimeä\nsamoin kuin me.',120,NULL),
 (3053,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',3,'Jumalan, meidän Isämme, ja Herran\nJeesuksen Kristuksen armo ja rauha teille. ',120,NULL),
 (3054,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',4,'Minä kiitän aina teidän tähtenne Jumalaani siitä\narmosta, joka teille on annettu Kristuksessa\nJeesuksessa.',120,NULL),
 (3055,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',5,'Hänen yhteydessään te olette saaneet\nkaikkea rikkautta, niin tietoa kuin puhetaitoakin.',120,NULL),
 (3056,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',6,'Sanoma Kristuksesta on juurtunut lujasti teihin,',120,NULL),
 (3057,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',7,'ja teillä on armolahjojen koko rikkaus\nodottaessanne Herramme Jeesuksen Kristuksen\nilmestymistä.',120,NULL),
 (3058,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',8,'Jumala on myös vahvistava teitä\nloppuun saakka, niin ettei teissä ole moitteen sijaa\nHerramme Jeesuksen Kristuksen tulemisen päivänä.',120,NULL),
 (3059,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',9,'Jumala on kutsunut teidät Poikansa Kristuksen\nJeesuksen, meidän Herramme, yhteyteen, ja hän pysyy\nsanassaan.      ',120,NULL),
 (3060,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',20,'0Missä ovat viisaat ja oppineet, missä tämän\nmaailman älyniekat? Eikö Jumala ole tehnyt maailman\nviisautta hulluudeksi?',120,NULL),
 (3061,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',21,'1Jumala on kyllä osoittanut\nviisautensa, mutta kun maailma ei omassa viisaudessaan\noppinut tuntemaan Jumalaa, Jumala katsoi hyväksi\njulistaa hulluutta ja näin pelastaa ne, jotka uskovat.',120,NULL),
 (3062,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',22,'2Juutalaiset vaativat ihmetekoja, ja kreikkalaiset\netsivät viisautta.',120,NULL),
 (3063,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',23,'3Me sen sijaan julistamme\nristiinnaulittua Kristusta. Juutalaiset torjuvat sen\nherjauksena, ja muiden mielestä se on hulluutta,',120,NULL),
 (3064,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',24,'4mutta kutsutuille, niin juutalaisille kuin\nkreikkalaisillekin, ristiinnaulittu Kristus on Jumalan\nvoima ja Jumalan viisaus.',120,NULL),
 (3065,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',25,'5Jumalan hulluus on\nihmisiä viisaampi ja Jumalan heikkous ihmisiä\nvoimakkaampi. ',120,NULL),
 (3066,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',26,'6Ajatelkaa, veljet, mitä te olitte, kun teidät\nkutsuttiin: teissä ei ollut monta ihmisten mielestä\nviisasta, ei monta vaikutusvaltaista, ei monta\njalosukuista.',120,NULL),
 (3067,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',27,'7Mikä maailmassa on hulluutta, sen\nJumala valitsi saattaakseen viisaat häpeään. Mikä\nmaailmassa on heikkoa, sen Jumala valitsi saattaakseen\nhäpeään sen, mikä on voimakasta.',120,NULL),
 (3068,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',28,'8Mikä maailmassa\non vähäpätöistä ja halveksittua, mikä ei ole yhtään\nmitään, sen Jumala valitsi tehdäkseen tyhjäksi sen,\nmikä on jotakin.',120,NULL),
 (3069,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',29,'9Näin ei yksikään ihminen voi\nylpeillä Jumalan edessä.',120,NULL),
 (3070,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',30,'0Jumalan vaikutusta on se,\nmitä te Kristuksessa Jeesuksessa olette. Hänet Jumala\non antanut meille viisaudeksi, vanhurskaudeksi,\npyhitykseksi ja lunastukseksi.',120,NULL),
 (3071,'Anonymous','2008-10-17 16:33:33','Anonymous','2008-10-17 16:33:33',31,'1Näin toteutuu\nkirjoitus: \"Joka ylpeilee, ylpeilköön Herrasta.\" ',120,NULL),
 (3072,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',1,'Kun tulin luoksenne, veljet, en julistanut Jumalan\nsalaisuutta teille suurenmoisen puhetaidon tai\nviisauden keinoin.',121,NULL),
 (3073,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',2,'En halunnut tietää teidän\nluonanne mistään muusta kuin Jeesuksesta Kristuksesta,\nen muusta kuin ristiinnaulitusta Kristuksesta.',121,NULL),
 (3074,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',3,'Te\nnäitte minut hyvin heikkona, hyvin arkana ja\npelokkaana.',121,NULL),
 (3075,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',4,'Puheeni ja julistukseni ei pyrkinyt\nvakuuttamaan viisaudellaan vaan ilmensi Jumalan Hengen\nvoimaa,',121,NULL),
 (3076,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',5,'jotta teidän uskonne ei perustuisi ihmisten\nviisauteen vaan Jumalan voimaan.    ',121,NULL),
 (3077,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',10,'0Meille Jumala on sen ilmoittanut Hengellään, sillä\nHenki tutkii kaiken, myös Jumalan syvimmät\nsalaisuudet. ',121,NULL),
 (3078,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',11,'1Kuka muu kuin ihmisen oma henki tietää, mitä\nihmisessä on? Samoin vain Jumalan Henki tietää, mitä\nJumalassa on.',121,NULL),
 (3079,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',12,'2Mutta me emme ole saaneet maailman\nhenkeä, vaan Jumalan oman Hengen, jotta tietäisimme,\nmitä hän on lahjoittanut meille.',121,NULL),
 (3080,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',13,'3Siitä me myös\npuhumme, mutta me käytämme Hengen emmekä\nihmisviisauden opettamia sanoja ja selitämme\nhengelliset asiat Hengen avulla.',121,NULL),
 (3081,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',14,'4Ihminen ei\nluonnostaan ota vastaan Jumalan Hengen puhetta, sillä\nse on hänen mielestään hulluutta. Hän ei pysty\ntajuamaan sitä, koska sitä on tutkittava Hengen\navulla.',121,NULL),
 (3082,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',15,'5Hengellinen ihminen sen sijaan pystyy\ntutkimaan kaikkea, mutta häntä itseään ei kukaan voi\ntutkia.  Mutta meillä on Kristuksen mieli. ',121,NULL),
 (3083,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',1,'Minä en voinut puhua teille, veljet, niin kuin\nhengellisille ihmisille puhutaan, vaan niin kuin\npuhutaan vanhan luontonsa vallassa oleville, niille\njotka ovat Kristuksen tuntemisessa vielä pikkulapsia.',122,NULL),
 (3084,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',2,'Annoin teille ravinnoksi maitoa, en vahvaa ruokaa,\nsillä sitä te ette olisi vielä kestäneet. Ette kestä\nsitä vielä nytkään,',122,NULL),
 (3085,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',3,'sillä olette yhä vanhan\nluontonne vallassa. Kun teillä kerran on keskinäistä\nkateutta ja riitaa, silloinhan vanha luontonne\nvallitsee teitä ja te elätte niin kuin ihmiset elävät.',122,NULL),
 (3086,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',4,'Jos yksi sanoo: \"Minä olen Paavalin puolella\",\ntoinen taas: \"Minä Apolloksen\", ettekö silloin ole\nkuin ihmiset ainakin?',122,NULL),
 (3087,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',5,'Mikä sitten Apollos on? Tai\nPaavali? He ovat palvelijoita, jotka ovat johtaneet\nteidät uskoon, kumpikin siinä tehtävässä, jonka Herra\non hänelle antanut.',122,NULL),
 (3088,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',6,'Minä istutin, Apollos kasteli,\nmutta Jumala antoi kasvun.',122,NULL),
 (3089,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',7,'Istuttaja ei siis ole\nmitään, ei myöskään kastelija, vaan kaikki on Jumalan\nkädessä, hän suo kasvun.',122,NULL),
 (3090,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',8,'Istuttaja ja kastelija\novat samassa työssä, mutta kumpikin saa palkan oman\ntyönsä mukaan.',122,NULL),
 (3091,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',9,'Me olemme Jumalan työtovereita, te\nolette Jumalan pelto ja Jumalan rakennus. ',122,NULL),
 (3092,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',10,'0Jumalalta saamani armon mukaan olen taitavan\nrakentajan tavoin laskenut perustuksen, jolle joku\ntoinen rakentaa. Mutta kukin katsokoon, miten\nrakentaa.',122,NULL),
 (3093,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',11,'1Perustus on jo laskettu, ja se on Jeesus\nKristus. Muuta perustusta ei kukaan voi laskea.',122,NULL),
 (3094,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',12,'2Rakennetaanpa tälle perustukselle kullasta,\nhopeasta, jalokivistä, puusta, heinistä tai oljista,',122,NULL),
 (3095,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',13,'3aikanaan tulee ilmi, mitä kukin on saanut aikaan.\nTuomiopäivä sen paljastaa: se päivä ilmestyy\ntulenliekeissä, ja tuli koettelee, millainen itse\nkunkin aikaansaannos on.',122,NULL),
 (3096,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',14,'4Se, jonka rakennus\nkestää, saa palkan.',122,NULL),
 (3097,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',15,'5Se taas, jonka rakennus palaa,\nkärsii vahingon. Itse hän tosin pelastuu, mutta kuin\ntulen läpi. ',122,NULL),
 (3098,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',16,'6Ettekö tiedä, että te olette Jumalan temppeli ja\nettä Jumalan Henki asuu teissä?',122,NULL),
 (3099,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',17,'7Jos joku turmelee\nJumalan temppelin, Jumala saattaa turmioon hänet.\nJumalan temppeli on pyhä, ja tämä temppeli olette te. ',122,NULL),
 (3100,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',18,'8Älköön vain kukaan pettäkö itseään. Jos joku\nteistä on olevinaan viisas tässä maailmassa, hänestä\ntäytyy ensin tulla hullu, jotta hänestä tulisi viisas.',122,NULL),
 (3101,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',19,'9Tämän maailman viisaus on näet Jumalan silmissä\nhulluutta. Onhan kirjoitettu:  ',122,NULL),
 (3102,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',20,'0Ja samoin:  ',122,NULL),
 (3103,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',21,'1Kenenkään ei siis pidä ylpeillä ihmisistä.\nKaikkihan on teidän:',122,NULL),
 (3104,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',22,'2Paavali, Apollos ja Keefas,\nmaailma, elämä ja kuolema, kaikki nykyinen ja tuleva.\nSe kaikki on teidän,',122,NULL),
 (3105,'Anonymous','2008-10-17 16:33:34','Anonymous','2008-10-17 16:33:34',23,'3mutta te olette Kristuksen,\nja Kristus on Jumalan. ',122,NULL),
 (3106,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',1,'Meitä on siis pidettävä Kristuksen palvelijoina,\njoiden huostaan on uskottu Jumalan salaisuudet.',123,NULL),
 (3107,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',2,'Siltä, jolle on jotakin uskottu, vaaditaan, että\nhän osoittautuu luottamuksen arvoiseksi.',123,NULL),
 (3108,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',3,'Mutta\nminulle on yhdentekevää, ryhdyttekö te tai jokin\nihmisten tuomioistuin minua tutkimaan. En itsekään\nryhdy tutkimaan itseäni.',123,NULL),
 (3109,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',4,'Minulla ei ole mitään\ntunnollani, mutta ei minua vielä sen perusteella ole\ntodettu syyttömäksi. Minun tuomarini on Herra.',123,NULL),
 (3110,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',5,'Älkää siis tuomitko ennenaikaisesti, ennen kuin\nHerra tulee. Hän valaisee pimeyden kätköt ja tuo esiin\nsydänten ajatukset, ja silloin itse kukin saa\nkiitoksen Jumalalta. ',123,NULL),
 (3111,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',6,'Veljet, olen käyttänyt itsestäni ja Apolloksesta\ntällaista vertauskuvaa teidän takianne, jotta meistä\noppisitte ymmärtämään sanat: \"Tyytykää siihen mitä on\nkirjoitettu.\" Älkää siis mahtaillen asettuko yhden\npuolelle toista vastaan.',123,NULL),
 (3112,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',7,'Kuka antaa sinulle\nerikoisaseman? Onko sinulla mitään, mitä et ole saanut\nlahjaksi? Jos kerran olet saanut kaiken lahjaksi,\nmiksi ylpeilet niin kuin se olisi omaa ansiotasi?',123,NULL),
 (3113,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',8,'Te olette näköjään jo kylläisiä, teistä on tullut\nrikkaita, olette päässeet kuninkaiksi -- toisin kuin\nme. Kunpa olisittekin kuninkaita, niin että mekin\nsaisimme kuninkaina hallita teidän kanssanne!',123,NULL),
 (3114,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',9,'Jumala näyttää asettaneen meidät apostolit\nvihoviimeisiksi: olemme kuin kuolemaantuomitut\nareenalla, koko maailman katseltavina, enkelien ja\nihmisten.',123,NULL),
 (3115,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',10,'0Me olemme hulluja Kristuksen tähden, te\nviisaita Kristuksessa. Me olemme heikkoja, te\nvoimakkaita; teitä kunnioitetaan, meitä halveksitaan.',123,NULL),
 (3116,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',11,'1Yhä edelleen me kärsimme nälkää ja janoa ja\nkuljemme puolialastomina, meitä pahoinpidellään, me\nvaellamme paikasta toiseen',123,NULL),
 (3117,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',12,'2ja hankimme\ntoimeentulomme omin käsin kovalla työllä. Meitä\nherjataan, mutta me siunaamme. Meitä vainotaan, mutta\nme kestämme.',123,NULL),
 (3118,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',13,'3Meistä puhutaan pahaa, mutta me\npuhumme hyvää. Tähän päivään saakka olemme olleet koko\nmaailman kaatopaikka, ihmiskunnan pohjasakkaa. ',123,NULL),
 (3119,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',14,'4En kirjoita tätä tuottaakseni teille häpeää vaan\nojentaakseni teitä kuin rakkaita lapsiani.',123,NULL),
 (3120,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',15,'5Vaikka\nteillä Kristukseen uskovina olisi tuhansia\nkasvattajia, teillä on vain yksi isä. Minähän teidät\nolen evankeliumia julistamalla synnyttänyt Kristukseen\nJeesukseen uskoviksi.',123,NULL),
 (3121,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',16,'6Kehotan teitä siis\nnoudattamaan omaa esimerkkiäni.',123,NULL),
 (3122,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',17,'7Olen sitä varten\nlähettänyt luoksenne Timoteuksen, joka on minun\nhengellinen lapseni, rakas ja uskollinen poikani. Hän\npalauttaa teidän mieleenne, miten minä Kristuksen\nomana elän ja mitä minä kaikkialla eri seurakunnissa\nopetan.',123,NULL),
 (3123,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',18,'8Jotkut teistä ovat ruvenneet rehentelemään\nsiinä luulossa, että minä en tulisikaan luoksenne.',123,NULL),
 (3124,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',19,'9Mutta pian minä tulen, jos Herra suo, ja otan\nselvää noiden rehentelijöiden voimasta. Heidän\npuheistaan en välitä,',123,NULL),
 (3125,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',20,'0sillä Jumalan valtakunta ei\nilmene puheina vaan voimana.',123,NULL),
 (3126,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',21,'1Kumpaa tahdotte:\ntulenko luoksenne piiska kädessä vai rakastavin ja\nlempein mielin? ',123,NULL),
 (3127,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',1,'Olen kuullut, että teidän keskuudessanne on\nhaureutta, jopa sellaista mitä ei tavata edes\npakanakansojen parissa: eräskin elää äitipuolensa\nkanssa.',124,NULL),
 (3128,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',2,'Ja te vielä pöyhkeilette, vaikka teillä\nolisi ollut syytä surra ja erottaa joukostanne mies,\njoka on syyllistynyt tuollaiseen.',124,NULL),
 (3129,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',3,'Vaikka minä\nruumiillisesti olen poissa luotanne, olen kuitenkin\nhengessä läsnä ja olen kuin paikalla olevana jo\npäättänyt, mitä syylliselle on tehtävä.',124,NULL),
 (3130,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',4,'Kun te\nolette yhdessä koolla Herramme Jeesuksen nimessä ja\nminun henkeni ja Herramme Jeesuksen voima ovat läsnä,',124,NULL),
 (3131,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',5,'tuo mies on luovutettava Saatanalle ja niin\nannettava hänen ruumiinsa tuhoutua, jotta hänen\nhenkensä pelastuisi Herran päivänä. ',124,NULL),
 (3132,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',6,'Te teette väärin, kun kerskutte. Ettekö tiedä, että\npieni määrä hapatetta hapattaa koko taikinan?',124,NULL),
 (3133,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',7,'Puhdistakaa siis talonne hapantaikinasta, niin että\nteistä tulee uusi taikina. Happamattomiahan te\nolettekin, sillä meidän pääsiäislampaamme, Kristus, on\njo teurastettu.',124,NULL),
 (3134,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',8,'Meidän on siis aika viettää juhlaa,\nei vanhan pahuuden ja kelvottomuuden hapattamina, vaan\nhappamattomina, vilpittömyydessä ja totuudessa. ',124,NULL),
 (3135,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',9,'Sanoin kirjeessäni teille, että teidän ei pidä olla\ntekemisissä siveettömästi elävien kanssa.',124,NULL),
 (3136,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',10,'0En\ntarkoittanut tämän maailman siveettömiä enkä ahneita,\nriistäjiä tai epäjumalanpalvelijoita, sillä silloinhan\nteidän pitäisi lähteä kokonaan pois maailmasta.',124,NULL),
 (3137,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',11,'1Nyt täsmennän vielä: jos jotakuta sanotaan\nveljeksi mutta hän on siveetön tai ahne,\nepäjumalanpalvelija, pilkkaaja, juomari tai riistäjä,\nälkää olko tekemisissä hänen kanssaan. Älkää edes\naterioiko tällaisen kanssa.',124,NULL),
 (3138,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',12,'2Ei kai minun asiani\nole tuomita ulkopuolisia? Ettehän tekään tuomitse\nmuita kuin omaan piiriinne kuuluvia.',124,NULL),
 (3139,'Anonymous','2008-10-17 16:33:35','Anonymous','2008-10-17 16:33:35',13,'3Ulkopuoliset\ntuomitsee Jumala. \"Poistakaa keskuudestanne se, joka\non paha.\" ',124,NULL),
 (3140,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',1,'Kun teidän keskuudessanne jollakin on riita-asia\ntoisen kanssa, kuinka hän julkeaa viedä sen pakanoiden\nratkaistavaksi? Miksi hän ei jätä sitä pyhien\nratkaistavaksi?',125,NULL),
 (3141,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',2,'Ettekö tiedä, että pyhät tulevat\nkerran olemaan maailman tuomareina? Jos te saatte\ntuomita maailmaa, ettekö muka kelpaa tuomareiksi\nvähäpätöisissä asioissa?',125,NULL),
 (3142,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',3,'Ettekö tiedä, että me\ntulemme tuomitsemaan enkeleitäkin -- kuinka emme\nsitten voisi ratkaista tämän elämän asioita?',125,NULL),
 (3143,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',4,'Mutta\nkun teillä on riitaa maallisista asioista, te otatte\ntuomareiksi sellaisia, joille seurakunnassa ei anneta\nmitään arvoa.',125,NULL),
 (3144,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',5,'Saisitte hävetä! Eikö omassa\njoukossanne ole yhtäkään viisasta, joka pystyisi\nratkaisemaan kahden veljen välisen riidan? ',125,NULL),
 (3145,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',6,'Veli siis käy oikeutta veljeään vastaan, ja vielä\nepäuskoisten edessä!',125,NULL),
 (3146,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',7,'Jo se, että yleensä käräjöitte\nkeskenänne, on teille tappioksi. Miksi ette ennemmin\nkärsi vääryyttä? Miksi ette ennemmin anna riistää\nitseltänne?',125,NULL),
 (3147,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',8,'Ei, te itse teette vääryyttä ja\nriistätte muilta, jopa omilta veljiltänne. ',125,NULL),
 (3148,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',9,'Ettekö tiedä, että vääryydentekijät eivät saa\nomakseen Jumalan valtakuntaa? Älkää eksykö! Jumalan\nvaltakunnan perillisiä eivät ole siveettömyyden\nharjoittajat eivätkä epäjumalien palvelijat, eivät\navionrikkojat, eivät miesten kanssa makaavat miehet,',125,NULL),
 (3149,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',10,'0eivät varkaat eivätkä ahneet, eivät juomarit,\npilkkaajat eivätkä riistäjät.',125,NULL),
 (3150,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',11,'1Tällaisia jotkut\nteistä olivat ennen, mutta nyt teidät on pesty\npuhtaiksi ja tehty pyhiksi ja vanhurskaiksi Herran\nJeesuksen Kristuksen nimessä ja Jumalamme Hengen\nvoimasta.   ',125,NULL),
 (3151,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',18,'8Pysykää erossa haureudesta! Kaikki muut synnit,\njoita ihminen tekee, kohdistuvat muualle kuin hänen\nruumiiseensa, mutta siveetön teko osuu ihmisen omaan\nruumiiseen.',125,NULL),
 (3152,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',19,'9Ettekö tiedä, että teidän ruumiinne on\nPyhän Hengen temppeli? Tämän Hengen on Jumala antanut\nasumaan teissä. Te ette itse omista itseänne,',125,NULL),
 (3153,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',20,'0sillä Jumala on ostanut teidät täydestä hinnasta.\nTuottakaa siis ruumiillanne Jumalalle kunniaa! ',125,NULL),
 (3154,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',1,'Otan nyt puheeksi asian, josta kirjoititte. Miehen\non kyllä hyvä olla koskematta naiseen,',126,NULL),
 (3155,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',2,'mutta\nhaureuden välttämiseksi tulee kunkin miehen elää oman\nvaimonsa ja kunkin naisen oman miehensä kanssa.',126,NULL),
 (3156,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',3,'Mies täyttäköön aviovelvollisuutensa vaimoaan\nkohtaan, samoin vaimo miestään kohtaan.',126,NULL),
 (3157,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',4,'Vaimon\nruumis ei ole hänen omassa vallassaan vaan miehen,\nsamoin ei miehen ruumis ole hänen omassa vallassaan\nvaan vaimon.',126,NULL),
 (3158,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',5,'Älkää keskeyttäkö yhdyselämäänne,\npaitsi ehkä yhteisestä sopimuksesta joksikin aikaa,\njotta voitte keskittyä rukoukseen; palatkaa sitten\ntaas yhteen. Muutenhan Saatana pääsee kiusaamaan\nteitä, kun ette kuitenkaan pysty hillitsemään\nitseänne.',126,NULL),
 (3159,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',6,'Sanon tämän myönnytyksenä enkä käskynä.',126,NULL),
 (3160,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',7,'Soisin kaikkien elävän niin kuin itse elän.\nJokaisella on kuitenkin oma Jumalalta saatu\narmolahjansa, yhdellä yksi, toisella toinen. ',126,NULL),
 (3161,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',8,'Naimattomille ja leskille minä sanon, että heidän\nolisi hyvä pysyä yksin niin kuin minäkin.',126,NULL),
 (3162,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',9,'Mutta\nelleivät he jaksa hillitä itseään, menkööt naimisiin,\nsillä on parempi mennä naimisiin kuin palaa himon\ntulessa.',126,NULL),
 (3163,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',10,'0Naimisissa oleville taas annan käskyn, en\nminä vaan Herra: vaimo ei saa erota miehestään.',126,NULL),
 (3164,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',11,'1Jos hän kuitenkin eroaa, olkoon menemättä enää\nnaimisiin tai sopikoon miehensä kanssa. Samoin ei mies\nsaa erota vaimostaan. ',126,NULL),
 (3165,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',12,'2Sitten sanon vielä, en Herran sanana vaan omanani:\nJos jollakin veljellä on vaimo, joka ei usko, ja tämä\nsuostuu asumaan hänen kanssaan, miehen ei pidä jättää\nhäntä.',126,NULL),
 (3166,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',13,'3Ja jos uskovalla naisella on aviomies, joka\nei usko, ja tämä suostuu asumaan hänen kanssaan,\nvaimon ei pidä jättää miestään.',126,NULL),
 (3167,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',14,'4Mies, joka ei\nusko, on uskovan vaimonsa pyhittämä, ja vaimo, joka ei\nusko, on uskovan miehensä pyhittämä. Muutenhan teidän\nlapsenne olisivat epäpuhtaita; nyt he kuitenkin ovat\npyhiä.',126,NULL),
 (3168,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',15,'5Mutta jos se puoliso, joka ei usko, tahtoo\nerota, niin erotkoon. Uskovaa veljeä tai sisarta ei\ntällaisessa tapauksessa sido mikään pakko. Jumala on\nkutsunut teidät elämään rauhassa.',126,NULL),
 (3169,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',16,'6Ja mistä tiedät,\nvaimo, voitko pelastaa miehesi? Tai mies, mistä\ntiedät, voitko pelastaa vaimosi?     ',126,NULL),
 (3170,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',29,'9Sanon teille, veljet: aika on käymässä vähiin.\nSiksi niidenkin, joilla on vaimo, on nyt elettävä kuin\nheillä ei vaimoa olisikaan,',126,NULL),
 (3171,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',30,'0surevien on oltava\nkuin he eivät surisi ja iloitsevien kuin eivät\niloitsisi; niiden, jotka ostavat jotakin, on oltava\nkuin eivät saisi pitää ostamaansa,',126,NULL),
 (3172,'Anonymous','2008-10-17 16:33:36','Anonymous','2008-10-17 16:33:36',31,'1ja niiden,\njotka käyttävät hyödykseen sitä mitä tässä maailmassa\non, on oltava kuin eivät saisi siitä mitään hyötyä.\nTämä nykyinen maailmamme on näet katoamassa. ',126,NULL),
 (3173,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',32,'2Toivoisin, ettei teillä olisi huolia. Naimaton\nmies kantaa huolta siitä, mikä koskee Herraa, siitä,\nmiten olisi Herralle mieliksi.',126,NULL),
 (3174,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',33,'3Naimisissa oleva\ntaas huolehtii maallisista asioista, siitä, miten\nolisi vaimolleen mieliksi.',126,NULL),
 (3175,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',34,'4Näin hän on jakautunut\nkahtaalle. Samoin naimaton nainen ja nuori neito\nkantavat huolta siitä, mikä koskee Herraa, jotta\nolisivat niin ruumiiltaan kuin hengeltäänkin pyhiä.\nNaimisissa oleva nainen taas huolehtii maallisista\nasioista, siitä, miten olisi miehelleen mieliksi.',126,NULL),
 (3176,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',35,'5Tämän sanon teidän parastanne ajatellen, en\nsitoakseni teitä vaan auttaakseni teitä elämään\nkunniallisesti ja pysymään lujasti ja häiriintymättä\nHerran yhteydessä. ',126,NULL),
 (3177,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',36,'6Joku ehkä arvelee menettelevänsä sopimattomasti\nmorsiantaan kohtaan, kun hän tuntee tähän ylivoimaista\nhalua eikä voi sille mitään. Hän saa kuitenkin tehdä\nniin kuin haluaa, se ei ole väärin, he voivat mennä\nnaimisiin.',126,NULL),
 (3178,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',37,'7Mutta jos joku ei tunne voittamatonta\npakkoa vaan hallitsee tahtonsa ja pysyy lujasti\npäätöksessään säilyttää morsiamensa koskemattomana,\nhän tekee oikein.',126,NULL),
 (3179,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',38,'8Se, joka menee morsiamensa\nkanssa naimisiin, tekee siis oikein, mutta se, joka ei\nmene, tekee vielä paremmin. ',126,NULL),
 (3180,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',39,'9Vaimo kuuluu yhteen miehensä kanssa niin kauan\nkuin tämä elää, mutta miehen kuoltua hän on vapaa\nmenemään naimisiin kenen kanssa haluaa, kunhan se vain\ntapahtuu Herrassa.',126,NULL),
 (3181,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',40,'0Hänen osansa on kuitenkin\nparempi, jos hän pysyy leskenä. Tämä on minun\nkäsitykseni, ja luullakseni minussakin on Jumalan\nHenki. ',126,NULL),
 (3182,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',1,'Sitten on puhuttava epäjumalille uhratusta lihasta.\nMeillä kaikilla toki on tästä asiasta tieto. Mutta\ntieto tekee ylpeäksi, rakkaus sen sijaan rakentaa.',127,NULL),
 (3183,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',2,'Jos joku luulee tietävänsä jotakin, hänellä ei\nvielä ole oikeaa tietoa.',127,NULL),
 (3184,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',3,'Mutta jos joku rakastaa\nJumalaa, hänet Jumala tuntee. ',127,NULL),
 (3185,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',4,'Epäjumalille uhratun lihan syömisestä sanon näin:\nMe tiedämme, ettei epäjumalia ole olemassa, on vain\nyksi ainoa Jumala.',127,NULL),
 (3186,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',5,'Onhan tosin sekä taivaassa että\nmaan päällä niin sanottuja jumalia, moniakin jumalia\nja herroja, mutta   ',127,NULL),
 (3187,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',7,'Mutta kaikilla ei tätä tietoa ole. Tottumuksesta\nepäjumaliin jotkut lihaa syödessään edelleenkin\najattelevat, että se on epäjumalille uhrattua, ja tämä\ntahraa heidän epävarman omantuntonsa.',127,NULL),
 (3188,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',8,'Ei ruoka vie\nmeitä lähemmäs Jumalaa. Emme menetä mitään, jos\njätämme syömättä, emmekä voita mitään, jos syömme.',127,NULL),
 (3189,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',9,'Pitäkää kuitenkin huoli siitä, ettei tämä teidän\nvapautenne saa heikkoja kompastumaan.',127,NULL),
 (3190,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',10,'0Jos heikko\nveljesi näkee sinut, jolla on tietoa, aterialla\nepäjumalan temppelissä, eikö hänen omatuntonsa saa\ntästä tukea siihen, että hänkin voisi syödä uhrilihaa?',127,NULL),
 (3191,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',11,'1Näin sinun tietosi vie tuhoon tuon heikon, oman\nveljesi, jonka vuoksi Kristus on kuollut.',127,NULL),
 (3192,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',12,'2Kun te\ntällä tavoin teette syntiä veljiänne vastaan ja\nhaavoitatte heidän horjuvaa omaatuntoaan, teette\nsyntiä Kristusta vastaan. ',127,NULL),
 (3193,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',13,'3Näin ollen: jos ruoka viettelee veljeni syntiin,\nen ikinä enää syö lihaa, jotta en viettelisi veljeäni. ',127,NULL),
 (3194,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',1,'Enkö minä ole vapaa? Enkö ole apostoli? Enkö ole\nnähnyt Herraamme Jeesusta? Ettekö te itse ole tulos\nminun työstäni Herran palveluksessa?',128,NULL),
 (3195,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',2,'Jos eivät muut\npidäkään minua apostolina, teidän apostolinne ainakin\nolen. Tehän olette sinetti, joka vahvistaa, että olen\nHerran apostoli. ',128,NULL),
 (3196,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',3,'Näin minä puolustaudun niiden edessä, jotka minua\ntutkivat:',128,NULL),
 (3197,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',4,'Eikö meillä olisi oikeutta ruokaan ja\njuomaan?',128,NULL),
 (3198,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',5,'Eikö meillä olisi oikeutta ottaa\nmatkoillemme mukaan vaimoa, uskonsisarta, niin kuin\ntekevät muut apostolit, Herran veljet ja Keefas?',128,NULL),
 (3199,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',6,'Olemmeko minä ja Barnabas ainoat, joiden on tehtävä\nansiotyötä?',128,NULL),
 (3200,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',7,'Ei kai kukaan palvele sotilaana omalla\nkustannuksellaan? Kai se, joka istuttaa viinitarhan,\nmyös syö sen hedelmiä? Ja kai se, joka paimentaa\nlampaita ja vuohia, myös käyttää niiden maitoa? ',128,NULL),
 (3201,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',8,'En tässä esitä vain ihmisten ajatuksia, sillä\nlakikin sanoo samaa.',128,NULL),
 (3202,'Anonymous','2008-10-17 16:33:37','Anonymous','2008-10-17 16:33:37',9,'Sanotaanhan Mooseksen laissa:\n\"Älä sido puivan härän suuta.\" Ei kai Jumala tässä\nhäristä huolehdi?',128,NULL),
 (3203,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',10,'0Kaiketi hän sanoo tämän meidän\nvuoksemme. Meitä ajatellenhan on kirjoitettu:\n\"Kyntäjän tulee kyntää ja puimamiehen puida siinä\ntoivossa, että saa oman osuutensa.\"',128,NULL),
 (3204,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',11,'1Kun me olemme\nkylväneet teihin hengellisen kylvön, olisiko liikaa,\njos korjaisimme teiltä aineellisen sadon?',128,NULL),
 (3205,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',12,'2Jos\nmuilla on oikeus saada teiltä jotakin, eikö vielä\nsuuremmalla syyllä meillä? Me emme kuitenkaan ole\nkäyttäneet tätä oikeutta hyväksemme, vaan kestämme\nmitä tahansa, jotta emme vain olisi esteenä Kristuksen\nevankeliumille. ',128,NULL),
 (3206,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',13,'3Ettekö tiedä, että temppelissä palvelevat papit\nsaavat temppelistä elatuksensa ja ne, jotka\ntoimittavat uhrit, saavat alttarille tuoduista\nuhreista osuutensa?',128,NULL),
 (3207,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',14,'4Samoin on Herra määrännyt,\nettä evankeliumin julistajien tulee saada elantonsa\nevankeliumista.',128,NULL),
 (3208,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',15,'5Mutta minä en ole lainkaan\nkäyttänyt hyväkseni näitä oikeuksia. En nytkään\nkirjoita tätä vaatiakseni niitä itselleni. Mieluummin\nvaikka kuolen! Tätä ylpeyden aihetta ei kukaan voi\nminulta riistää.',128,NULL),
 (3209,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',16,'6Siinä, että julistan\nevankeliumia, ei ole mitään ylpeilemistä, sillä minun\non pakko tehdä sitä. Voi minua, ellen evankeliumia\njulista!',128,NULL),
 (3210,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',17,'7Jos teen sitä vapaasta tahdostani, minun\nkuuluu saada palkkaa. Mutta jos en toimi omasta\ntahdostani, minä vain suoritan tehtävää, joka on\nminulle uskottu.',128,NULL),
 (3211,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',18,'8Mikä siis on palkkani? Se, että\njulistan evankeliumia ilman korvausta, käyttämättä\nhyväkseni niitä oikeuksia, joita minulla sen\njulistajana on. ',128,NULL),
 (3212,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',19,'9Vaikka olen vapaa ja kaikista riippumaton, olen\nruvennut kaikkien orjaksi voittaakseni Kristukselle\nmahdollisimman monia.',128,NULL),
 (3213,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',20,'0Voittaakseni hänen omikseen\njuutalaisia olen näille ollut kuin juutalainen;\nvoittaakseni lain alaisina eläviä olen näille ollut\nkuin lain alainen, vaikka en olekaan lain alainen.',128,NULL),
 (3214,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',21,'1Voittaakseni ilman lakia eläviä olen näille ollut\nkuin eläisin ilman lakia, vaikka en olekaan Jumalan\nlakia vailla -- onhan minulla Kristuksen laki.',128,NULL),
 (3215,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',22,'2Voittaakseni heikkoja olen näille ollut heikko.\nKaikille olen ollut kaikkea, jotta pelastaisin edes\nmuutamia.',128,NULL),
 (3216,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',23,'3Kaiken tämän teen evankeliumin vuoksi,\njotta olisin itsekin siitä osallinen. ',128,NULL),
 (3217,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',24,'4Tiedättehän, että vaikka juoksukilpailussa kaikki\njuoksevat, vain yksi saa palkinnon. Juoskaa siis niin,\nettä voitatte sen!',128,NULL),
 (3218,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',25,'5Jokainen kilpailija noudattaa\nlujaa itsekuria, juoksijat saavuttaakseen katoavan\nseppeleen, me saadaksemme katoamattoman.',128,NULL),
 (3219,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',26,'6Minä en\nsiis juokse päämäärättömästi enkä nyrkkeillessäni\nhuido ilmaan.',128,NULL),
 (3220,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',27,'7Kohdistan iskut omaan ruumiiseeni ja\npakotan sen tottelemaan, jottei itseäni lopulta\nhylättäisi, minua, joka olen kutsunut muita\nkilpailuun. ',128,NULL),
 (3221,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',1,'Veljet, haluan teidän tietävän, että isämme\nvaelsivat kaikki pilven johdattamina ja kulkivat meren\npoikki.',129,NULL),
 (3222,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',2,'Kaikki he saivat pilvessä ja meressä\nkasteen Mooseksen seuraajiksi.',129,NULL),
 (3223,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',3,'Kaikki he söivät\nsamaa hengellistä ruokaa',129,NULL),
 (3224,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',4,'ja joivat samaa\nhengellistä juomaa. Hehän joivat siitä hengellisestä\nkalliosta, joka kulki heidän mukanaan; tämä kallio oli\nKristus.',129,NULL),
 (3225,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',5,'Mutta useimmat heistä Jumala hylkäsi,\nkohtasihan heidät tuho autiomaassa.',129,NULL),
 (3226,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',6,'Näin heistä\ntuli meille varoittavia esimerkkejä: meidän ei pidä\nhimoita pahaa, niin kuin he tekivät.',129,NULL),
 (3227,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',7,'Älkää ruvetko\npalvelemaan epäjumalia niin kuin jotkut heistä.\nKirjoituksissa kerrotaan: \"He istuivat syömään ja\njuomaan, ja sitten he nousivat tanssimaan.\"',129,NULL),
 (3228,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',8,'Älkäämme antautuko siveettömyyteen niin kuin jotkut\nheistä; heitä kuoli yhtenä päivänä\nkaksikymmentäkolmetuhatta.',129,NULL),
 (3229,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',9,'Meidän ei myöskään tule\nkoetella Herran kärsivällisyyttä, niin kuin jotkut\nheistä tekivät; he kuolivat käärmeenpuremiin.',129,NULL),
 (3230,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',10,'0Älkää liioin nurisko niin kuin jotkut heistä; he\nsaivat surmansa kuolemanenkelin kädestä. ',129,NULL),
 (3231,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',11,'1Nämä tapahtumat ovat varoittavia esimerkkejä, ja\nne on kerrottu ojennukseksi meille, joiden osana on\nelää lopun aikoja.',129,NULL),
 (3232,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',12,'2Joka luulee seisovansa,\nkatsokoon, ettei kaadu.',129,NULL),
 (3233,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',13,'3Teitä kohdannut kiusaus ei\nole mitenkään epätavallinen. Jumalaan voi luottaa. Hän\nei salli kiusauksen käydä teille ylivoimaiseksi, vaan\nantaessaan teidän joutua koetukseen hän samalla\nvalmistaa pääsyn siitä, niin että voitte sen kestää. ',129,NULL),
 (3234,'Anonymous','2008-10-17 16:33:38','Anonymous','2008-10-17 16:33:38',14,'4Rakkaat ystävät, pysykää siis erossa epäjumalien\npalvelemisesta.',129,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (3235,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',15,'5Puhun teille kuin järkeville\nihmisille ainakin. Harkitkaa mitä sanon.',129,NULL),
 (3236,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',16,'6Eikö\nmalja, jonka me siunaamme, ole yhteys Kristuksen\nvereen? Ja eikö leipä, jonka me murramme, ole yhteys\nKristuksen ruumiiseen?',129,NULL),
 (3237,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',17,'7Leipä on yksi, ja niin\nmekin olemme yksi ruumis, vaikka meitä on monta, sillä\ntulemme kaikki osallisiksi tuosta yhdestä leivästä.',129,NULL),
 (3238,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',18,'8Ajatelkaa Israelin kansaa! Eivätkö papit, jotka\nsyövät uhrilihaa, joudu läheiseen yhteyteen alttarin\nkanssa?',129,NULL),
 (3239,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',19,'9Mitä tällä tarkoitan? En sitä, että\nepäjumalille uhrattu liha sinänsä olisi jotakin tai\nettä epäjumalat olisivat jotakin todellista.',129,NULL),
 (3240,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',20,'0Tarkoitan sitä, että epäjumalien palvelijat\nuhraavat pahoille hengille eivätkä Jumalalle, enkä\nhalua teidän joutuvan yhteyteen pahojen henkien\nkanssa.',129,NULL),
 (3241,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',21,'1Te ette voi juoda sekä Herran maljasta\nettä pahojen henkien maljasta, ette voi olla\nosallisina sekä Herran pöytään että pahojen henkien\npöytään.',129,NULL),
 (3242,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',22,'2Emme kai halua herättää Herran kiivautta?\nOlemmeko muka häntä vahvempia?   ',129,NULL),
 (3243,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',1,'Seuratkaa minun esimerkkiäni, niin kuin minäkin\nseuraan Kristusta.',130,NULL),
 (3244,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',2,'Kiitän teitä siitä, että aina\nmuistatte ja noudatatte niitä opetuksia, jotka olen\nvälittänyt teille.',130,NULL),
 (3245,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',3,'Teidän tulee kuitenkin tietää,\nettä jokaisen miehen pää on Kristus, naisen pää on\nmies ja Kristuksen pää on Jumala.',130,NULL),
 (3246,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',4,'Jos miehellä on\npäähine päässään, kun hän rukoilee tai profetoi*, hän\nhäpäisee päänsä.',130,NULL),
 (3247,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',5,'Sen sijaan jos nainen on avopäin,\nkun hän rukoilee tai profetoi, hän häpäisee päänsä,\nsillä sehän on samaa kuin hän olisi antanut ajaa\nhiuksensa pois.',130,NULL),
 (3248,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',6,'Ellei nainen peitä päätään, hän voi\nyhtä hyvin leikkauttaa hiuksensa. Mutta jos kerran\nhiusten leikkaaminen tai pois ajaminen on naiselle\nhäpeällistä, hänen tulee peittää päänsä. ',130,NULL),
 (3249,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',7,'Miehen ei pidä peittää päätään, koska hän on\nJumalan kuva ja heijastaa hänen kunniaansa. Nainen\ntaas heijastaa miehen kunniaa.',130,NULL),
 (3250,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',8,'Miestä ei ole luotu\nnaisesta vaan nainen miehestä,',130,NULL),
 (3251,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',9,'eikä miestä luotu\nnaisen takia vaan nainen miehen takia.',130,NULL),
 (3252,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',10,'0Naisen\ntulee siis pitää päässään arvonsa merkkiä enkeleiden\ntakia.',130,NULL),
 (3253,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',11,'1Herran edessä ei kuitenkaan ole naista\nilman miestä eikä miestä ilman naista,',130,NULL),
 (3254,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',12,'2sillä niin\nkuin nainen on luotu miehestä, myös mies on syntynyt\nnaisesta. Mutta kaikki on saanut alkunsa Jumalasta.',130,NULL),
 (3255,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',13,'3Päätelkää itse, onko naisen sopivaa rukoilla\nJumalaa avopäin.',130,NULL),
 (3256,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',14,'4Opettaahan jo luontokin teille,\nettä pitkä tukka on miehelle häpeäksi',130,NULL),
 (3257,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',15,'5mutta\nnaiselle kunniaksi. Pitkät hiukset on annettu naiselle\nhunnuksi.',130,NULL),
 (3258,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',16,'6Jos joku kuitenkin väittää vastaan, niin\nsanon hänelle, ettei meillä ole sellaista tapaa eikä\nyleensäkään Jumalan seurakunnilla.   ',130,NULL),
 (3259,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',23,'3Olen saanut Herralta tiedoksi tämän, minkä olen\nmyös opettanut teille: Herra Jeesus sinä yönä, jona\nhänet kavallettiin, otti leivän,',130,NULL),
 (3260,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',24,'4kiitti Jumalaa,\nmursi leivän ja sanoi: \"Tämä on minun ruumiini, joka\nannetaan teidän puolestanne. Tehkää tämä minun\nmuistokseni.\"',130,NULL),
 (3261,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',25,'5Samoin hän otti aterian jälkeen\nmaljan ja sanoi: \"Tämä malja on uusi liitto minun\nveressäni. Niin usein kuin siitä juotte, tehkää se\nminun muistokseni.\" ',130,NULL),
 (3262,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',26,'6Niin usein kuin te syötte tätä leipää ja juotte\ntästä maljasta, te siis julistatte Herran kuolemaa,\nsiihen asti kun hän tulee.',130,NULL),
 (3263,'Anonymous','2008-10-17 16:33:39','Anonymous','2008-10-17 16:33:39',27,'7Niinpä se, joka\narvottomalla tavalla syö tätä leipää ja juo Herran\nmaljasta, tekee syntiä Herran ruumista ja verta\nvastaan.',130,NULL),
 (3264,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',28,'8Jokaisen on tutkittava itseään, ennen\nkuin syö tätä leipää ja juo tästä maljasta.',130,NULL),
 (3265,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',29,'9Se,\njoka syö ja juo ajattelematta, että kysymys on\nKristuksen ruumiista, syö ja juo itselleen tuomion.',130,NULL),
 (3266,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',30,'0Sen vuoksihan teidän joukossanne on paljon\nheikkoja ja sairaita ja monet ovat jo nukkuneet pois.',130,NULL),
 (3267,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',31,'1Jos me itse tutkisimme itseämme, emme joutuisi\ntuomittaviksi.',130,NULL),
 (3268,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',32,'2Rangaistessaan Herra kuitenkin\nkurittaa meitä, jotta meitä ei yhdessä maailman kanssa\nlopullisesti tuomittaisi. ',130,NULL),
 (3269,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',33,'3Veljeni, kun siis kokoonnutte aterioimaan,\nodottakaa toisianne.',130,NULL),
 (3270,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',34,'4Se, jolla on nälkä, syököön\nkotonaan, jotteivät kokoontumisenne tuottaisi teille\nrangaistusta. Muusta annan ohjeet sitten kun tulen. ',130,NULL),
 (3271,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',1,'Veljet, haluan teidän olevan selvillä Pyhän Hengen\nlahjoista.',131,NULL),
 (3272,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',2,'Muistattehan, kuinka ollessanne vielä\npakanoita vastustamaton voima ajoi teitä mykkien\nepäjumalien luo.',131,NULL),
 (3273,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',3,'Siksi sanon teille selvästi, että\nkukaan, joka puhuu Jumalan Hengen valtaamana, ei voi\nsanoa: \"Jeesus on kirottu.\" Kukaan ei myöskään voi\nsanoa: \"Jeesus on Herra\", muuten kuin Pyhän Hengen\nvaikutuksesta. ',131,NULL),
 (3274,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',4,'Armolahjoja on monenlaisia, mutta Henki on sama.',131,NULL),
 (3275,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',5,'Myös palvelutehtäviä on monenlaisia, mutta Herra on\nsama.',131,NULL),
 (3276,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',6,'Jumalan voiman vaikutuksia on monenlaisia,\nmutta hän, joka meissä kaikissa kaiken vaikuttaa, on\nsama.',131,NULL),
 (3277,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',7,'Hän antaa Hengen ilmetä itse kussakin\nerityisellä tavalla, yhteiseksi hyödyksi.',131,NULL),
 (3278,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',8,'Yhden ja\nsaman Hengen voimasta toinen saa kyvyn jakaa\nviisautta, toinen kyvyn jakaa tietoa,',131,NULL),
 (3279,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',9,'toiselle sama\nHenki suo uskon voiman, toiselle parantamisen lahjan,',131,NULL),
 (3280,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',10,'0joku saa voiman tehdä ihmeitä, joku profetoimisen\nlahjan, joku kyvyn erottaa eri henget toisistaan, joku\nkielillä puhumisen lahjan, joku taas kyvyn tulkita\ntällaista puhetta.',131,NULL),
 (3281,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',11,'1Kaiken tämän saa aikaan yksi ja\nsama Henki, joka jakaa kullekin omat lahjansa niin\nkuin tahtoo.   ',131,NULL),
 (3282,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',14,'4Eihän ruumiskaan muodostu yhdestä jäsenestä vaan\nmonista.',131,NULL),
 (3283,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',15,'5Vaikka jalka sanoisi: \"Koska en ole käsi,\nen kuulu ruumiiseen\", se silti kuuluu ruumiiseen.',131,NULL),
 (3284,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',16,'6Ja jos korva sanoisi: \"Koska en ole silmä, en\nkuulu ruumiiseen\", se silti kuuluu ruumiiseen.',131,NULL),
 (3285,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',17,'7Jos\nkoko ruumis olisi pelkkää silmää, olisiko silloin\nkuuloa? Tai jos se olisi pelkkää korvaa, olisiko\nsilloin hajuaistia?',131,NULL),
 (3286,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',18,'8Jumala on kuitenkin asettanut\nruumiiseen kaikki eri jäsenet niin kuin on nähnyt\nhyväksi.',131,NULL),
 (3287,'Anonymous','2008-10-17 16:33:40','Anonymous','2008-10-17 16:33:40',19,'9Jos kaikki olisi yhtä ja samaa jäsentä,\nolisiko silloin mitään ruumista?',131,NULL),
 (3288,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',20,'0Jäseniä on\nkuitenkin monta, kun taas ruumis on yksi. ',131,NULL),
 (3289,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',21,'1Ei silmä voi sanoa kädelle: \"Minä en tarvitse\nsinua\", eikä liioin pää jaloille: \"Minä en tarvitse\nteitä.\"',131,NULL),
 (3290,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',22,'2Päinvastoin, juuri ne ruumiinjäsenet,\njotka meidän mielestämme ovat muita heikompia, ovat\nvälttämättömiä.',131,NULL),
 (3291,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',23,'3Me verhoamme erityisen arvokkaasti\nne ruumiinosat, joita emme pidä kovinkaan arvokkaina,\nja peitämme erityisen säädyllisesti ne, joita\nhäpeämme.',131,NULL),
 (3292,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',24,'4Säädylliset ruumiinosat eivät tällaista\nverhoa tarvitse. Kun Jumala yhdisti jäsenet ruumiiksi,\nhän antoi vähempiarvoisille jäsenille suuremman\nkunnian,',131,NULL),
 (3293,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',25,'5jotta ruumiissa ei syntyisi\neripuraisuutta vaan jäsenet yhteen kuuluen\nhuolehtisivat toinen toisestaan.',131,NULL),
 (3294,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',26,'6Jos yksi jäsen\nkärsii, kärsivät kaikki muutkin jäsenet, ja jos yksi\njäsen saa osakseen kunniaa, iloitsevat kaikki muutkin\nsen kanssa. ',131,NULL),
 (3295,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',27,'7Te olette Kristuksen ruumis, ja jokainen teistä on\ntämän ruumiin jäsen.',131,NULL),
 (3296,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',28,'8Jumala on seurakunnassaan\nasettanut ensinnäkin jotkut apostoleiksi, toiseksi\njotkut profeetoiksi ja vielä jotkut opettajiksi.\nMuutamilla on voima tehdä ihmeitä, toisilla\nparantamisen lahja, toisilla kyky auttaa muita, toimia\njohtajana tai puhua kielillä.',131,NULL),
 (3297,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',29,'9Eivät kai kaikki ole\napostoleja? Tai profeettoja? Tai opettajia? Tai\nihmeiden tekijöitä?',131,NULL),
 (3298,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',30,'0Eihän kaikilla ole\nparantamisen lahjaa? Eiväthän kaikki puhu kielillä tai\ntulkitse tällaista puhetta? ',131,NULL),
 (3299,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',31,'1Mutta tavoitelkaa kaikkein arvokkaimpia\narmolahjoja! Nyt minä osoitan teille tien, joka on\nverrattomasti muita parempi. ',131,NULL),
 (3300,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',1,'Vaikka minä puhuisin ihmisten ja enkelien kielillä\nmutta minulta puuttuisi rakkaus, olisin vain kumiseva\nvaski tai helisevä symbaali.',132,NULL),
 (3301,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',2,'Vaikka minulla olisi\nprofetoimisen lahja, vaikka tuntisin kaikki\nsalaisuudet ja kaiken tiedon ja vaikka minulla olisi\nkaikki usko, niin että voisin siirtää vuoria, mutta\nminulta puuttuisi rakkaus, en olisi mitään.',132,NULL),
 (3302,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',3,'Vaikka\njakaisin kaiken omaisuuteni nälkää näkeville ja vaikka\nantaisin polttaa itseni tulessa mutta minulta\npuuttuisi rakkaus, en sillä mitään voittaisi. ',132,NULL),
 (3303,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',4,'Rakkaus on kärsivällinen, rakkaus on lempeä.\nRakkaus ei kadehdi, ei kersku, ei pöyhkeile,',132,NULL),
 (3304,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',5,'ei\nkäyttäydy sopimattomasti, ei etsi omaa etuaan, ei\nkatkeroidu, ei muistele kärsimäänsä pahaa,',132,NULL),
 (3305,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',6,'ei\niloitse vääryydestä vaan iloitsee totuuden voittaessa.',132,NULL),
 (3306,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',7,'Kaiken se kestää, kaikessa uskoo, kaikessa toivoo,\nkaiken se kärsii. ',132,NULL),
 (3307,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',8,'Rakkaus ei koskaan katoa. Mutta profetoiminen\nvaikenee, kielillä puhuminen lakkaa, tieto käy\nturhaksi.',132,NULL),
 (3308,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',9,'Tietämisemme on näet vajavaista ja\nprofetoimisemme on vajavaista,',132,NULL),
 (3309,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',10,'0mutta kun\ntäydellinen tulee, vajavainen katoaa. ',132,NULL),
 (3310,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',11,'1Kun olin lapsi, minä puhuin kuin lapsi, minulla\noli lapsen mieli ja lapsen ajatukset. Nyt, kun olen\nmies, olen jättänyt sen mikä kuuluu lapsuuteen.',132,NULL),
 (3311,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',12,'2Nyt katselemme vielä kuin kuvastimesta, kuin\narvoitusta, mutta silloin näemme kasvoista kasvoihin.\nNyt tietoni on vielä vajavaista, mutta kerran se on\ntäydellistä, niin kuin Jumala minut täydellisesti\ntuntee. ',132,NULL),
 (3312,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',13,'3Niin pysyvät nämä kolme: usko, toivo, rakkaus.\nMutta suurin niistä on rakkaus. ',132,NULL),
 (3313,'Anonymous','2008-10-17 16:33:41','Anonymous','2008-10-17 16:33:41',1,'Pyrkikää rakkauteen, mutta tavoitelkaa myös\nhenkilahjoja, ennen kaikkea profetoimisen lahjaa.',133,NULL),
 (3314,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',2,'Kielillä puhuva ei näet puhu ihmisille vaan\nJumalalle; kukaan ei ymmärrä häntä, Hengen valtaamana\nhän puhuu salaisuuksia.',133,NULL),
 (3315,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',3,'Mutta se, joka profetoi,\npuhuu ihmisille: hän rakentaa, kehottaa ja lohduttaa.',133,NULL),
 (3316,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',4,'Kielillä puhuva rakentaa itseään, profetoiva\nrakentaa seurakuntaa.',133,NULL),
 (3317,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',5,'Toivoisin teidän kaikkien\npuhuvan kielillä, mutta vielä mieluummin toivoisin\nteidän profetoivan. Profetoiva on arvokkaampi kuin\nkielillä puhuva, ellei tämä sitten osaa myös tulkita\npuhettaan, niin että se koituu seurakunnan parhaaksi. ',133,NULL),
 (3318,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',6,'Mitä hyötyä minusta on, veljet, jos tulen luoksenne\nja vain puhun kielillä enkä välitä mitään ilmestystä\ntai anna teille tietoa, profetiaa tai opetusta?',133,NULL),
 (3319,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',7,'Samoinhan on elottomien soittimienkin laita,\nvaikkapa huilun tai lyyran. Miten huilulla tai\nlyyralla soitettu sävelmä voidaan tuntea, elleivät\nsävelet erotu toisistaan?',133,NULL),
 (3320,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',8,'Ja jos sotatorvesta\nlähtee epäselvä ääni, kuka valmistautuu taisteluun?',133,NULL),
 (3321,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',9,'Sama koskee teitäkin: ellette puhu selvää kieltä,\nkuinka teidän puhettanne voidaan ymmärtää? Sananne\nhaihtuvat taivaan tuuliin.',133,NULL),
 (3322,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',10,'0Niin paljon kuin\nmaailmassa onkin kieliä, kaikki ne ovat\nymmärrettävissä.',133,NULL),
 (3323,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',11,'1Mutta ellen ymmärrä puheen\nmerkitystä, olen puhujalle muukalainen, samoin hän\nminulle. ',133,NULL),
 (3324,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',12,'2Kun te nyt kerran tavoittelette henkilahjoja,\npyrkikää saamaan niitä runsain määrin seurakunnan\nrakentamiseksi.',133,NULL),
 (3325,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',13,'3Sen, joka puhuu kielillä, on siksi\nrukoiltava itselleen myös tulkitsemisen kykyä.',133,NULL),
 (3326,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',14,'4Jos\nnäet rukoilen kielillä, minun henkeni rukoilee mutta\nymmärrykseni jää hyödyttömäksi.',133,NULL),
 (3327,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',15,'5Mitä tämä siis\ntarkoittaa? Minun tulee rukoilla hengelläni, mutta\nmyös ymmärryksellä; minun tulee laulaa kiitosta\nhengelläni, mutta myös ymmärryksellä.',133,NULL),
 (3328,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',16,'6Jos kiität\nJumalaa vain hengelläsi, miten paikalla oleva\nulkopuolinen voi sanoa kiitokseesi aamenen, kun hän ei\nymmärrä mitä sanot?',133,NULL),
 (3329,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',17,'7Sinä kyllä kiität hyvin, mutta\nei tuo toinen siitä hyödy.',133,NULL),
 (3330,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',18,'8Minä puhun kielillä\nenemmän kuin kukaan teistä, ja siitä kiitän Jumalaa.',133,NULL),
 (3331,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',19,'9Jotta voisin opettaa muitakin, haluan\nseurakunnassa silti puhua mieluummin viisi\nymmärrettävää sanaa kuin tuhansia hurmoskielen sanoja. ',133,NULL),
 (3332,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',20,'0Veljet, älkää olko ajatuksiltanne lapsia.\nPahuudessa olkaa kehittymättömiä, ajattelussanne\naikuisia.',133,NULL),
 (3333,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',21,'1Lain kirjassa sanotaan:  ',133,NULL),
 (3334,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',22,'2Outoja kieliä ei siis ole tarkoitettu merkiksi\nuskoville vaan niille, jotka eivät usko; profetoiminen\nsen sijaan ei ole merkkinä epäuskoisille vaan\nuskoville.',133,NULL),
 (3335,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',23,'3Jos seurakunnan yhteisessä kokouksessa\nkaikki puhuisivat kielillä ja sinne tulisi\nulkopuolisia tai epäuskoisia, he varmasti sanoisivat,\nettä te olette järjiltänne.',133,NULL),
 (3336,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',24,'4Jos sen sijaan kaikki\nprofetoisivat ja joku epäuskoinen tai ulkopuolinen\ntulisi paikalle, hän joutuisi kaikkien koeteltavaksi\nja tutkittavaksi',133,NULL),
 (3337,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',25,'5ja hänen sydämensä salaisuudet\npaljastuisivat. Silloin hän heittäytyisi kasvoilleen\nmaahan, rukoilisi Jumalaa ja tunnustaisi: \"Jumala on\ntodella teidän keskuudessanne.\"   Niin kuin on laita kaikissa pyhien seurakunnissa,',133,NULL),
 (3338,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',34,'4naisten tulee olla vaiti seurakunnan kokouksissa.\nHeidän ei ole lupa puhua, vaan heidän on oltava\nkuuliaisia, niin kuin lakikin sanoo.',133,NULL),
 (3339,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',35,'5Jos he\ntahtovat tietoa jostakin, heidän on kysyttävä sitä\nkotona omalta mieheltään, sillä naisen on sopimatonta\npuhua seurakunnan kokouksessa.',133,NULL),
 (3340,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',36,'6Teidänkö luotanne\nJumalan sana on lähtenyt liikkeelle? Tai onko se\ntullut pelkästään teidän luoksenne? ',133,NULL),
 (3341,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',37,'7Jos joku uskoo olevansa profeetta tai saaneensa\nmuita Hengen lahjoja, hänen tulee tietää että tämä,\nmitä kirjoitan, on Herran käsky.',133,NULL),
 (3342,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',38,'8Jos joku ei tätä\ntunnusta, Jumala ei tunnusta häntä.',133,NULL),
 (3343,'Anonymous','2008-10-17 16:33:42','Anonymous','2008-10-17 16:33:42',39,'9Veljet,\ntavoitelkaa siis profetoimisen lahjaa älkääkä estäkö\nkielillä puhumista.',133,NULL),
 (3344,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',40,'0Kaiken on vain tapahduttava\narvokkaasti ja hyvässä järjestyksessä. ',133,NULL),
 (3345,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',1,'Veljet, minä palautan mieleenne sen evankeliumin,\njonka olen julistanut teille. Te olette ottaneet sen\nvastaan ja pidätte siitä kiinni,',134,NULL),
 (3346,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',2,'ja sen avulla te\nmyös pelastutte, jos säilytätte sen sellaisena kuin\nminä sen julistin; muuten olette turhaan tulleet\nuskoviksi.',134,NULL),
 (3347,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',3,'Ennen muuta annoin teille tiedoksi\ntämän, minkä itse olin saanut vastaanottaa:  ',134,NULL),
 (3348,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',6,'Sen jälkeen hän ilmestyi samalla kertaa yli\nviidellesadalle veljelle, joista useimmat ovat yhä\nelossa, vaikka jotkut ovatkin jo nukkuneet pois.',134,NULL),
 (3349,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',7,'Tämän jälkeen hän ilmestyi Jaakobille ja sitten\nkaikille apostoleille.',134,NULL),
 (3350,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',8,'Viimeiseksi kaikista hän\nilmestyi minullekin, joka olen kuin keskosena\nsyntynyt.',134,NULL),
 (3351,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',9,'Olenhan apostoleista vähäisin enkä edes\nansaitse apostolin nimeä, koska olen vainonnut Jumalan\nseurakuntaa.',134,NULL),
 (3352,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',10,'0Mutta Jumalan armosta minä olen se\nmikä olen, eikä hänen armonsa minua kohtaan ole mennyt\nhukkaan. Olen tehnyt enemmän työtä kuin kukaan heistä,\nen tosin minä itse, vaan Jumalan armo, joka on ollut\nvoimani. ',134,NULL),
 (3353,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',11,'1Näin me siis julistamme, minä niin kuin hekin, ja\nnäin te myös olette uskoneet.   ',134,NULL),
 (3354,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',20,'0Mutta nyt Kristus on herätetty kuolleista,\nesikoisena niiden joukosta jotka ovat kuolleet.',134,NULL),
 (3355,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',21,'1Kun kerran kuolema sai alkunsa ihmisestä, samoin\nkuolleiden ylösnousemus on alkanut ihmisestä.',134,NULL),
 (3356,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',22,'2Sillä niin kuin kaikki ihmiset Aadamista\nosallisina kuolevat, niin myös kaikki Kristuksesta\nosallisina tehdään eläviksi,',134,NULL),
 (3357,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',23,'3jokainen vuorollaan:\nesikoisena Kristus ja sen jälkeen Kristuksen omat, kun\nhän tulee.',134,NULL),
 (3358,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',24,'4Sitten seuraa kaiken päätös, kun hän\nluovuttaa kuninkuuden Jumalalle, Isälle, kukistettuaan\nkaiken vallan, mahdin ja voiman.',134,NULL),
 (3359,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',25,'5Kristuksen on\nnäet määrä hallita, kunnes hän on saattanut kaikki\nvihollisensa jalkojensa alle.',134,NULL),
 (3360,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',26,'6Vihollisista\nviimeisenä kukistetaan kuolema.',134,NULL),
 (3361,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',27,'7Sillä \"Jumala on\nalistanut hänen jalkojensa alle kaiken\". Kun sanotaan,\nettä kaikki on alistettu hänelle, tämän ulkopuolelle\njää tietysti Jumala, joka itse on kaiken alistanut\nKristuksen valtaan.',134,NULL),
 (3362,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',28,'8Sitten kun kaikki on saatettu\nhänen valtaansa, silloin hän itse, Poika, alistuu sen\nvaltaan, joka on saattanut kaiken hänen valtaansa, ja\nniin Jumala hallitsee täydellisesti kaikkea. ',134,NULL),
 (3363,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',29,'9Mitä varten sitten jotkut antavat kastaa itsensä\nkuolleiden puolesta? Ellei kuolleita lainkaan\nherätetä, miksi he kastattavat itsensä näiden\npuolesta?',134,NULL),
 (3364,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',30,'0Entä mitä varten me joka hetki\nantaudumme vaaraan?',134,NULL),
 (3365,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',31,'1Niin totta kuin te olette\nminun ylpeyteni Herramme Kristuksen Jeesuksen edessä,\nminä katson joka päivä kuolemaa silmiin.',134,NULL),
 (3366,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',32,'2Olisiko\nminun kannattanut taistella Efesoksessa petoja\nvastaan, jos asiaa ajatellaan vain tavalliseen\nihmistapaan? Ellei kuolleita herätetä eloon, niin\n\"syödään ja juodaan, huomenna kuollaan\".',134,NULL),
 (3367,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',33,'3Älkää\neksykö! \"Huono seura hyvät tavat turmelee.\"',134,NULL),
 (3368,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',34,'4Tulkaa\nlopultakin järkiinne, älkää tehkö syntiä! On näet\neräitä, jotka eivät Jumalasta mitään tiedä -- tämän\nsanon teidän häpeäksenne.   ',134,NULL),
 (3369,'Anonymous','2008-10-17 16:33:43','Anonymous','2008-10-17 16:33:43',42,'2Samoin tapahtuu kuolleiden ylösnousemuksessa. Se,\nmikä kylvetään katoavana, nousee katoamattomana.',134,NULL),
 (3370,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',43,'3Mikä kylvetään vähäpätöisenä, nousee kirkkaana.\nMikä kylvetään heikkona, nousee täynnä voimaa.',134,NULL),
 (3371,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',44,'4Kylvetään ajallinen ruumis, nousee hengellinen\nruumis. Jos kerran on olemassa ajallinen ruumis, on\nmyös hengellinen.',134,NULL),
 (3372,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',45,'5Onkin kirjoitettu:\n\"Ensimmäisestä ihmisestä, Aadamista, tuli elävä\nolento.\" Mutta viimeisestä Aadamista tuli eläväksi\ntekevä henki.',134,NULL),
 (3373,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',46,'6Ensimmäisenä ei siis ole hengellinen\nvaan ajallinen; vasta sen jälkeen tulee hengellinen.',134,NULL),
 (3374,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',47,'7Ensimmäinen ihminen on maallinen, maasta\nlähtöisin, toinen ihminen on taivaasta.',134,NULL),
 (3375,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',48,'8Millainen\ntuo maallinen ihminen oli, sellaisia ovat kaikki\nmaalliset ihmiset, ja millainen tuo taivaallinen\nihminen on, sellaisia tulevat olemaan taivaalliset\nihmiset.',134,NULL),
 (3376,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',49,'9Ja niin kuin me nyt olemme maallisen\nihmisen kaltaisia, niin me tulemme kerran taivaallisen\nihmisen kaltaisiksi. ',134,NULL),
 (3377,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',50,'0Sen sanon, veljet, ettei liha ja veri voi saada\nomakseen Jumalan valtakuntaa ja ettei katoava voi\nsaada omakseen katoamattomuutta.',134,NULL),
 (3378,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',51,'1Nyt ilmoitan\nteille salaisuuden: Me emme kaikki kuole, mutta kaikki\nme muutumme,',134,NULL),
 (3379,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',52,'2yhtäkkiä, silmänräpäyksessä,\nviimeisen pasuunan soidessa. Pasuuna soi, ja kuolleet\nherätetään katoamattomina ja me muut muutumme.',134,NULL),
 (3380,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',53,'3Tämän katoavan on näet pukeuduttava\nkatoamattomuuteen ja kuolevaisen kuolemattomuuteen.',134,NULL),
 (3381,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',54,'4Mutta kun katoava pukeutuu katoamattomuuteen ja\nkuolevainen kuolemattomuuteen, silloin toteutuu\nkirjoitusten sana:  ',134,NULL),
 (3382,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',56,'6Kuoleman pistin on synti, ja synnin voimana on\nlaki.',134,NULL),
 (3383,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',57,'7Mutta kiitos Jumalalle, joka antaa meille\nvoiton meidän Herramme Jeesuksen Kristuksen kautta! ',134,NULL),
 (3384,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',58,'8Olkaa siis vahvoja ja järkähtämättömiä, rakkaat\nveljeni, ja tehkää aina innokkaasti Herran työtä.\nTietäkää, ettei Herra anna teidän työnne mennä\nhukkaan. ',134,NULL),
 (3385,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',1,'Kun keräätte varoja Jerusalemin pyhille,\nnoudattakaa niitä ohjeita, jotka olen antanut Galatian\nseurakunnille:',135,NULL),
 (3386,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',2,'kunkin teistä on aina sapatin\njälkeisenä päivänä pantava syrjään rahaa sen mukaan\nkuin hänellä on varaa, jottei keräystä pantaisi\ntoimeen vasta kun olen tullut sinne.',135,NULL),
 (3387,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',3,'Sitten kun\ntulen, lähetän teidän valitsemanne ja valtuuttamanne\nhenkilöt viemään lahjaanne Jerusalemiin.',135,NULL),
 (3388,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',4,'Jos minun\nitsenikin on syytä lähteä matkaan, niin matkustamme\nyhdessä.   ',135,NULL),
 (3389,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',10,'0Kun Timoteus tulee, katsokaa ettei hänen tarvitse\nolla siellä mistään huolissaan. Hän tekee Herran työtä\nniin kuin minäkin,',135,NULL),
 (3390,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',11,'1kenenkään ei pidä halveksia\nhäntä. Auttakaa hänet sitten onnellisesti\npaluumatkalle. Odotan häntä täällä muiden veljien\nkanssa. ',135,NULL),
 (3391,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',12,'2Veli Apollosta olen kovasti kehottanut lähtemään\nmuiden veljien kanssa teidän luoksenne, mutta hänen\nmatkansa ei nyt voinut toteutua. Hän tulee sitten kun\nsaa sopivan tilaisuuden.   ',135,NULL),
 (3392,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',15,'5Minulla on teille pyyntö, veljet. Niin kuin\ntiedätte, Stefanas ja hänen perheväkensä ovat ensi\nhedelmä työstämme Akhaiassa, ja he ovat antautuneet\npalvelemaan pyhiä.',135,NULL),
 (3393,'Anonymous','2008-10-17 16:33:44','Anonymous','2008-10-17 16:33:44',16,'6Olkaa te puolestanne kuuliaisia\nheille ja kaikille, jotka työskentelevät ja näkevät\nvaivaa yhdessä heidän kanssaan.',135,NULL),
 (3394,'Anonymous','2008-10-17 16:33:45','Anonymous','2008-10-17 16:33:45',17,'7Olen iloinen\nsiitä, että Stefanas, Fortunatus ja Akaikos ovat\ntulleet tänne. He korvaavat minulle sen, että joudun\nolemaan erossa teistä.',135,NULL),
 (3395,'Anonymous','2008-10-17 16:33:45','Anonymous','2008-10-17 16:33:45',18,'8He ovat virkistäneet minua\nniin kuin teitäkin. Antakaa tunnustus tällaisille\nmiehille! ',135,NULL),
 (3396,'Anonymous','2008-10-17 16:33:45','Anonymous','2008-10-17 16:33:45',19,'9Aasian maakunnan seurakunnat lähettävät teille\nterveisensä. Myös Aquila ja Prisca ja heidän kodissaan\nkokoontuva seurakunta lähettävät teille paljon\nterveisiä Herran nimeen.',135,NULL),
 (3397,'Anonymous','2008-10-17 16:33:45','Anonymous','2008-10-17 16:33:45',20,'0Terveisiä kaikilta\nveljiltä. Tervehtikää toisianne pyhällä suudelmalla. ',135,NULL),
 (3398,'Anonymous','2008-10-17 16:33:45','Anonymous','2008-10-17 16:33:45',21,'1Minä, Paavali, kirjoitan tähän tervehdyksen\nomakätisesti.',135,NULL),
 (3399,'Anonymous','2008-10-17 16:33:45','Anonymous','2008-10-17 16:33:45',22,'2Jos joku ei rakasta Herraa, hän\nolkoon kirottu. Maranata!*Herramme, tule!',135,NULL),
 (3400,'Anonymous','2008-10-17 16:33:45','Anonymous','2008-10-17 16:33:45',23,'3Herran\nJeesuksen armo olkoon teidän kanssanne.',135,NULL),
 (3401,'Anonymous','2008-10-17 16:33:45','Anonymous','2008-10-17 16:33:45',24,'4Rakkaat\nterveiseni teille kaikille, te Kristuksen Jeesuksen\nomat.  ',135,NULL),
 (3402,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',1,'Paavali, Jumalan tahdosta Kristuksen Jeesuksen\napostoli, ja veli Timoteus tervehtivät Korintissa\nolevaa Jumalan seurakuntaa ja kaikkia pyhiä koko\nAkhaiassa.',136,NULL),
 (3403,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',2,'Jumalan, meidän Isämme, ja Herran\nJeesuksen Kristuksen armo ja rauha teille.   ',136,NULL),
 (3404,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',8,'Teidän on hyvä tietää, veljet, millaisessa\nahdingossa me olimme Aasian maakunnassa. Jouduimme\nniin suunnattomiin ja ylivoimaisiin vaikeuksiin,\nettemme enää uskoneet selviävämme hengissä.',136,NULL),
 (3405,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',9,'Me\nsaimme tuntea olevamme kuolemaan tuomittuja, jotta\nemme luottaisi itseemme, vaan Jumalaan, joka herättää\nkuolleet.',136,NULL),
 (3406,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',10,'0Tällaisesta kuolemanvaarasta hän pelasti\nmeidät, ja niin hän pelastaa vastedeskin. Me luotamme\nsiihen, että hän on pelastava meidät,',136,NULL),
 (3407,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',11,'1kun tekin\nautatte meitä rukouksellanne. Näin nousee monista\nsydämistä kiitos Jumalalle siitä armosta, jota hän on\nosoittanut meitä kohtaan.   ',136,NULL),
 (3408,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',15,'5Tähän luottaen aioin ensin tulla teidän luoksenne,\njotta olisitte saaneet uuden todistuksen Jumalan\narmosta.',136,NULL),
 (3409,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',16,'6Tarkoitukseni oli jatkaa teidän luotanne\nmatkaa Makedoniaan ja palata sieltä taas luoksenne. Te\nolisitte sitten varustaneet minut matkalle Juudeaan.',136,NULL),
 (3410,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',17,'7Ei kai tämä aikomukseni ollut vain hetkellinen\npäähänpisto? Teenkö ehkä suunnitelmiani oman pääni\nmukaan? Sanonko: \"Kyllä, kyllä\", kun tarkoitan: \"Ei,\nei\"?',136,NULL),
 (3411,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',18,'8Jumala voi todistaa, etten sano teille sekä:\n\"Kyllä\", että: \"Ei.\"',136,NULL),
 (3412,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',19,'9Eihän myöskään Jeesus\nKristus, Jumalan Poika, jota minä, Silvanus ja\nTimoteus olemme teille julistaneet, tullut ollakseen\nsekä \"kyllä\" että \"ei\", vaan hänessä toteutui \"kyllä\".',136,NULL),
 (3413,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',20,'0Ovathan Jumalan lupaukset, niin monta kuin niitä\non, saaneet hänessä vahvistuksen. Siksi mekin\nvastaamme hänen kauttaan: \"Aamen\", Jumalan kunniaksi.',136,NULL),
 (3414,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',21,'1Mutta Jumala itse vahvistaa meitä ja teitä\nuskossamme Kristukseen, Voideltuun, ja on myös antanut\nmeille voitelunsa:',136,NULL),
 (3415,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',22,'2hän on painanut meihin\nsinettinsä ja antanut meidän sydämiimme vakuudeksi\nHengen. ',136,NULL),
 (3416,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',23,'3Kutsun Jumalan todistajaksi ja vakuutan oman\nhenkeni kautta, että olen lykännyt Korintin-matkaani\nvain teitä säästääkseni.',136,NULL),
 (3417,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',24,'4En tarkoita, että\nhaluaisimme määräillä teidän uskoanne, tahdomme vain\nauttaa teitä saamaan ilon. Teidän uskonne on kyllä\nluja. ',136,NULL),
 (3418,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',1,'Päätin näet, että vierailuni teidän luonanne ei\ntällä kertaa saa aiheuttaa mielipahaa.',137,NULL),
 (3419,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',2,'Jos minä\npahoitan teidän mielenne, eihän silloin kukaan voi\nsaada minuakaan iloiseksi, paitsi se jolle olen\ntuottanut surua.',137,NULL),
 (3420,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',3,'Juuri tästä kirjoitinkin, jottei\nminun tarvitsisi tullessani pahoittaa mieltäni niiden\nvuoksi, joista minun pitäisi saada iloa. Luotin\nsiihen, että minun iloni on teidänkin ilonne.',137,NULL),
 (3421,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',4,'Kirjoitin teille suuressa tuskassa, sydän raskaana\nja kyynelsilmin, mutta en aiheuttaakseni teille surua,\nvaan osoittaakseni, kuinka suurta rakkautta tunnen\nerityisesti teitä kohtaan.     ',137,NULL),
 (3422,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',14,'4Kiitos olkoon Jumalalle, joka aina kuljettaa meitä\nKristuksen voittosaatossa ja antaa meidän kaikkialla\nlevittää Kristuksen tuntemisen tuoksua!',137,NULL),
 (3423,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',15,'5Me olemme\nKristuksen tuoksu, joka nousee Jumalan eteen; tämän\ntuoksun tuntevat sekä ne, jotka pelastuvat, että ne,\njotka joutuvat kadotukseen.',137,NULL),
 (3424,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',16,'6Se on toisille\nkuoleman haju, joka tuo kuoleman, toisille elämän\ntuoksu, joka tuo elämän. Mutta kuka on tähän\nkelvollinen?',137,NULL),
 (3425,'Anonymous','2008-10-17 16:33:46','Anonymous','2008-10-17 16:33:46',17,'7Me emme ainakaan ole sellaisia kuin\nne monet, jotka kaupittelevat Jumalan sanaa. Me\njulistamme sitä Jumalan edessä ja Kristusta palvellen,\nväärentämättömänä ja sellaisena kuin se tulee\nJumalalta. ',137,NULL),
 (3426,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',1,'Alammeko jälleen suositella itseämme? Tarvitsemmeko\nmekin teitä varten tai teiltä suosituskirjeitä niin\nkuin eräät muut?',138,NULL),
 (3427,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',2,'Te itse olette meidän\nsuosituskirjeemme, joka on kirjoitettu meidän\nsydämiimme kaikkien ihmisten nähtäväksi ja\nluettavaksi.',138,NULL),
 (3428,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',3,'Olettehan te selvästi Kristuksen\nkirje, jonka kirjoittamisen hän uskoi meille. Tätä\nkirjettä ei ole kirjoitettu musteella vaan elävän\nJumalan Hengellä, ei kivitauluihin vaan ihmissydämiin. ',138,NULL),
 (3429,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',4,'Tällaisen luottamuksen Jumalaan on Kristus saanut\nmeissä aikaan.',138,NULL),
 (3430,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',5,'En tarkoita, että kykenisimme\najattelemaan mitään omin päin, mitään mikä olisi\nperäisin meistä itsestämme. Meidän kykymme on saatu\nJumalalta,',138,NULL),
 (3431,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',6,'ja hän on myös tehnyt meidät kykeneviksi\npalvelemaan uutta liittoa, jota ei hallitse kirjain\nvaan Henki. Kirjain näet tuo kuoleman, mutta Henki\ntekee eläväksi. ',138,NULL),
 (3432,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',7,'Jo kuolemaa palveleva virka, jonka säädökset oli\nkaiverrettu kivitauluihin, säteili sellaista\nkirkkautta, että israelilaiset eivät voineet katsoa\nMoosesta kasvoihin. Ja kuitenkin hänen kasvojensa\nkirkkaus oli katoavaa.',138,NULL),
 (3433,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',8,'Kuinka paljon suurempaa\nkirkkautta säteileekään Hengen palveluvirka!',138,NULL),
 (3434,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',9,'Jos\nkerran tuomion julistamisen virkaa ympäröi kirkkaus,\npaljon kirkkaampi loiste ympäröi silloin vanhurskauden\njulistamisen virkaa.',138,NULL),
 (3435,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',10,'0Sen kirkkaus on niin\nylivoimainen, että sen rinnalla himmenee se, mikä\nennen säteili kirkkaana.',138,NULL),
 (3436,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',11,'1Sillä jos sekin, mikä on\nkatoavaa, säteilee kirkkautta, vielä paljon\nkirkkaampaa on se, mikä on pysyvää. ',138,NULL),
 (3437,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',12,'2Koska meillä on tällainen toivo, esiinnymme aivan\navoimesti,',138,NULL),
 (3438,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',13,'3toisin kuin Mooses, joka pani\nkasvoilleen peitteen, jotteivät israelilaiset näkisi\nkatoavaisen loisteen häviämistä.',138,NULL),
 (3439,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',14,'4Heidän mielensä\npaatuivat. Sama peite pysyy edelleenkin paikoillaan,\nkun he lukevat vanhan liiton kirjoituksia, sillä vasta\nKristus sen poistaa.',138,NULL),
 (3440,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',15,'5Yhä vieläkin heidän sydämensä\npäällä on peite Mooseksen lakia luettaessa.',138,NULL),
 (3441,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',16,'6Mutta\nkun heidän sydämensä kääntyy Herran puoleen, peite\notetaan pois.',138,NULL),
 (3442,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',17,'7Herra on Henki, ja missä Herran\nHenki on, siellä on vapaus.',138,NULL),
 (3443,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',18,'8Me kaikki, jotka\nkasvot peittämättöminä katselemme Herran kirkkautta\nkuin kuvastimesta, muutumme saman kirkkauden\nkaltaisiksi, kirkkaudesta kirkkauteen. Tämän saa\naikaan Herra, joka on Henki. ',138,NULL),
 (3444,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',1,'Kun meillä Jumalan armosta on tällainen\npalveluvirka, me emme lannistu.',139,NULL),
 (3445,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',2,'Olemme hylänneet\nkaiken salakähmäisen, emme toimi petollisesti emmekä\nvääristele Jumalan sanaa, vaan tuomme esiin totuuden\nja toivomme, että jokainen omassatunnossaan arvioi\nmeidät Jumalan edessä.',139,NULL),
 (3446,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',3,'Jos meidän julistamamme\nevankeliumi on peitossa, se on peitossa niiltä, jotka\njoutuvat kadotukseen.',139,NULL),
 (3447,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',4,'Tarkoitan niitä, joiden\nmielen tämän maailman jumala on sokaissut, niin että\nhe epäuskossaan eivät näe Kristuksen evankeliumin\nkirkkaudesta säteilevää valoa, Kristuksen, joka on\nJumalan kuva.',139,NULL),
 (3448,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',5,'Emmehän me julista sanomaa itsestämme\nvaan Jeesuksesta Kristuksesta: Jeesus on Herra, ja hän\non lähettänyt meidät palvelemaan teitä.',139,NULL),
 (3449,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',6,'Jumala,\njoka sanoi: \"Tulkoon pimeyteen valo\", valaisi itse\nmeidän sydämemme. Näin Jumalan kirkkaus, joka säteilee\nKristuksen kasvoilta, opitaan tuntemaan, ja se\nlevittää valoaan. ',139,NULL),
 (3450,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',7,'Tämä aarre on meillä saviastioissa, jotta\nnähtäisiin tuon valtavan voiman olevan peräisin\nJumalasta eikä meistä itsestämme.',139,NULL),
 (3451,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',8,'Me olemme kaikin\ntavoin ahtaalla mutta emme umpikujassa, neuvottomia\nmutta emme toivottomia,',139,NULL),
 (3452,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',9,'vainottuja mutta emme\nhylättyjä, maahan lyötyjä mutta emme tuhottuja.',139,NULL),
 (3453,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',10,'0Me\nkannamme aina ruumiissamme Jeesuksen kuolemaa, jotta\nmyös Jeesuksen elämä tulisi meidän ruumiissamme\nnäkyviin.',139,NULL),
 (3454,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',11,'1Me tosin elämme, mutta meidät annetaan\nJeesuksen tähden alituisesti alttiiksi kuolemalle,\njotta myös Jeesuksen elämä tulisi näkyviin\nkuolevaisessa ruumiissamme.',139,NULL),
 (3455,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',12,'2Meissä siis tekee\ntyötään kuolema, mutta teissä elämä.',139,NULL),
 (3456,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',13,'3Meillä on\nsama uskon Henki, josta on kirjoitettu: \"Minä uskon,\nja siksi puhun.\" Niin mekin puhumme, koska uskomme.',139,NULL),
 (3457,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',14,'4Me tiedämme, että hän, joka herätti kuolleista\nHerran Jeesuksen, herättää Jeesuksen tullessa\nmeidätkin ja tuo meidät eteensä yhdessä teidän\nkanssanne.',139,NULL),
 (3458,'Anonymous','2008-10-17 16:33:47','Anonymous','2008-10-17 16:33:47',15,'5Tämä kaikki tapahtuu teidän takianne,\njotta yhä enenevä armo synnyttäisi yhä useammissa\nrunsasta kiitollisuutta Jumalan kunniaksi.   ',139,NULL),
 (3459,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',1,'Me tiedämme, että vaikka tämä meidän maallinen\ntelttamajamme puretaankin, Jumalalla on taivaassa\nmeitä varten ikuinen asunto, joka ei ole ihmiskätten\ntyötä.',140,NULL),
 (3460,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',2,'Täällä ollessamme me huokailemme ja\nkaipaamme päästä pukeutumaan taivaalliseen asuumme,',140,NULL),
 (3461,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',3,'sillä sitten kun olemme pukeutuneet siihen, emme\njää alastomiksi.',140,NULL),
 (3462,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',4,'Me, jotka vielä asumme tässä\nmajassamme, huokailemme ahdistuneina. Emme haluaisi\nriisuutua vaan pukeutua uuteen asuun, niin että elämä\nkätkisi sisäänsä sen, mikä on kuolevaista.',140,NULL),
 (3463,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',5,'Juuri\ntähän Jumala on valmistanut meidät, ja vakuudeksi hän\non antanut meille Hengen. ',140,NULL),
 (3464,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',6,'Olemme siis aina turvallisella mielellä, vaikka\ntiedämmekin, että niin kauan kuin tämä ruumis on\nkotimme, olemme poissa Herran luota.',140,NULL),
 (3465,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',7,'Mehän elämme\nuskon varassa, emme näkemisen.',140,NULL),
 (3466,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',8,'Mutta mielemme on\nturvallinen, vaikka haluaisimmekin muuttaa pois\nruumiistamme, kotiin Herran luo.',140,NULL),
 (3467,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',9,'Siksi myös pyrimme\nnoudattamaan hänen mieltään, olimmepa jo perillä hänen\nluonaan tai vielä sieltä poissa.',140,NULL),
 (3468,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',10,'0Meidän kaikkien\non tultava Kristuksen tuomioistuimen eteen, jotta\nkukin saisi sen mukaan, mitä ajallisessa elämässään on\ntehnyt, hyvää tai pahaa.   ',140,NULL),
 (3469,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',16,'6Niinpä emme enää arvioi ketään pelkästään\ninhimilliseltä kannalta. Vaikka olisimmekin ennen\ntunteneet Kristuksen pelkästään inhimilliseltä\nkannalta, emme enää tunne.',140,NULL),
 (3470,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',17,'7Jokainen, joka on\nKristuksessa, on siis uusi luomus. Vanha on kadonnut,\nuusi on tullut tilalle!',140,NULL),
 (3471,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',18,'8Kaiken on saanut aikaan\nJumala, joka Kristuksen välityksellä on tehnyt meidän\nkanssamme sovinnon ja uskonut meille tämän sovituksen\nviran.',140,NULL),
 (3472,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',19,'9Jumala itse teki Kristuksessa sovinnon\nmaailman kanssa eikä lukenut ihmisille viaksi heidän\nrikkomuksiaan; meille hän uskoi sovituksen sanan.',140,NULL),
 (3473,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',20,'0Me olemme siis Kristuksen lähettiläitä, ja Jumala\npuhuu teille meidän kauttamme. Pyydämme Kristuksen\npuolesta: suostukaa sovintoon Jumalan kanssa.',140,NULL),
 (3474,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',21,'1Kristukseen, joka oli puhdas synnistä, Jumala\nsiirsi kaikki meidän syntimme, jotta me hänessä\nsaisimme Jumalan vanhurskauden. ',140,NULL),
 (3475,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',1,'Jumalan työtovereina me vetoamme teihin: ottakaa\nJumalan armo vastaan niin, ettei se jää turhaksi!',141,NULL),
 (3476,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',2,'Hänhän sanoo:  Juuri nyt on oikea hetki, juuri nyt on pelastuksen\npäivä.   ',141,NULL),
 (3477,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',11,'1Me puhumme teille avoimesti, korinttilaiset,\nsydämemme on avara,',141,NULL),
 (3478,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',12,'2siinä on teille tilaa. Sen\nsijaan teidän sydämenne on ahdas.',141,NULL),
 (3479,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',13,'3Antakaa\nvastalahja -- puhun kuin lapsilleni -- avartukaa\ntekin!    ',141,NULL),
 (3480,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',17,'7Siksi Herra sanoo:  ',141,NULL),
 (3481,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',1,'Rakkaat ystävät! Kun kerran olemme saaneet\ntällaiset lupaukset, meidän tulee puhdistautua\nkaikesta ruumiin ja hengen saastaisuudesta ja Jumalaa\npeläten pyhittää elämämme kokonaan hänelle.   ',142,NULL),
 (3482,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',5,'Makedoniaan tultuamme emme saaneet hetkenkään\nrauhaa. Meitä ahdistivat kaikenlaiset vaikeudet,\nulkoapäin taistelut, sisältäpäin pelot.',142,NULL),
 (3483,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',6,'Mutta\nJumala, joka rohkaisee masentuneita, lohdutti meitäkin\nantamalla Tituksen tulla luoksemme.',142,NULL),
 (3484,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',7,'Hänen tulonsa\nlisäksi meitä lohdutti myös se rohkaisu, jota hän oli\nsaanut teidän luonanne. Hän kertoi, kuinka te\nikävöitte minua, kuinka nyt surette ja kuinka\nkiivaasti pidätte minun puoltani, ja se on vielä\nenemmän ilahduttanut minua. ',142,NULL),
 (3485,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',8,'Vaikka tuotin kirjeelläni teille mielipahaa, en\nsitä kadu. Jos minua kaduttikin, kun näin teidän\npahoittaneen siitä mielenne vähäksi aikaa,',142,NULL),
 (3486,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',9,'olen nyt\niloinen -- en siksi, että te tulitte surullisiksi,\nvaan koska murheenne sai teidät muuttamaan mielenne.\nMurheenne oli Jumalan mielen mukaista, ja niinpä emme\naiheuttaneet teille mitään vahinkoa.',142,NULL),
 (3487,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',10,'0Jumalan\nmielen mukainen murhe saa aikaan parannuksen, jota ei\ntarvitse katua, sillä se johtaa pelastukseen.\nMaallinen murhe sen sijaan tuottaa kuoleman.',142,NULL),
 (3488,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',11,'1Näettehän, millaista intoa tuo Jumalan mielen\nmukainen murhe on luonut teihin, miten se on saanut\nteidät puolustautumaan, suuttumaan ja pelkäämään,\nikävöimään meitä ja päättäväisesti rankaisemaan\nsyyllistä. Olette kaikin tavoin osoittaneet olevanne\ntässä asiassa viattomia.',142,NULL),
 (3489,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',12,'2Minä en kirjoittanut\nteille sen enempää vääryyttä tehneen kuin vääryyttä\nkärsineenkään vuoksi, vaan jotta teille itsellenne\nJumalan edessä kävisi selväksi, kuinka vakavasti te\ntodella olette minun puolellani.',142,NULL),
 (3490,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',13,'3Tämä on meitä\nrohkaissut. Mutta vielä tätä rohkaisuakin enemmän meitä on\nilahduttanut Tituksen ilo siitä, että hän voi nyt\najatella teitä kaikkia huojentunein mielin.',142,NULL),
 (3491,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',14,'4Minun\nei tarvinnut hävetä sitä, että olin kiitellyt teitä\nhänelle. Niin kuin kaikki, mitä olen teille puhunut,\non totta, samoin on osoittautunut todeksi se, mistä\nolen teitä kehunut Titukselle.',142,NULL),
 (3492,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',15,'5Hänen sydämensä\nlämpenee, kun hän muistaa, miten kuuliaisia te kaikki\nolitte ja miten arasti ja kunnioittavasti te otitte\nhänet vastaan.',142,NULL),
 (3493,'Anonymous','2008-10-17 16:33:48','Anonymous','2008-10-17 16:33:48',16,'6Olen iloinen siitä, että voin joka\nsuhteessa luottaa teihin. ',142,NULL),
 (3494,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',1,'Veljet, tahdomme teidän tietävän, millaisen armon\nJumala on suonut Makedonian seurakunnille.',143,NULL),
 (3495,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',2,'Vaikka\nvaikeudet ovat raskaasti koetelleet niitä, uskovien\nilo oli niin ylitsevuotava, että he äärimmäisessä\nköyhyydessäänkin osoittivat runsasta anteliaisuutta.',143,NULL),
 (3496,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',3,'Voin vakuuttaa, että he antoivat voimiensa mukaan,\njopa yli voimiensa. Omasta aloitteestaan',143,NULL),
 (3497,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',4,'he\npyytämällä pyysivät, että antaisimme heidän osallistua\nyhteiseen rakkaudentyöhön, pyhien avustamiseen.',143,NULL),
 (3498,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',5,'Eivätkä he tehneet vain sitä mitä olimme toivoneet,\nvaan ennen kaikkea antoivat Jumalan tahdon mukaisesti\nitsensä Herralle ja myös meille.',143,NULL),
 (3499,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',6,'Niinpä me\nkehotimme Titusta jatkamaan tätä rakkaudentyötä ja\nsaattamaan sen teidän keskuudessanne päätökseen.',143,NULL),
 (3500,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',7,'Niin kuin teillä on runsain mitoin kaikkea, uskoa,\npuhetaitoa, tietoa, kaikkinaista intoa ja sitä\nrakkautta, joka meistä on tarttunut teihin, niin\nteidän tulee myös olla runsaskätisiä tätä lahjaa\nantaessanne. ',143,NULL),
 (3501,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',8,'En sano tätä käskeäkseni vaan koetellakseni teidän\nrakkautenne aitoutta kertomalla toisten innosta.',143,NULL),
 (3502,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',9,'Tehän tunnette Herramme Jeesuksen Kristuksen armon:\nhän oli rikas mutta tuli köyhäksi teidän vuoksenne,\njotta te rikastuisitte hänen köyhyydestään.',143,NULL),
 (3503,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',10,'0Annan\nvain neuvon tässä asiassa. Siitä on hyötyä teille,\njotka viime vuonna olitte alkamassa tätä työtä ja myös\nolitte siihen halukkaita.',143,NULL),
 (3504,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',11,'1Saattakaa työnne nyt\nloppuun! Halusitte sitä innokkaasti, viekää se siis\npäätökseen mahdollisuuksienne mukaan!',143,NULL),
 (3505,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',12,'2Jos\nantajalla on hyvää tahtoa, hänen lahjaansa pidetään\narvossa sen mukaan, mitä hänellä on, eikä vaadita\nsellaista, mitä hänellä ei ole.',143,NULL),
 (3506,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',13,'3Tarkoitus ei toki\nole, että muiden tilanteen helpottuessa te joutuisitte\ntiukalle. Kysymys on vastavuoroisesta jakamisesta.',143,NULL),
 (3507,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',14,'4Nyt on teillä yllin kyllin ja voitte lievittää\nheidän puutettaan, sitten voivat taas he\nyltäkylläisyydestään lievittää teidän puutettanne, ja\nnäin toteutuu oikeus ja kohtuus.',143,NULL),
 (3508,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',15,'5Onhan\nkirjoitettu:    ',143,NULL),
 (3509,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',1,'Minun on oikeastaan tarpeetonta kirjoittaa teille\npyhien avustamisesta.',144,NULL),
 (3510,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',2,'Tiedänhän teidän\nhalukkuutenne. Olen kehunut teitä sen vuoksi\nmakedonialaisille ja kertonut, että Akhaiassa asia on\nhoidettu jo viime vuonna. Teidän intonne on saanut\ntäälläkin useimmat liikkeelle.',144,NULL),
 (3511,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',3,'Lähetän nyt veljet\nmatkaan varmistaakseni, etten ole turhaan kehunut\nteitä tässä asiassa vaan että te olette valmiita, niin\nkuin olen sanonut.',144,NULL),
 (3512,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',4,'Joutuisihan meidän\nluottamuksemme häpeään, teistä itsestänne\npuhumattakaan, jos sinne tulisi joitakin\nmakedonialaisia minun kanssani ja he tapaisivat teidät\nvalmistautumattomina.',144,NULL),
 (3513,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',5,'Siksi pidin välttämättömänä\nkehottaa näitä veljiä lähtemään teidän luoksenne ennen\nmeitä ja järjestämään lupaamanne lahjan etukäteen\nkuntoon, niin että se olisi valmiina ja olisi runsas\neikä kitsas. ',144,NULL),
 (3514,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',6,'Muistakaa tämä: joka niukasti kylvää, se niukasti\nniittää, ja joka runsaasti kylvää, se runsaasti\nniittää.',144,NULL),
 (3515,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',7,'Kukin antakoon sen mukaan kuin on\nmielessään päättänyt, ei vastahakoisesti eikä pakosta,\nsillä iloista antajaa Jumala rakastaa.',144,NULL),
 (3516,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',8,'Hänellä on\nteille annettavana runsaasti kaikkia lahjoja, niin\nettä teillä on aina kaikki mitä tarvitsette ja voitte\ntehdä runsaasti kaikkea hyvää.',144,NULL),
 (3517,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',9,'Onhan kirjoitettu:  ',144,NULL),
 (3518,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',10,'0Hän, joka antaa kylväjälle siemenen ja suo\nravinnoksi leivän, antaa teillekin siemenen ja\nmoninkertaistaa sen, ja hän sallii teidän hyvyytenne\nsadon karttua.',144,NULL),
 (3519,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',11,'1Te saatte kaikkinaista rikkautta ja\nvoitte osoittaa runsaasti anteliaisuutta. Näin meidän\ntyömme synnyttää kiitollisuutta Jumalaa kohtaan.',144,NULL),
 (3520,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',12,'2Tämän palveluksen toimittaminen ei vain täytä\npyhien tarpeita, vaan lisäksi se saa yhä useammat\nkiittämään Jumalaa.',144,NULL),
 (3521,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',13,'3Kun te tällä työllänne annatte\ntodistuksen uskostanne, lahjanne saajat ylistävät\nJumalaa siitä, että te näin tunnustatte\nkuuliaisuutenne Kristuksen evankeliumille ja jaatte\nomastanne anteliaasti heille ja kaikille muillekin.',144,NULL),
 (3522,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',14,'4Näin he myös rukoilevat teidän puolestanne ja\nikävöivät teitä sen ylenpalttisen armon vuoksi, jota\nJumala on osoittanut teille.',144,NULL),
 (3523,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',15,'5Kiitos Jumalalle\nhänen sanomattoman suuresta lahjastaan! ',144,NULL),
 (3524,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',1,'Minä, Paavali, kehotan teitä vedoten Kristuksen\nlempeyteen ja hyvyyteen, minä, joka kuulemma teidän\nedessänne olen nöyrä mutta välimatkan päästä puhun\nteille rohkeasti.',145,NULL),
 (3525,'Anonymous','2008-10-17 16:33:49','Anonymous','2008-10-17 16:33:49',2,'Pyydän, ettei minun teidän\nluonanne tarvitsisi turvautua tähän rohkeuteen --\naikomukseni on näet käydä eräiden kimppuun, jotka\nkatsovat meidän toimivan niin kuin tämän maailman\nihmiset.',145,NULL),
 (3526,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',3,'Me elämme tässä maailmassa mutta emme\ntaistele tämän maailman tavoin.',145,NULL),
 (3527,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',4,'Taisteluaseemme\neivät ole ihmisten aseita, vaan niillä on Jumalan\nantama voima tuhota linnoituksia. Me kaadamme kumoon\njärjen päätelmät',145,NULL),
 (3528,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',5,'ja kaiken, mikä nousee ylpeänä\nvastustamaan Jumalan tuntemista. Me vangitsemme kaikki\najatukset kuuliaisiksi Kristukselle',145,NULL),
 (3529,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',6,'ja olemme\nvalmiit rankaisemaan kaikesta tottelemattomuudesta,\nheti kun te itse olette tulleet täysin kuuliaisiksi. ',145,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (3530,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',7,'Ottakaa huomioon selvät tosiasiat! Jos joku\nvarmasti uskoo kuuluvansa Kristukselle, hän ajatelkoon\nsitä, että samalla tavoin myös me olemme Kristuksen\nomia.',145,NULL),
 (3531,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',8,'Herra on valtuuttanut minut vahvistamaan\nteitä eikä lannistamaan, ja vaikka ylpeilisin\nvaltuuksistani hieman liikaakin, minun ei tarvitse\nhävetä.',145,NULL),
 (3532,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',9,'Älkää luulko, että minä kirjeilläni vain\npelottelen teitä.',145,NULL),
 (3533,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',10,'0Erääthän sanovat: \"Hänen\nkirjeensä ovat kyllä ankaria ja kiivaita, mutta hänen\noma esiintymisensä on avutonta ja puheensa mitätöntä.\"',145,NULL),
 (3534,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',11,'1Näiden on syytä pitää mielessä, että sitten kun\nolen taas luonanne, toimin samalla tavoin kuin olen\npoissa ollessani puhunut kirjeissäni. ',145,NULL),
 (3535,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',12,'2Meillä ei ole rohkeutta katsoa kuuluvamme\nsellaisiin, jotka itse suosittelevat itseään, eikä\nverrata itseämme heihin. He ovat ymmärtämättömiä, kun\nottavat mittapuukseen omat saavutuksensa ja vertaavat\nitseään vain itseensä.',145,NULL),
 (3536,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',13,'3Me taas emme ylpeile\nkohtuuttomasti, vaan sen mittapuun mukaan, jonka\nJumala on meille antanut määrätessään, että\ntyökenttämme ulottuu teihinkin asti.',145,NULL),
 (3537,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',14,'4Me emme\nkurkota liian pitkälle, ikään kuin te ette\nkuuluisikaan meidän toimintapiiriimme; olemmehan\nensimmäisinä tuoneet Kristuksen evankeliumin teille\nsaakka.',145,NULL),
 (3538,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',15,'5Emme ylpeile kohtuuttomasti emmekä toisten\nvaivannäöstä. Sen sijaan toivomme, että uskonne\nkasvaessa mekin pääsemme teidän keskuudessanne vielä\npaljon suurempaan arvoon saamamme tehtävän\ntoteuttajina.',145,NULL),
 (3539,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',16,'6Toivomme, että saamme julistaa\nevankeliumia vielä teitäkin etäämpänä asuville ja että\nvoimme ylpeillä omista emmekä toisten\naikaansaannoksista.',145,NULL),
 (3540,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',17,'7\"Joka ylpeilee, ylpeilköön\nHerrasta.\"',145,NULL),
 (3541,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',18,'8Luotettava ei ole se, joka itse\nsuosittelee itseään, vaan se, jota Herra suosittelee. ',145,NULL),
 (3542,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',1,'Kunpa sietäisitte minulta hiukan mielettömyyttä!\nVarmaan te siedättekin.',146,NULL),
 (3543,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',2,'Vartioin teitä\nintohimoisesti Jumalan puolesta. Olenhan kihlannut\nteidät yhdelle ainoalle miehelle, Kristukselle, ja\ntahdon tuoda teidät hänen eteensä kuin puhtaan\nneitsyen.',146,NULL),
 (3544,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',3,'Pelkään kuitenkin, että niin kuin käärme\nkavalasti petti Eevan, teidän mielenne turmeltuu\nettekä te enää ole vilpittömiä ja puhtaita\nsuhteessanne Kristukseen.',146,NULL),
 (3545,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',4,'Jos joku tulee luoksenne\nja julistaa toista Jeesusta kuin me olemme\njulistaneet, jos te saatte jonkin toisen hengen kuin\nsen, jonka olette jo saaneet, tai toisen evankeliumin\nkuin sen, jonka olette jo ottaneet vastaan, niin\nsellaista te kyllä hyvin siedätte.',146,NULL),
 (3546,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',5,'En minä\nkuitenkaan katso olevani mitenkään huonompi kuin nuo\nmainiot apostolit.',146,NULL),
 (3547,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',6,'Puhetaitoni on ehkä kehno, mutta\ntietoni ei, ja tietoni olen kaikin tavoin, kaikissa\nasioissa saattanut teidän nähtäväksenne. ',146,NULL),
 (3548,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',7,'Olenkohan tehnyt suurenkin synnin, kun auttaakseni\nteitä nousemaan olen alentanut itseni julistamalla\nteille Jumalan evankeliumia ilman korvausta?',146,NULL),
 (3549,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',8,'Voidakseni auttaa teitä olen riistänyt muita\nseurakuntia ottamalla niiltä vastaan rahaa.',146,NULL),
 (3550,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',9,'Teidän\nluonanne en ollut kenellekään rasituksena, vaikka\njouduinkin puutteeseen, sillä Makedoniasta tulleet\nveljet antoivat minulle kaiken mitä tarvitsin. Olen\nvaronut olemasta teille millään tavoin rasitukseksi ja\nvaron vastedeskin.',146,NULL),
 (3551,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',10,'0Niin totta kuin Kristuksen\ntotuus on minussa, tätä ylpeilemisen aihetta ei\nminulta Akhaian maakunnassa riistetä!',146,NULL),
 (3552,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',11,'1Kuinka niin?\nEnkö muka rakasta teitä? Sen tietää Jumala. ',146,NULL),
 (3553,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',12,'2Näin minä toimin nyt ja edelleenkin, jotta en\nantaisi halukkaille tilaisuutta ylpeilemiseen; eivät\nhe ole hiukkaakaan meitä parempia.',146,NULL),
 (3554,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',13,'3Semmoiset ovat\nvääriä apostoleja, petollisia työntekijöitä, he vain\ntekeytyvät Kristuksen apostoleiksi.',146,NULL),
 (3555,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',14,'4Eikä ihme,\ntekeytyyhän itse Saatanakin valon enkeliksi.',146,NULL),
 (3556,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',15,'5Ei\nsiis ole mitenkään merkillistä, että hänen\npalvelijansa esiintyvät Jumalan asian palvelijoina. He\nsaavat tekojensa mukaisen lopun.   ',146,NULL),
 (3557,'Anonymous','2008-10-17 16:33:50','Anonymous','2008-10-17 16:33:50',22,'2He ovat siis heprealaisia? Niin minäkin. He ovat\nisraelilaisia? Niin minäkin. He ovat Abrahamin\njälkeläisiä? Niin minäkin.',146,NULL),
 (3558,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',23,'3He ovat Kristuksen\npalvelijoita? Minä vielä enemmän -- puhun kuin\njärjetön. Olen saanut raataa kovemmin kuin he, olen\nollut enemmän vankeudessa, minua on ruoskittu ylen\nmäärin, monesti olen ollut kuolemanvaarassa.',146,NULL),
 (3559,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',24,'4Olen\njuutalaisilta viisi kertaa saanut \"yhtä vaille\nneljäkymmentä\" ruoskaniskua,',146,NULL),
 (3560,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',25,'5kolmesti olen saanut\nraippoja, kerran minua on kivitetty, kolme kertaa olen\njoutunut haaksirikkoon, olen ajelehtinut meressä\nvuorokauden.',146,NULL),
 (3561,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',26,'6Olen yhtenään ollut matkoilla, olen\nollut vaarassa hukkua jokiin ja joutua rosvojen\nkäsiin, vaarassa niin maanmiesteni kuin\nvierasheimoisten joukossa, vaarassa kaupungissa ja\nautiomaassa, vaarassa merellä, vaarassa valeveljien\nparissa.',146,NULL),
 (3562,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',27,'7Olen raatanut ja nähnyt vaivaa, valvonut\npaljon, kärsinyt nälkää ja janoa, paastonnut usein,\npalellut vähissä vaatteissa.',146,NULL),
 (3563,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',28,'8Kaikkien näiden\nhankaluuksien lisäksi minua joka päivä painaa huoli\nkaikista seurakunnista.',146,NULL),
 (3564,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',29,'9Jos joku on heikko, niin\nminäkin olen heikko. Jos joku lankeaa, se polttaa\nminua. ',146,NULL),
 (3565,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',30,'0Jos on pakko kerskailla, kerskun heikkoudestani.',146,NULL),
 (3566,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',31,'1Herran Jeesuksen Jumala ja Isä, joka on ikuisesti\nylistetty, tietää etten valehtele.',146,NULL),
 (3567,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',32,'2Damaskoksessa\nkuningas Aretaksen alainen päällikkö antoi vartioida\nkaupunkia pidättääkseen minut,',146,NULL),
 (3568,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',33,'3mutta minut\nlaskettiin muurinaukosta korissa alas, ja niin pääsin\npakenemaan hänen käsistään. ',146,NULL),
 (3569,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',1,'Minun on pakko kerskailla, vaikkei siitä hyötyä\nolekaan. Siirryn nyt näkyihin ja Herran ilmestyksiin.',147,NULL),
 (3570,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',2,'Tunnen erään Kristuksen oman, joka neljätoista\nvuotta sitten temmattiin kolmanteen taivaaseen. Oliko\nhän silloin ruumiissaan vai poissa siitä, en tiedä,\nsen tietää Jumala.',147,NULL),
 (3571,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',3,'--',147,NULL),
 (3572,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',4,'Ja tästä miehestä tiedän,\nettä hänet temmattiin paratiisiin ja hän kuuli sanoja,\njoita ihminen ei voi eikä saa lausua. Oliko hän\nruumiissaan vai poissa siitä, en tiedä, sen tietää\nJumala.',147,NULL),
 (3573,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',5,'Tästä miehestä minä kerskailen, itsestäni\nen, paitsi heikkoudestani. ',147,NULL),
 (3574,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',6,'Vaikka tahtoisinkin ylpeillä, en olisi mieletön,\nsillä puheeni on totta. Sitä en kuitenkaan tee, jottei\nkukaan ajattelisi minusta enempää kuin hän minusta\nnäkee tai kuulee.',147,NULL),
 (3575,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',7,'Jotta nämä valtavat ilmestykset\neivät tekisi minua ylpeäksi, olen saanut pistävän\npiikin ruumiiseeni, Saatanan enkelin kurittamaan\nitseäni, etten ylpistyisi.',147,NULL),
 (3576,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',8,'Olen kolme kertaa\npyytänyt Herralta, että pääsisin siitä.',147,NULL),
 (3577,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',9,'Mutta hän\non vastannut minulle: \"Minun armoni riittää sinulle.\nVoima tulee täydelliseksi heikkoudessa.\" Sen tähden\nylpeilen mieluimmin heikkoudestani, jotta minuun\nasettuisi Kristuksen voima.',147,NULL),
 (3578,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',10,'0Siksi iloitsen\nheikkoudesta, loukkauksista, vaikeuksista, vainoista\nja ahdingoista, joihin joudun Kristuksen tähden. Juuri\nheikkona olen voimakas.   ',147,NULL),
 (3579,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',14,'4Olen nyt valmis tulemaan luoksenne jo kolmannen\nkerran enkä nytkään aio rasittaa teitä. En kaipaa\nteidän varojanne vaan teitä itseänne. Eihän lasten\nvelvollisuus ole kerätä omaisuutta vanhemmilleen, vaan\nvanhempien lapsilleen.',147,NULL),
 (3580,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',15,'5Pelastaakseni teidät uhraan\nmielihyvin omat varani, uhraan vaikka itsenikin.\nRakastatteko te minua vähemmän siksi, että minä\nrakastan teitä niin suuresti?',147,NULL),
 (3581,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',16,'6Vaikka niinkin,\nmutta rasituksena en ole teille ollut. Mutta ehkäpä\nolen petkuttanut teitä, viekas kun olen?',147,NULL),
 (3582,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',17,'7Kukaties\nolen hyötynyt teistä jonkun työtoverini avulla, jonka\nolen lähettänyt luoksenne?',147,NULL),
 (3583,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',18,'8Kehotin Titusta\nlähtemään matkaan ja lähetin tuon toisen veljen hänen\nmukaansa. Onko Titus ehkä käyttänyt teitä hyväkseen?\nEmmekö me ole toimineet samassa hengessä ja kulkeneet\nsamoja jälkiä? ',147,NULL),
 (3584,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',19,'9Olette varmaan jo ehtineet ajatella, että me\nesitämme teille puolusteluja. Ei, me puhumme Jumalan\nedessä Kristuksen palvelijoina. Rakkaat ystävät, tällä\nkaikella pyrimme lujittamaan teidän seurakuntaanne.',147,NULL),
 (3585,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',20,'0Pelkään näet, että tullessani en ehkä tapaa teitä\nsellaisina kuin toivon, ja silloin tekin saatte nähdä\nminut toisenlaisena kuin toivoisitte. Pelkään\ntapaavani riitaisuutta, suvaitsemattomuutta, kiukkua,\njuonittelua, panettelua, juoruilua, pöyhkeyttä ja\nkurittomuutta.',147,NULL),
 (3586,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',21,'1Kunpa Jumalani ei nöyryyttäisi\nminua teidän takianne, kun taas tulen luoksenne! Kunpa\nminun ei tarvitsisi surra monien sellaisten vuoksi,\njotka ovat tehneet syntiä eivätkä vieläkään ole\nkatuneet sitä saastaisuutta, siveettömyyttä ja\nhillittömyyttä, jossa ovat eläneet! ',147,NULL),
 (3587,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',1,'Olen nyt kolmannen kerran tulossa luoksenne, sillä\n\"jokainen asia on vahvistettava kahden tai kolmen\ntodistajan sanalla\".',148,NULL),
 (3588,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',2,'Varoitin jo toisella\nkäynnilläni niitä, jotka olivat tehneet syntiä, ja nyt\ntäältä käsin varoitan heitä ja kaikkia muitakin: kun\ntaas tulen sinne, en aio säästää teitä.',148,NULL),
 (3589,'Anonymous','2008-10-17 16:33:51','Anonymous','2008-10-17 16:33:51',3,'Tehän\nkaipaatte todistetta siitä, että minussa puhuu\nKristus. Hän ei ole teidän edessänne heikko, vaan\nnäyttää voimansa teidän keskuudessanne.',148,NULL),
 (3590,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',4,'Hän oli\nheikko, kun hänet ristiinnaulittiin, mutta Jumalan\nvoimasta hän elää. Mekin olemme heikkoja, koska olemme\nhäneen liitettyjä, mutta yhdessä hänen kanssaan me\nelämme Jumalan voimasta, ja te saatte sen tuntea. ',148,NULL),
 (3591,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',5,'Tutkikaa itseänne, oletteko uskossa. Pankaa itsenne\nkoetteelle. Ettekö huomaa, että Jeesus Kristus on\nteidän keskellänne*? Ellei niin ole, ette selviydy\nkoetuksesta.',148,NULL),
 (3592,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',6,'Toivon, että saatte huomata meidät\nkoetuksen kestäviksi.',148,NULL),
 (3593,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',7,'Rukoilemme Jumalaa, ettette\ntekisi mitään pahaa, emme siksi, että meidät\nnähtäisiin koetuksen kestäviksi, vaan jotta te\ntekisitte hyvää, vaikka näyttäisikin siltä, että me\nemme selviydy koetuksesta.',148,NULL),
 (3594,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',8,'Totuutta vastaan emme\nvoi mitään, vaan me taistelemme sen puolesta.',148,NULL),
 (3595,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',9,'Me\niloitsemme, kun itse olemme heikkoja mutta te olette\nvoimakkaita. Sitä juuri rukoilemmekin, että elämänne\nkohentuisi.',148,NULL),
 (3596,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',10,'0Kirjoitan tästä jo täältä käsin,\njottei minun sinne tultuani tarvitsisi turvautua\nankaruuteen käyttäessäni sitä valtaa, jonka Herra on\nantanut minulle. Minunhan tulee sillä rakentaa, ei\nrepiä. ',148,NULL),
 (3597,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',11,'1Lopuksi, veljet: iloitkaa, antakaa ojentaa\nitseänne, ottakaa kehotukseni vastaan, olkaa\nyksimielisiä ja eläkää rauhassa, niin rakkauden ja\nrauhan Jumala on oleva teidän kanssanne.',148,NULL),
 (3598,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',12,'2Tervehtikää toisianne pyhällä suudelmalla. Kaikki\npyhät lähettävät teille terveisiä. ',148,NULL),
 (3599,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',13,'3Tulkoon teidän kaikkien osaksi Herran Jeesuksen\nKristuksen armo, Jumalan rakkaus ja Pyhän Hengen\nyhteys!  ',148,NULL),
 (3600,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',1,'Minä Paavali, apostoli, joka en ole saanut virkaani\nihmisiltä enkä kenenkään ihmisen välityksellä vaan\njonka ovat apostoliksi asettaneet Jeesus Kristus ja\nIsä Jumala, joka herätti hänet kuolleista,',149,NULL),
 (3601,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',2,'tervehdin Galatian seurakuntia, minä ja kaikki\nkanssani olevat veljet.',149,NULL),
 (3602,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',3,'Jumalan, meidän Isämme, ja\nHerran Jeesuksen Kristuksen armo ja rauha teille --\nKristuksen,',149,NULL),
 (3603,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',4,'joka uhrasi itsensä meidän syntiemme\ntähden pelastaakseen meidät nykyisestä pahasta\nmaailmasta, niin kuin oli Jumalan, meidän Isämme,\ntahto.',149,NULL),
 (3604,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',5,'Hänen on kunnia aina ja ikuisesti. Aamen.   ',149,NULL),
 (3605,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',10,'0Yritänkö minä tässä vaikuttaa ihmisiin? Tai\nkukaties Jumalaan? Pyrinkö olemaan ihmisille mieliksi?\nJos vielä pyrkisin miellyttämään ihmisiä, en olisi\nKristuksen palvelija.   ',149,NULL),
 (3606,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',18,'8Kolmen vuoden kuluttua menin sitten Jerusalemiin\ntutustuakseni Keefakseen ja viivyin hänen luonaan\nkaksi viikkoa.',149,NULL),
 (3607,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',19,'9Ketään muuta apostolia en tavannut,\nainoastaan Herran veljen Jaakobin.',149,NULL),
 (3608,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',20,'0Jumalan edessä\nvakuutan, että tämä, mitä teille kirjoitan, on totta.',149,NULL),
 (3609,'Anonymous','2008-10-17 16:33:52','Anonymous','2008-10-17 16:33:52',21,'1Sitten lähdin Syyriaan ja Kilikiaan.',149,NULL),
 (3610,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',22,'2Juudean\nkristillisissä seurakunnissa minua ei vielä tunnettu.',149,NULL),
 (3611,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',23,'3Niissä vain kerrottiin kuulopuheena: \"Se mies,\njoka ennen vainosi meitä, julistaa nyt samaa uskoa,\njota hän ennen yritti hävittää.\"',149,NULL),
 (3612,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',24,'4Ja Jumalaa\nylistettiin siitä, mitä minulle oli tapahtunut. ',149,NULL),
 (3613,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',1,'Neljätoista vuotta myöhemmin menin uudelleen\nJerusalemiin Barnabaksen kanssa ja otin mukaan myös\nTituksen.',150,NULL),
 (3614,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',2,'Menin sinne kokemani ilmestyksen\njohdosta. Tein siellä selkoa evankeliumista, jota\njulistan kansojen keskuudessa; puhuin yksityisesti\nseurakunnan arvohenkilöille. Tahdoin varmistautua\nsiitä, ettei kaikki vaivannäköni menisi hukkaan.',150,NULL),
 (3615,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',3,'Ei\nedes seuralaistani Titusta, joka on kreikkalainen,\npakotettu ympärileikkauttamaan itseään.',150,NULL),
 (3616,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',4,'Tosin\njoukkoomme oli tullut eräitä valeveljiä urkkimaan,\nmillaista on se vapaus, jonka me olemme Kristukselta\nJeesukselta saaneet. Heidän tarkoituksenaan oli\nsaattaa meidät lain orjuuteen.',150,NULL),
 (3617,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',5,'Mutta me emme\nhetkeksikään antaneet heille periksi, jotta\nevankeliumin totuus säilyisi teille puhtaana. ',150,NULL),
 (3618,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',6,'Ne taas, joita siellä pidettiin arvossa -- mitä he\nitse asiassa olivat, on minusta yhdentekevää, sillä\nJumala ei tee eroa ihmisten välillä -- nämä\narvohenkilöt eivät vaatineet minulta mitään enempää.',150,NULL),
 (3619,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',7,'He päinvastoin näkivät, että minun tehtäväkseni oli\nannettu evankeliumin julistaminen\nympärileikkaamattomille niin kuin Pietarin tehtäväksi\nsen julistaminen ympärileikatuille.',150,NULL),
 (3620,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',8,'Sillä hän, joka\non antanut Pietarille voiman toimia juutalaisten\napostolina, on antanut minulle voiman toimia muiden\nkansojen keskuudessa. ',150,NULL),
 (3621,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',9,'Kun Jaakob, Keefas ja Johannes, joita pidettiin\nseurakunnan pylväinä, käsittivät, minkä tehtävän\nJumala oli minulle armossaan antanut, he ojensivat\nminulle ja Barnabakselle kätensä yhteistyön merkiksi:\nmeidän oli määrä mennä vieraiden kansojen, heidän taas\njuutalaisten keskuuteen.',150,NULL),
 (3622,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',10,'0Heidän köyhiään meidän\nvain tuli muistaa, ja niin minä olen ahkerasti\ntehnytkin.     ',150,NULL),
 (3623,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',17,'7Mutta jos meidätkin, jotka tahdomme tulla\nvanhurskaiksi Kristuksessa, havaitaan syntisiksi, onko\nKristus siis synnin palveluksessa? Ei toki!',150,NULL),
 (3624,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',18,'8Vasta\njos alan uudelleen rakentaa sitä minkä olen\nhajottanut, osoitan olevani lainrikkoja.',150,NULL),
 (3625,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',19,'9Lain\nvaikutuksesta minä kuolin, mutta kuolin vapaaksi\nlaista elääkseni Jumalalle. Minut on Kristuksen kanssa\nristiinnaulittu.',150,NULL),
 (3626,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',20,'0Enää en elä minä, vaan Kristus\nelää minussa. Sen elämän, jota tässä ruumiissani vielä\nelän, elän uskoen Jumalan Poikaan, joka rakasti minua\nja antoi henkensä puolestani.',150,NULL),
 (3627,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',21,'1Minä en tee tyhjäksi\nJumalan armoa; jos näet vanhurskaus saadaan lakia\nnoudattamalla, silloin Kristus on kuollut turhaan. ',150,NULL),
 (3628,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',1,'Te mielettömät galatalaiset, kuka teidät on\nlumonnut? Asetettiinhan teidän silmienne eteen aivan\navoimesti Jeesus Kristus ristiinnaulittuna!',151,NULL),
 (3629,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',2,'Vastatkaa vain tähän kysymykseen: saitteko te\nHengen tekemällä lain vaatimat teot vai kuulemalla ja\nuskomalla evankeliumin?',151,NULL),
 (3630,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',3,'Kuinka voitte olla noin\nmielettömiä! Te aloititte Hengen varassa. Pyrittekö\nnyt päämäärään omin avuin?',151,NULL),
 (3631,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',4,'Turhaanko tämä kaikki on\ntullut teidän osaksenne? Tuskinpa vain.',151,NULL),
 (3632,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',5,'Lahjoittaako Jumala teille Hengen ja antaako hän\nvoimatekojen tapahtua teidän keskuudessanne sen\ntähden, että te noudatatte lain käskyjä, vai sen\ntähden, että te uskotte kuulemanne evankeliumin?',151,NULL),
 (3633,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',6,'Muistattehan Abrahamin: \"Hän uskoi Jumalan\nlupaukseen, ja Jumala katsoi hänet vanhurskaaksi.\" ',151,NULL),
 (3634,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',7,'Tietäkää siis, että todellisia Abrahamin\njälkeläisiä ovat ne, joilla on usko.',151,NULL),
 (3635,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',8,'Pyhät\nkirjoitukset tiesivät jo edeltäpäin, että Jumala tekee\nvanhurskaiksi kansat, kun ne uskovat. Jumala näet\nilmoitti edeltäpäin Abrahamille hyvän sanoman: sinun\nkanssasi saavat kaikki kansat siunauksen.',151,NULL),
 (3636,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',9,'Näin ne,\njoilla on usko, saavat siunauksen yhdessä Abrahamin\nkanssa, joka uskoi.',151,NULL),
 (3637,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',10,'0Ne taas, jotka luottavat lain\nnoudattamiseen, ovat kirouksen alaisia. Onhan\nkirjoitettu: \"Kirottu on jokainen, joka ei tee\nkaikkea, mitä lain kirja käskee.\"',151,NULL),
 (3638,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',11,'1On selvää, ettei\nkukaan tule Jumalan silmissä vanhurskaaksi lakia\nnoudattamalla, sillä \"uskosta vanhurskas saa elää\".',151,NULL),
 (3639,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',12,'2Mutta laki ei rakenna mitään uskon varaan, vaan:\n\"Joka tekee mitä laki vaatii, saa siitä elämän.\"',151,NULL),
 (3640,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',13,'3Kristus on lunastanut meidät vapaiksi lain\nkirouksesta tulemalla itse kirotuksi meidän\nsijastamme, niin kuin on kirjoitettu: \"Kirottu on\njokainen, joka on ripustettu paaluun.\"',151,NULL),
 (3641,'Anonymous','2008-10-17 16:33:53','Anonymous','2008-10-17 16:33:53',14,'4Näin\ntapahtui, jotta kaikki muutkin kansat Kristuksen\nJeesuksen yhteydessä saisivat Abrahamille luvatun\nsiunauksen ja me häneen uskovina saisimme luvatun\nHengen.     ',151,NULL),
 (3642,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',21,'1Onko laki siis ristiriidassa Jumalan lupausten\nkanssa? Ei toki! Jos olisi annettu laki, joka pystyy\ntekemään eläväksi, silloin vanhurskaus todella\nperustuisi lain noudattamiseen.',151,NULL),
 (3643,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',22,'2Pyhä kirjoitus on\nkaiken sulkenut synnin vankilaan sitä varten, että se,\nmitä on luvattu, annettaisiin niille, jotka uskovat\nJeesukseen Kristukseen.',151,NULL),
 (3644,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',23,'3Mutta ennen kuin uskon\naika tuli, me jouduimme olemaan lain vartioimina ja\nsen vankeina, kunnes usko ilmestyisi.',151,NULL),
 (3645,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',24,'4Näin laki\noli meidän valvojamme Kristuksen tuloon asti, jotta me\nsitten tulisimme vanhurskaiksi uskosta.',151,NULL),
 (3646,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',25,'5Nyt uskon\nilmestyttyä emme enää ole valvojan alaisia. ',151,NULL),
 (3647,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',26,'6Te kaikki olette Jumalan lapsia, kun uskotte\nKristukseen Jeesukseen.',151,NULL),
 (3648,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',27,'7Kaikki te, jotka olette\nKristukseen kastettuja, olette pukeneet Kristuksen\nyllenne.',151,NULL),
 (3649,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',28,'8Yhdentekevää, oletko juutalainen vai\nkreikkalainen, orja vai vapaa, mies vai nainen, sillä\nKristuksessa Jeesuksessa te kaikki olette yksi.',151,NULL),
 (3650,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',29,'9Ja\njos te kerran olette Kristuksen omia, te olette\nAbrahamin jälkeläisiä ja saatte periä sen, mikä\nhänelle oli luvattu. ',151,NULL),
 (3651,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',1,'Tarkoitan tätä: niin kauan kuin perillinen on\nalaikäinen, hän ei mitenkään eroa orjasta vaikka onkin\nkaiken omistaja.',152,NULL),
 (3652,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',2,'Hän on huoltajien ja holhoojien\nalainen isänsä asettamaan määräaikaan asti.',152,NULL),
 (3653,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',3,'Samoin\non meidän laitamme. Niin kauan kuin olimme alaikäisiä,\nolimme maailman alkuvoimien orjia.',152,NULL),
 (3654,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',4,'Mutta kun aika\noli täyttynyt, Jumala lähetti tänne Poikansa. Naisesta\nhän syntyi ja tuli lain alaiseksi',152,NULL),
 (3655,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',5,'lunastaakseen\nlain alaisina elävät vapaiksi, että me pääsisimme\nlapsen asemaan.',152,NULL),
 (3656,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',6,'Ja koska tekin olette Jumalan\nlapsia, hän on lähettänyt meidän kaikkien sydämiin\nPoikansa Hengen, joka huutaa: \"Abba! Isä!\"',152,NULL),
 (3657,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',7,'Sinä et\nsiis enää ole orja vaan lapsi. Ja jos kerran olet\nlapsi, olet myös perillinen, Jumalan tahdosta.   ',152,NULL),
 (3658,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',12,'2Pyydän teitä, veljet: tulkaa sellaisiksi kuin minä\nolen, koska minäkin olen tullut samanlaiseksi kuin te.\nEtte te ole minua millään tavoin loukanneet.',152,NULL),
 (3659,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',13,'3Tiedätte, että julistin teille ensimmäisen kerran\nevankeliumia sairastumiseni vuoksi.',152,NULL),
 (3660,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',14,'4Vaikka\nruumiillinen tilani pani teidät koetukselle, ette\nhalveksineet ettekä inhonneet minua vaan otitte minut\nvastaan kuin Jumalan enkelin, kuin Kristuksen\nJeesuksen.',152,NULL),
 (3661,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',15,'5Missä teidän riemunne ja innostuksenne\nnyt on? Voin todistaa teistä, että olisitte silloin\nantaneet minulle vaikka silmät päästänne, jos se olisi\nollut mahdollista.',152,NULL),
 (3662,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',16,'6Onko minusta siis tullut teidän\nvihamiehenne, kun sanon teille totuuden?',152,NULL),
 (3663,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',17,'7Nuo\ntoiset koettavat innokkaasti vaikuttaa teihin, mutta\nheidän intonsa ei ole oikeaa. He tahtovat erottaa\nteidät meistä, jotta te intoutuisitte kannattamaan\nheitä.',152,NULL),
 (3664,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',18,'8Into on hyvä, jos asia on hyvä -- näin aina\neikä vain silloin kun minä olen luonanne.',152,NULL),
 (3665,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',19,'9Rakkaat\nlapseni, teidän vuoksenne minun on jälleen kärsittävä\nsynnytystuskia, kunnes Kristus saa muodon teissä.',152,NULL),
 (3666,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',20,'0Kunpa voisin olla nyt luonanne ja löytää oikean\näänensävyn! En tiedä, mitä teille tekisin.    ',152,NULL),
 (3667,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',28,'8Veljet, te olette lupauksen lapsia, niin kuin\nIisak oli.',152,NULL),
 (3668,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',29,'9Niin kuin luonnonjärjestyksen\nmukaisesti syntynyt silloin vainosi Hengen\nvaikutuksesta syntynyttä, niin on laita nytkin. ',152,NULL),
 (3669,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',30,'0Mutta mitä pyhät kirjoitukset sanovatkaan? \"Aja\npois orjatar ja hänen poikansa, sillä orjattaren poika\nei saa jakaa perintöä vapaan naisen pojan kanssa.\" ',152,NULL),
 (3670,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',31,'1Me emme siis, veljet, ole orjattaren vaan vapaan\nnaisen lapsia. ',152,NULL),
 (3671,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',1,'Vapauteen Kristus meidät vapautti. Pysykää siis\nlujina älkääkä alistuko uudelleen orjuuden ikeeseen.',153,NULL),
 (3672,'Anonymous','2008-10-17 16:33:54','Anonymous','2008-10-17 16:33:54',2,'Minä, Paavali, sanon teille: jos annatte\nympärileikata itsenne, teille ei ole Kristuksesta\nmitään hyötyä.',153,NULL),
 (3673,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',3,'Vakuutan vielä kerran jokaiselle,\njoka antaa ympärileikata itsensä, että hän on\nvelvollinen noudattamaan lain kaikkia määräyksiä.',153,NULL),
 (3674,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',4,'Te, jotka pyritte vanhurskauteen lakia\nnoudattamalla, olette joutuneet eroon Kristuksesta,\narmon ulkopuolelle.',153,NULL),
 (3675,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',5,'Koska me uskosta olemme saaneet\nHengen, odotamme hartaasti, että toivomme toteutuisi\nja me saavuttaisimme vanhurskauden.',153,NULL),
 (3676,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',6,'Kristuksessa\nJeesuksessa on yhdentekevää, onko ihminen\nympärileikattu vai ei. Ainoa tärkeä on rakkautena\nvaikuttava usko. ',153,NULL),
 (3677,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',7,'Te etenitte jo hyvää vauhtia. Kuka teidät on\npysäyttänyt? Tehän ette enää tottele totuutta.',153,NULL),
 (3678,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',8,'Tämä\nei ole lähtöisin ainakaan hänestä, joka teitä kutsuu.',153,NULL),
 (3679,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',9,'Pieni määrä hapatetta hapattaa koko taikinan.',153,NULL),
 (3680,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',10,'0Herraan luottaen minä olen varma siitä, että te\ntulette ajattelemaan samoin kuin minä. Mutta se, joka\non hämmentänyt teidän ajatuksenne, tulee saamaan\nrangaistuksensa, olipa hän kuka tahansa.',153,NULL),
 (3681,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',11,'1Veljet,\njos minä tosiaan yhä julistan ympärileikkausta, miksi\nminua sitten vainotaan? Silloinhan ei julistamani\nristi olisi enää loukkauksena.',153,NULL),
 (3682,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',12,'2Saisivat kuohita\nitsensä, nuo teidän yllyttäjänne!   ',153,NULL),
 (3683,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',16,'6Tarkoitan tätä: antakaa Hengen ohjata elämäänne,\nniin ette toteuta lihanne, oman itsekkään luontonne\nhaluja.',153,NULL),
 (3684,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',17,'7Liha haluaa toista kuin Henki, Henki\ntoista kuin liha. Ne sotivat toisiaan vastaan, ja\nsiksi te ette tee mitä tahtoisitte.',153,NULL),
 (3685,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',18,'8Mutta jos\nHenki johtaa teitä, ette ole lain alaisia.',153,NULL),
 (3686,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',19,'9Lihan\naikaansaannokset ovat selvästi nähtävissä. Niitä ovat\nsiveettömyys, saastaisuus, irstaus,',153,NULL),
 (3687,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',20,'0epäjumalien\npalveleminen, noituus, vihamielisyys, riidat,\nkiihkoilu, kiukku, juonittelu, eripuraisuus,\nlahkolaisuus,',153,NULL),
 (3688,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',21,'1kateus, juomingit, remuaminen ja muu\nsellainen. Varoitan teitä, kuten olen jo ennenkin\nvaroittanut: ne, jotka syyllistyvät tällaiseen, eivät\nsaa omakseen Jumalan valtakuntaa. ',153,NULL),
 (3689,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',22,'2Hengen hedelmää taas ovat rakkaus, ilo, rauha,\nkärsivällisyys, ystävällisyys, hyvyys, uskollisuus,',153,NULL),
 (3690,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',23,'3lempeys ja itsehillintä. Näitä vastaan ei ole\nlaki.',153,NULL),
 (3691,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',24,'4Ne, jotka ovat Jeesuksen Kristuksen omia,\novat ristiinnaulinneet vanhan luontonsa himoineen ja\nhaluineen.',153,NULL),
 (3692,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',25,'5Jos me elämme Hengen varassa, meidän on\nmyös seurattava Hengen johdatusta.',153,NULL),
 (3693,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',26,'6Emme saa\ntavoitella turhaa kunniaa emmekä ärsyttää ja kadehtia\ntoisiamme. ',153,NULL),
 (3694,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',1,'Veljet, jos joku tavataan tekemästä väärin, on\nteidän, joita Henki ohjaa, lempeästi ojennettava\nhäntä. Olkaa kuitenkin varuillanne, ettette itse joudu\nkiusaukseen.',154,NULL),
 (3695,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',2,'Kantakaa toistenne taakkoja, niin te\ntoteutatte Kristuksen lain.',154,NULL),
 (3696,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',3,'Joka luulee olevansa\njotakin, vaikka ei ole mitään, pettää itseään.',154,NULL),
 (3697,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',4,'Kukin tutkikoon vain omia tekojaan. Silloin hän voi\nylpeillä vain siitä, mitä hän itse on, vertaamatta\nitseään toiseen.',154,NULL),
 (3698,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',5,'Jokaisen on kannettava oma\nkuormansa. ',154,NULL),
 (3699,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',6,'Se, jolle Jumalan sanaa opetetaan, antakoon\nopettajalleen kaikkea hyvää.',154,NULL),
 (3700,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',7,'Älkää pettäkö\nitseänne! Jumala ei salli itseään pilkattavan. Mitä\nihminen kylvää, sitä hän myös niittää.',154,NULL),
 (3701,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',8,'Joka kylvää\nsiemenen itsekkyyden peltoon, korjaa siitä satona\ntuhon, mutta se, joka kylvää Hengen peltoon, korjaa\nsiitä satona ikuisen elämän.',154,NULL),
 (3702,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',9,'Meidän ei pidä väsyä\ntekemään hyvää, sillä jos emme hellitä, saamme\naikanaan korjata sadon.',154,NULL),
 (3703,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',10,'0Kun meillä vielä on aikaa,\nmeidän on siis tehtävä hyvää kaikille, mutta varsinkin\nniille, joita usko yhdistää meihin.   ',154,NULL),
 (3704,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',17,'7Älköön kukaan enää tämän jälkeen tuottako minulle\nvaivaa. Onhan minulla Jeesuksen arvet ruumiissani. ',154,NULL),
 (3705,'Anonymous','2008-10-17 16:33:55','Anonymous','2008-10-17 16:33:55',18,'8Herramme Jeesuksen Kristuksen armo olkoon teidän\nkanssanne, veljet. Aamen.  ',154,NULL),
 (3706,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',1,'Paavali, Jumalan tahdosta Kristuksen Jeesuksen\napostoli, tervehtii Efesoksessa asuvia, Kristukseen\nJeesukseen uskovia pyhiä.',155,NULL),
 (3707,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',2,'Jumalan, meidän Isämme,\nja Herran Jeesuksen Kristuksen armo ja rauha teille.   ',155,NULL),
 (3708,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',7,'Kristuksen veressä meillä on lunastus,\nrikkomustemme anteeksianto. Näin Jumala on antanut\narmonsa rikkauden',155,NULL),
 (3709,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',8,'tulla runsaana osaksemme ja\nsuonut meille kaikkea viisautta ja ymmärrystä.',155,NULL),
 (3710,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',9,'Hän\non ilmaissut meille tahtonsa salaisuuden, sen\nKristusta koskevan suunnitelman, jonka hän oli nähnyt\nhyväksi tehdä',155,NULL),
 (3711,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',10,'0ja joka oli määräajan tullessa\ntoteutuva: hän oli yhdistävä Kristuksessa yhdeksi\nkaiken, mitä on taivaassa ja maan päällä. ',155,NULL),
 (3712,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',11,'1Jumala on myös antanut Kristuksessa meille\nperintöosan, niin kuin hän oli suunnitellut ja ennalta\nmäärännyt -- hän, joka saattaa kaiken tapahtumaan\ntahtonsa ja päätöksensä mukaisesti.',155,NULL),
 (3713,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',12,'2Siksi meidän,\njotka olemme jo ennalta saaneet panna toivomme\nKristukseen, tulee elää Jumalan kirkkauden\nylistykseksi. ',155,NULL),
 (3714,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',13,'3Kristukseen tekin nyt uskotte kuultuanne totuuden\nsanan, pelastuksenne evankeliumin. Häneen uskoessanne\nte myös olette saaneet luvatun Pyhän Hengen\nsinetiksenne.',155,NULL),
 (3715,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',14,'4Se on meidän perintöosamme\nvakuutena, joka takaa meille lunastuksen, Jumalan\nkirkkauden ylistykseksi.   ',155,NULL),
 (3716,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',1,'Jumala on tehnyt eläviksi teidät, jotka olitte\nkuolleita rikkomustenne ja syntienne tähden.',156,NULL),
 (3717,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',2,'Ennen\nte elitte niiden vallassa tämän maailman menon mukaan,\ntotellen avaruuden henkivaltojen hallitsijaa, sitä\nhenkeä, joka yhä vaikuttaa tottelemattomissa\nihmisissä.',156,NULL),
 (3718,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',3,'Heidän joukossaan mekin kaikki ennen\nelimme noudattaen oman luontomme haluja ja tehden niin\nkuin ruumiimme ja mielemme tahtoivat, ja näin olimme\nluonnostamme vihan alaisia niin kuin kaikki muutkin. ',156,NULL),
 (3719,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',4,'Jumalan laupeus on kuitenkin niin runsas ja hän\nrakasti meitä niin suuresti,',156,NULL),
 (3720,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',5,'että hän teki meidät,\nrikkomustemme tähden kuolleet, eläviksi Kristuksen\nkanssa. Armosta teidät on pelastettu.',156,NULL),
 (3721,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',6,'Jumala\nherätti meidät yhdessä Kristuksen Jeesuksen kanssa ja\nantoi meillekin paikan taivaassa',156,NULL),
 (3722,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',7,'osoittaakseen\nkaikille tuleville aikakausille, kuinka äärettömän\nrunsas on hänen armonsa ja kuinka suuri hänen\nhyvyytensä, kun hän antoi meille Kristuksen Jeesuksen.',156,NULL),
 (3723,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',8,'Armosta Jumala on teidät pelastanut antamalla\nteille uskon. Pelastus ei ole lähtöisin teistä, vaan\nse on Jumalan lahja.',156,NULL),
 (3724,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',9,'Se ei perustu ihmisen\ntekoihin, jottei kukaan voisi ylpeillä.',156,NULL),
 (3725,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',10,'0Mekin\nolemme Jumalan tekoa, luotuja Kristuksen Jeesuksen\nyhteyteen toteuttamaan niitä hyviä tekoja, joita\ntekemään Jumala on meidät tarkoittanut.   ',156,NULL),
 (3726,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',14,'4Kristus on meidän rauhamme. Hän on tehnyt nämä\nkaksi ihmisryhmää yhdeksi ja kuolemallaan hajottanut\nniitä erottaneen vihollisuuden muurin.',156,NULL),
 (3727,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',15,'5Hän on\nkumonnut lain käskyineen ja säädöksineen, jotta hän\nomassa itsessään loisi nuo kaksi yhdeksi uudeksi\nihmiseksi, ja näin hän on tehnyt rauhan.',156,NULL),
 (3728,'Anonymous','2008-10-17 16:33:56','Anonymous','2008-10-17 16:33:56',16,'6Ristillä\nkuollessaan hän omassa ruumiissaan sai aikaan sovinnon\nJumalan ja näiden molempien välille ja teki näin lopun\nvihollisuudesta.',156,NULL),
 (3729,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',17,'7Hän tuli julistamaan rauhaa\nteille, jotka olitte kaukana, ja rauhaa niille, jotka\nolivat lähellä.',156,NULL),
 (3730,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',18,'8Hän on avannut meille molemmille\npääsyn Isän luo yhden ja saman Hengen johdattamina.',156,NULL),
 (3731,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',19,'9Te ette siis enää ole vieraita ja muukalaisia,\nvaan kuulutte Jumalan perheeseen, samaan kansaan kuin\npyhät.',156,NULL),
 (3732,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',20,'0Te olette kiviä siinä rakennuksessa, jonka\nperustuksena ovat apostolit ja profeetat ja jonka\nkulmakivenä on itse Kristus Jeesus.',156,NULL),
 (3733,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',21,'1Hän liittää\nkoko rakennuksen yhteen niin että se kasvaa Herran\npyhäksi temppeliksi,',156,NULL),
 (3734,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',22,'2ja hän liittää teidätkin\nHengellään rakennuskivinä Jumalan asumukseen. ',156,NULL),
 (3735,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',1,'Tästä syystä minä Paavali, joka olen Kristuksen\nJeesuksen tahdosta vankina teidän vierasheimoisten\ntähden, polvistun Isän eteen.',157,NULL),
 (3736,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',2,'Olettehan kuulleet\nsiitä Jumalan suunnitelmasta, jonka hän armossaan on\nilmoittanut minulle, teidän parhaaksenne.',157,NULL),
 (3737,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',3,'Minulle\non ilmestyksessä annettu tiedoksi tämä salaisuus, niin\nkuin olen edellä lyhyesti kirjoittanut.',157,NULL),
 (3738,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',4,'Tätä\nlukiessanne voitte huomata, kuinka hyvin minä olen\nperillä Kristuksen salaisuudesta.',157,NULL),
 (3739,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',5,'Sitä ei menneiden\nsukupolvien aikana annettu ihmisten tietoon, mutta nyt\nHenki on ilmoittanut sen Kristuksen pyhille\napostoleille ja profeetoille:',157,NULL),
 (3740,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',6,'muihin kansoihin\nkuuluvilla on sama oikeus perintöön kuin\njuutalaisillakin, he ovat saman ruumiin jäseniä ja\nheitä koskee nyt sama lupaus, kun evankeliumi on\njohtanut heidät Kristuksen Jeesuksen yhteyteen.',157,NULL),
 (3741,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',7,'Tämän evankeliumin palvelija minusta on tullut sen\narmolahjan perusteella, jonka Jumala on voimassaan\nminulle suonut. ',157,NULL),
 (3742,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',8,'Minulle, kaikista pyhistä vähäisimmälle, on annettu\nse armo, että saan julistaa kansoille sanomaa\nKristuksen tutkimattomasta rikkaudesta',157,NULL),
 (3743,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',9,'ja ilmoittaa\nsen pyhän suunnitelman, jonka Jumala, kaiken luoja, on\nikiajoista asti pitänyt salaisuutenaan.',157,NULL),
 (3744,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',10,'0Näin myös\navaruuden henkivallat ja voimat tulevat seurakunnan\nvälityksellä tuntemaan Jumalan viisauden kaikessa\nmoninaisuudessaan.',157,NULL),
 (3745,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',11,'1Tällainen oli Jumalan\nikiaikainen suunnitelma, joka meidän Herramme\nKristuksen Jeesuksen oli määrä toteuttaa.',157,NULL),
 (3746,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',12,'2Kristuksen omina ja häneen uskoen voimme rohkeasti\nja luottavaisina lähestyä Jumalaa.',157,NULL),
 (3747,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',13,'3Siksi pyydän\nteitä olemaan lannistumatta, vaikka minä saan teidän\ntähtenne kärsiä vainoa. Se koituu teidän kunniaksenne. ',157,NULL),
 (3748,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',14,'4Tämän vuoksi minä polvistun Isän eteen,',157,NULL),
 (3749,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',15,'5hänen,\njonka asemaa jokainen isän ja lapsen suhde taivaassa\nja maan päällä kuvastaa.',157,NULL),
 (3750,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',16,'6Rukoilen, että hän\nsanomattomassa kirkkaudessaan hengellään vahvistaisi\nja voimistaisi teidän sisäistä olemustanne.',157,NULL),
 (3751,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',17,'7Näin\nKristus asuu teidän sydämissänne, kun te uskotte, ja\nrakkaus on elämänne perustus ja kasvupohja.',157,NULL),
 (3752,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',18,'8Silloin te kykenette yhdessä kaikkien pyhien\nkanssa käsittämään kaiken leveyden, pituuden,\nkorkeuden ja syvyyden,',157,NULL),
 (3753,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',19,'9ja voitte tajuta Kristuksen\nrakkauden, joka ylittää kaiken tiedon. Niin Jumalan\nkoko täyteys valtaa teidät. ',157,NULL),
 (3754,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',20,'0Jumalalle, joka meissä vaikuttavalla voimallaan\nkykenee tekemään monin verroin enemmän kuin osaamme\npyytää tai edes ajatella,',157,NULL),
 (3755,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',21,'1olkoon ylistys\nseurakunnassa ja Kristuksessa Jeesuksessa kautta\nkaikkien sukupolvien, aina ja ikuisesti. Aamen. ',157,NULL),
 (3756,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',1,'Minä, joka olen Herran vuoksi vankina, kehotan\nteitä siis elämään saamanne kutsun arvoisesti,',158,NULL),
 (3757,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',2,'aina\nnöyrinä, lempeinä ja kärsivällisinä. Auttakoon rakkaus\nteitä tulemaan toimeen keskenänne.',158,NULL),
 (3758,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',3,'Pyrkikää rauhan\nsitein säilyttämään Hengen luoma ykseys.',158,NULL),
 (3759,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',4,'On vain\nyksi ruumis ja yksi Henki, niin kuin myös se toivo,\njohon teidät on kutsuttu, on yksi.   ',158,NULL),
 (3760,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',7,'Mutta kukin meistä on saanut oman armolahjansa, sen\njonka Kristus on nähnyt hyväksi antaa.',158,NULL),
 (3761,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',8,'Kirjoituksissa sanotaankin:   ',158,NULL),
 (3762,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',9,'Eikö se, että hän nousi korkeuteen, merkitse, että\nhän oli laskeutunut alas, aina maan alimpiin\npaikkoihin?',158,NULL),
 (3763,'Anonymous','2008-10-17 16:33:57','Anonymous','2008-10-17 16:33:57',10,'0Hän, joka laskeutui alas, nousi myös\nkaikkia taivaita ylemmäs täyttääkseen kaikkeuden\nläsnäolollaan.',158,NULL),
 (3764,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',11,'1Hän antoi seurakunnalle sekä\napostolit että profeetat ja evankeliumin julistajat,\nsekä paimenet että opettajat,',158,NULL),
 (3765,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',12,'2varustaakseen kaikki\nseurakunnan jäsenet palvelutyöhön, Kristuksen ruumiin\nrakentamiseen.',158,NULL),
 (3766,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',13,'3Kun me kaikki sitten pääsemme\nyhteen ja samaan uskoon ja Jumalan Pojan tuntemiseen\nja niin saavutamme aikuisuuden, Kristuksen täyteyttä\nvastaavan kypsyyden,',158,NULL),
 (3767,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',14,'4silloin emme enää ole\nalaikäisiä, jotka ajelehtivat kaikenlaisten opin\ntuulten heiteltävinä ja ovat kavalien ja petollisten\nihmisten pelinappuloita.',158,NULL),
 (3768,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',15,'5Silloin me noudatamme\ntotuutta ja rakkautta ja kasvamme kaikin tavoin kiinni\nKristukseen, häneen, joka on pää.',158,NULL),
 (3769,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',16,'6Hän liittää\nyhteen koko ruumiin ja pitää sitä koossa kaikkien\njänteiden avulla, kunkin jäsenen toimiessa oman\ntehtävänsä mukaan, ja näin ruumis kasvaa ja rakentuu\nrakkaudessa.     ',158,NULL),
 (3770,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',1,'Pitäkää siis Jumalaa esikuvananne, olettehan hänen\nrakkaita lapsiaan.',159,NULL),
 (3771,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',2,'Rakkaus ohjatkoon elämäänne,\nonhan Kristuskin rakastanut meitä ja antanut meidän\ntähtemme itsensä lahjaksi, hyvältä tuoksuvaksi uhriksi\nJumalalle.',159,NULL),
 (3772,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',3,'Siveettömyydestä, kaikenlaisesta\nsaastaisuudesta ja ahneudesta ei teidän keskuudessanne\nsaa olla puhettakaan, eihän mikään sellainen sovi\npyhille.',159,NULL),
 (3773,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',4,'Myöskään rivoudet, typerät jutut tai\nkaksimielisyydet eivät teille sovi, teidän suuhunne\nsopii kiitos.',159,NULL),
 (3774,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',5,'Tehän tiedätte hyvin, ettei\nkenelläkään siveettömällä eikä saastaisella ole osaa\nKristuksen ja Jumalan valtakunnasta, ei myöskään\nahneella, sillä hän on epäjumalanpalvelija.     ',159,NULL),
 (3775,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',15,'5Katsokaa siis tarkoin, miten elätte: älkää eläkö\ntyhmien tavoin, vaan niin kuin viisaat.',159,NULL),
 (3776,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',16,'6Käyttäkää\noikein jokainen hetki, sillä tämä aika on paha.',159,NULL),
 (3777,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',17,'7Älkää olko järjettömiä, vaan ymmärtäkää, mikä on\nHerran tahto.',159,NULL),
 (3778,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',18,'8Älkää juopuko viinistä, sillä siitä\nseuraa rietas meno, vaan antakaa Hengen täyttää\nitsenne.',159,NULL),
 (3779,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',19,'9Veisatkaa yhdessä psalmeja, ylistysvirsiä\nja hengellisiä lauluja, soittakaa ja laulakaa täydestä\nsydämestä Herralle',159,NULL),
 (3780,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',20,'0ja kiittäkää aina ja kaikesta\nJumalaa, Isää, meidän Herramme Jeesuksen Kristuksen\nnimessä.   ',159,NULL),
 (3781,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',25,'5Miehet, rakastakaa vaimoanne niin kuin Kristuskin\nrakasti seurakuntaa ja antoi henkensä sen puolesta',159,NULL),
 (3782,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',26,'6pyhittääkseen sen. Hän pesi sen puhtaaksi vedellä\nja sanalla',159,NULL),
 (3783,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',27,'7voidakseen asettaa sen eteensä\nkirkkaana, pyhänä ja moitteettomana, vailla tahraa,\nryppyä tai virhettä.',159,NULL),
 (3784,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',28,'8Samoin aviomiehenkin\nvelvollisuus on rakastaa vaimoaan niin kuin omaa\nruumistaan. Joka rakastaa vaimoaan, rakastaa itseään.',159,NULL),
 (3785,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',29,'9Eihän kukaan vihaa omaa ruumistaan, vaan jokainen\nravitsee ja vaalii sitä. Juuri niin hoitaa Kristuskin\nseurakuntaansa,',159,NULL),
 (3786,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',30,'0omaa ruumistaan, jonka jäseniä me\nolemme.',159,NULL),
 (3787,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',31,'1\"Siksi mies jättää isänsä ja äitinsä ja\nliittyy vaimoonsa, niin että nämä kaksi tulevat\nyhdeksi lihaksi.\"',159,NULL),
 (3788,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',32,'2Tämä on suuri salaisuus; minä\ntarkoitan Kristusta ja seurakuntaa.',159,NULL),
 (3789,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',33,'3Mutta se\nkoskee myös kaikkia teitä: jokaisen tulee rakastaa\nvaimoaan niin kuin itseään, ja vaimon tulee\nkunnioittaa miestään. ',159,NULL),
 (3790,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',1,'Lapset, olkaa vanhemmillenne kuuliaisia Herraa\ntotellen, sillä se on oikein.',160,NULL),
 (3791,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',2,'\"Kunnioita isääsi ja\näitiäsi\" on ensimmäinen käsky, johon liittyy lupaus:',160,NULL),
 (3792,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',3,'\"jotta menestyisit ja eläisit kauan maan päällä\".',160,NULL),
 (3793,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',4,'Ja te isät, älkää herättäkö lapsissanne vihaa, vaan\nkasvattakaa ja ojentakaa heitä Herran tahdon mukaan.     ',160,NULL),
 (3794,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',18,'8Tehkää tämä kaikki rukoillen ja anoen. Rukoilkaa\njoka hetki Hengen antamin voimin. Pysykää valveilla ja\nrukoilkaa hellittämättä kaikkien pyhien puolesta.',160,NULL),
 (3795,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',19,'9Rukoilkaa myös minun puolestani, että minulle\nannettaisiin oikeat sanat suuhun, kun ryhdyn puhumaan,\nja että voisin rohkeasti julistaa evankeliumin\nsalaisuutta,',160,NULL),
 (3796,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',20,'0jonka vuoksi olen lähettiläänä, nyt\nvankilassa. Rukoilkaa, että voisin puhua rohkeasti,\nniin kuin minun tulee.   ',160,NULL),
 (3797,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',23,'3Jumala, meidän Isämme, ja Herra Jeesus Kristus\nantakoot teille, veljet, rauhan, rakkauden ja uskon.',160,NULL),
 (3798,'Anonymous','2008-10-17 16:33:58','Anonymous','2008-10-17 16:33:58',24,'4Armo tulkoon kaikkien niiden osaksi, jotka\nrakastavat Herraamme Jeesusta Kristusta, armo ja\nkatoamattomuus. ',160,NULL),
 (3799,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',1,'Paavali ja Timoteus, Kristuksen Jeesuksen\npalvelijat, tervehtivät kaikkia Filippissä olevia\nKristuksen Jeesuksen pyhiä sekä seurakunnan\nkaitsijoita ja palvelijoita.',161,NULL),
 (3800,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',2,'Jumalan, meidän\nIsämme, ja Herran Jeesuksen Kristuksen armo ja rauha\nteille.     ',161,NULL),
 (3801,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',15,'5Jotkut tosin julistavat Kristusta vain kateudesta\nja riidanhalusta, mutta toiset vilpittömin mielin.',161,NULL),
 (3802,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',16,'6Nämä toimivat rakkaudesta, koska tietävät, että\nminun tehtäväni on puolustaa evankeliumia.',161,NULL),
 (3803,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',17,'7Nuo\ntoiset taas julistavat Kristusta juonittelunhalusta,\nepäpuhtain mielin, ja uskovat näin tuottavansa minulle\nmurhetta täällä vankeudessani.',161,NULL),
 (3804,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',18,'8Mutta ei sillä\nväliä! Minä iloitsen siitäkin, kunhan Kristusta vain\nkaikin tavoin julistetaan, oli tarkoitus vilpitön tai\nei.   ',161,NULL),
 (3805,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',27,'7Teidän on vain käyttäydyttävä Kristuksen\nevankeliumin arvoa vastaavalla tavalla. Pääsenpä itse\nkäymään luonanne tai en, toivon saavani kuulla, että\nte pysytte lujasti samanhenkisinä ja taistelette\nyksimielisesti evankeliumin ja uskon puolesta',161,NULL),
 (3806,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',28,'8antamatta vastustajien säikyttää itseänne. Se on\nheille Jumalan antama osoitus siitä, että heitä\nodottaa kadotus mutta teitä pelastus.',161,NULL),
 (3807,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',29,'9Teille on\nsuotu se armo, että saatte uskoa Kristukseen ja vielä\nkärsiäkin hänen puolestaan.',161,NULL),
 (3808,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',30,'0Te käytte samaa\ntaistelua, jota olette nähneet minun käyvän ja jota\nnyt kuulette minun yhä jatkavan. ',161,NULL),
 (3809,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',1,'Jos kerran yhteys Kristukseen rohkaisee ja hänen\nrakkautensa suo lohdutusta, jos Henki meitä yhdistää\nja jos tunnemme hellyyttä ja myötätuntoa toisiamme\nkohtaan,',162,NULL),
 (3810,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',2,'niin tehkää minun iloni täydelliseksi ja\nolkaa yksimielisiä. Liittäköön teitä toisiinne\nrakkaus, sopu ja sama mieli.',162,NULL),
 (3811,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',3,'Älkää tehkö mitään\nitsekkyydestä tai turhamaisuudesta, vaan olkaa nöyriä\nja pitäkää kukin toista parempana kuin itseänne.',162,NULL),
 (3812,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',4,'Älkää tavoitelko vain omaa etuanne vaan myös muiden\nparasta.',162,NULL),
 (3813,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',5,'Olkoon teilläkin sellainen mieli, joka\nKristuksella Jeesuksella oli.       ',162,NULL),
 (3814,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',25,'5Nyt minun kuitenkin täytyy lähettää teidän\nluoksenne veljemme Epafroditos, työ- ja\ntaistelutoverini. Hän on myös teidän lähettinne, joka\ntuli palvelemaan minua ollessani avun tarpeessa,',162,NULL),
 (3815,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',26,'6ja hän on ikävöinyt teitä kaikkia ja ollut\nhuolissaan siitä, että te olette kuulleet hänen\nsairastuneen.',162,NULL),
 (3816,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',27,'7Sairas hän todella oli, aivan\nkuolemaisillaan, mutta Jumala armahti häntä, eikä vain\nhäntä vaan myös minua, etten saisi uutta surua\nentisten lisäksi.',162,NULL),
 (3817,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',28,'8Olen sitäkin innokkaampi\nlähettämään hänet takaisin, jotta te saisitte ilon\nnähdä hänet jälleen ja että itsekin voisin olla\nhuolettomampi.',162,NULL),
 (3818,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',29,'9Ottakaa siis hänet Herran\npalvelijana iloiten vastaan ja kunnioittakaa hänen\nkaltaisiaan!',162,NULL),
 (3819,'Anonymous','2008-10-17 16:33:59','Anonymous','2008-10-17 16:33:59',30,'0Hänhän oli Kristuksen työn vuoksi\nvähällä kuolla, kun vaaransi henkensä auttaakseen\nminua siinä, missä te ette voineet auttaa. ',162,NULL),
 (3820,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',1,'Veljeni, iloitkaa siis Herrassa! Minä en kyllästy\nkirjoittamaan teille yhä uudestaan samoista asioista,\nja sittenhän teillä on tarkka tieto. ',163,NULL),
 (3821,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',2,'Varokaa noita koiria, noita kelvottomia\ntyöntekijöitä, noita pilalle leikattuja!',163,NULL),
 (3822,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',3,'Todellisia\nympärileikattuja olemme me, jotka palvelemme Jumalaa\nHengen ohjaamina, ylpeilemme Kristuksesta Jeesuksesta\nemmekä luota mihinkään omaamme.',163,NULL),
 (3823,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',4,'Olisi minulla\naihetta kyllä siihenkin, minä jos kukaan voisin\nluottaa siihen mitä minulla on ollut.',163,NULL),
 (3824,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',5,'Minut on\nkahdeksantena päivänä ympärileikattu, olen\nsyntyperäinen israelilainen, Benjaminin heimoa,\nheprealainen heprealaisista vanhemmista. Lain\nnoudattajana olin fariseus*,',163,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (3825,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',6,'intoni ja kiivauteni\nosoitin vainoamalla seurakuntaa, lakiin perustuva\nvanhurskauteni oli moitteeton. ',163,NULL),
 (3826,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',7,'Mutta kaiken tämän, mikä oli minulle voittoa, olen\nKristuksen tähden lukenut tappioksi.',163,NULL),
 (3827,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',8,'Pidän todella\nsitä kaikkea pelkkänä tappiona, sillä Herrani\nKristuksen Jeesuksen tunteminen on minulle\narvokkaampaa kuin mikään muu. Hänen tähtensä olen\nmenettänyt kaiken, olen heittänyt kaiken roskana pois,\njotta voittaisin omakseni Kristuksen',163,NULL),
 (3828,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',9,'ja jotta\nkävisi ilmi, että kuulun hänelle. Näin minulla ei enää\nole mitään omaa, lain noudattamiseen perustuvaa\nvanhurskautta, vaan se vanhurskaus, jonka perustana on\nusko Kristukseen ja jonka Jumala antaa sille, joka\nuskoo.',163,NULL),
 (3829,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',10,'0Minä tahdon tuntea Kristuksen ja hänen\nylösnousemisensa voiman ja tulla hänen kaltaisekseen\nosallistumalla hänen kärsimyksiinsä ja kuolemaansa.',163,NULL),
 (3830,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',11,'1Ehkä silloin saan myös nousta kuolleista.   ',163,NULL),
 (3831,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',17,'7Noudattakaa minun antamaani esimerkkiä, veljet, ja\nottakaa oppia niistä, jotka elävät meidän tavallamme.',163,NULL),
 (3832,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',18,'8Olenhan usein sanonut teille ja nyt sanon aivan\nitkien, että monet elävät Kristuksen ristin\nvihollisina.',163,NULL),
 (3833,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',19,'9Heidän loppunsa on kadotus. Vatsa on\nheidän jumalansa ja häpeä heidän kunniansa, ja he\najattelevat vain maallisia asioita.',163,NULL),
 (3834,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',20,'0Mutta me\nolemme taivaan kansalaisia, ja taivaasta me odotamme\npelastajaksi Herraa Jeesusta Kristusta.',163,NULL),
 (3835,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',21,'1Hän\nmuuttaa meidän ruumiimme tästä alennustilasta oman\nkirkastuneen ruumiinsa kaltaiseksi voimallaan, jolla\nhän kykenee alistamaan valtaansa kaiken. ',163,NULL),
 (3836,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',1,'Rakkaat ja kaivatut veljeni, te minun iloni ja\nkunniaseppeleeni, pysykää siis horjumatta Herran\nyhteydessä, rakkaat ystävät! ',164,NULL),
 (3837,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',2,'Kehotan Euodiaa ja Syntykeä elämään sovussa Herraa\npalvellen.',164,NULL),
 (3838,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',3,'Pyydän myös sinua, uskollinen\ntyötoverini, auttamaan näitä naisia, jotka ovat minun\nkanssani taistelleet evankeliumin puolesta samoin kuin\nClemens ja muutkin työtoverini, joiden kaikkien nimet\novat elämän kirjassa.',164,NULL),
 (3839,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',4,'Iloitkaa aina Herrassa! Sanon\nvielä kerran: iloitkaa!',164,NULL),
 (3840,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',5,'Tulkoon teidän lempeytenne\nkaikkien ihmisten tietoon. Herra on jo lähellä.',164,NULL),
 (3841,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',6,'Älkää olko mistään huolissanne, vaan saattakaa aina\nse, mitä tarvitsette, rukoillen, anoen ja kiittäen\nJumalan tietoon.',164,NULL),
 (3842,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',7,'Silloin Jumalan rauha, joka\nylittää kaiken ymmärryksen, varjelee teidän sydämenne\nja ajatuksenne, niin että pysytte Kristuksessa\nJeesuksessa. ',164,NULL),
 (3843,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',8,'Lopuksi, veljet, ajatelkaa kaikkea mikä on totta,\nmikä on kunnioitettavaa, mikä oikeaa, puhdasta,\nrakastettavaa ja kaunista, mikä vain on hyvää ja\nansaitsee kiitoksen.',164,NULL),
 (3844,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',9,'Tehkää sitä, mitä olette\nminulta oppineet ja vastaanottaneet, mitä olette\nminulta kuulleet ja minusta nähneet. Silloin rauhan\nJumala on oleva teidän kanssanne.   ',164,NULL),
 (3845,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',15,'5Niin kuin itsekin tiedätte, te filippiläiset\nolitte ainoa seurakunta, joka evankeliumin\njulistustyön alkuvaiheessa, kun olin lähtenyt\nMakedoniasta, antoi minulle vastalahjaksi\ntaloudellista tukea.',164,NULL),
 (3846,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',16,'6Jo silloin, kun olin\nTessalonikassa, te lähetitte minulle avustusta parikin\nkertaa.',164,NULL),
 (3847,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',17,'7En minä tosin teidän lahjojanne\ntavoittele, toivon vain, että työnne tuottaisi teille\nyhä enemmän hedelmää.',164,NULL),
 (3848,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',18,'8Tunnustan saaneeni kaiken ja\nenemmänkin. Minulla on kaikkea yllin kyllin nyt, kun\nolen saanut Epafroditokselta teidän lähettämänne\nlahjan, joka on hyvältä tuoksuva, otollinen, Jumalan\nmielen mukainen uhri.',164,NULL),
 (3849,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',19,'9Minun Jumalani on Kristuksen\nJeesuksen tähden antava teille taivaallisen kunniansa\nrikkaudesta kaiken, mitä tarvitsette.',164,NULL),
 (3850,'Anonymous','2008-10-17 16:34:00','Anonymous','2008-10-17 16:34:00',20,'0Jumalan,\nmeidän Isämme, on kunnia aina ja ikuisesti. Aamen.   ',164,NULL),
 (3851,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',1,'Paavali, Jumalan tahdosta Kristuksen Jeesuksen\napostoli, ja veli Timoteus',165,NULL),
 (3852,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',2,'tervehtivät Kolossan\npyhiä, Kristukseen uskovia veljiä. Jumalan, meidän\nIsämme, armo ja rauha teille.          ',165,NULL),
 (3853,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',21,'1Tekin olitte ennen Jumalasta vieraantuneita ja\nhäntä kohtaan vihamielisiä, kun elitte pahojen\ntekojenne vallassa.',165,NULL),
 (3854,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',22,'2Mutta nyt hän on tehnyt teidän\nkanssanne sovinnon, kun Kristus omassa ruumiissaan\nkärsi kuoleman asettaakseen teidät pyhinä,\nnuhteettomina ja moitteettomina Jumalan eteen.',165,NULL),
 (3855,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',23,'3Teidän on vain pysyttävä lujina uskon perustalla,\nhorjahtamatta pois siitä toivosta, jonka teidän\nkuulemanne evankeliumi antaa. Tämä evankeliumi on\njulistettu kaikille luoduille taivaan alla, ja\nminusta, Paavalista, on tullut sen palvelija.   ',165,NULL),
 (3856,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',1,'Tahdon teidän tietävän, kuinka kovaa taistelua käyn\nteidän tähtenne ja laodikealaisten ja kaikkien muiden\ntähden, jotka eivät ole koskaan nähneet minua\nkasvoista kasvoihin.',166,NULL),
 (3857,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',2,'Toivon, että he rakkauden\nyhteen liittäminä saisivat rohkeutta ja saavuttaisivat\nrikkaimman ja syvimmän tiedon: tulisivat tuntemaan\nJumalan salaisuuden, Kristuksen,',166,NULL),
 (3858,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',3,'jossa kaikki\nviisauden ja tiedon aarteet ovat kätkettyinä.',166,NULL),
 (3859,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',4,'Sanon\ntämän, jottei kukaan pettäisi teitä vakuutteluillaan.',166,NULL),
 (3860,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',5,'Vaikka en olekaan ruumiillisesti läsnä, olen\nhengessä teidän luonanne, ja iloitsen nähdessäni, että\nkeskuudessanne vallitsee hyvä järjestys ja että teillä\non luja usko Kristukseen.     ',166,NULL),
 (3861,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',20,'0Jos kerran olette yhdessä Kristuksen kanssa\nkuolleet pois maailman alkuvoimien ulottuvilta, miksi\nyhä käyttäydytte tämän maailman mukaisesti ja\nalistutte sellaisiin sääntöihin kuin',166,NULL),
 (3862,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',21,'1\"älä tartu\",\n\"älä maista\", \"älä kosketa\"?',166,NULL),
 (3863,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',22,'2Tämä kaikkihan koskee\nsellaista, mikä käytön jälkeen häviää. Kysymys on vain\nihmisten käskyistä ja opeista.',166,NULL),
 (3864,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',23,'3Nämä omatekoista\nhurskautta, nöyryyden harjoitusta ja ruumiin\nkurittamista vaativat käskyt tosin näyttävät\nviisailta, mutta todellisuudessa ne ovat arvottomia ja\ntyydyttävät vain ihmisen ylpeyttä. ',166,NULL),
 (3865,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',1,'Jos siis teidät on yhdessä Kristuksen kanssa\nherätetty kuolleista, niin tavoitelkaa sitä mikä on\nylhäällä, missä Kristus istuu Jumalan oikealla\npuolella.',167,NULL),
 (3866,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',2,'Ajatelkaa sitä mikä on ylhäällä, älkää\nsitä mikä on maan päällä.',167,NULL),
 (3867,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',3,'Tehän olette kuolleet, ja\nteidän elämänne on Kristuksen kanssa kätketty\nJumalaan.',167,NULL),
 (3868,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',4,'Kun Kristus, teidän elämänne, ilmestyy,\nsilloin tekin ilmestytte hänen kanssaan kirkkaudessa. ',167,NULL),
 (3869,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',5,'Haudatkaa siis se, mikä teissä on maallista:\nsiveettömyys, saastaisuus, intohimot, pahat halut ja\nahneus, joka on epäjumalanpalvelusta.',167,NULL),
 (3870,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',6,'Niiden vuoksi\nJumalan viha kohtaa tottelemattomia ihmisiä.',167,NULL),
 (3871,'Anonymous','2008-10-17 16:34:01','Anonymous','2008-10-17 16:34:01',7,'Niiden\nmukaan tekin elitte, kun olitte niiden vallassa.',167,NULL),
 (3872,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',8,'Luopukaa nyt tekin tästä kaikesta: vihasta,\nkiukusta, pahuudesta, herjauksista ja siivottomista\npuheista.',167,NULL),
 (3873,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',9,'Älkää valehdelko toisillenne. Olettehan\nriisuneet yltänne vanhan minänne kaikkine tekoineen',167,NULL),
 (3874,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',10,'0ja pukeutuneet uuteen, joka jatkuvasti uudistuu\noppiakseen yhä paremmin tuntemaan Luojansa ja\ntullakseen hänen kaltaisekseen.',167,NULL),
 (3875,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',11,'1Silloin ei ole\nenää kreikkalaista eikä juutalaista, ei\nympärileikattua eikä ympärileikkaamatonta, ei\nbarbaaria, skyyttalaista, orjaa eikä vapaata, vaan\nKristus on kaikki, hän on kaikissa. ',167,NULL),
 (3876,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',12,'2Te, jotka olette Jumalan valittuja, pyhiä ja\nhänelle rakkaita, pukeutukaa siis sydämelliseen\narmahtavaisuuteen, ystävällisyyteen, nöyryyteen,\nlempeyteen ja kärsivällisyyteen.',167,NULL),
 (3877,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',13,'3Pitäkää huolta,\nettä tulette toimeen keskenänne, antakaa anteeksi\ntoisillenne, vaikka teillä olisikin moittimisen\naihetta. Niin kuin Herra on antanut teille anteeksi,\nniin antakaa tekin.',167,NULL),
 (3878,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',14,'4Mutta kaiken kruunuksi tulkoon\nrakkaus, sillä se tekee kaiken täydelliseksi.',167,NULL),
 (3879,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',15,'5Vallitkoon teidän sydämissänne Kristuksen rauha,\njohon teidät on yhden ja saman ruumiin jäseninä\nkutsuttu. Olkaa myös kiitollisia.',167,NULL),
 (3880,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',16,'6Antakaa\nKristuksen sanan asua runsaana keskuudessanne.\nOpettakaa ja neuvokaa toisianne kaikella viisaudella\nja laulakaa kiitollisin mielin Jumalalle psalmeja,\nylistysvirsiä ja hengellisiä lauluja.',167,NULL),
 (3881,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',17,'7Mitä\nteettekin, sanoin tai teoin, tehkää kaikki Herran\nJeesuksen nimessä, kiittäen hänen kauttaan Jumalaa,\nIsäämme.   ',167,NULL),
 (3882,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',22,'2Orjat, totelkaa kaikessa maallisia isäntiänne, ei\nsilmänpalvelijoina ja mielistellen, vaan vilpittömin\nsydämin, Herraa peläten.',167,NULL),
 (3883,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',23,'3Mitä teettekin, tehkää se\ntäydestä sydämestä, niin kuin tekisitte sen Herralle\nettekä ihmisille.',167,NULL),
 (3884,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',24,'4Muistakaa, että saatte Herralta\npalkan, osuutenne perinnöstä. Palvelkaa Herraa\nKristusta.',167,NULL),
 (3885,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',25,'5Väärintekijä saa maksaa tekemänsä\nvääryyden, sillä Jumala ei tee eroa ihmisten välillä. ',167,NULL),
 (3886,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',1,'Isännät, kohdelkaa orjianne oikeudenmukaisesti ja\ntasapuolisesti. Muistakaa, että teilläkin on taivaassa\nherranne.   ',168,NULL),
 (3887,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',7,'Kaikesta, mikä koskee minua, teille kertoo rakas\nveljemme Tykikos, uskollinen palvelija ja työtoverini\nHerran työssä.',168,NULL),
 (3888,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',8,'Lähetänkin hänet luoksenne juuri\nsitä varten, että saisitte tietää, mitä meille kuuluu,\nja että hän rohkaisisi teitä.',168,NULL),
 (3889,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',9,'Hänen mukanaan tulee\nuskollinen ja rakas veljemme Onesimos, joka on\nteikäläisiä. He kertovat teille kaikesta, mitä tänne\nkuuluu. ',168,NULL),
 (3890,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',10,'0Teille lähettää terveisiä vankitoverini\nAristarkos, samoin myös Markus, Barnabaksen serkku,\njosta olette saaneet ohjeet. Ottakaa hänet vastaan,\njos hän tulee luoksenne.',168,NULL),
 (3891,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',11,'1Terveisiä Jeesukselta,\njota sanotaan myös Justukseksi. He ovat niitä\njuutalaisia, jotka ovat tehneet kanssani työtä Jumalan\nvaltakunnan hyväksi, ja heistä ainoat, joilta olen\nsaanut todellista tukea.',168,NULL),
 (3892,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',12,'2Terveisiä lähettää\nteikäläinen Epafras, Kristuksen Jeesuksen palvelija,\njoka aina taistelee rukouksin teidän puolestanne,\njotta pysyisitte lujina ja nuhteettomina ja\nvankkumatta noudattaisitte kaikessa Jumalan tahtoa.',168,NULL),
 (3893,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',13,'3Voin todistaa, että hän näkee paljon vaivaa teidän\nvuoksenne sekä Laodikean ja Hierapoliin seurakuntien\nvuoksi.',168,NULL),
 (3894,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',14,'4Teille lähettävät terveisiä myös rakas\nveljemme lääkäri Luukas sekä Demas. ',168,NULL),
 (3895,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',15,'5Sanokaa terveisiä Laodikeassa oleville veljille\nsekä Nymfalle ja hänen kodissaan kokoontuvalle\nseurakunnalle.',168,NULL),
 (3896,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',16,'6Kun tämä kirje on luettu teidän\nkeskuudessanne, pitäkää huoli siitä, että se luetaan\nmyös Laodikean seurakunnassa ja että te puolestanne\nluette Laodikeaan lähettämäni kirjeen.',168,NULL),
 (3897,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',17,'7Sanokaa\nArkippokselle: \"Muista hoitaa hyvin se tehtävä, jonka\nolet Herralta saanut.\" ',168,NULL),
 (3898,'Anonymous','2008-10-17 16:34:02','Anonymous','2008-10-17 16:34:02',18,'8Omakätinen tervehdys minulta, Paavalilta.\nMuistakaa minun kahleitani. Armo olkoon teidän\nkanssanne. ',168,NULL),
 (3899,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',4,'tervehtii Titusta, omaa hengellistä\npoikaansa, saman uskon tunnustajaa. Isän Jumalan ja\nVapahtajamme Kristuksen Jeesuksen armo ja rauha\nsinulle!* 1bApostolina olen saanut tehtäväkseni auttaa Jumalan\nvalittuja uskomaan, tuntemaan pyhän totuuden',169,NULL),
 (3900,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',2,'ja\ntoivoen odottamaan ikuista elämää. Tämän on Jumala\nluvannut ennen aikojen alkua, ja hän pysyy sanassaan.',169,NULL),
 (3901,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',3,'Kun oikea aika oli tullut, hän ilmoitti sanansa\nsiinä evankeliumissa, joka uskottiin minulle Jumalan,\nmeidän pelastajamme, käskystä.   ',169,NULL),
 (3902,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',10,'0Varsinkin juutalaisuudesta kääntyneiden joukossa\non monia kurittomia, tyhjänpuhujia, jotka johtavat\ntoisia harhaan.',169,NULL),
 (3903,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',11,'1Heidän suunsa on tukittava, sillä\nhe villitsevät kokonaisia perheitä esittämällä oman\nvoiton toivossa vääriä oppejaan.',169,NULL),
 (3904,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',12,'2Onhan eräs heidän\nmaanmiehensä, heidän oma profeettansa, sanonut:   ',169,NULL),
 (3905,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',13,'3Tämä todistus pitää paikkansa. Nuhtele heitä sen\nvuoksi ankarasti, jotta he tulisivat uskossaan\nterveiksi',169,NULL),
 (3906,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',14,'4eivätkä enää kuuntelisi juutalaisia\ntaruja ja sellaisten ihmisten käskyjä, jotka ovat\nkääntäneet selkänsä totuudelle.',169,NULL),
 (3907,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',15,'5Puhtaille kaikki\non puhdasta, mutta epäpuhtaille ja epäuskoisille ei\nmikään ole puhdasta, vaan heidän mielensä ja\nomatuntonsakin on likaantunut.',169,NULL),
 (3908,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',16,'6He väittävät\ntuntevansa Jumalan, mutta teoillaan he kieltävät\nhänet. He ovat inhottavia ja tottelemattomia,\nkykenemättömiä tekemään mitään hyvää. ',169,NULL),
 (3909,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',1,'Sinun puheesi olkoon terveen opin mukaista.',170,NULL),
 (3910,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',2,'Kehota vanhoja miehiä olemaan raittiita, arvokkaita\nja maltillisia sekä osoittamaan tervettä uskoa,\nrakkautta ja kestävyyttä.',170,NULL),
 (3911,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',3,'Vanhojen naisten tulee\nsamoin käyttäytyä arvokkaasti. He eivät saa puhua\nmuista pahaa eivätkä olla viinin orjia, vaan heidän on\nopetettava muita hyvään.',170,NULL),
 (3912,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',4,'Heidän tulee kehottaa\nnuoria naisia rakastamaan miestään ja lapsiaan,',170,NULL),
 (3913,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',5,'olemaan puhtaita ja siveitä, hoitamaan hyvin\nkotinsa ja mukautumaan miehensä tahtoon, jottei\nJumalan sana joutuisi pilkan kohteeksi.',170,NULL),
 (3914,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',6,'Kehota myös\nnuoria miehiä käyttäytymään joka suhteessa hillitysti',170,NULL),
 (3915,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',7,'ja näytä itse esimerkkiä hyvän tekemisessä.\nOpetuksesi olkoon puhdasta ja arvokasta',170,NULL),
 (3916,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',8,'ja puheesi\ntervettä ja vakuuttavaa, niin että vastustajasi joutuu\nhäpeään, koska ei pysty sanomaan meistä mitään pahaa. ',170,NULL),
 (3917,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',9,'Kehota orjia alistumaan kaikessa isäntiensä tahtoon\nja toimimaan näiden mielen mukaan. Heidän ei pidä\nväittää vastaan',170,NULL),
 (3918,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',10,'0eikä varastella, vaan heidän on\nosoittauduttava aina luotettaviksi ja kunnollisiksi,\nniin että he voivat kaikessa olla kunniaksi Jumalan,\nmeidän pelastajamme, opille. ',170,NULL),
 (3919,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',11,'1Jumalan armo on näet ilmestynyt pelastukseksi\nkaikille ihmisille,',170,NULL),
 (3920,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',12,'2ja se kasvattaa meitä\nhylkäämään jumalattomuuden ja maailmalliset himot ja\nelämään hillitysti, oikeamielisesti ja Jumalaa\nkunnioittaen tässä maailmassa,',170,NULL),
 (3921,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',13,'3kun odotamme\nautuaan toivomme toteutumista, suuren Jumalan ja\nVapahtajamme Jeesuksen Kristuksen kirkkauden\nilmestymistä.',170,NULL),
 (3922,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',14,'4Hän antoi itsensä alttiiksi meidän\npuolestamme lunastaakseen meidät vapaiksi kaikesta\nvääryydestä ja puhdistaakseen meidät omaksi\nkansakseen, joka kaikin voimin tekee hyvää.',170,NULL),
 (3923,'Anonymous','2008-10-17 16:34:03','Anonymous','2008-10-17 16:34:03',15,'5Näin\nsinun tulee puhua, kehottaa ja nuhdella kaikella\narvovallalla, eikä kenenkään pidä väheksyä sinua. ',170,NULL),
 (3924,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',1,'Muistuta heitä kaikkia, että heidän on toteltava\nesivaltaa ja viranomaisia ja oltava näille kuuliaisia\nja valmiita tekemään kaikkea hyvää.',171,NULL),
 (3925,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',2,'He eivät saa\nparjata ketään eivätkä haastaa riitaa, vaan heidän on\noltava ystävällisiä ja osoitettava lempeyttä kaikille\nihmisille.',171,NULL),
 (3926,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',3,'Olimmehan mekin ennen ymmärtämättömiä ja\ntottelemattomia, olimme eksyksissä ja monenlaisten\nhimojen ja nautintojen orjia, elimme pahuuden ja\nkateuden vallassa, vihattuina ja toisiamme vihaten.',171,NULL),
 (3927,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',4,'Mutta kun Jumalan, meidän pelastajamme, hyvyys ja\nrakkaus ihmisiä kohtaan tuli näkyviin,',171,NULL),
 (3928,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',5,'hän pelasti\nmeidät, ei meidän hurskaiden tekojemme tähden, vaan\npelkästä armosta. Hän pelasti meidät pesemällä meidät\npuhtaiksi, niin että synnyimme uudesti ja Pyhä Henki\nuudisti meidät.',171,NULL),
 (3929,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',6,'Tämän Hengen hän vuodatti runsaana\nmeidän päällemme Vapahtajamme Jeesuksen Kristuksen\nkautta,',171,NULL),
 (3930,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',7,'jotta me hänen armonsa ansiosta tulisimme\nvanhurskaiksi ja saisimme osaksemme ikuisen elämän,\nniin kuin toivomme. ',171,NULL),
 (3931,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',8,'Tämä sana on varma, ja haluan sinun tähdentävän\nnäitä asioita, jotta ne, jotka uskovat Jumalaan,\ntekisivät ahkerasti hyvää. Tällainen on ihmisille\nhyväksi ja hyödyksi.',171,NULL),
 (3932,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',9,'Vältä typeriä väittelyitä,\nsukuluetteloita sekä lakia koskevia riitoja ja\nkiistoja, sillä ne ovat hyödyttömiä ja turhia.',171,NULL),
 (3933,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',10,'0Karta harhaoppista ihmistä, kun ensin olet\nojentanut häntä kerran tai kahdesti.',171,NULL),
 (3934,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',11,'1Sinähän\ntiedät, että tuollainen ihminen on kieroutunut ja\ntekee syntiä. Hän tuomitsee itse itsensä.   ',171,NULL),
 (3935,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',15,'5Kaikki, jotka ovat täällä minun luonani,\nlähettävät sinulle terveisiä. Sano terveisiä kaikille\nuskoville ystävillemme. Armo teille kaikille! ',171,NULL),
 (3936,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',1,'Paavali, Silvanus ja Timoteus tervehtivät\nTessalonikan seurakuntaa, joka elää Isän Jumalan ja\nHerran Jeesuksen Kristuksen yhteydessä. Armo ja rauha\nteille.   ',172,NULL),
 (3937,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',1,'Tiedättehän itsekin, veljet, ettei käyntimme teidän\nluonanne ollut turha.',173,NULL),
 (3938,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',2,'Vaikka meitä oli Filippissä\nloukattu ja pahoinpidelty, niin kuin tiedätte, Jumala\nantoi meille rohkeuden julistaa teille sanomaansa\nankaran vastustuksen keskelläkin.',173,NULL),
 (3939,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',3,'Meidän saarnamme\nei ole peräisin harha-ajatuksista, ei epäpuhtaasta\nmielestä eikä vilpillisistä aikeista.',173,NULL),
 (3940,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',4,'Koska Jumala\non katsonut meidät kelvollisiksi julistamaan\nevankeliumia, me emme puhuessamme pyri olemaan\nmieliksi ihmisille vaan Jumalalle, joka tutkii\nsydämemme.',173,NULL),
 (3941,'Anonymous','2008-10-17 16:34:04','Anonymous','2008-10-17 16:34:04',5,'Emme ole koskaan puhuneet mielistelevin\nsanoin, sen te tiedätte; emme ole salaa ajaneet omia\netujamme, siitä Jumala on todistajamme.',173,NULL),
 (3942,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',6,'Emme ole\nyrittäneet saada kunniaa ihmisiltä, emme teiltä emmekä\nmuilta,',173,NULL),
 (3943,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',7,'vaikka Kristuksen apostoleina olisimme\nvoineet vaatia itsellemme arvonantoa. Ollessamme teidän luonanne olimme lempeitä kuin\nlapsiaan hoivaava äiti.',173,NULL),
 (3944,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',8,'Rakastimme teitä niin\nhellästi, että olimme valmiit antamaan teille Jumalan\nevankeliumin lisäksi oman itsemmekin; niin rakkaiksi\nte olitte meille tulleet.',173,NULL),
 (3945,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',9,'Muistattehan, veljet,\nmiten me uurastimme ja näimme vaivaa. Kun julistimme\nteille Jumalan evankeliumia, teimme samalla yötä\npäivää ansiotyötä, jotta emme olisi olleet teidän\nvaivoinanne.',173,NULL),
 (3946,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',10,'0Te voitte todistaa, ja itse Jumala,\nettä käytöksemme teitä uskovia kohtaan oli puhdasta,\noikeudenmukaista ja moitteetonta.',173,NULL),
 (3947,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',11,'1Niin kuin\ntiedätte, me kehotimme ja rohkaisimme teitä jokaista\nkuin isä lapsiaan',173,NULL),
 (3948,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',12,'2ja vetosimme teihin, että\neläisitte Jumalalle kelpaavaa elämää, sillä hän kutsuu\nteitä valtakuntaansa ja kirkkauteensa.     ',173,NULL),
 (3949,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',1,'Niinpä, kun emme enää kestäneet kauempaa, päätimme\nitse jäädä Ateenaan',174,NULL),
 (3950,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',2,'ja lähetimme teidän luoksenne\nveljemme Timoteuksen, Jumalan työtoverin Kristuksen\nevankeliumin julistajana. Hänen tuli vahvistaa teitä\nja rohkaista uskoanne,',174,NULL),
 (3951,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',3,'jottei kukaan horjuisi,\nvaikka teitä näin ahdistetaan. Sehän on meidän osamme,\nniin kuin itse tiedätte.',174,NULL),
 (3952,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',4,'Jo ollessamme teidän\nluonanne sanoimme, että me joudumme kärsimään vainoa.\nJa juuri niin on käynyt, kuten tiedätte.',174,NULL),
 (3953,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',5,'Siksi en\nenää kestänyt kauempaa vaan lähetin Timoteuksen\nottamaan selkoa teidän uskostanne. Pelkäsin näet, että\nkiusaaja on onnistunut aikeissaan ja vaivannäkömme\nmenee hukkaan. ',174,NULL),
 (3954,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',6,'Nyt Timoteus on palannut teidän luotanne tänne ja\ntuonut meille hyviä uutisia teidän uskostanne ja\nrakkaudestanne. Hän kertoi, että te muistelette meitä\naina iloiten ja kaipaatte meitä yhtä paljon kuin me\nteitä.',174,NULL),
 (3955,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',7,'Näin te olette, veljet, omalla uskollanne\nvalaneet meihin rohkeutta kaiken hädän ja ahdingon\nkeskellä.',174,NULL),
 (3956,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',8,'Nyt voimme taas elää, jos te kerran\npysytte lujasti Herran yhteydessä.',174,NULL),
 (3957,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',9,'Miten voimme\nkylliksi kiittää Jumalaa teistä ja kaikesta siitä\nilosta, jota saamme hänen edessään tuntea teidän\ntähtenne?',174,NULL),
 (3958,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',10,'0Yötä päivää me rukoilemme kaikesta\nsydämestämme, että pääsisimme tapaamaan teitä ja\nvoisimme antaa teille sen, mitä uskostanne vielä\npuuttuu. ',174,NULL),
 (3959,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',11,'1Itse Jumala, meidän Isämme, ja Herramme Jeesus\ntasoittakoot meille tien teidän luoksenne.',174,NULL),
 (3960,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',12,'2Lisätköön Herra runsain määrin teidän rakkauttanne\ntoisianne ja kaikkia ihmisiä kohtaan -- niin kuin\nmekin rakastamme teitä --',174,NULL),
 (3961,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',13,'3ja vahvistakoon näin\nsydämenne, että ne olisivat pyhät ja moitteettomat\nJumalamme ja Isämme edessä, kun Herramme Jeesus saapuu\nkaikkien pyhiensä kanssa. ',174,NULL),
 (3962,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',1,'Sitten vielä, veljet! Te olette oppineet meiltä,\nmiten teidän on elettävä ollaksenne Jumalalle mieleen,\nja niinhän te elättekin. Mutta Herran Jeesuksen\nnimessä pyydämme ja kehotamme teitä pyrkimään yhä\nparempaan.',175,NULL),
 (3963,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',2,'Tiedättehän, mitä käskyjä me Herran\nJeesuksen puolesta olemme teille antaneet.',175,NULL),
 (3964,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',3,'Jumalan\ntahto on, että te pyhitytte. Kavahtakaa\nsiveettömyyttä!',175,NULL),
 (3965,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',4,'Jokaisen teistä on opittava\npitämään ruumiinsa*pyhänä ja kunniassa.',175,NULL),
 (3966,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',5,'Älkää\nantako sitä himon ja kiihkon valtaan, niin kuin\ntekevät pakanat, jotka eivät tunne Jumalaa.',175,NULL),
 (3967,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',6,'Kukaan\nei saa pyrkiä hyötymään veljensä kustannuksella. Herra\nrankaisee kaikesta sellaisesta, niin kuin olemme\nteille sanoneet ja teroittaneet mieleenne.',175,NULL),
 (3968,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',7,'Jumala\nei näet ole kutsunut meitä elämään synnillistä, vaan\npyhää elämää.',175,NULL),
 (3969,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',8,'Sen tähden se, joka kääntää selkänsä\nnäille ohjeille, ei käännä selkäänsä ihmiselle vaan\nJumalalle, joka antaa teihin Pyhän Henkensä. ',175,NULL),
 (3970,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',9,'Veljesrakkaudesta teille ei tarvitse kirjoittaa,\nsillä Jumala itse on opettanut teidät rakastamaan\ntoinen toistanne.',175,NULL),
 (3971,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',10,'0Tehän rakastatte kaikkia\nuskonveljiä koko Makedoniassa. Mutta me kehotamme\nteitä, veljet, pyrkimään yhä parempaan.',175,NULL),
 (3972,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',11,'1Pitäkää\nkunnia-asiananne, että elätte rauhallisesti, hoidatte\nomat asianne ja ansaitsette toimeentulonne kättenne\ntyöllä, niin kuin olemme käskeneet.',175,NULL),
 (3973,'Anonymous','2008-10-17 16:34:05','Anonymous','2008-10-17 16:34:05',12,'2Näin te elätte\nulkopuolistenkin mielestä nuhteettomasti ettekä\ntarvitse kenenkään apua.   ',175,NULL),
 (3974,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',1,'Aikamääristä teille ei tarvitse kirjoittaa, veljet,',176,NULL),
 (3975,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',2,'sillä te tiedätte itse aivan hyvin, että Herran\npäivä tulee kuin varas yöllä.',176,NULL),
 (3976,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',3,'Juuri kun ihmiset\nsanovat: \"Kaikki on hyvin, ei mitään hätää\", tuho\nkohtaa heidät äkkiarvaamatta niin kuin synnytyspoltot\nraskaana olevan naisen, eivätkä he pääse pakoon.',176,NULL),
 (3977,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',4,'Mutta te, ystävät, ette elä pimeydessä, eikä tuo\npäivä pääse yllättämään teitä kuin varas.',176,NULL),
 (3978,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',5,'Te kaikki\nolette valon ja päivän lapsia. Me emme kuulu yölle\nemmekä pimeydelle.',176,NULL),
 (3979,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',6,'Emme siis saa nukkua niin kuin\nmuut, vaan meidän on valvottava ja pysyttävä\nraittiina.',176,NULL),
 (3980,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',7,'Ne, jotka nukkuvat, nukkuvat yöllä; ne,\njotka juovat, ovat juovuksissa yöllä.',176,NULL),
 (3981,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',8,'Mutta meidän,\njotka kuulumme päivälle, on pysyttävä raittiina:\nmeidän on pukeuduttava uskon ja rakkauden haarniskaan\nja otettava kypäräksemme pelastuksen toivo.',176,NULL),
 (3982,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',9,'Jumala\nei ole tarkoittanut, että saisimme osaksemme vihan\nvaan että pelastuisimme Herramme Jeesuksen Kristuksen\ntullessa.',176,NULL),
 (3983,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',10,'0Kristus on kuollut puolestamme, jotta\nsaisimme elää yhdessä hänen kanssaan, olimmepa\nvalveilla tai kuoleman unessa.',176,NULL),
 (3984,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',11,'1Rohkaiskaa ja\nvahvistakaa siis toinen toistanne, ja niinhän te\nteettekin.   ',176,NULL),
 (3985,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',16,'6Iloitkaa aina.',176,NULL),
 (3986,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',17,'7Rukoilkaa lakkaamatta.',176,NULL),
 (3987,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',18,'8Kiittäkää kaikesta. Tätä Jumala tahtoo teiltä,\nKristuksen Jeesuksen omilta.',176,NULL),
 (3988,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',19,'9Älkää sammuttako\nHenkeä,',176,NULL),
 (3989,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',20,'0älkää väheksykö profetoimisen lahjaa.',176,NULL),
 (3990,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',21,'1Koetelkaa kaikkea ja pitäkää se mikä on hyvää.',176,NULL),
 (3991,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',22,'2Pysykää erossa kaikesta pahasta. ',176,NULL),
 (3992,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',23,'3Itse rauhan Jumala pyhittäköön teidät kokonaan ja\nvarjelkoon koko olemuksenne, teidän henkenne, sielunne\nja ruumiinne, niin että olette nuhteettomat Herramme\nJeesuksen Kristuksen tullessa.',176,NULL),
 (3993,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',24,'4Hän, joka teitä\nkutsuu, on uskollinen ja pitää lupauksensa. ',176,NULL),
 (3994,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',25,'5Veljet, rukoilkaa meidän puolestamme.',176,NULL),
 (3995,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',26,'6Tervehtikää kaikkia veljiä pyhällä suudelmalla.',176,NULL),
 (3996,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',27,'7Herran nimeen minä vaadin, että luette tämän\nkirjeen kaikille veljille. ',176,NULL),
 (3997,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',28,'8Herramme Jeesuksen Kristuksen armo olkoon teidän\nkanssanne.  ',176,NULL),
 (3998,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',1,'Paavali, Silvanus ja Timoteus tervehtivät\nTessalonikan seurakuntaa, joka elää Jumalan, meidän\nIsämme, ja Herran Jeesuksen Kristuksen yhteydessä.',177,NULL),
 (3999,'Anonymous','2008-10-17 16:34:06','Anonymous','2008-10-17 16:34:06',2,'Jumalan, meidän Isämme, ja Herran Jeesuksen\nKristuksen armo ja rauha teille.   ',177,NULL),
 (4000,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',6,'Jumala on oikeudenmukainen: hän antaa teidän\nahdistajienne joutua ahdinkoon,',177,NULL),
 (4001,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',7,'mutta ahdistetut --\nniin teidät kuin meidätkin -- hän päästää siitä, kun\nHerra Jeesus väkevien enkeleittensä kanssa ilmestyy\ntaivaasta',177,NULL),
 (4002,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',8,'tulenlieskojen keskellä ja rankaisee\nniitä, jotka eivät tunne Jumalaa eivätkä suostu\nolemaan kuuliaisia Herramme Jeesuksen evankeliumille.',177,NULL),
 (4003,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',9,'Heidän rangaistuksenaan on ikuinen kadotus, ero\nHerrasta ja hänen voimansa kirkkaudesta,',177,NULL),
 (4004,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',10,'0kun hän\ntuona päivänä saapuu ottamaan vastaan pyhiensä\nkunnioituksen ja kaikkien uskovien ylistyksen. Olettehan tekin niitä, jotka ovat uskoneet meidän\ntodistuksemme.',177,NULL),
 (4005,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',11,'1Sen tähden me rukoilemme aina\nteidän puolestanne, että Jumalamme pitäisi teitä\nsaamanne kutsun arvoisina ja että hän voimallaan\nvahvistaisi teidän haluanne hyvään ja saattaisi\npäätökseen ne teot, jotka todistavat uskostanne.',177,NULL),
 (4006,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',12,'2Näin te kirkastaisitte Herramme Jeesuksen nimeä ja\nhän kirkastaisi teidät, meidän Jumalamme ja Herran\nJeesuksen Kristuksen armon mukaisesti. ',177,NULL),
 (4007,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',1,'Otamme nyt puheeksi Herramme Jeesuksen Kristuksen\ntulemisen ja sen, että meidät kootaan hänen luokseen.\nMe pyydämme, veljet,',178,NULL),
 (4008,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',2,'ettette heti menetä malttianne\nja säikähdä, jos joku vedoten Hengen ilmoitukseen tai\nmuka meidän puheeseemme tai kirjeeseemme väittää, että\nHerran päivä on jo käsillä.',178,NULL),
 (4009,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',3,'Älkää antako kenenkään\nmillään tavoin johtaa itseänne harhaan. Ennen tuota\npäivää näet tapahtuu uskosta luopuminen ja ilmaantuu\nitse laittomuus ihmishahmossa, kadotuksen ihminen.',178,NULL),
 (4010,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',4,'Hän, Vastustaja, korottaa itsensä kaiken jumalana\npidetyn yläpuolelle, asettuu itse istumaan Jumalan\ntemppeliin ja julistaa olevansa Jumala.',178,NULL),
 (4011,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',5,'Muistattehan, että puhuin teille tästä jo silloin\nkun olin luonanne.',178,NULL),
 (4012,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',6,'Te tiedätte, mikä häntä vielä\npidättää, niin että hän ilmestyy vasta kun hänen\nhetkensä koittaa. ',178,NULL),
 (4013,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',7,'Vääryyden salainen vaikutus on jo olemassa. Sen,\njoka sitä on vielä pidättämässä, on vain ensin\npoistuttava tieltä;',178,NULL),
 (4014,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',8,'silloin ilmestyy tuo vääryyden\nihminen, jonka Herra Jeesus on surmaava suunsa\nhenkäyksellä ja tuhoava tulemisensa kirkkaudella.',178,NULL),
 (4015,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',9,'Vääryyden ihminen tulee Saatanan vaikutuksesta\nsuurella voimalla, tehden petollisia tunnustekoja ja\nihmeitä.',178,NULL),
 (4016,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',10,'0Hänen vääryytensä pettää ne, jotka\njoutuvat kadotukseen, koska he eivät ole rakastaneet\ntotuutta, joka olisi pelastanut heidät.',178,NULL),
 (4017,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',11,'1Siksi\nJumala lähettää rajun eksytyksen, jotta he uskoisivat\nvalheeseen,',178,NULL),
 (4018,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',12,'2ja niin saavat tuomionsa kaikki ne,\njotka eivät ole uskoneet totuuteen vaan ovat valinneet\nvääryyden.   ',178,NULL),
 (4019,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',1,'Lopuksi vielä, veljet: rukoilkaa puolestamme, että\nHerran sana leviäisi nopeasti ja pääsisi muualla\nsamaan kunniaan kuin teidän keskuudessanne.',179,NULL),
 (4020,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',2,'Rukoilkaa myös, että varjeltuisimme pahoilta ja\nkelvottomilta ihmisiltä; kaikkihan eivät usko.',179,NULL),
 (4021,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',3,'Mutta Herra on uskollinen, ja hän on vahvistava\nteitä ja varjeleva teitä pahalta.',179,NULL),
 (4022,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',4,'Herraan\nturvautuen me luotamme siihen, että te nyt ja vastedes\ntoimitte niin kuin me käskemme.',179,NULL),
 (4023,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',5,'Herra ohjatkoon\nsydämenne rakastamaan Jumalaa ja odottamaan kestävinä\nKristusta.     ',179,NULL),
 (4024,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',17,'7Minä, Paavali, kirjoitan tähän tervehdyksen\nomakätisesti. Näin minä kirjoitan, ja tämä on\ntuntomerkkinä jokaisessa kirjeessäni.',179,NULL),
 (4025,'Anonymous','2008-10-17 16:34:07','Anonymous','2008-10-17 16:34:07',18,'8Herramme\nJeesuksen Kristuksen armo olkoon teidän kaikkien\nkanssa. ',179,NULL),
 (4026,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',1,'Paavali, Kristuksen Jeesuksen apostoli, jonka\nJumala, meidän pelastajamme, ja Kristus Jeesus, meidän\ntoivomme, ovat määränneet tähän virkaan,',180,NULL),
 (4027,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',2,'tervehtii\nTimoteusta, omaa hengellistä poikaansa. Isän Jumalan\nja Herramme Kristuksen Jeesuksen armo, laupeus ja\nrauha sinulle!   ',180,NULL),
 (4028,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',8,'Me tiedämme, että laki on hyvä, jos sitä käytetään\nlain tarkoituksen mukaisesti.',180,NULL),
 (4029,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',9,'Eihän lakia ole\nsäädetty kunnon ihmisten takia, vaan lain ja\njärjestyksen rikkojien, jumalattomien ja syntisten,\nrienaajien ja pyhänhäpäisijöiden, isän- ja\näidinmurhaajien, tappajien,',180,NULL),
 (4030,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',10,'0siveettömien, miesten\nkanssa makaavien miesten, ihmisten sieppaajien,\nvalehtelijoiden, valapattojen ja ylipäänsä kaikkien\nsellaisten takia, jotka toimivat vastoin tervettä\noppia.',180,NULL),
 (4031,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',11,'1Näin sanoo iäti ylistettävän Jumalan\nkirkkautta julistava evankeliumi, joka on minulle\nuskottu.   ',180,NULL),
 (4032,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',18,'8Tämän käskyn jätän täytettäväksesi, poikani\nTimoteus, niiden profeetallisten sanojen mukaisesti,\njotka sinulle kerran on lausuttu. Niitä muistaen\ntaistele jalo taistelu',180,NULL),
 (4033,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',19,'9ja säilytä usko ja hyvä\nomatunto! Jotkut ovat sen hylänneet ja ovat\nhaaksirikkoutuneet uskossaan.',180,NULL),
 (4034,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',20,'0Näin on käynyt\nHymenaiokselle ja Aleksandrokselle. Olen luovuttanut\nheidät Saatanan kuritettaviksi, jotta he oppisivat\nolemaan pilkkaamatta Jumalaa. ',180,NULL),
 (4035,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',1,'Kehotan ennen kaikkea anomaan, rukoilemaan,\npitämään esirukouksia ja kiittämään kaikkien ihmisten\npuolesta,',181,NULL),
 (4036,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',2,'kuninkaiden ja kaikkien vallanpitäjien\npuolesta, jotta saisimme viettää tyyntä ja rauhallista\nelämää, kaikin tavoin hurskaasti ja arvokkaasti.',181,NULL),
 (4037,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',3,'Tällainen rukous on oikea ja mieluisa Jumalalle,\nmeidän pelastajallemme,',181,NULL),
 (4038,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',4,'joka tahtoo, että kaikki\nihmiset pelastuisivat ja tulisivat tuntemaan totuuden.  ',181,NULL),
 (4039,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',7,'Tämän sanoman julistajaksi ja apostoliksi minut on\nasetettu. Puhun totta, en valehtele. Minut on pantu\nkansojen opettajaksi, opettamaan niille uskon\ntotuutta. ',181,NULL),
 (4040,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',8,'Tahdon, että kun miehet rukoilevat, he kohottavat\nkätensä rukoukseen puhtain mielin, ilman vihaa ja\nilman epäilyä.',181,NULL),
 (4041,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',9,'Samoin tahdon, että naisten\nkaunistuksena on hillitty esiintyminen, vaatimattomuus\nja säädyllisyys -- eivät tukkalaitteet, kultakorut,\nhelmet tai kalliit vaatteet,',181,NULL),
 (4042,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',10,'0vaan hyvät teot, niin\nkuin sopii naisille, jotka tunnustavat palvelevansa\nJumalaa.',181,NULL),
 (4043,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',11,'1Naisen tulee kuunnella opetusta, hiljaa\nja kuuliaisena.',181,NULL),
 (4044,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',12,'2Sitä en salli, että nainen\nopettaa, enkä sitä, että hän hallitsee miestä; hänen\non elettävä hiljaisesti.',181,NULL),
 (4045,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',13,'3Ensinhän luotiin Aadam ja\nsitten Eeva,',181,NULL),
 (4046,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',14,'4eikä petetyksi joutunut Aadam, vaan\nnainen antoi pettää itsensä ja rikkoi käskyn.',181,NULL),
 (4047,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',15,'5Lasten synnyttäjänä hän on kuitenkin pelastuva,\njos vain pysyy uskossa ja rakkaudessa ja viettää\nhillittyä, Jumalalle pyhitettyä elämää. ',181,NULL),
 (4048,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',1,'Tämä sana on varma: joka pyrkii seurakunnan\nkaitsijan virkaan, tahtoo jaloon työhön.',182,NULL),
 (4049,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',2,'Seurakunnan kaitsijan tulee olla moitteeton, yhden\nvaimon mies, raitis, harkitseva, rauhallinen,\nvieraanvarainen ja taitava opettamaan,',182,NULL),
 (4050,'Anonymous','2008-10-17 16:34:08','Anonymous','2008-10-17 16:34:08',3,'ei juomiseen\ntaipuvainen, ei väkivaltainen eikä rahanahne, vaan\nlempeä ja sopuisa.',182,NULL),
 (4051,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',4,'Hänen on pidettävä hyvää huolta\nperheestään, kasvatettava lapsensa tottelevaisiksi ja\nsaavutettava kaikkien kunnioitus.',182,NULL),
 (4052,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',5,'Jos joku ei osaa\npitää huolta omasta perheestään, kuinka hän voisi\nhuolehtia Jumalan seurakunnasta?',182,NULL),
 (4053,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',6,'Hän ei saa olla\nvastakääntynyt, jotta hän ei ylpistyisi eikä joutuisi\ntuomittavaksi Paholaisen kanssa.',182,NULL),
 (4054,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',7,'Hänen on\nnautittava myös ulkopuolisten arvonantoa, jottei\nhänestä puhuttaisi pahaa ja jottei hän lankeaisi\nPaholaisen virittämään ansaan.      ',182,NULL),
 (4055,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',1,'Pyhä Henki sanoo selvästi, että viimeisinä aikoina\njotkut luopuvat uskosta ja alkavat seurata eksyttäviä\nhenkiä ja pahojen henkien opetuksia.',183,NULL),
 (4056,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',2,'He luottavat\ntekopyhiin valehtelijoihin, joiden omassatunnossa on\npolttomerkki.',183,NULL),
 (4057,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',3,'Nämä kieltävät menemästä naimisiin ja\nsyömästä ruokia, jotka Jumala on luonut sitä varten,\nettä ne, jotka tuntevat ja uskovat totuuden,\nnauttisivat niitä ja kiittäisivät Jumalaa.',183,NULL),
 (4058,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',4,'Kaikki,\nminkä Jumala on luonut, on hyvää, eikä siitä tarvitse\nhylätä mitään, kun se otetaan kiittäen vastaan.',183,NULL),
 (4059,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',5,'Jumalan sana ja rukous pyhittävät sen.   ',183,NULL),
 (4060,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',11,'1Neuvo ja opeta tällä tavoin.',183,NULL),
 (4061,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',12,'2Kenenkään ei pidä\nväheksyä sinua nuoruutesi vuoksi. Näytä sinä uskoville\nhyvää esimerkkiä puheissasi ja elämäntavoissasi,\nrakkaudessa, uskossa ja puhtaudessa.',183,NULL),
 (4062,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',13,'3Lue\nseurakunnalle pyhiä kirjoituksia, kehota ja opeta\nahkerasti siihen saakka, kun tulen.',183,NULL),
 (4063,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',14,'4Älä lyö laimin\narmolahjaa, jonka sait silloin, kun vanhimmat\nprofeettain sanojen perusteella panivat kätensä sinun\npäällesi.',183,NULL),
 (4064,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',15,'5Huolehdi tästä kaikesta ja toimi näin,\njotta kaikki näkisivät sinun edistymisesi.',183,NULL),
 (4065,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',16,'6Valvo\nitseäsi ja opetustasi, pidä kiinni näistä ohjeista!\nSilloin pelastat sekä itsesi että ne, jotka kuulevat\nsinua. ',183,NULL),
 (4066,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',1,'Älä nuhtele vanhaa miestä ankarasti, vaan neuvo\nhäntä kuin omaa isääsi, nuorempia miehiä kuin\nveljiäsi,',184,NULL),
 (4067,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',2,'vanhoja naisia kuin äitiäsi, nuorempia\nnaisia kuin sisariasi, siveästi ja puhtaasti. ',184,NULL),
 (4068,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',3,'Kunnioita ja tue leskivaimoja, jotka todella ovat\nvailla huoltajaa.',184,NULL),
 (4069,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',4,'Mutta jos leskellä on lapsia tai\nlastenlapsia, on ensi sijassa näiden opittava\ntäyttämään velvollisuutensa perheenjäseniään kohtaan\nja korvattava vanhempiensa vaivat, sillä se on Jumalan\nmielen mukaista.',184,NULL),
 (4070,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',5,'Aivan yksin jäänyt leskivaimo\npanee toivonsa Jumalaan ja rukoilee hellittämättä yötä\npäivää.',184,NULL),
 (4071,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',6,'Nautinnonhaluinen taas on jo eläessään\nkuollut.',184,NULL),
 (4072,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',7,'Muistuta leskiä tästäkin, jotta he\neläisivät nuhteettomasti.',184,NULL),
 (4073,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',8,'Mutta se, joka ei\nhuolehdi omaisistaan ja kaikkein lähimmistään, on\nkieltänyt uskonsa ja on epäuskoista pahempi. ',184,NULL),
 (4074,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',9,'Leskien luetteloon kelpaa vain sellainen, joka on\nkuusikymmentä vuotta täyttänyt ja joka on ollut vain\nyhden miehen vaimo.',184,NULL),
 (4075,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',10,'0Hänen on oltava tunnettu\nhyvistä teoista, siitä että hän on kasvattanut lapsia,\nmajoittanut kotiinsa matkalaisia, pessyt uskovien\nvieraidensa jalat, auttanut vaikeuksissa olevia ja\nkaikin tavoin harrastanut hyvää.',184,NULL),
 (4076,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',11,'1Nuorempia leskiä\nälä ota mukaan. Kun intohimot heräävät, he\nvieraantuvat Kristuksesta ja tahtovat mennä naimisiin.',184,NULL),
 (4077,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',12,'2He joutuvat tuomion alaisiksi, koska ovat\nrikkoneet ensimmäisen uskollisuudenlupauksensa.',184,NULL),
 (4078,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',13,'3Kierrellessään talosta taloon he oppivat\nlaiskottelemaan ja lisäksi juoruilemaan ja\nsekaantumaan muiden asioihin, puhumaan sellaista mitä\nei pitäisi.',184,NULL),
 (4079,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',14,'4Siksi tahdon, että nuoret lesket\nmenevät naimisiin, synnyttävät lapsia ja hoitavat\nkotiaan, niin etteivät he anna vastustajillemme\naihetta solvaamiseen.',184,NULL),
 (4080,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',15,'5Jotkut näet ovat jo\nkääntyneet seuraamaan Saatanaa.',184,NULL),
 (4081,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',16,'6Jos jollakin\nuskovalla naisella on kodissaan leskiä, hänen tulee\nhuolehtia heistä. Heillä ei pidä rasittaa seurakuntaa,\njotta se voisi pitää huolta yksin jääneistä leskistä.   ',184,NULL),
 (4082,'Anonymous','2008-10-17 16:34:09','Anonymous','2008-10-17 16:34:09',24,'4Joidenkin ihmisten synnit ovat kaikkien nähtävissä\nja saavat tuomionsa aikaisemmin, toisten vasta\nmyöhemmin.',184,NULL),
 (4083,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',25,'5Samoin toiset hyvät teot ovat\nnäkyvissä, mutta nekään, jotka eivät näy, eivät voi\npysyä salassa. ',184,NULL),
 (4084,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',1,'Orjan asemassa olevien tulee osoittaa isännilleen\nkaikkea kunnioitusta, jotteivät Jumalan nimi ja meidän\nopetuksemme joutuisi huonoon huutoon.',185,NULL),
 (4085,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',2,'Ei orjien\npidä osoittaa uskoville isännilleen vähäisempää\nkunnioitusta sen vuoksi, että he ovat uskonveljiä.\nHeidän on päinvastoin oltava sitäkin alttiimpia\npalvelemaan, koska nämä tekevät hyvää ja ovat uskovia\nja rakkaita veljiä.       ',185,NULL),
 (4086,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',17,'7Varoita niitä, jotka tässä maailmassa ovat\nrikkaita, etteivät he ylpeilisi eivätkä panisi\ntoivoaan epävarmaan rikkauteen, vaan Jumalaan, joka\nantaa kaikkea runsain mitoin nautittavaksemme.',185,NULL),
 (4087,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',18,'8Kehota heitä tekemään hyvää, keräämään\nrikkaudekseen hyviä tekoja ja jakamaan anteliaasti\nomastaan muille.',185,NULL),
 (4088,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',19,'9Näin he kokoavat itselleen\naarteen, hyvän perustuksen tulevaisuutta varten, niin\nettä voittavat omakseen todellisen elämän. ',185,NULL),
 (4089,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',20,'0Pidä tallessa, Timoteus, se mikä on uskottu sinun\nhaltuusi! Torju jumalattomat tyhjät puheet ja\nvastaväitteet, joita tiedon nimellä kulkeva valhetieto\nkeksii.',185,NULL),
 (4090,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',21,'1Muutamat ovat ryhtyneet sen tunnustajiksi\nja ovat eksyneet pois uskosta. Armo teille! ',185,NULL),
 (4091,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',1,'Paavali, Kristuksen Jeesuksen apostoli Jumalan\ntahdosta, sen lupauksen mukaisesti, jonka olemme\nsaaneet elämästä Kristuksen Jeesuksen yhteydessä,',186,NULL),
 (4092,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',2,'tervehtii rakasta poikaansa Timoteusta. Isän\nJumalan ja Herramme Kristuksen Jeesuksen armo, laupeus\nja rauha sinulle!      ',186,NULL),
 (4093,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',11,'1Sen kuuluttajaksi, apostoliksi ja opettajaksi\nminut on asetettu.',186,NULL),
 (4094,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',12,'2Siksi minä saan kärsiä tätä\nkaikkea. En silti häpeä, sillä tiedän, keneen uskon,\nja olen varma siitä, että hän kykenee varjelemaan sen,\nmikä on minun haltuuni uskottu, aina tulemisensa\npäivään asti.',186,NULL),
 (4095,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',13,'3Pidä esikuvanasi niitä terveitä\nopetuksia, jotka olet minulta kuullut, ja säilytä se\nusko ja rakkaus, jonka Kristus Jeesus saa aikaan.',186,NULL),
 (4096,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',14,'4Pidä meissä asuvan Pyhän Hengen avulla tallessa se\nhyvä, mikä on uskottu haltuusi. ',186,NULL),
 (4097,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',15,'5Niin kuin tiedät, Aasian maakunnassa kaikki ovat\nluopuneet minusta, heidän joukossaan Fygelos ja\nHermogenes.',186,NULL),
 (4098,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',16,'6Herra osoittakoon laupeutta\nOnesiforoksen perheelle, koska hän on usein minua\nrohkaissut eikä ole hävennyt minua, vaikka olen\nvankina.',186,NULL),
 (4099,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',17,'7Roomaan tultuaan hän päinvastoin ryhtyi\ntarmokkaasti etsimään, kunnes löysi minut.',186,NULL),
 (4100,'Anonymous','2008-10-17 16:34:10','Anonymous','2008-10-17 16:34:10',18,'8Osoittakoon Herra hänelle laupeutta tulemisensa\npäivänä! Ja kuinka paljon hän auttoi minua\nEfesoksessa, sen tiedät sinä parhaiten. ',186,NULL),
 (4101,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',1,'Vahvistakoon siis Kristuksen Jeesuksen armo sinua,\npoikani!',187,NULL),
 (4102,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',2,'Kaikki, mitä olet monien todistajien läsnä\nollessa minulta kuullut, sinun tulee uskoa\nluotettaville ihmisille, jotka pystyvät myös\nopettamaan muita.',187,NULL),
 (4103,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',3,'Kärsi sinäkin vaivaa niin kuin\nKristuksen Jeesuksen kelpo sotilas.',187,NULL),
 (4104,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',4,'Ei kukaan\nsotilaana palveleva sekaannu jokapäiväisen elämän\ntoimiin, jos haluaa, että päällikkö on häneen\ntyytyväinen.',187,NULL),
 (4105,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',5,'Ei myöskään urheilija saa\nvoitonseppelettä, ellei hän kilpaile sääntöjen\nmukaisesti.',187,NULL),
 (4106,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',6,'Maamiehen, joka raskaan työn tekee,\ntulee ensimmäisenä saada osansa sadosta.',187,NULL),
 (4107,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',7,'Mieti\nsanojani! Herra kyllä antaa sinulle ymmärrystä\nkaikkeen. ',187,NULL),
 (4108,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',8,'Muista, että Jeesus Kristus, Daavidin jälkeläinen,\non herätetty kuolleista, niin kuin minä julistan.',187,NULL),
 (4109,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',9,'Hänen vuokseen minä kärsin, olen vieläpä kahleissa\nkuin rikollinen. Mutta Jumalan sana ei ole kahleissa.',187,NULL),
 (4110,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',10,'0Siksi kestän mitä tahansa Jumalan valittujen\ntähden, jotta hekin saavuttaisivat pelastuksen, jonka\nKristus Jeesus on hankkinut, ja sen mukana ikuisen\nkirkkauden.',187,NULL),
 (4111,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',11,'1Tämä sana on varma:    ',187,NULL),
 (4112,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',20,'0Suuressa taloudessa ei ole vain kultaisia ja\nhopeisia astioita, vaan myös puusta ja savesta\ntehtyjä. Toiset on tarkoitettu arvokkaaseen, toiset\narkiseen käyttöön.',187,NULL),
 (4113,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',21,'1Siitä, joka puhdistaa itsensä\nkaikesta tuollaisesta, tulee arvokkaaseen käyttöön\nsopiva astia, pyhitetty, isännälleen hyödyllinen ja\nkaikkiin hyviin tarkoituksiin käyttökelpoinen. ',187,NULL),
 (4114,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',22,'2Karta nuoruuden kiihkoa ja pyri tasapuolisuuteen,\nuskoon, rakkauteen ja rauhaan niiden kanssa, jotka\nvilpittömin sydämin pyytävät avukseen Herraa.',187,NULL),
 (4115,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',23,'3Pysy\nerossa typeristä ja asiattomista väittelyistä; sinähän\ntiedät, että niistä syntyy riitoja.',187,NULL),
 (4116,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',24,'4Herran\npalvelija ei saa riidellä, vaan hänen on oltava\nystävällinen kaikille, taitava opettamaan ja\nkärsivällinen,',187,NULL),
 (4117,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',25,'5niin että hän lempeästi ojentaa\nvastustelevia. Ehkäpä Jumala antaa heidän mielensä\nmuuttua, niin että he tulevat tuntemaan totuuden',187,NULL),
 (4118,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',26,'6ja selviävät Paholaisen ansasta, johon tämä on\nheidät vanginnut pakottaakseen heidät tahtoonsa. ',187,NULL),
 (4119,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',1,'Sinun on tiedettävä, että viimeisinä päivinä\nkoittavat vaikeat ajat.',188,NULL),
 (4120,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',2,'Silloin ihmiset rakastavat\nvain itseään ja rahaa, he ovat rehenteleviä ja\npöyhkeitä, he herjaavat ja ovat vanhemmilleen\ntottelemattomia. He ovat kiittämättömiä, jumalattomia,',188,NULL),
 (4121,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',3,'rakkaudettomia, leppymättömiä, panettelevia,\nväkivaltaisia ja raakoja, kaiken hyvän vihollisia,',188,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (4122,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',4,'petollisia, häikäilemättömiä ja järjettömiä. He\nrakastavat enemmän nautintoja kuin Jumalaa,',188,NULL),
 (4123,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',5,'he ovat\nulkonaisesti hurskaita mutta kieltävät uskon voiman.\nKarta sellaisia!',188,NULL),
 (4124,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',6,'Nämä näet tunkeutuvat koteihin ja\nkietovat valtaansa syntien rasittamia, monenlaisten\noikkujensa riepottelemia naisparkoja,',188,NULL),
 (4125,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',7,'jotka ovat\naina alttiita uusille opetuksille mutta eivät voi\nkoskaan päästä selville totuudesta.',188,NULL),
 (4126,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',8,'Niin kuin\nJannes ja Jambres*vastustivat Moosesta, niin\ntuollaiset villitsijät vastustavat totuutta. He ovat\nmenettäneet järkensä, ja heidän uskonsa on kelvoton.',188,NULL),
 (4127,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',9,'Mutta kauan he eivät menesty, sillä heidän\njärjettömyytensä paljastuu pian kaikille, kuten noille\nkahdellekin kävi.   ',188,NULL),
 (4128,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',1,'Minä vannotan sinua Jumalan ja Kristuksen Jeesuksen\nnimessä, hänen, joka on tuomitseva elävät ja kuolleet,\nja hänen ilmestymisensä ja hänen valtakuntansa kautta:',189,NULL),
 (4129,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',2,'julista sanaa, astu esiin sopivaan ja sopimattomaan\naikaan, nuhtele, moiti ja kehota, aina kärsivällisesti\nopettaen.',189,NULL),
 (4130,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',3,'Tulee näet aika, jolloin ihmiset eivät\nsiedä kuulla tervettä oppia vaan haalivat itselleen\nhalunsa mukaisia opettajia kuullakseen sitä mitä\nkulloinkin mieli tekee.',189,NULL),
 (4131,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',4,'He tukkivat korvansa\ntotuudelta ja kääntyvät kuuntelemaan taruja.',189,NULL),
 (4132,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',5,'Mutta\npysy sinä järkevänä kaikissa tilanteissa, kestä\nvaivat, julista evankeliumia ja hoida virkasi\ntehtävät. ',189,NULL),
 (4133,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',6,'Minut itseni uhrataan jo pian, lähtöni hetki on\ntullut.',189,NULL),
 (4134,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',7,'Olen kilpaillut hyvän kilpailun, olen\njuossut perille ja säilyttänyt uskoni.',189,NULL),
 (4135,'Anonymous','2008-10-17 16:34:11','Anonymous','2008-10-17 16:34:11',8,'Minua\nodottaa nyt vanhurskauden seppele, jonka Herra,\noikeudenmukainen tuomari, on antava minulle\ntulemisensa päivänä, eikä vain minulle vaan kaikille,\njotka hartaasti odottavat hänen ilmestymistään.   ',189,NULL),
 (4136,'Anonymous','2008-10-17 16:34:12','Anonymous','2008-10-17 16:34:12',16,'6Kun ensimmäisen kerran puolustauduin oikeudessa,\nei kukaan tullut avukseni vaan kaikki jättivät minut\nyksin. Älköön sitä luettako heille viaksi!',189,NULL),
 (4137,'Anonymous','2008-10-17 16:34:12','Anonymous','2008-10-17 16:34:12',17,'7Herra\nkuitenkin auttoi minua ja antoi minulle voimaa, jotta\nvoisin saattaa julistustyöni päätökseen ja\nevankeliumin kaikkien kansojen kuuluville, ja näin\npelastuin leijonan kidasta.',189,NULL),
 (4138,'Anonymous','2008-10-17 16:34:12','Anonymous','2008-10-17 16:34:12',18,'8Herra pelastaa\nvastedeskin minut kaikesta pahasta ja vie minut\nturvaan taivaalliseen valtakuntaansa. Hänen on kunnia\naina ja ikuisesti. Aamen.   ',189,NULL),
 (4139,'Anonymous','2008-10-17 16:34:12','Anonymous','2008-10-17 16:34:12',17,'7Jos siis pidät minua kumppaninasi, ota hänet\nvastaan niin kuin minut.',190,NULL),
 (4140,'Anonymous','2008-10-17 16:34:12','Anonymous','2008-10-17 16:34:12',18,'8Jos hän on aiheuttanut\nsinulle vahinkoa tai on sinulle jotakin velkaa, pane\nse minun laskuuni.',190,NULL),
 (4141,'Anonymous','2008-10-17 16:34:12','Anonymous','2008-10-17 16:34:12',19,'9Minä Paavali kirjoitan tämän\nomakätisesti: minä maksan sen. Voisin tosin sanoa,\nettä sinä puolestasi olet minulle velkaa oman\nitsesikin.',190,NULL),
 (4142,'Anonymous','2008-10-17 16:34:12','Anonymous','2008-10-17 16:34:12',20,'0Niin, veljeni, kunpa tekisit minulle\ntämän palveluksen Herramme takia. Ilahduta minun\nmieltäni Kristuksen tähden! ',190,NULL),
 (4143,'Anonymous','2008-10-17 16:34:12','Anonymous','2008-10-17 16:34:12',21,'1Kirjoitan tämän luottaen siihen, että täytät\npyyntöni, ja tiedän, että teet enemmänkin kuin pyydän.',190,NULL),
 (4144,'Anonymous','2008-10-17 16:34:12','Anonymous','2008-10-17 16:34:12',22,'2Järjestä samalla minulle majapaikka, sillä toivon,\nettä rukoustenne tähden saatte minut luoksenne.    ',190,NULL),
 (4145,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',1,'Monet kerrat ja monin tavoin Jumala muinoin puhui\nisillemme profeettojen suulla,',191,NULL),
 (4146,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',2,'mutta näinä\nviimeisinä aikoina hän on puhunut meille Pojassaan,\njonka hän on pannut kaiken perilliseksi ja jonka\nvälityksellä hän myös on luonut maailmat.',191,NULL),
 (4147,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',3,'Poika on\nJumalan sädehtivä kirkkaus, hänen olemuksensa kuva, ja\nhän ylläpitää kaikkea olemassa olevaa sanansa\nvoimalla. Toimitettuaan puhdistuksen synneistä hän on\nasettunut korkeuksissa istuimelleen Majesteetin\noikealle puolelle.',191,NULL),
 (4148,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',4,'Näin hänestä on tullut enkeleitä\nsuurempi, yhtä lailla kuin hänen saamansa nimi on\nenkelien nimiä ylhäisempi.    Ei myöskään näin:  ',191,NULL),
 (4149,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',6,'Kun Jumala jälleen tuo Esikoisensa maailmaan, hän\nsanoo:  ',191,NULL),
 (4150,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',7,'Enkeleistä hän sanoo näin:  ',191,NULL),
 (4151,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',8,'Mutta Pojastaan hän sanoo:  ',191,NULL),
 (4152,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',10,'0Ja myös näin:  ',191,NULL),
 (4153,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',13,'3Yhdellekään enkelille ei Jumala milloinkaan ole\nsanonut:   ',191,NULL),
 (4154,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',14,'4Eivätkö enkelit ole palvelevia henkiä? Heidät\nlähetetään palvelemaan niitä, jotka saavat osakseen\nautuuden. ',191,NULL),
 (4155,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',1,'Sen tähden meidän on tarkoin ja tunnollisesti\npidettävä mielessämme se, minkä olemme kuulleet,\nettemme ajautuisi virran vieminä harhaan.',192,NULL),
 (4156,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',2,'Johan\nsekin sanoma, jonka enkelit toivat, osoittautui\npaikkansa pitäväksi, ja jokainen rikkomus ja\ntottelemattomuus sai ansaitsemansa palkan.',192,NULL),
 (4157,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',3,'Kuinka\nsitten me voisimme välttää rangaistuksen, jos emme\npidä arvossa sitä suurta pelastusta, jota itse Herra\nensimmäisenä julisti? Ne, jotka kuulivat hänen\njulistuksensa, ovat sen todistuksellaan vahvistaneet\nja välittäneet meille,',192,NULL),
 (4158,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',4,'ja itse Jumala vahvisti\nheidän todistuksensa: hän antoi heidän tehdä\ntunnustekoja ja ihmeitä, monia voimallisia tekoja, ja\njakoi Pyhän Hengen lahjoja tahtonsa mukaan.     Jumala siis antoi ihmisen valtaan kaiken, hän ei\njättänyt mitään tälle alistamatta. Vielä tosin emme\nvoi havaita, että kaikki olisi hänen vallassaan.',192,NULL),
 (4159,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',9,'Sen kuitenkin näemme, että tuo \"lyhyeksi aikaa\nenkeleitä alemmaksi asetettu\", Jeesus, on kuoleman\ntuskat kärsittyään \"seppelöity kirkkaudella ja\nkunnialla\". Armollisen Jumalan tahto näet oli, että\nJeesuksen oli kärsittävä kuolema jokaisen ihmisen\npuolesta. ',192,NULL),
 (4160,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',10,'0Hän, joka on kaiken luoja ja perusta, tahtoi tuoda\npaljon lapsia kirkkauteen, ja siksi hänen tuli tehdä\nheidän pelastajansa ja perilleviejänsä kärsimysten\nkautta täydelliseksi.',192,NULL),
 (4161,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',11,'1Pyhittäjällä ja\npyhitettävillä on kaikilla sama isä; siksi ei\npyhittäjä häpeäkään kutsua näitä veljikseen:  ',192,NULL),
 (4162,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',13,'3Hän sanoo myös:  ja edelleen:   ',192,NULL),
 (4163,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',14,'4Nämä lapset ovat ihmisiä, lihaa ja verta, ja siksi\nhänkin tuli ihmiseksi, heidän kaltaisekseen. Siten hän\nkykeni kuolemallaan riistämään vallan kuoleman\nvaltiaalta, Saatanalta,',192,NULL),
 (4164,'Anonymous','2008-10-17 16:34:13','Anonymous','2008-10-17 16:34:13',15,'5ja päästämään vapaiksi\nkaikki, jotka kuoleman pelosta olivat koko ikänsä\nolleet orjina. ',192,NULL),
 (4165,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',16,'6Hän ei siis ota suojelukseensa enkeleitä -- hän\nottaa suojelukseensa Abrahamin suvun.',192,NULL),
 (4166,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',17,'7Niinpä hänen\noli tultava joka suhteessa veljiensä kaltaiseksi,\njotta hänestä tulisi armahtava ja uskollinen ylipappi\nja hän voisi Jumalan edessä sovittaa kansansa synnit.',192,NULL),
 (4167,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',18,'8Koska hän on itse käynyt läpi kärsimykset ja\nkiusaukset, hän kykenee auttamaan niitä, joita\nkoetellaan. ',192,NULL),
 (4168,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',1,'Tämän tähden, te pyhät veljet, te, jotka olette\nsaaneet taivaasta kutsun, kiinnittäkää katseenne\nJeesukseen, joka on tunnustuksemme apostoli ja\nylipappi.',193,NULL),
 (4169,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',2,'Hän on herransa luottamuksen arvoinen,\nniin kuin Mooses oli \"kaikissa hänen huoneensa\nasioissa\",',193,NULL),
 (4170,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',3,'mutta hänet on katsottu suuremman\nkunnian arvoiseksi, yhtä paljon suuremman kuin\nrakentajan kunnia on rakennuksen kunniaa suurempi.',193,NULL),
 (4171,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',4,'Jokainen talo on jonkun rakentama, mutta Jumala on\nrakentanut kaiken.',193,NULL),
 (4172,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',5,'Mooses oli kyllä uskottu\n\"kaikissa hänen huoneensa asioissa\", mutta silti vain\npalvelija, elävä todiste siitä, mitä Jumala oli\nmyöhemmin puhuva.',193,NULL),
 (4173,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',6,'Kristus sen sijaan on Poika,\njonka haltuun on uskottu Jumalan koko rakennus. Tämä\nrakennus olemme me, kunhan loppuun saakka säilytämme\nrohkeutemme ja luottavaisin ja iloisin mielin tuomme\njulki toivomme.     ',193,NULL),
 (4174,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',12,'2Varokaa siis, veljet, ettei kukaan teistä ole\nsydämessään paha ja epäuskoinen ja näin luovu elävästä\nJumalasta.',193,NULL),
 (4175,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',13,'3Rohkaiskaa toinen toistanne joka päivä,\nniin kauan kuin tuo sana \"tänä päivänä\" on voimassa,\nettei kukaan teistä lankeaisi synnin viettelyksiin ja\npaatuisi.',193,NULL),
 (4176,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',14,'4Mehän olemme osalliset Kristuksesta, kun\nvain loppuun asti pidämme kiinni siitä\ntodellisuudesta, jonka yhteyteen jo alussa olemme\npäässeet. ',193,NULL),
 (4177,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',15,'5Kirjoituksissa siis sanotaan:  ',193,NULL),
 (4178,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',16,'6Ketkä tekivät näin: kuulivat mutta nousivat\nkapinaan? Kaikki ne, jotka Mooseksen johtamina olivat\nlähteneet pois Egyptistä.',193,NULL),
 (4179,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',17,'7Keille Jumala oli\nvihoissaan neljäkymmentä vuotta? Niille, jotka\nlankesivat syntiin ja joiden ruumiit sitten jäivät\nautiomaahan.',193,NULL),
 (4180,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',18,'8Keille hän vannoi, etteivät he pääse\nhänen lepopaikkaansa? Niille, jotka eivät totelleet.',193,NULL),
 (4181,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',19,'9Huomaamme näin, että perillepääsyn esti epäusko. ',193,NULL),
 (4182,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',1,'Olkaamme siis varuillamme. Jumalan lupaus, että\npääsemme hänen lepopaikkaansa, on vielä täyttymättä,\neikä yksikään teistä saa jäädä taipaleelle.',194,NULL),
 (4183,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',2,'Mehän\nolemme kuulleet hyvän sanoman aivan niin kuin nuo\nentisajan ihmiset. Heille ei kuitenkaan ollut mitään\nhyötyä sanasta, jonka he kuulivat, koska he eivät sitä\nuskoneet eikä se näin sulautunut heihin.',194,NULL),
 (4184,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',3,'Sen sijaan\nme, jotka uskoimme, pääsemme perille luvattuun levon\nmaahan. Jumala on siis sanonut:  Näin hän sanoi, vaikka hänen työnsä ovat olleet\nvalmiina maailman luomisesta asti.',194,NULL),
 (4185,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',4,'Kirjoituksissahan on seitsemännestä päivästä\nsanottu: \"Seitsemäntenä päivänä Jumala lepäsi kaikesta\ntyöstään.\"',194,NULL),
 (4186,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',5,'Mutta tuossa toisessa kohdassa sanotaan:\n\"Ikinä he eivät pääse minun lepopaikkaani.\" ',194,NULL),
 (4187,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',6,'Koska siis on varmaa, että jotkut pääsevät sinne,\nja koska ne, jotka muinoin kuulivat hyvän sanoman,\neivät niskoittelunsa vuoksi sinne päässeet,',194,NULL),
 (4188,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',7,'Jumala\non asettanut uuden määräpäivän, \"tämän päivän\". Hänhän\non paljon myöhemmin Daavidin suulla lausunut edellä\nmainitut sanat:   ',194,NULL),
 (4189,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',8,'Jos jo Joosua olisi vienyt kansan levon maahan, ei\nJumala puhuisi toisesta, myöhemmin tulevasta päivästä.',194,NULL),
 (4190,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',9,'Jumalan kansalla on siis yhä sapattijuhla edessään.',194,NULL),
 (4191,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',10,'0Se, joka pääsee levon maahan, saa levätä kaikkien\ntöidensä jälkeen niin kuin Jumalakin työnsä tehtyään.',194,NULL),
 (4192,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',11,'1Pyrkikäämme siis kaikin voimin tuohon lepoon,\nettei yksikään lankeaisi seuraamaan noiden\nniskoittelijoiden esimerkkiä ja sen vuoksi jäisi\ntaipaleelle. ',194,NULL),
 (4193,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',12,'2Jumalan sana on elävä ja väkevä. Se on terävämpi\nkuin mikään kaksiteräinen miekka, se iskee syvään ja\nviiltää halki sielun ja hengen, nivelet ja luiden\nytimet, se paljastaa sisimmät aikeemme ja ajatuksemme.',194,NULL),
 (4194,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',13,'3Jumalalta ei voi salata mitään. Kaikki, mikä on\nluotu, on avointa ja alastonta hänelle, jolle meidän\non tehtävä tili.   ',194,NULL),
 (4195,'Anonymous','2008-10-17 16:34:14','Anonymous','2008-10-17 16:34:14',1,'Jokainen ylipappi valitaan ihmisten joukosta, ja\nihmisiä hänet myös asetetaan edustamaan, tuomaan\nJumalalle lahjoja ja uhreja syntien sovittamiseksi.',195,NULL),
 (4196,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',2,'Koska hän itsekin on heikko, hän osaa kohdella\nymmärtävästi tietämättömiä ja erehtyviä,',195,NULL),
 (4197,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',3,'ja\nheikkoutensa tähden hänellä on myös velvollisuus\nuhrata syntiuhreja yhtä lailla itsensä kuin kansan\npuolesta.',195,NULL),
 (4198,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',4,'Kukaan ei itse ota tätä arvoa itselleen,\nvaan ylipapin kutsuu Jumala, joka kutsui jo Aaronin.',195,NULL),
 (4199,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',5,'Siten ei Kristuskaan itse korottanut itseään\nylipapin arvoon, vaan hänet korotti se, joka sanoi\nhänelle:  ',195,NULL),
 (4200,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',6,'ja toisessa paikassa:   ',195,NULL),
 (4201,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',7,'Ihmiselämänsä päivinä Jeesus ääneen itkien rukoili\nja huusi avukseen häntä, jolla oli valta pelastaa\nhänet kuolemasta, ja hänen uhrirukouksensa kuultiin,\nkoska hän taipui Jumalan tahtoon.',195,NULL),
 (4202,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',8,'Vaikka hän oli\nPoika, hän joutui kärsimyksistä oppimaan, mitä on\nkuuliaisuus.',195,NULL),
 (4203,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',9,'Kun hän oli saavuttanut\ntäydellisyyden, hänestä tuli iankaikkisen pelastuksen\ntuoja, kaikkien niiden pelastaja, jotka ovat hänelle\nkuuliaisia.',195,NULL),
 (4204,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',10,'0Hänestä tuli Jumalan asettama\nylipappi, jonka pappeus on Melkisedekin pappeutta.   ',195,NULL),
 (4205,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',1,'Älkäämme siis enää viipykö Kristuksen opin\nalkeissa, vaan edetkäämme täyteen tietoon. Emme voi\nuudestaan laskea perustusta ja opettaa sellaisia\nasioita kuin parannus kuolemaan johtavista teoista,\nusko Jumalaan,',196,NULL),
 (4206,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',2,'oppi kasteista ja puhdistusmenoista\nsekä kätten päällepanemisesta, kuolleiden ylösnousemus\nja iankaikkinen tuomio.',196,NULL),
 (4207,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',3,'Menkäämme eteenpäin, jos\nJumala suo. ',196,NULL),
 (4208,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',4,'Mahdotontahan on auttaa niitä, jotka kerran ovat\npäässeet valoon, maistaneet taivaan lahjaa ja tulleet\nosallisiksi Pyhästä Hengestä,',196,NULL),
 (4209,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',5,'nauttineet Jumalan\nhyvää sanaa ja kokeneet tulevan maailman voimia',196,NULL),
 (4210,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',6,'mutta luopuneet uskostaan. Ei heitä voi\ntoistamiseen johdattaa kääntymykseen -- niitä, jotka\nnyt itse ristiinnaulitsevat Jumalan Pojan ja nostavat\nhänet kaikkien pilkattavaksi! ',196,NULL),
 (4211,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',7,'Jos maa imee sisäänsä sateen, joka sitä usein\nkastelee, ja tuottaa sadon viljelijöilleen, se saa\nosakseen Jumalan siunauksen.',196,NULL),
 (4212,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',8,'Mutta jos se kasvaa\nohdaketta ja orjantappuraa, se ei kelpaa mihinkään.\nSitä uhkaa kirous, ja niin se lopulta poltetaan. ',196,NULL),
 (4213,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',9,'Te, rakkaat ystävät, olette kuitenkin tuo parempi\nmaa, ja teitä odottaa pelastus. Olemme siitä varmoja,\nvaikka puhunkin näin.',196,NULL),
 (4214,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',10,'0Eihän Jumala ole\nepäoikeudenmukainen, ei hän unohda teidän tekojanne\neikä rakkauttanne, jota olette osoittaneet hänen\nnimeään kohtaan, kun olette palvelleet hänen pyhiään\nja yhä palvelette heitä.',196,NULL),
 (4215,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',11,'1Hartaasti vain odotamme,\nettä te itse kukin loppuun asti yhtä innokkaasti\nhaluatte kokea yhteisen toivomme täyttymisen.',196,NULL),
 (4216,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',12,'2Älkää päästäkö itseänne veltostumaan, vaan\nseuratkaa niiden esimerkkiä, jotka ovat uskoneet ja\nkärsivällisesti odottaneet ja sen tähden saavat\nomakseen sen, minkä Jumala on luvannut.   ',196,NULL),
 (4217,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',16,'6Ihmiset vannovat jonkun itseään suuremman nimeen.\nVala merkitsee heille asian vahvistusta, se lopettaa\nkaikki vastaväitteet.',196,NULL),
 (4218,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',17,'7Siksi myös Jumala vannoi\nlupauksensa takeeksi vielä valan. Hän tahtoi tällä\nerityisellä tavalla osoittaa niille, joita lupaus\nkoskee, että hänen päätöksensä ei muutu.',196,NULL),
 (4219,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',18,'8Noilla\nkahdella järkähtämättömällä sanallaan, joissa hän,\nJumala, ei voi valehdella, hän tahtoi rohkaista meitä,\nhänen turviinsa paenneita, ja kannustaa pitämään\nkiinni toivosta, joka on edessämme.',196,NULL),
 (4220,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',19,'9Se toivo on\nelämämme ankkuri, luja ja varma. Se ulottuu väliverhon\ntuolle puolen.',196,NULL),
 (4221,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',20,'0Sinne Jeesus meidän\nedelläkävijänämme meni, kun hän oli tullut ylipapiksi,\njonka pappeus on ikuista, Melkisedekin pappeutta. ',196,NULL),
 (4222,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',1,'Tämä Melkisedek oli Salemin kuningas ja Korkeimman\nJumalan pappi. Kun Abraham oli paluumatkalla lyötyään\nkuninkaat, Melkisedek tuli häntä vastaan ja siunasi\nhänet,',197,NULL),
 (4223,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',2,'ja Abraham antoi hänelle kymmenykset\nkaikesta saaliistaan. Hän on ensinnäkin \"vanhurskauden\nkuningas\", mitä hänen nimensä merkitsee, ja lisäksi\nSalemin kuningas eli \"rauhan kuningas\".',197,NULL),
 (4224,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',3,'Hänellä ei\nole isää, ei äitiä eikä sukuluetteloa, hänen\nelinpäivillään ei ole alkua eikä loppua. Hänet on\nkuvattu Jumalan Pojan kaltaiseksi, ja hän pysyy\npappina ainaisesti. ',197,NULL),
 (4225,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',4,'Pankaa merkille, kuinka suuri hän on: kantaisämme\nAbrahamkin antoi hänelle kymmenykset, parhaimman osan\nsotasaaliistaan.',197,NULL),
 (4226,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',5,'Niiden Leevin jälkeläisten, jotka\non asetettu papeiksi, tulee lain käskyn mukaan kerätä\nkymmenykset kansalta, omilta veljiltään, jotka hekin\novat Abrahamin jälkeläisiä.',197,NULL),
 (4227,'Anonymous','2008-10-17 16:34:15','Anonymous','2008-10-17 16:34:15',6,'Mutta Melkisedek, joka\nei ollut heidän sukuaan, otti kymmenykset Abrahamilta\nja siunasi hänet, vaikka Abraham jo oli saanut Jumalan\nlupaukset.',197,NULL),
 (4228,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',7,'Kiistatontahan on, että siunauksen\nantaja on korkeampiarvoinen kuin siunauksen saaja.',197,NULL),
 (4229,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',8,'Edellisessä tapauksessa kymmenyksiä keräsivät\nkuolevaiset ihmiset, jälkimmäisessä kymmenykset otti\nmies, joka kirjoitusten todistuksen mukaan elää aina.',197,NULL),
 (4230,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',9,'Jopa Leevikin, kymmenysten kerääjä, joutui\nAbrahamin välityksellä tavallaan maksamaan hänelle\nkymmenykset.',197,NULL),
 (4231,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',10,'0Hänhän oli vielä esi-isässään\nAbrahamissa, kun Melkisedek tuli Abrahamia vastaan. ',197,NULL),
 (4232,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',11,'1Jos jo Leevin heimon pappisvirka, josta Israelin\nkansalle on annettu säädökset laissa, olisi johtanut\ntäydellisyyteen, mitä tarvetta olisi ollut sanoa, että\nvielä tulee uudenlainen pappi, jonka pappeus on\nMelkisedekin pappeutta, ei Aaronin?',197,NULL),
 (4233,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',12,'2Jos\npappisvirka muuttuu, muuttuu välttämättä myös laki.',197,NULL),
 (4234,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',13,'3Se, josta tässä on puhe, kuuluukin aivan toiseen\nheimoon, jonka jäsenistä ei yksikään ole toimittanut\nalttaripalvelusta.',197,NULL),
 (4235,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',14,'4Kaikkihan tietävät, että meidän\nHerramme on noussut Juudan heimosta, eikä Mooses ole\npuhunut mitään sille kuuluvasta pappeudesta.    ',197,NULL),
 (4236,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',18,'8Näin kumotaan aikaisempi määräys, koska se oli\nheikko ja hyödytön:',197,NULL),
 (4237,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',19,'9eihän laki tehnyt mitään\ntäydelliseksi. Sen tilalle me olemme saaneet parempaa:\ntoivon, joka antaa meille uskalluksen lähestyä\nJumalaa. ',197,NULL),
 (4238,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',20,'0On myös otettava huomioon Jumalan vala. Leevin\nheimon miehistä tuli pappeja ilman sitä,',197,NULL),
 (4239,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',21,'1mutta\ntämän toisen pappeus vahvistettiin valalla, näillä\nsanoilla:  ',197,NULL),
 (4240,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',22,'2Tästä näemme, kuinka paljon vahvempi on se liitto,\njonka takaajana Jeesus on. ',197,NULL),
 (4241,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',23,'3Muiden pappien määrä on jatkuvasti kasvanut, kun\nkuolema on estänyt heitä pysymästä virassaan.',197,NULL),
 (4242,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',24,'4Mutta Jeesus pysyy ikuisesti, hänen pappeutensa on\nmuuttumaton.',197,NULL),
 (4243,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',25,'5Siksi hän pystyy nyt ja aina\npelastamaan ne, jotka hänen välityksellään lähestyvät\nJumalaa. Hän elää iäti rukoillakseen heidän\npuolestaan. ',197,NULL),
 (4244,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',26,'6Juuri tällaisen ylipapin me tarvitsimme. Hän on\npyhä, viaton ja tahraton, hänet on erotettu syntisistä\nja korotettu taivaita korkeammalle.',197,NULL),
 (4245,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',27,'7Toisin kuin\nmuiden ylipappien, hänen ei tarvitse päivittäin uhrata\nensin omien syntiensä ja sitten kansan syntien\nsovittamiseksi. Hän on antanut kertakaikkisen uhrin\nuhratessaan itsensä.',197,NULL),
 (4246,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',28,'8Laki tekee ylipappeja\nihmisistä, jotka ovat heikkoja, mutta lakia myöhemmin\nvannottu vala nostaa ylipapiksi Jumalan Pojan, joka on\nsaavuttanut ikuisen täydellisyyden. ',197,NULL),
 (4247,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',1,'Tulemme nyt asian ytimeen: Meillä on ylipappi, joka\non asettunut taivaissa istuimelleen Majesteetin\nvaltaistuimen oikealle puolelle.',198,NULL),
 (4248,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',2,'Hän toimittaa\npalvelusta taivaan pyhäkössä, todellisessa\npyhäkköteltassa, jota ei ole pystyttänyt ihminen vaan\nHerra itse. ',198,NULL),
 (4249,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',3,'Jokaisen ylipapin virkaan kuuluu tuoda lahjoja ja\nuhreja Jumalalle, ja siksi myös meidän ylipapillamme\ntulee olla tuotavana uhrinsa.',198,NULL),
 (4250,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',4,'Jos hän olisi maan\npäällä, hän ei olisi edes pappi, koska täällä ovat jo\nne, jotka tuovat lain määräämät uhrilahjat.',198,NULL),
 (4251,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',5,'Heidän\npalveluksensa on kuitenkin vain taivaallisen\npalveluksen kuva ja varjo. Sanottiinhan Moosekselle,\nkun hänen oli määrä valmistaa teltta: \"Pidä huoli\nsiitä, että teet kaiken sen esikuvan mukaan, jonka\nsait nähdä vuorella.\"',198,NULL),
 (4252,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',6,'Mutta meidän ylipappimme on\nsaanut paljon tärkeämmän tehtävän, yhtä paljon\ntärkeämmän kuin on se liitto, jonka välittäjä hän on.\nSe liitto on suurempi, koska se perustuu suurempiin\nlupauksiin. ',198,NULL),
 (4253,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',7,'Jos ensimmäinen liitto olisi ollut rikkumaton, ei\nsen sijalle olisi tarvittu toista.',198,NULL),
 (4254,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',8,'Mutta nyt Jumala\nmoittii kansaansa:   ',198,NULL),
 (4255,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',13,'3Puhuessaan uudesta liitosta Jumala osoittaa, että\nensimmäinen liitto on vanhentunut. Ja se, mikä on\nvanhentunut ja aikansa elänyt, häviää pian. ',198,NULL),
 (4256,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',1,'Ensimmäisellä liitolla oli siis jumalanpalvelusta\nkoskevat säädöksensä ja pyhäkkö täällä maan päällä.',199,NULL),
 (4257,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',2,'Pyhäkkönä oli teltta, jonka etumaisessa huoneessa\noli lampunjalka, pöytä ja uhrileivät. Tätä osaa\nnimitettiin \"pyhäksi\".',199,NULL),
 (4258,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',3,'Toisen verhon takana oli se\nteltan osa, josta käytettiin nimeä \"kaikkeinpyhin\".',199,NULL),
 (4259,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',4,'Siellä oli kultainen suitsutusalttari ja\nliitonarkku, joka oli kauttaaltaan kullalla\npäällystetty. Arkussa oli kultainen, mannaa sisältävä\nastia ja Aaronin sauva, joka puhkesi lehteen, sekä\nliiton taulut.',199,NULL),
 (4260,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',5,'Arkun päällä olivat kerubit,\nkirkkauden enkelit, jotka siivillään varjosivat\nsyntien sovituspaikkaa. Kaikkea tätä ei nyt ole\ntarpeen selittää yksityiskohtaisesti. ',199,NULL),
 (4261,'Anonymous','2008-10-17 16:34:16','Anonymous','2008-10-17 16:34:16',6,'Näin siis on kaikki järjestetty. Teltan\netuhuoneessa käyvät papit päivittäin toimittaessaan\njumalanpalvelusta.',199,NULL),
 (4262,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',7,'Mutta teltan toiseen huoneeseen\nmenee ainoastaan ylipappi, ja hänkin vain kerran\nvuodessa. Hänellä täytyy silloin olla mukanaan verta;\nhän tuo sitä sovittaakseen sekä omat syntinsä että\nkansan tahattomat synnit.',199,NULL),
 (4263,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',8,'Näin Pyhä Henki osoittaa,\nettä kaikkeinpyhimpään ei avata pääsyä niin kauan kuin\nteltan etumainen huone on vielä paikallaan.',199,NULL),
 (4264,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',9,'Tämä\nkaikki kuvaa nykyistä aikaa: lahjoja ja uhreja kyllä\ntuodaan, mutta jumalanpalvelukseen osallistuvan\nomatunto ei niistä puhdistu, ne eivät tee häntä\ntäydelliseksi.',199,NULL),
 (4265,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',10,'0Samalla tavoin kuin määräruoat ja\nmääräjuomat ja erilaiset pesut nämäkin menot\nperustuvat ulkonaisiin säädöksiin ja pätevät\nainoastaan siihen asti, kun pysyvä järjestys astuu voimaan.   ',199,NULL),
 (4266,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',15,'5Sitä varten Kristus on tullut uuden liiton\nvälittäjäksi, että hänen kuolemansa vapauttaisi kaikki\nensimmäisen liiton aikana tapahtuneista rikkomuksista\nja että kutsutut niin saisivat heille luvatun\niankaikkisen perinnön.',199,NULL),
 (4267,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',16,'6Liitto on myös testamentti,\nja testamentin tekijän kuolema on aina voitava näyttää\ntoteen.',199,NULL),
 (4268,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',17,'7Testamenttihan astuu voimaan vasta sen\ntekijän kuoltua; tekijänsä eläessä sitä ei voida\nkoskaan panna täytäntöön.',199,NULL),
 (4269,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',18,'8Siksi ei ensimmäistäkään\nliittoa voitu vahvistaa ilman verta.',199,NULL),
 (4270,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',19,'9Luettuaan\nkansalle lain käskyt Mooses otti sonnien ja pukkien\nverta sekä vettä, punaista villaa ja iisopinvarsia ja\nvihmoi lain kirjan ja koko kansan',199,NULL),
 (4271,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',20,'0sanoen: \"Tämä on\nsen liiton veri, jonka Jumala on teille säätänyt.\"',199,NULL),
 (4272,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',21,'1Samalla tavoin hän vihmoi verta teltan ja kaikkien\njumalanpalveluksessa käytettävien esineiden päälle.',199,NULL),
 (4273,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',22,'2Lain mukaan melkein kaikki puhdistetaan verellä,\neikä anteeksianto ole mahdollinen, ellei vuodateta\nuhriverta. ',199,NULL),
 (4274,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',23,'3Kun siis taivaan todellisuuden kuvat on näin\npuhdistettava, tulee itse todellisuus puhdistaa\nparemmin uhrein.',199,NULL),
 (4275,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',24,'4Kristus ei mennytkään ihmiskäsin\ntehtyyn pyhäkköön, joka on vain todellisen pyhäkön\nkuva, vaan itse taivaaseen ollakseen nyt Jumalan\nedessä puhumassa meidän puolestamme.',199,NULL),
 (4276,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',25,'5Hänen\ntarkoituksenaan ei myöskään ole antaa uhriaan\ntoistuvasti, niin kuin tavallinen ylipappi kerran\nvuodessa vie kaikkeinpyhimpään verta, joka ei ole\nhänen omaansa.',199,NULL),
 (4277,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',26,'6Muutoinhan Kristuksen olisi pitänyt\nkärsiä kuolema monet kerrat maailman luomisesta\nlähtien. Hän on kuitenkin tullut tähän maailmaan vain\nkerran, nyt aikojen lopulla, hävittääkseen synnin\nuhrillaan.',199,NULL),
 (4278,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',27,'7Jokaisen ihmisen osana on kerran kuolla\nja sitten joutua tuomiolle.',199,NULL),
 (4279,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',28,'8Samoin on Kristus\nkerran uhrattu, jotta hän ottaisi pois kaikkien\nsynnit, ja hän ilmestyy vielä kerran, mutta ei enää\nsynnin tähden vaan pelastaakseen ne, jotka häntä\nodottavat. ',199,NULL),
 (4280,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',1,'Laissa siis on vain varjo tulevasta, paremmasta\ntodellisuudesta, ei sen varsinaista ilmentymää. Vaikka\nlain määräämät uhrit toistetaan vuodesta vuoteen, laki\nei ikinä pysty tekemään pysyvästi täydellisiksi niitä,\njotka astuvat Jumalan eteen.',200,NULL),
 (4281,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',2,'Muutoinhan uhraaminen\nolisi lopetettu. Jos ne, jotka ottavat osaa\njumalanpalvelukseen, olisivat jo kerran tulleet\npuhdistetuiksi, heillä ei olisi enää mitään syntejä\ntunnollaan.',200,NULL),
 (4282,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',3,'Mutta uhrit päinvastoin muistuttavat\nsynneistä joka vuosi.',200,NULL),
 (4283,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',4,'Mahdotontahan on, että\nhärkien ja pukkien veri poistaisi synnit. ',200,NULL),
 (4284,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',5,'Siksi Kristus sanoo maailmaan tullessaan:   ',200,NULL),
 (4285,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',8,'Hän sanoo siis ensin: \"Uhreja ja lahjoja,\npolttouhreja ja syntiuhreja sinä et halunnut, ne eivät\nsinua miellyttäneet\", vaikka näistä uhreista on laissa\nmääräykset.',200,NULL),
 (4286,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',9,'Mutta sitten hän sanoo: \"Tässä olen.\nOlen tullut täyttämään sinun tahtosi.\" Hän siis kumoaa\nnuo määräykset asettaakseen niiden tilalle Jumalan\ntahdon.',200,NULL),
 (4287,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',10,'0Tämän tahdon mukaisesti meidät on\npyhitetty ainutkertaisella uhrilla, kun Jeesus Kristus\nuhrasi oman ruumiinsa. ',200,NULL),
 (4288,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',11,'1Jokainen pappi seisoo joka päivä toimittamassa\njumalanpalvelusta ja uhraa kerran toisensa jälkeen\nsamat uhrit, jotka eivät milloinkaan voi poistaa\nsyntejä.',200,NULL),
 (4289,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',12,'2Kristus sitä vastoin on uhrannut yhden\nainoan syntiuhrin ja asettunut pysyvästi istuimelleen\nJumalan oikealle puolelle.',200,NULL),
 (4290,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',13,'3Hän odottaa siellä,\nkunnes hänen vihollisensa pannaan korokkeeksi hänen\njalkojensa alle,',200,NULL),
 (4291,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',14,'4sillä hän on jo yhdellä ainoalla\nuhrilla tehnyt pysyvästi täydellisiksi ne, jotka\npyhitetään. ',200,NULL),
 (4292,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',15,'5Tästä todistaa meille myös Pyhä Henki. Ensin näin:  ',200,NULL),
 (4293,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',17,'7Ja sitten:   ',200,NULL),
 (4294,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',18,'8Missä synnit on annettu anteeksi, siellä ei enää\ntarvita syntiuhria.   ',200,NULL),
 (4295,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',25,'5Me emme saa lyödä laimin seurakuntamme yhteisiä\nkokouksia, niin kuin muutamilla on tapana, vaan meidän\ntulee rohkaista toisiamme, sitä enemmän mitä lähempänä\nnäette Herran päivän olevan.',200,NULL),
 (4296,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',26,'6Jos me näet teemme\nsyntiä ehdoin tahdoin, senkin jälkeen, kun olemme\noppineet tuntemaan totuuden, ei ole enää mitään uhria\nsyntiemme sovitukseksi.',200,NULL),
 (4297,'Anonymous','2008-10-17 16:34:17','Anonymous','2008-10-17 16:34:17',27,'7Ei ole muuta kuin kauhea\ntuomion odotus ja ahnas tuli, joka nielee Jumalaa uhmaavat. ',200,NULL),
 (4298,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',28,'8Jos joku kieltää Mooseksen lain, hänet tuomitaan\nsäälittä kuolemaan; siihen riittää kahden tai kolmen\ntodistajan sana.',200,NULL),
 (4299,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',29,'9Kuinka paljon ankaramman\nrangaistuksen ansaitseekaan mielestänne se, joka\npolkee jalkoihinsa Jumalan Pojan, pitää epäpyhänä\nliiton verta, jolla hänet itsensä on pyhitetty, ja\nhäpäisee armon Henkeä!',200,NULL),
 (4300,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',30,'0Mehän tunnemme hänet, joka\non sanonut:  sekä myös:  ',200,NULL),
 (4301,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',31,'1On kauheaa joutua elävän Jumalan käsiin. ',200,NULL),
 (4302,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',32,'2Muistakaa, millaista oli ennen, silloin kun te\njuuri olitte päässeet valoon. Te kestitte silloin\nmonet kärsimykset ja kamppailut.',200,NULL),
 (4303,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',33,'3Toisia teistä\npilkattiin ja piinattiin katselijoiden huviksi, toiset\njakoivat toveriensa kovan osan.',200,NULL),
 (4304,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',34,'4Te kärsitte\nyhdessä vankilaan teljettyjen kanssa, te suostuitte\niloiten siihen, että omaisuutenne riistettiin, sillä\nte tiesitte omistavanne parempaa, omaisuuden joka\npysyy. ',200,NULL),
 (4305,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',35,'5Älkää siis heittäkö pois rohkeuttanne, sillä se\npalkitaan kerran runsaasti.',200,NULL),
 (4306,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',36,'6Kestävyys on teille\ntarpeen, jotta pystyisitte täyttämään Jumalan tahdon\nja siten saisitte omaksenne sen, minkä hän on\nluvannut.',200,NULL),
 (4307,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',37,'7Onhan sanottu näin:  ',200,NULL),
 (4308,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',39,'9Me emme ole niitä, jotka luopuvat ja joutuvat\ntuhoon, vaan niitä, jotka uskovat ja pelastavat\nsielunsa. ',200,NULL),
 (4309,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',1,'Usko on sen todellisuutta, mitä toivotaan, sen\nnäkemistä, mitä ei nähdä.',201,NULL),
 (4310,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',2,'Uskoon perustuu se\ntodistus, jonka Jumala on isistä antanut. ',201,NULL),
 (4311,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',3,'Uskon avulla me ymmärrämme, että maailmat on luotu\nJumalan sanalla: näkyvä on syntynyt näkymättömästä. ',201,NULL),
 (4312,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',4,'Uskon vuoksi oli Abelin Jumalalle antama uhri\nKainin uhria arvokkaampi. Koska Abel uskoi, hän sai\nvanhurskaudestaan todistuksen, kun Jumala otti vastaan\nhänen lahjansa, ja uskovana hän puhuu vielä\nkuoltuaankin. ',201,NULL),
 (4313,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',5,'Uskon vuoksi otettiin Henok pois niin, ettei hän\nkokenut kuolemaa. \"Sitten häntä ei enää ollut, sillä\nJumala otti hänet luokseen.\" Ennen kuin hänet otettiin\npois, hän näet oli -- niin hänestä todistetaan --\nelänyt Jumalan mielen mukaisesti.',201,NULL),
 (4314,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',6,'Ilman uskoa ei\nkuitenkaan kukaan ole Jumalan mielen mukainen. Sen,\njoka astuu Jumalan eteen, täytyy uskoa, että Jumala on\nolemassa ja että hän kerran palkitsee ne, jotka\netsivät häntä. ',201,NULL),
 (4315,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',7,'Usko sai Nooan taipumaan Jumalan tahtoon, kun hän\nsai ilmoituksen sellaisesta, mikä ei vielä ollut\nnähtävissä. Hän rakensi arkin, ja näin koko hänen\nperheensä pelastui. Hänen uskonsa langetti maailmalle\ntuomion ja antoi hänelle itselleen uskovien perinnön,\nvanhurskauden. ',201,NULL),
 (4316,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',8,'Usko sai Abrahamin tottelemaan Jumalan kutsua ja\nlähtemään kohti seutuja, jotka Jumala oli luvannut\nhänelle perintömaaksi. Hän lähti matkaan, vaikka ei\ntiennyt, minne oli menossa.',201,NULL),
 (4317,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',9,'Koska hän uskoi, hän\nasettui muukalaisena luvattuun maahan ja asui siellä\nteltoissa, ja niin asuivat myös Iisak ja Jaakob, jotka\nperivät saman lupauksen.',201,NULL),
 (4318,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',10,'0Abraham odotti sitä\nkaupunkia, joka on rakennettu vankalle perustalle ja\njonka Jumala itse on suunnitellut ja tehnyt.',201,NULL),
 (4319,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',11,'1Koska\nAbraham ja Saara uskoivat, he kykenivät saamaan\njälkeläisen, vaikka Saara oli hedelmätön ja heidän\naikansa jo oli ohi. Abraham piti lupauksen antajaa\nluotettavana,',201,NULL),
 (4320,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',12,'2ja siksi hän, yksi ainoa,\nmieskuntonsa jo menettänyt, sai jälkeläisiä yhtä\nsuuren määrän kuin on taivaalla tähtiä, yhtä\nluvuttoman joukon kuin on meren rannalla\nhiekkajyväsiä. ',201,NULL),
 (4321,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',13,'3Uskovina nämä kaikki kuolivat. Sitä, mikä heille\noli luvattu, he eivät saaneet; he olivat vain etäältä\nnähneet sen ja tervehtineet sitä iloiten, tunnustaen\nolevansa vieraita ja muukalaisia maan päällä.',201,NULL),
 (4322,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',14,'4Ne,\njotka puhuvat näin, osoittavat kaipaavansa isänmaata.',201,NULL),
 (4323,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',15,'5Mutta jos heidän mielessään olisi ollut se maa,\njosta he olivat lähteneet, he olisivat toki voineet\npalata sinne.',201,NULL),
 (4324,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',16,'6Ei, he odottivat parempaa,\ntaivaallista isänmaata. Sen tähden ei Jumalakaan häpeä\nheitä, vaan sallii itseään kutsuttavan heidän\nJumalakseen, sillä hän on heitä varten jo rakentanut\nvalmiin kaupungin. ',201,NULL),
 (4325,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',17,'7Usko sai Abrahamin tuomaan Iisakin uhriksi, kun\nhänet pantiin koetteelle. Hän oli valmis uhraamaan\nainoan poikansa, vaikka oli saanut lupaukset,',201,NULL),
 (4326,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',18,'8vaikka hänelle oli sanottu: \"Iisakin jälkeläisiä\nsanotaan sinun lapsiksesi.\"',201,NULL),
 (4327,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',19,'9Hän päätteli, että\nJumala kykenee jopa herättämään kuolleen, ja niin hän\nsai poikansa takaisin, ylösnousemuksen ennusmerkkinä. ',201,NULL),
 (4328,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',20,'0Uskon tähden, tulevaisuus mielessään, antoi Iisak\nsiunauksensa Jaakobille ja Esaulle.',201,NULL),
 (4329,'Anonymous','2008-10-17 16:34:18','Anonymous','2008-10-17 16:34:18',21,'1Uskon tähden\nsiunasi kuoleva Jaakob Joosefin kummankin pojan ja\nrukoili sauvaansa vasten kumartuneena.',201,NULL),
 (4330,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',22,'2Uskon\ntähden puhui Joosef loppunsa lähetessä siitä, että\nIsraelin kansa kerran lähtee Egyptistä, ja määräsi,\nmitä hänen luilleen oli tehtävä. ',201,NULL),
 (4331,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',23,'3Usko sai Mooseksen vanhemmat pitämään poikaansa\npiilossa kolme kuukautta. Lapsi oli heidän silmissään\nihmeen kaunis, eivätkä he pelänneet kuninkaan\nmääräystä.',201,NULL),
 (4332,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',24,'4Koska Mooses uskoi, hän aikuiseksi\nvartuttuaan kieltäytyi esiintymästä faraon tyttären\npoikana.',201,NULL),
 (4333,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',25,'5Hän mieluummin jakoi Jumalan kansan\nkärsimykset kuin hankki synnistä ohimenevää nautintoa.',201,NULL),
 (4334,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',26,'6Hän näet piti Kristuksen osaksi tulevaa\nhäväistystä suurempana rikkautena kuin koko Egyptin\naarteita, sillä hän kiinnitti katseensa tulevaan\npalkintoon.',201,NULL),
 (4335,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',27,'7Uskonsa tähden Mooses lähti Egyptistä\nkuninkaan vihaa pelkäämättä ja pysyi lujana, kuten\npysyy se joka ikään kuin näkee Näkymättömän.',201,NULL),
 (4336,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',28,'8Koska\nhän uskoi, hän pani toimeen pääsiäisen vieton ja veren\nsivelyn, ettei esikoisten surmaaja koskisi\nisraelilaisten lapsiin.',201,NULL),
 (4337,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',29,'9Uskon voimalla\nisraelilaiset kulkivat Punaisenmeren yli kuin kuivaa\nmaata pitkin, mutta samaa yrittäessään egyptiläiset\nhukkuivat. ',201,NULL),
 (4338,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',30,'0Uskon voimasta sortuivat Jerikon muurit, kun\nniiden ympäri oli kuljettu seitsemän päivää.',201,NULL),
 (4339,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',31,'1Koska\nportto Rahab uskoi, hän välttyi kuolemasta yhdessä\nniskoittelijoiden kanssa, sillä hän oli ottanut\ntiedustelijat ystävinä vastaan. ',201,NULL),
 (4340,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',32,'2Mitäpä tähän enää lisäisin? Minulta loppuisi aika,\njos kertoisin Gideonista, Barakista, Simsonista ja\nJeftasta, Daavidista, Samuelista ja profeetoista.',201,NULL),
 (4341,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',33,'3Uskon voimalla he kukistivat valtakuntia, pitivät\nyllä oikeutta ja pääsivät näkemään lupausten\ntäyttymisen. He tukkivat leijonien kidat,',201,NULL),
 (4342,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',34,'4sammuttivat roihuavan tulen ja välttivät\nmiekaniskut. He olivat heikkoja, mutta he\nvoimistuivat, heistä tuli väkeviä sotureita, he\ntyönsivät takaisin vihollisen joukot.',201,NULL),
 (4343,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',35,'5Jotkut\nnaiset saivat kuolleet omaisensa elävinä takaisin. Monet kidutettiin hengiltä. He olivat torjuneet heille\ntarjotun vapautuksen, koska halusivat parempaan\nylösnousemukseen.',201,NULL),
 (4344,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',36,'6Toiset saivat osakseen pilkkaa\nja ruoskaniskuja, jopa kahleet ja vankeuden.',201,NULL),
 (4345,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',37,'7Heitä\nkivitettiin kuoliaaksi, heitä sahattiin kahtia ja\nsurmattiin miekalla lyöden. He joutuivat kuljeksimaan\nlampaan- ja vuohennahat vaatteinaan, he kärsivät\npuutetta, heitä ahdistettiin ja piestiin.',201,NULL),
 (4346,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',38,'8He\nolivat liian hyviä tähän maailmaan, ja niin heidän oli\nharhailtava autiomaassa ja vuorilla ja asuttava\nluolissa ja maakuopissa. ',201,NULL),
 (4347,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',39,'9Kaikista näistä on heidän uskonsa perusteella\nannettu kirjoituksissa hyvä todistus, mutta sitä, mikä\non luvattu, he eivät vielä saaneet.',201,NULL),
 (4348,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',40,'0Jumalalla oli\nnäet meitä varten varattuna vielä parempaa, eivätkä he\nsiksi voineet päästä täydellisyyteen ilman meitä. ',201,NULL),
 (4349,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',1,'Kun siis ympärillämme on todistajia kokonainen\npilvi, pankaamme pois kaikki mikä painaa ja synti,\njoka niin helposti kietoutuu meihin. Juoskaamme\nsinnikkäästi loppuun se kilpailu, joka on edessämme,',202,NULL),
 (4350,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',2,'katse suunnattuna Jeesukseen, uskomme perustajaan\nja täydelliseksi tekijään. Edessään olleen ilon tähden\nhän häpeästä välittämättä kesti ristillä kärsimykset,\nja nyt hän istuu Jumalan valtaistuimen oikealla\npuolella.',202,NULL),
 (4351,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',3,'Ajatelkaa häntä, joka kesti syntisten\nankaran vastustuksen, jotta ette menettäisi\nrohkeuttanne ja antaisi periksi. ',202,NULL),
 (4352,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',4,'Vielä te ette ole joutuneet vuodattamaan vertanne\ntaistelussa syntiä vastaan.',202,NULL),
 (4353,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',5,'Te olette unohtaneet\ntämän sanan, joka rohkaisee teitä kuin isä poikiaan:  ',202,NULL),
 (4354,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',7,'Teidän kärsimyksenne on kasvatusta: Jumala kohtelee\nteitä omina poikinaan. Onko sellaista poikaa, jota isä\nei kurittaisi?',202,NULL),
 (4355,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',8,'Jos te siis olette jääneet vaille\nkuritusta, josta kaikki muut ovat osansa saaneet, te\nolette äpäriä, ette laillisia lapsia. ',202,NULL),
 (4356,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',9,'Kun maalliset isämme kurittivat meitä, me emme\ntohtineet vastustaa. Vielä paljon suurempi syy meillä\non alistua taivaallisen Isämme tahtoon, sillä se takaa\nmeille elämän.',202,NULL),
 (4357,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',10,'0Isämme kurittivat meitä vain\nlyhyttä aikaa varten ja niin kuin heistä näytti\nhyvältä, mutta Jumalan kuritus koituu meidän\ntodelliseksi parhaaksemme: me pääsemme osallisiksi\nhänen pyhyydestään.',202,NULL),
 (4358,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',11,'1Vaikka kuritus ei sitä vastaan\notettaessa koskaan tunnu iloiselta vaan ikävältä\nasialta, se lopulta antaa näin valmennetuille\nhedelmänsä: rauhan ja vanhurskauden. ',202,NULL),
 (4359,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',12,'2Oikaiskaa siis hervonneet kätenne ja halvaantuneet\npolvenne!',202,NULL),
 (4360,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',13,'3\"Kulkekaa suoria polkuja\", jottei ontuva\njalka menisi sijoiltaan vaan pikemminkin parantuisi.   ',202,NULL),
 (4361,'Anonymous','2008-10-17 16:34:19','Anonymous','2008-10-17 16:34:19',18,'8Ette te ole tulleet käsin kosketettavan, tulta\nsuitsevan vuoren juurelle, sinne, missä oli synkkä\npilvi, pimeys ja myrskytuuli,',202,NULL),
 (4362,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',19,'9ette sinne, missä\nkuultiin torventoitotus ja ääni, joka puhui niin, että\nkaikki pyysivät päästä kuulemasta enempää.',202,NULL),
 (4363,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',20,'0He\neivät kestäneet määräystä: \"Jokainen, vaikkapa\neläinkin, joka koskettaa vuorta, on kivitettävä\nhengiltä.\"',202,NULL),
 (4364,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',21,'1Se näky oli niin pelottava, että\nMooseskin sanoi: \"Minä pelkään ja vapisen.\" ',202,NULL),
 (4365,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',22,'2Ei, te olette tulleet Siionin vuoren juurelle,\nelävän Jumalan kaupungin, taivaallisen Jerusalemin\nluo. Teidän edessänne on tuhansittain enkeleitä',202,NULL),
 (4366,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',23,'3ja\njuhlaa viettävä esikoisten seurakunta, ne, joiden\nnimet ovat taivaan kirjassa. Siellä on Jumala,\nkaikkien tuomari, siellä ovat perille päässeiden\nvanhurskaiden henget,',202,NULL),
 (4367,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',24,'4ja siellä on uuden liiton\nvälimies Jeesus ja vihmontaveri, joka huutaa, mutta ei\nkostoa niin kuin Abelin veri. ',202,NULL),
 (4368,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',25,'5Varokaa torjumasta häntä, joka puhuu! Kun isät\nSiinailla torjuivat hänet, joka ilmoitti tahtonsa maan\npäällä, he eivät päässeet rangaistuksetta. Vielä\nhuonommin käy meidän, jos käännämme selkämme hänelle,\njonka sanat tulevat taivaasta.',202,NULL),
 (4369,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',26,'6Hänen äänensä pani\nsilloin maan järisemään, ja nyt hän on luvannut:  ',202,NULL),
 (4370,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',27,'7Sanat \"vielä viimeisen kerran\" tarkoittavat sitä,\nettä kaikki luotu järkkyy ja siirtyy sijoiltaan, jotta\nse, mikä ei järky, pysyisi paikallaan. ',202,NULL),
 (4371,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',28,'8Me saamme valtakunnan, joka ei järky. Olkaamme sen\nvuoksi kiitollisia, kiittäkäämme Jumalaa ja\npalvelkaamme häntä hänen tahtonsa mukaisesti,\nkunnioituksen ja pyhän pelon tuntein,',202,NULL),
 (4372,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',29,'9sillä meidän\nJumalamme on tuhkaksi polttava tuli. ',202,NULL),
 (4373,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',1,'Pysyköön veljesrakkautenne elävänä.',203,NULL),
 (4374,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',2,'Älkää\nunohtako osoittaa vieraanvaraisuutta, sillä jotkut\novat yösijan antaessaan tulleet majoittaneeksi\nenkeleitä.',203,NULL),
 (4375,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',3,'Muistakaa vankeja, niin kuin olisitte\nitse kahleissa heidän kanssaan, muistakaa kidutettuja,\ntunteehan teidänkin ruumiinne kivun. ',203,NULL),
 (4376,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',4,'Pitäkää avioliitto kaikin tavoin kunniassa älkääkä\nhäpäiskö aviovuodettanne, sillä Jumala tuomitsee\nsiveettömät ja avionrikkojat. ',203,NULL),
 (4377,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',5,'Älkää juosko rahan perässä, vaan tyytykää siihen,\nmitä teillä on. Jumala on itse sanonut:  ',203,NULL),
 (4378,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',6,'Sen tähden me voimme turvallisin mielin sanoa:    ',203,NULL),
 (4379,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',9,'Älkää antako kaikenlaisten vieraiden oppien johtaa\nitseänne harhaan. Meidän sydämemme tulee vahvistua\narmosta, ei ruoista, jotka eivät ole ketään\nhyödyttäneet. ',203,NULL),
 (4380,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',10,'0Meillä on alttari, jonka uhria ei liiton teltassa\npalvelevien ole lupa syödä.',203,NULL),
 (4381,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',11,'1Tehän tiedätte, että\nkun ylipappi syntien sovittamiseksi vie\nkaikkeinpyhimpään eläinten verta, näiden eläinten\nruhot poltetaan leirin ulkopuolella.',203,NULL),
 (4382,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',12,'2Siksi myös\nJeesus kärsi ja kuoli kaupunginportin ulkopuolella\npyhittääkseen kansan omalla verellään.',203,NULL),
 (4383,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',13,'3Lähtekäämme\nsiis hänen luokseen leirin ulkopuolelle, hänen\nhäväistystään kantaen.',203,NULL),
 (4384,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',14,'4Eihän meillä täällä ole\npysyvää kaupunkia, vaan me odotamme ikävöiden sitä\nkaupunkia, joka tulee. ',203,NULL),
 (4385,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',15,'5Olkaamme sen tähden hänen välityksellään alati\nuhraamassa Jumalalle kiitosuhria, niiden huulten\nhedelmää, jotka tunnustavat hänen nimeään.',203,NULL),
 (4386,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',16,'6Älkää\nmyöskään unohtako tehdä hyvää ja antaa omastanne,\nsillä sellaiset uhrit ovat Jumalalle mieleen. ',203,NULL),
 (4387,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',17,'7Olkaa johtajillenne kuuliaiset, totelkaa heitä. He\nvalvovat öitä teidän vuoksenne, koska joutuvat kerran\ntekemään teistä jokaisesta tiliä. Saakoot he tehdä\ntyönsä iloiten, ei huokaillen, sillä se olisi teille\npaha asia. ',203,NULL),
 (4388,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',18,'8Rukoilkaa meidän puolestamme. Olemme varmoja\nsiitä, että omatuntomme on puhdas -- tahdommehan\ntoimia kaikessa oikein.',203,NULL),
 (4389,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',19,'9Varsinkin siksi pyydän\nteiltä esirukousta, että jo pian pääsisin takaisin\nteidän luoksenne.    ',203,NULL),
 (4390,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',22,'2Veljet, pyydän teitä kestämään nämä kehotuksen\nsanat, olenhan kirjoittanut aivan lyhyesti.',203,NULL),
 (4391,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',23,'3Vielä\nvoin kertoa teille, että veljemme Timoteus on päässyt\nvapaaksi. Jos hän tulee ajoissa, otan hänet mukaani\nteidän luoksenne. ',203,NULL),
 (4392,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',24,'4Tervehtikää kaikkia johtajianne, tervehtikää\nkaikkia pyhiä. Italiasta kotoisin olevat veljet\nlähettävät teille tervehdyksensä. ',203,NULL),
 (4393,'Anonymous','2008-10-17 16:34:20','Anonymous','2008-10-17 16:34:20',25,'5Armo teille kaikille! ',203,NULL),
 (4394,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',1,'Jaakob, Jumalan ja Herran Jeesuksen Kristuksen\npalvelija, tervehtii kahtatoista hajaannuksessa elävää\nheimokuntaa.   ',204,NULL),
 (4395,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',5,'Jos kuitenkin joltakulta teistä puuttuu viisautta,\npyytäköön sitä Jumalalta. Hän on saava pyytämänsä,\nsillä Jumala antaa auliisti kaikille, ketään\nsoimaamatta.',204,NULL),
 (4396,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',6,'Mutta pyytäköön uskossa, lainkaan\nepäilemättä. Joka epäilee, on kuin meren aalto, jota\ntuuli ajaa sinne tänne.',204,NULL),
 (4397,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',7,'Älköön sellainen luulko\nsaavansa Herralta mitään,',204,NULL),
 (4398,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',8,'kahtaalle horjuva\nihminen, epävakaa kaikessa mitä tekee. ',204,NULL),
 (4399,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',9,'Olkoon alhainen veli ylpeä siitä, että on saanut\nkorkean arvon,',204,NULL),
 (4400,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',10,'0rikas siitä, että hänet on\nalennettu, sillä hän on katoava niin kuin kedon kukka.',204,NULL),
 (4401,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',11,'1Aurinko nousee, ja helle kuivattaa ruohon. Kukka\nputoaa maahan, ja sen kauneus on mennyttä. Samoin on\nrikas lakastuva kesken kukoistavien toimiensa. ',204,NULL),
 (4402,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',12,'2Autuas se, joka koettelemuksessa kestää. Sen\nkestettyään hän on saava voitonseppeleeksi elämän.\nJumala on sen luvannut niille, jotka häntä rakastavat.',204,NULL),
 (4403,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',13,'3Älköön kukaan kiusauksiin jouduttuaan ajatelko,\nettä kiusaus tulee Jumalalta. Jumala ei ole pahan\nkiusattavissa, eikä hän itse kiusaa ketään.',204,NULL),
 (4404,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',14,'4Jokaista kiusaa hänen oma himonsa; se häntä vetää\nja houkuttelee.',204,NULL),
 (4405,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',15,'5Ja sitten himo tulee raskaaksi ja\nsynnyttää synnin, ja kun synti on kasvanut täyteen\nmittaan, se synnyttää kuoleman. ',204,NULL),
 (4406,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',16,'6Älkää eksykö, rakkaat veljet!',204,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (4407,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',17,'7Jokainen hyvä\nanti ja jokainen täydellinen lahja tulee ylhäältä,\ntaivaan tähtien Isältä, jonka luona ei mikään muutu,\nei valo vaihdu varjoksi.',204,NULL),
 (4408,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',18,'8Päätöksensä mukaan hän\nsynnytti meidät totuuden sanalla, että olisimme hänen\nluomistekojensa ensi hedelmä.   ',204,NULL),
 (4409,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',22,'2Toteuttakaa sana tekoina, älkää pelkästään\nkuunnelko sitä -- älkää pettäkö itseänne.',204,NULL),
 (4410,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',23,'3Se, joka\nkuulee sanan mutta ei tee sen mukaan, on kuin mies,\njoka kuvastimesta katselee omien kasvojensa piirteitä.',204,NULL),
 (4411,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',24,'4Hän kyllä tarkastelee itseään, mutta poistuttuaan\nhän unohtaa saman tien, millainen on.',204,NULL),
 (4412,'Anonymous','2008-10-17 16:34:21','Anonymous','2008-10-17 16:34:21',25,'5Mutta se,\njoka kiinnittää katseensa vapauden täydelliseen lakiin\nja jää sen ääreen, ei unohda kuulemaansa vaan tekee\nsen mukaisesti, ja kerran hän on saava kiitoksen siitä\nmitä tekee.   ',204,NULL),
 (4413,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',1,'Veljeni, te jotka uskotte meidän Herraamme\nJeesukseen Kristukseen, kirkkauden Herraan, älkää\nerotelko ihmisiä.',205,NULL),
 (4414,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',2,'Jos teidän kokoukseenne tulee\nmies, jolla on kultasormus sormessaan ja hieno puku\nyllään, sekä samalla kertaa köyhä nuhruisissa\nvaatteissaan,',205,NULL),
 (4415,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',3,'niin ettekö vain osoitakin\nhuomiotanne tuolle hienosti pukeutuneelle ja sano\nhänelle: \"Istu tähän, tässä on hyvä paikka\"? Köyhälle\nte sen sijaan sanotte: \"Seiso sinä tuossa\", tai: \"Istu\ntähän lattialle jalkojeni viereen.\"',205,NULL),
 (4416,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',4,'Ettekö te\nsilloin syyllisty erotteluun omassa keskuudessanne?\nEikö teistä ole tullut tuomareita, jotka tuomitsevat\nväärin perustein? ',205,NULL),
 (4417,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',5,'Kuulkaa, rakkaat veljeni! Onhan Jumala valinnut\njuuri maailman silmissä köyhät olemaan uskossa rikkaat\nja perimään valtakunnan, jonka Jumala on luvannut\nhäntä rakastaville.',205,NULL),
 (4418,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',6,'Mutta te häpäisette köyhän.\nEivätkö juuri rikkaat sorra teitä ja raahaa teitä\noikeuden eteen?',205,NULL),
 (4419,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',7,'Eivätkö juuri he herjaa sitä jaloa\nnimeä, joka on lausuttu teidän ylitsenne?* ',205,NULL),
 (4420,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',8,'Jos te noudatatte lain kuningaskäskyä niin kuin se\nRaamatussa on: \"Rakasta lähimmäistäsi niin kuin\nitseäsi\", te teette oikein.',205,NULL),
 (4421,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',9,'Mutta jos te\nerottelette ihmisiä, te teette syntiä, ja laki\nosoittaa teidät rikkojiksi.',205,NULL),
 (4422,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',10,'0Sillä se, joka muuten\nkaikessa noudattaa lakia mutta rikkoo sitä yhdessä\nkohdassa, on syypää kaikilta kohdin.',205,NULL),
 (4423,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',11,'1Hän, joka\nsanoi: \"Älä tee aviorikosta\", sanoi myös: \"Älä tapa.\"\nVaikka siis et teekään aviorikosta, olet lainrikkoja,\njos tapat.',205,NULL),
 (4424,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',12,'2Vapauden lain mukaan teidät tuomitaan;\npitäkää se mielessänne, mitä puhutte tai teettekin.',205,NULL),
 (4425,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',13,'3Joka ei toista armahda, saa itse armottoman\ntuomion, mutta joka armahtaa, saa tuomiosta\nriemuvoiton.   ',205,NULL),
 (4426,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',18,'8Ehkä joku nyt sanoo: \"Sinulla on usko, minulla\nteot.\" Näytä sinä minulle uskosi ilman tekoja, minä\nkyllä näytän sinulle uskon teoillani.',205,NULL),
 (4427,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',19,'9Sinä uskot,\nettä Jumala on yksi ainoa. Oikein teet --  pahat\nhengetkin uskovat sen ja vapisevat.',205,NULL),
 (4428,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',20,'0Mutta etkö\nsinä tyhjänpäiväinen ihminen tahdo tietää, että ilman\ntekoja usko on hyödytön?',205,NULL),
 (4429,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',21,'1Eikö meidän isämme\nAbraham osoittautunut vanhurskaaksi tekojensa\nperusteella, kun hän vei poikansa Iisakin\nuhrialttarille?',205,NULL),
 (4430,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',22,'2Huomaat, että usko vaikutti\nyhdessä hänen tekojensa kanssa, teoissa usko tuli\ntodeksi.',205,NULL),
 (4431,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',23,'3Ja niin täyttyi Raamatun sana: \"Abraham\nuskoi Jumalan lupaukseen, ja Jumala katsoi hänet\nvanhurskaaksi\", ja hän sai nimen Jumalan ystävä.',205,NULL),
 (4432,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',24,'4Näette siis, että ihminen osoittautuu\nvanhurskaaksi tekojen, ei yksistään uskon perusteella.',205,NULL),
 (4433,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',25,'5Juuri tekojensa ansiosta portto Rahabkin\nosoittautui vanhurskaaksi, kun otti sanansaattajat\nluokseen ja auttoi heidät pois toista tietä.',205,NULL),
 (4434,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',26,'6Niin\nkuin ruumis ilman henkeä on kuollut, niin on uskokin\nkuollut ilman tekoja. ',205,NULL),
 (4435,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',1,'Veljeni, kovin monien teistä ei pidä ryhtyä\nopettajiksi, sillä te tiedätte, että meidät opettajat\ntullaan tuomitsemaan muita ankarammin.',206,NULL),
 (4436,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',2,'Kaikkihan me\nhairahdumme monin tavoin. Täydellinen on se, joka ei\nhairahdu puheissaan: hän kykenee hallitsemaan koko\nruumiinsa.',206,NULL),
 (4437,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',3,'Jos panemme suitset hevosen suuhun, me\nsaamme sen tottelemaan itseämme ja voimme ohjata koko\nhevosta.',206,NULL),
 (4438,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',4,'Entä laivat: vaikka ne ovat isoja ja rajut\ntuulet heittelevät niitä, pienen pieni peräsin ohjaa\nlaivan minne peränpitäjä haluaa.',206,NULL),
 (4439,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',5,'Samoin kieli on\npieni jäsen, mutta se voi kerskua suurilla asioilla.\nPieni tuli sytyttää palamaan suuren metsän!',206,NULL),
 (4440,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',6,'Ja\nkielikin on tuli; meidän jäsentemme joukossa se on\nvääryyden maailma. Se saastuttaa koko ruumiin ja\nsytyttää tuleen elämän pyörän, itse liekehtien\nhelvetin tulta. ',206,NULL),
 (4441,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',7,'Kaikki eläimet voi ihminen kesyttää ja onkin\nkesyttänyt, nelijalkaiset, linnut, matelijat ja meren\neläimet,',206,NULL),
 (4442,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',8,'mutta kieltä ei yksikään ihminen pysty\nkesyttämään. Se on hillitön ja paha, täynnä tappavaa\nmyrkkyä.',206,NULL),
 (4443,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',9,'Kielellä me ylistämme Herraa ja Isää, ja\nsillä me myös kiroamme ihmisiä, Jumalan kuvaksi\nluotuja.',206,NULL),
 (4444,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',10,'0Kiitos ja kirous lähtevät samasta suusta.\nTämä ei käy, veljeni!',206,NULL),
 (4445,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',11,'1Eihän samasta\nlähteensilmästä pulppua makeaa ja karvasta vettä.',206,NULL),
 (4446,'Anonymous','2008-10-17 16:34:22','Anonymous','2008-10-17 16:34:22',12,'2Ei viikunapuussa kasva oliiveja eikä\nviiniköynnöksessä viikunoita, vai mitä, veljeni?\nSamoin ei suolaisesta lähteestä juokse makeaa vettä.   ',206,NULL),
 (4447,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',1,'Mistä teidän keskinäiset kiistanne ja taistelunne\nsyntyvät? Mistäpä muusta kuin haluistanne, jotka\nkäyvät taistelua teidän ruumiissanne.',207,NULL),
 (4448,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',2,'Te\nhimoitsette, mutta jäätte vaille, kiihkon ja kateuden\nvallassa te vaikka riistätte hengen toisiltanne, mutta\nette silti saavuta päämääräänne. Te taistelette ja\niskette yhteen, mutta jäätte vaille, koska ette pyydä.',207,NULL),
 (4449,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',3,'Ja vaikka pyydätte, te ette saa, koska pyydätte\nväärässä tarkoituksessa, kuluttaaksenne kaiken\nmielihaluissanne. ',207,NULL),
 (4450,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',4,'Te uskottomat! Ettekö tiedä, että rakkaus maailmaan\non vihaa Jumalaa kohtaan? Joka tahtoo olla maailman\nystävä, asettuu Jumalan viholliseksi.',207,NULL),
 (4451,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',5,'Vai\nluuletteko, että Raamattu syyttä sanoo:\n\"Mustasukkaisen kiihkeästi hän halajaa henkeä, jonka\non meihin pannut\"?',207,NULL),
 (4452,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',6,'Mutta vielä suurempi on se armo,\njonka hän antaa. Siksi Raamattu sanoo:  ',207,NULL),
 (4453,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',7,'Taipukaa siis Jumalan alaisuuteen, mutta\nvastustakaa Paholaista, niin se lakkaa ahdistamasta\nteitä.',207,NULL),
 (4454,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',8,'Lähestykää Jumalaa, niin hän lähestyy teitä.\nPuhdistakaa kätenne, synnintekijät, puhdistakaa\nsydämenne, te kahtaalle horjuvat!',207,NULL),
 (4455,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',9,'Tuntekaa\nkurjuutenne, surkaa ja itkekää! Kääntyköön naurunne\nmurheeksi, ilonne masennukseksi.',207,NULL),
 (4456,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',10,'0Nöyrtykää Herran\nedessä, niin hän on teidät korottava.     ',207,NULL),
 (4457,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',1,'Kuulkaa, te rikkaat! Itkekää ja valittakaa\nkurjuutta, joka tulee osaksenne.',208,NULL),
 (4458,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',2,'Teidän rikkautenne\non lahonnut ja teidän vaatteenne ovat koin syömiä.',208,NULL),
 (4459,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',3,'Teidän kultanne ja hopeanne on ruosteessa. Niiden\nruoste on todistava teitä vastaan, ja se syö lihaanne\nkuin tuli. Te olette koonneet rikkauksia näinä lopun\naikoina.',208,NULL),
 (4460,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',4,'Te olette riistäneet palkan työmiehiltä,\njotka ovat leikanneet teidän peltonne. Kuulkaa: se\npalkka huutaa, ja korjuuväen valitukset ovat tulleet\nHerran Sebaotin korviin.',208,NULL),
 (4461,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',5,'Te olette maan päällä\neläneet yltäkylläisyydessä ja mässäilyissä, te olette\nlihottaneet sydäntänne teurastuspäivänä.',208,NULL),
 (4462,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',6,'Te olette\ntuominneet ja surmanneet viattoman; ei hän asetu teitä\nvastaan.         ',208,NULL),
 (4463,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',1,'Pietari, Jeesuksen Kristuksen apostoli, tervehtii\nvalittuja, jotka asuvat hajallaan, muukalaisina\nPontoksessa, Galatiassa, Kappadokiassa, Bityniassa ja\nAasian maakunnassa',209,NULL),
 (4464,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',2,'ja jotka Isä Jumala\nsuunnitelmansa mukaisesti on Hengellään pyhittänyt\nelämään Jeesukselle Kristukselle kuuliaisina ja hänen\nverellään puhtaaksi vihmottuina. Tulkoon armo ja rauha yhä runsaampana teidän\nosaksenne.   ',209,NULL),
 (4465,'Anonymous','2008-10-17 16:34:23','Anonymous','2008-10-17 16:34:23',6,'Siksi te riemuitsette, vaikka nyt jouduttekin\njonkin aikaa kärsimään monenlaisissa koettelemuksissa.',209,NULL),
 (4466,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',7,'Kultakin koetellaan tulessa, ja onhan teidän\nuskonne paljon arvokkaampaa kuin katoava kulta.\nKoettelemuksissa teidän uskonne todetaan aidoksi, ja\nsiitä koituu Jeesuksen Kristuksen ilmestyessä\nylistystä, kirkkautta ja kunniaa.',209,NULL),
 (4467,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',8,'Häntä te\nrakastatte, vaikka ette ole häntä nähneet, häneen te\nuskotte, vaikka ette häntä nyt näe, ja te riemuitsette\nsanoin kuvaamattoman, kirkastuneen ilon vallassa,',209,NULL),
 (4468,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',9,'sillä te saavutatte uskon päämäärän, sielujen\npelastuksen. ',209,NULL),
 (4469,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',10,'0Tätä pelastusta ovat profeetat etsineet ja\ntutkineet. He ovat ennustaneet ja puhuneet teidän\nosaksenne tulevasta armosta',209,NULL),
 (4470,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',11,'1ja tutkineet, mihin ja\nmillaiseen aikaan heissä vaikuttava Kristuksen Henki\nviittasi todistaessaan Kristuksen kärsimyksistä ja\nniitä seuraavasta kirkkaudesta.',209,NULL),
 (4471,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',12,'2Heille ilmaistiin,\netteivät he palvelleet itseään vaan teitä puhuessaan\nsiitä, minkä teille nyt ovat julistaneet ne, jotka\ntaivaasta lähetetyssä Pyhässä Hengessä ovat tuoneet\nteille evankeliumin. Tähän pelastukseen haluavat\nenkelitkin päästä edes luomaan silmäyksen.   ',209,NULL),
 (4472,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',22,'2Kun te totuudelle kuuliaisina olette\npuhdistautuneet elääksenne vilpittömässä,\nveljellisessä rakkaudessa, niin rakastakaa toisianne\nuskollisesti ja kaikesta sydämestänne.',209,NULL),
 (4473,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',23,'3Olettehan\nte syntyneet uudesti, ette katoavasta siemenestä, vaan\nkatoamattomasta, Jumalan elävästä ja pysyvästä\nsanasta.',209,NULL),
 (4474,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',24,'4Sillä  Juuri tämä sana on teille ilosanomana julistettu. ',209,NULL),
 (4475,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',1,'Jättäkää siis kaikki pahuus ja vilppi, kaikki\nteeskentely ja kateus ja kaikki panettelu.',210,NULL),
 (4476,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',2,'Niin\nkuin vastasyntyneet lapset tavoitelkaa puhdasta sanan\nmaitoa, jotta sen ravitsemina kasvaisitte\npelastukseen.',210,NULL),
 (4477,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',3,'Olettehan te \"maistaneet Herran\nhyvyyttä\".     ',210,NULL),
 (4478,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',7,'Te, jotka uskotte, saatte siis osaksenne tämän\narvon ja kunnian, mutta niille, jotka eivät usko,\n\"kivestä, jonka rakentajat hylkäsivät, on tullut\nkulmakivi,',210,NULL),
 (4479,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',8,'kompastuskivi ja kallio johon\nlangetaan\". Koska he eivät tottele sanaa, he\nkompastuvat, ja se heidän osakseen on määrättykin. ',210,NULL),
 (4480,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',9,'Mutta te olette valittu suku, kuninkaallinen\npapisto, pyhä heimo, Jumalan oma kansa, määrätty\njulistamaan hänen suuria tekojaan, joka teidät on\npimeydestä kutsunut ihmeelliseen valoonsa.',210,NULL),
 (4481,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',10,'0Ennen\nte ette olleet kansa, mutta nyt te olette Jumalan\nkansa. Ennen te olitte armoa vailla, mutta nyt on\nJumala teidät armahtanut.         ',210,NULL),
 (4482,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',23,'3Häntä herjattiin, mutta hän ei vastannut\nherjauksella, hän kärsi, mutta ei uhkaillut, vaan\nuskoi itsensä oikeamielisen tuomarin haltuun.',210,NULL),
 (4483,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',24,'4Itse, omassa ruumiissaan, hän \"kantoi meidän\nsyntimme\" ristinpuulle, jotta me kuolisimme pois\nsynneistä ja eläisimme vanhurskaudelle. \"Hänen\nhaavansa ovat teidät parantaneet.\"',210,NULL),
 (4484,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',25,'5Te olitte\n\"eksyksissä niin kuin lampaat\", mutta nyt te olette\npalanneet sielujenne paimenen ja kaitsijan luo. ',210,NULL),
 (4485,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',1,'Samoin te, vaimot, olkaa kuuliaisia miehellenne,\njotta myös ne miehet, jotka ehkä eivät usko Jumalan\nsanaan, nyt vaimonsa elävällä esimerkillä ilman\nsanojakin voitettaisiin,',211,NULL),
 (4486,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',2,'kun he näkevät teidän\nelävän jumalanpelossa puhdasta elämää.',211,NULL),
 (4487,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',3,'Älkää pitäkö\ntärkeänä ulkonaista kaunistusta, älkää hiuslaitteita,\nkultakoruja tai hienoja vaatteita.',211,NULL),
 (4488,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',4,'Teidän\nkaunistuksenne olkoon katoamatonta: salassa oleva\nsydämen ihminen, lempeä ja sävyisä henki. Tämä on\nJumalan silmissä kallisarvoista.',211,NULL),
 (4489,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',5,'Näin kaunistivat\nitsensä entisajankin pyhät vaimot, jotka panivat\ntoivonsa Jumalaan. He olivat miehilleen kuuliaiset --',211,NULL),
 (4490,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',6,'Saarakin oli kuuliainen Abrahamille ja kutsui häntä\nherraksi. Ja Saaran tyttäriä tekin olette, kun teette\nhyvää ettekä anna minkään uhkailun pelottaa itseänne. ',211,NULL),
 (4491,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',7,'Samoin te, miehet, eläkää vaimonne kanssa\nymmärtäväisesti, muistaen, että hän on heikompi\nosapuoli. Osoittakaa hänelle kunnioitusta, sillä hän\non yhtä lailla armon ja elämän perillinen. Näin ei\nmikään tule esteeksi rukouksillenne.     ',211,NULL),
 (4492,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',13,'3Kuka voi tehdä teille pahaa, jos te kiihkeästi\npyritte tekemään hyvää?',211,NULL),
 (4493,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',14,'4Ja vaikka joutuisittekin\nkärsimään vanhurskauden tähden, te olette autuaita.\n\"Älkää heitä pelätkö älkääkä hämmentykö\",',211,NULL),
 (4494,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',15,'5vaan\npyhittäkää Herra Kristus sydämessänne ja olkaa aina\nvalmiit antamaan vastaus jokaiselle, joka kysyy, mihin\nteidän toivonne perustuu.',211,NULL),
 (4495,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',16,'6Mutta vastatkaa\nsävyisästi ja kunnioittavasti ja säilyttäkää\nomatuntonne puhtaana -- silloin ne, jotka parjaavat\nteidän hyvää vaellustanne kristittynä, joutuvat\nhäpeään juuri siinä, mistä teitä panettelevat.',211,NULL),
 (4496,'Anonymous','2008-10-17 16:34:24','Anonymous','2008-10-17 16:34:24',17,'7Parempi on tehdä hyvää ja kärsiä, jos niin on\nJumalan tahto, kuin kärsiä pahojen tekojen tähden.   ',211,NULL),
 (4497,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',1,'--',212,NULL),
 (4498,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',2,'Kun siis Kristus on ruumiissaan kärsinyt, niin\nolkaa tekin valmiita kärsimään, jotta eläisitte\njäljellä olevan maallisen elämänne Jumalan tahdon\nmukaisesti ettekä ihmisten himoja seuraten. Sillä se,\njoka on ruumiissaan kärsinyt, on luopunut synnistä.',212,NULL),
 (4499,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',3,'Aivan riittämiin te menneenä aikana elitte niin\nkuin pakanat tahtovat, ryvitte irstaudessa ja\nhimoissa, juoppoudessa, mässäilyissä ja juomingeissa\nja palvelitte epäjumalia jumalattomin menoin.',212,NULL),
 (4500,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',4,'Nyt\nhe kummeksuvat sitä, että te ette enää riennä heidän\nkanssaan samaan riettauden virtaan, ja syytävät\nherjauksiaan.',212,NULL),
 (4501,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',5,'Mutta he joutuvat tekemään tilin\nhänelle, joka on valmiina tuomitsemaan elävät ja\nkuolleet.',212,NULL),
 (4502,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',6,'Sitä varten niillekin, jotka ovat\nkuolleet, on julistettu evankeliumi, että he eläisivät\nhengessä, niin kuin Jumala elää, vaikka ruumiissaan\novat saaneet saman tuomion kuin kaikki ihmiset.     ',212,NULL),
 (4503,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',17,'7On tullut tuomion aika, ja tuomio alkaa Jumalan\nhuoneesta. Mutta jos se alkaa meistä, mikä onkaan\nniiden loppu, jotka eivät taivu uskomaan Jumalan\nevankeliumia?   ',212,NULL),
 (4504,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',19,'9Sen tähden ne, jotka joutuvat kärsimään, jos niin\non Jumalan tahto, antakoot elämänsä uskollisen Luojan\nhaltuun ja tehkööt edelleen sitä, mikä on hyvää. ',212,NULL),
 (4505,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',1,'Seuraavan kehotuksen minä osoitan niille, jotka\nteidän joukossanne ovat vanhimman asemassa -- minä,\njoka itsekin olen vanhin*ja Kristuksen kärsimysten\ntodistaja sekä myös osallinen siitä kirkkaudesta, joka\non ilmestyvä:',213,NULL),
 (4506,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',2,'Kaitkaa sitä laumaa, jonka Jumala on\nteille uskonut, älkää pakosta, vaan vapaaehtoisesti,\nJumalan tahdon mukaan, älkää myöskään alhaisesta\nvoitonhimosta, vaan sydämenne halusta.',213,NULL),
 (4507,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',3,'Älkää\nherroina vallitko niitä, jotka teidän osallenne ovat\ntulleet, vaan olkaa laumanne esikuvana.',213,NULL),
 (4508,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',4,'Silloin te\nylimmän paimenen ilmestyessä saatte kirkkauden\nseppeleen, joka ei kuihdu. ',213,NULL),
 (4509,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',5,'Samoin te, jotka olette nuorempia, tyytykää\nasemaanne vanhimpien alaisina. Ja te kaikki:\npukeutukaa keskinäiseen nöyryyteen, sillä    ',213,NULL),
 (4510,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',8,'Pitäkää mielenne valppaana ja valvokaa. Teidän\nvastustajanne Saatana kulkee ympäriinsä kuin ärjyvä\nleijona ja etsii, kenet voisi niellä.',213,NULL),
 (4511,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',9,'Vastustakaa\nhäntä, uskossa lujina! Tiedättehän, että veljenne\nkaikkialla maailmassa joutuvat kokemaan samat\nkärsimykset. ',213,NULL),
 (4512,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',10,'0Kaiken armon Jumala on Kristuksessa Jeesuksessa\nkutsunut teidät iankaikkiseen kirkkauteensa. Vähän\naikaa kärsittyänne hän itse varustaa, voimistaa,\nvahvistaa ja lujittaa teidät.',213,NULL),
 (4513,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',11,'1Hänen on valta\niankaikkisesti. Aamen.   ',213,NULL),
 (4514,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',13,'3Teille lähettää tervehdyksen Babylonissa*oleva\nseurakunta, valittu niin kuin tekin. Samoin tervehtii\nteitä poikani Markus.',213,NULL),
 (4515,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',14,'4Tervehtikää toisianne\nrakkauden suudelmalla. Rauha teille kaikille Kristuksen yhteyteen\nliitetyille. ',213,NULL),
 (4516,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',1,'Simon Pietari, Jeesuksen Kristuksen palvelija ja\napostoli, tervehtii teitä, joille Jumalamme ja\nVapahtajamme Jeesus Kristus vanhurskaudessaan on\nsuonut saman kalliin uskon kuin meille. ',214,NULL),
 (4517,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',2,'Yhä runsaampana tulkoon osaksenne armo ja rauha,\njonka Jumalan ja meidän Herramme Jeesuksen Kristuksen\ntunteminen antaa.   ',214,NULL),
 (4518,'Anonymous','2008-10-17 16:34:25','Anonymous','2008-10-17 16:34:25',5,'Pyrkikää sen vuoksi osoittamaan uskossanne\nlujuutta, lujuudessa oikeaa tietoa,',214,NULL),
 (4519,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',6,'tiedossa\nitsehillintää, itsehillinnässä kestävyyttä,\nkestävyydessä jumalanpelkoa,',214,NULL),
 (4520,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',7,'jumalanpelossa\nkeskinäistä kiintymystä, kiintymyksessä rakkautta.',214,NULL),
 (4521,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',8,'Kun näet teillä on kaikki nämä avut ja ne vielä\nenenevät, te ette jää toimettomiksi eikä meidän\nHerramme Jeesuksen Kristuksen tunteminen jää teissä\nvaille hedelmää.',214,NULL),
 (4522,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',9,'Se taas, jolta kaikki tämä\npuuttuu, on likinäköinen, suorastaan sokea. Hän on\nunohtanut, että hänet on kerran puhdistettu\naikaisemmista synneistään. ',214,NULL),
 (4523,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',10,'0Pyrkikää siis, veljet, yhä innokkaammin tekemään\nlujaksi kutsumuksenne ja valintanne. Kun näin teette,\nte ette koskaan lankea,',214,NULL),
 (4524,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',11,'1ja niin te saatte\navatuista ovista vapaasti astua meidän Herramme ja\nVapahtajamme Jeesuksen Kristuksen iankaikkiseen\nvaltakuntaan.   ',214,NULL),
 (4525,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',16,'6Emmehän me, silloin kun saatoimme teidän tietoonne\nHerramme Jeesuksen Kristuksen voiman ja tulemisen,\nolleet lähteneet seuraamaan mitään ovelasti\nsepitettyjä taruja, vaan olimme omin silmin saaneet\nnähdä hänen jumalallisen suuruutensa.',214,NULL),
 (4526,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',17,'7Hän sai\nJumalalta, Isältä, kunnian ja kirkkauden, kun hänelle\nkantautui Ylhäisimmän Kirkkauden ääni: \"Tämä on minun\nrakas Poikani, johon minä olen mieltynyt.\"',214,NULL),
 (4527,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',18,'8Tämän\näänen me itse kuulimme tulevan taivaasta, kun olimme\nhänen kanssaan pyhällä vuorella.',214,NULL),
 (4528,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',19,'9Siksi me voimme\nentistä lujemmin luottaa profeetalliseen sanaan. Ja\nhyvin teette tekin, jos kiinnitätte katseenne siihen\nkuin pimeässä loistavaan lamppuun, kunnes päivä\nsarastaa ja kointähti syttyy teidän sydämessänne.',214,NULL),
 (4529,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',20,'0Ennen muuta teidän on oltava selvillä siitä,\netteivät pyhien kirjoitusten ennustukset ole kenenkään\nomin neuvoin selitettävissä.',214,NULL),
 (4530,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',21,'1Yksikään\nprofeetallinen sana ei ole tullut julki ihmisten\ntahdosta, vaan ihmiset ovat puhuneet Pyhän Hengen\njohtamina sen, minkä ovat Jumalalta saaneet. ',214,NULL),
 (4531,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',1,'Israelin kansan keskuudessa esiintyi kuitenkin myös\nvääriä profeettoja, ja samoin on teidänkin joukkoonne\nilmestyvä vääriä opettajia. He salakuljettavat teidän\nkeskuuteenne tuhoisia harhaoppeja, jopa kieltävät\nherransa, joka on ostanut heidät omikseen. Pian he\nkuitenkin saattavat itsensä tuhoon.',215,NULL),
 (4532,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',2,'Heidän irstasta\nmenoaan lähtevät monet seuraamaan, ja näiden takia\ntotuuden tie tulee häväistyksi.',215,NULL),
 (4533,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',3,'Ahneudessaan he\npyrkivät käyttämään teitä hyväkseen esittämällä\ntekaistuja tarinoita. Mutta heille muinoin langetettu\ntuomio on yhä voimassa, heidän tuhonsa odottaa heitä. ',215,NULL),
 (4534,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',4,'Ei Jumala säästänyt enkeleitäkään, jotka olivat\ntehneet syntiä, vaan syöksi heidät kahleissa manalan\npimentoihin odottamaan tuomion toteutumista.',215,NULL),
 (4535,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',5,'Eikä\nhän säästänyt muinaisajan maailmaa, vaan antoi\nvedenpaisumuksen hyökyä yli jumalattoman ihmiskunnan.\nKuitenkin hän pelasti Nooan, vanhurskauden julistajan,\nja seitsemän muuta.',215,NULL),
 (4536,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',6,'Jumala tuomitsi Sodoman ja\nGomorran kaupungit polttamalla ne poroksi ja antoi\nnäin varoittavan esimerkin vastaisten aikojen\njumalattomille.',215,NULL),
 (4537,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',7,'Sen sijaan hän pelasti hurskaan\nLootin, jonka mieltä jumalattomien riettaus painoi.',215,NULL),
 (4538,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',8,'Päivästä päivään tuo hurskas mies sydämessään\nkärsi, kun hän joutui elämään heidän keskellään ja\nkatselemaan ja kuuntelemaan heidän jumalattomuuttaan. ',215,NULL),
 (4539,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',9,'Herra siis voi pelastaa koetuksesta ne, jotka häntä\npelkäävät, mutta jumalattomien hän antaa rangaistuksen\nalaisina odottaa tuomion päivää',215,NULL),
 (4540,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',10,'0-- varsinkin\nniiden, jotka seuraavat saastaisia ruumiin haluja ja\nhalveksivat Herraa ja hänen valtaansa. Nuo itseään\ntäynnä olevat röyhkimykset eivät kaihda herjaamasta\nhenkivaltojakaan,',215,NULL),
 (4541,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',11,'1vaikka edes enkelit, joilla on\nenemmän voimaa ja valtaa, eivät Herran edessä niitä\nherjaa ja tuomitse.',215,NULL),
 (4542,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',12,'2Nuo ihmiset herjaavat\nsellaista, mitä eivät tunne. Siinä he ovat kuin\njärjettömät eläimet, jotka luonto on tarkoittanut\npyydystettäviksi ja tapettaviksi, ja eläinten lailla\nhe myös tuhoutuvat,',215,NULL),
 (4543,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',13,'3saavat vääryyksistään\nväärintekijän palkan. Heistä on nautinto mässäillä\nkeskellä kirkasta päivää. Häpeällisesti ja\niljettävästi he ruokkivat himojaan ollessaan aterialla\nteidän kanssanne.',215,NULL),
 (4544,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',14,'4Himokkain silmin he katsovat\nnaisia eivätkä saa synninteosta kyllikseen. He\nviekoittelevat ihmisiä, jotka eivät vielä ole varmalla\npohjalla, ja ahneudessa he ovat mestareita. Nuo\nkirotut!',215,NULL),
 (4545,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',15,'5Suoralta tieltä poikettuaan he ovat\neksyneet ja lähteneet samalle tielle kuin Bileam,\nBeorin poika, joka mieltyi petoksesta tarjottuun\npalkkaan',215,NULL),
 (4546,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',16,'6mutta jota ojennettiin hänen\nrikkomuksensa tähden: aasi, mykkä eläin, puhui ihmisen\näänellä ja esti profeetan mielettömän aikeen. ',215,NULL),
 (4547,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',17,'7Nuo ihmiset ovat vedettömiä lähteitä, sadepilviä,\njotka tuulenpuuska hajottaa. Heille on varattuna\nsynkin pimeys.',215,NULL),
 (4548,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',18,'8Heidän puheensa ovat suurellisia\nmutta tyhjänpäiväisiä, ja ruumiinsa halujen vallassa\nhe koettavat vietellä irstauteen niitä, jotka juuri\novat pääsemässä eroon eksyksissä vaeltavista.',215,NULL),
 (4549,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',19,'9He\nlupaavat näille vapautta, vaikka itse ovat turmeluksen\norjia: kenen voittama ihminen on, sen orja hän on.',215,NULL),
 (4550,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',20,'0Jos he ovat päässeet eroon maailman saastasta\ntullessaan tuntemaan meidän Herramme ja Vapahtajamme\nJeesuksen Kristuksen mutta sitten taas vajoavat siihen\nja joutuvat sen valtaan, heidän tilansa on lopussa\npahempi kuin alussa.',215,NULL),
 (4551,'Anonymous','2008-10-17 16:34:26','Anonymous','2008-10-17 16:34:26',21,'1Parempi olisi heille ollut,\netteivät olisi lainkaan oppineet tuntemaan\nvanhurskauden tietä, kuin että he sen opittuaan\nkääntävät selkänsä pyhälle käskylle, jonka ovat\nvastaanottaneet.',215,NULL),
 (4552,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',22,'2Heidän on käynyt juuri niin kuin\nsananlaskussa sanotaan: \"Koira palaa oksennukselleen\",\nja: \"Pesty sika rypee rapakossa.\" ',215,NULL),
 (4553,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',1,'Rakkaat ystävät! Tämä on jo toinen kirje, jonka\nteille kirjoitan. Molemmissa olen herätellyt ja\nmuistuttanut teitä, joiden ajatukset ovat vilpittömiä,',216,NULL),
 (4554,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',2,'pitämään mielessänne pyhien profeettojen ennalta\nlausumat sanat sekä apostoleilta saamanne Herran ja\nVapahtajan käskyn. ',216,NULL),
 (4555,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',3,'Erityisesti tietäkää, että lopun aikoina tulee\npilkkaajia, jotka ovat omien himojensa vietävinä.',216,NULL),
 (4556,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',4,'He sanovat pilkaten: \"Onko hän muka tullut, niin\nkuin lupasi? Isämme ovat nukkuneet pois, ja kaikki on\nentisellään, niin kuin on ollut maailman luomisesta\nasti.\"',216,NULL),
 (4557,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',5,'Tieten tahtoen he unohtavat, että jo aikojen\nalussa olivat olemassa taivaat ja maa, jotka Jumalan\nsana vedestä ja vedellä loi ja jotka se piti koossa.',216,NULL),
 (4558,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',6,'Vedellä ja sanalla sen ajan maailma myös\nhävitettiin vedenpaisumuksessa.',216,NULL),
 (4559,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',7,'Ja sen saman sanan\nvarassa ovat nykyiset taivaat ja nykyinen maa tulen\ntuhoa odottamassa. Niitä säilytetään sitä päivää\nvarten, jona tuomio tulee ja jumalattomat tuhotaan. ',216,NULL),
 (4560,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',8,'Mutta älkää te, rakkaat ystävät, unohtako tätä:\nHerralle yksi päivä on kuin tuhat vuotta ja tuhat\nvuotta kuin yksi päivä.',216,NULL),
 (4561,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',9,'Ei Herra vitkastele\ntäyttäessään lupaustaan, vaikka hän joidenkin mielestä\non myöhässä. Päinvastoin: hän on kärsivällinen teitä\nkohtaan, koska ei halua kenenkään tuhoutuvan vaan\ntahtoo, että kaikki kääntyisivät. ',216,NULL),
 (4562,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',10,'0Herran päivä tulee kuin varas. Sinä päivänä\ntaivaat katoavat jylisten, taivaankappaleet palavat ja\nhajoavat. Silloin paljastuu maa ja kaikki, mitä\nihminen on maan päällä saanut aikaan.',216,NULL),
 (4563,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',11,'1Koska tämä\nkaikki näin hajoaa, millaisia onkaan pyhässä elämässä\nja hurskaissa teoissa oltava niiden,',216,NULL),
 (4564,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',12,'2jotka\nodottavat Jumalan päivää ja jouduttavat sen tuloa --\ntuon päivän, joka saa taivaat liekehtien hajoamaan ja\ntaivaankappaleet sulamaan kuumuudesta.',216,NULL),
 (4565,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',13,'3Mutta\nmeillä on hänen lupauksensa, ja siihen luottaen me\nodotamme uusia taivaita ja uutta maata, joissa\nvanhurskaus vallitsee. ',216,NULL),
 (4566,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',14,'4Rakkaat ystävät! Kun te tätä kaikkea odotatte,\npyrkikää siihen, että Herra aikanaan voisi havaita\nteidät puhtaiksi ja moitteettomiksi ja teillä olisi\nrauha.',216,NULL),
 (4567,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',15,'5Ja pitäkää sitä pelastuksenanne, että Herra\non kärsivällinen. Näinhän myös rakas veljemme Paavali\non hänelle annetun viisauden mukaisesti teille\nkirjoittanut.',216,NULL),
 (4568,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',16,'6Samaa hän sanoo kaikissa\nkirjeissään, joissa puhuu näistä asioista. Niissä\ntosin on yhtä ja toista vaikeatajuista, mitä\ntietämättömät ja haihattelevat ihmiset omaksi\ntuhokseen vääristelevät niin kuin kaikkia muitakin\npyhiä kirjoituksia. ',216,NULL),
 (4569,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',17,'7Kun siis te, rakkaat ystävät, olette ennalta\nselvillä tästä kaikesta, niin olkaa varuillanne. Älkää\nantako jumalattomien johtaa itseänne harhaan, niin\nettä putoatte omalta varmalta pohjaltanne.',216,NULL),
 (4570,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',18,'8Kasvakaa meidän Herramme Jeesuksen Kristuksen\narmossa ja tuntemisessa. Hänelle kunnia nyt ja\niankaikkisuuden päivään asti. Aamen.  ',216,NULL),
 (4571,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',1,'Mikä on alusta alkaen ollut, minkä olemme kuulleet,\nminkä omin silmin nähneet, mitä katselleet ja käsin\nkoskettaneet, siitä me puhumme: elämän Sanasta.',217,NULL),
 (4572,'Anonymous','2008-10-17 16:34:27','Anonymous','2008-10-17 16:34:27',2,'Elämä ilmestyi, me olemme nähneet sen, ja siitä me\ntodistamme. Me ilmoitamme teille iankaikkisen elämän,\njoka oli Isän luona ja ilmestyi meille.',217,NULL),
 (4573,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',3,'Minkä\nolemme nähneet ja kuulleet, sen me myös teille\njulistamme, jotta teilläkin olisi yhteys meihin.\nMeillä on yhteys Isään ja hänen Poikaansa Jeesukseen\nKristukseen.',217,NULL),
 (4574,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',4,'Tämän me kirjoitamme, jotta ilomme\ntulisi täydelliseksi.     ',217,NULL),
 (4575,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',1,'Rakkaat lapset! Kirjoitan tämän teille, jotta ette\ntekisi syntiä. Jos joku kuitenkin syntiä tekee, meillä\non Isän luona puolustaja, joka on vanhurskas: Jeesus\nKristus.',218,NULL),
 (4576,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',2,'Hän on meidän syntiemme sovittaja, eikä\nvain meidän vaan koko maailman.       ',218,NULL),
 (4577,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',14,'4Lapseni, minä kirjoitan teille: te olette oppineet\ntuntemaan Isän. Isät, minä kirjoitan teille: te olette\noppineet tuntemaan hänet, joka on ollut alusta asti.\nNuoret, minä kirjoitan teille: te olette voimakkaita,\nJumalan sana pysyy teissä, ja te olette voittaneet\nPahan.     ',218,NULL),
 (4578,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',22,'2Kuka sitten on valehtelija, ellei se, joka kieltää\nJeesuksen olevan Kristus? Sellainen ihminen on\nantikristus, niin Isän kuin Pojan kieltäjä.',218,NULL),
 (4579,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',23,'3Joka\nkieltää Pojan, hänellä ei ole Isääkään, mutta joka\ntunnustaa Pojan, hänellä on myös Isä.',218,NULL),
 (4580,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',24,'4Pysyköön\nsiis teissä se, minkä jo alussa olette kuulleet. Jos\nse, minkä olette kuulleet alusta asti, pysyy teissä,\ntekin pysytte Pojassa ja Isässä.',218,NULL),
 (4581,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',25,'5Ja tämän hän itse\non meille luvannut: iankaikkisen elämän. ',218,NULL),
 (4582,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',26,'6Tämän olen kirjoittanut teille niistä, jotka\ntahtovat johtaa teitä harhaan.',218,NULL),
 (4583,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',27,'7Teissä kyllä pysyy\nse voitelu, jonka olette Pyhältä saaneet, ettekä te\ntarvitse kenenkään opetusta. Hänen Henkensä opettaa\nteitä kaikessa ja on tosi, hänessä ei ole petosta.\nNiin kuin hän on teitä opettanut, niin pysykää\nhänessä.   ',218,NULL),
 (4584,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',1,'Katsokaa, kuinka suurta rakkautta Isä on meille\nosoittanut: me olemme saaneet Jumalan lapsen nimen, ja\nhänen lapsiaan me myös olemme. Tästä syystä maailma ei\nmeitä tunne, eihän se tunne häntäkään.',219,NULL),
 (4585,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',2,'Rakkaat\nystävät, jo nyt me olemme Jumalan lapsia, mutta vielä\nei ole käynyt ilmi, mitä meistä tulee. Sen me\ntiedämme, että kun se käy ilmi, meistä tulee hänen\nkaltaisiaan, sillä me saamme nähdä hänet sellaisena\nkuin hän on.',219,NULL),
 (4586,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',3,'Jokainen, joka näin panee toivonsa\nhäneen, pitää itsensä puhtaana, niin kuin hän on\npuhdas ja pyhä.   ',219,NULL),
 (4587,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',7,'Lapseni, älkää antako kenenkään johtaa itseänne\nharhaan. Joka tekee Jumalan tahdon, on vanhurskas,\nniin kuin Kristus on vanhurskas.',219,NULL),
 (4588,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',8,'Joka tekee syntiä,\non Paholaisesta, sillä Paholainen on tehnyt syntiä\nalusta asti. Juuri sitä varten Jumalan Poika tuli\nmaailmaan, että hän tekisi tyhjäksi Paholaisen teot.',219,NULL),
 (4589,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',9,'Yksikään Jumalasta syntynyt ei tee syntiä, sillä\nJumalan siemen pysyy hänessä. Hän ei voi tehdä syntiä,\nkoska on syntynyt Jumalasta.',219,NULL),
 (4590,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',10,'0Tästä käy ilmi, ketkä\novat Jumalan, ketkä Paholaisen lapsia: se, joka ei tee\nJumalan tahtoa, ei ole Jumalasta, ei siis myöskään se,\njoka ei rakasta veljeään.   ',219,NULL),
 (4591,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',13,'3Älkää ihmetelkö, veljet, jos maailma teitä vihaa.',219,NULL),
 (4592,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',14,'4Me rakastamme veljiämme, ja siitä me tiedämme\nsiirtyneemme kuolemasta elämään. Joka ei rakasta,\npysyy kuoleman vallassa.',219,NULL),
 (4593,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',15,'5Jokainen, joka vihaa\nveljeään, on murhaaja, ja te tiedätte, ettei\niankaikkinen elämä voi pysyä yhdessäkään murhaajassa.',219,NULL),
 (4594,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',16,'6Siitä me olemme oppineet tuntemaan rakkauden, että\nJeesus antoi henkensä meidän puolestamme. Samoin\nolemme me velvolliset panemaan henkemme alttiiksi\nveljiemme puolesta.',219,NULL),
 (4595,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',17,'7Jos joku, jonka toimeentulo on\nturvattu, näkee veljensä kärsivän puutetta mutta\nsulkee häneltä sydämensä, kuinka Jumalan rakkaus voisi\npysyä hänessä?   ',219,NULL),
 (4596,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',21,'1Rakkaat ystävät, jos sydämemme ei meitä syytä, me\nvoimme rohkeasti lähestyä Jumalaa.',219,NULL),
 (4597,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',22,'2Ja mitä\npyydämmekin, sen me häneltä saamme, koska noudatamme\nhänen käskyjään ja teemme sitä, mikä on hänen mielensä\nmukaista. ',219,NULL),
 (4598,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',23,'3Tämä on hänen käskynsä: meidän tulee uskoa hänen\nPoikaansa Jeesukseen Kristukseen ja rakastaa toinen\ntoistamme, niin kuin hän on meitä käskenyt.',219,NULL),
 (4599,'Anonymous','2008-10-17 16:34:28','Anonymous','2008-10-17 16:34:28',24,'4Joka\npitää hänen käskynsä, pysyy Jumalassa, ja Jumala pysyy\nhänessä. Ja sen, että hän pysyy meissä, me tiedämme\nHengestä, jonka hän on meille antanut. ',219,NULL),
 (4600,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',1,'Rakkaat ystävät, älkää uskoko kaikkia henkiä.\nKoetelkaa ne, tutkikaa, ovatko ne Jumalasta, sillä\nmaailmassa on liikkeellä monia vääriä profeettoja.',220,NULL),
 (4601,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',2,'Tästä te tunnette Jumalan Hengen: jokainen henki,\njoka tunnustaa Jeesuksen Kristuksen ihmiseksi, lihaan\ntulleeksi, on Jumalasta.',220,NULL),
 (4602,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',3,'Yksikään henki, joka\nkieltää Jeesuksen, ei ole Jumalasta. Sellainen henki\non Antikristuksen henki, jonka te olette kuulleet\nolevan tulossa ja joka jo on maailmassa. ',220,NULL),
 (4603,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',4,'Te, lapseni, olette Jumalasta, ja te olette\nvoittaneet nuo väärät profeetat, sillä se, joka on\nteissä, on suurempi kuin se, joka on maailmassa.',220,NULL),
 (4604,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',5,'He\novat maailmasta. Maailmasta on se, mitä he puhuvat, ja\nmaailma kuuntelee heitä.',220,NULL),
 (4605,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',6,'Me olemme Jumalasta. Joka\ntuntee Jumalan, kuuntelee meitä, mutta joka ei ole\nJumalasta, se ei meitä kuuntele. Tästä me tunnemme\ntotuuden hengen ja eksytyksen hengen.   ',220,NULL),
 (4606,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',11,'1Rakkaat ystävät! Kun Jumala on meitä näin\nrakastanut, tulee meidänkin rakastaa toisiamme.',220,NULL),
 (4607,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',12,'2Jumalaa ei kukaan ole koskaan nähnyt. Mutta jos me\nrakastamme toisiamme, Jumala pysyy meissä ja hänen\nrakkautensa on saavuttanut meissä päämääränsä. ',220,NULL),
 (4608,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',13,'3Me pysymme hänessä ja hän pysyy meissä; tämän me\ntiedämme siitä, että hän on antanut meille Henkeään.',220,NULL),
 (4609,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',14,'4Me olemme nähneet, että Isä on lähettänyt Poikansa\nmaailmaan pelastajaksi, ja siitä me todistamme.',220,NULL),
 (4610,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',15,'5Joka tunnustaa Jeesuksen Jumalan Pojaksi, hänessä\nJumala pysyy, ja hän pysyy Jumalassa.',220,NULL),
 (4611,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',16,'6Me olemme\noppineet tuntemaan Jumalan rakkauden kaikkia meitä\nkohtaan ja uskomme siihen.     ',220,NULL),
 (4612,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',1,'Jokainen, joka uskoo, että Jeesus on Kristus, on\nsyntynyt Jumalasta, ja jokainen, joka rakastaa isää,\nrakastaa myös hänen lastaan.',221,NULL),
 (4613,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',2,'Siitä me tiedämme\nrakastavamme Jumalan lapsia, että rakastamme Jumalaa\nja noudatamme hänen käskyjään.',221,NULL),
 (4614,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',3,'Sitähän Jumalan\nrakastaminen on, että pidämme hänen käskynsä, eivätkä\nne ole raskaita noudattaa.',221,NULL),
 (4615,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',4,'Kaikki, mikä on\nsyntyisin Jumalasta, voittaa maailman. Ja tämä on se\nvoitto, tämä on maailman voittanut: meidän uskomme. ',221,NULL),
 (4616,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',5,'Kuka sitten voittaa maailman, ellei se, joka uskoo,\nettä Jeesus on Jumalan Poika?   ',221,NULL),
 (4617,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',9,'Hyväksymmehän me ihmistenkin todistuksen, ja\nJumalan todistus on paljon painavampi. Tämä on Jumalan\ntodistus: hän on todistanut Pojastaan.',221,NULL),
 (4618,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',10,'0Jokaisella,\njoka uskoo Jumalan Poikaan, on tämä todistus\nsisimmässään. Mutta jokainen, joka ei Jumalan\ntodistusta usko, tekee hänestä valehtelijan, hän kun\nei usko itse Jumalaa.',221,NULL),
 (4619,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',11,'1Ja tämä on se todistus:\nJumala on antanut meille iankaikkisen elämän, ja tämä\nelämä on hänen Pojassaan.',221,NULL),
 (4620,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',12,'2Jolla on Poika, sillä on\nelämä. Jolla ei Jumalan Poikaa ole, sillä ei ole\nelämää.       ',221,NULL),
 (4621,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',20,'0Me tiedämme myös, että Jumalan Poika on tullut ja\nantanut meille ymmärryksen, jotta tuntisimme hänet,\njoka on Todellinen. Ja tässä Todellisessa me elämme,\nkun olemme hänen pojassaan Jeesuksessa Kristuksessa.\nHän on tosi Jumala ja iankaikkinen elämä. ',221,NULL),
 (4622,'Anonymous','2008-10-17 16:34:29','Anonymous','2008-10-17 16:34:29',21,'1Lapseni, pysykää erossa vääristä jumalista! ',221,NULL),
 (4623,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',9,'Jokainen, joka ei pysy Kristuksen opetuksessa vaan\ntuo siihen jotakin lisää, on vailla Jumalaa.\nJokaisella, joka tässä opetuksessa pysyy, on sekä Isä\nettä Poika.',222,NULL),
 (4624,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',10,'0Jos joku tulee teidän luoksenne eikä\nopeta tällä tavoin, älkää ottako häntä kotiinne\nälkääkä edes tervehtikö häntä.',222,NULL),
 (4625,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',11,'1Se, joka lausuu\nhänet tervetulleeksi, osallistuu hänen pahoihin\ntekoihinsa.   ',222,NULL),
 (4626,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',13,'3Valitun sisaresi*lapset lähettävät sinulle\ntervehdyksensä. ',222,NULL),
 (4627,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',2,'Rakas ystävä, toivon, että sinulle kuuluu joka\nsuhteessa hyvää ja että olet terve; sisäisestihän\nsinulle kuuluu hyvää.',223,NULL),
 (4628,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',3,'Ilahduin suuresti, kun tänne\ntulleet veljet kertoivat sinun elävän totuudessa, niin\nkuin asia onkin.',223,NULL),
 (4629,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',4,'Mikään ei tuota minulle suurempaa\niloa kuin se, että kuulen lasteni elävän totuudessa.     ',223,NULL),
 (4630,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',11,'1Rakas ystävä, älä ota esimerkkiä pahasta, vaan\nhyvästä. Joka tekee hyvää, on Jumalasta, mutta se,\njoka tekee pahaa, ei ole Jumalaa nähnyt.',223,NULL),
 (4631,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',12,'2Demetrioksesta kaikki ovat kertoneet pelkkää\nhyvää; itse totuuskin on hänen puolellaan. Samoin\ntodistamme me, ja sinä tiedät, että meidän\ntodistuksemme on tosi.   ',223,NULL),
 (4632,'Anonymous','2008-10-17 16:34:30','Anonymous','2008-10-17 16:34:30',15,'5Rauha sinulle! Ystävät lähettävät sinulle\ntervehdyksensä. Tervehdi ystäviä, jokaista erikseen. ',223,NULL),
 (4633,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',5,'Vaikka te kaiken tiedättekin, tahdon silti\nmuistuttaa teitä siitä, että Herra kyllä ensin pelasti\nkansansa Egyptistä mutta tuhosi sitten ne, jotka eivät\nuskoneet.',224,NULL),
 (4634,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',6,'Ja niitä enkeleitä, jotka eivät pitäneet\nkiinni korkeasta asemastaan vaan hylkäsivät omat\nasuinsijansa, hän pitää pimeydessä ikuisissa kahleissa\nsuurta tuomion päivää varten.',224,NULL),
 (4635,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',7,'Niin kävi myös\nSodoman ja Gomorran ja muiden sen seudun kaupunkien,\njotka samalla tavoin antautuivat siveettömyyden ja\nluonnonvastaisten himojen valtaan: niiden saama\nrangaistus on varoittava esimerkki, joka muistuttaa\nikuisesta tulesta. ',224,NULL),
 (4636,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',8,'Yhtä kaikki nämä hurmahenget tekevät samalla\ntavoin. He saastuttavat ruumiinsa, halveksivat Herraa\nja hänen valtaansa ja herjaavat henkivaltoja.',224,NULL),
 (4637,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',9,'Ei\nedes ylienkeli Mikael, kiistellessään Mooseksen\nruumiista Paholaisen kanssa, rohjennut herjata eikä\ntuomita tätä, vaan sanoi: \"Nuhdelkoon Herra sinua!\"',224,NULL),
 (4638,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',10,'0Mutta nämä ihmiset herjaavat sitä, mitä eivät\ntunne, ja sillä, minkä he luonnostaan, järjettömien\neläinten tavoin tuntevat, he saattavat itsensä\nturmioon.',224,NULL),
 (4639,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',11,'1Voi heitä! He ovat lähteneet Kainin\ntielle, palkan toivossa he ovat harhautuneet Bileamin\njäljille, ja Korahin tavoin he ovat kapinoinnillaan\nhankkineet itselleen tuhon.',224,NULL),
 (4640,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',12,'2Nämä ihmiset ovat\nhäpeätahroja teidän rakkaudenaterioillanne, kun he\nmässäilevät mistään piittaamatta. He ovat paimenia,\njotka huolehtivat vain itsestään. He ovat pilviä,\njotka tuuli vie ja jotka eivät vettä anna, puita,\njotka syksyllä eivät kanna hedelmää, kahdesti\nkuolleita, juuriltaan repäistyjä,',224,NULL),
 (4641,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',13,'3pärskyviä meren\naaltoja, joiden vaahtoharjana on heidän häpeänsä,\nradaltaan harhautuneita tähtiä, joille ikuisiksi\najoiksi on varattuna synkin pimeys. ',224,NULL),
 (4642,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',14,'4Heistä on myös ennustanut Henok, Aadamista\nseitsemäs: \"Katso, Herra tulee pyhiensä tuhatlukuisen\njoukon kanssa',224,NULL),
 (4643,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',15,'5ja panee jokaisen tuomiolle. Hän\nrankaisee kaikkia jumalattomia jokaisesta teosta,\njonka he jumalattomuudessaan ovat tehneet, ja kaikista\njulkeista puheista, joita nuo jumalattomat syntiset\novat häntä vastaan puhuneet.\"',224,NULL),
 (4644,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',16,'6Nuo ihmiset ovat\nosaansa tyytymättömiä valittelijoita, jotka elävät\nhimojensa vallassa. Heidän puheensa ovat suurellisia,\nja omaa etuaan tavoitellen he mielistelevät ihmisiä.   ',224,NULL),
 (4645,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',20,'0Rakkaat ystävät, rakentakaa te edelleen elämäänne\npyhimmän uskonne perustalle, Pyhässä Hengessä\nrukoillen.',224,NULL),
 (4646,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',21,'1Pysykää Jumalan rakkaudessa ja\nodottakaa, että Herramme Jeesus Kristus armossaan\njohtaa meidät iankaikkiseen elämään.',224,NULL),
 (4647,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',22,'2Armahtakaa\nniitä, jotka epäilevät.',224,NULL),
 (4648,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',23,'3Pelastakaa ne, jotka\nvoitte, temmatkaa heidät tulesta! Toisia taas säälikää\nkavahtaen heitä, inhotkaa jopa heidän ruumiinsa\ntahraamaa vaatetta. ',224,NULL),
 (4649,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',24,'4Hänen, jolla on voima varjella teidät lankeamasta\nja riemun vallitessa asettaa teidät tahrattomina\nkirkkautensa eteen,',224,NULL),
 (4650,'Anonymous','2008-10-17 16:34:31','Anonymous','2008-10-17 16:34:31',25,'5ainoan Jumalan, hänen, joka on\npelastanut meidät Jeesuksen Kristuksen, meidän\nHerramme kautta, hänen on kirkkaus, korkeus, voima ja\nvalta, ennen aikojen alkua, nyt ja ikuisesti. Aamen. ',224,NULL),
 (4651,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',1,'Jeesuksen Kristuksen ilmestys, jonka Jumala antoi\nhänelle näyttääkseen palvelijoilleen, mitä pian on\ntapahtuva. Jumala lähetti enkelinsä ja ilmoitti kaiken tämän\npalvelijalleen Johannekselle,',225,NULL),
 (4652,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',2,'joka nyt omalla\ntodistuksellaan vahvistaa Jumalan sanan ja Jeesuksen\nKristuksen todistuksen, kaiken minkä on nähnyt. ',225,NULL),
 (4653,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',3,'Autuas se, joka tämän toisille lukee, autuaat ne,\njotka kuulevat nämä ennussanat ja ottavat varteen sen,\nmitä tähän kirjaan on kirjoitettu, sillä hetki on\nlähellä.   Hänelle, joka rakastaa meitä ja on verellään\nvapauttanut meidät synneistämme,',225,NULL),
 (4654,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',6,'hänelle, joka on\ntehnyt meidät kuningassuvuksi ja Jumalan, Isänsä,\npapeiksi, hänelle kunnia ja valta aina ja ikuisesti!\nAamen.   ',225,NULL),
 (4655,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',8,'\"Minä olen A ja O, alku ja loppu\"*, sanoo Herra\nJumala, hän, joka on, joka oli ja joka on tuleva,\nKaikkivaltias.   ',225,NULL),
 (4656,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',12,'2Käännyin nähdäkseni, mikä ääni minulle puhui, ja\nkun käännyin, näin seitsemän kultajalkaista lamppua',225,NULL),
 (4657,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',13,'3ja lamppujen keskellä hahmon, joka oli ihmisen\nkaltainen. Hänellä oli yllään pitkä viitta ja rinnan\nympärillä kultainen vyö.',225,NULL),
 (4658,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',14,'4Hänen päänsä ja hiuksensa\nhohtivat valkoisina kuin valkoinen villa, kuin lumi,\nja hänen silmänsä olivat kuin tulen liekit.',225,NULL),
 (4659,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',15,'5Hänen\njalkansa välkehtivät kuin sulatusuunissa hehkuva\npronssi, ja hänen äänensä oli kuin suurten vesien\npauhu.',225,NULL),
 (4660,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',16,'6Hänellä oli oikeassa kädessään seitsemän\ntähteä, hänen suustaan pisti esiin kaksiteräinen,\nterävä miekka, ja hänen kasvonsa olivat kuin loistava\nkeskipäivän aurinko.',225,NULL),
 (4661,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',17,'7Hänet nähdessäni minä vaivuin\nhänen jalkoihinsa kuin kuollut. Mutta hän laski oikean kätensä päälleni ja sanoi: \"Älä pelkää. Minä olen ensimmäinen ja viimeinen,',225,NULL),
 (4662,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',18,'8iäti elävä. Minä olin kuollut, mutta nyt minä\nelän, elän aina ja ikuisesti. Minulla on kuoleman ja\ntuonelan avaimet.',225,NULL),
 (4663,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',19,'9Kirjoita siis, mitä näet: se,\nmikä on nyt, ja se, mikä on myöhemmin tapahtuva.',225,NULL),
 (4664,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',20,'0Sinä näit seitsemän tähteä oikeassa kädessäni ja\nseitsemän kultajalkaista lamppua, ja niiden salaisuus\non tämä: seitsemän tähteä ovat seitsemän seurakunnan\nenkelit, ja seitsemän lamppua ovat ne seitsemän\nseurakuntaa. ',225,NULL),
 (4665,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',1,'\"Kirjoita Efesoksen seurakunnan enkelille: \"Näin sanoo hän, jolla on oikeassa kädessään seitsemän\ntähteä ja joka käyskentelee seitsemän kultajalkaisen\nlampun keskellä: ',226,NULL),
 (4666,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',2,'\"Minä tiedän sinun tekosi, sinun vaivannäkösi ja\nkestävyytesi. Minä tiedän, ettet voi sietää pahoja\nihmisiä. Sinä olet koetellut niitä, jotka sanovat\nitseään apostoleiksi vaikka eivät ole, ja olet\nhavainnut heidät valehtelijoiksi.',226,NULL),
 (4667,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',3,'Kestävyyttäkin\nsinulla on, olethan joutunut moneen vaivaan minun\nnimeni tunnustamisen tähden etkä ole antanut periksi.',226,NULL),
 (4668,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',4,'Mutta sitä en sinussa hyväksy, että olet luopunut\nensi ajan rakkaudesta.',226,NULL),
 (4669,'Anonymous','2008-10-17 16:34:33','Anonymous','2008-10-17 16:34:33',5,'Muista siis, mistä olet\nlangennut, käänny ja palaa tekemään ensi ajan tekoja.\nEllet tee parannusta, minä tulen luoksesi ja siirrän\nlamppusi paikaltaan.',226,NULL),
 (4670,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',6,'Siinä teet kuitenkin oikein,\nettä inhoat nikolaiittojen*tekoja, joita minäkin\ninhoan. ',226,NULL),
 (4671,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',7,'\"Jolla on korvat, se kuulkoon, mitä Henki sanoo\nseurakunnille. Sen, joka voittaa, minä annan syödä\nelämän puusta, joka on Jumalan puutarhassa.   \"Näin sanoo ensimmäinen ja viimeinen, hän, joka oli\nkuollut mutta heräsi eloon: ',226,NULL),
 (4672,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',9,'\"Minä tiedän sinun ahdinkosi ja köyhyytesi --\nsinun, joka kuitenkin olet rikas. Minä tiedän, miten\nsinua herjaavat nuo, jotka sanovat itseään\njuutalaisiksi vaikka eivät ole -- Saatanan synagogaa\nhe ovat!',226,NULL),
 (4673,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',10,'0Älä yhtään pelkää sitä, mitä joudut\nkärsimään. Saatana on sulkeva muutamia teistä\nvankeuteen, jotta teitä koeteltaisiin, ja te joudutte\nahdinkoon kymmeneksi päiväksi. Ole uskollinen\nkuolemaan asti, niin minä annan sinulle\nvoitonseppeleeksi elämän. ',226,NULL),
 (4674,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',11,'1\"Jolla on korvat, se kuulkoon, mitä Henki sanoo\nseurakunnille. Siihen, joka voittaa, ei toinen kuolema\nkajoa.   \"Näin sanoo hän, jolla on kaksiteräinen, terävä\nmiekka: ',226,NULL),
 (4675,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',13,'3\"Minä tiedän, missä sinä asut: siellä, missä\nSaatanalla on valtaistuimensa. Silti tunnustat\nhorjumatta minun nimeäni etkä ole kieltänyt uskoasi\nminuun, et edes silloin, kun uskollinen todistajani\nAntipas surmattiin teidän luonanne, siellä missä\nSaatana asuu.',226,NULL),
 (4676,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',14,'4Mutta kaikkea en sinussa hyväksy:\nLuonasi on sellaisia, jotka seuraavat Bileamin oppia,\ntuon miehen, joka opetti Balakille, miten virittää\nisraelilaisille ansa ja vietellä heidät syömään\nepäjumalille uhrattua lihaa ja harjoittamaan\nsiveettömyyttä.',226,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (4677,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',15,'5Heitä on sinunkin luonasi, noita\njotka seuraavat nikolaiittojen opetusta.',226,NULL),
 (4678,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',16,'6Käänny\nsiis! Ellet tee parannusta, minä tulen kohta luoksesi\nja käyn heitä vastaan, isken heitä suuni miekalla. ',226,NULL),
 (4679,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',17,'7\"Jolla on korvat, se kuulkoon, mitä Henki sanoo\nseurakunnille. Sille, joka voittaa, minä annan\nkätkettyä mannaa ja valkoisen kiven, ja siihen kiveen\non kirjoitettu uusi nimi, jota ei tunne kukaan muu\nkuin sen nimen saaja.   \"Näin sanoo Jumalan Poika, jolla on silmät kuin tulen\nliekit ja jonka jalat välkehtivät kuin hehkuva\npronssi: ',226,NULL),
 (4680,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',19,'9\"Minä tiedän sinun tekosi, sinun rakkautesi ja\nuskosi, palvelumielesi ja kestävyytesi. Viime aikoina\nolet tehnyt vielä enemmän kuin alussa teit.',226,NULL),
 (4681,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',20,'0Mutta\nsitä en sinussa hyväksy, että suvaitset tuota naista,\ntuota Isebeliä, joka sanoo itseään profeetaksi ja\njohtaa opetuksellaan minun palvelijoitani harhaan,\nharjoittamaan siveettömyyttä ja syömään epäjumalille\nuhrattua lihaa.',226,NULL),
 (4682,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',21,'1Olen antanut hänelle aikaa\nkääntyä, mutta hän ei halua luopua siveettömästä\nelämänmenostaan.',226,NULL),
 (4683,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',22,'2Voi häntä! Minä suistan hänet\nsairasvuoteeseen ja kaikki hänen rakastajansa ankaraan\nahdinkoon, elleivät he luovu hänen tavoistaan.',226,NULL),
 (4684,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',23,'3Hänen lapsensa minä surmaan rutolla. Kaikki\nseurakunnat tulevat tietämään, kuka minä olen: minä\ntutkin sydämet ja ajatukset ja annan jokaiselle teistä\nhänen tekojensa mukaan. ',226,NULL),
 (4685,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',24,'4\"Mutta teille muille tyatiralaisille, jotka ette\nseuraa tuota oppia ettekä ole vihkiytyneet Saatanan\nsalaisuuksiin, niin kuin sanonta kuuluu, teille minä\nsanon: Mitään uutta taakkaa en pane teidän\nkannettavaksenne.',226,NULL),
 (4686,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',25,'5Pitäkää kiinni siitä mitä teillä\non, kunnes minä tulen. ',226,NULL),
 (4687,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',26,'6\"Sille, joka voittaa, sille, joka loppuun asti\nnoudattaa minun tahtoani, minä annan vallan hallita\nkansoja.',226,NULL),
 (4688,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',27,'7Hän paimentaa niitä rautaisella sauvalla,\nlyö pirstoiksi kuin saviastioita.',226,NULL),
 (4689,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',28,'8Hän saa saman\nvallan, jonka minä olen Isältäni saanut; minä annan\nhänelle aamutähden. ',226,NULL),
 (4690,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',29,'9\"Jolla on korvat, se kuulkoon, mitä Henki sanoo\nseurakunnille. ',226,NULL),
 (4691,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',1,'\"Sardeksen seurakunnan enkelille kirjoita: \"Näin sanoo hän, jolla on Jumalan seitsemän henkeä ja\nseitsemän tähteä: \"Minä tiedän sinun tekosi. Sinä olet elävien\nkirjoissa, mutta sinä olet kuollut.',227,NULL),
 (4692,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',2,'Herää ja\nvahvista sitä, mikä vielä on jäljellä, sitä, mikä jo\noli kuolemaisillaan. Olen havainnut, että tekosi eivät\ntäytä minun Jumalani vaatimusta.',227,NULL),
 (4693,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',3,'Muista, kuinka\nkuulit sanan ja otit sen vastaan, tarkkaa sitä ja tee\nparannus. Ellet ole hereillä, minä tulen kuin varas,\nyllätän sinut hetkellä, jota et aavista. ',227,NULL),
 (4694,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',4,'\"Muutamia sinulla sentään on Sardeksessa, jotka\neivät ole tahranneet vaatteitaan. He saavat\nkäyskennellä minun seurassani valkeissa vaatteissa; he\novat sen arvoisia.',227,NULL),
 (4695,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',5,'Se, joka voittaa, saa ylleen\nvalkeat vaatteet, enkä minä pyyhi hänen nimeään elämän\nkirjasta, vaan tunnustan hänet omakseni Isäni ja hänen\nenkeliensä edessä. ',227,NULL),
 (4696,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',6,'\"Jolla on korvat, se kuulkoon, mitä Henki sanoo\nseurakunnille.   \"Näin sanoo Pyhä ja Tosi, Daavidin avaimen haltija,\nhän, jonka avaamaa ovea ei kukaan sulje, hän, jonka\nsulkemaa ovea ei kukaan avaa: ',227,NULL),
 (4697,'Anonymous','2008-10-17 16:34:34','Anonymous','2008-10-17 16:34:34',8,'\"Minä tiedän sinun tekosi. Edessäsi on nyt avoin\novi, minä olen sen avannut, eikä kukaan voi sitä\nsulkea. Sinun voimasi ovat vähäiset, mutta sinä olet\nottanut sanani varteen etkä ole kieltänyt nimeäni.',227,NULL),
 (4698,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',9,'Niinpä minä annan sinulle väkeä Saatanan\nsynagogasta, niitä valehtelijoita, jotka sanovat\nitseään juutalaisiksi vaikka eivät ole. Minä panen\nheidät tulemaan luoksesi ja kumartumaan maahan\njalkojesi edessä, ja he ymmärtävät silloin, että sinä\nolet minun valittuni.',227,NULL),
 (4699,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',10,'0Sinä olet tarkoin seurannut\nsanaani ja kestänyt, ja siksi minä puolestani tarkoin\nvarjelen sinua ja pelastan sinut siitä koetuksen\nhetkestä, joka kohtaa koko maailman ja panee\nkoetteelle maan asukkaat. ',227,NULL),
 (4700,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',11,'1\"Minä tulen pian. Pidä kiinni siitä mitä sinulla\non, ettei kukaan vie voitonseppelettäsi.',227,NULL),
 (4701,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',12,'2Siitä,\njoka voittaa, minä teen pylvään Jumalani temppeliin,\neikä hän joudu milloinkaan lähtemään sieltä. Minä\nkirjoitan häneen Jumalani nimen ja Jumalani kaupungin\nnimen, uuden Jerusalemin, joka laskeutuu taivaasta\nJumalani luota, ja oman uuden nimeni. ',227,NULL),
 (4702,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',13,'3\"Jolla on korvat, se kuulkoon, mitä Henki sanoo\nseurakunnille.   \"Näin sanoo Aamen, uskollinen ja luotettava todistaja,\nJumalan luomakunnan alku: ',227,NULL),
 (4703,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',15,'5\"Minä tiedän sinun tekosi: sinä et ole kylmä etkä\nkuuma. Kunpa olisitkin joko kylmä tai kuuma!',227,NULL),
 (4704,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',16,'6Mutta\nsinä olet haalea, et kuuma etkä kylmä, ja siksi minä\noksennan sinut suustani. ',227,NULL),
 (4705,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',17,'7\"Sinä kerskut, että olet rikas, entistäkin\nvarakkaampi, etkä tarvitse enää mitään. Et tajua, mikä\ntodella olet: surkea ja säälittävä, köyhä, sokea ja\nalaston.',227,NULL),
 (4706,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',18,'8Annan sinulle neuvon: osta minulta\ntulessa puhdistettua kultaa, niin tulet rikkaaksi,\nosta valkoiset vaatteet ja pue ne yllesi, niin\nhäpeällinen alastomuutesi peittyy, osta silmävoidetta\nja voitele silmäsi, niin näet.',227,NULL),
 (4707,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',19,'9Jokaista, jota\nrakastan, minä nuhtelen ja kuritan. Tee siis parannus,\nluovu penseydestäsi!',227,NULL),
 (4708,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',20,'0Minä seison ovella ja\nkolkutan. Jos joku kuulee minun ääneni ja avaa oven,\nminä tulen hänen luokseen, ja me aterioimme yhdessä,\nminä ja hän. ',227,NULL),
 (4709,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',21,'1\"Sen, joka voittaa, minä annan istua kanssani\nvaltaistuimellani, niin kuin minäkin olen voittoni\njälkeen asettunut Isäni kanssa hänen\nvaltaistuimelleen. ',227,NULL),
 (4710,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',22,'2\"Jolla on korvat, se kuulkoon, mitä Henki sanoo\nseurakunnille.\" ',227,NULL),
 (4711,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',1,'Sitten näin tämän: taivaan ovi oli auki, ja kuului\nsama ääni, jonka olin edellisellä kerralla kuullut --\nkuin olisi torveen puhallettu. Se sanoi minulle:\n\"Nouse tänne! Minä näytän sinulle, mitä tämän jälkeen\non tapahtuva.\" ',228,NULL),
 (4712,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',2,'Siinä samassa olin Hengen vallassa. Näin, että\ntaivaassa oli valtaistuin ja sillä valtaistuimella\nistui joku,',228,NULL),
 (4713,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',3,'joka loisti kuin jaspis ja karneoli.\nValtaistuinta ympäröi sateenkaaren kaltainen\nsmaragdinvärinen hohde.',228,NULL),
 (4714,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',4,'Tämän valtaistuimen\nympärillä oli kaksikymmentäneljä valtaistuinta, ja\nniillä istui kaksikymmentäneljä vanhinta, joilla oli\nyllään valkeat vaatteet ja päässään kultaseppele.',228,NULL),
 (4715,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',5,'Valtaistuimesta lähti salamoita ja kuin ukkosen\njyly. Sen edessä oli seitsemän soihtua, Jumalan\nseitsemän henkeä,',228,NULL),
 (4716,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',6,'ja sen edessä oli myös ikään kuin\nlasinen, kristallin tavoin kimmeltävä meri. Valtaistuimen edessä ja ympärillä oli neljä olentoa,\njotka sekä edestä että takaa olivat täynnä välkehtiviä\nsilmiä.',228,NULL),
 (4717,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',7,'Yksi muistutti leijonaa, toinen nuorta\nhärkää, kolmannella oli kuin ihmiskasvot, ja neljäs\nnäytti lentävältä kotkalta.',228,NULL),
 (4718,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',8,'Näillä neljällä\nolennolla oli kullakin kuusi siipeä, jotka olivat\nmolemmilta puolin täynnä välkehtiviä silmiä.\nTaukoamatta, päivin öin, nuo olennot lausuvat:  ',228,NULL),
 (4719,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',9,'Aina kun olennot ylistävät, kunnioittavat ja\nkiittävät häntä, joka istuu valtaistuimellaan ja joka\nelää aina ja ikuisesti,',228,NULL),
 (4720,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',10,'0nuo kaksikymmentäneljä\nvanhinta heittäytyvät maahan hänen valtaistuimensa\neteen ja osoittavat hänelle kunnioitustaan -- hänelle,\njoka elää aina ja ikuisesti. He asettavat seppeleensä\nvaltaistuimen eteen ja lausuvat:  ',228,NULL),
 (4721,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',1,'Minä näin, että valtaistuimella istuvan oikeassa\nkädessä oli kirjakäärö. Se oli kirjoitettu täyteen\nmolemmin puolin ja sinetöity seitsemällä sinetillä.',229,NULL),
 (4722,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',2,'Ja minä näin mahtavan enkelin, joka kuulutti\nkovalla äänellä: \"Kuka on arvollinen avaamaan kirjan\nja murtamaan sen sinetit?\"',229,NULL),
 (4723,'Anonymous','2008-10-17 16:34:35','Anonymous','2008-10-17 16:34:35',3,'Mutta ei ollut ketään,\nei taivaassa, ei maan päällä eikä maan alla, joka\nolisi kyennyt avaamaan kirjan tai edes katsomaan\nsiihen.',229,NULL),
 (4724,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',4,'Minä itkin katkerasti, kun ei löytynyt\nketään, joka olisi ollut arvollinen avaamaan kirjan\ntai katsomaan siihen.',229,NULL),
 (4725,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',5,'Mutta yksi vanhimmista sanoi\nminulle: \"Älä itke. Juudan heimon leijona, Daavidin\njuuriverso, on saanut voiton! Hän avaa kirjan ja\nmurtaa kaikki seitsemän sinettiä.\" ',229,NULL),
 (4726,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',6,'Ja minä näin, että valtaistuimen ja neljän olennon\nedessä vanhinten keskellä seisoi karitsa. Se oli kuin\nteurastettu, ja sillä oli seitsemän sarvea ja\nseitsemän silmää -- ne Jumalan seitsemän henkeä, jotka\non lähetetty kaikkialle maailmaan.',229,NULL),
 (4727,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',7,'Se astui\nlähemmäs ja otti kirjan valtaistuimella istuvan\noikeasta kädestä. ',229,NULL),
 (4728,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',8,'Kun Karitsa otti kirjan, ne neljä olentoa ja\nkaksikymmentäneljä vanhinta heittäytyivät hänen\neteensä. Vanhimmilla oli kaikilla harppu ja kultamalja\ntäynnä uhrisavua, pyhien rukouksia. ',229,NULL),
 (4729,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',9,'He lauloivat uuden laulun:   ',229,NULL),
 (4730,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',11,'1Valtaistuimen, olentojen ja vanhinten ympärillä\nnäin suuren joukon enkeleitä ja kuulin heidän äänensä.\nHeitä oli lukemattomia, kymmenentuhatta kertaa\nkymmenentuhatta ja tuhat kertaa tuhat,',229,NULL),
 (4731,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',12,'2ja he\nlausuivat kovalla äänellä:  ',229,NULL),
 (4732,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',13,'3Ja minä kuulin, kuinka kaikki luodut taivaassa,\nmaan päällä, maan alla ja meressä, kaikki mitä niissä\non, lausuivat:  ',229,NULL),
 (4733,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',14,'4Ne neljä olentoa sanoivat: \"Aamen\", ja vanhimmat\nheittäytyivät kasvoilleen ja osoittivat\nkunnioitustaan. ',229,NULL),
 (4734,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',1,'Minä näin, kuinka Karitsa avasi ensimmäisen\nseitsemästä sinetistä, ja kuulin yhden neljästä\nolennosta sanovan ukkosen tavoin jylisevällä äänellä:\n\"Tule!\"',230,NULL),
 (4735,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',2,'Ja siinä samassa näin valkoisen hevosen, ja\nsillä, joka istui hevosen selässä, oli jousi. Hänelle\nannettiin seppele, ja seppelöitynä voittajana hän\nlähti uusia voittoja kohti. ',230,NULL),
 (4736,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',3,'Kun Karitsa avasi toisen sinetin, kuulin toisen\nolennon sanovan: \"Tule!\"',230,NULL),
 (4737,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',4,'Nyt tuli esiin toinen,\ntulipunainen hevonen. Sille, joka istui hevosen\nselässä, annettiin valta riistää rauha maan päältä ja\npanna ihmiset tappamaan toisiaan. Hänelle annettiin\niso miekka. ',230,NULL),
 (4738,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',5,'Kun Karitsa avasi kolmannen sinetin, kuulin\nkolmannen olennon sanovan: \"Tule!\" Siinä samassa näin\nmustan hevosen, ja sillä, joka istui hevosen selässä,\noli kädessään vaaka.',230,NULL),
 (4739,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',6,'Minä kuulin äänen, joka tuntui\ntulevan noiden neljän olennon keskeltä, ja se sanoi:\n\"Mitta vehnää denaarilla, ohraa kolme mittaa.*Öljyyn\nja viiniin älä koske.\" ',230,NULL),
 (4740,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',7,'Kun Karitsa avasi neljännen sinetin, kuulin\nneljännen olennon sanovan: \"Tule!\"',230,NULL),
 (4741,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',8,'Siinä samassa\nnäin tuhkanharmaan hevosen. Se, joka istui hevosen\nselässä, oli nimeltään Kuolema, ja sen jäljessä tuli\nTuonela. Ratsastajille annettiin valta neljänteen\nosaan maata, ne saivat tappaa miekalla, nälällä ja\nrutolla ja jättää loput villipetojen kynsiin. ',230,NULL),
 (4742,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',9,'Kun Karitsa avasi viidennen sinetin, näin alttarin\nalla niiden sielut, jotka oli tapettu Jumalan sanan ja\noman todistuksensa tähden.',230,NULL),
 (4743,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',10,'0Ne huusivat kovalla\näänellä: \"Kuinka kauan kestää, Valtias, sinä Pyhä ja\nTosi, ennen kuin lausut tuomiosi ja kostat meidän\nveremme maan asukkaille?\"',230,NULL),
 (4744,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',11,'1Silloin niille kullekin\nannettiin valkea vaate ja niitä kehotettiin odottamaan\nrauhassa vielä vähän aikaa, kunnes niitä ja niiden\nveljiä, niiden lailla surmattuja Jumalan palvelijoita,\nolisi täysi määrä. ',230,NULL),
 (4745,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',12,'2Minä näin, kuinka Karitsa avasi kuudennen sinetin.\nMaa järkkyi ja vavahteli, aurinko pimeni mustaksi kuin\nsuruvaate, ja kuu muuttui kauttaaltaan\nverenpunaiseksi.',230,NULL),
 (4746,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',13,'3Taivaan tähdet putosivat maahan,\nniin kuin viikunat varisevat puusta myrskyn sitä\nravistellessa.',230,NULL),
 (4747,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',14,'4Taivas hävisi näkyvistä kuin kirja,\njoka kääritään kokoon, ja kaikki vuoret ja saaret\nsiirtyivät paikaltaan.',230,NULL),
 (4748,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',15,'5Maan kuninkaat, ylimykset\nja sotajoukkojen johtajat, rikkaat ja mahtavat miehet\nja kaikki muut, niin orjat kuin vapaat, piiloutuivat\nluoliin ja vuorten rotkoihin',230,NULL),
 (4749,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',16,'6ja huusivat vuorille\nja kallioille: \"Kaatukaa päällemme, kätkekää meidät\nvaltaistuimella istuvan katseelta ja Karitsan vihalta!',230,NULL),
 (4750,'Anonymous','2008-10-17 16:34:36','Anonymous','2008-10-17 16:34:36',17,'7Heidän vihansa suuri päivä on tullut -- kuka voi\nsen kestää?\" ',230,NULL),
 (4751,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',1,'Tämän jälkeen näin neljä enkeliä, jotka seisoivat\nmaan neljällä kulmalla ja pidättelivät maan neljää\ntuulta, ettei mikään niistä puhaltaisi yli maan ja\nmeren eikä vasten ainoatakaan puuta.',231,NULL),
 (4752,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',2,'Ja vielä näin\nyhden enkelin, joka nousi idästä kantaen elävän\nJumalan sinettiä. Hän huusi kovalla äänellä noille\nneljälle enkelille, joille oli annettu valta hävittää\nmaata ja merta:',231,NULL),
 (4753,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',3,'\"Älkää hävittäkö maata, älkää merta\nälkääkä puita, ennen kuin olemme painaneet sinetin\nmeidän Jumalamme palvelijoiden otsaan.\" ',231,NULL),
 (4754,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',4,'Minä kuulin myös sinetillä merkittävien määrän.\nSinetin sai sataneljäkymmentäneljätuhatta, ja heitä\noli kaikista Israelin heimoista:',231,NULL),
 (4755,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',5,'Juudan heimosta\nsai sinetin kaksitoistatuhatta, Ruubenin heimosta\nkaksitoistatuhatta, Gadin heimosta kaksitoistatuhatta,',231,NULL),
 (4756,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',6,'Asserin heimosta kaksitoistatuhatta, Naftalin\nheimosta kaksitoistatuhatta, Manassen heimosta\nkaksitoistatuhatta,',231,NULL),
 (4757,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',7,'Simeonin heimosta\nkaksitoistatuhatta, Leevin heimosta\nkaksitoistatuhatta, Isaskarin heimosta\nkaksitoistatuhatta,',231,NULL),
 (4758,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',8,'Sebulonin heimosta\nkaksitoistatuhatta, Joosefin heimosta\nkaksitoistatuhatta ja Benjaminin heimosta\nkaksitoistatuhatta. ',231,NULL),
 (4759,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',9,'Tämän jälkeen näin suuren kansanjoukon, niin\nsuuren, ettei kukaan kyennyt sitä laskemaan. Siinä oli\nihmisiä kaikista maista, kaikista kansoista ja\nheimoista, ja he puhuivat kaikkia kieliä. He seisoivat\nvaltaistuimen ja Karitsan edessä yllään valkeat\nvaatteet ja kädessään palmunoksa',231,NULL),
 (4760,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',10,'0ja huusivat\nkovalla äänellä:  ',231,NULL),
 (4761,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',11,'1Kaikki enkelit seisoivat valtaistuimen, vanhinten\nja neljän olennon ympärillä, ja he heittäytyivät\nkasvoilleen valtaistuimen eteen ja osoittivat\nJumalalle kunnioitustaan',231,NULL),
 (4762,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',12,'2sanoen:   ',231,NULL),
 (4763,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',13,'3Yksi vanhimmista kysyi minulta: \"Keitä nämä\nvalkeavaatteiset ovat? Mistä he ovat tulleet?\"',231,NULL),
 (4764,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',14,'4Minä vastasin: \"Herra, sinä sen tiedät.\" Hän sanoi\nminulle:  ',231,NULL),
 (4765,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',1,'Kun Karitsa avasi seitsemännen sinetin, taivaassa\nsyntyi hiljaisuus, ja sitä kesti puolen tunnin verran.',232,NULL),
 (4766,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',2,'Minä näin, kuinka niille seitsemälle enkelille,\njotka seisoivat Jumalan edessä, annettiin seitsemän\ntorvea. ',232,NULL),
 (4767,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',3,'Vielä tuli yksi enkeli, joka asettui alttarin\nääreen kädessään kultainen suitsutusastia. Hänelle\nannettiin paljon suitsuketta, jotta hän valtaistuimen\nedessä olevalla kultaisella alttarilla liittäisi\nsuitsutusuhrin kaikkien pyhien rukouksiin.',232,NULL),
 (4768,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',4,'Niin\nnousi suitsutuksen savu enkelin kädestä Jumalan eteen\nyhdessä pyhiltä tulevien rukousten kanssa.',232,NULL),
 (4769,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',5,'Enkeli\notti suitsutusastian, täytti sen alttarin tulella ja\nsinkosi tulen alas maan päälle, ja ukkonen jylisi,\nsalamat leimusivat ja maa järähteli.   ',232,NULL),
 (4770,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',7,'Ensimmäinen enkeli puhalsi torveensa. Silloin tuli\nrakeita ja tulenlieskoja ja niiden seassa verta, ja ne\niskivät maahan. Kolmannes maasta paloi, kolmannes\npuista paloi, ja samoin paloi kaikki vihanta ruoho. ',232,NULL),
 (4771,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',8,'Toinen enkeli puhalsi torveensa. Silloin syöksyi\nmereen jokin, mikä oli suuri kuin vuori ja liekehti\ntulta. Kolmannes merestä muuttui vereksi,',232,NULL),
 (4772,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',9,'kolmannes\nmeressä elävistä eläimistä kuoli ja kolmannes\nlaivoista tuhoutui. ',232,NULL),
 (4773,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',10,'0Kolmas enkeli puhalsi torveensa. Silloin putosi\ntaivaalta suuri, soihtuna palava tähti, ja maahan\npudotessaan se täytti kolmanneksen virroista ja vesien\nlähteistä.',232,NULL),
 (4774,'Anonymous','2008-10-17 16:34:37','Anonymous','2008-10-17 16:34:37',11,'1Tähden nimi oli Koiruoho. Kolmannes\nvesistä muuttui karvaaksi kuin koiruoho, ja monet\nihmiset kuolivat vedestä, koska se oli tullut\nmyrkylliseksi. ',232,NULL),
 (4775,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',12,'2Neljäs enkeli puhalsi torveensa. Silloin kolmannes\nauringosta, kolmannes kuusta ja kolmannes tähdistä\nsammui, niin että ne pimenivät kolmanneksen. Päivä\nmenetti kolmanneksen valostaan, ja samoin synkkeni yö. ',232,NULL),
 (4776,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',13,'3Silloin näin kotkan, joka lensi korkealla taivaan\nlaella ja huusi kovalla äänellä: \"Voi! Voi! Voi niitä,\njotka asuvat maan päällä! Vielä tulevat torvet\nsoimaan, vielä puhaltaa torveensa kolme enkeliä.\" ',232,NULL),
 (4777,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',1,'Viides enkeli puhalsi torveensa. Silloin minä näin,\nettä taivaasta oli pudonnut maahan tähti. Sille\nannettiin syvyyden kuilun avain,',233,NULL),
 (4778,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',2,'ja se avasi tuon\nsyvyyteen vievän kuilun. Kuilusta nousi savua kuin\nsuuresta uunista, ja savu pimensi auringon ja ilman.',233,NULL),
 (4779,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',3,'Savusta levisi maan päälle heinäsirkkoja, ja niille\nannettiin sama valta kuin on skorpioneilla maan\npäällä.',233,NULL),
 (4780,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',4,'Niille sanottiin, etteivät ne saa\nvahingoittaa kedon ruohoa eivätkä mitään muutakaan\nvihantaa eivätkä yhtään puuta, vaan ainoastaan niitä\nihmisiä, joilla ei ole otsassaan Jumalan sinettiä.',233,NULL),
 (4781,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',5,'Heitäkään ne eivät saaneet tappaa, ainoastaan\npiinata viiden kuukauden ajan. Tuska, jonka ne\nihmiselle tuottavat, on samanlainen kuin skorpionin\npiston tuottama.',233,NULL),
 (4782,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',6,'Sinä aikana ihmiset etsivät\nkuolemaa mutta eivät löydä, he haluavat kuolla, mutta\nkuolema pakenee heitä. ',233,NULL),
 (4783,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',7,'Heinäsirkat olivat näöltään kuin taisteluun\nvarustettuja hevosia. Niillä oli päässään kullalta\nkimaltavat seppeleet, niiden kasvot olivat kuin\nihmisen kasvot,',233,NULL),
 (4784,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',8,'ja niillä oli naisen tukan\nkaltainen tukka. Niiden hampaat olivat kuin leijonan\nhampaat,',233,NULL),
 (4785,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',9,'ja niillä oli rintapanssarit, jotka olivat\nkuin rautaa. Niiden siivistä lähti samanlainen jyminä\nkuin sotavaunuista, joita monet hevoset täyttä laukkaa\nvetävät taisteluun.',233,NULL),
 (4786,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',10,'0Niillä oli pyrstö ja pistin\nkuin skorpioneilla, ja pyrstöllään ne saivat tehdä\nihmisille pahaa viiden kuukauden ajan.',233,NULL),
 (4787,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',11,'1Kuninkaanaan niillä oli syvyyden enkeli,\nhepreankieliseltä nimeltään Abaddon, kreikankieliseltä\nApollyon.* ',233,NULL),
 (4788,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',12,'2Ensimmäinen voi-huuto on ohi. Mutta sen jälkeen\ntulee vielä kaksi huutoa. ',233,NULL),
 (4789,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',13,'3Kuudes enkeli puhalsi torveensa. Silloin kuulin,\nkuinka Jumalan edessä olevan kultaisen alttarin\nsarvista tuli ääni,',233,NULL),
 (4790,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',14,'4joka sanoi kuudennelle\ntorveaan soittaneelle enkelille: \"Päästä irti ne neljä\nenkeliä, jotka ovat kahleissa suuren Eufratvirran\nrannalla.\"',233,NULL),
 (4791,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',15,'5Niin päästettiin irti nämä neljä\nenkeliä, jotka olivat valmiina tätä tuntia, päivää,\nkuukautta ja vuotta varten: niiden oli määrä surmata\nkolmannes ihmiskunnasta.',233,NULL),
 (4792,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',16,'6Niillä oli ratsuväkeä\nkaksikymmentätuhatta kertaa kymmenentuhatta sotilasta;\nminä kuulin joukkojen lukumäärän. ',233,NULL),
 (4793,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',17,'7Tällaisia olivat näyssäni ratsut ja ratsastajat:\nniillä oli tulipunaiset, savunsiniset ja\nrikinkeltaiset rintapanssarit, ja hevosilla oli kuin\nleijonan pää ja niiden suusta suitsi tulta, savua ja\nrikin katkua.',233,NULL),
 (4794,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',18,'8Näihin kolmeen vitsaukseen --\ntuleen, savuun ja rikin katkuun, jotka suitsivat\nhevosten suusta -- kuoli kolmannes ihmiskuntaa.',233,NULL),
 (4795,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',19,'9Hevosten voima oli niiden suussa ja hännässä.\nHäntä oli kuin käärme, ja sillä oli käärmeen pää,\njolla se tuotti tuhoa. ',233,NULL),
 (4796,'Anonymous','2008-10-17 16:34:38','Anonymous','2008-10-17 16:34:38',20,'0Loput ihmiset, ne jotka eivät kuolleet näihin\nvitsauksiin, eivät kuitenkaan lakanneet palvomasta\nkättensä töitä. He kumarsivat yhä pahoja henkiä ja\nkullasta, hopeasta, pronssista, kivestä ja puusta\ntekemiään jumalankuvia, jotka eivät voi nähdä, eivät\nkuulla eivätkä kävellä.',233,NULL),
 (4797,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',21,'1He eivät kääntyneet, he\neivät luopuneet murhatöistään eivätkä taioistaan,\neivät siveettömästä elämästä eivätkä varastamisesta. ',233,NULL),
 (4798,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',1,'Sitten näin taas uuden, mahtavan enkelin, joka tuli\nalas taivaasta. Hänellä oli pukunaan pilvi ja hänen\npäänsä päällä oli sateenkaari, hänen kasvonsa olivat\nkuin aurinko ja hänen jalkansa kuin tulipatsaat,',234,NULL),
 (4799,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',2,'ja\nkädessään hänellä oli pieni avattu kirjakäärö. Hän\nlaski oikean jalkansa meren päälle, vasemman maalle',234,NULL),
 (4800,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',3,'ja huusi kovalla äänellä, niin kuin leijona olisi\närjynyt. Hänen huudettuaan puhui seitsemän ukkosta\njylisevällä äänellä.',234,NULL),
 (4801,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',4,'Kun nämä seitsemän ukkosta\nolivat jylisten puhuneet, minä rupesin kirjoittamaan,\nmutta silloin kuulin taivaasta äänen, joka sanoi:\n\"Sulje sinetillä se, mitä seitsemän ukkosta puhuivat,\nälä kirjoita sitä muistiin.\" ',234,NULL),
 (4802,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',5,'Enkeli, jonka näin seisovan meren ja maan päällä,\nkohotti oikean kätensä taivasta kohti',234,NULL),
 (4803,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',6,'ja vannoi\nhänen nimeensä, joka elää aina ja ikuisesti, hänen,\njoka on luonut taivaan, maan ja meren ja kaiken mitä\nniissä on. Hän vannoi: \"Aika on lopussa.',234,NULL),
 (4804,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',7,'Kun tulee\npäivä, jolloin seitsemäs enkeli kohottaa torvensa ja\npuhaltaa, on Jumalan salattu suunnitelma viety\npäätökseen, sen mukaisesti kuin hän on palvelijoilleen\nprofeetoille ilmoittanut.\" ',234,NULL),
 (4805,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',8,'Ääni, jonka olin taivaasta kuullut, puhui minulle\nuudestaan: \"Mene tuon enkelin luo, joka seisoo meren\nja maan päällä, ja ota avattu kirjakäärö, joka hänellä\non kädessään.\"',234,NULL),
 (4806,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',9,'Minä menin enkelin luo ja pyysin\nhäntä antamaan käärön minulle. Hän vastasi: \"Ota ja\nsyö se. Se karvastelee vatsassasi, mutta suussasi se\nmaistuu makealta kuin hunaja.\"',234,NULL),
 (4807,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',10,'0Minä otin käärön\nenkelin kädestä ja söin sen. Se maistui suussani\nmakealta kuin hunaja, mutta kun olin sen syönyt, alkoi\nvatsaani karvastella. ',234,NULL),
 (4808,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',11,'1Minulle sanottiin: \"Vieläkin sinun tulee ennustaa,\nmiten käy maailman kansoille, maille, kielille ja\nkuninkaille.\" ',234,NULL),
 (4809,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',1,'Sitten minulle annettiin mittakeppi, joka oli kuin\nsauva, ja minulle sanottiin: \"Mene mittaamaan Jumalan temppeli ja alttari ja laske\nne, jotka siellä rukoilevat.',235,NULL),
 (4810,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',2,'Temppelin ulompaa\nesipihaa älä kuitenkaan lue mukaan, jätä se\nmittaamatta, sillä se on annettu vieraiden kansojen\nhaltuun. Ne polkevat pyhää kaupunkia\nneljänkymmenenkahden kuukauden ajan. ',235,NULL),
 (4811,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',3,'\"Minä annan kahdelle todistajalleni tehtäväksi\njulistaa sanaani säkkivaatteeseen pukeutuneina tuhat\nkaksisataakuusikymmentä päivää.\" ',235,NULL),
 (4812,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',4,'Nämä todistajat ovat ne kaksi oliivipuuta ja kaksi\nlampunjalkaa, jotka seisovat maan Herran edessä.',235,NULL),
 (4813,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',5,'Jos joku aikoo tehdä heille pahaa, heidän suustaan\nlähtee tuli, joka nielee heidän vihollisensa. Näin on\nkuoleva jokainen, joka aikoo heille pahaa.',235,NULL),
 (4814,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',6,'Heillä\non valta sulkea taivas, niin ettei tule sadetta koko\nsinä aikana, jona he sanaa julistavat. Heillä on myös\nvalta muuttaa vedet vereksi ja piinata maata\nkaikenlaisilla vitsauksilla niin usein kuin haluavat. ',235,NULL),
 (4815,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',7,'Kun he ovat päättäneet todistajantyönsä, käy\nsyvyydestä nouseva peto taisteluun heitä vastaan, ja\nse voittaa heidät ja tappaa heidät.',235,NULL),
 (4816,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',8,'Heidän\nruumiinsa lojuvat sen suuren kaupungin kadulla, jota\nprofeettojen puheissa kutsutaan Sodomaksi ja Egyptiksi\nja jossa heidän Herransa ristiinnaulittiin.',235,NULL),
 (4817,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',9,'Kolmen\nja puolen päivän ajan käy ihmisiä eri kansoista,\nheimoista, kielistä ja maista katselemassa heidän\nruumiitaan, eikä niitä anneta haudata.',235,NULL),
 (4818,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',10,'0Maan\nasukkaat iloitsevat heidän kohtalostaan, pitävät\njuhlia ja lähettävät toisilleen lahjoja, sillä nuo\nkaksi profeettaa olivat pahoin vaivanneet maan\nasukkaita. ',235,NULL),
 (4819,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',11,'1Mutta noiden kolmen ja puolen päivän kuluttua tuli\nprofeettoihin elämän henki, jonka Jumala oli\nlähettänyt, ja he nousivat jaloilleen. Kauhu valtasi\nkaikki, jotka sen näkivät.',235,NULL),
 (4820,'Anonymous','2008-10-17 16:34:39','Anonymous','2008-10-17 16:34:39',12,'2He kuulivat, kuinka\ntaivaasta huudettiin kovalla äänellä: \"Nouskaa\ntänne!\", ja vihollistensa nähden profeetat nousivat\npilven kannattelemina taivaaseen.',235,NULL),
 (4821,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',13,'3Samalla \nhetkellä tuli ankara maanjäristys. Kymmenesosa\nkaupungista sortui, ja järistyksessä sai surmansa\nseitsemäntuhatta ihmistä. Muut kauhistuivat ja\ntaipuivat kunnioittamaan taivaan Jumalaa. ',235,NULL),
 (4822,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',14,'4Toinen voi-huuto on ohi. Mutta kolmas tulee pian.    ',235,NULL),
 (4823,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',16,'6Silloin ne kaksikymmentäneljä vanhinta, jotka\nistuivat Jumalan edessä valtaistuimillaan, lankesivat\nkasvoilleen ja osoittivat Jumalalle kunnioitustaan',235,NULL),
 (4824,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',17,'7sanoen:   ',235,NULL),
 (4825,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',19,'9Taivaassa avattiin Jumalan temppeli, niin että\ntemppelissä oleva liitonarkku voitiin nähdä. Salamat\nleimusivat, ukkonen jylisi, maa järähteli ja raemyrsky\nriehui. ',235,NULL),
 (4826,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',1,'Taivaalla näkyi suuri tunnusmerkki: nainen, jolla\noli pukunaan aurinko, kuu jalkojen alla ja pään päällä\nseppeleenä kaksitoista tähteä.',236,NULL),
 (4827,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',2,'Hän oli raskaana ja\nhuusi tuskissaan synnytyspolttojen vaivaamana. ',236,NULL),
 (4828,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',3,'Toinenkin tunnusmerkki näkyi taivaalla: suuri\ntulipunainen lohikäärme, jolla oli seitsemän päätä ja\nkymmenen sarvea ja kruunu kaikissa seitsemässä päässä.',236,NULL),
 (4829,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',4,'Pyrstöllään se pyyhkäisi pois kolmanneksen taivaan\ntähdistä ja sinkosi ne maahan. Lohikäärme asettui\nsynnyttävän naisen eteen nielaistakseen lapsen heti\nkun se syntyy. ',236,NULL),
 (4830,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',5,'Nainen synnytti lapsensa, pojan, joka on paimentava\nmaailman kansoja rautaisella sauvalla. Tämä lapsi\ntemmattiin Jumalan ja hänen valtaistuimensa luo,',236,NULL),
 (4831,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',6,'ja\nnainen pakeni autiomaahan. Jumala on sinne valmistanut\nhänelle turvapaikan, missä hänestä pidetään huolta\ntuhat kaksisataakuusikymmentä päivää. ',236,NULL),
 (4832,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',7,'Taivaassa syttyi sota. Mikael ja hänen enkelinsä\nkävivät taisteluun lohikäärmettä vastaan. Lohikäärme\nenkeleineen teki vastarintaa',236,NULL),
 (4833,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',8,'mutta kärsi tappion,\neikä sille ja sen joukolle ollut enää sijaa taivaassa.',236,NULL),
 (4834,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',9,'Tuo suuri lohikäärme, tuo muinaisaikojen käärme,\njota kutsutaan Paholaiseksi ja Saatanaksi, tuo koko\nihmiskunnan eksyttäjä, syöstiin maan päälle, ja samoin\nsyöstiin alas sen enkelit.',236,NULL),
 (4835,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',10,'0Minä kuulin, kuinka\ntaivaassa sanottiin kovalla äänellä:   ',236,NULL),
 (4836,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',13,'3Kun lohikäärme huomasi, että se oli syösty maahan,\nse lähti ajamaan takaa naista, joka oli pojan\nsynnyttänyt.',236,NULL),
 (4837,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',14,'4Mutta naiselle annettiin suuren\nkotkan siivet, jotta hän lentäisi turvapaikkaansa\nautiomaahan. Siellä hän on suojassa käärmeeltä, ja\nhänestä pidetään huolta vuosi, kaksi vuotta ja puoli\nvuotta.',236,NULL),
 (4838,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',15,'5Käärme syöksi kidastaan naisen perään\nvesivirran, jotta virta veisi hänet mennessään,',236,NULL),
 (4839,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',16,'6mutta maa auttoi naista: se avasi suunsa ja\nnielaisi virran, jonka lohikäärme oli syössyt\nkidastaan.',236,NULL),
 (4840,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',17,'7Lohikäärmeen raivo yltyi, ja se lähti\nsotimaan naisen muita lapsia vastaan, niitä, jotka\novat uskollisia Jumalan käskyille ja Jeesuksen todistukselle. ',236,NULL),
 (4841,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',18,'8Lohikäärme seisahtui meren rannalle. ',236,NULL),
 (4842,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',1,'Minä näin, kuinka merestä nousi peto. Sillä oli\nkymmenen sarvea ja seitsemän päätä, ja kaikissa\nkymmenessä sarvessa oli kruunu ja jokaiseen päähän oli\nkirjoitettu herjaava nimi.',237,NULL),
 (4843,'Anonymous','2008-10-17 16:34:40','Anonymous','2008-10-17 16:34:40',2,'Peto, jonka näin,\nmuistutti leopardia, mutta sillä oli jalat kuin\nkarhulla ja sen kita oli kuin leijonan kita.\nLohikäärme antoi sille voimansa ja valtaistuimensa ja\nsuuren vallan.',237,NULL),
 (4844,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',3,'Minä näin, että yksi pedon päistä\noli saanut surmaniskun mutta haava oli parantunut. Koko maailma ihmetteli petoa ja lähti seuraamaan sitä,',237,NULL),
 (4845,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',4,'ja kaikki kumarsivat lohikäärmettä, joka oli\nantanut pedolle sellaisen vallan. He kumarsivat myös\npetoa ja sanoivat:   ',237,NULL),
 (4846,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',5,'Pedolle annettiin suu, joka herjaa ja puhuu suuria\nsanoja, ja se sai tämän vallan neljänkymmenenkahden\nkuukauden ajaksi.',237,NULL),
 (4847,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',6,'Niin se avasi suunsa ja alkoi\nherjata Jumalaa. Se herjasi hänen nimeään ja hänen\nasuinsijaansa ja niitä, jotka asuvat taivaassa.',237,NULL),
 (4848,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',7,'Sille annettiin myös lupa käydä taisteluun pyhiä\nvastaan ja voittaa heidät, ja niin sen valtaan\nannettiin kaikki heimot, kansat, kielet ja maat.',237,NULL),
 (4849,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',8,'Kaikki maan asukkaat kumartavat sitä -- kaikki ne,\njoiden nimi ei maailman luomisesta alkaen ole ollut\nkirjoitettuna teurastetun Karitsan elämänkirjaan. ',237,NULL),
 (4850,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',9,'Jolla on korvat, se kuulkoon:   ',237,NULL),
 (4851,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',11,'1Sitten näin, kuinka toinen peto nousi maan\nuumenista. Sillä oli kaksi sarvea, kuin karitsan\nsarvet, mutta se puhui kuin lohikäärme.',237,NULL),
 (4852,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',12,'2Se käyttää\nensimmäisen pedon puolesta tämän koko valtaa ja panee\nmaan ja sen asukkaat kumartamaan ensimmäistä petoa,\nsitä, jonka kuolinhaava oli parantunut.',237,NULL),
 (4853,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',13,'3Se tekee\nsuuria tunnustekoja ja saa tulen lyömään taivaasta\nmaahan ihmisten nähden.',237,NULL),
 (4854,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',14,'4Niillä tunnusteoilla,\njoita sillä on valta tehdä ensimmäisen pedon nimissä,\nse johtaa harhaan kaikki maan asukkaat, niin että saa\nheidät tekemään patsaan sen pedon kunniaksi, jota on\nisketty miekalla mutta joka on vironnut henkiin.',237,NULL),
 (4855,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',15,'5Se on saanut vallan antaa pedon kuvalle hengen,\nniin että kuva jopa kykenee puhumaan, ja se on myös\nsaanut vallan tappaa kaikki, jotka eivät kumarra pedon\nkuvaa.',237,NULL),
 (4856,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',16,'6Se pakottaa kaikki, pienet ja suuret,\nrikkaat ja köyhät, vapaat ja orjat, ottamaan oikeaan\nkäteensä tai otsaansa merkin.',237,NULL),
 (4857,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',17,'7Kenenkään ei ole\nlupa ostaa eikä myydä mitään, ellei hänellä ole tätä\nmerkkiä, joka on pedon nimi tai sen nimen luku*. ',237,NULL),
 (4858,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',18,'8Tässä vaaditaan viisautta. Se, jolla on\nymmärrystä, laskekoon pedon nimen lukuarvon: se on\nerään ihmisen luku, ja se luku on\nkuusisataakuusikymmentäkuusi. ',237,NULL),
 (4859,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',1,'Sitten näin tämän: Karitsa seisoi Siionin vuorella,\nja hänen kanssaan oli sataneljäkymmentäneljätuhatta\nihmistä, joiden otsaan oli kirjoitettu hänen nimensä\nja hänen Isänsä nimi.',238,NULL),
 (4860,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',2,'Minä kuulin taivaasta äänen,\njoka oli kuin suurten vesien pauhu tai kuin ukkosen\njylinä. Ääni, jonka kuulin, oli myös kuin harppujen\nhelinää ja harpunsoittajien laulua.',238,NULL),
 (4861,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',3,'Valtaistuimen\nedessä ja neljän olennon ja vanhinten edessä\nlaulettiin uutta laulua, jota ei voinut oppia kukaan\nmuu kuin nuo sataneljäkymmentäneljätuhatta, jotka on\nostettu maan päältä.',238,NULL),
 (4862,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',4,'He eivät ole tahranneet\nitseään naisten kanssa vaan ovat pysyneet puhtaina\nkuin neitsyet, ja he seuraavat Karitsaa, minne hän\nmeneekin. Heidät on ostettu ihmisten joukosta\nesikoislahjaksi Jumalalle ja Karitsalle.',238,NULL),
 (4863,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',5,'Valhetta\nei heidän suustaan ole kuultu, he ovat puhtaita ja\nvirheettömiä.   ',238,NULL),
 (4864,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',8,'Hänen jäljessään tuli toinen enkeli, joka kuulutti:\n\"Kukistunut, kukistunut on suuri Babylon, tuo portto,\njoka iljetyksillään on vietellyt kaikki kansat juomaan\nvihan viiniä.\" ',238,NULL),
 (4865,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',9,'Näiden kahden jäljessä tuli vielä kolmas enkeli,\njoka kuulutti kovalla äänellä: \"Se, joka kumartaa\npetoa ja sen kuvaa ja ottaa otsaansa tai käteensä sen\nmerkin,',238,NULL),
 (4866,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',10,'0joutuu yhtä lailla juomaan Jumalan vihan\nviiniä, joka laimentamattomana on kaadettu Jumalan\nvihan maljaan. Häntä kidutetaan tulessa ja rikin\nkatkussa pyhien enkelien ja Karitsan edessä.',238,NULL),
 (4867,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',11,'1Tulesta, joka ihmisiä kiduttaa, nousee savu aina\nja ikuisesti. Heillä ei ole päivän, ei yön lepoa -- ei\nniillä, jotka kumartavat petoa ja sen kuvaa, eikä\nkenelläkään, joka ottaa pedon nimen merkikseen.',238,NULL),
 (4868,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',12,'2Tässä kysytään pyhiltä kestävyyttä, niiltä jotka\nnoudattavat Jumalan käskyjä ja uskovat Jeesukseen.\" ',238,NULL),
 (4869,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',13,'3Minä kuulin taivaasta äänen, joka sanoi:\n\"Kirjoita: Autuaita ne, jotka tästä lähtien kuolevat\nHerran omina. He ovat autuaita, sanoo Henki. He saavat\nlevätä vaivoistaan, sillä heidän tekonsa seuraavat\nheitä.\"   ',238,NULL),
 (4870,'Anonymous','2008-10-17 16:34:41','Anonymous','2008-10-17 16:34:41',17,'7Taivaan temppelistä tuli taas enkeli, ja\nhänelläkin oli terävä sirppi.',238,NULL),
 (4871,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',18,'8Myös alttarista tuli\nenkeli, se jolla on tuli vallassaan, ja hän huusi\nkovalla äänellä sille enkelille, jolla oli terävä\nsirppi: \"Pane terävä sirppisi työhön ja korjaa tertut\nmaan viinitarhasta. Rypäleet ovat kypsyneet.\"',238,NULL),
 (4872,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',19,'9Silloin enkeli heitti sirppinsä maan päälle,\nkorjasi sadon maan viinitarhasta ja heitti\nrypäletertut suureen altaaseen, Jumalan vihan\nviinikuurnaan.',238,NULL),
 (4873,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',20,'0Rypäleet poljettiin altaassa\nkaupungin ulkopuolella, ja verta pursui niin, että\nveri vielä tuhannen kuudensadan stadionmitan*päässä\nulottui hevosen kuolaimiin asti. ',238,NULL),
 (4874,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',1,'Minä näin taas taivaalla suuren ja ihmeellisen\ntunnusmerkin: seitsemän enkeliä, seitsemän vitsauksen\ntoimeenpanijat. Nämä vitsaukset ovat viimeiset, niihin\ntyhjentyy Jumalan viha. ',239,NULL),
 (4875,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',2,'Minä näin kuin kristallisen meren, joka välkehti\ntulta. Tuon kristallimeren rannalla seisoivat pedosta\nvoiton saaneet, ne, jotka eivät olleet kumartaneet sen\nkuvaa eivätkä ottaneet merkkiä, jossa on sen nimen\nluku. Heillä oli käsissään Jumalan harput,',239,NULL),
 (4876,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',3,'ja he\nlauloivat Mooseksen, Jumalan palvelijan, ja Karitsan\nlaulua:   ',239,NULL),
 (4877,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',5,'Tämän jälkeen minä näin, kuinka taivaan temppeli,\ntodistuksen teltta, avattiin',239,NULL),
 (4878,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',6,'ja temppelistä tuli\nulos seitsemän enkeliä, seitsemän vitsauksen\ntoimeenpanijat. Heidän pukunsa oli puhtaan valkoista\npellavaa, ja rinnan ympärillä heillä oli kultainen\nvyö.',239,NULL),
 (4879,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',7,'Yksi neljästä olennosta antoi näille\nseitsemälle enkelille seitsemän kultaista maljaa,\njotka olivat täynnä Jumalan vihaa, hänen, joka elää\naina ja ikuisesti.',239,NULL),
 (4880,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',8,'Jumalan kirkkaus ja voima täytti\ntemppelin savulla, niin ettei kukaan voinut mennä\nsisälle temppeliin, ennen kuin nuo seitsemän enkeliä\nolivat panneet seitsemän vitsausta täytäntöön. ',239,NULL),
 (4881,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',1,'Minä kuulin, kuinka temppelistä sanottiin kovalla\näänellä seitsemälle enkelille: \"Menkää! Tyhjentäkää\nJumalan vihan seitsemän maljaa maan päälle.\" ',240,NULL),
 (4882,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',2,'Ensimmäinen enkeli lähti ja tyhjensi maljansa maan\npäälle. Silloin tuli pahoja märkiviä haavoja niihin\nihmisiin, joilla oli pedon merkki ja jotka kumarsivat\nsen kuvaa. ',240,NULL),
 (4883,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',3,'Toinen enkeli tyhjensi maljansa mereen. Meri\nmuuttui kuin kuolleen vereksi, ja kaikki meressä elävä\nkuoli. ',240,NULL),
 (4884,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',4,'Kolmas enkeli tyhjensi maljansa virtoihin ja vesien\nlähteisiin, ja ne muuttuivat vereksi.',240,NULL),
 (4885,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',5,'Minä kuulin,\nkuinka vesien enkeli sanoi:  ',240,NULL),
 (4886,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',7,'Sitten kuulin alttarin sanovan:   ',240,NULL),
 (4887,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',8,'Neljäs enkeli tyhjensi maljansa aurinkoon, ja\naurinko sai vallan korventaa ihmisiä tulellaan.',240,NULL),
 (4888,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',9,'Ihmiset paahtuivat kovassa helteessä mutta\nherjasivat Jumalaa, jonka vallassa nämä vitsaukset\nolivat. He eivät kääntyneet, eivät antaneet kunniaa\nJumalalle. ',240,NULL),
 (4889,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',10,'0Viides enkeli tyhjensi maljansa pedon\nvaltaistuimelle, ja pedon valtakunta pimeni. Ihmiset\npurivat tuskissaan kielensä verille',240,NULL),
 (4890,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',11,'1mutta\nherjasivat taivaan Jumalaa tuskansa ja haavojensa\ntähden. He eivät kääntyneet, eivät luopuneet pahoista\nteoistaan. ',240,NULL),
 (4891,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',12,'2Kuudes enkeli tyhjensi maljansa suureen\nEufratvirtaan. Virran vesi kuivui, ja niin aukeni tie\nidän kuninkaille.',240,NULL),
 (4892,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',13,'3Minä näin, kuinka lohikäärmeen\nsuusta, pedon suusta ja väärän profeetan suusta lähti\nkolme saastaista henkeä, jotka olivat sammakon\nnäköisiä.',240,NULL),
 (4893,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',14,'4Ne ovat pahoja henkiä, jotka tekevät\nihmeitä, ja ne lähtivät kokoamaan maanpiirin kaikki\nkuninkaat taisteluun, joka käydään Jumalan,\nKaikkivaltiaan, suurena päivänä. ',240,NULL),
 (4894,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',15,'5\"Minä tulen kuin varas. Autuas se, joka valvoo ja\npitää huolen vaatteistaan, ettei joutuisi häpeään,\nkulkemaan alasti kaikkien nähden.\" ',240,NULL),
 (4895,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',16,'6Henget kokosivat kuninkaat paikkaan, jonka nimi on\nhepreaksi Harmageddon*. ',240,NULL),
 (4896,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',17,'7Seitsemäs enkeli tyhjensi maljansa ilmaan. Silloin\nkuului temppelin valtaistuimelta mahtava ääni, joka\nsanoi: \"Hetki on tullut.\"',240,NULL),
 (4897,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',18,'8Salamat leimusivat,\nukkonen jylisi, ja tuli ankara maanjäristys, niin\nankara järistys, ettei sellaista ole ollut koko sinä\naikana, jonka ihminen on ollut maan päällä.',240,NULL),
 (4898,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',19,'9Suuri\nkaupunki hajosi kolmeen osaan, ja joka maassa\nkaupungit sortuivat. Jumala ei ollut unohtanut suurta\nBabylonia, vaan se joutui juomaan täyden maljan hänen\nvihansa ja vimmansa viiniä.',240,NULL),
 (4899,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',20,'0Kaikki saaret\nkatosivat, eikä vuoria näkynyt enää.',240,NULL),
 (4900,'Anonymous','2008-10-17 16:34:42','Anonymous','2008-10-17 16:34:42',21,'1Isoja,\njärkälemäisiä rakeita satoi taivaalta ihmisten päälle,\nja ihmiset herjasivat Jumalaa tämän raevitsauksen\ntähden, sillä vitsaus oli tavattoman ankara. ',240,NULL),
 (4901,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',1,'Yksi niistä seitsemästä enkelistä, joilla oli\nseitsemän maljaa, tuli luokseni ja sanoi: \"Tule, minä\nnäytän sinulle, millainen tuomio annetaan sille\nsuurelle portolle, joka asuu suurten vesien äärellä,',241,NULL),
 (4902,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',2,'sille, jonka kanssa maailman kuninkaat ovat\nirstailleet ja jonka iljettävästä viinistä maan\nasukkaat ovat juopuneet.\"',241,NULL),
 (4903,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',3,'Henki valtasi minut, ja\nenkeli vei minut autiomaahan. Minä näin naisen, joka istui helakanpunaisen pedon\nselässä. Peto oli yltympäri täynnä siihen\nkirjoitettuja herjaavia nimiä, ja sillä oli seitsemän\npäätä ja kymmenen sarvea.',241,NULL),
 (4904,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',4,'Naisen puku hohti\npurppuraisena ja helakanpunaisena, hänen yllään\nkimalsivat kulta, jalokivet ja helmet, ja kädessään\nhän piti kultaista maljaa, joka oli täynnä hänen\nhaureutensa iljetystä ja saastaa.',241,NULL),
 (4905,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',5,'Hänen otsassaan\noli nimi, jolla on salainen merkitys: \"Suuri Babylon,\nmaailman porttojen ja iljetysten äiti.\"*',241,NULL),
 (4906,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',6,'Minä näin,\nettä nainen oli juopunut pyhien verestä ja Jeesuksen\ntodistajien verestä, ja hämmästyin suuresti hänet\nnähdessäni. ',241,NULL),
 (4907,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',7,'Enkeli sanoi minulle: \"Miksi ihmettelet? Minä paljastan sinulle naisen\nsalaisuuden ja häntä kantavan pedon salaisuuden, tuon,\njolla on seitsemän päätä ja kymmenen sarvea. ',241,NULL),
 (4908,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',8,'\"Peto, jonka näit, on ollut, mutta nyt sitä ei ole.\nSe tulee vielä nousemaan syvyydestä, mutta lopulta se\njoutuu tuhon omaksi. Ne maan asukkaat, joiden nimi ei\nmaailman luomisesta alkaen ole ollut kirjoitettuna\nelämän kirjaan, hämmästyvät pedon nähdessään, sillä\nvaikka se on ollut ja mennyt, se on vielä tuleva. ',241,NULL),
 (4909,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',9,'\"Tässä tarvitaan ymmärrystä ja viisautta. Seitsemän\npäätä tarkoittaa seitsemää vuorta, joilla nainen\nistuu. Myös kuninkaita on seitsemän.',241,NULL),
 (4910,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',10,'0Viisi näistä\non jo poissa, kuudes on vallassa. Yksi ei ole vielä\ntullut, ja kun hän tulee, hänen on määrä hallita vain\nvähän aikaa.',241,NULL),
 (4911,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',11,'1Peto itse -- se, joka on ollut mutta\njota ei enää ole -- on kahdeksas kuningas ja samalla\nyksi noista seitsemästä. Se joutuu tuhon omaksi. ',241,NULL),
 (4912,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',12,'2\"Kymmenen sarvea, jotka näit, tarkoittavat\nkymmentä kuningasta, jotka eivät ole vielä nousseet\nvaltaan mutta saavat myöhemmin kuninkuuden yhdessä\npedon kanssa, kuitenkin vain hetkeksi aikaa.',241,NULL),
 (4913,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',13,'3Yksissä tuumin he antavat valtansa ja voimansa\npedon palvelukseen.',241,NULL),
 (4914,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',14,'4Nuo kaikki käyvät taisteluun\nKaritsaa vastaan, mutta Karitsa voittaa heidät, sillä\nhän on herrojen Herra ja kuninkaiden Kuningas. Hänen\nkanssaan saavat voiton hänen uskollisensa, kaikki\nkutsutut ja valitut.\" ',241,NULL),
 (4915,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',15,'5Vielä enkeli sanoi: \"Vedet, jotka näit, ne, joiden äärellä portto asuu,\novat kansoja, joukkoja, maita ja kieliä.',241,NULL),
 (4916,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',16,'6Näkemäsi\nkymmenen sarvea ja peto vihastuvat porttoon ja repivät\nhänet paljaaksi ja alastomaksi, syövät hänen lihansa\nja polttavat hänet tuhkaksi.',241,NULL),
 (4917,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',17,'7Jumala näet panee\nheidät toimimaan oman suunnitelmansa mukaisesti. Siksi\nhe myös yksissä tuumin antavat kuninkaanvaltansa\npedolle siihen saakka kun Jumalan sanat toteutuvat. ',241,NULL),
 (4918,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',18,'8\"Nainen, jonka näit, on se suuri kaupunki, joka\nhallitsee maan kaikkia hallitsijoita.\" ',241,NULL),
 (4919,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',1,'Tämän jälkeen näin taas yhden enkelin tulevan alas\ntaivaasta. Hänellä oli suuri valta, ja hänen\nkirkkautensa valaisi koko maan.',242,NULL),
 (4920,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',2,'Hän huusi kovalla\näänellä: \"Kukistunut, kukistunut on suuri Babylon!\nSiitä on tullut pahojen henkien tyyssija ja kaikkien\nsaastaisten henkien ja kaikkien saastaisten ja\ninhottavien lintujen pesäpaikka.',242,NULL),
 (4921,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',3,'Sen porton viiniä,\nvihan viiniä, ovat kaikki kansat juoneet, kaikki\nmaailman kuninkaat ovat irstailleet hänen kanssaan, ja\nkaikki maailman kauppamiehet ovat rikastuneet hänen\nvauraudestaan ja ylellisestä elämästään.\" ',242,NULL),
 (4922,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',4,'Minä kuulin taivaasta toisenkin äänen, joka sanoi: \"Lähtekää pois, jättäkää hänet, te, jotka olette minun\nkansaani, ettette joutuisi ottamaan osaa hänen\nsynteihinsä ettekä jäisi alttiiksi niille\nvitsauksille, joilla häntä lyödään. ',242,NULL),
 (4923,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',5,'\"Hänen syntiensä röykkiö ulottuu jo taivaaseen\nasti, eikä Jumala unohda hänen pahoja tekojaan.',242,NULL),
 (4924,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',6,'Tehkää hänelle niin kuin hän on tehnyt muille,\nmaksakaa kaksin verroin hänen teoistaan. Siihen\nmaljaan, johon hän on toisille juomansa sekoittanut,\nsekoittakaa häntä itseään varten kahta katkerampi\njuoma.',242,NULL),
 (4925,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',7,'Minkä hän on mainetta ja rikkautta\nahnehtinut, saman verran antakaa hänelle tuskaa ja\nmurhetta. Hän vakuuttelee yhä itselleen: \'Minä olen\nkuningatar ja istun valtaistuimella. Leski en ole,\nsurua en ikinä koe.\'',242,NULL),
 (4926,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',8,'Sen tähden häneen iskevät\nyhtenä ja samana päivänä kaikki hänelle langetetut\nvitsaukset, rutto, murhe ja nälkä, ja hänet poltetaan\ntuhkaksi. Väkevä on Herra Jumala, joka hänet on\ntuominnut! ',242,NULL),
 (4927,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',9,'\"Maailman kuninkaat, jotka ovat hänen kanssaan\nirstailleet ja mässäilleet, itkevät ja valittavat\nhäntä, kun näkevät savun nousevan hänen roviostaan.',242,NULL),
 (4928,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',10,'0He seisovat loitolla, kauhuissaan siitä, miten\nhäntä piinataan. He sanovat:   ',242,NULL),
 (4929,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',11,'1\"Maailman kauppamiehet itkevät ja surevat häntä,\nsillä enää ei kukaan osta heidän tavaroitaan:',242,NULL),
 (4930,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',12,'2ei\nkultaa, hopeaa, jalokiviä eikä helmiä, ei\npellavakangasta, ei purppurakangasta, ei silkkiä eikä\nhelakanpunaisia kankaita, ei tuoksuvaa tuijapuuta, ei\nnorsunluusta eikä jalopuusta tehtyjä esineitä, ei\npronssi-, rauta- eikä marmoritöitä,',242,NULL),
 (4931,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',13,'3ei kanelia\neikä muita mausteita, ei suitsukkeita, ei hajuvoiteita\neikä suitsutuspihkaa, ei viiniä eikä öljyä, ei hienoja\njauhoja eikä viljaa, ei nautakarjaa, ei lampaita, ei\nhevosia eikä vaunuja, ei orjia, ihmisiä ruumiineen ja\nsieluineen.   ',242,NULL),
 (4932,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',15,'5\"Noiden tavaroiden kauppiaat, nuo, jotka ovat\nrikastuneet porton vauraudesta, seisovat loitolla,\nkauhuissaan siitä, miten häntä piinataan. He itkevät\nja valittavat:   \"Myös kaikki laivurit ja purjehtijat, kaikki\nmerimiehet ja muut merenkulkijat seisoivat loitolla,',242,NULL),
 (4933,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',18,'8ja kun he näkivät savun nousevan hänen roviostaan,\nhe huusivat: \'Mikä veti vertoja tälle suurelle\nkaupungille?\'',242,NULL),
 (4934,'Anonymous','2008-10-17 16:34:43','Anonymous','2008-10-17 16:34:43',19,'9He ripottivat hiekkaa hiuksiinsa ja\nhuusivat itkien ja valittaen:   ',242,NULL),
 (4935,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',20,'0\"Iloitse sen lopusta, taivas, iloitkaa, kaikki\npyhät, kaikki apostolit ja profeetat! Jumala on\ntuominnut sen, joka tuomitsi teidät.\" ',242,NULL),
 (4936,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',21,'1Muuan mahtava enkeli otti ison kiven, myllynkiven\nkokoisen, heitti sen mereen ja sanoi:  ',242,NULL),
 (4937,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',1,'Tämän jälkeen kuulin taivaasta mahtavan äänen,\nikään kuin suuri kansanjoukko olisi laulanut:  ',243,NULL),
 (4938,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',3,'Taas kuului ääni:  ',243,NULL);
INSERT INTO `verse` (`verse_id`,`created_by`,`created_on`,`updated_by`,`updated_on`,`verse_num`,`verse_text`,`fk_chapter_id`,`fk_biblesessionid`) VALUES 
 (4939,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',4,'Kaksikymmentäneljä vanhinta ja ne neljä olentoa\nheittäytyivät kasvoilleen Jumalan eteen ja osoittivat\nkunnioitustaan hänelle, joka istuu valtaistuimella. He\nsanoivat:  ',243,NULL),
 (4940,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',5,'Ja valtaistuimelta kuului ääni:   ',243,NULL),
 (4941,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',6,'Sitten kuulin äänen, joka oli kuin suuren\nkansanjoukon kohinaa, kuin suurten vesien pauhua ja\nvoimakasta ukkosen jylinää. Se lausui:  Se pellavapuku tarkoittaa pyhien vanhurskaita tekoja. ',243,NULL),
 (4942,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',9,'Enkeli sanoi minulle: \"Kirjoita: Autuaita ne, jotka\non kutsuttu Karitsan hääaterialle.\" Ja hän jatkoi:\n\"Nämä ovat tosia Jumalan sanoja.\" ',243,NULL),
 (4943,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',10,'0Minä heittäydyin hänen jalkoihinsa kumartaakseni\nhäntä, mutta hän sanoi: \"Älä tee niin! Minä olen vain\nJumalan palvelija, niin kuin sinä ja veljesi, jotka\nolette Jeesuksen todistajia. Jumalaa sinun tulee\nkumartaa!\" Jeesuksen todistajissa on profetoimisen Henki.   ',243,NULL),
 (4944,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',14,'4Hänen jäljessään tulivat taivaan sotajoukot,\nvalkoisilla hevosilla ratsastavat soturit, joiden puku\noli hohtavan valkeaa pellavaa.',243,NULL),
 (4945,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',15,'5Hänen suustaan\nlähtee terävä miekka. Sillä miekalla hän lyö kansoja,\nhän paimentaa niitä rautaisella sauvalla ja polkee\nviinikuurnassa kaikkivaltiaan Jumalan hehkuvan vihan\nviinin.',243,NULL),
 (4946,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',16,'6Hänen viittaansa on reiden kohdalle\nkirjoitettu nimi: kuninkaiden Kuningas, herrojen\nHerra. ',243,NULL),
 (4947,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',17,'7Sitten näin enkelin, joka seisoi ylhäällä\nauringossa. Hän huusi kovalla äänellä kaikille\nlinnuille, jotka lensivät korkealla taivaan laella:\n\"Tulkaa, kokoontukaa Jumalan juhla-aterialle!',243,NULL),
 (4948,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',18,'8Saatte syödä kuninkaiden lihaa, sotapäälliköiden\nja mahtimiesten lihaa, ratsujen ja ratsastajien lihaa,\nkaikkien lihaa, niin vapaiden kuin orjien, niin\npienten kuin suurten.\" ',243,NULL),
 (4949,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',19,'9Sitten näin, että peto ja maailman kuninkaat\nsotajoukkoineen olivat kokoontuneet yhteen käydäkseen\ntaisteluun ratsastajaa ja hänen sotajoukkoaan vastaan.',243,NULL),
 (4950,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',20,'0Mutta peto otettiin kiinni, samoin väärä\nprofeetta, joka pedon nimissä oli tehnyt tunnustekoja\nja johtanut harhaan sen merkin ottaneet ja sen kuvaa\nkumartaneet ihmiset. Ne molemmat heitettiin elävinä\ntuliseen järveen rikinkatkuisten lieskojen keskelle.',243,NULL),
 (4951,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',21,'1Loput saivat surmansa miekasta, joka lähti\nratsastajan suusta, ja kaikki linnut söivät heidän\nlihastaan itsensä kylläisiksi. ',243,NULL),
 (4952,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',1,'Minä näin, kuinka taivaasta tuli alas enkeli, jolla\noli kädessään syvyyden avain ja isot kahleet.',244,NULL),
 (4953,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',2,'Hän\notti kiinni lohikäärmeen, tuon muinaisaikojen\nkäärmeen, jonka nimet ovat Paholainen ja Saatana, ja\npani sen kahleisiin tuhanneksi vuodeksi,',244,NULL),
 (4954,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',3,'syöksi sen\nsyvyyteen ja lukitsi ja sinetöi sen jäljestä syvyyden\noven. Enää ei käärme saa johtaa kansoja harhaan, ennen\nkuin tuhat vuotta on tullut täyteen. Sitten se on\npäästettävä irti vähäksi aikaa. ',244,NULL),
 (4955,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',4,'Minä näin valtaistuimia, ja niille, jotka\nasettuivat istuimille, annettiin tuomiovalta. Minä\nnäin niiden sielut, jotka oli mestattu Jeesuksen\ntodistuksen ja Jumalan sanan tähden, niiden, jotka\neivät olleet kumartaneet petoa eivätkä sen kuvaa\neivätkä olleet ottaneet sen merkkiä otsaansa eivätkä\nkäteensä. He heräsivät eloon ja hallitsivat yhdessä\nKristuksen kanssa tuhat vuotta.',244,NULL),
 (4956,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',5,'Muut kuolleet eivät\nheränneet eloon, ennen kuin tuhat vuotta oli\ntäyttynyt. Tämä on ensimmäinen ylösnousemus.',244,NULL),
 (4957,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',6,'Autuaita ja pyhiä\novat ne, jotka pääsevät osallisiksi ensimmäisestä\nylösnousemuksesta! Heihin ei toisella kuolemalla ole\nvaltaa, vaan he ovat Jumalan ja Kristuksen pappeja ja\nhallitsevat Kristuksen kanssa tuhat vuotta.     ',244,NULL),
 (4958,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',1,'Minä näin uuden taivaan ja uuden maan. Ensimmäinen\ntaivas ja ensimmäinen maa olivat kadonneet, eikä merta\nollut enää.',245,NULL),
 (4959,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',2,'Näin, kuinka pyhä kaupunki, uusi\nJerusalem, laskeutui taivaasta Jumalan luota juhla-\nasuisena, niin kuin morsian, joka on kaunistettu\nsulhasta varten.',245,NULL),
 (4960,'Anonymous','2008-10-17 16:34:44','Anonymous','2008-10-17 16:34:44',3,'Ja minä kuulin valtaistuimen luota\nvoimakkaan äänen, joka sanoi: \"Katso, Jumalan\nasuinsija ihmisten keskellä! Hän asuu heidän luonaan,\nja heistä tulee hänen kansansa. Jumala itse on heidän\nluonaan,',245,NULL),
 (4961,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',4,'ja hän pyyhkii heidän silmistään joka\nainoan kyyneleen. Kuolemaa ei enää ole, ei murhetta,\nvalitusta eikä vaivaa, sillä kaikki entinen on\nkadonnut.\" ',245,NULL),
 (4962,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',5,'Valtaistuimella istuva lausui: \"Uudeksi minä teen\nkaiken.\" Hän sanoi: \"Kirjoita nämä sanat muistiin. Ne\novat luotettavat ja todet.\"',245,NULL),
 (4963,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',6,'Vielä hän sanoi\nminulle: \"Nyt ne ovat käyneet toteen. Minä olen A ja\nO, alku ja loppu. Sille, jolla on jano, minä annan\nlahjaksi vettä elämän veden lähteestä.',245,NULL),
 (4964,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',7,'Tämä on\nvoittajan palkinto. Minä olen hänen Jumalansa, ja hän\non minun poikani.',245,NULL),
 (4965,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',8,'Mutta pelkurit, luopiot ja\niljetysten kumartajat, murhamiehet ja irstailijat,\nnoidat, epäjumalien palvelijat ja kaikki valheen orjat\nsaavat tämän palkan: he joutuvat tuliseen järveen\nrikinkatkuisten lieskojen keskelle. Tämä on toinen\nkuolema.\"   ',245,NULL),
 (4966,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',15,'5Enkelillä, joka minulle puhui, oli kultainen\nmittakeppi kaupungin sekä sen porttien ja muurin\nmittaamista varten.',245,NULL),
 (4967,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',16,'6Kaupunki oli neliön muotoinen,\nyhtä leveä kuin pitkä. Enkeli mittasi kaupungin\nkepillään ja sai tulokseksi kaksitoistatuhatta\nstadionmittaa*; pituus, leveys ja korkeus olivat\nkaikki tämänsuuruiset.',245,NULL),
 (4968,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',17,'7Hän mittasi myös muurin, ja\nse oli sataneljäkymmentäneljä kyynärää -- enkeli\nkäytti siinä ihmismittaa.',245,NULL),
 (4969,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',18,'8Muuri oli rakennettu\njaspiskivestä, ja kaupunki oli kimmeltävää,\nlasinkirkasta kultaa.',245,NULL),
 (4970,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',19,'9Kaupunginmuurin perustukset\noli kaunistettu kaikenlaisilla kalliilla kivillä.\nEnsimmäisenä peruskivenä oli jaspis, toisena safiiri,\nkolmantena kalsedoni, neljäntenä smaragdi,',245,NULL),
 (4971,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',20,'0viidentenä sardonyksi, kuudentena karneoli,\nseitsemäntenä krysoliitti, kahdeksantena berylli,\nyhdeksäntenä topaasi, kymmenentenä krysopraasi,\nyhdentenätoista hyasinttikivi ja kahdentenatoista\nametisti.',245,NULL),
 (4972,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',21,'1Porttitorneina oli kaksitoista helmeä --\nkukin kahdestatoista tornista oli tehty yhdestä\nhelmestä. Kaupungin valtakatu oli kultaa, puhdasta\nkuin läpinäkyvä lasi. ',245,NULL),
 (4973,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',22,'2Temppeliä en kaupungissa nähnyt, sillä sen\ntemppelinä on Herra Jumala, Kaikkivaltias, hän ja\nKaritsa.',245,NULL),
 (4974,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',23,'3Kaupunki ei myöskään tarvitse valokseen\naurinkoa eikä kuuta, sillä Jumalan kirkkaus valaisee\nsen, ja sen lamppuna on Karitsa.',245,NULL),
 (4975,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',24,'4Kansat kulkevat\nsen valossa, ja maailman kuninkaat tuovat sinne\nmahtinsa kaiken loiston.',245,NULL),
 (4976,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',25,'5Sen portteja ei suljeta\npäiväsaikaan, ja yötä siellä ei olekaan.',245,NULL),
 (4977,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',26,'6Kaikki\nkansojen kalleudet ja ihanuudet tuodaan sinne.',245,NULL),
 (4978,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',27,'7Mitään epäpuhdasta ei sinne päästetä, ei\nainoatakaan iljettävän valheen palvelijaa, vaan\nainoastaan ne, joiden nimet on kirjoitettu Karitsan\nelämänkirjaan. ',245,NULL),
 (4979,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',1,'Enkeli näytti minulle elämän veden virran, joka\nkristallinkirkkaana kumpuaa Jumalan ja Karitsan\nvaltaistuimesta.',246,NULL),
 (4980,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',2,'Kaupungin valtakadulla, virran\nhaarojen keskellä kasvoi elämän puu. Puu antaa\nvuodessa kahdettoista hedelmät, uuden sadon kerran\nkuukaudessa, ja sen lehdistä kansat saavat terveyden.',246,NULL),
 (4981,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',3,'Mikään ei enää ole kirouksen kahleissa. Kaupungissa\non Jumalan ja Karitsan valtaistuin, ja kaikki\npalvelevat Jumalaa.',246,NULL),
 (4982,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',4,'He saavat nähdä hänen kasvonsa,\nja heidän otsassaan on hänen nimensä.',246,NULL),
 (4983,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',5,'Yötä ei enää\nole, eivätkä he tarvitse lampun tai auringon valoa,\nsillä Herra Jumala on heidän valonsa. He hallitsevat\nkuninkaina aina ja ikuisesti.   ',246,NULL),
 (4984,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',8,'Minä, Johannes, olen kaiken tämän kuullut ja\nnähnyt. Kuultuani ja nähtyäni sen minä heittäydyin\nkasvoilleni kumartaakseni enkeliä, joka oli sen\nminulle näyttänyt.',246,NULL),
 (4985,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',9,'Mutta hän sanoi: \"Älä tee niin!\nMinä olen vain Jumalan palvelija, niin kuin sinä ja\nprofeetat, sinun veljesi, ja kaikki ne, jotka ottavat\nvarteen tämän kirjan sanat. Jumalaa sinun tulee\nkumartaa!\" ',246,NULL),
 (4986,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',10,'0Sitten minulle sanottiin: \"Älä sulje sinetillä tämän kirjan ennussanoja, sillä\nhetki on lähellä.',246,NULL),
 (4987,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',11,'1Jumalaton eläköön yhä\njumalattomasti, saastainen rypeköön saastassaan,\nvanhurskas harjoittakoon vanhurskautta ja pyhä\nedetköön pyhyyden tiellä. ',246,NULL),
 (4988,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',12,'2\"Minä tulen pian, ja tullessani minä tuon\njokaiselle palkan, maksan kullekin hänen tekojensa\nmukaan.',246,NULL),
 (4989,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',13,'3Minä olen A ja O, ensimmäinen ja\nviimeinen, alku ja loppu. ',246,NULL),
 (4990,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',14,'4\"Autuaita ne, jotka pesevät vaatteensa: he\npääsevät syömään elämän puusta ja saavat mennä\nporteista sisälle kaupunkiin.',246,NULL),
 (4991,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',15,'5Ulkopuolelle jäävät\nkoirat ja noidat, irstailijat, murhaajat ja\nepäjumalien palvelijat ja kaikki, jotka rakastavat\nvalhetta ja noudattavat sitä. ',246,NULL),
 (4992,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',16,'6\"Minä, Jeesus, olen lähettänyt enkelin luoksenne,\njotta seurakunnat saisivat tämän todistuksen. Minä\nolen Daavidin juuriverso ja suku, kirkas aamutähti.\" ',246,NULL),
 (4993,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',17,'7Henki ja morsian sanovat: \"Tule!\" Joka tämän\nkuulee, sanokoon: \"Tule!\" Joka on janoissaan, tulkoon.\nJoka haluaa, saa lahjaksi elämän vettä. ',246,NULL),
 (4994,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',18,'8Jokaiselle, joka kuulee tämän kirjan ennussanat,\nminä vakuutan: Jos joku panee niihin jotakin lisää,\npanee Jumala hänen kärsittävikseen ne vitsaukset,\njoista tässä kirjassa on kerrottu.',246,NULL),
 (4995,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',19,'9Jos joku ottaa\npois jotakin tämän ennustusten kirjan sanoista, ottaa\nJumala pois hänen osallisuutensa elämän puuhun ja\npyhään kaupunkiin, joista tässä kirjassa on kerrottu. ',246,NULL),
 (4996,'Anonymous','2008-10-17 16:34:45','Anonymous','2008-10-17 16:34:45',20,'0Hän, joka todistaa tämän, sanoo: \"Tämä on tosi,\nminä tulen pian.\" Aamen. Tule, Herra Jeesus! ',246,NULL),
 (4997,'Anonymous','2008-10-17 16:34:46','Anonymous','2008-10-17 16:34:46',21,'1Herran Jeesuksen armo olkoon kaikkien kanssa.',246,NULL);
